/*Table structure for table `call_back` */

DROP TABLE IF EXISTS `call_back`;

CREATE TABLE `call_back` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `create_date` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `phone` varchar(50) NOT NULL COMMENT 'Телефон пользователя',
  `comment` varchar(150) DEFAULT NULL COMMENT 'Комментарий',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

/*Data for the table `call_back` */

/*Table structure for table `configs` */

DROP TABLE IF EXISTS `configs`;

CREATE TABLE `configs` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `name` varchar(32) NOT NULL COMMENT 'Название параметра',
  `value` varchar(256) NOT NULL COMMENT 'Значение параметра',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

/*Data for the table `configs` */

/*Table structure for table `menu_items` */

DROP TABLE IF EXISTS `menu_items`;

CREATE TABLE `menu_items` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `menu_id` int(10) unsigned NOT NULL COMMENT 'id из таблицы menus',
  `url` varchar(100) NOT NULL COMMENT 'ссылка меню',
  `weight` mediumint(9) DEFAULT '0' COMMENT 'вес ссылки',
  `title` varchar(100) NOT NULL COMMENT 'Заголовок ссылки',
  PRIMARY KEY (`id`),
  KEY `menu_id` (`menu_id`),
  CONSTRAINT `menu_items_ibfk_1` FOREIGN KEY (`menu_id`) REFERENCES `menus` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=11 DEFAULT CHARSET=utf8;

/*Data for the table `menu_items` */

insert  into `menu_items`(`id`,`menu_id`,`url`,`weight`,`title`) values (1,1,'/o-kompanii',0,'О компании'),(2,1,'/dostavka-oplata',0,'Доставка и оплата'),(3,1,'/warranty',0,'Гарантия'),(4,1,'/contacts',0,'Контакты'),(5,1,'/partnership',0,'Сотрудничество'),(6,2,'/news',0,'Новости'),(7,2,'/services',0,'Сервис и услуги'),(8,2,'/credit',0,'Кредит'),(9,2,'/feedback',0,'Оцените качество'),(10,2,'/social',0,'Мы в социальных сетях');

/*Table structure for table `menus` */

DROP TABLE IF EXISTS `menus`;

CREATE TABLE `menus` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `name` varchar(50) NOT NULL COMMENT 'Название меню',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8;

/*Data for the table `menus` */

insert  into `menus`(`id`,`name`) values (1,'Верхнее меню'),(2,'Меню под шапкой');

/*Table structure for table `meta` */

DROP TABLE IF EXISTS `meta`;

CREATE TABLE `meta` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `title` varchar(255) DEFAULT NULL,
  `description` varchar(512) DEFAULT NULL,
  `keywords` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

/*Data for the table `meta` */

/*Table structure for table `news` */

DROP TABLE IF EXISTS `news`;

CREATE TABLE `news` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `title` varchar(255) NOT NULL COMMENT 'Заголовок новости',
  `pre_content` varchar(500) DEFAULT NULL COMMENT 'Превью контента',
  `content` text NOT NULL COMMENT 'Полный контент',
  `meta_id` int(10) unsigned DEFAULT NULL COMMENT 'id мета данных',
  `category_id` int(10) unsigned DEFAULT '0' COMMENT 'id категории',
  `is_active` tinyint(1) unsigned DEFAULT NULL COMMENT 'Активна ли новость',
  PRIMARY KEY (`id`),
  KEY `meta_id` (`meta_id`),
  KEY `category_id` (`category_id`),
  CONSTRAINT `news_ibfk_1` FOREIGN KEY (`meta_id`) REFERENCES `meta` (`id`) ON DELETE SET NULL,
  CONSTRAINT `news_ibfk_2` FOREIGN KEY (`category_id`) REFERENCES `news_categories` (`id`) ON DELETE SET NULL
) ENGINE=InnoDB AUTO_INCREMENT=491 DEFAULT CHARSET=utf8;

/*Data for the table `news` */

insert  into `news`(`id`,`title`,`pre_content`,`content`,`meta_id`,`category_id`,`is_active`) values (1,'ВИДЕО: Идея для iPhone приложения - пожарная сигнализация','<h2>ПУСТЬ ВАШ iPHONE СПАСЕТ ВАМ ЖИЗНЬ</h2><p class=\"rtecenter\" style=\" text-align: center;\"><br /><object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/vcXGLGRUEiY?version=3&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/vcXGLGRUEiY?version=3&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\">','<h2>ПУСТЬ ВАШ iPHONE СПАСЕТ ВАМ ЖИЗНЬ</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\"><br>\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/vcXGLGRUEiY?version=3&amp;hl=ru_RU\"><param name=\"allowFullScreen\" value=\"true\"><param name=\"allowscriptaccess\" value=\"always\"><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/vcXGLGRUEiY?version=3&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></object></p>\r\n<p>Не смотря на всю нелепость идеи и невозможность ее элегантной реализации это видео поднимает очень важную тему. Как Ваше мобильное устройство может спасти вам жизнь или на худой конец, облегчить ее. как говориться: \"Есть приложение и для этого\".</p>\r\n<p>У сторонних разработчиков целое непаханое поле работы - кардиомониторы, сигнализации автомобильные и для помещений, пульт управления гаражом или \"умным\" домом. Все то, что выполняли ранее странные по габаритам и размерам устройства теперь можно заменить одним, которое всегда с тобой. Ведь смогли же телефоны вытеснить настольные часы и будильники, прикроватные магнитолы и светильники. Настал черед электробритв и кофеварок.</p>\r\n<p><!--break--></p>\r\n',NULL,3,1),(2,'TOP 5 слухов об iPad','<h4>\r\n	Представляем вам хит-парад слухов, связанных с Apple iPad.</h4>\r\n<h4>\r\n	<span class=\"Apple-style-span\" style=\"font-weight: normal; \"><strong>5 Место : Microsoft и Google уже представили &quot;убийц iPad&quot;.</strong></span></h4>\r\n<div class=\"mceTemp\" style=\"text-align: justify;\">\r\n	<dl class=\"wp-caption alignleft\" id=\"attachment_98\" style=\"width: 310px;\">\r\n		<dt class=\"wp-caption-dt\">\r\n			<a href=\"http://www.macov.net/wp-content/uploads/2010/02/Ballmer-HP-slate.jpg\"><img alt=\"убийца iPa','<p style=\"text-align: justify;\"><em>Мнение редакции Macov.net: Планшетные компьютеры под управлением настольной Windows появились несколько лет назад, еще раньше увидели свет ноутбуки-трансформеры с сенсорными дисплеями. То, что представил Стив Баллмер можно назвать UMPC. Такие устройства появились еще в 2007 году, но так и не завоевали популярности из-за небольшого времени работы (в среднем около двух-трех часов), низкой производительности и частым перегревам, а также из-за непомерно высоких цен. Всего было выпущено около двух десятков моделей UMPC, но после массового производства нетбуков их перестали выпускать. Стоит сказать, что Баллмер представил это устройство раньше, чем Apple анонсировали iPad (до этого вся информация о планшетном компьютере Apple была всего лишь слухами). С устройствами на базе Android ситуация обстоит несколько иначе: они гораздо ближе по функционалу к устройству, названному Стивом Джобсом главным достижением в его жизни, но без достаточного количества доступного ПО, книжного и музыкального контента, а также серьезной рекламы, они рискуют так и остаться \"убицами iPad\", почившими в забвении пользователей.</em></p>\r\n<p style=\"text-align: justify;\"><strong>4 Место: iPod Touch и iPhone получат рабочий стол iPad.</strong></p>\r\n<div class=\"mceTemp\" style=\"text-align: justify;\">\r\n	<dl class=\"wp-caption alignleft\" id=\"attachment_103\" style=\"width: 310px;\">\r\n		<dt class=\"wp-caption-dt\"><a href=\"http://www.macov.net/wp-content/uploads/2010/02/hardware-04-20100127.jpg\"><img alt=\"рабочий стол iPad\" class=\"size-medium wp-image-103\" height=\"174\" src=\"http://www.macov.net/wp-content/uploads/2010/02/hardware-04-20100127-300x174.jpg\" title=\"iPad Home Screen\" width=\"300\"></a></dt>\r\n		<dd class=\"wp-caption-dd\">Рабочий стол iPad на iPhone</dd>\r\n	</dl>\r\n</div>\r\n<p style=\"text-align: justify;\">Некие разарботчики ПО для iPhone OS, несколько дней назад получившие обновленную версию SDK, заявили о выходе обновления, которое даст существующим устройствам на базе iPhone OS возможность работы в качестве сетевого диска (iPad может определяться как сетевой компьютер через Bonjour, а файлы, добавленные на него таким образом, будут доступны для всех приложений iPad), поворота домашнего экрана на 90 градусов и ряда других функций. По словам тех же разработчиков, это обновление получит номер 3.2 и станет доступным для загрузки в самое ближайшее время.</p>\r\n<p style=\"text-align: justify;\"><em>Мнение редакции Macov.net: Зная любовь Apple к внедрению новых технологий и программных возможностей во все устройства, это предположение кажется весьма логичным, однако, 2 февраля вышло обновление iPhone OS 3.1.3, которое затронуло только исправление нескольких известных багов: исправлены проблемы с некоторыми приложениями, повышена точность определения уровня заряда аккумулятора iPhone 3GS, а также исправлены баги с японской клавиатурой. Выход обновления, которое дает новые возможности, сразу после очередной заплатки выглядел бы нелогичным, но не исключено, что в ближайшем будущем обновление 3.2 все же выйдет.</em></p>\r\n<p style=\"text-align: justify;\"><strong>3 Место: процессор Apple A4 позволяет запускать Windows 7.</strong></p>\r\n<div class=\"mceTemp\" style=\"text-align: justify;\">\r\n	<dl class=\"wp-caption alignleft\" id=\"attachment_105\" style=\"width: 310px;\">\r\n		<dt class=\"wp-caption-dt\"><a href=\"http://www.macov.net/wp-content/uploads/2010/02/win7ipad4.jpg\"><img alt=\"Windows 7 на iPad\" class=\"size-medium wp-image-105\" height=\"234\" src=\"http://www.macov.net/wp-content/uploads/2010/02/win7ipad4-300x234.jpg\" title=\"iPad Windows 7\" width=\"300\"></a></dt>\r\n		<dd class=\"wp-caption-dd\">Citrix позволяет увидеть рабочий стол Windows 7 на iPad</dd>\r\n	</dl>\r\n</div>\r\n<p style=\"text-align: justify;\">Процессор Apple A4 это результат работы инженеров компании P.A. Semi, купленной Apple около двух лет назад. Это один из самых мощных ARM процессоров, который по производительности может соперничать с одноядерной версией Intel Atom, при этом он не требует мощной системы охлаждения. Но все же ARM архитектура не поддерживается настольными ОС, такими как Mac OS X и Windows 7. Скриншоты Citrix могут создать впечатление, что Windows 7 запущена на iPad, но это всего лишь удаленное управление, которое доступно для всех устройств на базе iPhone OS уже достаточно давно (реализуется программами из AppStore).</p>\r\n<p style=\"text-align: justify;\"><em>Мнение редакции Macov.net: Вы можете точно также управлять своим компьютером и с помощью iPhone, но скриншоты будут не такими реалистичными из-за небольшого экрана. Скорее всего скриншоты Windows 7 были выбраны для демонстрации Citrix из-за новизны этой операционной системы, но революции этот клиент удаленного управления не несет.</em></p>\r\n<p style=\"text-align: justify;\"><strong>2 Место: Apple собирается выпустить iPad под управлением Mac OS X.</strong></p>\r\n<div class=\"mceTemp\" style=\"text-align: justify;\">\r\n	<dl class=\"wp-caption alignleft\" id=\"attachment_106\" style=\"width: 310px;\">\r\n		<dt class=\"wp-caption-dt\"><a href=\"http://www.macov.net/wp-content/uploads/2010/02/ipad020210.jpg\"><img alt=\"Mac OS X на новой модели iPad\" class=\"size-medium wp-image-106\" height=\"192\" src=\"http://www.macov.net/wp-content/uploads/2010/02/ipad020210-300x192.jpg\" title=\"iPad Mac OS X\" width=\"300\"></a></dt>\r\n		<dd class=\"wp-caption-dd\">Mac OS X на новой модели iPad</dd>\r\n	</dl>\r\n</div>\r\n<p style=\"text-align: justify;\">По информации от издания <em>TechChurch, </em>которое ссылается на \"хороший источник\" (имя которого, как обычно, не подлежит огласке) Apple работает над еще одним планшетом, который будет иметь больший дисплей, чем iPad. Этот компьютер будет работать на настольной Mac OS X, в отличии от iPad, а также иметь на борту встроенный Super Drive.</p>\r\n<p style=\"text-align: justify;\"><em>Мнение редакции Macov.net: Если вы очень хотите Mac c сенсорным дисплеем, то можете присмотреться к продукции компании Axiotron - модифицированным компьютерам MacBook в специальном корпусе. Выход в свет подобного устройства, но произведенного самой Apple, по мнению редакции Macov.net, маловероятен в ближайшее время, а новость - слух, спровоцированный желаниями многих фанатов Apple - iPad под управлением Mac OS X.</em></p>\r\n<p style=\"text-align: justify;\"><strong>1 Место: iPad Стива Джобса снабжен встроенной камерой iSight.</strong></p>\r\n<div class=\"mceTemp\" style=\"text-align: justify;\">\r\n	<dl class=\"wp-caption alignleft\" id=\"attachment_107\" style=\"width: 310px;\">\r\n		<dt class=\"wp-caption-dt\"><a href=\"http://www.macov.net/wp-content/uploads/2010/02/isight02_00.jpg\"><img alt=\"iPad Стива Джобса с камерой\" class=\"size-medium wp-image-107\" height=\"215\" src=\"http://www.macov.net/wp-content/uploads/2010/02/isight02_00-300x215.jpg\" title=\"iPad with built-in iSight\" width=\"300\"></a></dt>\r\n		<dd class=\"wp-caption-dd\">iPad Стива Джобса с камерой iSight</dd>\r\n	</dl>\r\n</div>\r\n<p style=\"text-align: justify;\">На следующий день после выступления Стива Джобса, видео с презентации было выложено на официальный сайт Apple. На девятой секунде девятой минуты презентации есть кадр, где отчетливо видно пятно над экраном, похожее на объектив камеры iSight. Масло в огнонь подлило и заявление сервис-центра <em>Mission repair </em>о том, что эта компания располагает частью корпуса iPad, в которой есть отверстие под камеру, такое же, как на MacBook. Нужно сказать, что камера в таком устройстве была бы полезна, ведь на iPad можно использовать Skype.</p>\r\n<p style=\"text-align: justify;\"><em>Мнение редакции Macov.net: Вполне вероятно, что у Apple могли быть планы выпустить iPad со встроенной веб-камерой, но её могли оставить для следующего поколения устройств или вовсе отказаться. Происхождение запчасти Mission repair весьма сомнительно, а также не ясно как к ним могла попасть такая деталь. Веб-камера на кадре из записи демонстрации может оказаться сенсором для автоматической подсветки дисплея, который присутствует в iPhone и iPod Touch и виден только при сильном свете под определенным углом. Но этот слух не стоит сбрасывать со счетов, ведь камеру могут встроить в следующее поколение iPad или в 3G версию, выпуск которой, как известно, будет чуть позже, чем обычной.</em></p>\r\n',NULL,3,1),(3,'Четверка  лучших убийц времени.','<p>\r\n	Думаю, многие замечали, что порой лучшие игры для iPhone/iPod, а теперь и iPad - самые простые аркады. Да, гонки с леталками популярны, но в основном за счет графики и попросту из-за знаменитого имени. Но самые продаваемые игры в AppStore, как правило, самые элементарные 2D убивалки времени. Ведь мы расчехляем свой девайс на лекциях, в очередях и в прочих занудных ситуациях. Здесь я собрал 4 отличные игрушки, помогающие справиться со скукой. В порядке очереди, но не по значению. Цели игр п','\r\n<p>\r\n<!--more-->	1. Любимица AppStore - FlightControl. Цель игры - посадить как можно большее количество самолетов и избежать столкновений. На первый взгляд все элементарно, несколько типов самолетов, вертолеты и 3 посадочные полосы. Цепляем пальцем летательный аппарат и указываем ему дорогу до аэродрома. В последней версии игры 4 эродрома, к каждому надо привыкнуть. После себя игра оставила кучи поделок, но все они немного, но уступают оригиналу, и по популярности, и по геймплею.</p>\r\n<p>\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/04/IMG_0014.png\"><img alt=\"\" class=\"alignnone size-full wp-image-447\" height=\"320\" src=\"http://www.macov.net/wp-content/uploads/2010/04/IMG_0014.png\" title=\"IMG_0014\" width=\"480\" /></a></p>\r\n<p>\r\n	2. В игре BioDefense Zombie Outbreak мы начинаем миссию на опустошенной планете, единственным видом топлива на которой является некая зеленая масса. У нас &quot;база&quot;, энергия и &quot;зелень&quot;. Зелень мы добываем при помощи нехитрой постройки, которая как и все, что мы строим, питается от электричества. Генераторы мы строим на &quot;зелень&quot;. Итого, цель игры достаточно проста: защитить нашу базу, а вместе с ней и все имущество при помощи пушек, ракетных установок и прожекторов, которые помогают раньше заметить противника. Да, играем мы против зомби. Зомби бывают разные и по ходу игры вы поймете, что не все они еле двигаются и наносят небольшой урон, будучи красиво расстреливаемыми нашим оружием. с самого начала игра не кажется простой, в разных режимах зомби нападают по-разному. Либо с определенной стороны, либо со всех сразу. В зависимости от этого и придется планировать оборону. Игра захватывает минимум на пол часа.</p>\r\n<p style=\"text-align: center;\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/04/IMG_0009.png\"><img alt=\"\" class=\"alignnone size-full wp-image-446\" height=\"480\" src=\"http://www.macov.net/wp-content/uploads/2010/04/IMG_0009.png\" title=\"IMG_0009\" width=\"320\" /></a> <a href=\"http://www.macov.net/wp-content/uploads/2010/04/IMG_0007.png\"><img alt=\"\" class=\"alignnone size-full wp-image-445\" height=\"480\" src=\"http://www.macov.net/wp-content/uploads/2010/04/IMG_0007.png\" title=\"IMG_0007\" width=\"320\" /></a></p>\r\n<p>\r\n	3. FieldRunners тоже в свое время восседала на троне AppStore, причем достаточно долгое время. Цель игры - как можно дольше сдерживать поток солдафонов, роботов, мотоциклистов, вертолетов и не дать им пройти в ворота на другом конце экрана. Строим лабиринт из пушек и наблюдаем за сотнями смертей. За каждого убитого дают еще денег, на которые можно сделать апгрейд или купить еще пушек. Одна из лучших &quot;tower defense&quot; игр.</p>\r\n<p class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/04/IMG_0025.png\"><img alt=\"\" class=\"alignnone size-full wp-image-449\" height=\"320\" src=\"http://www.macov.net/wp-content/uploads/2010/04/IMG_0025.png\" title=\"IMG_0025\" width=\"480\" /></a></p>\r\n<p>\r\n	4. AngryBirds. Это не защита замка/базы, а наоборот. Наша цель: при помощи озлобленных птиц разрушить баррикады свиней, укравших птичьи яйца. Птица прыгает в рогатку, оттягиваем её - огонь! И наш камикадзе уходит в полет, прошибая лбом стены, стекла и камни. А игре несколько видов птиц, каждая сильна в чем-то одном. Желтый птенец хорошо пробивает дерево, но терпит полнейшую неудачу при столкновении с камнем. Игра захватывает с первых минут, а оторваться уже сложно.</p>\r\n<p class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/04/IMG_0003.png\"><img alt=\"\" class=\"alignnone size-full wp-image-444\" height=\"320\" src=\"http://www.macov.net/wp-content/uploads/2010/04/IMG_0003.png\" title=\"IMG_0003\" width=\"480\" /></a> <a href=\"http://www.macov.net/wp-content/uploads/2010/04/IMG_0001.png\"><img alt=\"\" class=\"alignnone size-full wp-image-443\" height=\"320\" src=\"http://www.macov.net/wp-content/uploads/2010/04/IMG_0001.png\" title=\"IMG_0001\" width=\"480\" /></a></p>\r\n',NULL,1,1),(4,'Делаем iTunes еще более удобным','<p>\r\n	&nbsp;</p>\r\n<p style=\"font-family: Arial, Tahoma, Helvetica, sans-serif; font: normal normal normal 13px/160% Arial, Tahoma, Helvetica, sans-serif; \">\r\n	<span class=\"Apple-style-span\" style=\"font: normal normal normal 13px/160% Arial, Tahoma, Helvetica, sans-serif; \">У каждого пользователя Mac время от времени возникает желание немного улучшить интерфейс системы, каким бы удобным он не был. Сегодня речь пойдет о программе iTunes - стандартном медиакомбайне Mac OS X и программе для синхрони','\r\n<div style=\"margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; padding-top: 0px; padding-right: 0px; padding-bottom: 0px; padding-left: 0px; background-image: initial; background-attachment: initial; background-origin: initial; background-clip: initial; background-color: rgb(255, 255, 255); font-family: Arial, Tahoma, Helvetica, sans-serif; font: normal normal normal 13px/160% Arial, Tahoma, Helvetica, sans-serif; line-height: normal; font-size: medium; \">\r\n	<p style=\"font-family: Arial, Tahoma, Helvetica, sans-serif; font: normal normal normal 13px/160% Arial, Tahoma, Helvetica, sans-serif; \">\r\n		Эта бесплатная программа позволяет показывать на рабочем столе обложку проигрываемого трэка iTunes, а также немного информации о нем: исполнитель, альбом, название песни и рейтинг. Кроме показа информации, программа прекрасно заменяет миниплеер iTunes, причем вплне успешно. Благодаря бесплатности программы (аналоги стоят недешево), количество тем оформления растет с каждым днем, при этом Bowtie появился позже своих конкурентов. Если вам не хочется искать темы, на&nbsp;<a href=\"http://bowtieapp.com/\" style=\"color: rgb(2, 122, 198); text-decoration: underline; \">сайте разработчика</a>&nbsp;можно скачать небольшой пакет с прекрасными темами оформления, возможно что вам и не захочется искать других, ведь в этом пакете можно найти много красивых и интересных решений: от классической пластинки с обложкой альбома, до темы в виде иконки рабочего стола. Настройкине многочисленны: автозапуск, смена темы, аккаунт Last.fm, а также закрпеление и скрытие программы, на случай, если она нужна вам только для интеграции iTunes в Growl, речь о котором пойдет дальше.</p>\r\n	<h2 style=\"font-family: Arial, Tahoma, Helvetica, sans-serif; text-align: justify; \">\r\n		Growl</h2>\r\n	<p style=\"font-family: Arial, Tahoma, Helvetica, sans-serif; font: normal normal normal 13px/160% Arial, Tahoma, Helvetica, sans-serif; text-align: justify; \">\r\n		&nbsp;</p>\r\n	<div class=\"mceTemp\" style=\"font-family: Arial, Tahoma, Helvetica, sans-serif; font: normal normal normal 13px/160% Arial, Tahoma, Helvetica, sans-serif; text-align: justify; \">\r\n		<dl class=\"wp-caption alignleft\" id=\"attachment_197\" style=\"width: 310px; \">\r\n			<dt class=\"wp-caption-dt\">\r\n				<a href=\"http://www.macov.net/wp-content/uploads/2010/02/%D0%A1%D0%BD%D0%B8%D0%BC%D0%BE%D0%BA-%D1%8D%D0%BA%D1%80%D0%B0%D0%BD%D0%B0-2010-02-10-%D0%B2-17.39.53.jpg\" style=\"color: rgb(2, 122, 198); text-decoration: underline; \"><img alt=\"Growl - система оповещений для Mac OS X\" class=\"size-medium wp-image-197\" height=\"252\" src=\"http://www.macov.net/wp-content/uploads/2010/02/%D0%A1%D0%BD%D0%B8%D0%BC%D0%BE%D0%BA-%D1%8D%D0%BA%D1%80%D0%B0%D0%BD%D0%B0-2010-02-10-%D0%B2-17.39.53-300x252.jpg\" title=\"Growl - система оповещений для Mac OS X\" width=\"300\" /></a></dt>\r\n			<dd class=\"wp-caption-dd\">\r\n				Панель настроек Growl</dd>\r\n		</dl>\r\n	</div>\r\n	<p style=\"font-family: Arial, Tahoma, Helvetica, sans-serif; font: normal normal normal 13px/160% Arial, Tahoma, Helvetica, sans-serif; text-align: justify; \">\r\n		&nbsp;</p>\r\n	<p style=\"font-family: Arial, Tahoma, Helvetica, sans-serif; font: normal normal normal 13px/160% Arial, Tahoma, Helvetica, sans-serif; text-align: justify; \">\r\n		Эта программа незаменима для каждого пользоваетля Mac OS X. К медиаплееру iTunes она, на первый взгляд, отношения не имеет, но в сочетании с Bowtie всё меняется: вы получите красивые всплывающие окна с информацией о том, какая песня сейчас играет. Growl интегрируется в системные настройки и запускается при старте Mac OS X (нужно отметить специальный чекбокс), в настройках программы можно выбрать разные темы для каждого приложения, а вид каждой темы легко настраивается. Growl по умолчанию работает с такими программами, как Folx (популярный менеджер загрузок с поддержкой торрентов), Stuffit Expander (самый популярный распаковщик архивов для Mac OS X), Adium (лучший IM клиент для Mac на сегодняшний день) и другими программами. Даже если вам не нужен Bowtie - Growl это одна из Must Have программ для Mac.</p>\r\n	<h2 style=\"font-family: Arial, Tahoma, Helvetica, sans-serif; text-align: justify; \">\r\n		SongGenie</h2>\r\n	<p style=\"font-family: Arial, Tahoma, Helvetica, sans-serif; font: normal normal normal 13px/160% Arial, Tahoma, Helvetica, sans-serif; text-align: justify; \">\r\n		&nbsp;</p>\r\n	<div class=\"mceTemp\" style=\"font-family: Arial, Tahoma, Helvetica, sans-serif; font: normal normal normal 13px/160% Arial, Tahoma, Helvetica, sans-serif; text-align: justify; \">\r\n		<dl class=\"wp-caption alignleft\" id=\"attachment_194\" style=\"width: 310px; \">\r\n			<dt class=\"wp-caption-dt\">\r\n				<a href=\"http://www.macov.net/wp-content/uploads/2010/02/%D0%A1%D0%BD%D0%B8%D0%BC%D0%BE%D0%BA-%D1%8D%D0%BA%D1%80%D0%B0%D0%BD%D0%B0-2010-02-10-%D0%B2-17.37.23.jpg\" style=\"color: rgb(2, 122, 198); text-decoration: underline; \"><img alt=\"SongGenie найдет информацю о ваших треках\" class=\"size-medium wp-image-194\" height=\"202\" src=\"http://www.macov.net/wp-content/uploads/2010/02/%D0%A1%D0%BD%D0%B8%D0%BC%D0%BE%D0%BA-%D1%8D%D0%BA%D1%80%D0%B0%D0%BD%D0%B0-2010-02-10-%D0%B2-17.37.23-300x202.jpg\" title=\"SongGenie найдет информацю о ваших треках\" width=\"300\" /></a></dt>\r\n			<dd class=\"wp-caption-dd\">\r\n				Интерфейс SongGenie</dd>\r\n		</dl>\r\n	</div>\r\n	<p style=\"font-family: Arial, Tahoma, Helvetica, sans-serif; font: normal normal normal 13px/160% Arial, Tahoma, Helvetica, sans-serif; text-align: justify; \">\r\n		&nbsp;</p>\r\n	<p style=\"font-family: Arial, Tahoma, Helvetica, sans-serif; font: normal normal normal 13px/160% Arial, Tahoma, Helvetica, sans-serif; text-align: justify; \">\r\n		&nbsp;</p>\r\n	<p style=\"font-family: Arial, Tahoma, Helvetica, sans-serif; font: normal normal normal 13px/160% Arial, Tahoma, Helvetica, sans-serif; text-align: justify; \">\r\n		Если вам надоело что импортированные CD или песни из интернета имеют неправильные названия, что затрудняет их поиск в больших медиатеках, а также делает невозможным поиск обложки альбома, то эта программа, обладающая удобным и очень красивым интерфейсом, даст вам возможность найти правильные названия для песен и альбомов, а также имена Артистов и названия групп. SongGenie проста в использовании и отлично интегрирована с программой CoverScout от того же разработчика. CoverScout дает Вам возможность искать обложки альбомов для песен из вашей медиатеки iTunes, подробнее о CoverScout чуть ниже.</p>\r\n	<p style=\"font-family: Arial, Tahoma, Helvetica, sans-serif; font: normal normal normal 13px/160% Arial, Tahoma, Helvetica, sans-serif; text-align: justify; \">\r\n		&nbsp;</p>\r\n	<h2 style=\"font-family: Arial, Tahoma, Helvetica, sans-serif; text-align: justify; \">\r\n		CoverScout</h2>\r\n	<p style=\"font-family: Arial, Tahoma, Helvetica, sans-serif; font: normal normal normal 13px/160% Arial, Tahoma, Helvetica, sans-serif; text-align: justify; \">\r\n		&nbsp;</p>\r\n	<div class=\"mceTemp\" style=\"font-family: Arial, Tahoma, Helvetica, sans-serif; font: normal normal normal 13px/160% Arial, Tahoma, Helvetica, sans-serif; text-align: justify; \">\r\n		<dl class=\"wp-caption alignleft\" id=\"attachment_195\" style=\"width: 310px; \">\r\n			<dt class=\"wp-caption-dt\">\r\n				<a href=\"http://www.macov.net/wp-content/uploads/2010/02/%D0%A1%D0%BD%D0%B8%D0%BC%D0%BE%D0%BA-%D1%8D%D0%BA%D1%80%D0%B0%D0%BD%D0%B0-2010-02-10-%D0%B2-17.38.12.jpg\" style=\"color: rgb(2, 122, 198); text-decoration: underline; \"><img alt=\"CoverScout найдет все обложки\" class=\"size-medium wp-image-195\" height=\"213\" src=\"http://www.macov.net/wp-content/uploads/2010/02/%D0%A1%D0%BD%D0%B8%D0%BC%D0%BE%D0%BA-%D1%8D%D0%BA%D1%80%D0%B0%D0%BD%D0%B0-2010-02-10-%D0%B2-17.38.12-300x213.jpg\" title=\"CoverScout найдет все обложки\" width=\"300\" /></a></dt>\r\n			<dd class=\"wp-caption-dd\">\r\n				CoverScout найдет все обложки</dd>\r\n		</dl>\r\n	</div>\r\n	<p style=\"font-family: Arial, Tahoma, Helvetica, sans-serif; font: normal normal normal 13px/160% Arial, Tahoma, Helvetica, sans-serif; text-align: justify; \">\r\n		Обложки альбомов удобны для быстрого поиска нужной песни в медиатеке iTunes. В медиакомбайне Apple есть возможность искать обложки самостоятельно, но есть ряд недостатков. Во-первых, песня должна быть представлена в магазине iTunes Store, так что если ваш любимый исполнитель не настолько популярен в США, как вам хотелось бы, то увы, обложку вам скорее всего не найти. Во-вторых, iTunes сохраняет обложки в отдельной папке и при копировании на телефон или плеер (к iPod не относится) обложки вряд ли скопируются, ведь информации о них нет в свойствах самого файла песни. CoverScout найдет обложки и присоеденит их в сами файлы, при поиске используется Amazon, Google и ряд музыкальных интернет-магазинов, так что шансы найти правильную обложку в хорошем качестве очень велики. Если поиск оказался безуспешным, то программа предложит найти обложку самостоятельно, через встроенный веб-браузер. Вы также можете открыть SongGenie прямо из CoverScout, чтобы проверить правильность названия песни и исправить, если потребуется.</p>\r\n	<h2 style=\"font-family: Arial, Tahoma, Helvetica, sans-serif; text-align: justify; \">\r\n		Заключение</h2>\r\n	<p style=\"font-family: Arial, Tahoma, Helvetica, sans-serif; font: normal normal normal 13px/160% Arial, Tahoma, Helvetica, sans-serif; text-align: justify; \">\r\n		<em>Mac OS X, несмотря на слухи, имеет множество программ для повышения юзабилити системы, в этом небольшом обзоре приведены лишь самые популярные программы, но решений для повышения удобства использования iTunesd разы больше и у вас есть шансы самостоятельно найти именно ту программу, которая будет для вас наиболее удобной и необходимой.</em></p>\r\n</div>\r\n',NULL,1,1),(5,'Записываем качественный звук на iPad','<p>\r\n	Наконец, iPad camera connection kit стала доступной в американских магазинах, теперь владельцы айпадов могут подключить гарнитуры, клавиатуры, но не только. В инструкции написано, что мы можем подключить карту памяти, либо фотоаппарат. Но это последние вещи, которые я хочу подключить к своему iPad. Перепробовав горку разных гаджетов, я начал думать, то действительно этот usb порт будет видеть только мою камеру, да айфон (с которого можно перекинуть фотографии, но не более). Айпад жаловался','\r\n<p>\r\n	Но, как вы знаете, для айпада еще(?) нет VoiceMemo, так что пришлось поискать специальное приложение от Blue, чтобы не томить вас, это <a href=\"http://itunes.apple.com/us/app/blue-fire/id336713577?mt=8\">BlueFire</a>. Приложение создавалось для Blue Mikey, еще одного микрофона для айдейвайсов. Все заработало прекрасно, звук пишет и сохранеяет качественный. Осталось решить, нужен ли айпад для звукозаписи? Он однозначно пригодится музыкантам, но он не многимме ньше ноутбука, а Yeti гораздо больше обычного микрофона.</p>\r\n',NULL,1,1),(6,'BookBooK – креативный чехол для вашего бука.','<p>\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/02/1264495469_1264418216_bookbook_4.jpg\"><img alt=\"\" class=\"aligncenter size-medium wp-image-159\" height=\"206\" src=\"http://www.macov.net/wp-content/uploads/2010/02/1264495469_1264418216_bookbook_4-300x206.jpg\" width=\"300\" /></a></p>\r\n<p>\r\n	Имея ноутбук многие мечтают выделяться, покупая красочный или выполненный из дорогих материалов чехол. Компания <a href=\"http://www.twelvesouth.com/\" target=\"_blank\" title=\"http://www.twelvesouth.com/','\r\n<p>\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/02/1264495487_1264418222_bookbook_3.jpg\"><img alt=\"\" class=\"aligncenter size-medium wp-image-160\" height=\"300\" src=\"http://www.macov.net/wp-content/uploads/2010/02/1264495487_1264418222_bookbook_3-300x300.jpg\" width=\"300\" /></a></p>\r\n',NULL,1,1),(7,'Немного юмора о Apple iPad','<p>Ставшее уже традиционным сравнение Apple iPad с Amazon Kindle и обычным камнем</p>\r\n<p><a href=\"http://www.macov.net/wp-content/uploads/2010/02/stone1.jpg\"><img alt=\"\" class=\"alignnone size-full wp-image-165\" height=\"216\" src=\"http://www.macov.net/wp-content/uploads/2010/02/stone1.jpg\" title=\"stone1\" width=\"387\"></a> <a href=\"http://www.macov.net/wp-content/uploads/2010/02/stone2-400x227.jpg\"><img alt=\"\" class=\"alignnone size-full wp-image-166\" height=\"227\" src=\"http://www.macov.net/wp-conten','<p>Ставшее уже традиционным сравнение Apple iPad с Amazon Kindle и обычным камнем</p>\r\n<p><a href=\"http://www.macov.net/wp-content/uploads/2010/02/stone1.jpg\"><img alt=\"\" class=\"alignnone size-full wp-image-165\" height=\"216\" src=\"http://www.macov.net/wp-content/uploads/2010/02/stone1.jpg\" title=\"stone1\" width=\"387\"></a> <a href=\"http://www.macov.net/wp-content/uploads/2010/02/stone2-400x227.jpg\"><img alt=\"\" class=\"alignnone size-full wp-image-166\" height=\"227\" src=\"http://www.macov.net/wp-content/uploads/2010/02/stone2-400x227.jpg\" title=\"stone2-400x227\" width=\"400\"></a></p>\r\n<p>&nbsp;</p>\r\n<p>Эволюция Apple</p>\r\n<p><a href=\"http://www.macov.net/wp-content/uploads/2010/02/evolution.jpg\"><img alt=\"\" class=\"alignnone size-full wp-image-167\" height=\"463\" src=\"http://www.macov.net/wp-content/uploads/2010/02/evolution.jpg\" title=\"evolution\" width=\"550\"></a></p>\r\n<p>Представляем iPad Nano! <a href=\"http://www.macov.net/wp-content/uploads/2010/02/ipadnano-392x400.jpg\"><img alt=\"\" class=\"alignnone size-full wp-image-168\" height=\"400\" src=\"http://www.macov.net/wp-content/uploads/2010/02/ipadnano-392x400.jpg\" title=\"ipadnano-392x400\" width=\"392\"></a></p>\r\n',NULL,1,1),(8,'Весьма своеобразный чехол для Apple iPad','<p>Такого количества и разнообразия чехлов, как для продуктов Apple, нет ни для каких устройств других производителей. Ipad еще не вышел, а аксессуары для него начали производить на следующий после премьеры день. В ряду необычных чехлов отдельно хочется отметить вот такое произведение искусства, девушкам должно понравиться! .</p>\r\n<p><a href=\"http://www.macov.net/wp-content/uploads/2010/03/il_430xN.125922357.jpg\"><img alt=\"\" class=\"alignnone size-full wp-image-284\" height=\"287\" src=\"http://www.m','<p>Такого количества и разнообразия чехлов, как для продуктов Apple, нет ни для каких устройств других производителей. Ipad еще не вышел, а аксессуары для него начали производить на следующий после премьеры день. В ряду необычных чехлов отдельно хочется отметить вот такое произведение искусства, девушкам должно понравиться! .</p>\r\n<p><a href=\"http://www.macov.net/wp-content/uploads/2010/03/il_430xN.125922357.jpg\"><img alt=\"\" class=\"alignnone size-full wp-image-284\" height=\"287\" src=\"http://www.macov.net/wp-content/uploads/2010/03/il_430xN.125922357.jpg\" title=\"ipad\" width=\"430\"></a> <a href=\"http://www.macov.net/wp-content/uploads/2010/03/il_430xN.125922408.jpg\"><img alt=\"\" class=\"alignnone size-full wp-image-285\" height=\"287\" src=\"http://www.macov.net/wp-content/uploads/2010/03/il_430xN.125922408.jpg\" title=\"iPad\" width=\"430\"></a></p>\r\n',NULL,1,1),(9,'iPhone, iPad - что же будет дальше?','<p>В истоках стоял iPhone, появившийся в далеком 2008 году. Появление его вызвало фурор, он вызвал рывок в развитии такого рода устройств.</p>\r\n<p><a href=\"http://www.macov.net/wp-content/uploads/2010/03/apple-iphone.jpg\"><img alt=\"\" class=\"alignnone size-full wp-image-300\" height=\"438\" src=\"http://www.macov.net/wp-content/uploads/2010/03/apple-iphone.jpg\" title=\"apple-iphone\" width=\"600\"></a></p>\r\n<p>Но время идет, и людям приелся iPhone, Apple решает что пора сделать что-то посерьезнее, поболь','<p>В истоках стоял iPhone, появившийся в далеком 2008 году. Появление его вызвало фурор, он вызвал рывок в развитии такого рода устройств.</p>\r\n<p><a href=\"http://www.macov.net/wp-content/uploads/2010/03/apple-iphone.jpg\"><img alt=\"\" class=\"alignnone size-full wp-image-300\" height=\"438\" src=\"http://www.macov.net/wp-content/uploads/2010/03/apple-iphone.jpg\" title=\"apple-iphone\" width=\"600\"></a></p>\r\n<p>Но время идет, и людям приелся iPhone, Apple решает что пора сделать что-то посерьезнее, побольше, посильнее. и раздули его до размеров iPad. <a href=\"http://www.macov.net/wp-content/uploads/2010/03/apple-ipad.jpg\"><img alt=\"\" class=\"alignnone size-full wp-image-299\" height=\"420\" src=\"http://www.macov.net/wp-content/uploads/2010/03/apple-ipad.jpg\" title=\"apple-ipad\" width=\"600\"></a></p>\r\n<p>Но кто знает, чего захочется рядовому пользователю дальше? может ему понадобится что-то еще большее, чем iPad? <a href=\"http://www.macov.net/wp-content/uploads/2010/03/apple-iboard.jpg\"><img alt=\"\" class=\"alignnone size-full wp-image-297\" height=\"442\" src=\"http://www.macov.net/wp-content/uploads/2010/03/apple-iboard.jpg\" title=\"apple-iboard\" width=\"600\"></a></p>\r\n<p>И вот она, вершина развития, самый лучший девайс для семьи и друзей - встречайте - iMat. Это огромный мат, с сенсорным мульти-тач экраном. Так же его можно использовать вместо обоев или напольного покрытия.</p>\r\n<p><a href=\"http://www.macov.net/wp-content/uploads/2010/03/apple-imat.jpg\"><img alt=\"\" class=\"alignnone size-full wp-image-298\" height=\"456\" src=\"http://www.macov.net/wp-content/uploads/2010/03/apple-imat.jpg\" title=\"apple-imat\" width=\"600\"></a></p>\r\n',NULL,1,1),(10,'Надоедливый апельсин против Яблока','<p style=\"text-align: justify;\">\r\n	В последнее время в сети стали очень популярными ролики про Надоедливый апельсин. У него даже появилась своя личная страничка на Ютубе. И очередной жертвой стал, кто, как вы думаете? Правильно, iPhone собственной персоной. Осторожно, видеоролик на английском языке.</p>\r\n<p class=\"rtecenter\">\r\n	<object classid=\"clsid:d27cdb6e-ae6d-11cf-96b8-444553540000\" codebase=\"http://download.macromedia.com/pub/shockwave/cabs/flash/swflash.cab#version=6,0,40,0\" height=\"340\" ','<p style=\"text-align: justify;\">\r\n	В последнее время в сети стали очень популярными ролики про Надоедливый апельсин. У него даже появилась своя личная страничка на Ютубе. И очередной жертвой стал, кто, как вы думаете? Правильно, iPhone собственной персоной. Осторожно, видеоролик на английском языке.</p>\r\n<p class=\"rtecenter\">\r\n	<object classid=\"clsid:d27cdb6e-ae6d-11cf-96b8-444553540000\" codebase=\"http://download.macromedia.com/pub/shockwave/cabs/flash/swflash.cab#version=6,0,40,0\" height=\"340\" width=\"560\"> <param name=\"allowFullScreen\" value=\"true\" /> <param name=\"allowscriptaccess\" value=\"always\" /> <param name=\"src\" value=\"http://www.youtube.com/v/E4GhMYxE2Lc&amp;hl=ru_RU&amp;fs=1&amp;\" /> <param name=\"allowfullscreen\" value=\"true\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"340\" src=\"http://www.youtube.com/v/E4GhMYxE2Lc&amp;hl=ru_RU&amp;fs=1&amp;\" type=\"application/x-shockwave-flash\" width=\"560\"></embed></object></p>\r\n\r\n<p style=\"text-align: justify;\">\r\n	Ссылка на страницу Апельсина: <a href=\"http://www.youtube.com/user/realannoyingorange\">http://www.youtube.com/user/realannoyingorange</a></p>\r\n',NULL,1,1),(11,'Анатомия дизайна Apple','\r\n<p>\r\nРебята из компании Transparent House, занимающейся 3д моделированием, сделали великолепный ролик, в котором показали весь нелегкий путь Apple - от первого компьютера до iPad. Что характерно, все модели смоделированы в 3д, а качество просто поражает. Смотрим и наслаждаемся.</p>\r\n<p class=\"rtecenter\">\r\n<object classid=\"clsid:d27cdb6e-ae6d-11cf-96b8-444553540000\" codebase=\"http://download.macromedia.com/pub/shockwave/cabs/flash/swflash.cab#version=6,0,40,0\" height=\"225\" width=\"400\"><param na','<p>&nbsp;</p>\r\n<div>&lt;p&gt;</div>\r\n<div>Ребята из компании Transparent House, занимающейся 3д моделированием, сделали великолепный ролик, в котором показали весь нелегкий путь Apple - от первого компьютера до iPad. Что характерно, все модели смоделированы в 3д, а качество просто поражает. Смотрим и наслаждаемся.&lt;/p&gt;</div>\r\n<div>&lt;p class=\"rtecenter\"&gt;</div>\r\n<div>&lt;object classid=\"clsid:d27cdb6e-ae6d-11cf-96b8-444553540000\" codebase=\"http://download.macromedia.com/pub/shockwave/cabs/flash/swflash.cab#version=6,0,40,0\" height=\"225\" width=\"400\"&gt;&lt;param name=\"allowfullscreen\" value=\"true\" /&gt;&lt;param name=\"allowscriptaccess\" value=\"always\" /&gt;&lt;param name=\"src\" value=\"http://vimeo.com/moogaloop.swf?clip_id=9820827&amp;amp;server=vimeo.com&amp;amp;show_title=1&amp;amp;show_byline=1&amp;amp;show_portrait=0&amp;amp;color=&amp;amp;fullscreen=1\" /&gt;&lt;embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"225\" src=\"http://vimeo.com/moogaloop.swf?clip_id=9820827&amp;amp;server=vimeo.com&amp;amp;show_title=1&amp;amp;show_byline=1&amp;amp;show_portrait=0&amp;amp;color=&amp;amp;fullscreen=1\" type=\"application/x-shockwave-flash\" width=\"400\"&gt;&lt;/embed&gt;&lt;/object&gt;&lt;/p&gt;</div>\r\n<div>&lt;p&gt;</div>\r\n<div>&lt;a href=\"http://vimeo.com/9820827\"&gt;Anatomy of Apple Design&lt;/a&gt; from &lt;a href=\"http://vimeo.com/user1724770\"&gt;Transparent House&lt;/a&gt; on &lt;a href=\"http://vimeo.com\"&gt;Vimeo&lt;/a&gt;.&lt;/p&gt;</div>\r\n<div>&nbsp;</div>\r\n',NULL,1,1),(12,'Обновление CleanMyMac','<p style=\"text-align: justify;\">\r\n	<strong>MacPaw</strong> выпустила новую версию приложения <strong>CleanMyMac 1.8</strong>, в которое добавили возможность работы с автозагрузкой, а так же с . Это приложение позволяет без особых проблем отчистить ваш Mac от мусора. СleanMyMac без проблем удалит кэши, логи, ненужные локализации и т.д.</p>\r\n<p class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/03/cleanmymac12_00.jpg\"><img alt=\"\" class=\"alignnone size-full wp-image-281\" hei','<p style=\"text-align: justify;\">\r\n	<strong>MacPaw</strong> выпустила новую версию приложения <strong>CleanMyMac 1.8</strong>, в которое добавили возможность работы с автозагрузкой, а так же с . Это приложение позволяет без особых проблем отчистить ваш Mac от мусора. СleanMyMac без проблем удалит кэши, логи, ненужные локализации и т.д.</p>\r\n<p class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/03/cleanmymac12_00.jpg\"><img alt=\"\" class=\"alignnone size-full wp-image-281\" height=\"537\" src=\"http://www.macov.net/wp-content/uploads/2010/03/cleanmymac12_00.jpg\" title=\"cleanmymac12_00\" width=\"600\" /></a></p>\r\n\r\n<p style=\"text-align: justify;\">\r\n	Все что по мнению приложения не нужно оно сортирует по разным категориям, дабы вы могли наглядно понять, что лучше отчистить.</p>\r\n<p style=\"text-align: justify;\">\r\n	Нововведения:</p>\r\n<p style=\"text-align: justify;\">\r\n	Категория Системный Мусор - в которой группируется весь бесполезный системный мусор.</p>\r\n<p style=\"text-align: justify;\">\r\n	удаление с внешних накопителей скрытых служебных файлов.</p>\r\n<p style=\"text-align: justify;\">\r\n	Новые предупреждающие диалоги, исправлены многие ошибки...</p>\r\n<p style=\"text-align: justify;\">\r\n	Системные требования: Mac OS X 10.4.11 и выше.</p>\r\n<p style=\"text-align: justify;\">\r\n	официальный сайт: <a href=\"http://www.macpaw.com/\">http://www.macpaw.com/</a></p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	&nbsp;</p>\r\n',NULL,1,1),(13,'iTunes Store бьет все рекорды','<p>\r\n	Как стало недавно известно, iTunes Store продала юбилейную, десятимиллиардную песню. Ради такого события компания сделала специальный счетчик, который зафиксировал рекорд 25го февраля.</p>\r\n<p class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/03/apple-olympic.jpg\"><img alt=\"\" class=\"alignnone size-full wp-image-275\" height=\"128\" src=\"http://www.macov.net/wp-content/uploads/2010/03/apple-olympic.jpg\" title=\"apple-olympic\" width=\"558\" /></a></p>\r\n<p>\r\n	К этому резуль','<p>&nbsp;</p>\r\n<div>&lt;p&gt;</div>\r\n<div>Как стало недавно известно, iTunes Store продала юбилейную, десятимиллиардную песню. Ради такого события компания сделала специальный счетчик, который зафиксировал рекорд 25го февраля.&lt;/p&gt;</div>\r\n<div>&lt;p class=\"rtecenter\"&gt;</div>\r\n<div>&lt;a href=\"http://www.macov.net/wp-content/uploads/2010/03/apple-olympic.jpg\"&gt;&lt;img alt=\"\" class=\"alignnone size-full wp-image-275\" height=\"128\" src=\"http://www.macov.net/wp-content/uploads/2010/03/apple-olympic.jpg\" title=\"apple-olympic\" width=\"558\" /&gt;&lt;/a&gt;&lt;/p&gt;</div>\r\n<div>&lt;p&gt;</div>\r\n<div>К этому результату Apple стремилась целых 7 лет, за которые iTunes Store развился до небывалых размеров.&lt;/p&gt;</div>\r\n<div>\r\n	<div>&nbsp;</div>\r\n	<div>&lt;p&gt;</div>\r\n	<div>Юбилейным десятимиллиардным покупателем стал 70 летний дедушка Луи Сулсер. В качестве выигрыша он получил iTunes gift card на 10 тысяч долларов. Поздравить же его позвонил сам Стив Джобс!&lt;/p&gt;</div>\r\n	<div>&nbsp;</div>\r\n</div>\r\n<p>&nbsp;</p>\r\n',NULL,1,1),(14,'Оживите свой рабочий стол','<p style=\"text-align: justify;\">\r\n	Вам надоел ваш рабочий стол? Пытаетесь изменить, улучшить, но вам все равно чего то не хватает? Тогда <strong>Mach Desktop</strong> создан специально для вас! Это приложение позволяет ставить видео обои, QuikTime ролики а так же Веб страницы.</p>\r\n<center>\r\n	<p>\r\n		<a href=\"http://www.macov.net/wp-content/uploads/2010/03/desktopmain.jpg\"><img alt=\"\" class=\"size-medium wp-image-341\" height=\"186\" src=\"http://www.macov.net/wp-content/uploads/2010/03/desktopmain-30','<p style=\"text-align: justify;\">\r\n	Вам надоел ваш рабочий стол? Пытаетесь изменить, улучшить, но вам все равно чего то не хватает? Тогда <strong>Mach Desktop</strong> создан специально для вас! Это приложение позволяет ставить видео обои, QuikTime ролики а так же Веб страницы.</p>\r\n<center>\r\n	<p>\r\n		<a href=\"http://www.macov.net/wp-content/uploads/2010/03/desktopmain.jpg\"><img alt=\"\" class=\"size-medium wp-image-341\" height=\"186\" src=\"http://www.macov.net/wp-content/uploads/2010/03/desktopmain-300x233.jpg\" title=\"desktopmain\" width=\"240\" /></a> <a href=\"http://www.macov.net/wp-content/uploads/2010/03/desktop1.jpg\"><img alt=\"\" class=\"size-medium wp-image-340\" height=\"186\" src=\"http://www.macov.net/wp-content/uploads/2010/03/desktop1-300x233.jpg\" title=\"desktop1\" width=\"240\" /></a></p>\r\n</center>\r\n\r\n<p style=\"text-align: justify;\">\r\n	В активе Mach Desktop двадцать высококачественных видео обоев, а так же семь видео роликов для рабочего стола. Кроме того имеется функция Widget, позволяющая выводить на рабочий стол необходимые вам виджеты.</p>\r\n<center>\r\n	<p>\r\n		<object classid=\"clsid:d27cdb6e-ae6d-11cf-96b8-444553540000\" codebase=\"http://download.macromedia.com/pub/shockwave/cabs/flash/swflash.cab#version=6,0,40,0\" height=\"344\" width=\"425\"><param name=\"allowFullScreen\" value=\"true\" /> <param name=\"allowScriptAccess\" value=\"always\" /> <param name=\"src\" value=\"http://www.youtube.com/v/NSic2TqSI-Q&amp;color1=0xb1b1b1&amp;color2=0xcfcfcf&amp;hl=en_US&amp;feature=player_embedded&amp;fs=1\" /> <param name=\"allowfullscreen\" value=\"true\" /> <embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"344\" src=\"http://www.youtube.com/v/NSic2TqSI-Q&amp;color1=0xb1b1b1&amp;color2=0xcfcfcf&amp;hl=en_US&amp;feature=player_embedded&amp;fs=1\" type=\"application/x-shockwave-flash\" width=\"425\"></embed></object></p>\r\n</center>\r\n<p style=\"text-align: justify;\">\r\n	Системные требования: Mac OSX 10.4 и выше.</p>\r\n<p style=\"text-align: justify;\">\r\n	официальный сайт: <a href=\"http://www.machsoftwaredesign.com/\">http://www.machsoftwaredesign.com/</a></p>\r\n',NULL,1,1),(15,'Сенсорная беда','<p>\r\n	Что-то не успокаиваются люди, отдавшие сотни долларов за айфон 4. Все находят и находят новые неполадки.Если проблему с приемом сигнала решали скотчем или новеньким бампером, то что делать с лагающим датчиком отдаленности? Если верить <a href=\"http://www.macnn.com/articles/10/06/28/issue.blamed.on.software/\">Macnn</a> и жалобам на форумах Эппл, датчик временами не срабатывает и людские уши творят все, что им угодно с экраном новенького и стеклянного. Можно предположить, это недоделка прогр','<p>&nbsp;</p>\r\n<div>&lt;p&gt;</div>\r\n<div>Что-то не успокаиваются люди, отдавшие сотни долларов за айфон 4. Все находят и находят новые неполадки.Если проблему с приемом сигнала решали скотчем или новеньким бампером, то что делать с лагающим датчиком отдаленности? Если верить &lt;a href=\"http://www.macnn.com/articles/10/06/28/issue.blamed.on.software/\"&gt;Macnn&lt;/a&gt; и жалобам на форумах Эппл, датчик временами не срабатывает и людские уши творят все, что им угодно с экраном новенького и стеклянного. Можно предположить, это недоделка программная, хочется верить. Имеют успех перезагрузки телефона, но не у каждого.&lt;/p&gt;</div>\r\n<div>&nbsp;</div>\r\n',NULL,1,1),(16,'Синхронизация без проводов? Возможно!','<p class=\"rtecenter\">\r\n	<object classid=\"clsid:d27cdb6e-ae6d-11cf-96b8-444553540000\" codebase=\"http://download.macromedia.com/pub/shockwave/cabs/flash/swflash.cab#version=6,0,40,0\" height=\"350\" width=\"425\"> <param name=\"src\" value=\"http://www.youtube.com/v/rZ7xn86Zwyo\" /><embed height=\"350\" src=\"http://www.youtube.com/v/rZ7xn86Zwyo\" type=\"application/x-shockwave-flash\" width=\"425\"></embed></object></p>\r\n<p>\r\n	Видео выложенное на youtube Грегом Хьюгсом не может не обрадовать миллионы людей, мающи','<p class=\"rtecenter\">\r\n	<object classid=\"clsid:d27cdb6e-ae6d-11cf-96b8-444553540000\" codebase=\"http://download.macromedia.com/pub/shockwave/cabs/flash/swflash.cab#version=6,0,40,0\" height=\"350\" width=\"425\"> <param name=\"src\" value=\"http://www.youtube.com/v/rZ7xn86Zwyo\" /><embed height=\"350\" src=\"http://www.youtube.com/v/rZ7xn86Zwyo\" type=\"application/x-shockwave-flash\" width=\"425\"></embed></object></p>\r\n<p>\r\n	Видео выложенное на youtube Грегом Хьюгсом не может не обрадовать миллионы людей, мающихся с коротенькими яблочными проводами. Грег заставил свой мак и айфон синхронизироваться, как видно из ролика - весьма удачно и изящно. На данный момент, приложение ждет вердикта Apple и размещения в AppStore. Если уж Стиву не будет по нраву приложение, возможно мы увидим его в Cydia.</p>\r\n',NULL,1,1),(17,'Расширь возможности Finder','<p>\r\n	Компанией BinaryAge была выпущена новая версия TotalFinder - небольшого плагина, добавляющего функциональности привычной Finder. основные возможности: работа с горячими клавишами, работа с вкладками, отображение папок в верхней части списка.</p>\r\n<p class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/03/totalfinder01_00.jpg\"><img alt=\"\" class=\"alignnone size-full wp-image-372\" height=\"397\" src=\"http://www.macov.net/wp-content/uploads/2010/03/totalfinder01_00.jpg\" tit','<p>\r\n	Компанией BinaryAge была выпущена новая версия TotalFinder - небольшого плагина, добавляющего функциональности привычной Finder. основные возможности: работа с горячими клавишами, работа с вкладками, отображение папок в верхней части списка.</p>\r\n<p class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/03/totalfinder01_00.jpg\"><img alt=\"\" class=\"alignnone size-full wp-image-372\" height=\"397\" src=\"http://www.macov.net/wp-content/uploads/2010/03/totalfinder01_00.jpg\" title=\"totalfinder01_00\" width=\"600\" /></a></p>\r\n\r\n<p>\r\n	Вкладки выглядят как в браузере Chrome. Они позволяют увеличить удобство работы между папками. Системные требования: Mac OS X 10.6 и выше. официальный сайт:<a href=\"http://www.binaryage.com/\"> http://www.binaryage.com/</a></p>\r\n',NULL,1,1),(18,'Steam теперь и на Mac','<p style=\"text-align: justify;\">\r\n	Геймеры всего мира долго ждали этого, и вот свершилось, вчера, в разгар международного женского дня Valve анонсировала выход Steam под Mac OS. Клиент будет полноценный, как на Windows.</p>\r\n<p style=\"text-align: justify;\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/03/204931-valve_6.jpg\"><img alt=\"\" class=\"alignnone size-full wp-image-375\" height=\"215\" src=\"http://www.macov.net/wp-content/uploads/2010/03/204931-valve_6.jpg\" title=\"204931-valve_6\" w','<p style=\"text-align: justify;\">\r\n	Геймеры всего мира долго ждали этого, и вот свершилось, вчера, в разгар международного женского дня Valve анонсировала выход Steam под Mac OS. Клиент будет полноценный, как на Windows.</p>\r\n<p style=\"text-align: justify;\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/03/204931-valve_6.jpg\"><img alt=\"\" class=\"alignnone size-full wp-image-375\" height=\"215\" src=\"http://www.macov.net/wp-content/uploads/2010/03/204931-valve_6.jpg\" title=\"204931-valve_6\" width=\"1024\" /></a></p>\r\n\r\n<p style=\"text-align: justify;\">\r\n	Клиент появится в общем доступе в апреле. Сразу будут доступны такие игры как Team Fortress 2, Counter-Strike, Left 4 dead 2, и все части Half-Life. В дальнейшем же компания будет выпускать игры как для Windows так и для Mac. Что примечательно, теперь, при покупке версии для одной платформы, вы сможете играть и на других платформах, абсолютно бесплатно! А при помощи Steam Cloud вы сможете получить свои сохранения везде, где бы вы ни были, на какой бы платформе не играли!</p>\r\n',NULL,1,1),(19,'Обновление GraphicConverter X','<p style=\"text-align: justify;\">\r\n	<strong>Lemke Software </strong>выпустила новую версию <strong>GraphicConverter X 6.7</strong>, в которой они расширили список поддерживаемых форматов. GraphicConverter X известен практически всем, кто хоть как то связан с графикой. Это приложение является незаменимым когда дело косается конвертирования графики в различные форматы. Сейчас <strong>GraphicConverter X</strong> способен открывать графику 200 форматов, и конвертировать их в любой из 80; Дает возможн','<p style=\"text-align: justify;\">\r\n	<strong>Lemke Software </strong>выпустила новую версию <strong>GraphicConverter X 6.7</strong>, в которой они расширили список поддерживаемых форматов. GraphicConverter X известен практически всем, кто хоть как то связан с графикой. Это приложение является незаменимым когда дело косается конвертирования графики в различные форматы. Сейчас <strong>GraphicConverter X</strong> способен открывать графику 200 форматов, и конвертировать их в любой из 80; Дает возможность редактирования, и поддерживает работу с плагинами от Photoshop и много других полезных возможностей.</p>\r\n<p class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/03/graphicconverter00.jpg\"><img alt=\"\" class=\"alignnone size-full wp-image-386\" height=\"369\" src=\"http://www.macov.net/wp-content/uploads/2010/03/graphicconverter00.jpg\" title=\"graphicconverter00\" width=\"500\" /></a></p>\r\n\r\n<p style=\"text-align: justify;\">\r\n	В новой версии добавлена возможность работы с HMR, SVG, DM2, DM3; добавлена поддержка Unicode; исправлено множество ошибок. Системные требования: Mac OS X 10.4.4 и выше.</p>\r\n<p style=\"text-align: justify;\">\r\n	Официальный сайт:<a href=\"http://www.lemkesoft.com/\"> http://www.lemkesoft.com/</a></p>\r\n',NULL,1,1),(20,'Выходит Maya 2011 для MacOSX','<p class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/03/maya_2011_box.jpg\"><img alt=\"\" class=\"alignleft size-full wp-image-391\" height=\"158\" src=\"http://www.macov.net/wp-content/uploads/2010/03/maya_2011_box.jpg\" title=\"maya_2011_box\" width=\"90\" /></a></p>\r\n<p style=\"text-align: justify;\">\r\n	Вчера Autodesk анонсировала новую версию известного приложения для создания 3д анимации, и рендеринга - Maya 2011. Новая версия будет иметь лучший интерфейс, и и получит поддержку 64','<p>&nbsp;</p>\r\n<div>&lt;p class=\"rtecenter\"&gt;</div>\r\n<div>&lt;a href=\"http://www.macov.net/wp-content/uploads/2010/03/maya_2011_box.jpg\"&gt;&lt;img alt=\"\" class=\"alignleft size-full wp-image-391\" height=\"158\" src=\"http://www.macov.net/wp-content/uploads/2010/03/maya_2011_box.jpg\" title=\"maya_2011_box\" width=\"90\" /&gt;&lt;/a&gt;&lt;/p&gt;</div>\r\n<div>&lt;p style=\"text-align: justify;\"&gt;</div>\r\n<div>Вчера Autodesk анонсировала новую версию известного приложения для создания 3д анимации, и рендеринга - Maya 2011. Новая версия будет иметь лучший интерфейс, и и получит поддержку 64 битных систем, что хорошо скажется на его производительности.&lt;/p&gt;</div>\r\n<div>\r\n	<div>&nbsp;</div>\r\n	<div>&lt;p style=\"text-align: justify;\"&gt;</div>\r\n	<div>Подробнее обо всех улучшениях и нововведениях Maya 2011 вы сможете ознакомиться на &lt;a href=\"http://area.autodesk.com/maya2011/features\"&gt;официальном сайте Autodesk&lt;/a&gt;.&lt;/p&gt;</div>\r\n	<div>&lt;p style=\"text-align: justify;\"&gt;</div>\r\n	<div>Для работы нового Maya 2011 потребуется Mac OS X не ниже 10.6.2.&lt;/p&gt;</div>\r\n	<div>&nbsp;</div>\r\n</div>\r\n<p>&nbsp;</p>\r\n',NULL,1,1),(21,'Migmoco готовит 6 игр для iPad','<p style=\"text-align: justify;\">\r\n	Уже к началу продаж iPad компания <strong>Nigmoco </strong>готовит сразу 6 игр. Пока известны названия лишь двух игр - стратегии <strong>Warp Gate</strong>, и <strong>шутера Dropship</strong>. В списке игр, выпущенных Nigmoco такие, как Star Defence, Rolando 1 и 2, Topple 1 и 2, и множества других.</p>\r\n<p class=\"rtecenter\">\r\n	<object classid=\"clsid:d27cdb6e-ae6d-11cf-96b8-444553540000\" codebase=\"http://download.macromedia.com/pub/shockwave/cabs/flash/swflash.c','<p style=\"text-align: justify;\">\r\n	Уже к началу продаж iPad компания <strong>Nigmoco </strong>готовит сразу 6 игр. Пока известны названия лишь двух игр - стратегии <strong>Warp Gate</strong>, и <strong>шутера Dropship</strong>. В списке игр, выпущенных Nigmoco такие, как Star Defence, Rolando 1 и 2, Topple 1 и 2, и множества других.</p>\r\n<p class=\"rtecenter\">\r\n	<object classid=\"clsid:d27cdb6e-ae6d-11cf-96b8-444553540000\" codebase=\"http://download.macromedia.com/pub/shockwave/cabs/flash/swflash.cab#version=6,0,40,0\" height=\"385\" width=\"640\"><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowScriptAccess\" value=\"always\" /><param name=\"src\" value=\"http://www.youtube.com/v/RN1bSJT4Sko&amp;color1=0xb1b1b1&amp;color2=0xcfcfcf&amp;hl=ru_RU&amp;feature=player_embedded&amp;fs=1\" /><param name=\"allowfullscreen\" value=\"true\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"385\" src=\"http://www.youtube.com/v/RN1bSJT4Sko&amp;color1=0xb1b1b1&amp;color2=0xcfcfcf&amp;hl=ru_RU&amp;feature=player_embedded&amp;fs=1\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n\r\n<p style=\"text-align: justify;\">\r\n	От других разработчиков еще мало информации, но мы считаем, что с приближением дня начала продаж резко возрастут аналогичные анонсы игр и приложений.</p>\r\n',NULL,1,1),(22,'Обновление iMovie и Aperture','<p class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/03/aperture301_20100224142523.jpg\"><img alt=\"\" class=\"alignleft size-full wp-image-408\" height=\"269\" src=\"http://www.macov.net/wp-content/uploads/2010/03/aperture301_20100224142523.jpg\" title=\"aperture301_20100224142523\" width=\"300\" /></a></p>\r\n<p style=\"text-align: justify;\">\r\n	Последнее время практически каждую пятницу компания Apple выпускает обновления для своих продуктов. И эта пятница не стала исключением - Купер','\r\n<p style=\"text-align: justify;\">\r\n	В iMovie повышена стабильность и надежность. Обновление рекомендуется для установки.</p>\r\n<p style=\"text-align: justify;\">\r\n	Что же косается Aperture - здесь список изменений и улучшений намного шире. Подробнее об изменениях можете прочитать <a href=\"http://images.apple.com/downloads/macosx/apple/application_updates/aperture302.html\">здесь</a>. В целом поднят уровень стабильности работы приложения при работе с библиотеками, кривыми и инструментами.</p>\r\n<p class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/03/imovie03_00.jpg\"><img alt=\"\" class=\"size-full wp-image-407 alignnone\" height=\"348\" src=\"http://www.macov.net/wp-content/uploads/2010/03/imovie03_00.jpg\" title=\"imovie03_00\" width=\"558\" /></a></p>\r\n<p style=\"text-align: justify;\">\r\n	Системные требования: Mac OS X 10.5.8 и выше. Обновление iMovie 8.0.6 можете скачать <a href=\"http://www.macupdate.com/download.php/5662/iMovie_806.dmg\">Здесь</a>. (35 mb) Обновление Aperture 3.0.2 можете скачать <a href=\"http://www.macupdate.com/download.php/19717/Aperture3.0.2Update.dmg\">Здесь</a>. (69 mb)</p>\r\n',NULL,1,1),(23,'Apple будет продавать билеты','<p style=\"text-align: left;\">\r\n	Ни для кого не секрет, что компания Apple старается всеми силами проникнуть во все сферы общества. Новшество &quot;Concert Ticket +&quot; делает небольшую революцию в области развлечений, а именно продаже билетов. Больше никаких очередей!</p>\r\n<p class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/04/1270727536992.jpg\"><img alt=\"\" class=\"aligncenter size-full wp-image-428\" height=\"377\" src=\"http://www.macov.net/wp-content/uploads/2010/04/127','<p style=\"text-align: left;\">\r\n	Ни для кого не секрет, что компания Apple старается всеми силами проникнуть во все сферы общества. Новшество &quot;Concert Ticket +&quot; делает небольшую революцию в области развлечений, а именно продаже билетов. Больше никаких очередей!</p>\r\n<p class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/04/1270727536992.jpg\"><img alt=\"\" class=\"aligncenter size-full wp-image-428\" height=\"377\" src=\"http://www.macov.net/wp-content/uploads/2010/04/1270727536992.jpg\" title=\"Ticket+\" width=\"600\" /></a></p>\r\n\r\n<p style=\"text-align: left;\">\r\n	Запатентованная Apple технология &quot;Concert Ticket +.&quot; будет совмещена с iTunes и позволит пользователям приобретать билеты на различного рода мероприятия. Особенностью данного способа является использование самого i-устройства, как билета. В патенте Эппл описывается, что электронный билет может включать в себя множество дисконтов и приятных радостей, таких как возможность аудиозаписи концерта, дополнительные скидки в баре, возможность загрузить эксклюзивные интервью с артистами, карты мероприятий и другой электронный контент.</p>\r\n<p>\r\n	Надпись WWDC на билете намекает нам, что скоре всего новинка будет представлена на Apple&#39;s World Wide Developer Conference 2010. Предположительные даты 28 июня - 2 июля.</p>\r\n',NULL,1,1),(24,'Еще патент','<p>\r\n	Еще один патент Apple, позволит iPhone заменить собой банковские карты.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" class=\"aligncenter\" height=\"395\" src=\"http://www.patentlyapple.com/.a/6a0120a5580826970c01347fb82801970c-800wi\" title=\"Pop pattent\" width=\"455\" /></p>\r\n','<p>&nbsp;</p>\r\n<div>&lt;p&gt;</div>\r\n<div>Еще один патент Apple, позволит iPhone заменить собой банковские карты.&lt;/p&gt;</div>\r\n<div>&lt;p style=\"text-align: center;\"&gt;</div>\r\n<div>&lt;img alt=\"\" class=\"aligncenter\" height=\"395\" src=\"http://www.patentlyapple.com/.a/6a0120a5580826970c01347fb82801970c-800wi\" title=\"Pop pattent\" width=\"455\" /&gt;&lt;/p&gt;</div>\r\n<div>\r\n	<div>&nbsp;</div>\r\n	<div>&lt;p&gt;</div>\r\n	<div>Речь идет о приложении для совершения покупок с помощью айфона. Поможет в этом приложение POP, что в переводе на русский - ККМ&lt;em&gt; (контрольно-кассовая машина)&lt;/em&gt;. Приложение позволит считывать отпечаток пальца для идентификации владельца, расширенные возможности фронтальной камеры и сканера штрихкодов помогут самому &amp;quot;пробить&amp;quot; товары еще до кассы.&lt;/p&gt;</div>\r\n	<div>&nbsp;</div>\r\n</div>\r\n<p>&nbsp;</p>\r\n',NULL,1,1),(25,'По полочкам','<p style=\"text-align: left;\">\r\n	Кратко, для спешащих и ленивых.</p>\r\n<ul>\r\n	<li>\r\n		Adobe больше не разрабатывает свои продукты для iPhone.</li>\r\n	<li>\r\n		Немцы приглашают на пиво тестера, оставившего в баре прототип iPhone 4G.</li>\r\n	<li>\r\n		Apple может приобрести ARM, главного производителя процессоров для iPad/iPhone/iPod. <em> </em>Apple собирается выложить 8 миллиардов, чтобы не только самим снабжать себя процессорами. ARM также производит чипы для Palm и для некоторых производителей androi','<p style=\"text-align: left;\">\r\n	Кратко, для спешащих и ленивых.</p>\r\n<ul>\r\n	<li>\r\n		Adobe больше не разрабатывает свои продукты для iPhone.</li>\r\n	<li>\r\n		Немцы приглашают на пиво тестера, оставившего в баре прототип iPhone 4G.</li>\r\n	<li>\r\n		Apple может приобрести ARM, главного производителя процессоров для iPad/iPhone/iPod. <em> </em>Apple собирается выложить 8 миллиардов, чтобы не только самим снабжать себя процессорами. ARM также производит чипы для Palm и для некоторых производителей android устройств.</li>\r\n	<li>\r\n		Android портировали на iPhone, правда не очень понятно зачем.</li>\r\n	<li>\r\n		Стив рекоммендует Android любителям порно.<em> И отказывается размещать его в iTunes Store. </em></li>\r\n	<li>\r\n		Производители автомобильных медиацентров волнуются. Ведь 500 долларовый айпад, соединенный с аудиосистемой может &quot;убить&quot; любой их продукт.</li>\r\n	<li>\r\n		Новый MacBook Pro стал еще круче, мощнее и дороже. <em>Теперь i7</em></li>\r\n	<li>\r\n		Общественность задумалась, а не пиар ли эта ваша &quot;потеря&quot; нового айфона?</li>\r\n</ul>\r\n',NULL,1,1),(26,'Ноутбук за 10 миллионов.','<p>\r\n	Все знают, что ноутбуки Apple дорогие.</p>\r\n<p>\r\n	Все знают, что ноутбуки Apple дорогие. Теперь они самые дорогие. MacBook Air, покрытый 24 каратами золота и инкрустированный 25.5 каратами бриллиантов обойдется вам в $340 000 (9 миллионов 900 тысяч рублей).</p>\r\n<p>\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/04/macairsun-front.jpg\"><img alt=\"\" class=\"alignright size-thumbnail wp-image-470\" height=\"150\" src=\"http://www.macov.net/wp-content/uploads/2010/04/macairsun-front-150x15','<p>\r\n	Все знают, что ноутбуки Apple дорогие.</p>\r\n<p>\r\n	Все знают, что ноутбуки Apple дорогие. Теперь они самые дорогие. MacBook Air, покрытый 24 каратами золота и инкрустированный 25.5 каратами бриллиантов обойдется вам в $340 000 (9 миллионов 900 тысяч рублей).</p>\r\n<p>\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/04/macairsun-front.jpg\"><img alt=\"\" class=\"alignright size-thumbnail wp-image-470\" height=\"150\" src=\"http://www.macov.net/wp-content/uploads/2010/04/macairsun-front-150x150.jpg\" title=\"macairsun-front\" width=\"150\" /></a></p>\r\n\r\n<p>\r\n	Но если бюджет не позволяет, запросто можете подобрать себе платиновую модель всего за $216 000 (6 миллионов 290 тысяч рублей) А еще за эти деньги можно купить Lamborgini Gallardo либо соответственно BMW X6 максимальной комплектации. Фото далее.</p>\r\n<p>\r\n<!--more-->	<a href=\"http://www.macov.net/wp-content/uploads/2010/04/macairsun-back.jpg\"><img alt=\"\" class=\"alignnone size-full wp-image-469\" height=\"338\" src=\"http://www.macov.net/wp-content/uploads/2010/04/macairsun-back.jpg\" title=\"macairsun-back\" width=\"438\" /></a></p>\r\n<p>\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/04/macair-supreme-inside.jpg\"><img alt=\"\" class=\"alignnone size-full  wp-image-471\" height=\"340\" src=\"http://www.macov.net/wp-content/uploads/2010/04/macair-supreme-inside.jpg\" title=\"macair-supreme-inside\" width=\"440\" /></a></p>\r\n',NULL,1,1),(27,'iDobavki','<p style=\"text-align: center;\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/04/534189.jpg\"><img alt=\"\" class=\"size-full wp-image-478  aligncenter\" height=\"226\" src=\"http://www.macov.net/wp-content/uploads/2010/04/534189.jpg\" title=\"534189\" width=\"150\" /></a></p>\r\n<p>\r\n	Программа Украинских разработчиков хочет помочь нам в выборе еды.Позиционируется, как справочник добавок &quot;E&quot;, а попросту эмульгаторов. За $0.99 вы получите возможность в любой момент глянуть, каким ракообразу','<p style=\"text-align: center;\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/04/534189.jpg\"><img alt=\"\" class=\"size-full wp-image-478  aligncenter\" height=\"226\" src=\"http://www.macov.net/wp-content/uploads/2010/04/534189.jpg\" title=\"534189\" width=\"150\" /></a></p>\r\n<p>\r\n	Программа Украинских разработчиков хочет помочь нам в выборе еды.Позиционируется, как справочник добавок &quot;E&quot;, а попросту эмульгаторов. За $0.99 вы получите возможность в любой момент глянуть, каким ракообразующим или иным вредным ингредиентом вы себя травите, попивая прохладный шипящий напиток. <!--more-->Выбор параноика, сохрани мегабайты траффика и драгоценные минуты поиска всего за один доллар. Другое дело, везде есть эмульгаторы, так что нужно еще решить для себя: &quot;а все ли я хочу знать?&quot;.</p>\r\n\r\n<p class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/04/iDobavki-4-180x270.jpg\"><img alt=\"\" class=\"size-full wp-image-480  alignleft\" height=\"270\" src=\"http://www.macov.net/wp-content/uploads/2010/04/iDobavki-4-180x270.jpg\" title=\"iDobavki-4-180x270\" width=\"180\" /></a><a href=\"http://www.macov.net/wp-content/uploads/2010/04/iDobavki-2-180x270.jpg\"><img alt=\"\" class=\"size-full wp-image-479    alignleft\" height=\"270\" src=\"http://www.macov.net/wp-content/uploads/2010/04/iDobavki-2-180x270.jpg\" title=\"iDobavki-2-180x270\" width=\"180\" /></a></p>\r\n',NULL,1,1),(28,'На вручении Гремми был замечен Apple iPad','<p>\r\n	Совсем недавно состоялась церемония вручения премии Grammy, на которой был замечен свеже анонсированный Apple iPad! Ведущий Стивен Кольбер, называя лучшую песню года, достал из кармана пиджака новый гаджет. Не обошлось и без подколки в адрес известного репера Jay-Z</p>\r\n<div id=\"_mcePaste\">\r\n	&laquo;Извините, не могу найти список. О, нашел! Он в моем iPad. А у тебя есть такой, Jay-z? Получается, я круче тебя? Любимая, ведь я же круче него?&raquo;</div>\r\n<div>\r\n	<object classid=\"clsid:d27cd','<p>\r\n	Совсем недавно состоялась церемония вручения премии Grammy, на которой был замечен свеже анонсированный Apple iPad! Ведущий Стивен Кольбер, называя лучшую песню года, достал из кармана пиджака новый гаджет. Не обошлось и без подколки в адрес известного репера Jay-Z</p>\r\n<div id=\"_mcePaste\">\r\n	&laquo;Извините, не могу найти список. О, нашел! Он в моем iPad. А у тебя есть такой, Jay-z? Получается, я круче тебя? Любимая, ведь я же круче него?&raquo;</div>\r\n<div>\r\n	<object classid=\"clsid:d27cdb6e-ae6d-11cf-96b8-444553540000\" codebase=\"http://download.macromedia.com/pub/shockwave/cabs/flash/swflash.cab#version=6,0,40,0\" height=\"344\" width=\"425\"> <param name=\"allowFullScreen\" value=\"true\" /> <param name=\"allowscriptaccess\" value=\"always\" /> <param name=\"src\" value=\"http://www.youtube.com/v/ULOynUb6vAM&amp;hl=ru_RU&amp;fs=1&amp;\" /> <param name=\"allowfullscreen\" value=\"true\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"344\" src=\"http://www.youtube.com/v/ULOynUb6vAM&amp;hl=ru_RU&amp;fs=1&amp;\" type=\"application/x-shockwave-flash\" width=\"425\"></embed></object></div>\r\n',NULL,1,1),(29,'Мнение людей об iPad','<p>\r\n	Вот уже прошло 2 недели с момента анонса iPad, и многое с тех пор изменилось. Больше всего изменились мнения людей. Агенство Retrovo провело опрос до и после анонса планшетника.</p>\r\n<p>\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/02/chart02_00.jpg\"><img alt=\"\" class=\"aligncenter size-full wp-image-183\" height=\"298\" src=\"http://www.macov.net/wp-content/uploads/2010/02/chart02_00.jpg\" title=\"chart02_00\" width=\"620\" /></a></p>\r\n','<p>\r\n	Вот уже прошло 2 недели с момента анонса iPad, и многое с тех пор изменилось. Больше всего изменились мнения людей. Агенство Retrovo провело опрос до и после анонса планшетника.</p>\r\n<p>\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/02/chart02_00.jpg\"><img alt=\"\" class=\"aligncenter size-full wp-image-183\" height=\"298\" src=\"http://www.macov.net/wp-content/uploads/2010/02/chart02_00.jpg\" title=\"chart02_00\" width=\"620\" /></a></p>\r\n\r\n<p>\r\n	И как мы видим - желание приобрести iPad отпало у большого процента опрошенных. И больше половины опрошенных после анонса твердо указали, что айпад в списке покупок не значится. Это значит что народ разочаровался в устройстве, либо же еще не поняли люди замысла инженеров компании Apple. Но, возможно, после выхода iPad на рынок ситуация изменится?</p>\r\n',NULL,1,1),(30,'Презентация Apple сегодня в 21.00 LIVE','<p>&nbsp;</p>\r\n<p>Презентация, которая состоится сегодня в Сан-Хосе в 21:00 по московскому времени.&nbsp;Самый оперативный способ узнать о том, что представит сегодня&nbsp;яблочники это наша <a href=\"http://vk.com/macov_net\">группа Вконтакте</a> и <a href=\"https://twitter.com/macov_net\">наш твиттер</a>.</p>\r\n<p>Сегодня вечером Apple официально представит уменьшенную версию iPad&nbsp;— iPad Mini. Как пишет The Wall Street Journal, сегодняшний анонс — шанс для компании доказать, что она по-прежнем','<p>Презентация, которая состоится сегодня в Сан-Хосе в 21:00 по московскому времени.&nbsp;Самый оперативный способ узнать о том, что представит сегодня&nbsp;яблочники это наша <a href=\"http://vk.com/macov_net\">группа Вконтакте</a> и <a href=\"https://twitter.com/macov_net\">наш твиттер</a>.</p>\r\n<p>Сегодня вечером Apple официально представит уменьшенную версию iPad&nbsp;— iPad Mini. Как пишет The Wall Street Journal, сегодняшний анонс — шанс для компании доказать, что она по-прежнему является лидером отрасли в технологических инновациях.</p>\r\n<p><img alt=\"\" src=\"/sites/default/files/1350543790_apple-ipad-mini-jpg.jpg\" style=\"width: 637px; height: 511px; \"></p>\r\n<p>Слухи об iPad mini начали появляться уже давно, а к настоящему моменту, благодаря многочисленным утечкам от производственных партнеров Apple, о \"младшем брате\" iPad известно почти все. Диагональ экрана устройства составит 7,85 дюйма (у обычного iPad — 9,7 дюйма), а корпус будет не только меньше, но и тоньше, и наверняка легче. Для синхронизации с компьютером, зарядки батареи и подключения к аксессуарам новинка будет использовать разъем Lightning, впервые появившийся в iPhone 5.</p>\r\n<p>Кстати, о ценах — если последние месяцы от Apple ждали ценника на базовую модель iPad mini в США в районе 250 долларов, то на прошлой неделе появилась другая информация. Якобы компактный планшетник с 16 гигабайтами встроенной памяти и без модуля сотовой связи обойдется в 330 долларов.<br style=\"font-family: tahoma, arial, verdana, sans-serif, \'Lucida Sans\'; font-size: 11px; line-height: 14px; \">\r\n	Многие предполагают, что презентацией одного только iPad mini сегодняшнее мероприятие в Сан-Хосе не ограничится. От Apple ждут новой версии \"большого\" iPad со слегка улучшенными характеристиками и новым разъемом Lightning, а также обновления значительной части линейки компьютеров Mac.</p>\r\n<p>Вы уже можете сделать <a href=\"http://macov.net/kupit-ipad-mini\">предзаказ на iPad Mini и получить его первым</a>!</p>\r\n',NULL,1,1),(31,'ВИДЕО: Официальный промо-ролик о iPad Mini.','Официальный промо-ролик о iPad Mini.\r\n<iframe width=\"560\" height=\"315\" src=\"http://www.youtube.com/embed/TPXh-CYKRk4\" frameborder=\"0\" allowfullscreen></iframe>','<iframe width=\"560\" height=\"315\" src=\"http://www.youtube.com/embed/TPXh-CYKRk4\" frameborder=\"0\" allowfullscreen></iframe>',NULL,3,1),(32,'iBooks 3.0','<p>&nbsp;</p>\r\n<div>На вчерашней презентации Apple представила обновление своей программы-читалки iBooks.&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://a1635.phobos.apple.com/us/r1000/072/Purple/v4/ba/eb/8e/baeb8e1f-6aec-6f2b-76c1-65dd87b65ffa/mzl.sliysfqf.480x480-75.jpg\" style=\"width: 480px; height: 351px; \"></div>','<p><strong><span style=\"font-size:16px;\">На вчерашней презентации Apple представила обновление своей программы-читалки iBooks.&nbsp;</span></strong></p>\r\n<div><img alt=\"\" src=\"http://a1635.phobos.apple.com/us/r1000/072/Purple/v4/ba/eb/8e/baeb8e1f-6aec-6f2b-76c1-65dd87b65ffa/mzl.sliysfqf.480x480-75.jpg\" style=\"width: 480px; height: 351px; \"></div>\r\n<div>В новой версии 3.0 доступны новые функции:</div>\r\n<ul style=\"margin-left: 40px; \">\r\n	<li>Поддержка iCloud;</li>\r\n	<li>Бесконечная прокрутка текста;</li>\r\n	<li>Поддержка Twitter, Facebook и другие возможности;</li>\r\n	<li>Добавлены корейский, китайский и японский языки.</li>\r\n</ul>\r\n<p>Новая версия iBooks 3.0 уже доступна для скачивания в <a href=\"https://itunes.apple.com/ru/app/ibooks/id364709193?mt=8\">App Store</a>.</p>\r\n<div><img alt=\"\" src=\"http://a1902.phobos.apple.com/us/r1000/061/Purple/v4/ab/69/42/ab6942cb-4b6f-b57c-378d-52281c23f432/mzl.gqrlfpys.480x480-75.jpg\" style=\"width: 350px; height: 458px; \">&nbsp;<img alt=\"\" src=\"http://a1861.phobos.apple.com/us/r1000/092/Purple/v4/45/a4/e6/45a4e645-48ce-532a-91ed-131cffbc64ac/mzl.ayjqmadc.480x480-75.jpg\" style=\"width: 350px; height: 458px; \"></div>\r\n<div>Помимо всего этого появилась новая функция для авторов книг, которые с сегодняшнего дня могут обновлять свои книги, а обновления вы можете скачать прямо в iBooks. Обновлять свои книги авторы могут через новую версию приложения <a href=\"https://itunes.apple.com/ru/app/ibooks-author/id490152466?ls=1&amp;mt=12\">iBooks Author</a>, которую Apple также обновила на вчерашней презентации. Теперь авторам доступны новые шаблоны и возможность использовать собственные шрифты.</div>\r\n<div><img alt=\"\" src=\"http://images.apple.com/ru/ibooks-author/images/hero.jpg\" style=\"width: 700px; height: 513px; \"></div>\r\n<div><img alt=\"\" src=\"http://images.apple.com/euro/ibooks-author/images/customize.png\" style=\"height: 484px; width: 700px; \"></div>\r\n',NULL,4,1),(33,'MacBook Pro 13\" Retina','<p><strong style=\"font-size: 16px; \">Новый MacBook Pro 13\" Retina.</strong></p>\r\n<div>\r\n	<div>Вчера нам представили совершенно новый MacBook Pro 13\" Retina. Разрешение экрана для 13 дюймов на столько большое, что вы не сможете увидеть пиксели на нем, благодаря технологии IPS на 75% меньше бликов. Приложения выглядят просто великолепно.</div>\r\n	<div>Это самый легкий MacBook Pro.</div>\r\n	<div>&nbsp;</div>\r\n</div>\r\n<div><span style=\"font-size:16px;\"><strong><img alt=\"\" src=\"http://i-ekb.ru/wp-conte','<p><strong style=\"font-size: 16px; \">Новый MacBook Pro 13\" Retina.</strong></p>\r\n<div>\r\n	<div>Вчера нам представили совершенно новый MacBook Pro 13\" Retina. Разрешение экрана для 13 дюймов на столько большое, что вы не сможете увидеть пиксели на нем, благодаря технологии IPS на 75% меньше бликов. Приложения выглядят просто великолепно.</div>\r\n	<div>Это самый легкий MacBook Pro.</div>\r\n	<div>&nbsp;</div>\r\n</div>\r\n<div><span style=\"font-size:16px;\"><strong><img alt=\"\" src=\"http://i-ekb.ru/wp-content/uploads/2012/10/d3s6144-1351016972.jpeg\" style=\"width: 700px; height: 466px; \"></strong></span></div>\r\n<div>&nbsp;</div>\r\n<div><strong>Характеристики:</strong></div>\r\n<div>&nbsp;</div>\r\n<ul>\r\n	<li style=\"margin-left: 40px; \">IPS Retina-дисплей с разрешением 2,560 x 1,600 пикселей;</li>\r\n	<li style=\"margin-left: 40px; \">Процессоры Core i5 и Core i7 Ivy Bridge;</li>\r\n	<li style=\"margin-left: 40px; \">8Гб RAM;</li>\r\n	<li style=\"margin-left: 40px; \">SSD до 768 Гб;</li>\r\n	<li style=\"margin-left: 40px; \">Самый легкий MacBook Pro (весит 3,5 фунта) и на 20% тоньше 15″ MacBook Pro;</li>\r\n	<li style=\"margin-left: 40px; \">Нет оптического привода, нет дискретной графики;</li>\r\n	<li style=\"margin-left: 40px; \">Камера FaceTime HD, два микрофона, стерео динамики, Bluetooth 4.0;</li>\r\n	<li style=\"margin-left: 40px; \">Порты MagSafe 2, 2хThunderbolts, SD card, HDMI и 2хUSB 3.0.</li>\r\n	<li style=\"margin-left: 40px; \">Новые MacBook Pro 13\" Retina уже доступен для продаже по цене от $1699.</li>\r\n</ul>\r\n<p style=\"margin-left: 40px; \">&nbsp;</p>\r\n<p><img alt=\"\" src=\"http://i-ekb.ru/wp-content/uploads/2012/10/d3s6145-1351016974.jpeg\" style=\"width: 700px; height: 466px; \"></p>\r\n<p><img alt=\"\" src=\"http://i-ekb.ru/wp-content/uploads/2012/10/d3s6147-1351016976.jpeg\" style=\"width: 700px; height: 466px; \"></p>\r\n<p><img alt=\"\" src=\"http://i-ekb.ru/wp-content/uploads/2012/10/d3s6158-1351016990.jpeg\" style=\"width: 700px; height: 466px; \"></p>\r\n',NULL,3,1),(34,'Новый Mac Mini','<p>&nbsp;</p>\r\n<div><span style=\"font-size:16px;\"><strong>Итак, новый Mac Mini.</strong></span></div>\r\n<div>Внешне Mac mini никак не изменился, да и внутри произошли не сильные изменения.</div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://images.apple.com/mac-mini/images/overview_hero_desk_1.jpg\" style=\"width: 700px; height: 352px; \"></div>\r\n<div><img alt=\"\" src=\"http://images.apple.com/mac-mini/images/overview_hero.jpg\" style=\"width: 693px; height: 278px; \"></div>\r\n<div>&nbsp;</div>','<p>&nbsp;</p>\r\n<div><span style=\"font-size:16px;\"><strong>Итак, новый Mac Mini.</strong></span></div>\r\n<div>Внешне Mac mini никак не изменился, да и внутри произошли не сильные изменения.</div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://images.apple.com/mac-mini/images/overview_hero_desk_1.jpg\" style=\"width: 700px; height: 352px; \"></div>\r\n<div><img alt=\"\" src=\"http://images.apple.com/mac-mini/images/overview_hero.jpg\" style=\"width: 693px; height: 278px; \"></div>\r\n<div>&nbsp;</div>\r\n<div><strong>Характеристики:&nbsp;</strong></div>\r\n<div>&nbsp;</div>\r\n<ul>\r\n	<li style=\"margin-left: 40px; \">Процессоры Core i5 и Core i7 Ivy Bridge;</li>\r\n	<li style=\"margin-left: 40px; \">4Гб RAM, расширение до 16 ГБ;</li>\r\n	<li style=\"margin-left: 40px; \">HDD 500 Гб;</li>\r\n	<li style=\"margin-left: 40px; \">Нет оптического привода, нет дискретной графики;</li>\r\n	<li style=\"margin-left: 40px; \">USB 3.0, Thunderbolt и HDMI.</li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n<div><strong>Доступные конфигурации Mac mini:</strong></div>\r\n<div>&nbsp;</div>\r\n<ul>\r\n	<li style=\"margin-left: 40px; \">$599, 2.5 GHz Dual-Core Intel Core i5, 4GB RAM, 500GB HD</li>\r\n	<li style=\"margin-left: 40px; \">$799, 2.3 GHz Quad-Core Intel Core i5, 4GB RAM, 1TB HD</li>\r\n	<li style=\"margin-left: 40px; \">$999, 2.3 GHz Quad-Core Intel Core i5, 4GB RAM, 2TB HD с Mountain Lion Server</li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" src=\"http://cs419017.userapi.com/v419017523/1630/FLUkhEMSFAc.jpg\" style=\"width: 640px; height: 423px; \"></p>\r\n<p>&nbsp;</p>\r\n',NULL,1,1),(35,'Новый iMac','<p>&nbsp;</p>\r\n<div><span style=\"font-size:16px;\"><strong>Последний из обновленных маков вчера — iMac.&nbsp;</strong></span></div>\r\n<div>Новый iMac просто невероятно тонкий! Он на 80 % тоньше своих предшественников. Apple с новым iMac сделала не просто шаг вперед, а два-три! За $1299 вы просто не сможете найти настольный компьютер лучше, чем iMac. Это настоящий прорыв для Apple.</div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://i-ekb.ru/wp-content/uploads/2012/10/imachandson.jpeg\" style=\"wi','<p><strong style=\"font-size: 16px; \">Последний из обновленных маков вчера — iMac.&nbsp;</strong></p>\r\n<div>&nbsp;</div>\r\n<div>Новый iMac просто невероятно тонкий! Он на 80 % тоньше своих предшественников. Apple с новым iMac сделала не просто шаг вперед, а два-три! За $1299 вы просто не сможете найти настольный компьютер лучше, чем iMac. Это настоящий прорыв для Apple.</div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://i-ekb.ru/wp-content/uploads/2012/10/imachandson.jpeg\" style=\"width: 620px; height: 413px; \"></div>\r\n<div>&nbsp;</div>\r\n<div><strong>Характеристики:&nbsp;</strong></div>\r\n<div>Две диагонали дисплеев 21,5 и 27 дюйма</div>\r\n<div>IPS-дисплей с разрешением 2,560 x 1,440 пикселей и 1,920 x 1,080 пикселей, новое антибликовое покрытие, на 75% меньше бликов;</div>\r\n<div>Процессоры Core i5 и Core i7 Ivy Bridge;</div>\r\n<div>До 32 Гб RAM;</div>\r\n<div>SSD до 768 Гб или HDD до 3Тб, Apple Fusion Drive;</div>\r\n<div>Толщина края 5мм, на 80% тоньше, чем предыдущее поколение;</div>\r\n<div>Нет оптического привода;</div>\r\n<div>Nvidia GeForce GTX 640M или 660M;</div>\r\n<div>Камера FaceTime HD, два микрофона, стерео динамики, Bluetooth 4.0;</div>\r\n<div>Порты Ethernet, 2хThunderbolts, SD card и 4хUSB 3.0;</div>\r\n<div>&nbsp;</div>\r\n<div>По цене от $1299 за 21.5″ и $1799 за 27″ iMac будет доступен в декабре.</div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://i-ekb.ru/wp-content/uploads/2012/10/d3s6168.jpeg\" style=\"width: 700px; height: 466px; \"></div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://i-ekb.ru/wp-content/uploads/2012/10/d3s6170.jpeg\" style=\"width: 700px; height: 466px; \"></div>\r\n<div>&nbsp;</div>\r\n<div>Небольшое разочарование только в том, что на сайте Apple заявлено, что добавлять самостоятельно оперативную память можно только в 27″ модель нового iMac. Да, обе модели по умолчанию имеют 8Гб (2 планки по 4Гб), но только в iMac 27″ можно самому поставить память в четыре существующих слота, два из которых, напоминаю, заняты 8Гб памятью.</div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://i-ekb.ru/wp-content/uploads/2012/10/Screen-Shot-2012-10-23-at-2.30.45-PM.jpg\" style=\"width: 640px; height: 495px; \"></div>\r\n',NULL,1,1),(36,'Обновленный новый iPad 4-го поколения. ','<p><strong style=\"font-size: 16px; \">Обновленный новый iPad 4-го поколения.</strong></p>\r\n<div>&nbsp;</div>\r\n<div>Для многих это было неожиданным, то, что на вчерашней презентации Apple кроме всех ожидаемого iPad mini представила и обновленный iPad.</div>\r\n<div>Новый iPad четвертого поколения лишь немного обновили. iPad четвертого поколения стал еще быстрее и теперь работает до 10 часов!</div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://www.appleinsider.ru/wp-content/uploads/2012/10/a6x.jpg','<p><strong style=\"font-size: 16px; \">Обновленный новый iPad 4-го поколения.</strong></p>\r\n<div>&nbsp;</div>\r\n<div>Для многих это было неожиданным, то, что на вчерашней презентации Apple кроме всех ожидаемого iPad mini представила и обновленный iPad.</div>\r\n<div>Новый iPad четвертого поколения лишь немного обновили. iPad четвертого поколения стал еще быстрее и теперь работает до 10 часов!</div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://www.appleinsider.ru/wp-content/uploads/2012/10/a6x.jpg\" style=\"width: 650px; height: 302px; \"></div>\r\n<div>&nbsp;</div>\r\n<div><strong>Характеристики:&nbsp;</strong></div>\r\n<ul>\r\n	<li style=\"margin-left: 40px; \">Процессор А6Х, в два раза улучшает графическую производительность;</li>\r\n	<li style=\"margin-left: 40px; \">Retina-дисплей;</li>\r\n	<li style=\"margin-left: 40px; \">Батарея – 10 часов;</li>\r\n	<li style=\"margin-left: 40px; \">FaceTime HD;</li>\r\n	<li style=\"margin-left: 40px; \">Поддержка LTE по всему миру;</li>\r\n	<li style=\"margin-left: 40px; \">Lightning;</li>\r\n	<li style=\"margin-left: 40px; \">WiFi c поддержкой 5Ггц;</li>\r\n	<li style=\"margin-left: 40px; \">Два цвета: черный и белый;</li>\r\n</ul>\r\n<div>&nbsp;</div>\r\n<div>Цена от $499 и от $629.</div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://images.apple.com/ipad/specs/images/specs_size.jpg\" style=\"width: 700px; height: 314px; \"></div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://images.apple.com/ipad/specs/images/specs_buttons.jpg\" style=\"width: 700px; height: 220px; \"></div>\r\n<p>&nbsp;</p>\r\n',NULL,1,1),(37,'iPad mini','<p>&nbsp;</p>\r\n<div><span style=\"font-size:16px;\"><strong>Маленький, тонкий, невероятно легкий&nbsp;<span style=\"color: rgb(34, 34, 34); font-family: arial, sans-serif; line-height: 14.545454025268555px; \">—</span>&nbsp;iPad mini.</strong></span></div>\r\n<div>&nbsp;</div>\r\n<div>Его долго ждали, ходило много слухов и вот мы дождались.&nbsp;</div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://www.appleinsider.ru/wp-content/uploads/2012/10/126.png\" style=\"width: 650px; height: 413px; \"></div>\r\n<d','<p>&nbsp;</p>\r\n<div><span style=\"font-size:16px;\"><strong>Маленький, тонкий, невероятно легкий&nbsp;<span style=\"color: rgb(34, 34, 34); font-family: arial, sans-serif; line-height: 14.545454025268555px; \">—</span>&nbsp;iPad mini.</strong></span></div>\r\n<div>&nbsp;</div>\r\n<div>Его долго ждали, ходило много слухов и вот мы дождались.&nbsp;</div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://www.appleinsider.ru/wp-content/uploads/2012/10/126.png\" style=\"width: 650px; height: 413px; \"></div>\r\n<div>&nbsp;</div>\r\n<div>\r\n	<div><strong>Характеристики:</strong></div>\r\n	<ul>\r\n		<li style=\"margin-left: 40px; \">Процессор А5;</li>\r\n		<li style=\"margin-left: 40px; \">Дисплей 7.9 дюймов, разрешение 1024х768;</li>\r\n		<li style=\"margin-left: 40px; \">Батарея – 10 часов;</li>\r\n		<li style=\"margin-left: 40px; \">FaceTime HD, 5MP iSight;</li>\r\n		<li style=\"margin-left: 40px; \">Поддержка LTE по всему миру;</li>\r\n		<li style=\"margin-left: 40px; \">Lightning, для него доступны Smart Cover;</li>\r\n		<li style=\"margin-left: 40px; \">Толщина 7.2 мм (на 23% тоньше iPad 3), вес 0.86 унций</li>\r\n		<li style=\"margin-left: 40px; \">Два цвета: полностью черный и белый.</li>\r\n	</ul>\r\n	<div>&nbsp;</div>\r\n	<div>Цена варьируется в зависимости от конфигурации от $329 и от $459.&nbsp;Предзаказ стартует в пятницу, продажи WiFi-версии начнутся 2 ноября, в середине ноября начнутся продажи WiFi+4G.</div>\r\n	<div>&nbsp;</div>\r\n	<div><img alt=\"\" src=\"http://cs419017.userapi.com/v419017523/1738/X9ICRHfMmQY.jpg\" style=\"width: 640px; height: 423px; \"></div>\r\n	<div>&nbsp;</div>\r\n	<div>Apple уже заранее позаботилась о том, что пользователи могут случайно задевать пальцем экран на iPad mini. Из-за того, что у этого устройства очень тонкая рамка, кончики пальцев могут касаться экрана и запускать приложения или листать страницы. Однако благодаря отличной работе устройства с ее iOS, этого никогда не произойдет. Стало известно, что новое устройство от компании Apple iPad mini получило поддержку Siri.&nbsp;</div>\r\n</div>\r\n<p><img alt=\"\" src=\"http://images.apple.com/ipad-mini/overview/images/hero.jpg\" style=\"width: 526px; height: 646px; \"></p>\r\n<p>Защищать экран вашего новенького iPad mini будут специальные маленькие чехлы Smart Cover.</p>\r\n<p><img alt=\"\" src=\"http://www.appleinsider.ru/wp-content/uploads/2012/10/ipadmini_0630.jpeg\" style=\"width: 650px; height: 430px; \"></p>\r\n<p><img alt=\"\" src=\"http://images.apple.com/ipad-mini/specs/images/specs_color.jpg\" style=\"width: 677px; height: 267px; \"></p>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" src=\"http://images.apple.com/ipad-mini/specs/images/specs_buttons.jpg\" style=\"width: 666px; height: 219px; \"></p>\r\n',NULL,1,1),(38,'Как удалить стандартные иконки без джейлбрейка?','<p>&nbsp;</p>\r\n<div>Всем известно, что иконки стандартных приложений с рабочего стола устройства, работающего на iOS, нельзя. Многие выходят из этой ситуации создав папку с ненужными им стандартными приложениями.&nbsp;</div>\r\n<div>Сегодня мы расскажем вам как избавиться от поднадоевших иконок!&nbsp;</div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://cs419017.userapi.com/v419017523/1888/xZhrLIjq7jU.jpg\" style=\"width: 350px; height: 525px; \">&nbsp;<img alt=\"\" src=\"http://cs419017.userapi.com/v','<p>&nbsp;</p>\r\n<div>Всем известно, что иконки стандартных приложений с рабочего стола устройства, работающего на iOS, нельзя. Многие выходят из этой ситуации создав папку с ненужными им стандартными приложениями.&nbsp;</div>\r\n<div>Сегодня мы расскажем вам как избавиться от поднадоевших иконок!&nbsp;</div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://cs419017.userapi.com/v419017523/1888/xZhrLIjq7jU.jpg\" style=\"width: 350px; height: 525px; \">&nbsp;<img alt=\"\" src=\"http://cs419017.userapi.com/v419017523/1891/2tORPGbKeYM.jpg\" style=\"width: 350px; height: 525px; \"></div>\r\n<div>&nbsp;</div>\r\n<div>Эта возможность появилась всего пару дней и самое приятное, что работает она без использования джейлбрейка.</div>\r\n<div>&nbsp;</div>\r\n<div><strong>Итак, по порядку:&nbsp;</strong></div>\r\n<ul>\r\n	<li style=\"margin-left: 40px; \">Заходим с вашего гаджета на www.cydiahacks.com;</li>\r\n	<li style=\"margin-left: 40px; \">Выбираем нужную программу</li>\r\n	<li style=\"margin-left: 40px; \">На всплывающем сообщении нажимаем «Install»;</li>\r\n	<li style=\"margin-left: 40px; \">Нас выкидывает на рабочий стол и мы видим, что данное приложение застряло на загрузке;</li>\r\n	<li style=\"margin-left: 40px; \">Держим приложение пару секунд, чтобы иконки задрожали</li>\r\n	<li style=\"margin-left: 40px; \">Жмем на крестик и приложение удалено.</li>\r\n</ul>\r\n<div>&nbsp;</div>\r\n<div>К сожалению или к счастью, <strong>после перезагрузки устройства, все удаленные этим методом иконки вернутся на свои места</strong>. Никак по другому их не вернуть.</div>\r\n',NULL,2,1),(39,'Подробные схемы iPad mini и iPad 4.','<p><strong style=\"font-size: 16px; \">Подробные схемы iPad mini и iPad 4.</strong></p>\r\n<div>&nbsp;</div>\r\n<div>Компания Apple выложила подробные схемы своих новых устройств iPad mini и iPad 4, как это было с iPhone 5, iPod touch 5g и iPod nano 7g. Производители чехлов и и различных других аксессуаров уже вооружились этими схемами и начали штамповать свои продукты.</div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://i-ekb.ru/wp-content/uploads/2012/10/screen20shot202012-10-2520at2012-34-3520am','<p><strong style=\"font-size: 16px; \">Подробные схемы iPad mini и iPad 4.</strong></p>\r\n<div>&nbsp;</div>\r\n<div>Компания Apple выложила подробные схемы своих новых устройств iPad mini и iPad 4, как это было с iPhone 5, iPod touch 5g и iPod nano 7g. Производители чехлов и и различных других аксессуаров уже вооружились этими схемами и начали штамповать свои продукты.</div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://i-ekb.ru/wp-content/uploads/2012/10/screen20shot202012-10-2520at2012-34-3520am.jpg\" style=\"width: 600px; height: 463px; \"></div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://i-ekb.ru/wp-content/uploads/2012/10/screen20shot202012-10-2520at2012-35-1020am.jpg\" style=\"width: 600px; height: 388px; \"></div>\r\n<div>&nbsp;</div>\r\n<div>&nbsp;</div>\r\n',NULL,3,1),(40,'Отзывы про iPad mini, iMac, MacBook Pro 13″ Retina','<p><strong style=\"font-size: 16px; \">Первые отзывы про новинки Apple</strong></p>\r\n<div>&nbsp;</div>\r\n<div>Как обычно бывает, после завершения трансляции Apple показала репортерам интернет-порталов свои новые продукты. Дали посмотреть и потрогать новый MacBook Pro 13″ Retina, невероятно тонкий iMac, и, естественно, долгожданный iPad mini вместе с его мини Smart Cover. Большинство первых впечатлений и отзывов известных зарубежных яблочных порталов положительные!</div>\r\n<div>&nbsp;</div>\r\n<div><im','<p><strong style=\"font-size: 16px; \">Первые отзывы про новинки Apple</strong></p>\r\n<div>&nbsp;</div>\r\n<div>Как обычно бывает, после завершения трансляции Apple показала репортерам интернет-порталов свои новые продукты. Дали посмотреть и потрогать новый MacBook Pro 13″ Retina, невероятно тонкий iMac, и, естественно, долгожданный iPad mini вместе с его мини Smart Cover. Большинство первых впечатлений и отзывов известных зарубежных яблочных порталов положительные!</div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://applemini.ru/wp-content/uploads/2012/10/ipad-mini-hands-on.jpg\" style=\"width: 600px; height: 335px; \"></div>\r\n<div>&nbsp;</div>\r\n<div><strong>Про iMac:&nbsp;</strong></div>\r\n<blockquote>\r\n	<div><em>Engadget:</em> Вы не найдете что-то хотя бы близко такое же шикарное и сексуальное за $1299.</div>\r\n</blockquote>\r\n<blockquote>\r\n	<div><em>The Verge:</em> Это по настоящему тонкий, революционный и новый iMac.</div>\r\n</blockquote>\r\n<div>&nbsp;</div>\r\n<div><strong>Про MacBook Retina 13″:&nbsp;</strong></div>\r\n<blockquote>\r\n	<div><em>Engadget:</em> Для начала — он неимоверно тонкий. Конечно, он больше листка бумаги, но достаточно тонкий, чтобы поместиться в любой портфель или рюкзак, да так, что он будет даже не заметен. Дизайн, детали, сборка, отделка и прочее даже не обсуждается — все великолепно. Как нам кажется, он должен будет заменить MacBook Air 13″ в будущем обновлении.</div>\r\n</blockquote>\r\n<blockquote>\r\n	<div><em>The Verge:</em> Он очень тонкий и при этом его изображение все еще такое же великолепное, как у его старшего брата.</div>\r\n</blockquote>\r\n<blockquote>\r\n	<div><em>TechCrunch: </em>Он отлично подойдет как основной компьютер. Однако, если этот основной компьютер постоянно носить с собой, то 15″ модель вам бы, наверно, не подошла, а вот новая 13″ модель подходит для этого отлично. Поэтому если вас не смущает цена, то это определенно must have.</div>\r\n</blockquote>\r\n<div>&nbsp;</div>\r\n<div><strong>Про iPad mini:&nbsp;</strong></div>\r\n<blockquote>\r\n	<div><em>Engadget:</em> Он все еще не достаточно маленький. Однако вы уже сможете спокойно поместить его в ладонь. Да, в карман он не влезет, но места в сумке или рюкзаке сэкономит прилично.</div>\r\n</blockquote>\r\n<blockquote>\r\n	<div><em>The Verge:</em> Четкость экрана iPad mini очень хороша, даже не смотря на то, что разрешение у него меньше, чем у 3-го или 4-го поколения iPad. Так просто и не сказать, что у него всего 1024х768. Меньшая площадь экрана не сказалась на его качестве.</div>\r\n</blockquote>\r\n<blockquote>\r\n	<div><em>TechCrunch:</em> Матовая задняя поверхность очень приятна на ощупь. Однако больше всего впечатляет — вес. Повторяется то потрясающее чувство, когда мы получили iPhone 5, но только в еще большей степени. Новый iPad mini почти невесомый по сравнению с его старшим братом. Сложно поверить, что это полноценно работающее устройство, а не макет.</div>\r\n</blockquote>\r\n<p>&nbsp;</p>\r\n',NULL,3,1),(41,'ВИДЕО: Реклама MacBook Pro 13″ Retina','<p><span style=\"font-size:16px;\"><strong>Реклама Macbook Pro 13″ Retina</strong></span></p>\r\n<p>&nbsp;</p>\r\n<p>Apple выпустила рекламный ролик MacBook Pro 13″ Retina под названием \"Colors\". Занимательно то, что в отличие от других яблочных роликов фон использовался черный, а не белый.</p>\r\n<p>&nbsp;</p>\r\n<p><iframe allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"http://www.youtube.com/embed/HfiJOP2uGk4\" width=\"560\"></iframe></p>\r\n\r\n<p>&lt;iframe width=\"560\" height=\"315\" src=\"http://www.yout','<p><span style=\"font-size:16px;\"><strong>Реклама Macbook Pro 13″ Retina</strong></span></p>\r\n<p>&nbsp;</p>\r\n<p>Apple выпустила рекламный ролик MacBook Pro 13″ Retina под названием \"Colors\". Занимательно то, что в отличие от других яблочных роликов фон использовался черный, а не белый.</p>\r\n<p>&nbsp;</p>\r\n<p><iframe allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"http://www.youtube.com/embed/HfiJOP2uGk4\" width=\"560\"></iframe></p>\r\n',NULL,3,1),(42,'Чехол и газовый балоннчик — два в одном.','<p>&nbsp;</p>\r\n<div><span style=\"font-size:16px;\"><strong>Чехол и газовый балоннчик — два в одном.</strong></span></div>\r\n<div>&nbsp;</div>\r\n<div>Бренд Spraytect выпустил уникальный чехол для iPhone 4/4s со встроенным балоннчиком. При использовании по назначению картридж с перцовым аэрозолем можно сменить на новый или заправить его.&nbsp;</div>\r\n<div>Кейс доступен в 4-х цветах и благодаря своей конструкции удобно лежит на столе.</div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://spraytect.co','<p>&nbsp;</p>\r\n<div><span style=\"font-size:16px;\"><strong>Чехол и газовый балоннчик — два в одном.</strong></span></div>\r\n<div>&nbsp;</div>\r\n<div>Бренд Spraytect выпустил уникальный чехол для iPhone 4/4s со встроенным балоннчиком. При использовании по назначению картридж с перцовым аэрозолем можно сменить на новый или заправить его.&nbsp;</div>\r\n<div>Кейс доступен в 4-х цветах и благодаря своей конструкции удобно лежит на столе.</div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://spraytect.com/wp-content/gallery/products/spraytect_firingcloseup_white-phone_crop.jpg\" style=\"width: 450px; height: 321px; \">&nbsp;<img alt=\"\" src=\"http://24gadget.ru/uploads/gallery/2012-10/fullsize/1351149568_41ajhyoall.jpg\" style=\"width: 250px; height: 333px; \"></div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://spraytect.com/wp-content/gallery/products/spraytectwhite_0003_back.jpg\" style=\"width: 300px; height: 400px; \">&nbsp;<img alt=\"\" src=\"http://spraytect.com/wp-content/gallery/products/spraytectwhite_0001_test-cartridge.jpg\" style=\"width: 300px; height: 400px; \"></div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://spraytect.com/wp-content/uploads/2012/05/pull-rotate-fire.gif\" style=\"width: 564px; height: 383px; \"></div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://spraytect.com/wp-content/uploads/2012/02/tilt.gif\" style=\"width: 233px; height: 175px; \">&nbsp;<img alt=\"\" src=\"http://spraytect.com/wp-content/uploads/2012/02/kickstand.gif\" style=\"width: 233px; height: 175px; \"></div>\r\n<div>&nbsp;</div>\r\n<div>&nbsp;</div>\r\n',NULL,1,1),(43,'Внутренности MacBook Pro Retina 13″','<p>&nbsp;</p>\r\n<div><span style=\"font-size:16px;\"><strong>Внутренности MacBook Pro Retina 13″</strong></span></div>\r\n<div>&nbsp;</div>\r\n<div>Ребята из iFixit одни из первых получили новый 13-ти дюймовый MacBook Pro Retina. Ну и сделали с ним то, чего делают обычно с каждым гаджетом - разобрали его и посмотрели чего внутри.&nbsp;</div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://guide-images.ifixit.net/igi/JYjJoWrXcngjA4B3.medium\" style=\"width: 591px; height: 444px; \"></div>','<p>&nbsp;</p>\r\n<div><span style=\"font-size:16px;\"><strong>Внутренности MacBook Pro Retina 13″</strong></span></div>\r\n<div>&nbsp;</div>\r\n<div>Ребята из iFixit одни из первых получили новый 13-ти дюймовый MacBook Pro Retina. Ну и сделали с ним то, чего делают обычно с каждым гаджетом - разобрали его и посмотрели чего внутри.&nbsp;</div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://guide-images.ifixit.net/igi/JYjJoWrXcngjA4B3.medium\" style=\"width: 591px; height: 444px; \"></div>\r\n<div>&nbsp;</div>\r\n<div>iFixit посчитали, что новый MacBook все еще по прежнему сложно разбирать и чинить и дали оценили новинку всего на 2 балла. Напомним, что 15-дюймовую модель MacBook Pro Retina умельцы оценили на 1 балл. На балл больше тринашка получила благодаря изменению в конструкции расположения компонентов под трекпадом, и теперь вы сможете без проблем его починить, если возникнут проблемы.&nbsp;</div>\r\n<div>&nbsp;</div>\r\n<div>Внутри MacBook Pro Retina 13″ были найдены следующие чипы:</div>\r\n<ul>\r\n	<li style=\"margin-left: 40px; \">Процессор Intel Core i5-3210M 2.5 GHz processor</li>\r\n	<li style=\"margin-left: 40px; \">Hynix H5TC4G83MFR 8 GB DDR3L SDRAM</li>\r\n	<li style=\"margin-left: 40px; \">Intel BD82QS77</li>\r\n	<li style=\"margin-left: 40px; \">Thunderbolt Intel DSL3510L</li>\r\n	<li style=\"margin-left: 40px; \">Микроконтроллер с интегрированным ARM ядром Texas Instruments Stellaris LM4FS1AH</li>\r\n	<li style=\"margin-left: 40px; \">USB 2.0 SMSC USB2512B</li>\r\n	<li style=\"margin-left: 40px; \">Питание Maxim MAX15119 Apple-specific IMVP7 CPU/GPU</li>\r\n	<li style=\"margin-left: 40px; \">Аудио Cirrus Audio 4206BCNZ</li>\r\n	<li style=\"margin-left: 40px; \">Texas Instruments TPS 51980</li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" src=\"http://guide-images.ifixit.net/igi/t2CrwKDtLBSEDtEG.medium\" style=\"width: 591px; height: 444px; \"></p>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" src=\"http://guide-images.ifixit.net/igi/ITpJBZcahnMEHnvd.medium\" style=\"width: 592px; height: 444px; \"></p>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" src=\"http://guide-images.ifixit.net/igi/FaT5LiABHROoMpqB.medium\" style=\"width: 591px; height: 444px; \"></p>\r\n',NULL,2,1),(44,'В русском App Store теперь платим рублями.','<p>&nbsp;</p>\r\n<div><span style=\"font-size:16px;\"><strong>В русском App Store теперь платим рублями.</strong></span></div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://i-ekb.ru/wp-content/uploads/2012/10/appstorerubbles.jpg\" style=\"width: 700px; height: 429px; \"></div>\r\n<div>&nbsp;</div>\r\n<div>Apple несколько часов назад перевела цены для русского App Store из американских долларов в русские рубли. Теперь вместо привычных глазу цен в $0.99 или $1.99 мы видим 33 р. или 66 р. По сегодняшнему к','<p>&nbsp;</p>\r\n<div><span style=\"font-size:16px;\"><strong>В русском App Store теперь платим рублями.</strong></span></div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://i-ekb.ru/wp-content/uploads/2012/10/appstorerubbles.jpg\" style=\"width: 700px; height: 429px; \"></div>\r\n<div>&nbsp;</div>\r\n<div>Apple несколько часов назад перевела цены для русского App Store из американских долларов в русские рубли. Теперь вместо привычных глазу цен в $0.99 или $1.99 мы видим 33 р. или 66 р. По сегодняшнему курсу если перевести доллар в рубли, то максимум получим 31 р., поэтому можно говорить о повышение цен для русских пользователей. Понятно, что курс доллара в нашей стране постоянно меняется и из-за это был рассчитан такой не самый выгодный для нас ценник.</div>\r\n',NULL,3,1),(45,'Apple перевела цены в App Store на местные валюты.','<p>&nbsp;</p>\r\n<div><span style=\"font-size:16px;\"><strong>Apple перевела цены в App Store на местные валюты.</strong></span></div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://www.macstories.net/wp-content/uploads/2012/10/Screen-Shot-2012-10-26-at-12.13.02-AM.png\" style=\"width: 664px; height: 365px; \"></div>','<p>&nbsp;</p>\r\n<div><span style=\"font-size:16px;\"><strong>Apple перевела цены в App Store на местные валюты.</strong></span></div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://www.macstories.net/wp-content/uploads/2012/10/Screen-Shot-2012-10-26-at-12.13.02-AM.png\" style=\"width: 664px; height: 365px; \"></div>\r\n<div>&nbsp;</div>\r\n<div>Мы уже писали, что в русском App Store сменилась валюта с долларов на рубли. Оказалось, что такая ситуация произошла и с другими странами. Местные валюты появились в восьми странах: Россия, Турция, Индия, Индонезия, Израиль, Саудовская Аравия, Южная Африка и Объединенные Арабские Эмираты. Помимо рублей, Apple добавила в App Store и Mac App Store турецкие лиры, индийские рупии, индонезийские рупии, израильские шекели, саудовские риалы, южноафриканские ранды и арабские дирхамы.</div>\r\n<div>Сообщается, что Apple также изменила минимальную цену приложения в странах Европы. Если раньше она составляла 0,79 евро, то теперь 0,89 евро.</div>\r\n<div>&nbsp;</div>\r\n<p>&nbsp;</p>\r\n',NULL,3,1),(46,'2 ноября в ряде стран стартуют продажи iPhone 5.','<p>&nbsp;</p>\r\n<div><span style=\"font-size:16px;\"><strong>2 ноября в ряде стран стартуют продажи iPhone 5.</strong></span></div>\r\n<div>&nbsp;</div>\r\n<div>iPhone 5 официально поступит 2 ноября на прилавки магазинов Индии, Болгарии, Мальты, Румынии, Тайланда и еще нескольких близлежащих странах.</div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://cs309420.userapi.com/v309420523/2297/nEmNxOdeE-I.jpg\" style=\"width: 387px; height: 433px; \"></div>','<p>&nbsp;</p>\r\n<div><span style=\"font-size:16px;\"><strong>2 ноября в ряде стран стартуют продажи iPhone 5.</strong></span></div>\r\n<div>&nbsp;</div>\r\n<div>iPhone 5 официально поступит 2 ноября на прилавки магазинов Индии, Болгарии, Мальты, Румынии, Тайланда и еще нескольких близлежащих странах.</div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://cs309420.userapi.com/v309420523/2297/nEmNxOdeE-I.jpg\" style=\"width: 387px; height: 433px; \"></div>\r\n<div>&nbsp;</div>\r\n<div>Пару дней назад Тим Кук заявлял, что Apple сделает все возможное, чтобы до нового года iPhone 5 начал официальные продажи в 100 странах.</div>\r\n<div>Официальный старт продаж iPhone 5 в России по многочисленным слухам назначен на декабрь, предположительно на середину месяца.</div>\r\n<div>Напомним вам, что мы уже во всю продаем iPhone 5 по самым низким ценам!</div>\r\n<div>&nbsp;</div>\r\n',NULL,3,1),(47,'Стеклянный Apple Store в Пало-Альто.','<p><span style=\"font-size:16px;\"><strong>Стеклянный Apple Store в Пало-Альто.</strong></span></p>\r\n<p>Сегодня открылся еще один невероятно красивый стеклянный яблочный магазин! Располагается он в городе Пало-Альто на 340 University Ave.&nbsp;</p>\r\n<p><img alt=\"\" src=\"http://cs309420.userapi.com/v309420523/22c2/vlps-QNT9v4.jpg\" style=\"width: 660px; height: 495px; \"></p>\r\n<p>&nbsp;</p>','<p><span style=\"font-size:16px;\"><strong>Стеклянный Apple Store в Пало-Альто.</strong></span></p>\r\n<p>Сегодня открылся еще один невероятно красивый стеклянный яблочный магазин! Располагается он в городе Пало-Альто на 340 University Ave.&nbsp;</p>\r\n<p><img alt=\"\" src=\"http://cs309420.userapi.com/v309420523/22c2/vlps-QNT9v4.jpg\" style=\"width: 660px; height: 495px; \"></p>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" src=\"http://cs309420.userapi.com/v309420523/22ca/CMS0EugAfmI.jpg\" style=\"width: 660px; height: 495px; \"></p>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" src=\"http://cs309420.userapi.com/v309420523/22da/-2WYwJOQgXg.jpg\" style=\"width: 660px; height: 495px; \"></p>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" src=\"http://cs309420.userapi.com/v309420523/22d2/ElBQxcc2U0Q.jpg\" style=\"width: 700px; height: 294px; \"></p>\r\n',NULL,3,1),(48,'Как скрыть строку статуса в Safari на iPhone без джейлбрейка','<p>&nbsp;</p>\r\n<div><span style=\"font-size:16px;\"><strong>Как скрыть строку статуса в Safari на iPhone без джейлбрейка</strong></span></div>\r\n<div>&nbsp;</div>\r\n<div>Несколько дней назад мы писали <a href=\"http://macov.net/kak-ubrat-ikonki\">как удалить стандартные иконки без джейлбрейка</a>. Сегодня мы расскажем о еще одном хаке, который придумал тот же автор.&nbsp;</div>\r\n<div>В этот раз мы будем скрывать строку статуса (полоска с часами и % заряда) в стандартном браузере Safari. Делается это о','<p>&nbsp;</p>\r\n<div><span style=\"font-size:16px;\"><strong>Как скрыть строку статуса в Safari на iPhone без джейлбрейка</strong></span></div>\r\n<div>&nbsp;</div>\r\n<div>Несколько дней назад мы писали <a href=\"http://macov.net/kak-ubrat-ikonki\">как удалить стандартные иконки без джейлбрейка</a>. Сегодня мы расскажем о еще одном хаке, который придумал тот же автор.&nbsp;</div>\r\n<div>В этот раз мы будем скрывать строку статуса (полоска с часами и % заряда) в стандартном браузере Safari. Делается это очень просто.</div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://cs309420.userapi.com/v309420523/22fe/WJqSOeEsw1Y.jpg\" style=\"width: 350px; height: 525px; \"></div>\r\n<div>&nbsp;</div>\r\n<div><strong>Итак, по порядку:</strong></div>\r\n<ul>\r\n	<li style=\"margin-left: 40px; \">открываем браузер Safari;</li>\r\n	<li style=\"margin-left: 40px; \">переходим по ссылке <a href=\"http://rag3hack.no-ip.org/status.html\">rag3hack.no-ip.org/status.html</a>;</li>\r\n	<li style=\"margin-left: 40px; \">наживаем на «No Safari Status»;</li>\r\n	<li style=\"margin-left: 40px; \">экран станет черным, ждем несколько секунд;</li>\r\n	<li style=\"margin-left: 40px; \">все, строка статуса исчезла.</li>\r\n</ul>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://cs309420.userapi.com/v309420523/22f5/I1KaxgcaYFA.jpg\" style=\"width: 350px; height: 525px; \"></div>\r\n<div>&nbsp;</div>\r\n<div>Теперь вы можете увидеть больше информации на экране, благодаря этому серфинг по интернету стал удобнее.</div>\r\n<div>&nbsp;</div>\r\n<div><strong>Чтобы вернуть все на прежнее место, надо закрыть приложение из панели многозадачности и запустить его снова.</strong></div>\r\n<div>&nbsp;</div>\r\n<div><strong><img alt=\"\" src=\"http://cs309420.userapi.com/v309420523/2307/LSN4Pec0By0.jpg\" style=\"width: 350px; height: 525px; \"></strong></div>\r\n',NULL,2,1),(49,'Apple Store готовы к приближающемуся урагану','<p>&nbsp;</p>\r\n<div><span style=\"font-size:16px;\"><strong>Метеорологи предвещают ураган на восточное побережье США.&nbsp;</strong></span></div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://cs309420.userapi.com/v309420523/27a0/YkMuRWriV58.jpg\" style=\"width: 600px; height: 294px; \"></div>\r\n<div>&nbsp;</div>\r\n<div>Ураган, названный Сэнди, придет уже завтра, в связи с этим все Apple Store уже подготовлены. Техника обернута в пакеты, а снаружи перед витринами уложены мешки с песком.&nbsp;</div>','<p>&nbsp;</p>\r\n<div><span style=\"font-size:16px;\"><strong>Метеорологи предвещают ураган на восточное побережье США.&nbsp;</strong></span></div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://cs309420.userapi.com/v309420523/27a0/YkMuRWriV58.jpg\" style=\"width: 600px; height: 294px; \"></div>\r\n<div>&nbsp;</div>\r\n<div>Ураган, названный Сэнди, придет уже завтра, в связи с этим все Apple Store уже подготовлены. Техника обернута в пакеты, а снаружи перед витринами уложены мешки с песком.&nbsp;</div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://cs309420.userapi.com/v309420523/27a7/8AMvYJ9HqRA.jpg\" style=\"width: 600px; height: 450px; \"></div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://cs309420.userapi.com/v309420523/27ae/q8aXd4jVxf4.jpg\" style=\"width: 600px; height: 450px; \"></div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://cs309420.userapi.com/v309420523/27b5/8TfryL7Enbo.jpg\" style=\"width: 480px; height: 360px; \"></div>\r\n<p>&nbsp;</p>\r\n',NULL,3,1),(50,'Яндекс добавил поиск приложений для iOS и Android в веб-поиск','<p>&nbsp;</p>\r\n<div><span style=\"font-size:16px;\"><strong>Яндекс добавил поиск приложений для iOS и Android в веб-поиск.</strong></span></div>\r\n<div>&nbsp;</div>\r\n<p><img alt=\"\" src=\"http://cs309420.userapi.com/v309420523/27be/hIGIpJjaCFk.jpg\" style=\"height: 427px; width: 700px; \"></p>\r\n<p>&nbsp;</p>\r\n<div>«Яндекс» предоставляет данную возможность начиная с сентября 2011 года, однако для этого необходимо было использовать отдельный поисковый сервис — appsearch.yandex.ru. Теперь получить информац','<p>&nbsp;</p>\r\n<div><span style=\"font-size:16px;\"><strong>Яндекс добавил поиск приложений для iOS и Android в веб-поиск.</strong></span></div>\r\n<div>&nbsp;</div>\r\n<p><img alt=\"\" src=\"http://cs309420.userapi.com/v309420523/27be/hIGIpJjaCFk.jpg\" style=\"height: 427px; width: 700px; \"></p>\r\n<p>&nbsp;</p>\r\n<div>«Яндекс» предоставляет данную возможность начиная с сентября 2011 года, однако для этого необходимо было использовать отдельный поисковый сервис — appsearch.yandex.ru. Теперь получить информацию о различных приложения можно непосредственно в основном поиске.</div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://cs309420.userapi.com/v309420523/27c7/FF-D_sSgg-o.jpg\" style=\"width: 700px; height: 463px; \"></div>\r\n<div>&nbsp;</div>\r\n<div>Сервис очень удобен тем, кто не помнит или не знает точного названия приложения. В поиске можно выбрать для какой платформы искать приложения - &nbsp;iPhone, iPad или Android. Можно осуществлять поиск по всем приложениям, а можно только по бесплатным, что очень удобно.&nbsp;</div>\r\n<div>Сервис показывает рейтинг приложений, а также их краткое описание.</div>\r\n',NULL,3,1),(51,'Левенштейн удалит приложения','<p>\r\n	AppTrasher - на первый взгляд - обыкновенное приложение для удаления программ не оставляя следов их присутствия на компьютере. Но не все так просто &mdash; для поиска файлов приложений AppTrasher использует алгоритм Левенштейна. Подробнее о самом алгоритме можете ознакомиться на <a href=\"http://ru.wikipedia.org/wiki/%D0%90%D0%BB%D0%B3%D0%BE%D1%80%D0%B8%D1%82%D0%BC_%D0%9B%D0%B5%D0%B2%D0%B5%D0%BD%D1%88%D1%82%D0%B5%D0%B9%D0%BD%D0%B0\" target=\"_blank\" title=\"http://ru.wikipedia.org/wiki/Алгорит','\r\n<p>\r\nAppTrasher - на первый взгляд - обыкновенное приложение для удаления программ не оставляя следов их присутствия на компьютере. Но не все так просто &mdash; для поиска файлов приложений AppTrasher использует алгоритм Левенштейна. Подробнее о самом алгоритме можете ознакомиться на <a href=\"http://ru.wikipedia.org/wiki/%D0%90%D0%BB%D0%B3%D0%BE%D1%80%D0%B8%D1%82%D0%BC_%D0%9B%D0%B5%D0%B2%D0%B5%D0%BD%D1%88%D1%82%D0%B5%D0%B9%D0%BD%D0%B0\" target=\"_blank\" title=\"http://ru.wikipedia.org/wiki/Алгоритм_Левенштейна\">википедии</a></p>\r\n<p class=\"rtecenter\">\r\n<a href=\"http://ru.wikipedia.org/wiki/%D0%90%D0%BB%D0%B3%D0%BE%D1%80%D0%B8%D1%82%D0%BC_%D0%9B%D0%B5%D0%B2%D0%B5%D0%BD%D1%88%D1%82%D0%B5%D0%B9%D0%BD%D0%B0\" target=\"_blank\" title=\"http://ru.wikipedia.org/wiki/Алгоритм_Левенштейна\">.</a> <a href=\"http://www.macov.net/wp-content/uploads/2010/02/apptrasher02_00.jpg\"><img alt=\"\" class=\"aligncenter size-full wp-image-181\" height=\"397\" src=\"http://www.macov.net/wp-content/uploads/2010/02/apptrasher02_00.jpg\" title=\"apptrasher02_00\" width=\"519\" /></a></p>\r\n\r\n<p>\r\nAppTrasher может работать в двух режимах &mdash; обычнычном и, так называемом, expert mode(основным различием между ними является то, что в последнем используется этот алгоритм). Так же к особенностям можно отнести автоматический поиск всех файлов удаляемого приложения, поиск вне папок приложений,поиск видимых и скрытых файлов, а так же возможность восстановления файлов. Системные требования: Mac OS X 10.5 и выше. Найти приложение можно <a href=\"http://www.macupdate.com/info.php/id/33375\" target=\"_blank\" title=\"http://www.macupdate.com/info.php/id/33375\">тут</a></p>\r\n',NULL,3,1),(52,'Новый твиттер-клиент Kiwi','<p class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/02/screens1.png\"><img alt=\"\" class=\"aligncenter size-full wp-image-206\" height=\"200\" src=\"http://www.macov.net/wp-content/uploads/2010/02/screens1.png\" title=\"screens\" width=\"400\" /></a></p>\r\n<p style=\"text-align: left;\">\r\n	Пускай этот клиент еще молод, но развивается он стремительно. И недавно появилась новая версия <strong>Kiwi</strong>. Естественно, <strong>Kiwi </strong> содержит все необходимые стандартные функции','<p class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/02/screens1.png\"><img alt=\"\" class=\"aligncenter size-full wp-image-206\" height=\"200\" src=\"http://www.macov.net/wp-content/uploads/2010/02/screens1.png\" title=\"screens\" width=\"400\" /></a></p>\r\n<p style=\"text-align: left;\">\r\n	Пускай этот клиент еще молод, но развивается он стремительно. И недавно появилась новая версия <strong>Kiwi</strong>. Естественно, <strong>Kiwi </strong> содержит все необходимые стандартные функции твиттер клиента, такие, как поддержка нескольких учетных записей, работа с твистами и личными сообщениями, вставка изображений и т.д.</p>\r\n\r\n<p class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/02/kiwi_themes.png\"><img alt=\"\" class=\"aligncenter size-full wp-image-208\" height=\"533\" src=\"http://www.macov.net/wp-content/uploads/2010/02/kiwi_themes.png\" title=\"kiwi_themes\" width=\"464\" /></a></p>\r\n<p style=\"text-align: left;\">\r\n	К особенностям <strong>Kiwi</strong> можно причислить поддержку пользовательских тем. Стандартно приложение поставляется девятью темами, но при желании вы сможете скачать с специального сервера темы от сторонних разработчиков.</p>\r\n<p style=\"text-align: left;\">\r\n	Также интересной особенностью<strong> Kiwi </strong>является возможность работы с группами. Это вам позволит объединять несколько лент в одну и читать их не переключаясь между аккаунтами. И , конечно, не можем не отметить работу с фильтрами. Таким образом, к примеру, вы сможете скрывать ненужные вам сообщения, и подсвечивать ключевые слова.</p>\r\n<p class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/02/kiwi_main.png\"><img alt=\"\" class=\"aligncenter size-full wp-image-207\" height=\"619\" src=\"http://www.macov.net/wp-content/uploads/2010/02/kiwi_main.png\" title=\"kiwi_main\" width=\"352\" /></a></p>\r\n<p style=\"text-align: left;\">\r\n	В новой версии <strong>Kiwi </strong>появился просмотр фотографий из сервисов Ember, TwitPic и yFrog; добавлена возможность работы с Tumblr; добавлена поддержка уведомлений Growи через иконку в Доке</p>\r\n<p style=\"text-align: left;\">\r\n	Системные требования: Mac OS X 10.6</p>\r\n<p style=\"text-align: left;\">\r\n	официальный сайт: <a href=\"http://kiwi-app.net/\" target=\"_blank\" title=\"http://kiwi-app.net/\">http://kiwi-app.net/</a></p>\r\n',NULL,3,1),(53,'Parallels или VMware?','<p>\r\n	Какую виртуальную машину ставить? Наверное, многие из вас задавались этим вопросом, и выбор ваш стоял между Parallels и VMware. И в итоге, склонялись к той программе, что вам посоветовали другие пользователи этого софта. считается, что эти продукты практически идентичны, лишь местами уступая друг другу. Но издание MacTech Magazine решило разобраться в этом спорном моменте, и, наконец, выбрать лучшего из этих двух претендентов.</p>\r\n<p class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-c','<p>\r\n	Какую виртуальную машину ставить? Наверное, многие из вас задавались этим вопросом, и выбор ваш стоял между Parallels и VMware. И в итоге, склонялись к той программе, что вам посоветовали другие пользователи этого софта. считается, что эти продукты практически идентичны, лишь местами уступая друг другу. Но издание MacTech Magazine решило разобраться в этом спорном моменте, и, наконец, выбрать лучшего из этих двух претендентов.</p>\r\n<p class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/02/virt02_00.jpg\"><img alt=\"\" class=\"aligncenter size-full wp-image-213\" height=\"320\" src=\"http://www.macov.net/wp-content/uploads/2010/02/virt02_00.jpg\" title=\"virt02_00\" width=\"571\" /></a></p>\r\n\r\n<p>\r\n	Подробнее о результатах теста можете прочитать <a href=\"http://www.mactech.com/articles/special/1002-VirtualizationHeadToHead/index-001.html\" target=\"_blank\" title=\"http://www.mactech.com/articles/special/1002-VirtualizationHeadToHead/index-001.html\">здесь</a>. По итогам теста Parallels Desktop 5 показал быстродействие при работе с Windows XP аж на 30% выше, чем VMware Fusion 3, а что косается случая с Windows 7 - Parallels быстрее на все 43%! Так же, как видно из таблицы - работа с графикой, играми, или же 3D удается Parallels намного лучше. Но в конечном счете выбор виртуальной машины за вами...</p>\r\n',NULL,3,1),(54,'Представляем iSandwich 4G!','<p>\r\n	Пока все мы ждем начала продаж iPad, iманьяки смастерили iSandwich 4g! А что, очень удобно - по крайней мере можно &quot;заточить&quot; полностью под свой вкус! При разработке были использованы такие устройства, как: Хлеб; Соусы Чили, Вассаби, Горчица; Огурец и др. овощи; ананас, яблоко, киви, банан; колбасы и сыр; подробнее о том, как происходила разработка устройства смотрите ниже:</p>\r\n<p class=\"rtecenter\">\r\n	<object classid=\"clsid:d27cdb6e-ae6d-11cf-96b8-444553540000\" codebase=\"http://','<p>\r\n	Пока все мы ждем начала продаж iPad, iманьяки смастерили iSandwich 4g! А что, очень удобно - по крайней мере можно &quot;заточить&quot; полностью под свой вкус! При разработке были использованы такие устройства, как: Хлеб; Соусы Чили, Вассаби, Горчица; Огурец и др. овощи; ананас, яблоко, киви, банан; колбасы и сыр; подробнее о том, как происходила разработка устройства смотрите ниже:</p>\r\n<p class=\"rtecenter\">\r\n	<object classid=\"clsid:d27cdb6e-ae6d-11cf-96b8-444553540000\" codebase=\"http://download.macromedia.com/pub/shockwave/cabs/flash/swflash.cab#version=6,0,40,0\" height=\"340\" width=\"560\"><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><param name=\"src\" value=\"http://www.youtube.com/v/5P2gKYv2pGY&amp;hl=ru_RU&amp;fs=1&amp;\" /><param name=\"allowfullscreen\" value=\"true\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"340\" src=\"http://www.youtube.com/v/5P2gKYv2pGY&amp;hl=ru_RU&amp;fs=1&amp;\" type=\"application/x-shockwave-flash\" width=\"560\"></embed></object></p>\r\n',NULL,3,1),(55,'Представлен OmniGrowl 3.8','<p>\r\n	К великому сожалению, не все приложения поддерживают систему оповещений Growl, и у многих разработчиков даже в планах нет добавлять поддержку. И в такие моменты будет полезна программа <strong>OmniGrowl, </strong>встраивающая Growl в ряд приложений Mac OS X.</p>\r\n<p>\r\n	<img alt=\"\" src=\"http://www.deepapple.com/images/news/082008/omnigrowl00.jpg\" /></p>\r\n','<p>\r\n	К великому сожалению, не все приложения поддерживают систему оповещений Growl, и у многих разработчиков даже в планах нет добавлять поддержку. И в такие моменты будет полезна программа <strong>OmniGrowl, </strong>встраивающая Growl в ряд приложений Mac OS X.</p>\r\n<p>\r\n	<img alt=\"\" src=\"http://www.deepapple.com/images/news/082008/omnigrowl00.jpg\" /></p>\r\n\r\n<p>\r\n	Программа способна следить за RSS каналами и выводить новую информацию, отображать сводку погоды по городам, и т.д. Только при первом запуске OmniGrowl придется настроить под себя, потому что многие функции изначально выключены.</p>\r\n<p>\r\n	К великому сожалению, не все приложения поддерживают систему оповещений Growl, и у многих разработчиков даже в планах нет добавлять поддержку. И в такие моменты будет полезна программа <strong>OmniGrowl, </strong>встраивающая Growl в ряд приложений Mac OS X.</p>\r\n<p>\r\n	<img alt=\"\" src=\"http://www.deepapple.com/images/news/082008/omnigrowl00.jpg\" /></p>\r\n<p>\r\n	Программа способна следить за RSS каналами и выводить новую информацию, отображать сводку погоды по городам, и т.д. Только при первом запуске OmniGrowl придется настроить под себя, потому что многие функции изначально выключены. В новой версии появилась возможность увеличить частоту проверки каналов RSS, а так же появилась возможность добавить до 10 потоков, вместо 5. Так же, при нажатии на метеосводку с сайта Yahoo! приложение откроет страницу с более подробным прогнозом. Системные требования: Mac OS X 10.4 и выше. Официальный сайт <a href=\"http://www.woodenbrain.com/\" target=\"_blank\" title=\"http://www.woodenbrain.com/\">http://www.woodenbrain.com/ </a></p>\r\n',NULL,3,1),(56,'Бесплатная версия Get Backup','<p>\r\n	BeLight Software выпустила бесплатный вариант программы для резервного копирования Get Backup. Get Backup - это приложение, автоматизирующее процесса резервного копирования. В бесплатной версии Get Backup есть возможность работы по рассписанию, полное или частичное архивирование, запрет копирования определенных файлов или папок, если в копии они не нужны, возможность сохранения архивов как на сетевой, так и на локальный раздел.</p>\r\n<p class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-','<p>BeLight Software выпустила бесплатный вариант программы для резервного копирования Get Backup. Get Backup - это приложение, автоматизирующее процесса резервного копирования. В бесплатной версии Get Backup есть возможность работы по рассписанию, полное или частичное архивирование, запрет копирования определенных файлов или папок, если в копии они не нужны, возможность сохранения архивов как на сетевой, так и на локальный раздел.</p>\r\n<p class=\"rtecenter\"><a href=\"http://www.macov.net/wp-content/uploads/2010/02/get02_00.jpg\"><img alt=\"\" class=\"aligncenter size-full wp-image-229\" height=\"690\" src=\"http://www.macov.net/wp-content/uploads/2010/02/get02_00.jpg\" title=\"get02_00\" width=\"420\"></a></p>\r\n<p>Платный же вариантGet BackUp кроме всего вышеназванного способен архивировать только модифицированные файлы, синхронизировать в оба направления, выборочно восстанавливать файлы и папки, а также защищать конечные архивы паролем.И за все это удовольствие разработчики просят 40 американских долларов. Системные требования: Mac OS X 10.4 и выше. официальный сайт: <a href=\"http://www.belightsoft.com/products/getbackup/overview.php\" target=\"_blank\" title=\"http://www.belightsoft.com/products/getbackup/overview.php\">http://www.belightsoft.com</a></p>\r\n',NULL,3,1),(57,'iPhone теперь может качать 20Мб через 3G','<p>\r\n	Как стало известно совсем недавно, в новой версии прошивки iPhone OS 3.1.3 разработчики разрешили качать через сети 3G файлы, размером до 20Мб. Раньше этот лимит составлял 10Мб, при превышении которого iPhone предлагал загрузить файл через сети Wi-Fi. Такой ход окажется полезен всем, как Apple, позволив качать чаще из App Store и iTunes Store, так и сотовым операторам, получающим свою прибыль за предоставление услуг сетей 3G.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" class=\"size-full wp-ima','<p>\r\n	Как стало известно совсем недавно, в новой версии прошивки iPhone OS 3.1.3 разработчики разрешили качать через сети 3G файлы, размером до 20Мб. Раньше этот лимит составлял 10Мб, при превышении которого iPhone предлагал загрузить файл через сети Wi-Fi. Такой ход окажется полезен всем, как Apple, позволив качать чаще из App Store и iTunes Store, так и сотовым операторам, получающим свою прибыль за предоставление услуг сетей 3G.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" class=\"size-full wp-image-234 aligncenter\" height=\"480\" src=\"http://www.macov.net/wp-content/uploads/2010/02/2lvoc39.jpg\" title=\"2lvoc39\" width=\"320\" /></p>\r\n',NULL,3,1),(58,'Титановый iPhone существует!','<p class=\"rtecenter\">\r\n	&nbsp;<a href=\"http://www.macov.net/wp-content/uploads/2010/02/24feb10po3b4tnn.jpg\"><img alt=\"\" class=\"alignnone size-full wp-image-248\" height=\"440\" src=\"http://www.macov.net/wp-content/uploads/2010/02/24feb10po3b4tnn.jpg\" title=\"Iphone titanium\" width=\"600\" /></a></p>\r\n<p>\r\n	Скучаете по приятным ощущениям от холодного алюминия iPhone 2G в руках? Как сообщают наши друзья с engadget.com, вы не одиноки! Приветствуем фантастический iPhone 3GS в титановом корпусе! Умелец из ','<p class=\"rtecenter\">\r\n	&nbsp;<a href=\"http://www.macov.net/wp-content/uploads/2010/02/24feb10po3b4tnn.jpg\"><img alt=\"\" class=\"alignnone size-full wp-image-248\" height=\"440\" src=\"http://www.macov.net/wp-content/uploads/2010/02/24feb10po3b4tnn.jpg\" title=\"Iphone titanium\" width=\"600\" /></a></p>\r\n<p>\r\n	Скучаете по приятным ощущениям от холодного алюминия iPhone 2G в руках? Как сообщают наши друзья с engadget.com, вы не одиноки! Приветствуем фантастический iPhone 3GS в титановом корпусе! Умелец из Австралии переделал свой телефон до полной неузнаваемости.</p>\r\n\r\n<p class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/02/24feb10po3b4tmm.jpg\"><img alt=\"Titanium \" class=\"alignnone size-full wp-image-250\" height=\"440\" src=\"http://www.macov.net/wp-content/uploads/2010/02/24feb10po3b4tmm.jpg\" title=\"Titanium \" width=\"600\" /></a></p>\r\n<p>\r\n	На наш взгляд едиственный минус этого чудного устройства - огромное лого Apple сзади.</p>\r\n<p class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/02/24feb109h43v.jpg\"><img alt=\"Titanium Iphone\" class=\"alignnone size-full wp-image-249\" height=\"590\" src=\"http://www.macov.net/wp-content/uploads/2010/02/24feb109h43v.jpg\" title=\"Titanium Iphone\" width=\"445\" /></a></p>\r\n',NULL,1,1),(59,'Первое обновление Aperture 3','<p class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/02/image.png\"><br />\r\n	<img alt=\"\" class=\"alignleft size-full wp-image-255\" height=\"120\" src=\"http://www.macov.net/wp-content/uploads/2010/02/image.png\" title=\"image\" width=\"165\" /></a></p>\r\n<p style=\"text-align: justify;\">\r\n	Еще совсем недавно компания Apple выпустила на свет Aperture 3, но не прошло и месяца с того дня, как появилось первое обновление, до версии 3.0.1. Обновление включает в себя 15 исправлений, а так','<p class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/02/image.png\"><br />\r\n	<img alt=\"\" class=\"alignleft size-full wp-image-255\" height=\"120\" src=\"http://www.macov.net/wp-content/uploads/2010/02/image.png\" title=\"image\" width=\"165\" /></a></p>\r\n<p style=\"text-align: justify;\">\r\n	Еще совсем недавно компания Apple выпустила на свет Aperture 3, но не прошло и месяца с того дня, как появилось первое обновление, до версии 3.0.1. Обновление включает в себя 15 исправлений, а так же повышает общую стабильность приложения.</p>\r\n\r\n<p style=\"text-align: justify;\">\r\n	Подробнее об обновлении можно прочитать<a href=\"http://support.apple.com/kb/TS2518\" title=\"http://support.apple.com/kb/TS2518\"> здесь</a>.(Осторожно, Английский язык.)</p>\r\n<p>\r\n	<a href=\"http://download.info.apple.com/Mac_OS_X/061-7419.20100224.VfgtA/Aperture3.0.1Update.dmg\" title=\"http://download.info.apple.com/Mac_OS_X/061-7419.20100224.VfgtA/Aperture3.0.1Update.dmg\">Скачать обновление Aperture</a>. (29mb)</p>\r\n',NULL,3,1),(60,'Очередной таймкиллер Cake Shop 2','<p style=\"text-align: justify;\">\r\n	Если вы любите убивать время мини играми, то аркада<strong> Cake Shop 2</strong> от EleFun Games вам придется по вкусу.</p>\r\n<p class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/02/371_1.jpg\"><img alt=\"\" class=\"alignnone size-full wp-image-261\" height=\"480\" src=\"http://www.macov.net/wp-content/uploads/2010/02/371_1.jpg\" title=\"371_1\" width=\"640\" /></a></p>\r\n','<p style=\"text-align: justify;\">\r\n	Если вы любите убивать время мини играми, то аркада<strong> Cake Shop 2</strong> от EleFun Games вам придется по вкусу.</p>\r\n<p class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/02/371_1.jpg\"><img alt=\"\" class=\"alignnone size-full wp-image-261\" height=\"480\" src=\"http://www.macov.net/wp-content/uploads/2010/02/371_1.jpg\" title=\"371_1\" width=\"640\" /></a></p>\r\n\r\n<p style=\"text-align: justify;\">\r\n	Запустив игру, вы станете девушкой Эмили, у которой с самого начала в распоряжении небольшое кафе. Ваша задача - обслуживать посетителей, расширять ассортимент товаров, для этого вам необходимо приобретать оборудование для производства вкусностей.</p>\r\n<p class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/02/371_4.jpg\"><img alt=\"\" class=\"size-full wp-image-262 aligncenter\" height=\"480\" src=\"http://www.macov.net/wp-content/uploads/2010/02/371_4.jpg\" title=\"371_4\" width=\"640\" /></a></p>\r\n<p style=\"text-align: justify;\">\r\n	Особенности игры: пять различных кафе, в каждом из которых 20 уровней, большое количество оборудования, много разных посетителей, каждый со своими запросами.</p>\r\n<p class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/02/cake02_00.jpg\"><img alt=\"\" class=\"size-full wp-image-263 aligncenter\" height=\"465\" src=\"http://www.macov.net/wp-content/uploads/2010/02/cake02_00.jpg\" title=\"cake02_00\" width=\"620\" /></a></p>\r\n<p style=\"text-align: justify;\">\r\n	Системные требования: Mac OS X 10.4 и выше.</p>\r\n<p style=\"text-align: justify;\">\r\n	Официальный сайт: <a href=\"http://www.elefun-games.com/html/products/elefun-games-cake-shop-2-game.html\" target=\"_blank\" title=\"http://www.elefun-games.com/html/products/elefun-games-cake-shop-2-game.html\">http://www.elefun-games.com/</a></p>\r\n',NULL,3,1),(61,'Remo Recover спасет данные','<div id=\"_mcePaste\">\r\n	<strong>Remo Software</strong> выпустила <strong>Remo Recover</strong> - приложение для восстановления уничтоженных данных.</div>\r\n<div class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/02/remo02_00.jpg\"><img alt=\"\" class=\"aligncenter size-full wp-image-153\" height=\"280\" src=\"http://www.macov.net/wp-content/uploads/2010/02/remo02_00.jpg\" title=\"remo02_00\" width=\"508\" /></a></div>\r\n','<div id=\"_mcePaste\">\r\n	<strong>Remo Software</strong> выпустила <strong>Remo Recover</strong> - приложение для восстановления уничтоженных данных.</div>\r\n<div class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/02/remo02_00.jpg\"><img alt=\"\" class=\"aligncenter size-full wp-image-153\" height=\"280\" src=\"http://www.macov.net/wp-content/uploads/2010/02/remo02_00.jpg\" title=\"remo02_00\" width=\"508\" /></a></div>\r\n\r\n<div id=\"_mcePaste\">\r\n	Существует две версии приложения - <strong>Media Edition</strong> и <strong>Pro</strong>. В первом случае вам будут доступны лишь основные инструменты в виде автоматического поиска мультимедиа файлов, а так же поддержки файлов RAW</div>\r\n<div id=\"_mcePaste\">\r\n	А версия Pro представляет куда больший интерес. В этой версии есть возможность осуществить поиск удаленных файлов, независимо от формата, в том числе на отформатированном разделе или даже поврежденном диске.</div>\r\n<div id=\"_mcePaste\">\r\n	Системные требования: Mac OS X 10.5 и выше.</div>\r\n<div>\r\n	официальный сайт: <a href=\"http://www.remosoftware.com/\" target=\"_blank\" title=\"http://www.remosoftware.com/\">http://www.remosoftware.com/</a></div>\r\n',NULL,3,1),(62,'Все блоги в одной программе','<div id=\"_mcePaste\">\r\n	Brainjuice, LLC представила сегодня новую версию блог клиента <strong>Blogo 1.3</strong>. Добавили поддержку сервиса Tumblr и улучшили поддержку Twitter, а так же появилась поддержка видеосервисов Dailymotion и Metacafe.</div>\r\n<div class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/02/blogo04_00.jpg\"><img alt=\"\" class=\"aligncenter size-full wp-image-147\" height=\"319\" src=\"http://www.macov.net/wp-content/uploads/2010/02/blogo04_00.jpg\" title=\"blogo0','<div id=\"_mcePaste\">\r\n	Brainjuice, LLC представила сегодня новую версию блог клиента <strong>Blogo 1.3</strong>. Добавили поддержку сервиса Tumblr и улучшили поддержку Twitter, а так же появилась поддержка видеосервисов Dailymotion и Metacafe.</div>\r\n<div class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/02/blogo04_00.jpg\"><img alt=\"\" class=\"aligncenter size-full wp-image-147\" height=\"319\" src=\"http://www.macov.net/wp-content/uploads/2010/02/blogo04_00.jpg\" title=\"blogo04_00\" width=\"600\" /></a></div>\r\n\r\n<div id=\"_mcePaste\">\r\n	Brainjuice, LLC представила сегодня новую версию блог клиента <strong>Blogo 1.3</strong>. Добавили поддержку сервиса Tumblr и улучшили поддержку Twitter, а так же появилась поддержка видеосервисов Dailymotion и Metacafe.</div>\r\n<div class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/02/blogo04_00.jpg\"><img alt=\"\" class=\"aligncenter size-full wp-image-147\" height=\"319\" src=\"http://www.macov.net/wp-content/uploads/2010/02/blogo04_00.jpg\" title=\"blogo04_00\" width=\"600\" /></a></div>\r\n<div id=\"_mcePaste\">\r\n	<strong>Blogo</strong> будет полезен активным пользователям блогов. Публиковать свое письменное творчество, выкладывать свои видеоролики и фотографии, беседовать с друзьями &mdash; стало еще удобнее, вместе с <strong>Blogo</strong>.</div>\r\n<div id=\"_mcePaste\">\r\n	<strong>Blogo</strong> поддерживает большое число сервисов, таких, как Wordpress, Typepad, Blogger, и многие другие...</div>\r\n<div class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/02/screenshot_3.jpg\"><img alt=\"\" class=\"aligncenter size-full wp-image-148\" height=\"319\" src=\"http://www.macov.net/wp-content/uploads/2010/02/screenshot_3.jpg\" title=\"screenshot_3\" width=\"510\" /></a></div>\r\n<div id=\"_mcePaste\">\r\n	<strong>Blogo</strong> также умеет работать с Ping.FM и Twitter,имеет удобный редактор текстов; сохранение черновиков а также может работать без подключения к Интернету. И конечно будет полезна возможность полноэкранного режима &mdash; и вас ничто не будет отвлекать от блогов.</div>\r\n<div id=\"_mcePaste\">\r\n	разнообразные мелкие исправления и изменения.</div>\r\n<div class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/02/screenshot_8.jpg\"><img alt=\"\" class=\"aligncenter size-full wp-image-146\" height=\"319\" src=\"http://www.macov.net/wp-content/uploads/2010/02/screenshot_8.jpg\" title=\"screenshot_8\" width=\"510\" /></a></div>\r\n<div id=\"_mcePaste\">\r\n	Системные требования: Mac OS X 10.4 и выше.</div>\r\n<div>\r\n	официальный сайт: <a href=\"http://www.drinkbrainjuice.com/blogo\" title=\"http://www.drinkbrainjuice.com/\">http://www.drinkbrainjuice.com/</a></div>\r\n',NULL,3,1),(63,'Trillian бросает вызов Adium','<p>\r\n	<strong>Trillian</strong> на Windows - неплохой универсальный IM-клиент, и недавно появилась альфа-версия на Mac OS X! Пока неизвестно, будет ли он лучше, чем привычныйAdium.</p>\r\n<p class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/02/mac-top-1.png\"><img alt=\"\" class=\"size-full wp-image-82 alignleft\" height=\"379\" src=\"http://www.macov.net/wp-content/uploads/2010/02/mac-top-1.png\" title=\"mac-top-1\" width=\"128\" /></a></p>\r\n','<p>\r\n	<strong>Trillian</strong> на Windows - неплохой универсальный IM-клиент, и недавно появилась альфа-версия на Mac OS X! Пока неизвестно, будет ли он лучше, чем привычныйAdium.</p>\r\n<p class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/02/mac-top-1.png\"><img alt=\"\" class=\"size-full wp-image-82 alignleft\" height=\"379\" src=\"http://www.macov.net/wp-content/uploads/2010/02/mac-top-1.png\" title=\"mac-top-1\" width=\"128\" /></a></p>\r\n\r\n<p style=\"text-align: center;\">\r\n	На сегодня Trillian имеет поддержку большинства существующих сетей ; запись журнала переговоров; передачу файлов; вкладки. Но пока что Trillian имеет ряд недостатков, зачастую существенных. Первый и, пожалуй, самый существенный &mdash; он пока не умеет работать с кодировкой сообщений, а значит - никакой кириллицы. Уже в ближайшем будущем разработчики обещают поддержку Twitter и Facebook, возможность отправки сообщений по почте, и многое другое... Если Cerulean Studios будут продолжать в том же духе, то уже скоро у нас будет реальный выбор. Но пока Trillian не дотягивает до реальной конкурентоспособности с Adium. Системные требования: Mac OS X 10.5 и выше. официальный сайт: <a href=\"http://www.trillian.im/learn/tour-mac.html\" title=\"http://www.trillian.im/\">http://www.trillian.im/</a></p>\r\n',NULL,3,1),(64,'Chronories — Ваша жизнь под запись','<p class=\"rtecenter\">\r\n	&nbsp;</p>\r\n<p class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/01/feelinglines1.png\"><img alt=\"\" class=\"alignleft size-medium wp-image-75\" height=\"234\" src=\"http://www.macov.net/wp-content/uploads/2010/01/feelinglines1-300x234.png\" title=\"feelinglines\" width=\"300\" /></a></p>\r\n<p>\r\n	Компания <strong>Synium Software</strong> открыла бета-тестирование нового приложения под названием <strong>Chronories</strong>, представляющее собой журнал жизни. Ch','<p class=\"rtecenter\">\r\n	&nbsp;</p>\r\n<p class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/01/feelinglines1.png\"><img alt=\"\" class=\"alignleft size-medium wp-image-75\" height=\"234\" src=\"http://www.macov.net/wp-content/uploads/2010/01/feelinglines1-300x234.png\" title=\"feelinglines\" width=\"300\" /></a></p>\r\n<p>\r\n	Компания <strong>Synium Software</strong> открыла бета-тестирование нового приложения под названием <strong>Chronories</strong>, представляющее собой журнал жизни. Chronories позволяет вам вести ежедневные заметки,добавлять в них фотографии и другую графику.</p>\r\n\r\n<p>\r\n	Но возможности его намного шире, чем просто дневник. При первом запуске Chronories предлагает вам ввести ваше место жительства для того, чтобы к вашим записям прикреплять сводку погоды, а также другую сопутствующую информацию об данном дне.Также программа дает полную информацию о том, как вы провели этот день за компьютером: с кем вели беседу, какие программы использовали, какие сайты посетили и много другое. И в дополнение к этому, ведется журнал прослушанных музыкальных треков, приложение интегрируется с iCal, а также присутствует много других интересных &quot;фишек&quot;.</p>\r\n<p class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/01/Chronories01_00.jpg\"><img alt=\"\" class=\"alignnone size-full wp-image-71\" height=\"446\" src=\"http://www.macov.net/wp-content/uploads/2010/01/Chronories01_00.jpg\" title=\"Chronories01_00\" width=\"600\" /></a></p>\r\n<p>\r\n	Таким образом приложение позволяет вести запись вашей жизни перед компьютером, и даже ни один день не будет забыт.</p>\r\n<p>\r\n	Системные требования: Mac OS X 10.5 и выше.</p>\r\n<p>\r\n	официальный сайт:<a href=\"http://www.synium.de/\" title=\"http://www.synium.de/\"> http://www.synium.de/ </a></p>\r\n',NULL,3,1),(65,'Вышла новая версия Snowtape 1.5.4','<p>\r\n	Недавно появилась новая версия <strong>Snowtape 1.5.4</strong> программы для работы с Интернет-радио. На самом деле Snowtape практически полная копия &laquo;Радио&raquo; в iTunes: он имеет сходную классификацию радио-каналов, только пока что у него их меньше (но поддерживается импорт из iTunes).</p>\r\n','<p>\r\n	Недавно появилась новая версия <strong>Snowtape 1.5.4</strong> программы для работы с Интернет-радио. На самом деле Snowtape практически полная копия &laquo;Радио&raquo; в iTunes: он имеет сходную классификацию радио-каналов, только пока что у него их меньше (но поддерживается импорт из iTunes).</p>\r\n\r\n<p>\r\n	Основной особенностью Snowtape является легкость записи трансляций. Запись производится нажатием всего одной кнопки, и этот трек сразу же получит все необходимые теги. Запись можно сохранить в виде файла или сразу экспортировать в iTunes. Так же интересной особенностью Snowtape является возможность редактирования трека перед экспортом. [gallery link=&quot;file&quot;] Что нового: повышено качество экспорта MP3 и AAC появилась лицензия на несколько компьютеров Баг фиксы: Улучшен импорт радиостанций из iTunes Решена проблема с редактированием и экспортом MP3 треков Изменено отображение действий в активной панели Улучшен старт после крушения Системные требования: Mac OS X 10.5.2 и выше. официальный сайт: <a href=\"http://snowtape.com/\" target=\"_blank\" title=\"http://snowtape.com/\">http://snowtape.com/</a></p>\r\n',NULL,3,1),(66,'Джобс показал iPad','<p>\r\n	Последние месяцы по сети ходило множество слухов о новом устройстве компании Apple, которое должно будет занять пропасть между iPhone и MacBook. И наконец-то, 27 января 2010 года Стив Джобс показал его! Встречаем - iPad! Устройство представляет собой планшет, на базе iPhone OS 3.2, разработанной специально для iPad, и не совместимой с iPhone. Планшет оборудован LED-дисплеем диагональю 9.7 дюймов, с разрешением 1024х768 . Экран защищен от следов пальцев специальным покрытием. Устройство мож','<p>\r\n	Последние месяцы по сети ходило множество слухов о новом устройстве компании Apple, которое должно будет занять пропасть между iPhone и MacBook. И наконец-то, 27 января 2010 года Стив Джобс показал его! Встречаем - iPad! Устройство представляет собой планшет, на базе iPhone OS 3.2, разработанной специально для iPad, и не совместимой с iPhone. Планшет оборудован LED-дисплеем диагональю 9.7 дюймов, с разрешением 1024х768 . Экран защищен от следов пальцев специальным покрытием. Устройство может быть укомплектовано 16GB, 32GB или 64GB встроенной памяти. Питание iPad производится от несъемного аккумулятора, достаточного аж на 10 часов непрерывной работы.</p>\r\n<p class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/01/zz7401-27-10ipade132b0.jpg\"><img alt=\"\" class=\"alignnone size-full wp-image-22\" height=\"600\" src=\"http://www.macov.net/wp-content/uploads/2010/01/zz7401-27-10ipade132b0.jpg\" title=\"ipad\" width=\"495\" /></a></p>\r\n\r\n<p>\r\n	Стандартно в iPad установлены следущие программы: Safari - поддержки Flash нет, Mail, Photos, Video, YouTube, iPod, iTunes, App Store, iBooks, Maps - Google карты, Notes, Calendar, Contacts И напоследок крупное фото создателя. На лице читается неподдельная гордость за свое творение.</p>\r\n<p class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/01/apple-creation-0097-rm-eng.jpg\"><img alt=\"\" class=\"alignnone size-full wp-image-26\" height=\"398\" src=\"http://www.macov.net/wp-content/uploads/2010/01/apple-creation-0097-rm-eng.jpg\" title=\"apple-creation-0097-rm-eng\" width=\"600\" /></a></p>\r\n<p>\r\n	Также стали известны цены на новинку: iPad Wi-Fi будет стоить 500-700 долларов, iPad Wi-Fi + 3G &mdash; 630-830 долларов - в зависимости от объема встроенной памяти.</p>\r\n',NULL,3,1),(67,'“It’s a kind of magic” – обзор и сравнение Apple Magic Mouse','<p>\r\n	Как пел всем известный Фреди Мэркури : &ldquo;It&rsquo;s a kind of magic&rdquo;. В компании apple инженеры и дизайнеры не перестают колдовать над той самой магией волшебства своих инноваций. Как вы уже догадались речь в нашей сегодняшней статье пойдет о Apple Magic Mouse. Что такое Magic? Магия, волшебство. В принципе, волшебными можно назвать многие девайсы от Apple. C другой стороны, сейчас уже мультитачем никого не удивить, акселерометром тоже ( датчик движения), но совмещая уже привычн','<p>\r\n	Как пел всем известный Фреди Мэркури : &ldquo;It&rsquo;s a kind of magic&rdquo;. В компании apple инженеры и дизайнеры не перестают колдовать над той самой магией волшебства своих инноваций. Как вы уже догадались речь в нашей сегодняшней статье пойдет о Apple Magic Mouse. Что такое Magic? Магия, волшебство. В принципе, волшебными можно назвать многие девайсы от Apple. C другой стороны, сейчас уже мультитачем никого не удивить, акселерометром тоже ( датчик движения), но совмещая уже привычные технологии в необычных устройствах, получаются по истине произведения искусства.</p>\r\n<p class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/01/1.png\"><img alt=\"Apple Magic Mouse\" height=\"314\" src=\"http://www.macov.net/wp-content/uploads/2010/01/1.png\" title=\"Apple Magic Mouse\" width=\"469\" /></a></p>\r\n\r\n<p>\r\n<!--more-->	<strong>Кто такая </strong><strong>apple </strong><strong>mouse вообще? Немного истории.</strong> Давным давно, в 1984м году, впечатлившись разработками Xerox, Apple выпустили свою первую мышку. Даже тогда в компании &raquo; думали иначе &raquo; и сделали вместо 3х одну кнопку</p>\r\n<p class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/01/lisa-mouse.jpg\"><img alt=\"\" height=\"375\" src=\"http://www.macov.net/wp-content/uploads/2010/01/lisa-mouse.jpg\" title=\"macov net lisa mouse\" width=\"500\" /></a></p>\r\n<p>\r\n	С того момента мыши эволюционировали, но кнопка оставалась одна. Первой USB мышкой стала знаменитая &laquo;Шайба&raquo;</p>\r\n<p class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/01/Untitled.png\"><img alt=\"\" height=\"333\" src=\"http://www.macov.net/wp-content/uploads/2010/01/Untitled.png\" title=\"apple mouse\" width=\"279\" /></a></p>\r\n<p class=\"rtecenter\">\r\n	&nbsp;</p>\r\n<p>\r\n	Дальше в 2000 году был выпущен прадедушка того, что мы сейчас имеем, Apple Pro Mouse.</p>\r\n<p class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/01/Untitled1.png\"><img alt=\"\" height=\"347\" src=\"http://www.macov.net/wp-content/uploads/2010/01/Untitled1.png\" title=\"Apple Pro Mouse\" width=\"517\" /></a></p>\r\n<p>\r\n	Позже Apple pro mouse перерос в Mighty Mouse ( а так же wireless mighty mouse).</p>\r\n<p class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/01/11.png\"><img alt=\"\" height=\"329\" src=\"http://www.macov.net/wp-content/uploads/2010/01/11.png\" title=\"Apple mighty mouse\" width=\"438\" /></a></p>\r\n<p>\r\n	Добавлю от себя,что года 2 использовал майти маус. Мышка была очень удобная. Но колесико, которое крутилось как душе угодно в любой угол засорялось. Приходилось разбирать и чистить, а на одном экземпляре и вовсе умерло. (примечение d567o13sx Меня майти за 3 месяца своим колесиком вывела из состояния душевного равновесия, и момент покупки брутальной Logitech MX1100 был самым счастливым днем за время пользования майти ) И, наконец-то, современный сын mighty mouse, apple magic mouse !</p>\r\n<p class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/01/magic.png\"><img alt=\"Apple Magic Mouse\" height=\"339\" src=\"http://www.macov.net/wp-content/uploads/2010/01/Unt2itled.png\" title=\"Apple Magic Mouse\" width=\"452\" /></a></p>\r\n<p>\r\n	<strong>Apple magic mouse!</strong> Magic Mouse &ndash; устройство очень в стиле Apple. Привнесение удобства, функциональности и красоты в каждый дом за не очень большие деньги. Я получил эту мышку из Сингапура, когда, думаю, здесь она была у единиц. В использовании она у меня 2 месяца. Обо всем по порядку. <strong>Дизайн и удобство.</strong></p>\r\n<p class=\"rtecenter\">\r\n	<strong><a href=\"http://www.macov.net/wp-content/uploads/2010/01/magicmouse.png\"><img alt=\"Apple Magic Mouse\" height=\"351\" src=\"http://www.macov.net/wp-content/uploads/2010/01/Untit33led.png\" title=\"Apple Magic Mouse\" width=\"683\" /></a> </strong></p>\r\n<p>\r\n	Что можно сказать про дизайн? Он прекрасен и лаконичен. Эпловский минимализм, Людям он может нравится, а может и не нравиться, но обратит внимание на продукт каждый. Сверху мышка без каких &ndash; либо острых углов, все гладко. Покрыта прозрачным глянцем. Такое решение помогает не оставлять отпечатков в процессе работе и выглядит красиво. Две основные кнопки спрятаны так, что не понятно даже, есть ли они там или нет. Хотя, по сути, мышка &ndash; сама по себе одна большая кнопка&hellip;</p>\r\n<p class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/01/321.png\"><img alt=\"\" height=\"277\" src=\"http://www.macov.net/wp-content/uploads/2010/01/321.png\" title=\"Apple Magic Mouse\" width=\"414\" /></a></p>\r\n<p>\r\n	Низ выполнен из уже привычного алюминия. Так же, внизу кнопка включения, в стиле блокировки айподов. Индикатор состояния мышки. Отсек для батареек.</p>\r\n<p class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/01/324.png\"><img alt=\"Aple Magic mouse\" height=\"294\" src=\"http://www.macov.net/wp-content/uploads/2010/01/324.png\" title=\"Aple Magic mouse\" width=\"439\" /></a></p>\r\n<p>\r\n	Мышка тяжеловата. Но в процессе использования вес не создает дискомфорта. Размеры мышки малы. Вот сравнение с Razer Copperhead и Logitech g5.</p>\r\n<p class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/01/32425.png\"><img alt=\"сравнение Apple Magic Mouse\" height=\"280\" src=\"http://www.macov.net/wp-content/uploads/2010/01/32425.png\" title=\"сравнение Apple Magic Mouse\" width=\"418\" /></a></p>\r\n<p>\r\n	Да, теперь мышки поставляют не в коробках, как Mighty Mouse, а в стеклянных боксах в стиле iPod. Видимо это дань экологичности, за которую борется Apple.</p>\r\n<p class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/01/12.png\"><img alt=\"apple magic mouse\" height=\"293\" src=\"http://www.macov.net/wp-content/uploads/2010/01/12.png\" title=\"apple magic mouse\" width=\"437\" /></a></p>\r\n<p>\r\n	Внутри коробки, сама мышка и интрукция. На основных языках мира. Русский тоже есть.</p>\r\n<p class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/01/2.png\"><img alt=\"apple magic mouse\" height=\"299\" src=\"http://www.macov.net/wp-content/uploads/2010/01/2.png\" title=\"apple magic mouse\" width=\"447\" /></a></p>\r\n<p>\r\n	<strong>Технические характеристики и процесс использования.</strong> Мышка имеет лазерный сенсор. Питается от двух батареек AA. Подключается по каналу Bluetooth. В процессе работы никакх трудностей не возникает. Подключается быстро, без каких либо проблем. В два клика. Настройка тоже понятна. Лично я заметил, проблему в использовании мышки на скатерти или одеяле. Мышка бывает не очень четко отвечает на перемещение. По ровной поверхности никаких проблем нету. <strong>Софт и возможности:</strong> Как я уже говорил, подключается она оченль легко. Включаете мышку, заходите в меню блютуса, находите, нажимаете просмотр и ставьте галочку запомнить это устройство =&gt;</p>\r\n<p class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/01/3.png\"><img alt=\"apple magic mouse\" height=\"291\" src=\"http://www.macov.net/wp-content/uploads/2010/01/3.png\" title=\"apple magic mouse\" width=\"539\" /></a></p>\r\n<p>\r\n	Всё, через пару секунд мышка подключена!</p>\r\n<p class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/01/4.png\"><img alt=\"apple magic mouse\" height=\"315\" src=\"http://www.macov.net/wp-content/uploads/2010/01/4.png\" title=\"apple magic mouse\" width=\"469\" /></a></p>\r\n<p>\r\n	О возможностях в самой системе. Имеются вот такие настройки:</p>\r\n<p class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/01/5.png\"><img alt=\"apple magic mouse\" height=\"508\" src=\"http://www.macov.net/wp-content/uploads/2010/01/5.png\" title=\"apple magic mouse\" width=\"586\" /></a></p>\r\n<p>\r\n	Стандартные настройки dpi , прокрутки и скорости нажатия. Ниже есть возможность имитации правой кнопки мыши. Причем как на правую кнопку, так и на левую. Прокрутка возможна с инерцией, как на айподе, айфоне в листе контактов например. Так и без неё. Настройки масштаба экрана. Зажав например по стандарту control , можно проводя по поверхности мышки ( по вертикали) увеличить масштаб экрана. И пролистывание фотографий двумя пальцами. Смахиваете два пальца вправо, влево, вперед, назад. Так же работает в браузере, если надо вернуться на предыдущую страницу или на следующую. В настройках так же показывается уровень заряда мышки. Под Windows официальных драйверов пока нет, но есть неофициальные Скачать <a href=\"http://uneasysilence.com/media/2009/11/AppleWirelessMouse3.exe\">32-битную версию драйверов (1,7МВ)</a> <strong>Чем кормить?</strong></p>\r\n<p class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/01/6.png\"><img alt=\"apple magic mouse\" height=\"611\" src=\"http://www.macov.net/wp-content/uploads/2010/01/6.png\" title=\"apple magic mouse\" width=\"408\" /></a></p>\r\n<p>\r\n	Что касаемо заряда,то питается мышь от двух пальчиковых АА. Насколько хватает&hellip; У меня до сих пор стандартные батарейки, шедшие в комплекте. Мышку использую часто, уже 2 месяца. Заряд 69%, судите сами! <strong>Минусы</strong> Из минусов я нашел только один: Глянец в процессе работы начинает царапаться. Но видно это только под углом на свет. <strong>Что почём?</strong> В Москве цена на мышку установилась в среднем около 4000р. Думаю можно найти и подешевле. В принцип, это уж не такие большие деньги за такое устройство. <strong>Заключение</strong> Использовав magic mouse, стало понятно, что это вынесеный на волю тачпад от последний линейки macbook. Думаю владельцам Imac это устройство очень удобно в работе. Имея macbook последних линеек, обойтись без нее, можно легко. Но удобство в работе с этой мышкой, ее дизайн и ощущения в руке, потрясные. Зачем тогда себя ограничивать? Так, что думаю это отличная мышка. И отличный подарок себе или другу маководу на праздник.</p>\r\n<p class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/01/7.png\"><img alt=\"apple magic mouse\" height=\"413\" src=\"http://www.macov.net/wp-content/uploads/2010/01/7.png\" title=\"apple magic mouse\" width=\"618\" /></a></p>\r\n<p>\r\n	С уважением, D1G1tal под чутким руководством d567o13sx</p>\r\n',NULL,3,1),(68,'Новое зарядное устройство от Apple','<p>\r\n	Пока весь мир с нетерпением ждет 27го января, надеясь, что apple покажут все-таки мифический планшет, энтузиасты обнаружили на Норвежском сайте новый вид зарядного устройства. Оно намного компактнее и удобнее продаваемого сейчас, правда для путешествий будет не так удобно &ndash; сменные вилки тут просто некуда вставлять.</p>\r\n<p class=\"rtecenter\">\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/01/power01_00.jpg\"><img alt=\"\" height=\"352\" src=\"http://www.macov.net/wp-content/upload','<p>Пока весь мир с нетерпением ждет 27го января, надеясь, что apple покажут все-таки мифический планшет, энтузиасты обнаружили на Норвежском сайте новый вид зарядного устройства. Оно намного компактнее и удобнее продаваемого сейчас, правда для путешествий будет не так удобно – сменные вилки тут просто некуда вставлять.</p>\r\n<p class=\"rtecenter\"><a href=\"http://www.macov.net/wp-content/uploads/2010/01/power01_00.jpg\"><img alt=\"\" height=\"352\" src=\"http://www.macov.net/wp-content/uploads/2010/01/power01_00.jpg\" title=\"New Power Adapter\" width=\"600\"></a></p>\r\n<p>Цена в переводе с крон – около 40 $ Ссылка на сайт http://store.apple.com/no/product/MB707ZM/A?fnode=MTY1NDA4NA&amp;mco=MTU3MjM2Njk#overview</p>\r\n',NULL,3,1),(69,'Стив Балмер показал «убийцу» iSlate','<p>\r\n	Планшетник от Apple еще не вышел ( по прогнозам аналитиков это состоится 27го января ), а Microsoft на CES2010 показали его потенциального убийцу.</p>\r\n<p>\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/01/00xax24p.jpg\"><img alt=\"Microsoft slate\" height=\"540\" src=\"http://www.macov.net/wp-content/uploads/2010/01/00xax24p.jpg\" title=\"Microsoft slate\" width=\"452\" /></a></p>\r\n','<p>\r\n	Планшетник от Apple еще не вышел ( по прогнозам аналитиков это состоится 27го января ), а Microsoft на CES2010 показали его потенциального убийцу.</p>\r\n<p>\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/01/00xax24p.jpg\"><img alt=\"Microsoft slate\" height=\"540\" src=\"http://www.macov.net/wp-content/uploads/2010/01/00xax24p.jpg\" title=\"Microsoft slate\" width=\"452\" /></a></p>\r\n\r\n<p>\r\n	Устройство разработано совместно с HP, работает под управлением Windows 7, судя по возможности автоматически поворачивать картинку, имеет акселерометр. Так же заявлен мультитач ( хочется верить, что полноценный, а не как в HTC HD2 ). Сообщается, что гаджет сможет продержаться&nbsp;день без подзарядки.</p>\r\n<p>\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/01/00xaw6as.jpg\"><img alt=\"Microsoft tablet\" height=\"461\" src=\"http://www.macov.net/wp-content/uploads/2010/01/00xaw6as.jpg\" title=\"Microsoft tablet\" width=\"378\" /></a></p>\r\n<p>\r\n	Посмотрим, чем на это ответит Apple.</p>\r\n',NULL,3,1),(70,'Mac or PC?','<div id=\"_mcePaste\">\r\n	Можно до бесконечности спорить о том, что же лучше, Mac, или PC, каждый из оппонентов всегда найдет доводы в свою пользу.<strong> Furia &amp; Beau-J </strong>подошли к вопросу творчески.</div>\r\n<div id=\"_mcePaste\">\r\n	Получилось забавно, и весьма неплохо.</div>\r\n<div>\r\n	<object classid=\"clsid:d27cdb6e-ae6d-11cf-96b8-444553540000\" codebase=\"http://download.macromedia.com/pub/shockwave/cabs/flash/swflash.cab#version=6,0,40,0\" height=\"344\" width=\"425\"><param name=\"allowFullScr','<div id=\"_mcePaste\">\r\n	Можно до бесконечности спорить о том, что же лучше, Mac, или PC, каждый из оппонентов всегда найдет доводы в свою пользу.<strong> Furia &amp; Beau-J </strong>подошли к вопросу творчески.</div>\r\n<div id=\"_mcePaste\">\r\n	Получилось забавно, и весьма неплохо.</div>\r\n<div>\r\n	<object classid=\"clsid:d27cdb6e-ae6d-11cf-96b8-444553540000\" codebase=\"http://download.macromedia.com/pub/shockwave/cabs/flash/swflash.cab#version=6,0,40,0\" height=\"344\" width=\"425\"><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><param name=\"src\" value=\"http://www.youtube.com/v/Jkrn6ecxthM&amp;hl=ru_RU&amp;fs=1&amp;\" /><param name=\"allowfullscreen\" value=\"true\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"344\" src=\"http://www.youtube.com/v/Jkrn6ecxthM&amp;hl=ru_RU&amp;fs=1&amp;\" type=\"application/x-shockwave-flash\" width=\"425\"></embed></object></div>\r\n\r\n<div>\r\n	&nbsp;</div>\r\n<div id=\"_mcePaste\">\r\n	В качестве добавки новый клип ребят с сайта <strong>switchtomac.com</strong></div>\r\n<div id=\"_mcePaste\">\r\n	Вот они, настоящие фанаты!</div>\r\n<div>\r\n	<object classid=\"clsid:d27cdb6e-ae6d-11cf-96b8-444553540000\" codebase=\"http://download.macromedia.com/pub/shockwave/cabs/flash/swflash.cab#version=6,0,40,0\" height=\"340\" width=\"560\"><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><param name=\"src\" value=\"http://www.youtube.com/v/T8FnACj25xM&amp;hl=ru_RU&amp;fs=1&amp;\" /><param name=\"allowfullscreen\" value=\"true\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"340\" src=\"http://www.youtube.com/v/T8FnACj25xM&amp;hl=ru_RU&amp;fs=1&amp;\" type=\"application/x-shockwave-flash\" width=\"560\"></embed></object></div>\r\n',NULL,3,1),(71,'The Settlers для iPhone','<p>\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/06/settlers231.jpg\"><img alt=\"\" class=\"aligncenter size-full wp-image-587\" height=\"320\" src=\"http://www.macov.net/wp-content/uploads/2010/06/settlers231.jpg\" title=\"settlers231\" width=\"480\" /></a></p>\r\n<p>\r\n	Еще одна потрясающая игра от gameloft! На этот раз точь-в-точь портированная pc версия <strong>Settlers</strong>! Многие наверняка уже расплылись в довольной улыбке, вспоминая ночи, проведенные за старым PC.</p>\r\n','<p>\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/06/settlers231.jpg\"><img alt=\"\" class=\"aligncenter size-full wp-image-587\" height=\"320\" src=\"http://www.macov.net/wp-content/uploads/2010/06/settlers231.jpg\" title=\"settlers231\" width=\"480\" /></a></p>\r\n<p>\r\n	Еще одна потрясающая игра от gameloft! На этот раз точь-в-точь портированная pc версия <strong>Settlers</strong>! Многие наверняка уже расплылись в довольной улыбке, вспоминая ночи, проведенные за старым PC.</p>\r\n\r\n<p>\r\n<!--more-->	Поражает уровень оптимизации, графика на высоте, производительность не отстает. Это 100% хит. Отныне судьба поселенцев снова в ваших руках, стройте свой город, налаживаейте отношения с другими поселениями и не забывайте про армию! Спешите приобрести,<a href=\"http://itunes.apple.com/ru/app/the-settlers/id337938346?mt=8\"> в iTunes всего за $0.99</a>! Или <a href=\"http://narod.ru/disk/14853576000/The%20Settlers%20v1.1.1by%20SejRiz.ipa.html\">скачать cracked ipa</a>.</p>\r\n<p>\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/06/settlers23.jpg\"><img alt=\"\" class=\"aligncenter size-full wp-image-586\" height=\"320\" src=\"http://www.macov.net/wp-content/uploads/2010/06/settlers23.jpg\" title=\"settlers23\" width=\"480\" /></a><a href=\"http://www.macov.net/wp-content/uploads/2010/06/settlers2.jpg\"><img alt=\"\" class=\"aligncenter size-full wp-image-585\" height=\"320\" src=\"http://www.macov.net/wp-content/uploads/2010/06/settlers2.jpg\" title=\"settlers2\" width=\"480\" /></a><a href=\"http://www.macov.net/wp-content/uploads/2010/06/settlers.jpg\"><img alt=\"\" class=\"aligncenter size-full wp-image-584\" height=\"320\" src=\"http://www.macov.net/wp-content/uploads/2010/06/settlers.jpg\" title=\"settlers\" width=\"480\" /></a></p>\r\n',NULL,3,1),(72,'Tap Tap Revenge 3 Nickelback','<p>\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/06/img1.jpg\"><img alt=\"\" class=\"aligncenter size-full wp-image-596\" height=\"320\" src=\"http://www.macov.net/wp-content/uploads/2010/06/img1.jpg\" title=\"img1\" width=\"480\" /></a></p>\r\n<p>\r\n	Еще одна часть очень затягивающей игры для iPhone/iPod. <strong>TapTap Revenge</strong> теперь с Nickelback. Комбинация великолепной музыки и &quot;тапательный&quot; геймплей, что еще нужно, чтобы скоротать время? Главное не отвлекаться!</p>\r\n','<p>\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/06/img1.jpg\"><img alt=\"\" class=\"aligncenter size-full wp-image-596\" height=\"320\" src=\"http://www.macov.net/wp-content/uploads/2010/06/img1.jpg\" title=\"img1\" width=\"480\" /></a></p>\r\n<p>\r\n	Еще одна часть очень затягивающей игры для iPhone/iPod. <strong>TapTap Revenge</strong> теперь с Nickelback. Комбинация великолепной музыки и &quot;тапательный&quot; геймплей, что еще нужно, чтобы скоротать время? Главное не отвлекаться!</p>\r\n\r\n<p>\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/06/img2.jpg\"><img alt=\"\" class=\"aligncenter size-full wp-image-597\" height=\"480\" src=\"http://www.macov.net/wp-content/uploads/2010/06/img2.jpg\" title=\"img2\" width=\"320\" /></a><a href=\"http://www.macov.net/wp-content/uploads/2010/06/img.jpg\"><img alt=\"\" class=\"aligncenter size-full wp-image-595\" height=\"480\" src=\"http://www.macov.net/wp-content/uploads/2010/06/img.jpg\" title=\"img\" width=\"320\" /></a></p>\r\n<p>\r\n	<em>Игра затягивает, как и музыка :)</em> Список песен. * Animals * Animals (Live) * Burn It to the Ground * Figured You Out * Gotta Be Somebody * How You Remind Me * Photograph * Rockstar * Something In Your Mouth * This Afternoon * If Today Was Your Last Day Распродаж нет, поэтому в<a href=\"http://itunes.apple.com/ru/app/nickelback-revenge/id378029159?mt=8\"> iTunes за 5 долларов</a>.</p>\r\n',NULL,3,1),(73,'Amazon Kindle для macOSX','<p>\r\n	Компания Amazon, похоже, твердо решила не сдавать свои позиции на рынке электронных книг, и в преддверии выхода iPad на рынок выпустила Kindle для Mac.</p>\r\n','<p>\r\n	Компания Amazon, похоже, твердо решила не сдавать свои позиции на рынке электронных книг, и в преддверии выхода iPad на рынок выпустила Kindle для Mac.</p>\r\n\r\n<p>\r\n	Приложение способно выполнять три главные функции - покупать книги с Amazon, читать эти книги, а так же добавлять комментарии к ним. И последнее на что приложение способно это синхронизировать ваш Amazon Kindle с компьютером с сохранением всех ваших коментариев, закладок, и прочитанных страниц. Системные требования: Mac OS X 10.5 и выше. Официальный сайт: <a href=\"http://amazon.com/\">http://amazon.com/</a> Скачать приложение можно <a href=\"http://kindleformac.amazon.com/27214/KindleForMac.dmg\">здесь.</a></p>\r\n',NULL,3,1),(74,'DaisyDisk 1.5 - поиск больших файлов','<p>\r\n	Появилась новая версия утилиты для поиска крупногабаритных файлов &mdash;<strong> DaisyDisk 1.5. </strong><a href=\"http://www.macov.net/wp-content/uploads/2010/02/daisydisk02_00.jpg\"><img alt=\"\" class=\"aligncenter size-full wp-image-117\" height=\"158\" src=\"http://www.macov.net/wp-content/uploads/2010/02/daisydisk02_00.jpg\" title=\"daisydisk02_00\" width=\"600\" /></a></p>\r\n<div id=\"_mcePaste\">\r\n	Это приложение сканирует диски и показывает секторную диаграмму по папкам: чем больше та или иная па','<p>\r\n	Появилась новая версия утилиты для поиска крупногабаритных файлов &mdash;<strong> DaisyDisk 1.5. </strong><a href=\"http://www.macov.net/wp-content/uploads/2010/02/daisydisk02_00.jpg\"><img alt=\"\" class=\"aligncenter size-full wp-image-117\" height=\"158\" src=\"http://www.macov.net/wp-content/uploads/2010/02/daisydisk02_00.jpg\" title=\"daisydisk02_00\" width=\"600\" /></a></p>\r\n<div id=\"_mcePaste\">\r\n	Это приложение сканирует диски и показывает секторную диаграмму по папкам: чем больше та или иная папка, тем больше размер ее сектора. Таким образом будет легко определить где искать скопления тяжеловесных файлов.</div>\r\n\r\n<div>\r\n	&nbsp;</div>\r\n<div id=\"_mcePaste\">\r\n	Кроме интересного интерфейса необходимо отметить удобную навигацию по папкам. При нажатии на участок диаграммы откроется соответствующая папка, и будет наглядно показан размер ее файлов и под папок. Нажатием на центр перейдете в корневую папку. Если навести курсор мышки к какому-либо сектору, справа появится информация о папке. Так же примечательно наличие русского языка в приложении.</div>\r\n<div>\r\n	<a href=\"http://www.macov.net/wp-content/uploads/2010/02/mainScreenshot2.png\"><img alt=\"\" class=\"aligncenter size-full wp-image-120\" height=\"450\" src=\"http://www.macov.net/wp-content/uploads/2010/02/mainScreenshot2.png\" title=\"mainScreenshot2\" width=\"557\" /></a></div>\r\n<div id=\"_mcePaste\">\r\n	Системные требования: Mac OS X 10.5 и выше.</div>\r\n<div>\r\n	официальный сайт: <a href=\"http://www.daisydiskapp.com/\" target=\"_blank\" title=\"http://www.daisydiskapp.com/\">http://www.daisydiskapp.com/</a></div>\r\n',NULL,3,1),(75,'ВИДЕО: Неофициальный промо-ролик iPhone 5.','<p><span style=\"font-size:16px;\"><strong>Неофициальный промо-ролик iPhone 5.</strong></span></p>\r\n<p>&nbsp;</p>\r\n<p><iframe allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"http://www.youtube.com/embed/qFUz6vHEQCM\" width=\"560\"></iframe></p>\r\n<p>&nbsp;</p>\r\n<p>Ребята, снявшие этот ролик двольно популярны на ютубе. Многие их помнят по предыдущим промо-роликам, в которых парни рассказывают с юмором о новых устройствах Apple.</p>\r\n','<p><span style=\"font-size:16px;\"><strong>Неофициальный промо-ролик iPhone 5.</strong></span></p>\r\n<p>&nbsp;</p>\r\n<p><iframe allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"http://www.youtube.com/embed/qFUz6vHEQCM\" width=\"560\"></iframe></p>\r\n<p>&nbsp;</p>\r\n<p>Ребята, снявшие этот ролик двольно популярны на ютубе. Многие их помнят по предыдущим промо-роликам, в которых парни рассказывают с юмором о новых устройствах Apple.</p>\r\n',NULL,3,1),(76,'Mac App Store начнет работать 13 декабря?','<p>\r\n	<span class=\"Apple-style-span\" style=\"color: rgb(68, 68, 68); \">Ни для кого не секрет, что <strong>Mac App Store </strong>запустится уже в ближайшее время. На презентации 20 октября Джобс лично заявил, что все заработает в течение 90 дней - а это как раз середина Января. Но как сообщают&nbsp;&nbsp;MacRumors,&nbsp;есть инсайдерская информация, что Apple пытаются запустить<strong> Mac App Store</strong> до рождества, заставляя производителей ПО подготовить свои продукты к понедельнику, 6 дек','\r\n<p>\r\n<span class=\"Apple-style-span\" style=\"color: rgb(68, 68, 68); \">Ни для кого не секрет, что <strong>Mac App Store </strong>запустится уже в ближайшее время. На презентации 20 октября Джобс лично заявил, что все заработает в течение 90 дней - а это как раз середина Января. Но как сообщают&nbsp;&nbsp;MacRumors,&nbsp;есть инсайдерская информация, что Apple пытаются запустить<strong> Mac App Store</strong> до рождества, заставляя производителей ПО подготовить свои продукты к понедельнику, 6 декабря, что бы 13го декабря уже начать работать.</span></p>\r\n<div class=\"post_body\" style=\"margin-top: 10px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; padding-top: 0px; padding-right: 0px; padding-bottom: 0px; padding-left: 0px; font-family: georgia; font-size: 14px; line-height: 19px; color: rgb(68, 68, 68); letter-spacing: 0px; \">\r\n<p class=\"rtecenter\">\r\n<img alt=\"Mac App Store\" src=\"http://macov.net/sites/default/files/appstorehero20101020-small.jpg\" style=\"width: 600px; height: 360px; \" /></p>\r\n<p class=\"rtecenter\">\r\n</p>\r\n<p class=\"rteleft\" style=\"font-family: Arial, Tahoma, Helvetica, sans-serif; font: normal normal normal 13px/160% Arial, Tahoma, Helvetica, sans-serif; \">\r\nСтоит отметить, что для работы Mac App Store необходимо обновление операционной системы, а, как известно, совсем недавно финальная версия &nbsp;OS X 10.6.6 была разослана тестерам, что еще раз указывает на скорые сроки запуска магазина. Как говорится, Stay tuned!</p>\r\n</div>\r\n',NULL,3,1),(77,'Оригинальный Camera Connection Kit, или более функциональный аналог?','<p>\r\n	Будем честны с собой - практически ни одна китайская подделка устройств Apple не смогла по качеству даже близко подойти&nbsp;к оригиналу. Но новый 3-в-1 <strong>iPad Camera Connection Kit</strong>, доступный как в белом, так и в черном цвете - скорее исключение из общего правила. Он совмещает в себе сразу 3 порта - USB, SD и MicroSD, позволяя загружать на iPad видео и фото практически с любого цифрового носителя. И все это в одном устройстве, в отличие от 2х раздельных от Apple.&nbsp;</p>\r','<p>\r\n	Будем честны с собой - практически ни одна китайская подделка устройств Apple не смогла по качеству даже близко подойти&nbsp;к оригиналу. Но новый 3-в-1 <strong>iPad Camera Connection Kit</strong>, доступный как в белом, так и в черном цвете - скорее исключение из общего правила. Он совмещает в себе сразу 3 порта - USB, SD и MicroSD, позволяя загружать на iPad видео и фото практически с любого цифрового носителя. И все это в одном устройстве, в отличие от 2х раздельных от Apple.&nbsp;</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"iPad Camera\" src=\"http://macov.net/sites/default/files/ipad-3-in-1-camera-kit.jpg\" style=\"width: 497px; height: 328px; \" /></p>\r\n<p>\r\n	<a href=\"http://macov.net/news/original-ipad-camera-connection-kit-ili\">Подробнее...</a></p>\r\n\r\n<p>\r\n	&nbsp;</p>\r\n<p style=\"font-family: Arial, Tahoma, Helvetica, sans-serif; font: normal normal normal 13px/160% Arial, Tahoma, Helvetica, sans-serif; \">\r\n	Будем честны с собой - практически ни одна китайская подделка устройств Apple не смогла по качеству даже близко подойти&nbsp;к оригиналу. Но новый 3-в-1&nbsp;<strong>iPad Camera Connection Kit</strong>, доступный как в белом, так и в черном цвете - скорее исключение из общего правила. Он совмещает в себе сразу 3 порта - USB, SD и MicroSD, позволяя загружать на iPad видео и фото практически с любого цифрового носителя. И все это в одном устройстве, в отличие от 2х раздельных от Apple.&nbsp;</p>\r\n<p class=\"rtecenter\" style=\"font-family: Arial, Tahoma, Helvetica, sans-serif; font: normal normal normal 13px/160% Arial, Tahoma, Helvetica, sans-serif; text-align: center; \">\r\n	<img alt=\"iPad Camera\" src=\"http://macov.net/sites/default/files/ipad-3-in-1-camera-kit.jpg\" style=\"width: 497px; height: 328px; \" /></p>\r\n<p style=\"font-family: Arial, Tahoma, Helvetica, sans-serif; font: normal normal normal 13px/160% Arial, Tahoma, Helvetica, sans-serif; \">\r\n	Если бы у вас была необходимость такого аксессуара - какой вариант выбрали бы вы?</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	&nbsp;</p>\r\n',NULL,3,1),(78,'Incase выпустили Snap Battery Case для iPhone 4','<p>\r\n	Буквально на днях компания<strong> Incase </strong>выпустила новый чехол для iPhone 4 со встроенной батареей, способный продлить время жизни вашего устройства до 40%. Название новинки -<strong>&nbsp;Incase Snap Battery Case для iPhone 4</strong>.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"incase Snap \" src=\"http://macov.net/sites/default/files/incase_snap_extended_battery_iphone_4_case_1.jpg\" style=\"width: 600px; height: 418px; \" /></p>\r\n<p>\r\n	<a href=\"http://macov.net/news/incase-snap-battery','<p>\r\n	Буквально на днях компания<strong> Incase </strong>выпустила новый чехол для iPhone 4 со встроенной батареей, способный продлить время жизни вашего устройства до 40%. Название новинки -<strong>&nbsp;Incase Snap Battery Case для iPhone 4</strong>.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"incase Snap \" src=\"http://macov.net/sites/default/files/incase_snap_extended_battery_iphone_4_case_1.jpg\" style=\"width: 600px; height: 418px; \" /></p>\r\n<p>\r\n	<a href=\"http://macov.net/news/incase-snap-battery-for-iphone-4\">Подробнее...</a></p>\r\n\r\n<p>\r\n	&nbsp;</p>\r\n<p style=\"font-family: Arial, Tahoma, Helvetica, sans-serif; font: normal normal normal 13px/160% Arial, Tahoma, Helvetica, sans-serif; \">\r\n	Буквально на днях компания<strong>&nbsp;Incase&nbsp;</strong>выпустила новый чехол для iPhone 4 со встроенной батареей, способный продлить время жизни вашего устройства до 40%. Название новинки -<strong>&nbsp;Incase Snap Battery Case для iPhone 4</strong>.</p>\r\n<p class=\"rtecenter\" style=\"font-family: Arial, Tahoma, Helvetica, sans-serif; font: normal normal normal 13px/160% Arial, Tahoma, Helvetica, sans-serif; text-align: center; \">\r\n	<img alt=\"incase Snap \" src=\"http://macov.net/sites/default/files/incase_snap_extended_battery_iphone_4_case_1.jpg\" style=\"width: 600px; height: 418px; \" /></p>\r\n<p style=\"font-family: Arial, Tahoma, Helvetica, sans-serif; font: normal normal normal 13px/160% Arial, Tahoma, Helvetica, sans-serif; \">\r\n	Чехол оснащен встроенным 900 mAh аккумулятором, корпусы выполнен из приятного на ощупь софт - тач пластика, внизу расположен классический 30ти пиновый разьем, через который осуществляется зарядка и синхронизация устройства ( провод в комплекте ). Для того, что бы посмотреть уровень заряда аккумулятора достаточно нажать на кнопку на задней панели.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"Incase snap\" src=\"http://macov.net/sites/default/files/iphone4-battery-case.jpg\" style=\"width: 600px; height: 380px; \" /></p>\r\n<p>\r\n	В США чехол стоит на 30 долларов дешевле своего прямого конкурента - <strong>Mophie Juice Pack Air</strong>, к тому же он немного тоньше.&nbsp;</p>\r\n<p>\r\n	В целом<strong> Incase Snap Battery Case</strong> получился весьма неплохим устройством - он будет незаменим, если вы часто смотрите видео в дороге, много разговариваете или играете в игры.</p>\r\n<p>\r\n	Ссылка на официальную презентацию Incase&nbsp;http://goincase.com/blog/2010/12/09/introducing-the-snap-battery-case-for-iphone-4/</p>\r\n<p>\r\n	&nbsp;</p>\r\n',NULL,1,1),(79,'А у нас Новый Год!','<p>\r\n	<img alt=\"\" src=\"http://macov.net/sites/default/files/NEWYEAR.jpg\" style=\"width: 800px; height: 533px; \" /></p>\r\n','<p>\r\n	<img alt=\"\" src=\"http://macov.net/sites/default/files/NEWYEAR.jpg\" style=\"width: 800px; height: 533px; \" /></p>\r\n',NULL,3,1),(80,'Новости новогодних каникул','<p>\r\n	Вот и прошли новогодние праздники и <strong>macov.net</strong> поздравляет вас с первым полноценным рабочим днем. Это первая, по настоящему трудовая неделя. Неделя, которая является переходным этапом между праздным бездельем и напряженной работой.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/newyear copy.jpg\" style=\"width: 300px; height: 300px; \" /></p>\r\n<p>\r\n	Мы вам желаем, чтобы прошла она так же легко и непринужденно, как и праздничные каникулы, которые принесли ст','<p>\r\n	Вот и прошли новогодние праздники и <strong>macov.net</strong> поздравляет вас с первым полноценным рабочим днем. Это первая, по настоящему трудовая неделя. Неделя, которая является переходным этапом между праздным бездельем и напряженной работой.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/newyear copy.jpg\" style=\"width: 300px; height: 300px; \" /></p>\r\n<p>\r\n	Мы вам желаем, чтобы прошла она так же легко и непринужденно, как и праздничные каникулы, которые принесли столько нового и интересного. Давайте посмотрим, что произошло в мире <strong>Apple</strong> за эти каникулы.</p>\r\n\r\n<div>\r\n	<span class=\"Apple-style-span\" style=\"font-size: 20px; font-weight: bold; \">Открытие Mac App Store</span>\r\n	<p>\r\n		<br />\r\n		Если вы пропустили, то мы напомним - 6 января 2011 года вышло обновление <strong>Mac OS X 10.6.6</strong>, которое принесло долгожданный <strong>Mac App Store</strong>. Этого неоднозначного события ждали как противники, так и доброжелатели. Первые, чтобы иметь очередной повод для критики, вторые, чтобы иметь удобный способ получать платные и не очень приложения на свои mac-устройства.</p>\r\n	<p class=\"rtecenter\">\r\n		<img alt=\"\" src=\"/sites/default/files/10.1_1.png\" style=\"width: 500px; height: 182px; \" /></p>\r\n	<p>\r\n		Магазин открыт, за первые несколько дней было сделано более <strong>1 миллиона загрузок</strong>. Беспорными хитами стали <strong>Twitter for Mac</strong> среди бесплатных приложений и хорошо всем знакомая игра <strong>Angry Birds</strong> среди платных.</p>\r\n	<p class=\"rtecenter\">\r\n		<img alt=\"\" src=\"/sites/default/files/10.1.png\" style=\"width: 500px; height: 167px; \" /></p>\r\n	<p>\r\n		Десктопный <strong>Twitter for Mac</strong> стал продолжателем идей <strong>Tweetie 2</strong> со всеми его плюсами и минусами. Перечисление их достойно отдельной статьи. Angry Birds же является десктопной версией одноименной игры для мобильных платформ, портированной на mac. Она принесла возможность использовать большой тачпад не только как замена традиционной мышки, но и как игрового манипулятора. Хотя <strong>iPad</strong> в этом отношении все равно ни с чем не сравнить.</p>\r\n	<h2>\r\n		Выставка CES 2011</h2>\r\n	<p>\r\n		<br />\r\n		Хотя эта выставка в <strong>Лас Вегасе</strong> и не коснулась <strong>Apple</strong>, но она явила миру просто огромное количество разнообразных гаджетов.</p>\r\n	<p class=\"rtecenter\">\r\n		<img alt=\"\" src=\"/sites/default/files/ces_logo.png\" style=\"width: 300px; height: 190px; \" /></p>\r\n	<p>\r\n		Больше десятка планшетов на <strong>Android</strong> и <strong>Windows 7</strong>. Представление мобильной операционной системы <strong>Android 3.0 Honeycomb</strong> оптимизированной для работы на планшетах.</p>\r\n	<p class=\"rtecenter\">\r\n		<img alt=\"\" src=\"/sites/default/files/3_1_Honeycomb.png\" style=\"width: 300px; height: 166px; \" /></p>\r\n	<p>\r\n		Была представлена платформа <strong>Intel Sandy Bridge</strong> и много ноутбуков на ее основе. И смартфоны, смартфоны, смартфоны...</p>\r\n	<h2>\r\n		Слухи о iPad 2</h2>\r\n	<p>\r\n		<br />\r\n		Ну и просто колоссальный объем слухов по поводу выхода нового поколения iPad. Изобилие разнообразных слухов сводилось к одному - он будет, ориентировочно в феврале. Будет тоньше и легче, иметь 2 камеры, экран с большим разрешением, звук громче, с разъемом USB.</p>\r\n	<p class=\"rtecenter\">\r\n		<img alt=\"\" src=\"/sites/default/files/ipad_2_mockup_1.png\" /></p>\r\n	<p>\r\n		С каждым днем некоторые слухи находят подтверждение, другие опровергаются. Как будет на самом деле - покажет время.</p>\r\n	<p>\r\n		А мы постараемся держать вас в курсе событий.</p>\r\n</div>\r\n',NULL,3,1),(81,'Verizon iPhone 4 - свершилось','<p>\r\n	Таки свершилось - мир увидел это. В следующем месяце начинаются продажи <strong>CDMA</strong> версии iPhone 4. Как я и предполагал Стив Джобс не посетил <a href=\"http://macov.net/news/iphone-cdma-verizon\">Мероприятия</a>, но его почтил своим вниманием Тим Кук - более чем достаточно.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/verizon-iphone-0993.jpg\" style=\"width: 500px; height: 332px; \" /></p>\r\n<p>\r\n	Чем это грозит отечественному рынку и будет ли возможность у нас в','<p>\r\n	Таки свершилось - мир увидел это. В следующем месяце начинаются продажи <strong>CDMA</strong> версии iPhone 4. Как я и предполагал Стив Джобс не посетил <a href=\"http://macov.net/news/iphone-cdma-verizon\">Мероприятия</a>, но его почтил своим вниманием Тим Кук - более чем достаточно.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/verizon-iphone-0993.jpg\" style=\"width: 500px; height: 332px; \" /></p>\r\n<p>\r\n	Чем это грозит отечественному рынку и будет ли возможность у нас воспользоваться этими аппаратами пока не известно. Но думаю, нет ничего невозможного.</p>\r\n',NULL,3,1),(82,'Обновление Twitter for Mac','<p>\r\n	Не прошло и недели, а <a href=\"http://itunes.apple.com/us/app/twitter/id409789998\"><strong>Twitter for Mac</strong></a> уже подготовил свежее обновление под номером 2.0.1. Причем обновление это выглядит как работа над ошибками, которую следовало провести до запуска приложения.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/twitter_doc_nom.png\" style=\"width: 500px; height: 174px; \" /></p>\r\n<p>\r\n	Отсутствие Beta-версий в Mac App Store заставляет разработчиков вносить изме','<p>\r\n	Не прошло и недели, а <a href=\"http://itunes.apple.com/us/app/twitter/id409789998\"><strong>Twitter for Mac</strong></a> уже подготовил свежее обновление под номером 2.0.1. Причем обновление это выглядит как работа над ошибками, которую следовало провести до запуска приложения.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/twitter_doc_nom.png\" style=\"width: 500px; height: 174px; \" /></p>\r\n<p>\r\n	Отсутствие Beta-версий в Mac App Store заставляет разработчиков вносить изменения уже по ходу использования релиза. Не считаю это ошибочным, наоборот, массовое недовольство приведет к скорейшим изменениям в лучшую сторону.</p>\r\n\r\n<h2>\r\n	ЧТО ИЗМЕНИЛОСЬ</h2>\r\n<p>\r\n	<br />\r\n	Первое, что бросилось в глаза - цвет иконки в доке сменил траурно-черный цвет на приятный синий. Было ли это сделано в угоду HIG (Human Interface Guidelines), который предписывает иметь иконку синего цвета, либо сами догадались что так будет лучше - получилось здорово.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/twitter_doc.png\" style=\"width: 500px; height: 182px; \" /></p>\r\n<p>\r\n	Были внесены изменения технического характера. Впрочем я с такими проблемами не сталкивался, мне судить сложно.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/twitter_update.png\" /></p>\r\n<p>\r\n	Из полезного для меня хочу отметить, что теперь иконка в меню-баре горит только при наличии непрочитанных твитов той учетной записи, которая активна. Ранее, если у вас было подключено несколько аккаунтов, иконка горела до тех пор, пока не будут прочитаны абсолютно все сообщения.</p>\r\n<h2>\r\n	ВЫВОДЫ</h2>\r\n<p>\r\n	<br />\r\n	Рекомендую прежде чем проводить обновление - закрыть программу. В противном случае MAS не засчитает обновление установленным и предложить обновить еще раз.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/twitter_ready.png\" style=\"width: 500px; height: 129px; \" /></p>\r\n<p>\r\n	Обновление годное, стоит сделать, хотя бы ради того, чтобы посмотреть сам механизм обновления Mac App Store.</p>\r\n<br />\r\n<p>\r\n	&nbsp;</p>\r\n',NULL,3,1),(83,'Инструкция к обновленному iWork.com','<p>\r\n	Вчера обновился сервис <a href=\"http://iwork.com\">iWork.com beta</a> одноименного пакета офисных приложений. Он позволяет обмениваться вашими документами через интернет, а так же является онлайн-хранилищем этих документов. Ничего революционного, Microsoft и Google уже давно реализовали подобные продукты.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/key%230.jpg\" style=\"width: 468px; height: 188px; \" /></p>\r\n<p>\r\n	Но для Apple это событие, особенно после длительного зат','<p>\r\n	Вчера обновился сервис <a href=\"http://iwork.com\">iWork.com beta</a> одноименного пакета офисных приложений. Он позволяет обмениваться вашими документами через интернет, а так же является онлайн-хранилищем этих документов. Ничего революционного, Microsoft и Google уже давно реализовали подобные продукты.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/key%230.jpg\" style=\"width: 468px; height: 188px; \" /></p>\r\n<p>\r\n	Но для Apple это событие, особенно после длительного затишья во всем, что связано с iWork. Означает ли это, что выход новой 11 версии уже не за горами или нет - я предлагаю пользоваться тем, что есть уже сейчас - это действительно интересный сервис. Зачем этот сервис нужен и как им пользоваться я расскажу в этой статье.</p>\r\n\r\n<h2>\r\n	Зачем это нужно</h2>\r\n<p>\r\n	<br />\r\n	Хочу показать пример использования этого сервиса. Недавно столкнулся с проблемой - как удаленно показать презентацию, сделанную в <strong>Keynote.app</strong> нескольким пользователям, чтобы у них не возникло проблем с просмотром. Я остановился на PDF - презентации, которая весила довольно прилично, и не все ее даже открывали.</p>\r\n<p>\r\n	Был еще вариант разместить ее на одном из многочисленных хостингах для презентаций, но не стал. Некоторые сервисы довольно запутанные для пользователя, другие грешат жутко медленной скоростью. Теперь появилась возможность создать веб-презентацию быстро и в привычном редакторе, чтобы в несколько касаний поместить ее на хостинг и показать другим. Но обо всем по порядку.</p>\r\n<h2>\r\n	Как это сделать</h2>\r\n<p>\r\n	<br />\r\n	Я создам простую презентацию в <strong>Keynote.app</strong> и покажу как все это делается.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/key%231.png\" style=\"width: 500px; height: 178px; \" /></p>\r\n<p>\r\n	Надеюсь все из вас умеют пользоваться этим редактором - если нет, рекомендую вам научиться, действительно удобный и простой инструмент. Помещаем наш текст и картинки в нужном нам порядке, создаем слайды.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/key%232_1.png\" style=\"width: 500px; height: 369px; \" />&nbsp;&nbsp; &nbsp;&nbsp;</p>\r\n<p>\r\n	Нажимаем кнопку <strong><em>&quot;Опубликовать&quot;</em></strong>, которая расположена в верхнем меню и выглядит как желтый конверт со стрелкой.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/key%232_2.png\" style=\"width: 550px; height: 95px; \" /></p>\r\n<p>\r\n	Перед нами появляется меню, которое позволяет опубликовать документ для конкретных пользователей, для всех или для персонального использования.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/key%236.png\" style=\"width: 400px; \" /></p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/key%235.png\" style=\"width: 400px; height: 372px; \" /></p>\r\n<p>\r\n	Выбираем нужный пункт и отправляем документ на сервер iWork. Замечу, скорость загрузки сейчас довольно высокий. Не знаю, что произойдет, когда им начнут массово пользоваться, но не зря же Apple строит свои огромные дата-центры.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/key%237.png\" style=\"width: 460px; height: 201px; \" /></p>\r\n<p>\r\n	После окончания загрузки предоставляется возможность скопировать ссылку на документ либо получить код, для вставки этого документа на веб-страницу. Очень удобно разместить эту презентацию на своем сайте, если вы хотите ознакомить пользователей с какой либо информацией. Это может быть, например, пошаговая инструкция для пользователей или еще что нибудь.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/key%238.png\" style=\"width: 482px; height: 286px; \" /></p>\r\n<p>\r\n	Стоит отметить кросплатформенность такой презентации. Не смотря на страшные уведомления о не полной поддержке браузера, моя простая презентация открылась во всех имеющихся у меня браузерах (Chrome, Firefox, Opera).</p>\r\n<h2>\r\n	Как это выглядит</h2>\r\n<p>\r\n	<br />\r\n	Сам сервис выглядит очень приятно. Минималистичная страница входа уже как бы намекает на то, что вы идете работать с документами.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/key%233.png\" style=\"width: 500px; height: 337px; \" /></p>\r\n<p>\r\n	Вот так выглядит рабочее пространство. Пока еще пустое. Видим, что под документы отводится <strong>1 Гб</strong>, это не так уж и мало для документов, да и сервис пока не выглядит довольно привлекательным для облачного хранения документов.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/key%234.png\" style=\"width: 500px; height: 337px; \" /></p>\r\n<p>\r\n	Сама презентация на сервисе выглядит следующим образом.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/key%2310.png\" style=\"width: 500px; height: 404px; \" /></p>\r\n<p>\r\n	В режиме просмотра презентации это будет выглядеть довольно симпатично. Меню управления не имеет лишних или ненужных функций, только самое необходимое для просмотра презентации.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/key%2311.png\" /></p>\r\n<p>\r\n	В целом, инструмент удобный и интересный, работает довольно быстро. В следующий раз я воспользуюсь этим сервисом для показа своей презентации, чтобы не мучаться с отображением на чужих компьютерах.</p>\r\n',NULL,3,1),(84,'По следам презентации Verizon ','<p>\r\n	Вчера прошла презентация iPhone 4 CDMA от Verizon Wireles. Ну что тут можно сказать - все, что было предсказано - все сбылось. С 3 февраля начинают принимать на них заказы, а 10 он уже поступает в продажу. Но самое интересное в том, что это событие дало больше вопросов, чем ответов.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/verizon_iphone.jpg\" style=\"width: 500px; height: 479px; \" /></p>\r\n<p>\r\n	Будет ли такая версия iPhone работать в наших широтах? Будут ли доступн','<p>\r\n	Вчера прошла презентация iPhone 4 CDMA от Verizon Wireles. Ну что тут можно сказать - все, что было предсказано - все сбылось. С 3 февраля начинают принимать на них заказы, а 10 он уже поступает в продажу. Но самое интересное в том, что это событие дало больше вопросов, чем ответов.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/verizon_iphone.jpg\" style=\"width: 500px; height: 479px; \" /></p>\r\n<p>\r\n	Будет ли такая версия iPhone работать в наших широтах? Будут ли доступны специальные функции этого устройства доступны всем остальным? А как же iPad? тоже будет в 2 видах? А белый iPhone?! Давайте подробно с этим разберемся.</p>\r\n\r\n<h2>\r\n	БЕЛЫЙ iPHONE</h2>\r\n<p>\r\n	<br />\r\n	Слух о белом iPhone появился после того, как на сайте Verizon появилась страница заказа, на которой была как черная так и белая версия смартфона. Впрочем это недоразумение моментально ликвидировали, но подозрение осталось.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/verizon_white.jpg\" style=\"width: 500px; height: 383px; \" /></p>\r\n<p>\r\n	Что это было, случайная ошибка? А зачем тогда Apple дала Verizone материалы по белому аппарату, если он не предвидится в ближайшем будущем? Вероятно, все таки предвидится.</p>\r\n<h2>\r\n	GSM ХОТСПОТ</h2>\r\n<p>\r\n	<br />\r\n	Уникальной функцией iPhone CDMA будет являться приложение, которое позволит превращать ваш смартфон в мобильный &quot;хотспот&quot;. Это значит, что вы абсолютно официально сможете раздавать ваш мобильный интернет по WiFi пяти пользователям. Без специального устройства, специально для этого предназначенного.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/verizon_mifi.png\" style=\"width: 500px; height: 320px; \" /></p>\r\n<p>\r\n	Ключевыми словами является &quot;абсолютно официально&quot;. Подобное приложение давно присутствует в Cydia, но для законопослушных и сознательных пользователей она является недоступным.</p>\r\n<p>\r\n	Пользователям же альтернативных стандартов связи интересно, является ли эта функция прерогативой CDMA или в новой прошивке (4.2.5 или 4.3) тоже появится такая функция. Если верить слухам, что 19 января выйдет очередная версия iOS, то ждать осталось совсем недолго./p&gt;</p>\r\n<h2>\r\n	CDMA ВЕРСИЯ iPAD 2</h2>\r\n<p>\r\n	<br />\r\n	Появление CDMA версии планшета уже не является тайной. Соответствующая ссылка уже присутствует на сайте Verizon.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/verizon_ipad.png\" style=\"width: 460px; height: 300px; \" /></p>\r\n<p>\r\n	Вопрос в другом - это будет CDMA версия первой или второй модели? Если предположить, что iPad выйдет через несколько месяцев, то вариант с выпуском первой модели альтернативного стандарта является маловероятным. Видимо во второй версии планшета нас ждет нечто интересное.</p>\r\n<h2>\r\n	VERIZON и УКРАИНА</h2>\r\n<p>\r\n	<br />\r\n	Ну и самый животрепещущий вопрос - &quot;А будет ли работать такой iPhone у нас?&quot; По предварительным данным - нет. Стандарт, на котором работает Verizon не поддерживается в России. А вот в Украине, как оказалось, поддерживается.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/verizon_ua.png\" style=\"width: 500px; height: 192px; \" /></p>\r\n<p>\r\n	Зеленым цветом выделены области покрытия нужного стандарта. Мягко говоря - не густо. Целесообразность покупки такого телефона в связи с этим стремится к нулю, хотя со временем ситуация может изменится.</p>\r\n<h2>\r\n	ВЫВОДЫ</h2>\r\n<p>\r\n	Появление такого устройства - очень важный шаг не только для Apple, но и для всего мира. Вспомните, как выход iPhone повлиял на судьбу технологии Flash и HTML5. Неужто нас ждет мировой рассвет CDMA и падение GSM?</p>\r\n<p>\r\n	Остается только порадоваться за США и пожалеть AT&amp;T, которую ожидает массовый отток абонентов.</p>\r\n',NULL,3,1),(85,'Доступна для загрузки iOS 4.3 Beta 1','<p>\r\n	Вчера доступной для загрузки стала первая бета версия iOS 4.3 для разработчиков. Не смотря на отсутствие таких мажорных изменений, как в предыдущем обновлении (многозадачность, папки и т.д.) новой версии iOS все же есть чем удивить.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/10s43.jpg\" style=\"width: 214px; height: 200px; \" /></p>\r\n<p>\r\n	Давайте рассмотрим нововведения, которые принесет iOS 4.3</p>\r\n','<p>\r\n	Вчера доступной для загрузки стала первая бета версия iOS 4.3 для разработчиков. Не смотря на отсутствие таких мажорных изменений, как в предыдущем обновлении (многозадачность, папки и т.д.) новой версии iOS все же есть чем удивить.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/10s43.jpg\" style=\"width: 214px; height: 200px; \" /></p>\r\n<p>\r\n	Давайте рассмотрим нововведения, которые принесет iOS 4.3</p>\r\n\r\n<h2>\r\n	КОМУ ПРЕДНАЗНАЧЕНА</h2>\r\n<p>\r\n	<br />\r\n	Новые функции это хорошо, но только не для всех они станут доступными. Развитие программной части продукта невозможно без мощной аппаратной базы. К сожалению такой базой обладают лишь последние устройства.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ios43.png\" style=\"width: 500px; height: 143px; \" /></p>\r\n<p>\r\n	Пока рано делать выводы, но все говорит о том, что не вошедшие в этот список девайсы будут лишены дальнейшей поддержки Apple вместе с другими продуктами. Но это лишь первая бета, возможно ближе к релизу появятся другие новости.</p>\r\n<h2>\r\n	НОВЫЕ ФУНКЦИИ</h2>\r\n<p>\r\n	<br />\r\n	Глобальных обновлений не предвидится а по мелочам их довольно много. Перечислять все не имеет смысла, можно ограничиться лишь действительно интересными. Из основных новых функций:</p>\r\n<p>\r\n	&nbsp;&nbsp; &nbsp; <b>-</b> 4-х и 5-и пальцевые жесты на iPad, жест заменяющий кнопку Home</p>\r\n<p>\r\n	&nbsp;&nbsp; &nbsp; <b>-</b> Ожидаемый GSM Хотспот</p>\r\n<p>\r\n	&nbsp;&nbsp; &nbsp; <b>-</b> Новый растягивающийся интерфейс</p>\r\n<p>\r\n	&nbsp;&nbsp; &nbsp; <b>-</b> Эффекты для записи видео</p>\r\n<p>\r\n	&nbsp;&nbsp; &nbsp; <b>-</b> Новые настройки сообщений и новые шрифт в записках</p>\r\n<p>\r\n	&nbsp;&nbsp; &nbsp; <b>-</b> Кнопка блокировки поворота экрана на iPad снова работает</p>\r\n<p>\r\n	&nbsp;&nbsp; &nbsp; <b>-</b> Доступ к AirPlay сторонних приложений</p>\r\n<p>\r\n	&nbsp;&nbsp; &nbsp; <b>-</b> HTTP Live Streaming.</p>\r\n<p>\r\n	Помимо новых функций в iOS 4.3 Beta нашли еще несколько интересных вещей, но о них мы напишем чуть позже.</p>\r\n<h2>\r\n	ВЫВОДЫ</h2>\r\n<p>\r\n	<br />\r\n	Новая версия iOS обещает быть интересной. Не забывайте, что это лишь первая бета, а значит более интересные нововведения могут появиться в следующих версиях. Точно о дате релиза ничего не известно, хотя некоторые привязывают дату выхода с датой начала продаж iPad 2. Мы будем держать вас в курсе событий.</p>\r\n',NULL,3,1),(86,'Новые слухи о iPad 2','<p>\r\n	После выхода iOS 4.3 beta, о которой мы уже писали ранее, завеса тайны над iPad 2 слегка приоткрылась. Для некоторых функций не существует аппаратной поддержки, существование других ставит под сомнение необходимость существующего функционала. Нечто подтверждает гипотезы и прогнозы, что то напрочь опровергает.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ipad2%3F.png\" style=\"width: 500px; height: 300px; \" /></p>\r\n<p>\r\n	Я не пытаюсь в очередной раз строить предположения','<p>После выхода iOS 4.3 beta, о которой мы уже писали ранее, завеса тайны над iPad 2 слегка приоткрылась. Для некоторых функций не существует аппаратной поддержки, существование других ставит под сомнение необходимость существующего функционала. Нечто подтверждает гипотезы и прогнозы, что то напрочь опровергает.</p>\r\n<p class=\"rtecenter\"><img alt=\"\" src=\"/sites/default/files/ipad2%3F.png\" style=\"width: 500px; height: 300px; \"></p>\r\n<p>Я не пытаюсь в очередной раз строить предположения, просто хочу систематизировать все известные на сегодня данные и поделиться свежими слухами.</p>\r\n<h2>НОВЫЕ ЖЕСТЫ</h2>\r\n<p><br>\r\n	Безусловно, самой интересной функцией в свежей прошивке является поддержка четырех и пяти пальцевых жестов. Такие привычные мак-пользователям движения теперь доступны и на iPad. Теперь смахивание четырьмя пальцами влево или вправо позволят переключаться между окнами открытых приложений. Аналогичный жест вверх или вниз вызовет список запущенных приложений и меню управления iPod. Чтобы было более понятно предлагаю посмотреть на видео как это выглядит.</p>\r\n<p class=\"rtecenter\"><object height=\"300\" width=\"500\"><param name=\"movie\" value=\"http://www.youtube.com/v/PJOFd_kCvoo?fs=1&amp;hl=ru_RU\"><param name=\"allowFullScreen\" value=\"true\"><param name=\"allowscriptaccess\" value=\"always\"><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"300\" src=\"http://www.youtube.com/v/PJOFd_kCvoo?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"500\"></object></p>\r\n<p>Для желающих попробовать напомню, что версия прошивки доступна лишь зарегистрированным разработчикам, а новые жесты можно будет активировать только в свежем iOS SDK, подключив свой планшет к компьютеру.</p>\r\n<h2>ОТСУТСТВИЕ КНОПКИ HOME</h2>\r\n<p><br>\r\n	Появление новых жестов, которые делают аппаратную кнопку Home бесполезной, вызвало ряд гепотиз и предположений. Так журналисты из <a href=\"http://bgr.com\">BGR</a>, ссылаясь на свои источники в Apple заявили, что iPad 2 а следом и iPhone 5 лишаться аппаратной кнопки на лицевой панели. Вполне реалистичное заявление, особенно если учесть любовь Стива Джобса к таким революционным демаршам.</p>\r\n<p class=\"rtecenter\"><img alt=\"\" src=\"/sites/default/files/iPhone-iPad-home-button.jpg\" style=\"width: 500px; \"></p>\r\n<p>Мнения по поводу таких изменений разделились. Кто то считает, что без аппаратной кнопки новый пользователь не сразу поймет, как пользоваться устройством. Но о телефонах с сенсорным экраном говорили то же самое. Так что это не такая уж и фантастика.</p>\r\n<h2>ДВЕ КАМЕРЫ</h2>\r\n<p><br>\r\n	Ну а две камеры уже не являются фантастикой вообще. Это уже можно назвать научно доказанным фактом. Ребята из редакции <a href=\"http://9to5mac.com\">9to5mac</a> раскопали в недрах свежей прошивки занятные картинки, которые доказывают существование камер в свежем iPad.</p>\r\n<p class=\"rtecenter\"><img alt=\"\" src=\"/sites/default/files/ipadcamera9to5mac.png\" style=\"width: 460px; height: 531px; \"></p>\r\n<p>Скептики могут заметить, что одной фронтальной камеры будет вполне достаточно. Господа, камера на оборотной стороне устройства нужна не только для фотографий на фоне ковра, но и для таких полезных вещей, как расширенная реальность и сканер кодов. Не у всех под рукой есть iPhone или фотоаппарат. Возможно камеры будут не во всех версиях iPad\'a но они будут.</p>\r\n<h2>ТРИ ВЕРСИИ</h2>\r\n<p><br>\r\n	Ну а о нескольких версиях устройства информацию нашли <a href=\"http://engadget.com\">Engadget</a>. На картинке показаны несуществующие коды продуктов. Велика вероятность того, что эти продукты мы скоро увидим.</p>\r\n<p class=\"rtecenter\"><img alt=\"\" src=\"/sites/default/files/new-iphone-keys-rm-eng.jpg\" style=\"width: 500px; height: 188px; \"></p>\r\n<p>Возможно одним из них будет семидюймовый планшет, а может различия будут менее координальными. Не так уж и долго осталось ждать до появления этого устройства.</p>\r\n',NULL,3,1),(87,'Как выглядит iPad camera.app','<p>\r\n	Редакция <a href=\"http://www.9to5mac.com/47248/is-this-the-ipad-2s-camera-app?utm_source=feedburner&amp;utm_medium=feed&amp;utm_campaign=Feed%3A+9To5Mac-MacAllDay+%289+to+5+Mac+-+Apple+Intelligence%29\">9to5mac</a> продолжает исследовать содержимое iOS 4.3 Beta 1 и свежего SDK для нее. На сей раз они опубликовали скриншоты приложения для съемки фото и видио на iPad, которое было собрано из файлов последнего SDK.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/iPad1.png\" s','<p>\r\n	Редакция <a href=\"http://www.9to5mac.com/47248/is-this-the-ipad-2s-camera-app?utm_source=feedburner&amp;utm_medium=feed&amp;utm_campaign=Feed%3A+9To5Mac-MacAllDay+%289+to+5+Mac+-+Apple+Intelligence%29\">9to5mac</a> продолжает исследовать содержимое iOS 4.3 Beta 1 и свежего SDK для нее. На сей раз они опубликовали скриншоты приложения для съемки фото и видио на iPad, которое было собрано из файлов последнего SDK.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/iPad1.png\" style=\"width: 500px; height: 667px; \" /></p>\r\n<p>\r\n	Это на самом деле внешний вид реального приложения или умелая подделка - не известно. Но выглядит очень правдоподобно.</p>\r\n\r\n<h2>\r\n	Разрешение экрана</h2>\r\n<p>\r\n	<br />\r\n	Все скриншоты имеют стандартное расширение 1024 х 768, что подтверждает тот факт, что дисплей в новой версии iPad&#39;a не изменится. Правда, остается надежда на то, что это всего лишь разрешение программного эмулятора, тогда у всех жаждущих новый экран еще остается надежда.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ipad3-524x700.png\" style=\"width: 500px; height: 668px; \" /></p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ipad2.png\" style=\"width: 500px; height: 667px; \" /></p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ipad4.png\" style=\"width: 500px; height: 667px; \" /></p>\r\n<p>\r\n	А вам было бы удобно пользоваться такой &quot;большой&quot; камерой?</p>\r\n',NULL,3,1),(88,'Обзор бесплатного архиватора Stuffit Expander','<p>\r\n	Мне интересно, а те кто нахваливают Mac App Store там часто бывают вообще? Я читал очень много о различных играх, сервисах и никому не нужных платных приложениях. И это все здорово, но действительно полезные программы на каждый день что-то мне в обзорах не попадались. И вот я решил бороться с данной несправедливостью и решил обозреть действительно полезное приложение <a href=\"http://itunes.apple.com/us/app/stuffit-expander/id405580712?mt=12\">Stuffit Expander</a> .</p>\r\n<p class=\"rtecenter\"','<p>\r\n	Мне интересно, а те кто нахваливают Mac App Store там часто бывают вообще? Я читал очень много о различных играх, сервисах и никому не нужных платных приложениях. И это все здорово, но действительно полезные программы на каждый день что-то мне в обзорах не попадались. И вот я решил бороться с данной несправедливостью и решил обозреть действительно полезное приложение <a href=\"http://itunes.apple.com/us/app/stuffit-expander/id405580712?mt=12\">Stuffit Expander</a> .</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/stex_dockicon.png\" style=\"width: 500px; height: 162px; \" /></p>\r\n<p>\r\n	Это бесплатный &quot;разархиватор&quot;, который позволит очень удобно открыть любой архив и поместить его содержимое в любое удобное для вас место.</p>\r\n\r\n<h2>\r\n	ВНЕШНИЙ ВИД</h2>\r\n<p>\r\n	<br />\r\n	На экране приложение выглядит как виджет, который можно свободно перемещать по рабочему столу. Чтобы извлечь данные из архива достаточно перенести его на виджет и ваши данные аккуратно лягут в папку рядом с архивом. Не мешает работать с окнами, зато очень легко из нескольких окон переносить архивы.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/stex_mainwindow.png\" style=\"width: 500px; height: 500px; \" /></p>\r\n<p>\r\n	Интересный момент - при наведении стрелка на виджете мигает, не сразу заметно но интересно. Еще очень понравились иконки архивов, действительно интересные.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/stex_icon.png\" style=\"width: 500px; height: 500px; \" /></p>\r\n<p>\r\n	Вообще интерфейс доставляет удовольствие во время использования, придает элемент игры обычным рутинным задачам.</p>\r\n<h2>\r\n	ВОЗМОЖНОСТИ И МЕНЮ</h2>\r\n<p>\r\n	<br />\r\n	Stuffit Expander поразил меня сразу, открыв архив, который отказался открывать Rucksack. Этим он привлек мое внимание, но право на постоянную прописку он заслужил другим. Список его возможностей я приведу чуть ниже, но сразу скажу он довольно большой. Не скажу, что встречал все из перечисленных в нем архивов. Но довольно многие мне попадались и вызывали легкое недоумение у моего архиватора.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/stex_menulist.png\" style=\"width: 500px; height: 412px; \" /></p>\r\n<p>\r\n	Настройки больше касаются работы с файлами и организации файлов. Сам процесс разархивирования для внесения изменений недоступен.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/staex_menumain.png\" style=\"width: 433px; height: 275px; \" /></p>\r\n<p>\r\n	Выполнять свои функции Stuffit Expander может как при перетаскивании на его окно архива, так и по двойному щелчку, если эта функция включена в настройках.</p>\r\n<h2>\r\n	ОСОБЕННОСТИ</h2>\r\n<p>\r\n	<br />\r\n	Самой главной особенностью я считаю наличие виджета, на который можно перетаскивать архивы. Пригодится, когда у вас несколько архивов разбросанных по разным папкам, а их нужно все вместе разархивировать в одну.</p>\r\n<p>\r\n	Еще одна интересная функция - переименование. При попытке поместить содержимое архива рядом с папкой обладающей одноименным названием Expander предложит переименовать свежесозданную папку.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/stex_rename.png\" style=\"width: 419px; height: 160px; \" /></p>\r\n<p>\r\n	А вообще приложение годное и бесплатное. Думаю каждый может попробовать и сам для себя решить, что ему больше подходит.</p>\r\n<h2>\r\n	ВЫВОДЫ</h2>\r\n<p>\r\n	<br />\r\n	Приложение подойдет для тех, кто не часто встречается со сложными архивами и ему достаточно их распаковывать. Если же вы предпочитаете запаковывать архивы отличные от *.zip то вам стоит поискать нечто более функциональное.</p>\r\n',NULL,3,1),(89,'10 000 000 000 загруженых приложений из App Store','<p>\r\n	Очень интересные цифры озвучила Apple по поводу количества загрузок из App Store. На сегодня эта цифра очень уверенно приближается к десяти миллиардам загрузок. В честь такого события на сайте Apple был размещен <a href=\"http://www.apple.com/itunes/10-billion-app-countdown/\">большой счетчик</a> который в реальном времени считает количество установленных приложений. Это действительно завораживающее зрелище.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/AppStore.jpg\" sty','<p>\r\n	Очень интересные цифры озвучила Apple по поводу количества загрузок из App Store. На сегодня эта цифра очень уверенно приближается к десяти миллиардам загрузок. В честь такого события на сайте Apple был размещен <a href=\"http://www.apple.com/itunes/10-billion-app-countdown/\">большой счетчик</a> который в реальном времени считает количество установленных приложений. Это действительно завораживающее зрелище.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/AppStore.jpg\" style=\"width: 500px; height: 291px; \" /></p>\r\n<p>\r\n	Изюминкой этого события будет вручение пользователю, установившему 10 000 000 000-е по счету приложение, подарочного сертификата номиналом 10 000 долларов, на покупки новых приложений.</p>\r\n\r\n<h2>\r\n	МНОГО ИЛИ МАЛО?</h2>\r\n<p>\r\n	<br />\r\n	Десять миллиардов приложений - на первый взгляд это просто умопомрачительное количество для довольно молодого магазина. Но только на первый взгляд. Если обратить внимание на динамику развития платформы в последнее время, то становится понятно откуда берутся эти миллиарды. iOS семимильными шагами набирает обороты: сначала iPad, следом CDMA версия iPhone 4. Пока не ясно как относится и относится ли Mac App Store с его миллионом в первый день к этой круглой сумме, но не думаю, что эти цифры что то решают.</p>\r\n<p>\r\n	В свете выхода новых продуктов и расширения инфраструктуры эти десять миллиардов являются всего лишь отметкой, преодолев которую iOS устремится к новым горизонтам, где 10 000 000 000 загрузок не будет такой уж знаменательной цифрой.</p>\r\n<h2>\r\n	10 МИЛЛИАРДОВ В iTUNES</h2>\r\n<p>\r\n	<br />\r\n	Напомню, что для достижения подобного рубежа магазину музыки iTunes Store потребовалось восемь лет. И это не смотря на бесчисленное количество проданных устройств для прослушивания и огромную аудиторию лояльных пользователей.</p>\r\n<p>\r\n	Apple нащупала золотую жилу, и появление Mac App Store тому подтверждение. Все эти магазины лучше всего мотивируют разработчиков на создание действительно интересного продукта. Это не музыка, которую в Купертино не могли контролировать и отсеивать - это полностью контролируемый продукт, который приносит прибыль компании, разработчикам и радость пользователю.</p>\r\n',NULL,3,1),(90,'Remind Me Later - Добавление задач в iCal','<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Недавно я начал использовать iCal как средство планирования дня и постановки задач. Очень удобное приложение с приличным функционалом. И все меня устраивало, пока количество задач не выросло в несколько раз. Стало уже не так удобно. Для тех, кто никогда не пользовался iCal поясню - процесс создания заданий в нем требует сноровки и терпения. Заполнять 5-10 задач в день стало не очень удобно и я решил упростить эту задачу и и начал использовать <a href=\"http://itunes.apple.','<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Недавно я начал использовать iCal как средство планирования дня и постановки задач. Очень удобное приложение с приличным функционалом. И все меня устраивало, пока количество задач не выросло в несколько раз. Стало уже не так удобно. Для тех, кто никогда не пользовался iCal поясню - процесс создания заданий в нем требует сноровки и терпения. Заполнять 5-10 задач в день стало не очень удобно и я решил упростить эту задачу и и начал использовать <a href=\"http://itunes.apple.com/us/app/remind-me-later/id408236729?mt=12\">Remind Me Later</a>.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/remind_dock.png\" style=\"width: 500px; height: 182px; \" /></p>\r\n<p>\r\n	Remind Me Later - это программа, которая в одно действие позволяет создать задание в iCal. Предлагаю вашему вниманию небольшой обзор этой полезной утилиты.</p>\r\n\r\n<h2>\r\n	ВНЕШНИЙ ВИД</h2>\r\n<p>\r\n	<br />\r\n	О своей работе Remind Me Later напоминает только иконкой в статус-баре. Так же имеется иконка в Доке, но я не вижу смысла в ее присутствии.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/remind_barr.png\" style=\"width: 445px; height: 142px; \" /></p>\r\n<p>\r\n	Выглядит все строго, никаких лишних деталей. В целом, от такого приложения я и не ждал ничего, кроме выполнения возложенных на нее функций.</p>\r\n<h2>\r\n	ВОЗМОЖНОСТИ</h2>\r\n<p>\r\n	<br />\r\n	Возможность у Remind Me Later одна - быстро создавать новое задание в iCal. Причем делается это таким образом - вы просто пишете название, дату и время и это тут же отправляется в iCal именно на то время, которое вы запланировали. Без ввода даты вы можете внести задачу на выполнение через час.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/remind_new.png\" style=\"width: 419px; height: 137px; \" /></p>\r\n<p>\r\n	Недостатком считаю отсутствие возможности выставлять начало и конец задания. Вы можете только поставить заметку на конкретное время. Надеюсь разработчики учтут этот недостаток и исправят его в следующей версии.</p>\r\n<h2>\r\n	МЕНЮ И УПРАВЛЕНИЕ</h2>\r\n<p>\r\n	<br />\r\n	В настройках вы можете выбрать календарь для записей, установить автозапуск приложения при загрузке системы. Больше настраивать нечего, да и нет никакого смысла - программа проста именно в том виде, в каком она есть. Рекомендую больше внимания уделить настройкам iCal, ведь Remind Me Later является всего лишь дополнением к нему.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/remind_menu.png\" style=\"width: 330px; height: 215px; \" /></p>\r\n<p>\r\n	Очень порадовала возможность использования горячих клавиш. Так комбинацией Option+Command+R вы можете вызвать окно приложения чем бы вы не занимались.</p>\r\n<h2>\r\n	ВЫВОДЫ</h2>\r\n<p>\r\n	<br />\r\n	Если вы активный пользователь iCal, то это приложение как раз для вас. Оно абсолютно бесплатно упростит вам работу с большим количеством задач. Причем превратит эту тривиальную задачу в интересный процесс &quot;разговора&quot;. Пользуюсь сам и рекомендую другим.</p>\r\n<p>\r\n	Установить его можно из <a href=\"http://itunes.apple.com/us/app/remind-me-later/id408236729?mt=12\">Mac App Store</a> либо взять на <a href=\"http://bdunagan.com/remind-me-later\">сайте разработчика</a>.</p>\r\n',NULL,3,1),(91,'Используем Mac App Store для обновления','<p>\r\n	Для тех кто по каким либо причинам не успел / не смог /не захотел обновится до 10.6.6 и воспользоваться Mac App Store появился еще один аргумент в пользу обновления. Вот уже неделю стараюсь регулярно пользоваться магазином с целью поиска интересных бесплатных программ. И таки нашел применение ему для тех, кто не собирается покупать там программы.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/appStore.png\" style=\"width: 500px; height: 500px; \" /></p>\r\n<p>\r\n	Mac версия A','<p>\r\n	Для тех кто по каким либо причинам не успел / не смог /не захотел обновится до 10.6.6 и воспользоваться Mac App Store появился еще один аргумент в пользу обновления. Вот уже неделю стараюсь регулярно пользоваться магазином с целью поиска интересных бесплатных программ. И таки нашел применение ему для тех, кто не собирается покупать там программы.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/appStore.png\" style=\"width: 500px; height: 500px; \" /></p>\r\n<p>\r\n	Mac версия App Stor не на много отстает от своей iOS версии и обладает идентичными функциями. Этими функциями я и рекомендую вам воспользоваться</p>\r\n<h2>\r\n	ИСТОРИЯ EVERNOTE</h2>\r\n<p>\r\n	<br />\r\n	Уверен, что вы пользуетесь или как минимум слышали о таком интересном сервисе, как Evernote. Так вот, клиентское приложение Mac App Store не увидел. И я воспользовался его предложением установить такую же версию, но через App Store. Думаю, не я один воспользовался такой возможностью и обновил клиент Evernote. Зачем? Все очень просто - для того, чтобы своевременно получать обновления в удобном виде.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/evernote.png\" style=\"width: 500px; height: 163px; \" /></p>\r\n<p>\r\n	Вы, возможно, слышали о приросте мак-пользователей в 1100% у Evernote после запуска Mac App Store. Так вот, это не связано с количеством установленных приложений, только с количеством зарегистрированных пользователей. Про количество установленных приложений они даже и не говорят.</p>\r\n<h2>\r\n	ЧТО ДАЛЬШЕ</h2>\r\n<p>\r\n	<br />\r\n	Я еще не получил обновлений кроме Twitter for Mac, но думаю это будет намного удобнее получать их без необходимости запускать эти программы. Еще чуть-чуть и все самые популярные программы, которые присутствуют у каждого попадут в Mac App Store. Adium, Transmission, VLC, браузеры - все это можно будет обновлять удобно и быстро.</p>\r\n<p>\r\n	И это я только упомянул бесплатные приложения, которые есть у каждого. А есть еще платные утилиты, без которых уже мало кто представляет работу за своим маком. В общем, рекомендую обновляться и пользоваться Mac App Store.</p>\r\n',NULL,3,1),(92,'Свежий IM клиент - Trillian','<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Если у вас есть много друзей и, что самое главное, времени, то вам очень часто приходится пользоваться различными IM сервисами. Неоспоримым лидером на этом поле был и остается Adium, со всеми его минусами и недостатками. Стандартный iChat к сожалению не получится использовать в наших широтах - преобладающее количество пользователей пользуется не поддерживаемым ICQ, да и количество доступных сервисов крайне мало. Для тех, кто хотел бы попробовать чего-то нового, я представ','<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Если у вас есть много друзей и, что самое главное, времени, то вам очень часто приходится пользоваться различными IM сервисами. Неоспоримым лидером на этом поле был и остается Adium, со всеми его минусами и недостатками. Стандартный iChat к сожалению не получится использовать в наших широтах - преобладающее количество пользователей пользуется не поддерживаемым ICQ, да и количество доступных сервисов крайне мало. Для тех, кто хотел бы попробовать чего-то нового, я представляю вашему вниманию очередной бесплатный IM клиент с хорошим функционалом и приятным интерфейсом - <a href=\"http://itunes.apple.com/us/app/trillian/id412056820?mt=12\">Trillian</a>.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/trilliam_dock.png\" style=\"width: 530px; height: 175px; \" /></p>\r\n<p>\r\n	Он тоже не идеален, но для меня Trillian надолго будет IM клиентом номер один. Давайте рассмотрим его подробнее.</p>\r\n\r\n<h2>\r\n	ВНЕШНИЙ ВИД</h2>\r\n<p>\r\n	<br />\r\n	Я довольно требователен в выборе интерфейса программ, с которыми я работаю. Но, Trillian мне действительно понравился, это нечто среднее между спартанским Adium и красотой iChat. Он вобрал в себя лучшее от обоих и, тем не менее, выделяется на их фоне. Например, окно разговора очень напоминает стандартный IM клиент, но выполнено в стиле альтернативного менеджера.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/trillian_dialog.png\" style=\"width: 500px; height: 395px; \" /></p>\r\n<p>\r\n	В целом, настройки Trillian позволяют очень сильно изменять внешний вид приложений. Лично мне больше всего понравился внешний вид по умолчанию, так как он интуитивно понятен, удобен и практичен. Очень понравилось меню подключенных сервисов, которое дает понять, какие сервисы активны на данный момент.</p>\r\n<h2>\r\n	ВОЗМОЖНОСТИ</h2>\r\n<p>\r\n	<br />\r\n	По своим возможностям Trillian ничуть не уступает самым продвинутым IM клиентам. В его арсенале поддержка таких популярных сервисов как: ICQ, GTalk/Jabber, Facebook и т.д. Говоря о последней, хочу отметить, что Trilllian позволяет не только использовать Facebook-чат, но и просматривать ленту статусов/событий, запросов в отдельном окне.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/trillian_facebook.png\" style=\"width: 500px; height: 536px; \" /></p>\r\n<p>\r\n	На счет ICQ, сразу скажу - Trillian поддерживает все кодировки, так что теперь Win-юзеры всегда поймут, что вы хотите им сказать. Еще удивила поддержка 0auth авторизации в Twitter - от вас потребуется только ввести аккаунт, а дальше появится окно, где вы подтвердите разрешение на использование ваших данных. То есть, пароль вводить не потребуется.</p>\r\n<p>\r\n	Двоякое чувство вызывает необходимость создание специального Trillian аккаунта. Это напоминает Window QIP и слегка напрягает, что необходимо создавать еще один аккаунт. Но не стоит недооценивать эту функцию Теперь все аккаунты будут находится в одном месте и, вы сможете получить к ним доступ введя лишь пароль от Trillian.</p>\r\n<h2>\r\n	МЕНЮ И УПРАВЛЕНИЕ</h2>\r\n<p>\r\n	<br />\r\n	Меню позволяет очень гибко настроить работу приложения. Давайте поближе познакомимся с каждым пунктом Меню. Первым идет подменю Accounts. В нем мы можем добавить аккаунты различных сервисов и настроить каждую учетную запись.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/trillian_menu_1.png\" style=\"width: 500px; height: 409px; \" /></p>\r\n<p>\r\n	В подменю Appearance мы можем настроить внешний вид списка контактов и отображения элементов управления.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/trillian_menu_2.png\" style=\"width: 500px; height: 174px; \" /></p>\r\n<p>\r\n	Notifications позволяет настроить уведомления о событиях. Поддерживается Growl.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/trillian_menu_3.png\" style=\"width: 500px; height: 339px; \" /></p>\r\n<p>\r\n	Подменю Chat содержит в себе настройки окна разговора. Все очень просто и понятно.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/trillian_menu_4.png\" style=\"width: 500px; height: 301px; \" /></p>\r\n<p>\r\n	Я не понимаю особого смысла в присутствии подменю Transfers. Скажу только, что оно позволяет настраивать передачу файлов.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/trillian_menu_5.png\" style=\"width: 500px; height: 181px; \" /></p>\r\n<p>\r\n	Подводя итоги хочу сказать, что в принципе меню довольно простое , понятное и удобное. Оно позволяет подогнать работу программы под себя без нанесения ущерба функционалу.</p>\r\n<h2>\r\n	ОСОБЕННОСТИ</h2>\r\n<p>\r\n	<br />\r\n	Очень бросилось в глаза то, что информация о моих контактах появляется не в приложении, а на открывшейся странице на сайте сервиса. Теперь пользователь вынужден ожидать загрузки страницы для того, чтобы узнать необходимую информацию. Trillian позволяет работать с несколькими своими аккаунтами, которые вы можете выбрать при загрузке приложения.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/trillian_login.png\" style=\"width: 500px; height: 314px; \" /></p>\r\n<p>\r\n	Интересным моментом является то, что загрузка передаваемых файлов осуществляется браузером. Загруженные файлы вы всегда можете найти в папке За... подождите-подождите...Загрузки! В целом, приложение оставляет приятные впечатления. Оно не стремится нас удивлять, просто выполняет те функции, которые на него возложены.</p>\r\n<p>\r\n	Помимо Mac версии у приложения имеются iOS версии. Заявлена поддержка синхронизации данных и архивов, что не может не радовать.</p>\r\n<h2>\r\n	ВЫВОДЫ</h2>\r\n<p>\r\n	<br />\r\n	Я бы рекомендовал попробовать это приложение тем, кто разочаровался в Adium. Для меня Trillian надолго стал надежным помощником. Его простой интерфейс не отвлекает от главного - общения. И пусть все еще не так гладко, но это приложение обладает поистине большим потенциалом.</p>\r\n<p>\r\n	Найти Trillian вы можете в каталоге <a href=\"http://itunes.apple.com/us/app/trillian/id412056820?mt=12\">Mac App Store</a> либо на сайте <a href=\"http://www.trillian.im/learn/tour-mac.html\">разработчика</a>.</p>\r\n',NULL,3,1),(93,'Свежие слухи о iPad2 - новый экран и видеокарта','<p>\r\n	Эти выходные принесли очередную порцию слухов. На этот раз они касаются экрана еще не вышедшего планшетника и его графической подсистемы. Так ресурс <a href=\"http://macrumors.com\"><strong>Macrumors</strong></a> сопоставил сразу несколько фактов, которые, пусть и не доказывают наличие Retina Display в iPad 2, но выглядят вполне реалистично и убедительно.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ipad217_logo_0.png\" style=\"width: 500px; height: 500px; \" /></p>\r\n<p>\r\n','<p>\r\n	Эти выходные принесли очередную порцию слухов. На этот раз они касаются экрана еще не вышедшего планшетника и его графической подсистемы. Так ресурс <a href=\"http://macrumors.com\"><strong>Macrumors</strong></a> сопоставил сразу несколько фактов, которые, пусть и не доказывают наличие Retina Display в iPad 2, но выглядят вполне реалистично и убедительно.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ipad217_logo_0.png\" style=\"width: 500px; height: 500px; \" /></p>\r\n<p>\r\n	Давайте рассмотрим эти факты более подробно.</p>\r\n\r\n<h2>\r\n	ЭКРАН</h2>\r\n<p>\r\n	<br />\r\n	Все предположения сводятся к тому, что в новой версии iPad будет экран с разрешением 2048х1536. Это станет логичным усовершенствованием устройства и не принесет больших хлопот разработчикам приложений. Но эти предположения подкреплены некоторыми фактами. Во-первых найденные в августе иконки закладок для iBooks 1.1, обладающие аномально большим разрешением, во-вторых фоновый рисунок, найденный в iBooks 1.2 обладающий подобными свойствами.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ipad217_book.jpg\" /></p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ipad217_fon.png\" style=\"width: 500px; height: 260px; \" /></p>\r\n<p>\r\n	Помимо этого на сайте <a href=\"http://www.globaldirectparts.com%2F&amp;t=1295245801 \"><strong>GlobalDirectParts</strong></a>&nbsp;появился в продаже iPad 2 LCD Screen. Отличие по цене более чем в четыре раза по сравнению с присутствующем устройстве. Даже если взять цену экрана первого iPad до начала продаж - все равно буде намного дороже - 218$ против 145$.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ipad217_SCREEN.jpg\" style=\"width: 500px; height: 250px; \" /></p>\r\n<p>\r\n	Напомню, что благодаря этому ресурсу все увидели корпуса CDMA iPhone еще до того, как Verizon начал намекать на то, что такая возможность существует.</p>\r\n<h2>\r\n	ГРАФИКА</h2>\r\n<p>\r\n	<br />\r\n	Вторая часть слухов касается появления в iPad 2 новой графической карты PowerVR SGX543MP2 на базе SGX543 от Imagination Technologies. Как говорит <a href=\"http://appleinsider.com\"><strong>AppleInsider</strong></a> она позволить вдвое увеличить производительность устройства и высокое разрешение нового дисплея не скажется на скорости работы устройства.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ipad217_graph.jpg\" style=\"width: 500px; height: 286px; \" /></p>\r\n<p>\r\n	Подтверждает это предположение наличие драйвера для этого чипа в составе недавно вышедшей iOS 4.3 Beta 1. Остается правда открытым вопрос по поводу времени работы устройства. Новый двухядерный чип на базе Cortex A9 плюс новая видеокарта и дисплей - если еще и уменьшат толщину устройства, то что за батарея обеспечит новый планшет достаточным запасом энергии?</p>\r\n<h2>\r\n	ВИБРАЦИЯ</h2>\r\n<p>\r\n	И последний слух на сегодня - появление в новом планшете вибро-мотора. Разбалованные вибро-откликом в мобильных телефонах пользователи не отказались бы от такой функции и в iPad. Наличие сего девайса подтверждает <a href=\"http://www.globaldirectparts.com/Apple-iPad-Vibrating-Motor-p/appl8002650.htm\"><strong>GlobalDirectParts</strong></a>. О гироскопе пока упоминаний не было.</p>\r\n',NULL,3,1),(94,'Следующий iPod Touch будет с 3D экраном','<p>\r\n	В конце этого месяца Nintendo представит свою новую портативную игровую консоль - Nintendo 3DS, основным преимуществом которой будет 3D дисплей, для использования которого не будут нужны очки. Основным же конкурентом среди портативных устройств для Nintendo 3DS является Apple iPod Touch.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ipod3d_logo.jpg\" /></p>\r\n<p>\r\n	В свете этого, очень актуальными становятся слухи из Японии, где предполагают, что следующее поколение iPod','<p>\r\n	В конце этого месяца Nintendo представит свою новую портативную игровую консоль - Nintendo 3DS, основным преимуществом которой будет 3D дисплей, для использования которого не будут нужны очки. Основным же конкурентом среди портативных устройств для Nintendo 3DS является Apple iPod Touch.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ipod3d_logo.jpg\" /></p>\r\n<p>\r\n	В свете этого, очень актуальными становятся слухи из Японии, где предполагают, что следующее поколение iPod Touch будет так же обладать 3D дисплеем, свободным от использования очков.</p>\r\n\r\n<h2>\r\n	КАК ЭТО ВЫГЛЯДИТ</h2>\r\n<p>\r\n	<br />\r\n	Японский блог <a href=\"http://translate.googleusercontent.com/translate_c?hl=en&amp;sl=en&amp;tl=ru&amp;u=http://translate.google.com/translate%3Fjs%3Dn%26prev%3D_t%26hl%3Den%26ie%3DUTF-8%26layout%3D2%26eotf%3D1%26sl%3Dja%26tl%3Den%26u%3Dhttp%253A%252F%252Fwww.macotakara.jp%252Fblog%252Findex.php%253FID%253D11085&amp;rurl=translate.google.com&amp;twu=1&amp;usg=ALkJrhikevy24kzy_mFLAVNU-ZwW5PTOAA\"><strong>Macotakara</strong></a> ссылается на не названный источник, который является производителем 3D дисплеев для нового iPod.</p>\r\n<p class=\"rtecenter\">\r\n	<object height=\"385\" width=\"480\"><param name=\"movie\" value=\"http://www.youtube.com/v/2gm7u6f2glE?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"385\" src=\"http://www.youtube.com/v/2gm7u6f2glE?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"480\"></embed></object></p>\r\n<p>\r\n	Выглядит довольно интересно, хотя в живую будет выглядеть намного привлекательнее. Особенно в 3D играх, где это позволить намного удобнее управлять перемещениями в игровом пространстве и добавит реалистичности событиям.</p>\r\n<h2>\r\n	КАК ЭТО РАБОТАЕТ</h2>\r\n<p>\r\n	Macotakara предполагает, что технология будет заключаться в отслеживании движений головы через камеру FaceTime, и будет производится настройка 3D для оптимального просмотра в соответствии с положением головы пользователя и положение iPod в пространстве, по данным его гироскопа и датчиков движения.</p>\r\n<p class=\"rtecenter\">\r\n	<object height=\"385\" width=\"480\"><param name=\"movie\" value=\"http://www.youtube.com/v/Jd3-eiid-Uw?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"385\" src=\"http://www.youtube.com/v/Jd3-eiid-Uw?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"480\"></embed></object></p>\r\n<p>\r\n	Напомню, что у Apple уже давно имеется патент на подобную технологию и ее внедрение лишь дело времени.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ipod3d_patent.png\" style=\"width: 500px; height: 371px; \" /></p>\r\n<p>\r\n	Правда, не думаю, что раньше сентября Apple будет предпринимать решительные шаги в этом направлении. Если компания не стала первой в какой-то отрасли, она старается стать лучшей. Поскольку линейка iPod обновилась сравнительно недавно не стоит ждать скорого выпуска новинки. Да и не в духе Apple обновлять только один продукт - если 3D экраны придут, то только на все iOS устройства одновременно. А возможно и на Mac OS в том числе.</p>\r\n',NULL,3,1),(95,'Стив Джобс снова уходит на больничный','<p>\r\n	Сегодня утром сотрудники компании Apple узнали о том, что их рулевой ушел в отпуск по состоянию здоровья. Официальное обращения Стива к своим коллегам можно прочитать <a href=\"http://www.apple.com/pr/library/2011/01/17advisory.html\">здесь</a>. И все бы хорошо, человек решил подлечиться, с кем не бывает. Но это если бы на его месте был кто либо другой.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/jobs_fase.jpg\" style=\"width: 500px; height: 353px; \" /></p>\r\n<p>\r\n	Два го','<p>\r\n	Сегодня утром сотрудники компании Apple узнали о том, что их рулевой ушел в отпуск по состоянию здоровья. Официальное обращения Стива к своим коллегам можно прочитать <a href=\"http://www.apple.com/pr/library/2011/01/17advisory.html\">здесь</a>. И все бы хорошо, человек решил подлечиться, с кем не бывает. Но это если бы на его месте был кто либо другой.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/jobs_fase.jpg\" style=\"width: 500px; height: 353px; \" /></p>\r\n<p>\r\n	Два года назад Стив Джобс точно так же ушел на больничный, спустя пол года стало известно, что у него было осложнение после рака поджелудочной - проблемы с печенью и он перенес операцию по ее трансплантации. Что же случилось на этот раз?</p>\r\n\r\n<h2>\r\n	О БОЛЕЗНИ</h2>\r\n<p>\r\n	<br />\r\n	То, что он ушел на больничный сегодня еще не означает, что до сегодня все было хорошо. Вспомните презентацию CDMA iPhone от Verizon. Он просто обязан был быть там, чтобы представить продукт. Но вместо него был Тим Кук, который теперь будет исполнять его обязанности в компании до возвращения Джобса. А завтра, 18 января, будет показан финансовый отчет за квартал, самый удачный квартал в истории Apple по мнению аналитиков. Но Стив ушел уже сегодня. Вот текст письма, адресованного сотрудникам:</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/jobs_letter.png\" style=\"width: 500px; height: 270px; \" /></p>\r\n<p>\r\n	Он не объясняет причин и не говорит, когда вернется. Он всего лишь признается в любви к своей компании и обещает вернуться настолько быстро, насколько это возможно. И еще он просить оставить в покое частную жизнь его семьи.</p>\r\n<h2>\r\n	ПРИЧИНЫ</h2>\r\n<p>\r\n	<br />\r\n	Не вижу особого смысла рассуждать о причинах, их мы, возможно, никогда не узнаем. В интернете мнения разделились на &quot;Рецидив Старой Болезни&quot;, &quot;Сердечный Приступ&quot; который <a href=\"http://www.businessinsider.com/2008/10/apple-s-steve-jobs-rushed-to-er-after-heart-attack-says-cnn-citizen-journalist\">отрицала</a> &nbsp;Apple и &quot;ВИЧ&quot; <a href=\"http://www.informationweek.com/blog/main/archives/2009/01/wikileaks_owes.html\">предсказанный wikileaks</a>.</p>\r\n<p>\r\n	Как видите, ситуация с проблемами здоровья главы Apple обстоит таким образом, что малейшее его изменение влечет за собой мгновенную реакцию акционеров. Не удивительно, что со своим уходом он тянул до конца квартала. Может и в правду лучше никому ничего не знать?</p>\r\n',NULL,3,1),(96,'Причины слухов о iTunes + Safari','<p>\r\n	Небольшая новость о том, что в будущем функционал iTunes будет добавлен в Safari, что сделает этот браузер самым популярным в мире. Точнее даже не новость а предположение. &nbsp;Оно было высказано в одном подкасте и подхвачено всеми зарубежными и отечественными журналистами.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/safari%2Btunes.png\" style=\"width: 500px; height: 355px; \" /></p>\r\n<p>\r\n	На самом деле это предположение имеет под собой 2 факта: покупка сервиса Lala и','<p>\r\n	Небольшая новость о том, что в будущем функционал iTunes будет добавлен в Safari, что сделает этот браузер самым популярным в мире. Точнее даже не новость а предположение. &nbsp;Оно было высказано в одном подкасте и подхвачено всеми зарубежными и отечественными журналистами.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/safari%2Btunes.png\" style=\"width: 500px; height: 355px; \" /></p>\r\n<p>\r\n	На самом деле это предположение имеет под собой 2 факта: покупка сервиса Lala и огромный дата-центр в Северной Королине. Это позволило бы давно вынести весь механизм покупки приложений и музыки в облако, а iTunes сделать просто хорошим проигрывателем.</p>\r\n\r\n<p>\r\n	Но такой поступок сейчас слишком новаторский даже для Apple. Ведь теперь придется хранить в облаке персональные данные каждого пользователя, его контакты, записи, календари. Пока что эту функцию на себя берет MobileMe, но сейчас у пользователя есть возможность отказаться. С помещением всего функционала iTunes в браузер каждый будет обязан передавать всю свою информацию в облаке при синхронизации устройства с компьютером.</p>\r\n<p>\r\n	Не думаю, что все пользователи iOS устройств сразу согласятся на такое.</p>\r\n',NULL,3,1),(97,'Квартальный отчет AAPL Q1','<p>\r\n	Вчера в 2.00 PM PST или 1.00 по Москве Компания Apple представила общественности результаты своего первого квартала 2011. Традиционно рекордные финансовые показатели подкрепляются высокими темпами роста продаж. По сравнению с аналогичным кварталом прошлого года цифры довольно впечатляющие.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/apple-invite.png\" style=\"width: 500px; height: 260px; \" /></p>\r\n<p>\r\n	Сам вэбкаст был интересен тем, что практически не разговаривали о ','<p>\r\n	Вчера в 2.00 PM PST или 1.00 по Москве Компания Apple представила общественности результаты своего первого квартала 2011. Традиционно рекордные финансовые показатели подкрепляются высокими темпами роста продаж. По сравнению с аналогичным кварталом прошлого года цифры довольно впечатляющие.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/apple-invite.png\" style=\"width: 500px; height: 260px; \" /></p>\r\n<p>\r\n	Сам вэбкаст был интересен тем, что практически не разговаривали о процентах и миллионах. Основной темой разговоров были рост продаж iPad, выход CDMA iPhone 4 и рынок планшетных компьютеров.</p>\r\n\r\n<h2>\r\n	ОЖИДАНИЯ</h2>\r\n<p>\r\n	<br />\r\n	В ожидании объявления результатов квартала аналитики строили различные предположения, но Apple как всегда всех удивила и ее показатели вплотную приблизился к самым смелым предположениям.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/apple-wait.png\" style=\"width: 500px; height: 588px; \" /></p>\r\n<p>\r\n	Так доход компании по итогам квартала составил $26,74 млрд. а чистая прибыль $6 млрд. Это более чем на 70% больше, чем в аналогичном квартале 2010 года.</p>\r\n<h2>\r\n	ПРОДАЖИ УСТРОЙСТВ</h2>\r\n<p>\r\n	<br />\r\n	Продажи устройств выросли так же рекордно. И если продажи Маков скромно набрали 22,92% с 4,13 млн. шт. проданных устройств, то у iPhone просто рекордный рост в этом году - 86,67% с 16.24 млн. шт. по сравнению с прошлым кварталом! И это до начала продаж Verizone и заключения не эксклюзивного договора с AT&amp;T. Более доходчиво результаты можно посмотреть на диаграмме от <strong><a href=\"http://9to5mac.com\">9to5mac</a></strong>&nbsp;на которой видно из чего состоит&nbsp;доход&nbsp;компании.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/apple-graph.png\" style=\"width: 500px; height: 591px; \" /></p>\r\n<p>\r\n	Как видите, доход от&nbsp;продажи iPhone равен суммарному доходу от продаж Маков, iPod&#39;ов и музыки в iTunes Store. Хотел бы отметить продажи iPad - в этом квартале было продано 7,33 миллионов устройств. За все время продаж это количество насчитывает&nbsp;14,78 млн. устройств. Это несколько больше, чем предполагали аналитики, когда iPad только поступил в продажу, больше миллионов на 9.</p>\r\n<h2>\r\n	ДЕТАЛЬНАЯ СТАТИСТИКА</h2>\r\n<p>\r\n	Если вы интересуетесь более детальной информацией - вы можете ознакомиться с детальной статистикой результатов, предоставленной Apple.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/apple-01.jpg\" style=\"width: 500px; height: 566px; \" /></p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/apple-02.jpg\" style=\"width: 500px; height: 619px; \" /></p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/apple-03.jpg\" /></p>\r\n<p>\r\n	Нам остается только поздравить Тима Кука с тем, что он оказался во главе Apple в такое удачное время и пожелать скорейшего выздоровления Стиву Джобсу, благодаря которому компания достигла таких результатов.</p>\r\n',NULL,3,1),(98,'Падение курса акций AAPL продолжается','<p>\r\n	Не смотря на закрытие квартала и превосходную финансовую отчетность акции Apple продолжают падать. Связано ли это с уходом Стива Джобса на больничный или какими либо другими факторами - пока не ясно. Возможно это просто замедленный рост курса акций.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/AAPL-wrong.png\" style=\"width: 500px; height: 181px; \" /></p>\r\n<p>\r\n	Будем надеяться, что &nbsp;в дальнейшем ситуация исправиться и кривая пойдет вверх, но пока наблюдается нечто','<p>\r\n	Не смотря на закрытие квартала и превосходную финансовую отчетность акции Apple продолжают падать. Связано ли это с уходом Стива Джобса на больничный или какими либо другими факторами - пока не ясно. Возможно это просто замедленный рост курса акций.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/AAPL-wrong.png\" style=\"width: 500px; height: 181px; \" /></p>\r\n<p>\r\n	Будем надеяться, что &nbsp;в дальнейшем ситуация исправиться и кривая пойдет вверх, но пока наблюдается нечто другое.</p>\r\n\r\n<h2>\r\n	КРИВАЯ ЗАМЕРЛА</h2>\r\n<p>\r\n	В начале дня еще казалось, что кривая замерла и будет балансировать с интервалом плюс-минус 2 доллара, но уже сейчас понятно, что наметился спад. Причем ничем не обоснованный и не имеющий под собой явных предпосылок. Болезнь главы компании это конечно событие, но разве не должна была смягчить этот удар вчерашняя отчетность.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/AAPL-balance.png\" style=\"width: 500px; height: 221px; \" /></p>\r\n<p>\r\n	Дальше будет видно насколько сильным будет падение, но упасть ниже чем вчера - это уже странно. Хотя возможно причины не только в Apple, у других компаний ситуация схожая.</p>\r\n<h2>\r\n	ОБЩИЙ СПАД</h2>\r\n<p>\r\n	К сожалению я не являюсь биржевым аналитиком, но я могу сопоставлять факты и сравнивать предидущие значения с настоящими. Так, за этот вечер несколько упали в цене акции Google, Ebay и Amazon. Так что рано хоронить Apple, надеюсь на этой неделе ситуация исправится в лучшую сторону.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/AAPL-spad.png\" style=\"width: 500px; height: 300px; \" /></p>\r\n<p>\r\n	Надеюсь это падение никак не связано с Facebook и началом их инвестиционной компании от Goldman Sachs. Но об этом чуть позже.</p>\r\n',NULL,3,1),(99,'HOT NEWS: iOS 4.3 Beta 2 доступна для разработчиков + ссылка','<p>\r\n	Только что от <a href=\"http://9to5mac.com\"><strong>9to5mac</strong></a> стало известно, что стала доступной для загрузки iOS 4.3 Beta 2. Подробностей пока нет, так же не ясны отличия этой версии от Beta 1.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ios43beta2.png\" style=\"width: 500px; height: 242px; \" /></p>\r\n<p>\r\n	Пока только известно, что новые многопальцевые жесты не будут включены в 4.3 а представлены только для ознакомления. Возможно они появятся уже в iOS 5. Ж','<p>\r\n	Только что от <a href=\"http://9to5mac.com\"><strong>9to5mac</strong></a> стало известно, что стала доступной для загрузки iOS 4.3 Beta 2. Подробностей пока нет, так же не ясны отличия этой версии от Beta 1.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ios43beta2.png\" style=\"width: 500px; height: 242px; \" /></p>\r\n<p>\r\n	Пока только известно, что новые многопальцевые жесты не будут включены в 4.3 а представлены только для ознакомления. Возможно они появятся уже в iOS 5. Ждите подробностей, они обязательно будут.</p>\r\n<h2>\r\n	UPDATE:&nbsp;</h2>\r\n<p>\r\n	По просьбам трудящихся даем ссылку на свободное скачивание беты --&gt;&nbsp;<strong><a href=\"http://download.imodzone.net/\">iModZone</a></strong></p>\r\n',NULL,3,1),(100,'Новые функции в iOS 4.3 Beta 2','<p>\r\n	Буквально сегодня стала доступна для загрузки iOS 4.3 Beta 2. Но об этом я уже писал. Давайте посмотрим, что новое она принесла пользователям.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ios-beta2-logo.png\" style=\"width: 500px; height: 207px; \" /></p>\r\n<p>\r\n	Если вы недавно присоединились ко мне в моем поиске, то рекомендую так же почитать вот эти две статьи (<a href=\"http://macov.net/news/ipad2-new-screen-and-video\">раз</a> и <a href=\"http://macov.net/news/ipad2-new','<p>\r\n	Буквально сегодня стала доступна для загрузки iOS 4.3 Beta 2. Но об этом я уже писал. Давайте посмотрим, что новое она принесла пользователям.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ios-beta2-logo.png\" style=\"width: 500px; height: 207px; \" /></p>\r\n<p>\r\n	Если вы недавно присоединились ко мне в моем поиске, то рекомендую так же почитать вот эти две статьи (<a href=\"http://macov.net/news/ipad2-new-screen-and-video\">раз</a> и <a href=\"http://macov.net/news/ipad2-new-functions\">два</a>), чтобы быть в курсе всех событий.</p>\r\n\r\n<h2>\r\n	КАМЕРЫ В iPAD 2.0</h2>\r\n<p>\r\n	<br />\r\n	Новая прошивка - новое подтверждение наличия камер в новом iPad. На сей раз появилась информация о иконках FaceTime, Camera и Photo Booth. Вполне ожидаемый джентльменский набор, впрочем это уже не новость а лишь подтверждение фактов.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ios-beta2-ipadapps.jpeg\" style=\"width: 500px; height: 497px; \" /></p>\r\n<p>\r\n	Остается дождаться официального представления гаджета и убедиться в собственной правоте.</p>\r\n<h2>\r\n	ДАТЧИК РАССТОЯНИЯ</h2>\r\n<p>\r\n	<br />\r\n	Так же появилась информация о том, что в iPad 2.0 будет датчик расстояния. Это предположили исходя из новой функции, которая призвана блокировать экран устройства при закрытии крышки чехла планшетника.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ios-beta2-datchik.jpg\" style=\"width: 500px; height: 217px; \" /></p>\r\n<p>\r\n	Функция интересная, но я уверен, что одной лишь блокировкой экрана поле деятельности этого датчика не ограничится.</p>\r\n<h2>\r\n	НОВОСТИ ДЛЯ iPHONE</h2>\r\n<p>\r\n	<br />\r\n	Так же обновление iOS не обошло стороной владельцев iPhone - во второй бета-версии появились многопальцевые жесты, которые были в первой версии только для iPad. Хотя на планшете <a href=\"http://macov.net/news/ipad2-new-functions\">жесты</a> и стали работать намного стабильнее, быстрее и красивее, официальная позиция apple - многоплальцевые жесты не будут включены в финальную версию iOS 4.3. Они предназначены лишь для ознакомления разработчиков с новой функцией и тестирования приложений.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ios-beta2-iphone.jpg\" style=\"width: 500px; height: 400px; \" /></p>\r\n<p>\r\n	К сожалению у меня пока нет возможности оценить удобство четырехпальцевых жестов на iPhone, но если у вас есть что сказать - реплайте нам в твиттер <a href=\"http://twitter.com/macov_net\">@macov_net</a> Мы с удовольствием с вами пообщаемся.</p>\r\n',NULL,3,1),(101,'Джеилбрэйк-портал ra1nb0wra1n.com оказался подделкой.','<p>\r\n	Автором неудавшейся вечерней сенсации стали <a href=\"http://9to5mac.com\"><strong>9to5mac</strong></a>. Они опубликовали видео на котором во всей красе показывали якобы новый портал от GeoHot, на котором представлены все виды джеилбрейка, вплоть до еще не существующего RubiRa1n. Впрочем они довольно быстро <a href=\"http://www.9to5mac.com/48360/ra1nb0wra1n-jailbreaks-everything?utm_source=feedburner&amp;utm_medium=twitter&amp;utm_campaign=Feed%3A+9To5Mac-MacAllDay+%289+to+5+Mac+-+Apple+Intel','<p>\r\n	Автором неудавшейся вечерней сенсации стали <a href=\"http://9to5mac.com\"><strong>9to5mac</strong></a>. Они опубликовали видео на котором во всей красе показывали якобы новый портал от GeoHot, на котором представлены все виды джеилбрейка, вплоть до еще не существующего RubiRa1n. Впрочем они довольно быстро <a href=\"http://www.9to5mac.com/48360/ra1nb0wra1n-jailbreaks-everything?utm_source=feedburner&amp;utm_medium=twitter&amp;utm_campaign=Feed%3A+9To5Mac-MacAllDay+%289+to+5+Mac+-+Apple+Intelligence%29\"><strong>исправились</strong></a>&nbsp;и признали этот портал искусной китайской подделкой.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ra1n-fake.png\" style=\"width: 500px; height: 240px; \" /></p>\r\n<p>\r\n	В этом вы можете убедится сами - вот оба портала <a href=\"http://www.sonky.com/ra1nb0wra1n/recovery.html\"><strong>ra1nb0wra1n China</strong></a>&nbsp;&nbsp;и <a href=\"http://ra1nb0wra1n.com\"><strong>ra1nb0wra1n.com</strong></a> - как видите чуда не произошло. Увы и ах, GeoHot видимо чересчур занят судебными разбирательствами с Sony, чтобы заниматься джеилбрейком iOS 4.3 и разработкой rubira1n.</p>\r\n<p>\r\n	Хочу отметить, что сам портал в виде веб-приложения довольно занимательно сделан. Рекомендую посетить хотя бы ради ознакомления.</p>\r\n',NULL,3,1),(102,'Разработчики тестируют Mac OS X10.6.7','<p>\r\n	Стала доступна для загрузки первая бета версия <strong>Mac OS X 10.6.7</strong> 10J842. Это произошло всего через две недели после выхода 10.6.6, которая не являлась мажорным обновлением, а только добавила поддержку Mac App Store. Теперь же нас ожидает действительно нечто новое.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/10_6_7_beta.jpg\" style=\"width: 500px; height: 249px; \" /></p>\r\n<p>\r\n	Тестеров просили обратить внимание на работу <strong>Airport</strong>, <strong','<p>\r\n	Стала доступна для загрузки первая бета версия <strong>Mac OS X 10.6.7</strong> 10J842. Это произошло всего через две недели после выхода 10.6.6, которая не являлась мажорным обновлением, а только добавила поддержку Mac App Store. Теперь же нас ожидает действительно нечто новое.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/10_6_7_beta.jpg\" style=\"width: 500px; height: 249px; \" /></p>\r\n<p>\r\n	Тестеров просили обратить внимание на работу <strong>Airport</strong>, <strong>Bonjour</strong>, <strong>SMB</strong> и <strong>графической подсистемы</strong>.</p>\r\n<p>\r\n	Почему такая спешка? Видимо новые функции в iOS 4 требуют поддержки, которую сейчас не может обеспечить Снежный Барс. Так функция <strong>Hotspot</strong>, которая появиться с обновлением iOS должна адекватно восприниматься системой, развивающийся <strong>AirPrint</strong> и <strong>AirPlay Live Stream</strong> должны работать в любых условиях. Ну и не стоит забывать об обновлении линейки MacBookPro в 2011 году. Уже сейчас ведутся поиски намеков на новое &quot;железо&quot;, которым они будут укомплектованы.</p>\r\n',NULL,3,1),(103,'Apple получила новый патент на Magic Mouse с экраном.','<p>\r\n	Кампания Apple обзавелась еще одним интересным патентом. Технология на которую они получили патент довольно проста - теперь поверхность Magic Mouse может стать одним небольшим сенсорным дисплеем.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/glass_mouse-mouse.jpg\" style=\"width: 500px; height: 575px; \" /></p>\r\n<p>\r\n	Причем сделано это устройство будет из особого стекла, так что прощай пластик.</p>\r\n','<p>\r\n	Кампания Apple обзавелась еще одним интересным патентом. Технология на которую они получили патент довольно проста - теперь поверхность Magic Mouse может стать одним небольшим сенсорным дисплеем.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/glass_mouse-mouse.jpg\" style=\"width: 500px; height: 575px; \" /></p>\r\n<p>\r\n	Причем сделано это устройство будет из особого стекла, так что прощай пластик.</p>\r\n\r\n<h2>\r\n	НОВОЕ - ХОРОШО ЗАБЫТОЕ СТАРОЕ</h2>\r\n<p>\r\n	Идея не нова, нечто похожее предлагал Артемий Лебедев в своей <a href=\"http://tema.livejournal.com/682453.html\"><strong>Оптимус Популярис</strong></a>. Правда это была клавиатура и у нее были кнопки, но точно так же кнопки являлись отдельными дисплеями.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/glass_mouse-keyboard.jpg\" style=\"width: 500px; height: 328px; \" /></p>\r\n<p>\r\n	Будет ли этот патент использоваться и появятся ли в скором времени подобное устройство - не известно. Так же не ясно что будет отображаться на таком дисплее. Возможно это будут долгожданные цифровые кнопки.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/glass_mouse-numbers.jpg\" style=\"width: 500px; height: 683px; \" /></p>\r\n<p>\r\n	А нам остается только ждать, затаив дыхание, когда опять появится очередная вещь изменившая нашу жизнь.</p>\r\n',NULL,3,1),(104,'Непривязанный джэилбрэйк iOS 4.2.1 может появиться в течение нескольких часов','<p>\r\n	Собственно, вся новость рассказана в заголовке, и подтверждена одним постом в Twitter&#39;e. В&nbsp;нем говориться что <strong>@pod2g</strong> <strong>@0naj</strong> и <strong>@p0sixninja</strong> работали вместе и у них есть что-то интересное, что они покажут через несколько часов.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/jail421.png\" style=\"width: 500px; height: 225px; \" /></p>\r\n<p>\r\n	Конкретного упоминания конечно нет, но единственное в чем действительно заинте','<p>\r\n	Собственно, вся новость рассказана в заголовке, и подтверждена одним постом в Twitter&#39;e. В&nbsp;нем говориться что <strong>@pod2g</strong> <strong>@0naj</strong> и <strong>@p0sixninja</strong> работали вместе и у них есть что-то интересное, что они покажут через несколько часов.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/jail421.png\" style=\"width: 500px; height: 225px; \" /></p>\r\n<p>\r\n	Конкретного упоминания конечно нет, но единственное в чем действительно заинтересованы сейчас пользователи - нормальный непривязанный джейлбрейк.</p>\r\n',NULL,3,1),(105,'GreenPois0n для 4.2.1 задерживается','<p>\r\n	Официального релиза отвязанного джеилбрейка еще не было, но Cronic Dev Team и GeoHot уже готовы к его выпуску. Возвращаясь к вчерашней новости - @pod2g таки показал непривязанный джеил на iPhone.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/jail421_news.png\" style=\"width: 500px; height: 232px; \" /></p>\r\n<p>\r\n	Только в нем присутствовала одна маленькая ошибка - отсутствует отображение WiFi. После исправления этого досадного недостатка нас ожидает релиз свежей <a href=\"','<p>\r\n	Официального релиза отвязанного джеилбрейка еще не было, но Cronic Dev Team и GeoHot уже готовы к его выпуску. Возвращаясь к вчерашней новости - @pod2g таки показал непривязанный джеил на iPhone.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/jail421_news.png\" style=\"width: 500px; height: 232px; \" /></p>\r\n<p>\r\n	Только в нем присутствовала одна маленькая ошибка - отсутствует отображение WiFi. После исправления этого досадного недостатка нас ожидает релиз свежей <a href=\"http://www.greenpois0n.com/\"><strong>GreenPoison</strong></a>.</p>\r\n\r\n<h2>\r\n	ДОКАЗАТЕЛЬСТВО</h2>\r\n<p>\r\n	<br />\r\n	Вот, собственно, и само видео, которое доказывает скорый выход непривязанного джеилбрейка. На нем устройство выключают и включают заново, чтобы удостовериться в непривязанности джеилбрейка.</p>\r\n<p class=\"rtecenter\">\r\n	<iframe allowfullscreen=\"\" class=\"youtube-player\" frameborder=\"0\" height=\"300\" src=\"http://www.youtube.com/embed/C5qNray7FHU\" title=\"YouTube video player\" type=\"text/html\" width=\"500\"></iframe></p>\r\n<p>\r\n	При включении все осталось на своих местах, Cydia заработала.</p>\r\n<h2>\r\n	КОНКУРЕНТ</h2>\r\n<p>\r\n	<br />\r\n	Судя по всему, GeoHot тоже не намерен сидеть сложа руки. По <strong><a href=\"http://www.macdigger.ru/iphone-ipod/geohot-gotovit-unlock-iphone-4-3gs-i-neprivyazannyj-dzhejlbrejk-ios-4-2-1-4-3.html?utm_source=feedburner&amp;utm_medium=feed&amp;utm_campaign=Feed%3A+Macdigger+%28MacDigger.ru%29\">его словам</a>,</strong>&nbsp;альтернативное приложение - <a href=\"http://www.limera1n.com/\"><strong>limerain</strong></a> уже готово и скоро будет доступно для всего семейства устройств Apple</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/geohot.jpg\" style=\"width: 500px; height: 267px; \" /></p>\r\n<p>\r\n	Нам остается лишь немного подождать до релиза.</p>\r\n',NULL,3,1),(106,'ВОСКРЕСНЫЙ ДАЙДЖЕСТ: Angry Birds, Growl, камера iPad 2, Cydia, App Store','<p>\r\n	За эти выходные появилось некоторое количество интересных, но незначительных новостей, которые я решил объединить в единый дайджест выходных дней.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/sunday_d-logo.png\" style=\"width: 500px; height: 180px; \" /></p>\r\n<p>\r\n	Надеюсь такие дайджесты приживутся и мы будем радовать вас большим количеством полезной информации по выходным.</p>\r\n','<p>\r\n	За эти выходные появилось некоторое количество интересных, но незначительных новостей, которые я решил объединить в единый дайджест выходных дней.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/sunday_d-logo.png\" style=\"width: 500px; height: 180px; \" /></p>\r\n<p>\r\n	Надеюсь такие дайджесты приживутся и мы будем радовать вас большим количеством полезной информации по выходным.</p>\r\n\r\n<h2>\r\n	ANGRY BIRDS</h2>\r\n<p>\r\n	<br />\r\n	Первая новость из мира iOS игр. Как сообщает 9to5mac, компания Rovio готовит очередную версию своего великолепного приложения ко Дню Святого Валентина. Об этом вы можете узнать из небольшого видео, в котором рассказывают о компании, разработке Angry Birds и планах на будущее.</p>\r\n<p class=\"rtecenter\">\r\n	<iframe allowfullscreen=\"\" class=\"youtube-player\" frameborder=\"0\" height=\"300\" src=\"http://www.youtube.com/embed/dY5itbK9Ai0\" title=\"YouTube video player\" type=\"text/html\" width=\"500\"></iframe></p>\r\n<p>\r\n	Напомню, что игра на сегодняшний день портирована практически на все мобильные платформы, а так же доступна для Windows и Mac.</p>\r\n<h2>\r\n	СПОНТАННЫЙ GROWL</h2>\r\n<p>\r\n	<br />\r\n	В своем интервью для Macworld разработчики дали пояснения не санкционированной установке их приложения. Growl - это система всплывающих уведомлений, которая используется большим количеством программ. И некоторые из них (например Dropbox) имеют нехорошее свойство устанавливать Growl, так как он им необходим для нормальной работы.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/sunday_d-growl.png\" style=\"width: 500px; height: 300px; \" /></p>\r\n<p>\r\n	ПРичем они его установят все равно, даже если он уже установлен. Как с этим бороться - читайте в их <a href=\"http://www.macworld.com/article/154862/2011/01/growl_installation.html\"><strong>интервью</strong></a>. Осторожно, английский.</p>\r\n<h2>\r\n	КАМЕРА iPAD 2</h2>\r\n<p>\r\n	<br />\r\n	Очередная новость о камере в iPad 2.0 - скорее всего она будет идентична камере iPod последнего поколения. Да, это не будет 5 мегапиксельная камера iPhone с HDR, но и этой камеры будет достаточно для съемки HD видео 720p.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/sunday_d-ipad-cam.png\" /></p>\r\n<p>\r\n	Не думаю, что кто-то будет использовать iPad в качестве фотоаппарата, а для всего остального хватит и 1 мегапиксельной камеры.</p>\r\n<h2>\r\n	НОВЫЙ РЕЛИЗ CYDIA</h2>\r\n<p>\r\n	<br />\r\n	Следующая новость касается всех, кто не представляет жизни без джэилбрейка на своем iOS устройстве. В новой версии Cydia появится функция Менеджер Аккаунтов. Он будет следить за установленными приложениями и своевременно их обновлять. Так же при очередном джеилбрейке Cydia уже будет знать какие пакеты вы будете устанавливать.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/sunday_d-cydia.png\" style=\"width: 500px; height: 500px; \" /></p>\r\n<p>\r\n	Вам не понадобиться запоминать свои учетные данные, т.к. вы сможете войти под своей учетной записью Facebook или Google. Так же появится возможность выставлять приложениям рейтинг и оставлять комментарии.</p>\r\n<h2>\r\n	10 МИЛЛИАРДОВ ЗАГРУЗОК В APP STORE</h2>\r\n<p>\r\n	<br />\r\n	И последняя новость дайджеста. Десятимиллиардное приложение в AppStore было скачано! Счастливым обладателем Gift Card на $10000 стал Гэил Дэвис (Gail Davis) из Великобритании.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/sunday_d-appstore.png\" /></p>\r\n<p>\r\n	На этом радостном событии, я пожалуй закончу сегодняшний дайджест. Если вам понравилось или не понравилось - ждем ваших комментариев в twitter - пишите реплаи на <a href=\"http://twitter.com/macov_net\"><strong>@macov_net</strong></a></p>\r\n',NULL,3,1),(107,'Конкурент iPad от Motorola выйдет 17 февраля','<p>\r\n	Пока iPad 2.0 только готовится к выходу и ого характеристиках ходят лишь слухи, его прямой и самый интересный конкурент от Motorola уже готовится поступить в продажу. Информацию о точной дате начала продаж и стартовой цене сообщает <a href=\"http://www.engadget.com/2011/01/22/motorola-xoom-launching-february-17th-at-best-buy/\"><strong>Engadget</strong></a> &nbsp;со ссылкой на внутренний документ сети Best Buy.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/xoom-17.jpg\" s','<p>\r\n	Пока iPad 2.0 только готовится к выходу и ого характеристиках ходят лишь слухи, его прямой и самый интересный конкурент от Motorola уже готовится поступить в продажу. Информацию о точной дате начала продаж и стартовой цене сообщает <a href=\"http://www.engadget.com/2011/01/22/motorola-xoom-launching-february-17th-at-best-buy/\"><strong>Engadget</strong></a> &nbsp;со ссылкой на внутренний документ сети Best Buy.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/xoom-17.jpg\" style=\"width: 500px; height: 267px; \" /></p>\r\n<p>\r\n	По их информации, запуск продаж состоится 17 февраля и цена за модель с 3G будет составлять $699.99 (что в действительности является $700). За что же просят такие деньги?</p>\r\n\r\n<h2>\r\n	ХАРАКТЕРИСТИКИ</h2>\r\n<p>\r\n	<br />\r\n	За $700 вам предоставляется планшет с экраном 10 дюймов, на платформе NVIDIA Tegra 2 с двухьядерным процессором работающим на частоте 1 ГГц. Xoom имеет оперативную память объемом 1 гигабайт. Вес устройства - 730 граммов, толщина корпуса - 13 мм. Также в планшет встроен HDMI-порт для вывода изображения на внешний экран, барометр, акселерометр и гироскоп. Имеется 2 камеры, а работает это все на Android 3.0 Honeycomb.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/xoom-moto.jpg\" style=\"width: 500px; height: 458px; \" /></p>\r\n<p>\r\n	Возникает вопрос, чем обусловлена высокая цена устройства. Некоторые предполагают, что это стоимость барометра повлияла на цену планшета). Шутки шутками, а главный вопрос остается открытым - Что может Xoom, чего не может любой другой Android-планшет за $200?</p>\r\n',NULL,3,1),(108,'Почтовый клиент Sparrow выходит из статуса Beta','<p>\r\n	Спешим сообщить, что замечательный почтовый клиент Sparrow в течении недели будет доступен в Mac App Store. Об этом разработчики недавно сообщили в <a href=\"http://blog.sparrowmailapp.com/post/2908697184/sparrow1-0\"><strong>своем блоге</strong></a>. Там же были озвучены цены на Sparrow 1.0 и условия распространения бесплатной версии.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/sparrow-logo.png\" style=\"width: 500px; height: 283px; \" /></p>\r\n<p>\r\n	Так, версий будет две','<p>\r\n	Спешим сообщить, что замечательный почтовый клиент Sparrow в течении недели будет доступен в Mac App Store. Об этом разработчики недавно сообщили в <a href=\"http://blog.sparrowmailapp.com/post/2908697184/sparrow1-0\"><strong>своем блоге</strong></a>. Там же были озвучены цены на Sparrow 1.0 и условия распространения бесплатной версии.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/sparrow-logo.png\" style=\"width: 500px; height: 283px; \" /></p>\r\n<p>\r\n	Так, версий будет две: платная за $24,99 (в начале продаж ее цена будет $19,99) и Lite версия, которая будет нести в себе рекламу от <a href=\"http://www.carbonads.net/\"><strong>Carbon</strong></a> и в которой можно будет подключить только одну учетную запись. Так же разработчики обещают несколько приятных сюрпризов и новую иконку приложения. Будем с нетерпением ждать.</p>\r\n',NULL,3,1),(109,'Macintosh исполнилось 27','<p>\r\n	Несмотря на трагические события в Домодедово, мы бы не хотели забывать и о сегодняшнем дне, как о дне появления на свет первого Макинтоша. Пусть это видео поможет вам отвлечься и проникнутся торжеством этого события.</p>\r\n<p class=\"rtecenter\">\r\n	<iframe allowfullscreen=\"\" class=\"youtube-player\" frameborder=\"0\" height=\"390\" src=\"http://www.youtube.com/embed/G0FtgZNOD44\" title=\"YouTube video player\" type=\"text/html\" width=\"480\"></iframe></p>\r\n<p>\r\n	Надеюсь вам понравилось. С праздником!</p>\r','<p>\r\n	Несмотря на трагические события в Домодедово, мы бы не хотели забывать и о сегодняшнем дне, как о дне появления на свет первого Макинтоша. Пусть это видео поможет вам отвлечься и проникнутся торжеством этого события.</p>\r\n<p class=\"rtecenter\">\r\n	<iframe allowfullscreen=\"\" class=\"youtube-player\" frameborder=\"0\" height=\"390\" src=\"http://www.youtube.com/embed/G0FtgZNOD44\" title=\"YouTube video player\" type=\"text/html\" width=\"480\"></iframe></p>\r\n<p>\r\n	Надеюсь вам понравилось. С праздником!</p>\r\n',NULL,3,1),(110,'App Store появился в Twitter\'e','<p>\r\n	Не знаю, что именно заставило App Store появиться в твиттере, но верю что решение это верное. Как показали трагические события в Домодедово - твиттер это самое быстрое средство распространения информации на сегодняшний день. Теперь Все самые свежие новости App Store можно будет узнать практически сразу после их появления.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/appstore-twitter.png\" style=\"width: 500px; height: 387px; \" /></p>\r\n<p>\r\n	Аккаунт <a href=\"http://twitt','<p>\r\n	Не знаю, что именно заставило App Store появиться в твиттере, но верю что решение это верное. Как показали трагические события в Домодедово - твиттер это самое быстрое средство распространения информации на сегодняшний день. Теперь Все самые свежие новости App Store можно будет узнать практически сразу после их появления.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/appstore-twitter.png\" style=\"width: 500px; height: 387px; \" /></p>\r\n<p>\r\n	Аккаунт <a href=\"http://twitter.com/#!/appstore\"><strong>@AppStore</strong></a> отметился 7-ю твитами, зафоловил 5 iTunes-сервисов и был зафоловлен только за первые часы 65-ю тысячами человек. И его популярность продолжает расти.</p>\r\n<p>\r\n	Следуйте за <a href=\"http://twitter.com/#!/appstore\"><strong>@AppStore</strong></a> и <a href=\"http://twitter.com/#!/macov_net\"><strong>@macov_net</strong></a> чтобы оставаться в курсе событий.</p>\r\n',NULL,3,1),(111,'Вышла новая бета-версия Mac OS X 10.6.7 билд 10J846','<p>\r\n	Новая бета-версия <strong>Mac OS X 10.6.7</strong> стала доступна для загрузки. Билд за номером 10J846 появился 24 января потяжелевшим на 35 Мб. От <a href=\"http://macov.net/news/10-6-7-beta-update\"><strong>предыдущей бета-версии</strong></a> отличается новым пунктом, на который нужно обратить внимание разработчикам.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/macos1067beta2.png\" style=\"width: 500px; height: 249px; \" /></p>\r\n<p>\r\n	Теперь помимо <strong>AirPort</stron','<p>\r\n	Новая бета-версия <strong>Mac OS X 10.6.7</strong> стала доступна для загрузки. Билд за номером 10J846 появился 24 января потяжелевшим на 35 Мб. От <a href=\"http://macov.net/news/10-6-7-beta-update\"><strong>предыдущей бета-версии</strong></a> отличается новым пунктом, на который нужно обратить внимание разработчикам.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/macos1067beta2.png\" style=\"width: 500px; height: 249px; \" /></p>\r\n<p>\r\n	Теперь помимо <strong>AirPort</strong>, <strong>Bonjour</strong>, <strong>SMB</strong> и <strong>графической подсистемы</strong> теперь стоит уделить внимание <strong>Mac App Store</strong>. Видимо, нас таки ожидает обновление магазина вместе с новым релизом Mac OS.</p>\r\n',NULL,3,1),(112,'Начался редизайн apple.com и переучет в Online Apple Store','<p>\r\n	Утренняя новость - редизайн на сайт <a href=\"http://apple.com\"><strong>apple.com</strong></a> и онлайн-магазин закрытый на переучет.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/applecom-store.png\" style=\"width: 500px; height: 152px; \" /></p>\r\n<p>\r\n	Некоторые считают, что это связано с появлением белого iPhone 4. Мне слабо верится в то, что Apple добавит его в каталог своего американского магазина ночью, когда его никто не увидит.</p>\r\n','<p>\r\n	Утренняя новость - редизайн на сайт <a href=\"http://apple.com\"><strong>apple.com</strong></a> и онлайн-магазин закрытый на переучет.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/applecom-store.png\" style=\"width: 500px; height: 152px; \" /></p>\r\n<p>\r\n	Некоторые считают, что это связано с появлением белого iPhone 4. Мне слабо верится в то, что Apple добавит его в каталог своего американского магазина ночью, когда его никто не увидит.</p>\r\n\r\n<h2>\r\n	РЕДИЗАЙН</h2>\r\n<p>\r\n	<br />\r\n	Зато было бы очень своевременно обновить внешний вид сайта и сам интернет-магазин перед поступлением большого количества новинок в продажу.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/applecom-site.png\" style=\"width: 500px; height: 131px; \" /></p>\r\n<p>\r\n	Сайт теперь работает ощутимо быстрее, а новые эффекты радуют глаз. Обновленное меню навигации по сайту, которое падает сверху - действительно круто.</p>\r\n',NULL,3,1),(113,'CleanMyMac обновился для удаления приложений из MAS','<p>\r\n	Маленькая ночная новость - утилита <a href=\"http://macpaw.com/\"><strong>CleanMyMac</strong></a> обновилась до версии 1.9.4 и теперь может корректно удалять приложения, установленные через <strong>Mac App Store</strong>.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/cleanmymac-update.png\" style=\"width: 500px; height: 359px; \" /></p>\r\n<p>\r\n	Помимо этого исправили ряд минорных ошибок и добавили возможность удаления старых iOS-прошивок из корзины.</p>\r\n','<p>\r\n	Маленькая ночная новость - утилита <a href=\"http://macpaw.com/\"><strong>CleanMyMac</strong></a> обновилась до версии 1.9.4 и теперь может корректно удалять приложения, установленные через <strong>Mac App Store</strong>.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/cleanmymac-update.png\" style=\"width: 500px; height: 359px; \" /></p>\r\n<p>\r\n	Помимо этого исправили ряд минорных ошибок и добавили возможность удаления старых iOS-прошивок из корзины.</p>\r\n\r\n<h2>\r\n	ЧТО ВСЕ ЭТО ЗНАЧИТ</h2>\r\n<p>\r\n	<br />\r\n	Для тех, кто не в курсе, что такое <a href=\"http://macpaw.com/\"><strong>CleanMyMaс</strong></a> поясняем - это утилита для очистки вашего компьютера от ненужного мусора, который незаметно скапливается в <strong>Mac OS X</strong>. Одной из самых популярных программы считают функцию удаления приложений. Она позволяет удалить не только сам файл из папки <em>&quot;Программы&quot;</em>, но и все сопутствующие файлы, которые порой очень сложно отискать.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/cleanmymac-uninstall.png\" style=\"width: 500px; height: 463px; \" /></p>\r\n<p>\r\n	<strong>Mac App Store</strong> принес нам возможность удобно устанавливать приложения. Обновление <a href=\"http://macpaw.com/\"><strong>CleanMyMac</strong></a> принесло нам возможность так же удобно их удалять.</p>\r\n',NULL,3,1),(114,'Macworld Daily Reader для iPad уже доступно в App Store','<p>\r\n	Вышло приложение <strong>Macworld Daily Reader</strong>, которое позволит вам с головой окунуться в мир Apple. Приложение будет содержать как статьи из одноименного журнала так и лучшие материалы расположенные на веб сайте. Особенно это актуально в преддверии выставки Macworld 2011, которая пройдет с 26 по 29 января в Сан Франциско.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/Macworld-daily.png\" style=\"width: 500px; height: 200px; \" /></p>\r\n<p>\r\n	Однако приложение не','<p>\r\n	Вышло приложение <strong>Macworld Daily Reader</strong>, которое позволит вам с головой окунуться в мир Apple. Приложение будет содержать как статьи из одноименного журнала так и лучшие материалы расположенные на веб сайте. Особенно это актуально в преддверии выставки Macworld 2011, которая пройдет с 26 по 29 января в Сан Франциско.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/Macworld-daily.png\" style=\"width: 500px; height: 200px; \" /></p>\r\n<p>\r\n	Однако приложение не будет просто зеркалом этих информационных ресурсов, а позволит абсолютно по новому получать новости на ваш iPad. Так же порадует вас прямой трансляцией выставки.</p>\r\n\r\n<h2>\r\n	ЧТО ЭТО ЗНАЧИТ</h2>\r\n<p>\r\n	<br />\r\n	Приложение доступно для бесплатной загрузки в <a href=\"http://itunes.apple.com/ru/app/macworld-daily-reader/id393179523?mt=8\"><strong>App Store</strong></a>. Более подробно функционал можно посмотреть на видео:&nbsp;</p>\r\n<p class=\"rtecenter\">\r\n	<iframe allowfullscreen=\"\" class=\"youtube-player\" frameborder=\"0\" height=\"300\" src=\"http://www.youtube.com/embed/LgU658rCcT4\" title=\"YouTube video player\" type=\"text/html\" width=\"500\"></iframe></p>\r\n<p>\r\n	Если вас не смущает огромное количество информации на английском языке, то рекомендую к обязательному скачиванию.</p>\r\n',NULL,3,1),(115,'Что нового появится в MacBook Pro\'11','<p>\r\n	Как сообщает ссылаясь на свои источники, в сетях розничной продажи начинают заканчиваться запасы MacBook Pro 15&#39; и 17&#39;. Так, в сети Amazon сроки доставки данных моделей выросли до 1-2 месяцев, в то время, как в Apple Store Online они по прежнему доставляются за 1-2 дня.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/macbookpro11-book.png\" style=\"width: 500px; height: 167px; \" /></p>\r\n<p>\r\n	Как показывает статистика, такие опустошения складов Apple проводит перед','<p>\r\n	Как сообщает ссылаясь на свои источники, в сетях розничной продажи начинают заканчиваться запасы MacBook Pro 15&#39; и 17&#39;. Так, в сети Amazon сроки доставки данных моделей выросли до 1-2 месяцев, в то время, как в Apple Store Online они по прежнему доставляются за 1-2 дня.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/macbookpro11-book.png\" style=\"width: 500px; height: 167px; \" /></p>\r\n<p>\r\n	Как показывает статистика, такие опустошения складов Apple проводит перед выпуском новых моделей. Если учесть, что цикл обновления линейки MacBook Pro приблизительно год, то можно быть уверенным - обновление не за горами.</p>\r\n\r\n<h2>\r\n	ЧЕГО СТОИТ ОЖИДАТЬ</h2>\r\n<p>\r\n	<br />\r\n	Если речь идет о обновлении линейки самых высокопроизводительных ноутбуков серии, то здесь стоит ожидать появления архитектуры <strong>Intel Sandy Bridge</strong>, переход на твердотельные накопители (SSD) и отказ от CD-привода. Так же возможно увеличение емкости батареи и обновление внешнего вида, чтобы сделать его еще тоньше и легче. Все перечисленные мной обновки получил вышедший 20 октября MacBook Air, и эти нововведения пришлись по вкусу пользователям.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/macbookpro11-air.jpg\" style=\"width: 500px; height: 253px; \" /></p>\r\n<p>\r\n	Ничего пока еще неизвестно о появлении таких технологий, как USB 3.0 и Light Peak. Но информация про обновление появляется регулярно, так что следите за новостями.</p>\r\n<p>\r\n	Для всех желающих покупать MacBook Pro 15&#39; или 17&#39; рекомендуем подождать обновления линейки. С выходом новых моделей цены на предыдущую серию снизятся, а возможно новая серия вам понравится намного больше.</p>\r\n',NULL,3,1),(116,'iPad празднует свой первый день рождения!','<p>\r\n	Ровно год назад все неравнодушные к прогрессу и гаджетам люди затаив дыхание ожидали увидеть &quot;нечто&quot;. На специальной презентации, организованной Apple по этому особому случаю Стив Джобс рассыпая эпитеты и прилагательные представил всему миру будущее портативных компьютеров - iPad.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ipad-birthday.jpg\" style=\"width: 500px; height: 250px; \" /></p>\r\n<p>\r\n	Это событие перевернуло рынок персональных компьютеров. Появилас','<p>\r\n	Ровно год назад все неравнодушные к прогрессу и гаджетам люди затаив дыхание ожидали увидеть &quot;нечто&quot;. На специальной презентации, организованной Apple по этому особому случаю Стив Джобс рассыпая эпитеты и прилагательные представил всему миру будущее портативных компьютеров - iPad.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ipad-birthday.jpg\" style=\"width: 500px; height: 250px; \" /></p>\r\n<p>\r\n	Это событие перевернуло рынок персональных компьютеров. Появилась целая отрасль, которая начала расти и развиваться невиданными темпами. Прошел всего год, а было продано уже более 14 миллионов устройств, и не за горами уже вторая версия планшета, которая обещает быть еще интересней своего младшего брата.</p>\r\n\r\n<h2>\r\n	КАК ЭТО БЫЛО</h2>\r\n<p>\r\n	<br />\r\n	В то, что произошло не каждый сразу поверил - настолько новым и неестественным казался этот хрупкий гаджет, который оказался у Стива в руках. Как это было вы можете увидеть на видео:</p>\r\n<p class=\"rtecenter\">\r\n	<iframe allowfullscreen=\"\" class=\"youtube-player\" frameborder=\"0\" height=\"300\" src=\"http://www.youtube.com/embed/1l8bmRjw7Mw\" title=\"YouTube video player\" type=\"text/html\" width=\"500\"></iframe></p>\r\n<p>\r\n	В реальности все было намного дольше и каждую фразу, каждое действие оратора заканчивали крики восхищения и бурные аплодисменты. И это была единственная естественная реакция на то, что было показано ровно год назад и так прочно вошедшее в нашу повседневную жизнь.</p>\r\n',NULL,3,1),(117,'Skype for Mac 5 вышел из беты','<p>\r\n	Маленькая новость - <a href=\"http://www.skype.com/intl/en/get-skype/on-your-computer/macosx/\"><strong>Skype for Mac 5.0</strong></a> вышел из статуса бета и теперь доступен для загрузки всем желающим. Отличается от предыдущей версии минималистичным интерфейсом и скоростью работы - стал работать намного быстрее.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/skype5.png\" style=\"width: 500px; height: 209px; \" /></p>\r\n<p>\r\n	К сожалению, групповые видеоконференции стали прер','<p>\r\n	Маленькая новость - <a href=\"http://www.skype.com/intl/en/get-skype/on-your-computer/macosx/\"><strong>Skype for Mac 5.0</strong></a> вышел из статуса бета и теперь доступен для загрузки всем желающим. Отличается от предыдущей версии минималистичным интерфейсом и скоростью работы - стал работать намного быстрее.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/skype5.png\" style=\"width: 500px; height: 209px; \" /></p>\r\n<p>\r\n	К сожалению, групповые видеоконференции стали прерогативой платного пакета услуг. По сравнению с Beta-версией приятно выглядит область диалогов, появились новые элементы управления. Рекомендую для загрузки.</p>\r\n',NULL,3,1),(118,'Sony и Android объединяются против iOS','<p>\r\n	Недавно Sony показала всему миру очередную версию своей игровой консоли. Появиться в продаже она не раньше, чем в конце года. И не смотря на это, ее появление заставляет задуматься о будущей битве между iOS и Android.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/psp2-lodo.png\" style=\"width: 500px; height: 261px; \" /></p>\r\n<p>\r\n	Может возникнуть вопрос, как связаны портативная консоль от Sony и операционная система от Google. Теперь они связаны самым прямым образом. И ','<p>\r\n	Недавно Sony показала всему миру очередную версию своей игровой консоли. Появиться в продаже она не раньше, чем в конце года. И не смотря на это, ее появление заставляет задуматься о будущей битве между iOS и Android.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/psp2-lodo.png\" style=\"width: 500px; height: 261px; \" /></p>\r\n<p>\r\n	Может возникнуть вопрос, как связаны портативная консоль от Sony и операционная система от Google. Теперь они связаны самым прямым образом. И пока не ясно чем это грозит Apple - победой Давида или поражением Голиафа.</p>\r\n\r\n<h2>\r\n	НОВАЯ КОНСОЛЬ</h2>\r\n<p>\r\n	<br />\r\n	Давайте сначала познакомимся с новой игровой консолью. Форм фактор устройства не изменился. Такой же большой экран и аппаратные кнопки. вот только экран теперь стал 5 дюймовым сенсорным OLED-дисплеем с разрешением 960 на 544 пиксела. Кроме этого обратная сторона устройства теперь будет сенсорной поверхность. Ее удобно будет использовать для более точного управления. Девайс обзаведется поддержкой 3G, WiFi, GPS, акселерометром и гироскопом, а так же двумя камерами - для общения и расширенной реальности.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/psp2-all.jpg\" style=\"width: 500px; height: 501px; \" /></p>\r\n<p>\r\n	Все это великолепие будет работать при поддержке двухядерного процессора ARM Cortex A9 (Такой планируется и в новом поколении iOS устройств.) и графического процессора SGX543MP4+ от Imagination Technologies. И все это хорошо, но одной аппаратной начинкой войну не выиграть.</p>\r\n<h2>\r\n	КАК ЭТО РАБОТАЕТ</h2>\r\n<p>\r\n	<br />\r\n	Выглядит все интересно и привлекательно. Но боюсь всего этого не хватит для превращения устройства для школьников в устройство для всех и каждого.</p>\r\n<p class=\"rtecenter\">\r\n	<iframe allowfullscreen=\"\" class=\"youtube-player\" frameborder=\"0\" height=\"300\" src=\"http://www.youtube.com/embed/Kb9nTptUIL4\" title=\"YouTube video player\" type=\"text/html\" width=\"500\"></iframe></p>\r\n<p>\r\n	В компании Sony все прекрасно понимают, что само по себе устройство сейчас ничего не значит. Всех интересует &quot;платформа&quot;. И они ее готовы представить.</p>\r\n<blockquote>\r\n	<p>\r\n		Sony заявила, что версия <strong>PlayStation Suite</strong>, совместимая с Android, уже готова и будет доступна позднее в этом году. Для ее использования понадобится версия Android 2.3 Gingerbread или новей. <strong>Sony PlayStation Suite</strong> представляет собой контентную платформу, которую, по слухам, будет использовать телефон PlayStation от компании Sony Ericsson, оснащенный операционной системой Android. Система первоначально будет включать в себя эмулятор игр для PlayStation Portable и PlayStation 1, а впоследствии расширится и превратится в платформу для разработки для самых разных устройств.</p>\r\n</blockquote>\r\n<p>\r\n	Это означает, что теперь на Android будет своя собственная игровая платформа, которой у Apple может и не быть. И нас ждет кровопролитная битва на этом поле.</p>\r\n<h2>\r\n	ЧТО НАС ЖДЕТ</h2>\r\n<p>\r\n	<br />\r\n	Мировые производители техники и интернет компании сейчас предпочитают объединяться против единого врага. Когда-то таким врагом была Microsoft и совместными усилиями компании смогли удержаться на рынке. Теперь таким врагом становится Apple, которая своими продуктами вытесняет с рынка конкурентов. Все в едином порыве сплотятся перед ее агрессией под знаменами Android и с Google во главе пойдут крушить супостата. И никого не смутит, что весь этот конгломерат Androd&#39;ов сам превратиться в Главного Врага. Это как в сказке, тот кто отрубил голову дракону - сам становится драконом.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/psp2-david_0.jpg\" style=\"width: 500px; height: 314px; \" /></p>\r\n<p>\r\n	Для нас, как для пользователей все эти баталии только к лучшему. Основным орудием конкурентов будут понижение цен и новые технологии. И здесь самое главное - лояльность потребителя, который и проголосует &quot;рублем&quot;.</p>\r\n',NULL,3,1),(119,'Appshopper заработал и для Mac App Store','<p>\r\n	Популярный веб-сервис <a href=\"http://appshopper.com\"><strong>Appshopper</strong></a>, который являлся одним из главных информеров о скидках и акциях в App Store, 26 января получил долгожданное обновление. Теперь в сервисе появилась возможность следить за скидками в Mac App Store!</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/AppShopper.png\" style=\"width: 500px; height: 113px; \" /></p>\r\n<p>\r\n	К сожалению, хороших бесплатных приложений в магазине для мака пока не много.','<p>\r\n	Популярный веб-сервис <a href=\"http://appshopper.com\"><strong>Appshopper</strong></a>, который являлся одним из главных информеров о скидках и акциях в App Store, 26 января получил долгожданное обновление. Теперь в сервисе появилась возможность следить за скидками в Mac App Store!</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/AppShopper.png\" style=\"width: 500px; height: 113px; \" /></p>\r\n<p>\r\n	К сожалению, хороших бесплатных приложений в магазине для мака пока не много. Но как показывают результаты продаж скоро их должно появится достаточное количество.</p>\r\n\r\n<h2>\r\n	ЧЕМ ПОЛЕЗЕН APPSHOPPER</h2>\r\n<p>\r\n	<br />\r\n	Для тех, кто еще не успел воспользоваться Appshopper - краткое описание сервиса. Он позволяет следить за обновлениями, скидками и изменениями цены на приложения из App Store и теперь уже Mac App Store.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/appsopper-apps.png\" style=\"width: 500px; height: 181px; \" /></p>\r\n<p>\r\n	На странице сервиса присутствует прямая ссылка на приложение в iTunes Store, где вы можете сразу его купить или скачать бесплатно.</p>\r\n<h2>\r\n	НЕ ТОЛЬКО В БРАУЗЕРЕ</h2>\r\n<p>\r\n	<br />\r\n	Работает сервис не только в браузере. Существует универсальное приложение для iOS, которое посредством push-уведомлений проинформирует вас о скидках. Выглядит оно довольно симпатично, хотя внешний вид и не является главным для такого полезного приложения.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/appshopper-lopoplol.png\" style=\"width: 500px; height: 306px; \" /></p>\r\n<p>\r\n	По вполне понятным причинам, в приложении для iOS не появилось ссылок на Mac App Store.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/appshooter-ipad.png\" style=\"width: 500px; height: 264px; \" /></p>\r\n<p>\r\n	Рекомендую пользоваться и следить за новинками, которые иногда становятся бесплатными на один день. А в Mac App Store у вас может появиться возможность попасть на распродажу и купить желанное приложение за 20% от его стоимости.</p>\r\n',NULL,3,1),(120,'Проблемы с обновлениями в Mac App Store','<p>\r\n	Безусловно Mac App Store удобный и полезный сервис. Я уже <a href=\"http://macov.net/news/mac-app-store-update-center\"><strong>писал</strong></a> о том, как его можно использовать для обновлений. Но последняя неутешительная информация, связанная с обновлением FTP-клиента <a href=\"http://panic.com/transmit/\"><strong>Transmit 4</strong></a> от Panic.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/APPSTORE FAIL.png\" style=\"width: 495px; height: 259px; \" /></p>\r\n<p>\r\n	Разраб','<p>\r\n	Безусловно Mac App Store удобный и полезный сервис. Я уже <a href=\"http://macov.net/news/mac-app-store-update-center\"><strong>писал</strong></a> о том, как его можно использовать для обновлений. Но последняя неутешительная информация, связанная с обновлением FTP-клиента <a href=\"http://panic.com/transmit/\"><strong>Transmit 4</strong></a> от Panic.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/APPSTORE FAIL.png\" style=\"width: 495px; height: 259px; \" /></p>\r\n<p>\r\n	Разработчики <strong>Coda</strong> и <strong>Candybar</strong> 6 января подготовили обновление для своего файлового менеджера, которое исправляет несколько важных ошибок и улучшает работу приложения с подключением к Amazon S3. Планировалось разместить обновление на сайте одновременно с появлением его в Mac App Store, но оно не прошло проверку и им пришлось разместить обновление только у себя на сайте.</p>\r\n\r\n<h2>\r\n	ЧТО ЭТО ЗНАЧИТ</h2>\r\n<p>\r\n	<br />\r\n	Такое поведение Apple в отношении исправления критических ошибок в профессиональных приложениях наводит на неутешительные мысли. Transmit 4 является полноценным рабочим инструментом, причем не самым дешевым. Я как пользователь хотел бы получать своевременное обновление купленного мной приложения, в особенности, если это касается безопасности моих данных или моей работы.</p>\r\n<p>\r\n	Выхода может быть только два. Либо Apple упрощает свою политику в отношении апдейтов, либо такие серьезные приложения, как MS Office и Adobe Photoshop никогда не появятся в Mac App Store. А те, кто пришел - спешно его покинут.</p>\r\n',NULL,3,1),(121,'ВОСКРЕСНЫЙ ДАЙДЖЕСТ: сервисы от Google, Adobe Packager для iPad, XServe и Cathode','<p>\r\n	Это очередной воскресный дайджест в котором я расскажу о событиях заслуживающих внимания.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/digest30.png\" style=\"width: 500px; height: 180px; \" /></p>\r\n<p>\r\n	Я решил расширить временные рамки и рассмотреть события за всю неделю, которые не попали в нашу новостную ленту, но о которых стоит упомянуть.</p>\r\n','<p>\r\n	Это очередной воскресный дайджест в котором я расскажу о событиях заслуживающих внимания.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/digest30.png\" style=\"width: 500px; height: 180px; \" /></p>\r\n<p>\r\n	Я решил расширить временные рамки и рассмотреть события за всю неделю, которые не попали в нашу новостную ленту, но о которых стоит упомянуть.</p>\r\n\r\n<h2>\r\n	ВЫШЛИ СЕРВИСЫ ОТ GOOGLE ДЛЯ iOS</h2>\r\n<p>\r\n	<br />\r\n	На этой неделе Google представил сразу два сервиса для iOS. Появилась &quot;облачная&quot; печать для iPhone прямо из браузера. Этот сервис доступен пока только для соединенных штатов, но вскоре станет доступным для всех. Сервис предоставляет возможность распечатать любой документ из Google Docs на любом принтере, подключенном к компьютеру с доступом в интернет или сетевом принтере.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/digest30-cloud.jpg\" style=\"width: 500px; height: 368px; \" /></p>\r\n<p>\r\n	Второй сервис анонсировали сегодня. Это всем уже знакомый &quot;Instant Preview&quot; в поиске, но уже для мобильных браузеров. Для iOS Safari это должно выглядеть так:</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/digest30-goolgle.png\" /></p>\r\n<p>\r\n	Пока над этим сервисом работают и ничего не известно о сроках и ограничениях его запуска. Но активность Google в отношении iOS платформы приятно удивляет.</p>\r\n<h2>\r\n	ОБНОВИЛСЯ ADOBE PACKAGER ДЛЯ iPAD&gt;</h2>\r\n<p>\r\n	<br />\r\n	Недавно появились <a href=\"http://j.mp/dLODNb\"><strong>слухи</strong></a>, что Adobe планирует обновить свой Flash Packager для iPhone. В новой версии должна появится поддержка iPad, что открывает некоторые перспективы для Flash разработчиков.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/digest30-flash.png\" style=\"width: 300px; height: 300px; \" /></p>\r\n<p>\r\n	Flash Packager - это утилита, которая позволяет превратить Flash приложение в приложение для iOS. После того, как разработчики научили его превращать flash-анимацию в HTML5, у Flash появилась возможность стать хорошим средством разработки для iOS.</p>\r\n<h2>\r\n	ПОЯВИЛСЯ ПРИЕМНИК XSERVE</h2>\r\n<p>\r\n	<br />\r\n	31 января перестанут принимать заказы на всеми любимый XServe. Его функции призваны заменить Mac Pro и Mac Mini Server. Уже существует несколько дата-центров на основе этих технологий, но отказаться от удобного форм-фактора стойки пока тяжело. Своевременно отредактировали в <a href=\"http://www.activestorage.com/\"><strong>Active Storage</strong></a> и 31 января они представят нечто очень похожее на XServe.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/digest30-xserv.png\" style=\"width: 500px; height: 313px; \" /></p>\r\n<p>\r\n	Будет ли это схожий по характеристикам сервер или будет превосходить его. Некоторые считают, что Active Storage могут воспользоваться технологией &quot;виртуальной&quot; Mac OS с молчаливого согласия Apple. Осталось подождать несколько дней до этого события, тогда все станет известно.</p>\r\n<h2>\r\n	ДЛЯ ЛЮБИТЕЛЕЙ TERMINAL ТЕПЕРЬ ЕСТЬ CATHODE</h2>\r\n<p>\r\n	<br />\r\n	Ну и напоследок интересное приложение для тех, кто много времени проводит в Terminal.app и делает это уже очень давно. У вас появилась возможность погрузится в ностальгическую атмосферу лампового экрана с приложением <a href=\"http://www.secretgeometry.com/apps/cathode/\"><strong>Cathode</strong></a>.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/digest30-cathode.png\" /></p>\r\n<p>\r\n	Приложение изменяет внешний вид окна терминала и позволяет работать в полноэкранном режиме. Ощутимая стоимость в $20 для приложения размером в 1,5 Мб заставляет переоценить значимость внешнего вида терминала. Но посмотреть стоит.</p>\r\n',NULL,3,1),(122,'Мощный процессор появится в iPad 2.0','<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Новая порция аналитики по поводу характеристик нового iPad поступила от <a href=\"http://www.appleinsider.com/articles/11/01/30/report_details_ipad_2_components_5_million_unit_supply.html\"><strong>Concord Securities</strong></a>. Помимо известных фактов (<a href=\"http://macov.net/news/ipad2-new-functions\"><strong>раз</strong></a>, <a href=\"http://macov.net/news/ipad2-new-screen-and-video\"><strong>два</strong></a>, <a href=\"http://macov.net/news/ipad-camera-screen\"><strong>','<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Новая порция аналитики по поводу характеристик нового iPad поступила от <a href=\"http://www.appleinsider.com/articles/11/01/30/report_details_ipad_2_components_5_million_unit_supply.html\"><strong>Concord Securities</strong></a>. Помимо известных фактов (<a href=\"http://macov.net/news/ipad2-new-functions\"><strong>раз</strong></a>, <a href=\"http://macov.net/news/ipad2-new-screen-and-video\"><strong>два</strong></a>, <a href=\"http://macov.net/news/ipad-camera-screen\"><strong>три</strong></a> и <a href=\"http://macov.net/news/ios43-beta2-new-functions\"><strong>четыре</strong></a>) появилась и свежая информация. Аналитик компании предполагает, что iPad 2.0 будет обладать двухядерным процессором Cortex-A9 с тактовой частотой 1.2ГГц и увеличенным объемом оперативной памяти.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ipad2cpu.png\" style=\"width: 500px; height: 300px; \" /></p>\r\n<p>\r\n	Помимо внутренних характеристик изменения коснутся и внешних. Экран устройства, если и не получит экстремальное разрешение, то обязательно должен улучшить свои антибликовые свойства. Невозможность работы на ярком солнце не устраивает покупателей куда больше, чем отсутствие Flash или порта USB.</p>\r\n\r\n<p>\r\n	&nbsp;</p>\r\n',NULL,3,1),(123,'Вышла новая реклама Motorola Xoom','<p>\r\n	Маркетологи из Motorola продолжают радовать нас веселыми рекламными роликами своего планшета Xoom, о котором мы <a href=\"http://macov.net/news/motorola-xoom-17\"><strong>уже писали</strong></a>. На сей раз они решили обратиться к самому известному рекламному ролику Apple - 1984. Только теперь в роли Большого Брата выступает сама Apple, а конкретнее iPad. И освободить нас из рабства единообразия призван именно Xoom, ибо он быстрее и &quot;свободнее&quot;.</p>\r\n<p class=\"rtecenter\">\r\n	<iframe','<p>\r\n	Маркетологи из Motorola продолжают радовать нас веселыми рекламными роликами своего планшета Xoom, о котором мы <a href=\"http://macov.net/news/motorola-xoom-17\"><strong>уже писали</strong></a>. На сей раз они решили обратиться к самому известному рекламному ролику Apple - 1984. Только теперь в роли Большого Брата выступает сама Apple, а конкретнее iPad. И освободить нас из рабства единообразия призван именно Xoom, ибо он быстрее и &quot;свободнее&quot;.</p>\r\n<p class=\"rtecenter\">\r\n	<iframe allowfullscreen=\"\" class=\"youtube-player\" frameborder=\"0\" height=\"300\" src=\"http://www.youtube.com/embed/ndhuEUX1kIU\" title=\"YouTube video player\" type=\"text/html\" width=\"500\"></iframe></p>\r\n<p>\r\n	Довольно смелые заявления, как для устройства, которое еще не поступило в продажу. Да и от Motorola здесь по сути только название. С учетом увеличения доли Android-планшетов, могу сказать, что Xoom поборется если и не с iPad&#39;ом, то с собратьями-киллерами уж точно.</p>\r\n\r\n<h2>\r\n	1984</h2>\r\n<p>\r\n	<br />\r\n	На мой взгляд сравнение Apple с Большим Братом это интересно, но если уж и делать &quot;обратку&quot;, то она должна быть достойна оригинала. Как можно сравнить блестяще снятый короткометражный фильм с убогой презентацией, слепленной на коленке за несколько часов. Вот вам оригинал для сравнения:</p>\r\n<p class=\"rtecenter\">\r\n	<iframe allowfullscreen=\"\" class=\"youtube-player\" frameborder=\"0\" height=\"300\" src=\"http://www.youtube.com/embed/HhsWzJo2sN4\" title=\"YouTube video player\" type=\"text/html\" width=\"500\"></iframe></p>\r\n<p>\r\n	Надеюсь, что Xoom не станет такой же бледной копией устройства Apple, как стал новый рекламный ролик.</p>\r\n',NULL,3,1),(124,'Летающий MacBook Air был замечен в Apple Store ','<p>\r\n	Вчера на сайте <strong>obamapacman</strong> появилось интересное видео с участием MacBook Air. Так же как и в мультфильме &quot;Вверх!&quot; от Pixar он парил в воздухе на воздушном шаре.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/airinair.png\" style=\"width: 500px; height: 388px; \" /></p>\r\n<p>\r\n	Зрелище завораживает своей неестественностью. Это наглядный пример того, что MacBook Air действительно невесомый.</p>\r\n','<p>\r\n	Вчера на сайте <strong>obamapacman</strong> появилось интересное видео с участием MacBook Air. Так же как и в мультфильме &quot;Вверх!&quot; от Pixar он парил в воздухе на воздушном шаре.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/airinair.png\" style=\"width: 500px; height: 388px; \" /></p>\r\n<p>\r\n	Зрелище завораживает своей неестественностью. Это наглядный пример того, что MacBook Air действительно невесомый.</p>\r\n\r\n<h2>\r\n	КАК ЭТО СДЕЛАНО</h2>\r\n<p>\r\n	<br />\r\n	Мало того, что ультратонкий ноутбук повис в воздухе, при каждом открытии двери он начинает раскачиваться, подтверждая, что все без обмана. Правда сам шарик закреплен при помощи лески, но заметить это трудно и впечатлений не портит.</p>\r\n<p class=\"rtecenter\">\r\n	<iframe allowfullscreen=\"\" class=\"youtube-player\" frameborder=\"0\" height=\"300\" src=\"http://www.youtube.com/embed/4eGqE3IpDYY\" title=\"YouTube video player\" type=\"text/html\" width=\"500\"></iframe></p>\r\n<p>\r\n	От себя добавим, что новый 11-и дюймовый MacBook Air действительно очень легкий. Не настолько, чтобы взлететь на воздушном шаре, но ощущать его вес вы не будете.</p>\r\n',NULL,3,1),(125,'В iOS 4.3 появится функция отмены загрузки приложения','<p>\r\n	По информации <a href=\"http://appadvice.com/appnn/2011/02/ios-43-finally-bringing-ability-cancel-app-downloads-progress/\"><strong>AppAdvice.com</strong></a> в iOS 4.3 появится функция отмены загрузки приложения из App Store. Теперь, когда вы случайно начали загружать приложение, у вас появится возможность отменить загрузку и вернуть деньги. Ранее для этого приходилось проходить сложную бюрократическую процедуру и связываться с представителями компании.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt','<p>\r\n	По информации <a href=\"http://appadvice.com/appnn/2011/02/ios-43-finally-bringing-ability-cancel-app-downloads-progress/\"><strong>AppAdvice.com</strong></a> в iOS 4.3 появится функция отмены загрузки приложения из App Store. Теперь, когда вы случайно начали загружать приложение, у вас появится возможность отменить загрузку и вернуть деньги. Ранее для этого приходилось проходить сложную бюрократическую процедуру и связываться с представителями компании.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/appstore-deleate.png\" style=\"width: 500px; height: 358px; \" /></p>\r\n<p>\r\n	Теперь достаточно привычным способом подержать иконку загружаемого приложения, чтобы в верхнем левом углу появилась кнопка отмены. Пока еще неизвестен механизм возврата денег, но его появление - очень хорошая новость.</p>\r\n',NULL,3,1),(126,'iOS 4.3 Beta 3 доступна для разработчиков','<p>\r\n	Третья по счету бета-версия iOS 4.3 стала доступна для загрузки разработчикам всего несколько минут назад. Еще не известно &nbsp;о нововведениях в свежей версии, но вероятно, в ней уже будет сервис подписок на регулярные издания. Напомню, что этот сервис могут анонсировать завтра, на пресс-конференции, посвященной выпуску <b>The Daily</b>.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ios43b3.png\" style=\"width: 500px; height: 254px; \" /></p>\r\n<p>\r\n	Скоро станет ясно, к','<p>\r\n	Третья по счету бета-версия iOS 4.3 стала доступна для загрузки разработчикам всего несколько минут назад. Еще не известно &nbsp;о нововведениях в свежей версии, но вероятно, в ней уже будет сервис подписок на регулярные издания. Напомню, что этот сервис могут анонсировать завтра, на пресс-конференции, посвященной выпуску <b>The Daily</b>.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ios43b3.png\" style=\"width: 500px; height: 254px; \" /></p>\r\n<p>\r\n	Скоро станет ясно, как связанны завтрашнее событие, исчезновение приложения Sony из App Store и новая прошивка.</p>\r\n',NULL,3,1),(127,'Появились фотографии экрана iPad 2 и его характеристики','<p>\r\n	Журналисты из <strong>9to5mac</strong> при поддержке <strong>iFixYouri</strong> <a href=\"http://www.9to5mac.com/50297/exclusive-ipad-2-screen-fresh-from-china\"><strong>достали фотографии</strong></a> экрана iPad следующего поколения. Фото пришли из Китая и на них запечатлены два 9,7 экрана. Предположительно от устройств первого и второго поколения.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ipad2scr.jpg\" style=\"width: 500px; height: 282px; \" /></p>\r\n<p>\r\n	Трудно суд','<p>\r\n	Журналисты из <strong>9to5mac</strong> при поддержке <strong>iFixYouri</strong> <a href=\"http://www.9to5mac.com/50297/exclusive-ipad-2-screen-fresh-from-china\"><strong>достали фотографии</strong></a> экрана iPad следующего поколения. Фото пришли из Китая и на них запечатлены два 9,7 экрана. Предположительно от устройств первого и второго поколения.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ipad2scr.jpg\" style=\"width: 500px; height: 282px; \" /></p>\r\n<p>\r\n	Трудно судить о его разрешении и характеристиках в выключенном состоянии. Известно, что новый экран легче и на 1 миллиметр тоньше чем экран iPad и обладает меньшей рамкой. Эти характеристики позволят сделать iPad 2.0 значительно легче.</p>\r\n\r\n<h2>\r\n	ХАРАКТЕРИСТИКИ</h2>\r\n<p>\r\n	<br />\r\n	Удалось приблизительно расшифровать серийный номер нового дисплея - LP097X02-SLN1. В нем зашифрованы характеристики устройства и название производителя.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ipad2scr-serial.jpg\" style=\"width: 500px; height: 374px; \" /></p>\r\n<p>\r\n	Расшифровка:</p>\r\n<p>\r\n	(<strong>LP</strong>)097&times;02 SLN1 = LG Phillips<br />\r\n	LP(<strong>097</strong>)x02 SLN1 = 9.7 дюймовый дисплей<br />\r\n	LP097x(<strong>02</strong>) SLN1 = использован на дисплеях с xga,sxga,wsxga<br />\r\n	LP097x02 (<b>SL</b>)N1 = H-IPS<br />\r\n	LP097x02 SL (<b>N1</b>) = Не Известно</p>\r\n<p>\r\n	Так что <strong>Lg Phillips 9.7in XGA 1024&times;768 H-IPS</strong> экран.</p>\r\n',NULL,3,1),(128,'Большие кадровые изменения в Apple.','<p>&nbsp;</p>\r\n<div><span style=\"font-size:16px;\"><strong>Большие кадровые изменения в Apple.</strong></span></div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://cs309420.userapi.com/v309420523/288b/sbTFtP8QyKM.jpg\" style=\"width: 650px; height: 396px; \"></div>\r\n<div>&nbsp;</div>\r\n<div>Сегодня в полвторого ночи по московскому времени Apple опубликовала пресс-релиз, объявив о существенных кадровых изменений.</div>\r\n<div>Сегодня компанию покидают два вице-президента:</div>\r\n<div>- Скотт Форсталл','<p>&nbsp;</p>\r\n<div><span style=\"font-size:16px;\"><strong>Большие кадровые изменения в Apple.</strong></span></div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://cs309420.userapi.com/v309420523/288b/sbTFtP8QyKM.jpg\" style=\"width: 650px; height: 396px; \"></div>\r\n<div>&nbsp;</div>\r\n<div>Сегодня в полвторого ночи по московскому времени Apple опубликовала пресс-релиз, объявив о существенных кадровых изменений.</div>\r\n<div>Сегодня компанию покидают два вице-президента:</div>\r\n<div>- Скотт Форсталл, отвечающий за разработку операционной системы iOS;</div>\r\n<div>- Джон Броветт, отвечавший за розницу компании — сеть магазинов Apple Store.</div>\r\n<div>&nbsp;</div>\r\n<div>Джон Броветт проработал в яблочной компании чуть больше полугода и за это время наблюдались негативные тенденции в сфере розничной торговли. Пока не нашли ему замену, за розницу будет отвечать лично Тим Кук.</div>\r\n<div>После масштабных перестановок Джонатан Айв, Боб Мэнсфилд, Эдди Кью и Крейг Федериги примут на себя следующие обязанности:&nbsp;</div>\r\n<div>Джонатан Айв в дополнение к промышленному дизайну, будет отвечать и за Human Interface (в том числе и в iOS).</div>\r\n<div>Эдди Кью, до этого запустивший iTunes Store и App Store, будет отвечать за онлайн-сервисы Siri и Maps, а также за консолидацию онлайн-услуг в единое целое.</div>\r\n<div>Крейг Федериги теперь ответственный за разработку OS X и iOS,.</div>\r\n<div>Боб Мэнсфилд остается в Apple еще, как минимум на 2 года, будет руководить новой командой \"Технологии\", которая сосредоточит свое внимание на беспроводных технологbz[ и полупроводников.</div>\r\n<div>&nbsp;</div>\r\n<div>Официальные причины отставки Аpple не сообщает и возможно мы их никогда не узнаем.</div>\r\n<div>Джона Броветта многие не возлюбили сразу, его идеями по ритейлу многие были не довольны. Поэтому его увольнение не стало ни для кого шоком. А вот ситуация со Скотом Форсталлом менее ясная. Конечно, формально Форсталл до следующего года станет советником Кука. Потенциальные факторы, которые сыграли против него: иной вкус в дизайне продуктов, инженерные навыки, стиль поведения и, похоже, он провалил проект “Карты” — именно под его началом был разработан этот проект, который на старте iOS 6 успели обозвать “картогейтом”. Сейчас карты резко улучшились, количество жалоб на них резко уменьшилось, но осадок остался. Кроме того, Форсталл отказался подписать письмо Тима Кука, в котором он извинялся за фиаско с Картами в iOS 6.</div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://cs309420.userapi.com/v309420523/2893/Z2VVIyDDp1Y.jpg\" style=\"width: 640px; height: 450px; \"></div>\r\n',NULL,3,1),(129,'Новые MacBook Pro получат видеокарты на базе NVIDIA Fermi','<p>\r\n	Третья по счету бета-версия Mac OS X 10.6.7 стала доступна для разработчиков. Это было бы еще одним скучным обновлением бета-версии, если бы не комментарий <strong>Netkas&#39;a</strong>&nbsp;у себя в <a href=\"http://netkas.org/?p=599\"><strong>блоге</strong></a>.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/fermi-netkas.png\" style=\"width: 500px; height: 150px; \" /></p>\r\n<p>\r\n	Остается теряться в догадках о том, что же означает данный комментарий. Но большинство предпол','<p>\r\n	Третья по счету бета-версия Mac OS X 10.6.7 стала доступна для разработчиков. Это было бы еще одним скучным обновлением бета-версии, если бы не комментарий <strong>Netkas&#39;a</strong>&nbsp;у себя в <a href=\"http://netkas.org/?p=599\"><strong>блоге</strong></a>.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/fermi-netkas.png\" style=\"width: 500px; height: 150px; \" /></p>\r\n<p>\r\n	Остается теряться в догадках о том, что же означает данный комментарий. Но большинство предполагает, что в новой бета-версии появилась поддержка топовых видеокарт от NVIDIA. Означать это может только одно - скоро появятся новые маки и видеокарты в них будут довольно мощные. Связано ли это с грядущим обновлением линейки MacBook Pro пока неизвестно, но ожидаемо.</p>\r\n',NULL,3,1),(130,'Эксклюзивно для iPad - The Daily стало доступно для подписчиков','<p>\r\n	В среду 2 февраля, в 19-00 по мск, глава <strong>News Corp.</strong> Руперт Мердок представил публике свой новый проект - электронное издание <strong>The Daily</strong>. Это первое электронное издание, которое создано специально для iPad. Со временем <strong>The Daily</strong> появится и на других планшетных компьютерах. По словам Руперта Мердока это издание будет наполнятся только эксклюзивными материалами и будет предоставлять всю необходимую подписчикам информацию.</p>\r\n<p class=\"rtecen','<p>\r\n	В среду 2 февраля, в 19-00 по мск, глава <strong>News Corp.</strong> Руперт Мердок представил публике свой новый проект - электронное издание <strong>The Daily</strong>. Это первое электронное издание, которое создано специально для iPad. Со временем <strong>The Daily</strong> появится и на других планшетных компьютерах. По словам Руперта Мердока это издание будет наполнятся только эксклюзивными материалами и будет предоставлять всю необходимую подписчикам информацию.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/daily.png\" style=\"width: 500px; height: 279px; \" /></p>\r\n<p>\r\n	Так же это событие напрямую связано с новым сервисом <strong>In-App Subscriptions</strong> от Apple. Этот сервис позволит пользователю получать доступ к содержимому приложения на определенный период. Появление такого сервиса открывает новую эру электронных СМИ. Теперь бумажные издания смогут зарабатывать на электронных копиях своих материалов.</p>\r\n\r\n<h2>\r\n	ЧТО В НЕМ ОСОБЕННОГО</h2>\r\n<p>\r\n	<br />\r\n	На первый взгляд платить за новости в интернете - дурной тон, да и вся информация находится в открытом доступе. Но это только если вам эти новости не поступают от одной из крупнейших новостных компаний в режиме реального времени.</p>\r\n<p class=\"rtecenter\">\r\n	<iframe allowfullscreen=\"\" frameborder=\"0\" height=\"300\" src=\"http://www.youtube.com/embed/KHILJBw-104\" title=\"YouTube video player\" width=\"500\"></iframe></p>\r\n<p>\r\n	Список отличительных черт The Daily от других приложений достаточно внушителен.<br />\r\n	<br />\r\n	В своем активе оно имеет:<br />\r\n	<br />\r\n	- Более 100 страниц оригинальных новостей каждый день на протяжении года;<br />\r\n	- Эксклюзивные видеоматериалы;<br />\r\n	- Подборка статей, которые можно прослушать;<br />\r\n	- Панорамные фото на 360 градусов, которые можно легко поворачивать;<br />\r\n	- Интерактивные рейтинги, инфографика;<br />\r\n	- Функция &quot;Сохранить для прочтения&quot;;<br />\r\n	- Версии статей, которые можно отправить в Twitter, Facebook и по e-mail;<br />\r\n	- Встроенная функция комментариев в приложении;<br />\r\n	- Погода в том регионе, где вы сейчас находитесь;<br />\r\n	- Спортивные новости о любимой команде;<br />\r\n	- Кроссворды и головоломки.<br />\r\n	<br />\r\n	Ложкой дегтя ко всему этому - приложение пока доступно только в американском App Store и новости в нем рассчитаны на американскую аудиторию. Но никто не мешает отечественным СМИ сделать что либо подобное для своей аудитории.</p>\r\n<h2>\r\n	КАК ЭТО БЫЛО</h2>\r\n<p>\r\n	<br />\r\n	Сама презентация была скучна и не выразительна. Вначале Руперт Мердок долго рассказывал о iPad и его вкладе в будущее. Дальше появились трое грустных парней из Apple и вкратце показали основные функции нового приложения. За презентацией последовали вопросы ни о чем.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/daily-voprosi.png\" style=\"width: 500px; height: 274px; \" /></p>\r\n<p>\r\n	Зачем и кому нужна была эта презентация - не ясно. Возможно для господина Мердока это событие, но для нас с вами это еще одно из миллионов приложений для iPad, которое сочетает в себе уже доступные функции, причем доступные бесплатно. Для компании Apple это был повод рассказать о новом сервисе, но они уклонялись как могли, и обещали позже рассказать об этом в следующий раз.</p>\r\n',NULL,3,1),(131,'Skype для iPad 2.0 появится в апреле','<p>\r\n	Очередной слух о iPad 2.0 с возможностью видеозвонков появился после того, как Сэр Элтон Джон в своем <a href=\"http://www.bbc.co.uk/news/entertainment-arts-12379815\"><strong>интервью</strong></a> для ВВС, рассказал о планах приобретения &quot;iPad для Skype&quot; в апреле.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/elton-skype-ipad.png\" style=\"width: 500px; height: 316px;\" /></p>\r\n<p>\r\n	Причем сам певец отмечает, что он убежденный луддит и у него нет ни компьютера, ','<p>\r\n	Очередной слух о iPad 2.0 с возможностью видеозвонков появился после того, как Сэр Элтон Джон в своем <a href=\"http://www.bbc.co.uk/news/entertainment-arts-12379815\"><strong>интервью</strong></a> для ВВС, рассказал о планах приобретения &quot;iPad для Skype&quot; в апреле.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/elton-skype-ipad.png\" style=\"width: 500px; height: 316px;\" /></p>\r\n<p>\r\n	Причем сам певец отмечает, что он убежденный луддит и у него нет ни компьютера, ни iPhone или iPod. Он до сих пор пользуется бумажной записной книжкой и покупка iPad станет для него &quot;переходом&quot; в 21 век. Хотя остается вопрос, откуда тогда он узнал про Skype?</p>\r\n\r\n<h2>\r\n	ПРИЧИНА СЛУХА</h2>\r\n<p>\r\n	<br />\r\n	О появлении Skype для iPad впервые заговорили перед выставкой CES 2011, когда Skype представил возможность видеозвонков для iOS версии приложения. Владельцы iPad ожидали версии для планшета, но не сбылось. Возможно в апреле Skype для iPad получит не только Элтон Джон, но и все желающие. Поговаривают, что это произойдет на презентации iPad 2.0, которая намечается в феврале.</p>\r\n',NULL,3,1),(132,'Вышла новая реклама iOS устройств и конкурентов','<p>\r\n	Прошедшая пятница была полна событиями, но больше всего порадовала реклама. AT&amp;T и Verizon выпустили практически одновременно видео, в которых указывают на проблемы конкурентов. Motorola продолжает рекламную кампанию против Apple сравнивая ее с большим братом. А разработчики Game for Cats дали поиграть iPad диким кошкам. Далее - более подробно.</p>\r\n<h2>\r\n	VERIZON И iPHONE CDMA</h2>\r\n<p>\r\n	<br />\r\n	Сосем недавно открылись предзаказы на CDMA версию смартфона от Apple и первая партия был','<p>\r\n	Прошедшая пятница была полна событиями, но больше всего порадовала реклама. AT&amp;T и Verizon выпустили практически одновременно видео, в которых указывают на проблемы конкурентов. Motorola продолжает рекламную кампанию против Apple сравнивая ее с большим братом. А разработчики Game for Cats дали поиграть iPad диким кошкам. Далее - более подробно.</p>\r\n<h2>\r\n	VERIZON И iPHONE CDMA</h2>\r\n<p>\r\n	<br />\r\n	Сосем недавно открылись предзаказы на CDMA версию смартфона от Apple и первая партия была распродана всего за 17 часов. Для тех кто еще не купил или не решился купить Verizon выпустил отличный рекламный ролик.</p>\r\n<p class=\"rtecenter\">\r\n	<iframe allowfullscreen=\"\" frameborder=\"0\" height=\"300\" src=\"http://www.youtube.com/embed/e2H-3Gf_fL8\" title=\"YouTube video player\" width=\"500\"></iframe></p>\r\n<p>\r\n	Ключевой фразой стала &quot;<strong>Сейчас я могу тебя слышать</strong>&quot;, что явно является камнем в огород AT&amp;T у которой наблюдаются проблемы с качеством сигнала.</p>\r\n\r\n<h2>\r\n	AT&amp;T И iPHONE GSM</h2>\r\n<p>\r\n	<br />\r\n	Бывший iPhone-монополист не остался в долгу и помимо увеличения объема трафика вдвое выпустил короткую и емкую рекламу своего контракта для iPhone.</p>\r\n<p class=\"rtecenter\">\r\n	<iframe allowfullscreen=\"\" frameborder=\"0\" height=\"300\" src=\"http://www.youtube.com/embed/8M5u6ES7BBo\" title=\"YouTube video player\" width=\"500\"></iframe></p>\r\n<p>\r\n	Суть этой рекламы в том, что 3G от AT&amp;T позволит одновременно пользоваться интернетом и голосовыми звонками. К сожалению стандарт CDMA позволяет одновременно пользоваться только чем-то одним.</p>\r\n<h2>\r\n	MOTOROLA И XOOM</h2>\r\n<p>\r\n	<br />\r\n	Компания Motorola так стремится создать ажиотаж вокруг своего &quot;убийцы iPad&quot;, что снимает один рекламный ролик за другим. В них он называет iPad большим телефоном и сравнивает Apple с Большим Братом. Последнее отсылает нас в 1984 год, когда Apple точно так же сравнивала с литературным персонажем IBM.</p>\r\n<p class=\"rtecenter\">\r\n	<iframe allowfullscreen=\"\" frameborder=\"0\" height=\"300\" src=\"http://www.youtube.com/embed/09vmRWeNZSM\" title=\"YouTube video player\" width=\"500\"></iframe></p>\r\n<p>\r\n	Это лишь малая часть рекламного ролика, который появится на экранах во время американского суперкубка. У этого видео есть только один недостаток - устройство которое в нем представляют пока еще не готово. Будет очень печально, если реальный продукт окажется несколько хуже чем его прототип.</p>\r\n<h2>\r\n	GAME FOR CATS И iPAD</h2>\r\n<p>\r\n	<br />\r\n	Последним интересным видео пятницы стала реклама Game for Cats, увлекательной игры на iPad предназначенной для кошек. На сей раз в нее дали поиграть различным диким представителям вида кошачих.</p>\r\n<p class=\"rtecenter\">\r\n	<iframe allowfullscreen=\"\" frameborder=\"0\" height=\"300\" src=\"http://www.youtube.com/embed/DKBhTr5-ryg\" title=\"YouTube video player\" width=\"500\"></iframe></p>\r\n<p>\r\n	Символично, что животные были показаны в порядке появления операционных систем Mac OS, которые традиционно носили имя той или иной дикой кошки.</p>\r\n',NULL,3,1),(133,'iLuv представляет аксессуары для iPad','<p>\r\n	Всемирно известный производитель аксессуаров для продукции Apple и не только с явно китайскими корнями и забавным названием iLuv на днях&nbsp;представил полный набор аксессуаров для iPad&nbsp;</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\" iLuv for iPad\" class=\"size-full wp-image-219 \" height=\"198\" src=\"http://www.macov.net/wp-content/uploads/2010/02/screen-capture.png\" title=\" iLuv iPad\" width=\"458\" /></p>\r\n','<p>\r\n	Всемирно известный производитель аксессуаров для продукции Apple и не только с явно китайскими корнями и забавным названием iLuv на днях&nbsp;представил полный набор аксессуаров для iPad&nbsp;</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\" iLuv for iPad\" class=\"size-full wp-image-219 \" height=\"198\" src=\"http://www.macov.net/wp-content/uploads/2010/02/screen-capture.png\" title=\" iLuv iPad\" width=\"458\" /></p>\r\n\r\n<p>\r\n	&nbsp;В асортименте компании уже есть пара чехлов, внешние динамики, пленочки и другие, жизненно необходимые дополнения для нового планшетника. Факт появления аксессуаров для iPad не может не радовать - по крайней мере можно будет спасти гаджет от царапин с первых дней покупки. Ссылка на сайт производителя <a href=\"http://www.i-luv.com/product_list.asp?category_cd=t41&amp;cat_lev=1&amp;&amp;keyword=\" title=\"iLuv\">iLuv</a></p>\r\n',NULL,3,1),(134,'В 2011 году нас ждут новые Macbook Pro и iMac?','<p>\r\n	Конечно с трудом верится, но некоторые действительно считают, что Apple может предоставить новую линейку домашних компьютеров iMac и ноутбуков Macbook Pro в следующем году. Ресурс DigiTimes докладывает, что обновление действительно произойдет.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"http://macov.net/sites/default/files/imac-2010-12-16-arrows.jpg\" style=\"width: 600px; height: 382px;\" /></p>\r\n<p>\r\n	<a href=\"http://macov.net/news/new-imac-and-macbook-pro\">Подробнее...</a></p>\r\n','<p>\r\n	Конечно с трудом верится, но некоторые действительно считают, что Apple может предоставить новую линейку домашних компьютеров iMac и ноутбуков Macbook Pro в следующем году. Ресурс DigiTimes докладывает, что обновление действительно произойдет.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"http://macov.net/sites/default/files/imac-2010-12-16-arrows.jpg\" style=\"width: 600px; height: 382px;\" /></p>\r\n<p>\r\n	<a href=\"http://macov.net/news/new-imac-and-macbook-pro\">Подробнее...</a></p>\r\n\r\n<p>\r\n	Конечно с трудом верится, но некоторые действительно считают, что Apple может предоставить новую линейку домашних компьютеров iMac и ноутбуков Macbook Pro в следующем году. Ресурс DigiTimes докладывает, что обновление действительно произойдет.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"http://macov.net/sites/default/files/imac-2010-12-16-arrows.jpg\" style=\"width: 600px; height: 382px;\" /></p>\r\n<p>\r\n	Цитируем:&nbsp;&quot;a new panel size and a price point for the mainstream market.&quot;, что в переводе означает - новые диагонали ( видимо iMac ) и ценовая политика, направеленная на массовый сегмент. Касательно Macbook Pro опять проскакивают слухе о полной смене дизайна и Mac Os X 10.7 Lion на борту.</p>\r\n<p>\r\n	Новые машины обещают показать уже в первой половине года, так что ждать осталось не так уж и долго.&nbsp;StayTuned!</p>\r\n<p>\r\n	&nbsp;</p>\r\n',NULL,3,1),(135,'Завтра покажут iPhone CDMA от Verizon?','<p>\r\n	И опять в центре внимания слухи. Всему виной статья <a href=\"http://online.wsj.com/article/SB10001424052748704739504576068170230339348.html\"><strong>The Wall Street Journal</strong></a>, которая повествует о скором появлении <strong>CDMA</strong> версии iPhone у оператора Verizon Wireless. Сразу скажу - статья более чем спорная, никаких конкретных фактов не приводится, кроме домыслов и предположений.</p>\r\n<p>\r\n	Все крутиться вокруг <strong>События</strong>, которое произойдет завтра, <stro','<p>\r\n	И опять в центре внимания слухи. Всему виной статья <a href=\"http://online.wsj.com/article/SB10001424052748704739504576068170230339348.html\"><strong>The Wall Street Journal</strong></a>, которая повествует о скором появлении <strong>CDMA</strong> версии iPhone у оператора Verizon Wireless. Сразу скажу - статья более чем спорная, никаких конкретных фактов не приводится, кроме домыслов и предположений.</p>\r\n<p>\r\n	Все крутиться вокруг <strong>События</strong>, которое произойдет завтра, <strong>11 января</strong> и на котором Verizon представит &quot;нечто&quot;.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/priglashenie.png\" style=\"width: 348px; height: 296px;\" /></p>\r\n<p>\r\n	Можно сопоставить факты, предшествующие такому заявлению. Давайте так и поступим.</p>\r\n\r\n<h2>\r\n	АРГУМЕНТЫ И ФАКТЫ</h2>\r\n<div>\r\n	<br />\r\n	У громих заявлений в таких изданиях, как <strong>Wall Street Journal</strong>, должна быть причина. И если это не инсайдерская информация, в чем я очень сомневаюсь, то аналитическое предположение. Что же говорит в пользу того, что первого февраля компания Verizon все таки получит iPhone?</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	Одним из аргументов я считаю тот факт, что Apple из-за своего эксклюзива с AT&amp;T начала проигрывать android<strong>&#39;</strong>у&nbsp;гонку за потребителя. Прошедшая недавно выставка <strong>CES</strong> явила миру огромное количество смартфонов, способных потеснить Apple на их рынке. Не качеством так количеством. И контракт с Verizon не будет являться спасательным кругом - это еще один этап развития продукта. Просто настало время завоевывать новые рынки.</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	Второй аргумент - Verizon ввел в эксплуатацию линии 4-го поколения&nbsp;(LTE), избавив своих абонентов от проблем с сигналом и предоставив шикарные безлимитные тарифы на мобильный интернет. Момент для появления на этом поле iPhone будет удачным как никогда. Особенно если это будет LTE iPhone.</div>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/lte_cdma_iphone.png\" style=\"width: 300px; height: 196px;\" /></p>\r\n<div>\r\n	Ну а теперь факты - в руки журналистам попали фотографии корпуса нового iPhone, который с одинаковым успехом может являться корпусом как 5-го поколения смартфонов, так и корпусом CDMA аппарата.</div>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/iphone_5 copy.PNG\" style=\"width: 500px; height: 258px;\" /></p>\r\n<div>\r\n	Ну а разговоры о том, что в Купертино заняты разработкой такого аппарата идут уже не первый год. Но пока это только разговоры и слухи.&nbsp;</div>\r\n<h2>\r\n	ЖЕЛАЕМОЕ ЗА ДЕЙСТВИТЕЛЬНОЕ</h2>\r\n<div>\r\n	<br />\r\n	Так было уже не раз, когда различные аналитики-предсказатели в один голос заявляли о событии такого масштаба и это событие оказывалось не менее значимым, но совершенно не связанным с предсказанием. Да и шумиха вокруг iPhone в прессе может быть нужна лишь для привлечения внимания к событию как таковому.</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	Хотя интересные события уже произошли - на выставке CES была представлена сеть 4G и около десяти устройств (планшеты/смартфоны), которые будут выпущены в ближайшем будущем.</div>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/mnogo_gadjetov.png\" style=\"width: 300px; height: 225px;\" /></p>\r\n<div>\r\n	Не будет ли для Стива Джобса зазорно так просто отдать свое детище в копилку Verizon? Его продукты уникальны и неповторимы. Легко ли сделать свое любимое детище &quot;одним из&quot;?</div>\r\n<h2>\r\n	ВЫВОДЫ</h2>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	Слухи и домыслы не лучшая основа для новостей. Возможно мечтам о CDMA версии iPhone и суждено будет сбыться. Может даже завтра. Мы будем следить за этим и обязательно проинформируем вас одними из первых.</div>\r\n',NULL,3,1),(136,'Пародия на Mac Or PC от Parallels ','<p>\r\n	Помните&nbsp;<strong style=\"margin: 0px; padding: 0px; border-width: 0px; line-height: 1.2em;\">Furia &amp; Beau-J </strong>с хитом <strong>Mac Or PC</strong>? (http://macov.net/news/mac-or-pc.html)</p>\r\n<p>\r\n	Маркетологи <strong>Parallels</strong>, вдохновившись таким креативом, решили выпустить собственное рекламное видео. В основе аудио дорожки - песня Eminem&#39;а а визуальный ряд позаимствован у Джеймса Кэмерона и его Аватара. Получилось весьма забавно.</p>\r\n<p class=\"rtecenter\">\r\n	<im','<p>\r\n	Помните&nbsp;<strong style=\"margin: 0px; padding: 0px; border-width: 0px; line-height: 1.2em;\">Furia &amp; Beau-J </strong>с хитом <strong>Mac Or PC</strong>? (http://macov.net/news/mac-or-pc.html)</p>\r\n<p>\r\n	Маркетологи <strong>Parallels</strong>, вдохновившись таким креативом, решили выпустить собственное рекламное видео. В основе аудио дорожки - песня Eminem&#39;а а визуальный ряд позаимствован у Джеймса Кэмерона и его Аватара. Получилось весьма забавно.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"Parallels video\" src=\"http://macov.net/sites/default/files/macpc.jpg\" style=\"width: 631px; height: 352px;\" /></p>\r\n<p>\r\n	<a href=\"http://macov.net/news/parallels-mac-or-pc-video\">Подробнее...</a></p>\r\n\r\n<p>\r\n	&nbsp;</p>\r\n<p style=\"font: 13px/160% Arial,Tahoma,Helvetica,sans-serif;\">\r\n	Помните&nbsp;<strong style=\"margin: 0px; padding: 0px; border-width: 0px; line-height: 1.2em;\">Furia &amp; Beau-J&nbsp;</strong>с хитом&nbsp;<strong>Mac Or PC</strong>? (http://macov.net/news/mac-or-pc.html)</p>\r\n<p style=\"font: 13px/160% Arial,Tahoma,Helvetica,sans-serif;\">\r\n	Маркетологи&nbsp;<strong>Parallels</strong>, вдохновившись таким креативом, решили выпустить собственное рекламное видео. В основе аудио дорожки - песня Eminem&#39;а а визуальный ряд позаимствован у Джеймса Кэмерона и его Аватара. Получилось весьма забавно.</p>\r\n<p class=\"rtecenter\" style=\"font: 13px/160% Arial,Tahoma,Helvetica,sans-serif; text-align: center;\">\r\n	<img alt=\"Parallels video\" src=\"http://macov.net/sites/default/files/macpc.jpg\" style=\"width: 631px; height: 352px;\" /></p>\r\n<p style=\"font: 13px/160% Arial,Tahoma,Helvetica,sans-serif;\">\r\n	I&#39;m not ashamed</p>\r\n<p>\r\n	Of my PC</p>\r\n<p>\r\n	But Macintosh is&nbsp;</p>\r\n<p>\r\n	Oh so Sexy</p>\r\n<p class=\"rtecenter\">\r\n	<object height=\"385\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/SlUGAhKHoyM?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"385\" src=\"http://www.youtube.com/v/SlUGAhKHoyM?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	&nbsp;</p>\r\n',NULL,3,1),(137,'App Store для iPad получил новый поиск','<p>\r\n	Хорошая новость для владельцев iPad - в App Store для этого устройства обновился поиск по приложениям. Теперь найти интересующую вас программу стало намного проще. Появилась возможность настроить поиск по таким критериям как <strong>категория</strong>, <strong>дата выпуска</strong>, <strong>рейтинг</strong>, <strong>цена</strong> и <strong>устройство</strong>.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/applestorenew.png\" style=\"width: 500px; height: 265px;\" /></p>\r\n','<p>\r\n	Хорошая новость для владельцев iPad - в App Store для этого устройства обновился поиск по приложениям. Теперь найти интересующую вас программу стало намного проще. Появилась возможность настроить поиск по таким критериям как <strong>категория</strong>, <strong>дата выпуска</strong>, <strong>рейтинг</strong>, <strong>цена</strong> и <strong>устройство</strong>.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/applestorenew.png\" style=\"width: 500px; height: 265px;\" /></p>\r\n<p>\r\n	Помимо поиска так же обновились статусы приложений. Теперь, купленная но не установленная программа будет легко опознана по кнопке <strong>Install</strong> вместо привычной <strong>Buy</strong>.</p>\r\n<p>\r\n	Пока новинки радуют только владельцев iPad, но не исключено и появление этих функций в App Store для остальных iOS устройств. Вероятно это будет упрощенная версия поиска, так как 4&#39; экран не позволит вместить в себе все критерии поиска.</p>\r\n',NULL,3,1),(138,'Вышла Greenpois0n, утилита для непривязанного джеилбрейка 4.2.1 + ССЫЛКА','<p>\r\n	<strong>Chronic Dev Team</strong> выпустили обновленную версию <strong>Greenpois0n</strong>, утилиты для непривязанного джеилбрейка устройств с <b>iOS 4.2.1.</b> Теперь у вас появится возможность перезагружать свой девайс, без необходимости загружать его через компьютер.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/greenpois0n.png\" style=\"width: 500px; height: 279px;\" /></p>\r\n<p>\r\n	Новая версия пока доступна только для <b>Mac</b> и <b>не</b> рекоменд','<p>\r\n	<strong>Chronic Dev Team</strong> выпустили обновленную версию <strong>Greenpois0n</strong>, утилиты для непривязанного джеилбрейка устройств с <b>iOS 4.2.1.</b> Теперь у вас появится возможность перезагружать свой девайс, без необходимости загружать его через компьютер.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/greenpois0n.png\" style=\"width: 500px; height: 279px;\" /></p>\r\n<p>\r\n	Новая версия пока доступна только для <b>Mac</b> и <b>не</b> рекомендуется к установке тем, кто нуждается в анлоке своего устройства.</p>\r\n<p>\r\n	Скачать утилиту можно на сайте разработчиков <b><a href=\"http://www.greenpois0n.com//dl/greenpois0n-osx_rc5.zip\">Greenpois0n</a></b>, но из за большого количества желающих сервис может не работать. Ребята из <b>Cult of Mac </b>дают альтернативную ссылку <a href=\"http://www.mediafire.com/?1kdf96u59b12zte\"><strong>СКАЧАТЬ</strong></a></p>\r\n',NULL,3,1),(139,'Внезапно! ','\r\n<p>\r\n	У нас заработали новости на главной странице! И это прекрасно! Давайте вместе тестировать!</p>\r\n<p>\r\n	<img alt=\"\" src=\"http://macov.net/sites/default/files/appstore-deleate.png\" style=\"width: 500px; height: 358px; \" /></p>\r\n','\r\n<p>\r\n	У нас заработали новости на главной странице! И это прекрасно! Давайте вместе тестировать!</p>\r\n<p>\r\n	<img alt=\"\" src=\"http://macov.net/sites/default/files/appstore-deleate.png\" style=\"width: 500px; height: 358px; \" /></p>\r\n',NULL,3,1),(140,'Появился процессор для фантастического смартфона','<p>\r\n	Компания Texas Instruments представила свой новый процессор для мобильных устройств OMAP 5 . Чип, ориентированный на применение в смартфонах и планшетах, оборудован двумя ядрами ARM Cortex-A15, выполненными по 28-нанометровому техпроцессу и работающими на частоте до 2 ГГц.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/omap5.png\" style=\"width: 500px; height: 300px; \" /></p>\r\n<p>\r\n	Как утверждает производитель, процессор пятого поколения в три раза производительнее, чем ','<p>\r\n	Компания Texas Instruments представила свой новый процессор для мобильных устройств OMAP 5 . Чип, ориентированный на применение в смартфонах и планшетах, оборудован двумя ядрами ARM Cortex-A15, выполненными по 28-нанометровому техпроцессу и работающими на частоте до 2 ГГц.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/omap5.png\" style=\"width: 500px; height: 300px; \" /></p>\r\n<p>\r\n	Как утверждает производитель, процессор пятого поколения в три раза производительнее, чем чип, установленный в RIM BlackBerry PlayBook. Графическая производительность новинок в пять раз больше, а кроме того, платформа OMAP 5 поддерживает управление жестами, как Microsoft Kinect.</p>\r\n\r\n<h2>\r\n	ЧТО ЭТО ЗНАЧИТ</h2>\r\n<p>\r\n	<br />\r\n	Технические характеристики не дадут вам полностью проникнутся событием. Для этого предлагаем вам посмотреть видео, в котором фантастика становится реальностью.</p>\r\n<p class=\"rtecenter\">\r\n	<iframe allowfullscreen=\"\" frameborder=\"0\" height=\"300\" src=\"http://www.youtube.com/embed/qOH3iwH6dbE\" title=\"YouTube video player\" width=\"500\"></iframe></p>\r\n<p>\r\n	Технические характеристики чипа впечатляют. Он способен одновременно обрабатывать видео с четырех камер, поддерживает воспроизведение и запись 3D-контента с разрешением 1080p, умеют конвертировать 2D в трехмерное видео в режиме реального времени. С помощью пико-проектора платформа TI OMAP 5 может создавать интерактивные проекции на столе или стене. При этом пользователь получит возможность с помощью жестов управлять проекциями. Первые устройства с этими чипами на борту должны появится в середине 2012 года.</p>\r\n',NULL,3,1),(141,'Выбираем между iPhone 3GS и iPhone 4','<p>\r\n	С выходом iPhone 4 самый главный вопрос изменился, и теперь он звучит: &quot;Какой iPhone купить, 3GS или 4&quot;? Но здесь однозначного ответа нет и не будет - каждый сам определяет свои потребности и финансовые возможности. Но вот определиться в том, чем они отличаются не помешает.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/iphone4-sgs-1.png\" style=\"width: 500px; height: 261px; \" /></p>\r\n<p>\r\n	Для всех, кто еще не решил, какой именно смартфон ему нужен - эта стать','<p>\r\n	С выходом iPhone 4 самый главный вопрос изменился, и теперь он звучит: &quot;Какой iPhone купить, 3GS или 4&quot;? Но здесь однозначного ответа нет и не будет - каждый сам определяет свои потребности и финансовые возможности. Но вот определиться в том, чем они отличаются не помешает.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/iphone4-sgs-1.png\" style=\"width: 500px; height: 261px; \" /></p>\r\n<p>\r\n	Для всех, кто еще не решил, какой именно смартфон ему нужен - эта статья.</p>\r\n\r\n<h2>\r\n	НАГЛЯДНЫЕ ОТЛИЧИЯ</h2>\r\n<p>\r\n	<br />\r\n	Для людей, желающих быстро и без лишних подробностей узнать различия между iPhone 3GS и iPhone 4 есть наглядное пособие. В нем вы узнаете основные отличия устройств.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/iphone4-sgs-2.jpeg\" style=\"width: 400px; height: 1290px; \" /></p>\r\n<p>\r\n	Для тех, кто не понимает английского или ему лень вглядываться в картинки - вкратце резюмирую. У iPhone 4 более мощный процессор, емкая батарея, больший объем памяти, камера 5мп со вспышкой и возможностью снимать HD видео. Так же у него тонкий корпус, Retina-дисплей, фронтальная камера для видеозвонков и Face Time, гироскоп и другие вкусности.</p>\r\n<h2>\r\n	ДЕТАЛЬНЫЕ ТЕХНИЧЕСКИЕ ПОДРОБНОСТИ</h2>\r\n<p>\r\n	<br />\r\n	Если вы любитель точных данных советую вам изучить следующую таблицу, в которой в полной мере отображены технические детали. Стоит обратить внимание на поддержку стандарта 802.11n - он присутствует только в iPhone 4.</p>\r\n<center>\r\n	<p class=\"rtecenter\">\r\n		<img alt=\"\" src=\"/sites/default/files/iphone4-sgs-3.png\" style=\"width: 500px; height: 538px; \" /></p>\r\n</center>\r\n<p>\r\n	Если эти технические преимущества четвертой модели для вас не играют никакой роли - ваш аппарат 3GS, он удобно лежит в руке и цена его приятно радует глаз. Ну а если вы боитесь опоздать на экспресс высоких технологий и иметь самый современный на сегодняшний день смартфон - iPhone 4 из стекла и стали именно то,что вам нужно.</p>\r\n<p>\r\n	Купить понравившийся вам iPhone вы можете прямо у нас - <a href=\"http://macov.net/kupit-apple-iphone-4-3gs\"><strong>Купить iPhone Сейчас!</strong></a></p>\r\n',NULL,3,1),(142,'Появился официальный Google Translate для iOS','<p>\r\n	Компания Google только что выпустила iOS версию своего сервиса переводов - Google Translate. В программе поддерживается функция голосового ввода для 15 языков. Так же как и в web-сервисе утилита позволит перевести слово или фразу на один из 50 языков мира.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/GT1.png\" style=\"width: 500px; height: 320px; \" /></p>\r\n<p>\r\n	Для голосового ввода необходимо нажать на иконку микрофона и произнести фразу для перевода.</p>\r\n','<p>\r\n	Компания Google только что выпустила iOS версию своего сервиса переводов - Google Translate. В программе поддерживается функция голосового ввода для 15 языков. Так же как и в web-сервисе утилита позволит перевести слово или фразу на один из 50 языков мира.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/GT1.png\" style=\"width: 500px; height: 320px; \" /></p>\r\n<p>\r\n	Для голосового ввода необходимо нажать на иконку микрофона и произнести фразу для перевода.</p>\r\n\r\n<h2>\r\n	ПРОСЛУШАТЬ ПЕРЕВОД</h2>\r\n<p>\r\n	<br />\r\n	Так же у вас будет возможность прослушать перевод требуемой фразы, что будет осень полезно при переводе на язык, чьи буквы вы не знаете как произносить.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/GT2.png\" style=\"width: 500px; height: 364px; \" /></p>\r\n<p>\r\n	Эта функция полезна для чтения китайских надписей, произношение которых вы бы хотели узнать.</p>\r\n<p>\r\n	Пока приложение доступно только для iPhone и iPod с версией iOS выше 3. Скачать можно <a href=\"http://itunes.apple.com/us/app/google-translate/id414706506?mt=8&amp;ls=1\"><strong>бесплатно по ссылке</strong></a>.</p>\r\n',NULL,3,1),(143,'Всем планшетам планшет','<p>\r\n	Совсем скоро, в <strong>июне 2011</strong>, в продажу поступит планшет <a href=\"http://noteslate.com/\"><strong>NoteSlate</strong></a>, обладающий одноцветным дисплеем, прекрасным дизайном и низкой стоимостью. Всего за <strong>$99</strong> вы сможете получить удобное устройство для записей, черновиков и чтения электронных книг.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/NoteSlate BASIC.jpeg\" style=\"width: 500px; height: 306px; \" /></p>\r\n<p>\r\n	Концепция устройства про','<p>\r\n	Совсем скоро, в <strong>июне 2011</strong>, в продажу поступит планшет <a href=\"http://noteslate.com/\"><strong>NoteSlate</strong></a>, обладающий одноцветным дисплеем, прекрасным дизайном и низкой стоимостью. Всего за <strong>$99</strong> вы сможете получить удобное устройство для записей, черновиков и чтения электронных книг.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/NoteSlate BASIC.jpeg\" style=\"width: 500px; height: 306px; \" /></p>\r\n<p>\r\n	Концепция устройства проста - вы получаете электронную бумагу для записей, которая может быть всегда под рукой и в то же время девайс остается электронным устройством со всеми вытекающими.</p>\r\n\r\n<h2>\r\n	ЧТО БУДЕТ</h2>\r\n<p>\r\n	<br />\r\n	Устройство будет выглядеть как обыкновенный лист бумаги, только шириной в <strong>6 миллиметров</strong> и диагональю в <strong>13 дюймов</strong>. У него будет одноцветный дисплей ( цвет на выбор), стилус, батарея на <strong>180 часов</strong>. Так же опционально присутствует WiFi, но тогда заряд батареи будет расходоваться значительно быстрее.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/noteslate-piramyd.png\" style=\"width: 500px; height: 199px; \" /></p>\r\n<p>\r\n	Устройство не вступает в конкуренцию с планшетниками типа iPad, оно остается над схваткой. Предназначено для &quot;гиков&quot; экономящих свое время и деньги. Пока неизвестно когда такие устройства появятся у нас в продаже, но мы постараемся сделать все от нас зависящее.</p>\r\n',NULL,3,1),(144,'Сравненения производительности iPhone и iPad.','В сети появились результаты тестирования производительности Geekbench 4-поколения iPad. Прирост в производительности планшета, по сравнению с третьим поколением, увеличился более чем в 2 раза! В тестах Geekbench iPad 3 набрал 791 балл, iPhone 5 получил 1571 балл, а новый iPad 4 набрал 1757 баллов! Кроме того, стало известно что процессор A6X является двухядерным и работает на частоте 1,4 Ггц. Объем оперативной памяти в устройстве остался прежним – 1 ГБ.','<p><span style=\"font-size:16px;\"><strong>Сравненения производительности iPhone и iPad.</strong></span></p>\r\n<p>&nbsp;</p>\r\n<p>Появились результаты тестирования производительности iPad 2-го, 3-го и 4-го поколения и iPhone 5. Разница 3-го и 4-го айпада поразительна, увеличилась почти в 2 раза. Двухъядерный процессор A6X, работающий на частоте 1.4 Ггц, который стоит на новом айпаде дает о себе знать!</p>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" src=\"http://cs309420.userapi.com/v309420523/28ab/62MYKmecNgE.jpg\" style=\"width: 580px; height: 271px; \"></p>\r\n',NULL,3,1),(145,'ВИДЕО: Геймплей в Angry Birds Star Wars','<p><span style=\"font-size:16px;\"><strong>Геймплей в Angry Birds Star Wars</strong></span></p>\r\n<p>&nbsp;</p>\r\n<p><iframe allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"http://www.youtube.com/embed/0Yq9idnT1OM\" width=\"560\"></iframe></p>\r\n','<p><span style=\"font-size:16px;\"><strong>Геймплей в Angry Birds Star Wars</strong></span></p>\r\n<p>&nbsp;</p>\r\n<p><iframe allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"http://www.youtube.com/embed/0Yq9idnT1OM\" width=\"560\"></iframe></p>\r\n',NULL,3,1),(146,'ВИДЕО: Неофициальный промо-ролик iPad mini','<p><span style=\"font-size:16px;\"><strong>Неофициальный промо-ролик iPad mini</strong></span></p>\r\n<p>&nbsp;</p>\r\n<p><iframe allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"http://www.youtube.com/embed/Os87PLlyU4k\" width=\"560\"></iframe></p>\r\n','<p><span style=\"font-size:16px;\"><strong>Неофициальный промо-ролик iPad mini</strong></span></p>\r\n<p>&nbsp;</p>\r\n<p><iframe allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"http://www.youtube.com/embed/Os87PLlyU4k\" width=\"560\"></iframe></p>\r\n',NULL,3,1),(147,'Выход iTunes 11 задерживается.','<p>&nbsp;</p>\r\n<div><span style=\"font-size:16px;\"><strong>Выход iTunes 11 задерживается.</strong></span></div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://cs309420.userapi.com/v309420523/28b9/dS6guY3mmMA.jpg\" style=\"width: 635px; height: 404px; \"></div>\r\n<div>&nbsp;</div>\r\n<div>Представитель Apple сообщил редакции CNET, что не стоит ждать выход iTunes 11 до конца ноября. В сообщение он передал, что задержка связана с тем, что Apple нужно еще немного времени, чтобы сделать все на отлично. Но','<p>&nbsp;</p>\r\n<div><span style=\"font-size:16px;\"><strong>Выход iTunes 11 задерживается.</strong></span></div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://cs309420.userapi.com/v309420523/28b9/dS6guY3mmMA.jpg\" style=\"width: 635px; height: 404px; \"></div>\r\n<div>&nbsp;</div>\r\n<div>Представитель Apple сообщил редакции CNET, что не стоит ждать выход iTunes 11 до конца ноября. В сообщение он передал, что задержка связана с тем, что Apple нужно еще немного времени, чтобы сделать все на отлично. Но нам все-таки кажется, что задержка связана с вчерашними кадровыми перестановками в Apple.</div>\r\n<div>В любом случае, нам остается только ждать.</div>\r\n',NULL,3,1),(148,'Звездные войны возвращаются!','<p>&nbsp;</p>\r\n<div><span style=\"font-size:16px;\"><strong>Звездные войны возвращаются!</strong></span></div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"https://instagr.am/p/RHHVDvv30a/media/?size=l\" style=\"width: 612px; height: 612px; \"></div>\r\n<div>&nbsp;</div>\r\n<div>Корпорация The Walt Disney сообщила о покупке компании Джорджа Лукаса Lucasfilm Ltd. вместе с легендарной киносерией \"Звездные войны\".</div>','<p>&nbsp;</p>\r\n<div><span style=\"font-size:16px;\"><strong>Звездные войны возвращаются!</strong></span></div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"https://instagr.am/p/RHHVDvv30a/media/?size=l\" style=\"width: 612px; height: 612px; \"></div>\r\n<div>&nbsp;</div>\r\n<div>Корпорация The Walt Disney сообщила о покупке компании Джорджа Лукаса Lucasfilm Ltd. вместе с легендарной киносерией \"Звездные войны\".</div>\r\n<div>Глава The Walt Disney Роберт Игер объявил о намерение продолжить выпуск эпизодов популярной саги и уже к 2015 году выпустить седьмой эпизод \"Звездных войн\".</div>\r\n<div>Сделку на основании текущей стоимости акций оценивают в 4 миллиарда долларов.</div>\r\n<div>Создатель \"Звездных войн\" Джорд Лукас прокомментировал сделку так: \"Пришло время передать \"Звездные войны\" новому поколению кинематографистов. Я всегда верил, что \"Звездные войны\" смогут пережить меня, и решил, что очень важно передать их другим в течение моей жизни\".</div>\r\n',NULL,3,1),(149,'ВИДЕО: “The Launch Day video\" (\"День запуска\")','<p><span style=\"font-size:16px;\"><strong>“The Launch Day video\" (\"День запуска\")</strong></span></p>\r\n<p>&nbsp;</p>\r\n<p><iframe allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"http://www.youtube.com/embed/IDsUiW6OMDU\" width=\"560\"></iframe></p>\r\n<p>&nbsp;</p>\r\n<p>Apple опубликовала видео “The Launch Day video\" (\"День запуска\") со старта продаж iPhone 5.</p>\r\n','<p><span style=\"font-size:16px;\"><strong>“The Launch Day video\" (\"День запуска\")</strong></span></p>\r\n<p>&nbsp;</p>\r\n<p><iframe allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"http://www.youtube.com/embed/IDsUiW6OMDU\" width=\"560\"></iframe></p>\r\n<p>&nbsp;</p>\r\n<p>Apple опубликовала видео “The Launch Day video\" (\"День запуска\") со старта продаж iPhone 5.</p>\r\n',NULL,3,1),(150,'Китайскую \"Молнию\" разобрали.','<p>&nbsp;</p>\r\n<div><span style=\"font-size:16px;\"><strong>Китайскую \"Молнию\" разобрали.&nbsp;</strong></span></div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://cs309422.userapi.com/v309422523/5ad2/myaipNIU7tI.jpg\" style=\"width: 600px; height: 462px; \"></div>\r\n<div>&nbsp;</div>\r\n<div>Китайцы давно взломали чип нового кабеля Lightning (Молния) и даже уже наладили производство своих кабелей. Одну китайскую \"молнию\" разобрали и посмотрели что находится внутри.&nbsp;</div>','<p>&nbsp;</p>\r\n<div><span style=\"font-size:16px;\"><strong>Китайскую \"Молнию\" разобрали.&nbsp;</strong></span></div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://cs309422.userapi.com/v309422523/5ad2/myaipNIU7tI.jpg\" style=\"width: 600px; height: 462px; \"></div>\r\n<div>&nbsp;</div>\r\n<div>Китайцы давно взломали чип нового кабеля Lightning (Молния) и даже уже наладили производство своих кабелей. Одну китайскую \"молнию\" разобрали и посмотрели что находится внутри. Пишут, что китайские кабели работают хорошо при зарядке и синхронизации гаджета. Проблемы начаинаются при воспроизведении музыки через сторонние устройства, могут появится небольшие шумы.&nbsp;</div>\r\n<div>Китайцы выпускают свою кабеля разного качества, от некоторых может перенагреваться телефон, поэтому будьте осторожны!</div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://cs309422.userapi.com/v309422523/5ad9/oDQpA9qjr3g.jpg\" style=\"width: 600px; height: 397px; \"></div>\r\n<div>&nbsp;</div>\r\n',NULL,3,1),(151,'В iPad mini стерео звук!','<p>&nbsp;</p>\r\n<div><span style=\"font-size:16px;\"><strong>В iPad mini стерео звук!</strong></span></div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://cs309422.userapi.com/v309422523/5af0/0-On1iVNKoQ.jpg\" style=\"width: 641px; height: 171px; \"></div>','<p>&nbsp;</p>\r\n<div><span style=\"font-size:16px;\"><strong>В iPad mini стерео звук!</strong></span></div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://cs309422.userapi.com/v309422523/5af0/0-On1iVNKoQ.jpg\" style=\"width: 641px; height: 171px; \"></div>\r\n<div>&nbsp;</div>\r\n<div>Несколько дней назад Amazon запустила рекламу, на которой отмечены характеристики, по которым планшет Kindle Fire HD превосходит iPad mini. Одной из такой характеристикой было то, что в яблочном планшете отсутствует стерео звук. Кто-то решил узнать достоверную информацию у Фила Шиллера. Он ответил просто: \"Там есть стерео\".&nbsp;</div>\r\n<div>Больше на сайте Amazon этой рекламы нет.</div>\r\n<div>Напомним, что это первое первое устройство такого плана от Apple.</div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://cs309422.userapi.com/v309422523/5af7/ZRulTfqb4zs.jpg\" style=\"width: 593px; height: 343px; \"></div>\r\n',NULL,3,1),(152,'iPad mini разобрали.','<p>&nbsp;</p>\r\n<div><span style=\"font-size:16px;\"><strong>iPad mini разобрали.</strong></span></div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://cs309422.userapi.com/v309422523/5b0f/kQwcTMDeLBI.jpg\" style=\"width: 700px; height: 525px; \"></div>\r\n<div>&nbsp;</div>\r\n<div>Мастера из iFixit уже получили iPad mini немного раньше официальных продаж и как обычно они делают, разобрали и посмотрели что же находится внутри.&nbsp;</div>\r\n<div>Там они обнаружили стерео динамики, о наличие которых мы уже','<p>&nbsp;</p>\r\n<div><span style=\"font-size:16px;\"><strong>iPad mini разобрали.</strong></span></div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://cs309422.userapi.com/v309422523/5b0f/kQwcTMDeLBI.jpg\" style=\"width: 700px; height: 525px; \"></div>\r\n<div>&nbsp;</div>\r\n<div>Мастера из iFixit уже получили iPad mini немного раньше официальных продаж и как обычно они делают, разобрали и посмотрели что же находится внутри.&nbsp;</div>\r\n<div>Там они обнаружили стерео динамики, о наличие которых мы уже писали. Непонятно только почему стерео является уникальной фичей на мини, почему его нет на iPad 4?</div>\r\n<div>&nbsp;</div>\r\n<div><strong>Внутренности iPad mini:&nbsp;</strong></div>\r\n<ul style=\"margin-left: 40px; \">\r\n	<li>Батарея 3.72 V, 16.5 Whr, 4400 mAh</li>\r\n	<li>Broadcom IC BCM5976C0KUB6G</li>\r\n	<li>Samsung W1235 S6TNMR1X01 драйвер дисплея</li>\r\n	<li>SM4031 DA1232 SMCP043</li>\r\n	<li>416R 8227</li>\r\n	<li>HDU 2YC 34</li>\r\n	<li>Murata 339S0171 Wi-Fi модуль.</li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" src=\"http://cs309422.userapi.com/v309422523/5b18/zDTa8a_wliU.jpg\" style=\"width: 700px; height: 525px; \"></p>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" src=\"http://cs309422.userapi.com/v309422523/5b21/E7gOBi0hwjY.jpg\" style=\"width: 700px; height: 525px; \"></p>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" src=\"http://cs309422.userapi.com/v309422523/5b29/2cFx8LEfc8A.jpg\" style=\"width: 640px; height: 512px; \"></p>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" src=\"http://cs309422.userapi.com/v309422523/5b32/bLZPBLkPiU4.jpg\" style=\"width: 640px; height: 848px; \"></p>\r\n',NULL,3,1),(153,'Скотт Форсталл - специальный советник Тима Кука.','<p>&nbsp;</p>\r\n<div><span style=\"font-size:16px;\"><strong>Скотт Форсталл - специальный советник Тима Кука.</strong></span></div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://cs309422.userapi.com/v309422523/5b3a/zgHf5x_s8ag.jpg\" style=\"width: 630px; height: 320px; \"></div>\r\n<div>&nbsp;</div>\r\n<div>Недавно мы писали вам о <a href=\"http://macov.net/news/kadrovye-izmineniya-apple\">больших кадровых перестановках в Apple</a>. Стало известно, что Скотт Форсталл – старший вице-президент Apple отвеча','<p>&nbsp;</p>\r\n<div><span style=\"font-size:16px;\"><strong>Скотт Форсталл - специальный советник Тима Кука.</strong></span></div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://cs309422.userapi.com/v309422523/5b3a/zgHf5x_s8ag.jpg\" style=\"width: 630px; height: 320px; \"></div>\r\n<div>&nbsp;</div>\r\n<div>Недавно мы писали вам о <a href=\"http://macov.net/news/kadrovye-izmineniya-apple\">больших кадровых перестановках в Apple</a>. Стало известно, что Скотт Форсталл – старший вице-президент Apple отвечающий за разработку iOS покинул свой пост, ноон не ушел из компании, а переведен на другую менее значимую должность.</div>\r\n<div>&nbsp;</div>\r\n<div>Скотт Форсталл проработает в Apple весь 2013 год и будет занимать особую должность «специального советника генерального директора Apple». Дословно в отчете Apple сказано:</div>\r\n<div>«С 29 октября 2012 года Скотт Форсталл старший вице-президент мобильного подразделения, перешел с этой должности на новую роль в качестве специального советника генерального директора».</div>\r\n',NULL,3,1),(154,'Вышла iOS 6.0.1','<p><span style=\"font-size:16px;\"><strong>Вышла iOS 6.0.1</strong></span></p>\r\n<p><img alt=\"\" src=\"http://cs309422.userapi.com/v309422523/5b43/53sMlI0TYk4.jpg\" style=\"width: 233px; height: 350px; \"></p>\r\n<p>&nbsp;</p>\r\n<div>Сегодня ночью Apple выложила первое обновление шестой прошивке iOS 6.0.1.</div>\r\n<div>Кроме исправления ряда ошибок пользователи уже установившие &nbsp;обновление заявляют о более быстрой работе яблочного магазина App Store.</div>\r\n<div>&nbsp;</div>','<p><span style=\"font-size:16px;\"><strong>Вышла iOS 6.0.1</strong></span></p>\r\n<p><img alt=\"\" src=\"http://cs309422.userapi.com/v309422523/5b43/53sMlI0TYk4.jpg\" style=\"width: 233px; height: 350px; \"></p>\r\n<p>&nbsp;</p>\r\n<div>Сегодня ночью Apple выложила первое обновление шестой прошивке iOS 6.0.1.</div>\r\n<div>Кроме исправления ряда ошибок пользователи уже установившие &nbsp;обновление заявляют о более быстрой работе яблочного магазина App Store.</div>\r\n<div>&nbsp;</div>\r\n<div><strong>Изменения в iOS 6.0.1:</strong></div>\r\n<ul>\r\n	<li style=\"margin-left: 40px; \">Исправлена ошибка, препятствовавшая установке устройством iPhone 5 обновлений ПО беспроводным способом.</li>\r\n	<li style=\"margin-left: 40px; \">Исправлена ошибка, которая могла приводить к отображению горизонтальных линий поверх клавиатуры.</li>\r\n	<li style=\"margin-left: 40px; \">Устранена проблема, которая могла приводить к отказам в работе вспышки камеры.</li>\r\n	<li style=\"margin-left: 40px; \">Повышен уровень надежности работы iPhone 5 и iPod touch (5-го поколения) при подключении к зашифрованным сетям Wi-Fi WPA2.</li>\r\n	<li style=\"margin-left: 40px; \">Устранена проблема, в некоторых случаях препятствовавшая использованию устройством iPhone сотовых сетей.</li>\r\n	<li style=\"margin-left: 40px; \">Представлен единый переключатель «Сотовые данные» для iTunes Match.</li>\r\n	<li style=\"margin-left: 40px; \">Исправлена ошибка с защитой паролем, которая в отдельных случаях могла приводить к предоставлению доступа к информации карт Passbook с заблокированного экрана.</li>\r\n	<li style=\"margin-left: 40px; \">Исправлена ошибка, влиявшая на функциональность собраний Exchange.</li>\r\n</ul>\r\n<div>&nbsp;</div>\r\n',NULL,3,1),(155,'Вышла iOS 6.1 beta','<p><span style=\"font-size:16px;\"><strong>Вышла iOS 6.1 beta</strong></span></p>\r\n<p><img alt=\"\" src=\"http://cs309422.userapi.com/v309422523/5b4a/QYEA4bOK1Ek.jpg\" style=\"width: 203px; height: 350px; \"></p>\r\n<p>&nbsp;</p>\r\n<div>Кроме обновления iOS 6.0.1 Apple ночью выложила также и бету версию iOS 6.1. Прошивка доступна только разработчикам. Когда будет финальная версия доступная всем пока не ясно. Многие ждали в iOS 6.1 добавление русского языка в Siri, но его в бета версии нет. Возможно он появ','<p><span style=\"font-size:16px;\"><strong>Вышла iOS 6.1 beta</strong></span></p>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" src=\"http://cs309422.userapi.com/v309422523/5b4a/QYEA4bOK1Ek.jpg\" style=\"width: 203px; height: 350px; \"></p>\r\n<div>&nbsp;</div>\r\n<div>Кроме обновления iOS 6.0.1 Apple ночью выложила также и бету версию iOS 6.1. Прошивка доступна только разработчикам. Когда будет финальная версия доступная всем пока не ясно. Многие ждали в iOS 6.1 добавление русского языка в Siri, но его в бета версии нет. Возможно он появится в финальной версии, будем надеется на это.</div>\r\n<div>&nbsp;</div>\r\n<div>Изменения в iOS 6.1 beta:</div>\r\n<div>В прошивке содержится новый фреймворк Map Kit – MKLocalSearch. Который из текста может показывать места на карте. Например поиск по строке «Кинотеатр» выкинет вас на карту и покажет ближайшие к вам кинотеатры с информацией о каждом из них.</div>\r\n<div>На картах появилась большая кнопка «Сообщить о проблеме».</div>\r\n<div>Переделаны иконки управления треками на заблокированном рабочем столе.</div>\r\n<div>&nbsp;</div>\r\n<div>Данная бета истекает 30 ноября, а значит либо до 30 ноября последует еще одна бета, либо будет финальная версия 6.1.</div>\r\n',NULL,3,1),(156,'Вышел TouchPad от HP - планшет на WebOS','<p>\r\n	Вчера на специальной пресс-конференции HP представили свои новые мобильные устройства. Плоды совместных трудов инженеров Palm и HP призваны составить конкуренцию на рынке смартфонов и планшетов. Интереснее всего было увидеть планшет TouchPad с WebOS на борту, он действительно конкурирует не только &quot;железом&quot;, но и программной частью.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/touchpad-logo.png\" style=\"width: 500px; height: 315px; \" /></p>\r\n<p>\r\n	По словам с','<p>\r\n	Вчера на специальной пресс-конференции HP представили свои новые мобильные устройства. Плоды совместных трудов инженеров Palm и HP призваны составить конкуренцию на рынке смартфонов и планшетов. Интереснее всего было увидеть планшет TouchPad с WebOS на борту, он действительно конкурирует не только &quot;железом&quot;, но и программной частью.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/touchpad-logo.png\" style=\"width: 500px; height: 315px; \" /></p>\r\n<p>\r\n	По словам создателей - WebOS 3.0, на которой будет работать планшет, будет настолько революционным продуктом, что в результате займет лидирующие позиции на рынке. Убедиться в этом мы сможем уже летом этого года, когда устройства попадут в продажу.</p>\r\n\r\n<h2>\r\n	ПОДРОБНЕЕ О TOUCHPAD</h2>\r\n<p>\r\n	<br />\r\n	TouchPad представляет собой 9,7 дюймовый планшет, с двухядерным 1.2ГГц Qualcomm Snapdragon APQ8060 и 1 гигабайтом оперативной памяти на борту. Работать это все будет под управлением WebOS 3.0, которая поможет легко взаимодействовать всем вашим устройствам от HP и предложит удобный интерфейс и богатый функционал. Революционного ничего нет, все уже давно придумано ребятами из Apple и Google, но интересные функции появятся.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/touchpad-type.jpeg\" style=\"width: 500px; height: 371px; \" /></p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/touchpad-virtual.jpeg\" style=\"width: 500px; height: 371px; \" /></p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/touchpad-calendar.jpeg\" style=\"width: 500px; height: 371px; \" /></p>\r\n<p>\r\n	Взаимодействовать с окружающим миром TouchPad будет при помощи уже стандартных 3G/4G, WiFi 802.11 b/g/n, Bluetooth 2.1 + EDR. Под вопросом разъем USB и наличие слота для карт памяти. В целом устройство привлекательное и у него есть все шансы составить конкуренцию на рынке планшетников если и не iPad, то Blackberry и Motorola уж точно.</p>\r\n<h2>\r\n	АМБИЦИОЗНЫЕ ПЛАНЫ HP</h2>\r\n<p>\r\n	<br />\r\n	На презентации своих мобильных устройств HP поделилась амбициозными планами по созданию PC версии WebOS. Это очень гармонирует с новыми разработками HP, и их стратегией развития.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/touchpad-webos-pc.jpeg\" style=\"width: 500px; height: 333px; \" /></p>\r\n<p>\r\n	Легкое чувство дежавю при виде этой картинки действительно присутствует. Вся продуктовая линейка HP своим внешним видом дает понять их стремление быть похожими на Apple. Вот и собственная ОС для всех устройств,и алюминиевые корпуса и мобильное подразделение. Возможно будущее именно за &quot;экосистемами&quot; и продуктовыми линейками а-ля Купертино. Apple это доказали, но удастся ли подражателям достигнуть их успеха?</p>\r\n',NULL,3,1),(157,'Обновления для iOS: iBooks и Cut the Rope','<p>\r\n	Предпраздничные дни известны не только своими скидками, но и большим количеством интересных обновлений. На сей раз инновации коснулись &quot;родной читалки&quot; iBooks и одной из самых популярных игр для iOS - Cut The Rope.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ibooks%2Bcuttr.png\" style=\"width: 500px; height: 428px; \" /></p>\r\n<p>\r\n	Не только эти приложения сегодня получили обновления. Но про остальных как нибудь в следующий раз. Итак, поехали.</p>\r\n','<p>\r\n	Предпраздничные дни известны не только своими скидками, но и большим количеством интересных обновлений. На сей раз инновации коснулись &quot;родной читалки&quot; iBooks и одной из самых популярных игр для iOS - Cut The Rope.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ibooks%2Bcuttr.png\" style=\"width: 500px; height: 428px; \" /></p>\r\n<p>\r\n	Не только эти приложения сегодня получили обновления. Но про остальных как нибудь в следующий раз. Итак, поехали.</p>\r\n\r\n<h2>\r\n	iBOOKS ПОЛУЧИЛА ПЕРЕНОСЫ</h2>\r\n<p>\r\n	<br />\r\n	Для отечественных пользователей обновление принесло две полезные функции - возможность распечатать PDF через <strong>AirPrint</strong> и долгожданные переносы в книгах, которые возможны только для прошивки 4.2 и старше.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ibooks-121.png\" style=\"width: 500px; height: 374px; \" /></p>\r\n<p>\r\n	Впрочем, выглядит это на любителя и &quot;автоперенос&quot; легко отключается в настройках приложения.</p>\r\n<h2>\r\n	CAT THE ROPE ПОЛУЧИЛА НОВЫЕ КОНФЕТЫ</h2>\r\n<p>\r\n	<br />\r\n	Новые уровни сегодня получила игра Cut the Rope. Специально в честь Дня Святого Валентина вышло обновление в котором добавлены новые праздничные уровни. В них вам придется вместо одной конфеты использовать уже две ее половинки. Для победы эти половинки должны быть вами склеены в одну и отправлены в рот зеленой зверушке. Ниже - видео того, как это должно выглядеть</p>\r\n<p class=\"rtecenter\">\r\n	<iframe allowfullscreen=\"\" frameborder=\"0\" height=\"300\" src=\"http://www.youtube.com/embed/LealLVar6mU\" title=\"YouTube video player\" width=\"500\"></iframe></p>\r\n<p>\r\n	Для того, чтобы попасть в коробку &quot;Валентинка&quot; вам нужно собрать минимум 300 звезд. Отличный подарок для вашей второй половинки.</p>\r\n',NULL,3,1),(158,'Сегодня Nokia объединилась с Microsoft','<p>\r\n	Событие которого все ждали и боялись - произошло. Сегодня компания Nokia объединилась с Microsoft в своей борьбе за рынок смартфонов. Теперь флагманские устройства Nokia будут работать под управлением недавно вышедшей Windows Phone 7, и снабжены сервисами от ребят из Редмонда. Bing, Xbox Live и Office теперь станут доступными для будущих владельцев смартфонов финского производителя.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/nosoft-logo.jpeg\" /></p>\r\n<p>\r\n	Кроме фак','<p>\r\n	Событие которого все ждали и боялись - произошло. Сегодня компания Nokia объединилась с Microsoft в своей борьбе за рынок смартфонов. Теперь флагманские устройства Nokia будут работать под управлением недавно вышедшей Windows Phone 7, и снабжены сервисами от ребят из Редмонда. Bing, Xbox Live и Office теперь станут доступными для будущих владельцев смартфонов финского производителя.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/nosoft-logo.jpeg\" /></p>\r\n<p>\r\n	Кроме факта объединения усилий пока еще ничего не известно. Не ясна и ситуация с платформами Symbian и MeeGo. Возможно, об их судьбе объявят позднее.</p>\r\n\r\n<h2>\r\n	ЧТО ПРОИЗОШЛО</h2>\r\n<p>\r\n	<br />\r\n	Ключевым словом на презентации была &quot;экосистема&quot;. До руководящего состава Nokia наконец дошло, что конкурировать на рынке становится все труднее из-за однотипности технических характеристик устройств. Никого не удивишь мощным процессором или большим объемом памяти. Теперь всех интересует, чем бы нагрузить этот процессор и как удобнее эту память занять приложениями, музыкой и фильмами.</p>\r\n<p class=\"rtecenter\">\r\n	<iframe allowfullscreen=\"\" frameborder=\"0\" height=\"300\" src=\"http://www.youtube.com/embed/xe3ksR8zgXg\" title=\"YouTube video player\" width=\"500\"></iframe></p>\r\n<p>\r\n	Поскольку своими силами Nokia уже не обойтись, она приняла решение отдать вопросы экосистемы на аутсорсинг в Microsoft. Мудрое и очень своевременное решение. Особенно если учесть, что штат компании радикально сократят и все управление перенесут в США. Возможно это перерождение и спасет компанию от неминуемого кризиса.</p>\r\n<h2>\r\n	ЧТО НАС ЖДЕТ</h2>\r\n<p>\r\n	<br />\r\n	Это событие является не просто важным, это событие знаковое. И не по тому, что Nokia перестала быть независимой и &quot;легла&quot; под Microsoft. Просто Nokia перестает быть неповоротливым европейским оплотом бюрократии и превращается в подвижную американскую компанию, которая готова идти на смелые шаги ради победы. Да, это объединение предназначено не столько для потребителей, сколько для инвесторов. На кону стоит слишком многое, чтобы жертвовать этим обоим компаниям.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/nosoft-friends.jpg\" style=\"width: 500px; height: 329px; \" /></p>\r\n<p>\r\n	Так Nokia может навсегда лишится лидерства на рынке и в случае неудачи ее ожидает забвение. В случае если не &quot;выстрелит&quot; Windows Phone 7, то Microsoft окончательно утратит свои позиции на рынке мобильных устройств. Хотя их амбициозные планы на Windows 8 с поддержкой ARM - процессоров говорят о многом. Например о том, что Microsoft по примеру Apple скрестит ОС для мобильных и настольных компьютеров. И вот здесь для Nokia могут настать золотые времена.</p>\r\n',NULL,3,1),(159,'Зачем нам нужен iPhone Nano','<p>\r\n	В конце недели очередной слух запустили <a href=\"http://www.bloomberg.com/news/2011-02-10/apple-said-to-work-on-cheaper-more-versatile-iphone-models.html\"><strong>Bloomberg</strong></a>, рассказав о планах Apple по выпуску уменьшенной версии iPhone за <strong>$200</strong> без контракта. Автор сопоставил ряд слухов и приписал несуществующему устройству такие функции как отсутствие SIM-карты и возможность одновременной работы в GSM и CDMA сетях.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=','<p>\r\n	В конце недели очередной слух запустили <a href=\"http://www.bloomberg.com/news/2011-02-10/apple-said-to-work-on-cheaper-more-versatile-iphone-models.html\"><strong>Bloomberg</strong></a>, рассказав о планах Apple по выпуску уменьшенной версии iPhone за <strong>$200</strong> без контракта. Автор сопоставил ряд слухов и приписал несуществующему устройству такие функции как отсутствие SIM-карты и возможность одновременной работы в GSM и CDMA сетях.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/mini-iphone.png\" style=\"width: 500px; height: 300px; \" /></p>\r\n<p>\r\n	Слух о появлении этого необычного устройства появился после презентации HP, на которой был представлен мини-смартфон <strong>HP Veer</strong>, своими размерами не превосходящий кредитную карту. Скорее всего, слух и был запущен в поддержку этого смартфона, так как Apple в борьбе с Android и так получила весомый аргумент в лице CDMA версии iPhone.</p>\r\n\r\n<h2>\r\n	ЗАЧЕМ НУЖЕН iPHONE NANO</h2>\r\n<p>\r\n	<br />\r\n	Самый главный вопрос, зачем нужен этот странный аппарат. Даже странно приписывать Apple такой нелогичный шаг, как выпуск никому не нужного устройства. Как может компания, которая отказывается от нежизнеспособных технологий выпустить устройство для &quot;нецелевой&quot; аудитории. Для борьбы с Android достаточно распродать iPhone 3GS по $200 без контракта. Да, это заполонит рынок, но нанесет огромный ущерб репутации Apple. Исчезнет аура &quot;невероятности&quot; продуктов.</p>\r\n<p>\r\n	Ну а на счет iPhone Nano, ребята из <a href=\"http://appadvice.com/appnn/2011/02/humor-unboxing-iphone-nano/\"><strong>AppAdvice</strong></a> специально к этому событию сняли юмористическое видео. На нем происходит процесс &quot;распаковывания&quot; (unboxing) их версии маленького iPhone.</p>\r\n<p class=\"rtecenter\">\r\n	<iframe allowfullscreen=\"\" frameborder=\"0\" height=\"300\" src=\"http://www.youtube.com/embed/75gG8Kcn0io\" title=\"YouTube video player\" width=\"500\"></iframe></p>\r\n<p>\r\n	Надеемся до такого не дойдет. Но от &quot;распускателей&quot; слухов всего можно ожидать.</p>\r\n',NULL,3,1),(160,'Samsung показал свой новый планшет','<p>\r\n	На выставке <strong>MWC2011</strong> в Барселоне компания Samsung показала <strong>Galaxy Tab 10.1</strong> - свой новый планшет. Он оснащен 10.1 дюймовым емкостным экраном и будет работать под управлением Android 3.0 Honeycomb.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/samsung-galaxy.png\" style=\"width: 500px; height: 300px; \" /></p>\r\n<p>\r\n	Это вторая попытка корейского производителя в производстве планшетника. Первое устройство в Galaxy серии обладало 7-и дюймовым','<p>\r\n	На выставке <strong>MWC2011</strong> в Барселоне компания Samsung показала <strong>Galaxy Tab 10.1</strong> - свой новый планшет. Он оснащен 10.1 дюймовым емкостным экраном и будет работать под управлением Android 3.0 Honeycomb.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/samsung-galaxy.png\" style=\"width: 500px; height: 300px; \" /></p>\r\n<p>\r\n	Это вторая попытка корейского производителя в производстве планшетника. Первое устройство в Galaxy серии обладало 7-и дюймовым экраном и больше походило на большой смартфон из-за GSM модуля и ОС Android 2.2, которая не предназначена для планшетных устройств.</p>\r\n\r\n<h2>\r\n	ЧТО ЭТО ЗНАЧИТ</h2>\r\n<p>\r\n	<br />\r\n	Это уже вторая попытка отхватить кусок рынка у Apple и с каждым разом попытки эти все удачнее. Но в очередной раз чуда не произошло. Да, новый двухьядерный процессор Nvidia Tegra 2, новая ОС от Google, но этим сейчас может похвастаться любой производитель электроники, намеревающийся выйти на этот рынок. По дизайну - опять пластик. Устройство получилось легким и удобным, интересные &quot;наплывы&quot; на задней крышке. Они должны быть удобны для рук, но в портретной ориентации будут скорее мешать. В общем - смотрите сами.</p>\r\n<p class=\"rtecenter\">\r\n	<iframe allowfullscreen=\"\" frameborder=\"0\" height=\"300\" src=\"http://www.youtube.com/embed/J9nscBYzMX8\" title=\"YouTube video player\" width=\"500\"></iframe></p>\r\n<p>\r\n	Самыми интересными вопросами по поводу нового Galaxy Tab остаются его цена и время работы от батареи. Об этом производитель пока скромно умалчивает. В продажу устройство поступит уже весной этого года.</p>\r\n<h2>\r\n	ТЕХНИЧЕСКИЕ ХАРАКТЕРИСТИКИ</h2>\r\n<p>\r\n	<br />\r\n	Для тех, кто еще обращает внимание на такие мелочи, как технические характеристики - вот сводная таблица по палншету.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/galaxy10.jpeg\" style=\"width: 500px; height: 412px; \" /></p>\r\n<p>\r\n	Ничего удивительного и революционного, но таков удел догоняющих. Надеемся удивлять нас будет Apple, когда представит второй iPad.</p>\r\n',NULL,3,1),(161,'Рано хоронить Windows Phone 7 ','<p>\r\n	Сегодня на выставке MWC2011 в Барселоне произошло очередное интересное <a href=\"http://www.microsoft.com/presspass/presskits/windowsphone/\"><strong>событие</strong></a>. На сей раз CEO Microsoft Стив Балмер поведал о планах развития Windows Phone 7 в 2011 году. Новая ОС от Microsoft получит ряд новых функций, которые помогут ей занять лидирующие позиции на рынке.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/microsoft.png\" /></p>\r\n<p>\r\n	В новых версиях WP7 Балмер пообе','<p>\r\n	Сегодня на выставке MWC2011 в Барселоне произошло очередное интересное <a href=\"http://www.microsoft.com/presspass/presskits/windowsphone/\"><strong>событие</strong></a>. На сей раз CEO Microsoft Стив Балмер поведал о планах развития Windows Phone 7 в 2011 году. Новая ОС от Microsoft получит ряд новых функций, которые помогут ей занять лидирующие позиции на рынке.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/microsoft.png\" /></p>\r\n<p>\r\n	В новых версиях WP7 Балмер пообещал многозадачность для сторонних приложений, Internet Explorer 9 с поддержкой стандарта <strong>HTML5</strong> и бесплатный доступ к <strong>SkyDrive</strong> - облачное файловое хранилище для ваших файлов и документов. Помимо этого добавится встроенная интеграция социальной сети <strong>Twitter</strong> и поддержка Xbox и игрового модуля <strong>Kinect</strong>.</p>\r\n\r\n<h2>\r\n	УПРАВЛЕНИЕ KINECT ЧЕРЕЗ WP7</h2>\r\n<p>\r\n	<br />\r\n	Пожалуй самой интересной и востребованной функцией новой ОС является тесная интеграция мобильного гаджета с игровой приставкой Xbox. То о чем начали поговаривать с выходом последнего поколения Apple TV уже реализовала Microsoft. Теперь вы можете управлять играми через свой смартфон. Причем компанию вам может составить как обладатель идентичного устройства, так и пользователь модуля Kinect. На видео показан реальный пример такого взаимодействия.</p>\r\n<p class=\"rtecenter\">\r\n	<iframe allowfullscreen=\"\" frameborder=\"0\" height=\"300\" src=\"http://www.youtube.com/embed/ehS-AfM4b8I\" title=\"YouTube video player\" width=\"500\"></iframe></p>\r\n<p>\r\n	Пока владельцы новых гаджетов ждут мартовского обновления, которое принесет возможность копирования и вставки текста на их устройство, мы вам рекомендуем взглянуть на <a href=\"http://macov.net/news/nokia-love-microsoft\"><strong>альянс</strong></a> Nokia и Microsoft с совершенно другой стороны. Обе компании резко изменили свои планы и их союз уже не так похож на &quot;семью двух индюшек&quot;.</p>\r\n',NULL,3,1),(162,'\"Волшебная Палочка\" для владельцев \"волшебных\" гаджетов от Apple','<p>\r\n	Сегодня в продажу поступил очень интересный девайс, который позволит объединить Magic Trackpad и вашу беспроводную клавиатуру в единое устройство. <a href=\"http://www.twelvesouth.com/products/magicwand/\"><strong>Magic Wend от Twelve South</strong></a> позволяет проделать эту операцию быстро и надежно. Выглядит это очень стильно и позволяет пользоваться беспроводными гаджетами не только на столе, но и на коленях.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/MW_header_k','<p>\r\n	Сегодня в продажу поступил очень интересный девайс, который позволит объединить Magic Trackpad и вашу беспроводную клавиатуру в единое устройство. <a href=\"http://www.twelvesouth.com/products/magicwand/\"><strong>Magic Wend от Twelve South</strong></a> позволяет проделать эту операцию быстро и надежно. Выглядит это очень стильно и позволяет пользоваться беспроводными гаджетами не только на столе, но и на коленях.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/MW_header_keyboard_large.jpeg\" style=\"width: 500px; height: 118px; \" /></p>\r\n<p>\r\n	Принцип устройства довольно простой - в полукруглую трубку вы вставляете оба &quot;магических&quot; манипулятора, где они надежно закрепляются перемычкой. Как это выглядит можно посмотреть на видео.</p>\r\n<p class=\"rtecenter\">\r\n	<iframe allowfullscreen=\"\" frameborder=\"0\" height=\"300\" src=\"http://www.youtube.com/embed/EAO028qBLXo\" title=\"YouTube video player\" width=\"500\"></iframe></p>\r\n<p>\r\n	Стоит добавить, что стоит приспособа $30 и пока еще доставляется бесплатно по территории США. Если у вас есть желание заполучить такой гаджет на родине - пишите нам в твиттер, мы постараемся вам помочь.</p>\r\n',NULL,3,1),(163,'Появились характеристики новых MacBook Pro 2011','<p>\r\n	Мы уже <a href=\"http://macov.net/news/macbook-pro-update\"><strong>рассказывали</strong></a> вам про грядущее обновление серии MacBook Pro. Сегодня стали известны новые подробности и некоторые характеристики. Так, по словам <a href=\"http://www.mactrast.com/exclusive-new-macbooks-to-be-20-lighter-have-significant-battery-improvements-and-more/\"><strong>MacTrast</strong></a> новые модели потеряют 20% веса, получат более мощные батареи и обзаведутся экранами с более высоким разрешением. В прод','<p>\r\n	Мы уже <a href=\"http://macov.net/news/macbook-pro-update\"><strong>рассказывали</strong></a> вам про грядущее обновление серии MacBook Pro. Сегодня стали известны новые подробности и некоторые характеристики. Так, по словам <a href=\"http://www.mactrast.com/exclusive-new-macbooks-to-be-20-lighter-have-significant-battery-improvements-and-more/\"><strong>MacTrast</strong></a> новые модели потеряют 20% веса, получат более мощные батареи и обзаведутся экранами с более высоким разрешением. В продаже новинки появятся в начале весны.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/mbp2011.png\" style=\"width: 500px; height: 300px; \" /></p>\r\n<p>\r\n	Новые модели переведут на Intel <strong>Sandy Bridge</strong>, что позволит продлить время автономной работы и снизить нагрев корпуса. Появится возможность заказать устройство с матовым экраном, возможно в 15&quot;и 17&quot; моделях появится возможность заменить оптический дисковод на второй жесткий диск.</p>\r\n\r\n<h2>\r\n	УМЕНЬШИТСЯ ВЕС</h2>\r\n<p>\r\n	<br />\r\n	Изменения в весе связаны с применением нового материала. Аморфный сплав будет прочнее анодированного алюминия плюс применение технологии плавки под давлением позволит сбросить 15-20 процентов веса, сделав корпус достаточно крепким.</p>\r\n<p>\r\n	Патент на такие технологии у Apple появился давно, хотя до недавнего времени оставалось загадкой, где будет использоваться данный сплав.</p>\r\n<h2>\r\n	МОЩНЫЕ БАТАРЕИ</h2>\r\n<p>\r\n	Технологии не стоят на месте, и емкость батарей растет день ото дня. Apple планирует увеличить емкость батарей новых ноутбуков на 20%. К этому можно добавить и новые экраны с меньшим энергопотреблением.</p>\r\n<p>\r\n	Безусловно переход на платформу Sandy Bridge скажется на длительности работы новых MacBook Pro, причем скажется положительно. Низкое энергопотребление процессоров и меньшее тепловыделение - основная причина для перехода на эту платформу.</p>\r\n<h2>\r\n	CD-ПРИВОД И USB</h2>\r\n<p>\r\n	<br />\r\n	Если в младшей модели CD-привод останется на своем месте, то при выборе старших моделей у пользователя есть варианты. Можно будет по желанию заменить неактуальный CD-привод на более чем актуальный второй жесткий диск. Очень интересна комбинация SSD + HDD которая позволит насладится необычайной скоростью работы и достаточным объемом памяти.</p>\r\n<p>\r\n	На счет USB пока известно, что может появится дополнительный порт в старших моделях. Будет ли это порт USB 3.0 пока не известно. Но вероятность этого довольно велика.</p>\r\n<h2>\r\n	ЦЕНА ИЗМЕНЯТСЯ</h2>\r\n<p>\r\n	И самая приятная новость для всех, собирающихся приобрести новинки - цены снизятся. MacBook Pro 13&quot; может стоить $1099, $1549 за 15&quot; MacBook Pro и старший MacBook Pro возможно будет стоить $2099.</p>\r\n<p>\r\n	Как сообщает редакция <a href=\"http://mactrust.com\"><strong>MacTrast</strong></a> эта информация попала к ним из инсайдерского источника. Никто не может сказать, насколько можно доверять этой информации, но выглядит довольно реалистично. Какими будут на самом деле MacBook Pro мы узнаем только после их официально релиза.</p>\r\n',NULL,3,1),(164,'iWork\'11 поступит в продажу 19 февраля','<p>\r\n	В мексиканском филиале <strong>BestBuy</strong> была замечена реклама новой версии пакета для работы с документами iWork&#39;11. Пока это только слухи, но выглядит довольно реалистично. Реклама обещает запуск продаж 19 февраля в 19.00 в сети BestBuy.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/iwork11.png\" style=\"width: 500px; height: 277px; \" /></p>\r\n<p>\r\n	Судя по фотографиям нас ожидает полноэкранная версия приложения. Возможна более тесная интеграция с сервисом <a','<p>\r\n	В мексиканском филиале <strong>BestBuy</strong> была замечена реклама новой версии пакета для работы с документами iWork&#39;11. Пока это только слухи, но выглядит довольно реалистично. Реклама обещает запуск продаж 19 февраля в 19.00 в сети BestBuy.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/iwork11.png\" style=\"width: 500px; height: 277px; \" /></p>\r\n<p>\r\n	Судя по фотографиям нас ожидает полноэкранная версия приложения. Возможна более тесная интеграция с сервисом <a href=\"https://www.iwork.com\"><strong>iwork.com</strong></a> и упрощенная панель управления редакторами. Ближе к субботе должны появится новые подробности.</p>\r\n',NULL,3,1),(165,'В iPhone 5 может появится 4\" экран и клавиатура','<p>\r\n	Сегодня появилось сразу два слуха о <strong>iPhone 5</strong>. Сначала &nbsp;&nbsp;<a href=\"http://www.digitimes.com/news/a20110214PD225.html\"><strong>DigiTimes</strong></a> напророчила 4&quot; экан, якобы для конкуренции с Android-фонами. Второй слух пришел из солнечного Тайваня. По словам автора <a href=\"http://tw.apple.pro/?uid-2-action-viewspace-itemid-6425\"><strong>Apple.pro</strong></a> существует &quot;три прототипа устройства, одно из которых обладает сдвигающейся крышкой, возможно','<p>\r\n	Сегодня появилось сразу два слуха о <strong>iPhone 5</strong>. Сначала &nbsp;&nbsp;<a href=\"http://www.digitimes.com/news/a20110214PD225.html\"><strong>DigiTimes</strong></a> напророчила 4&quot; экан, якобы для конкуренции с Android-фонами. Второй слух пришел из солнечного Тайваня. По словам автора <a href=\"http://tw.apple.pro/?uid-2-action-viewspace-itemid-6425\"><strong>Apple.pro</strong></a> существует &quot;три прототипа устройства, одно из которых обладает сдвигающейся крышкой, возможно это физическая клавиатура&quot;. Два других экземпляра отличаются от актуального iPhone 4 только техническими характеристиками.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/iphone5-rumor.png\" style=\"width: 500px; height: 300px; \" /></p>\r\n<p>\r\n	Возможно рост армии Android-фонов и заставит Apple изменить дизайн и формфактор своих устройств. Но уж никак не в сторону ухудшения. Как может компания первой в мире отказавшаяся от кнопок на смартфоне снова к ним вернутся? Да и не предназначена iOS для клавиатурного ввода. С экраном все еще проще - пользователю важен не размер в сантиметрах, а удобство использования. Если iPhone станет шире, то будет ли он так же удобно лежать как и прежде?</p>\r\n',NULL,3,1),(166,'Стив Джобс не собирается умирать через 6 недель','<p>\r\n	Сегодня появились слухи про 6 недель, которые осталось жить Стиву Джобсу, главе компании Apple. Его отпуск по состоянию здоровья породил немало слухов, но таких радикальных еще не было. Виной всему фотография, на которой запечатлен пожилой исхудавший мужчина направляющийся в Stanford Cancer Center (Стенфордский Центр Раковых заболеваний). Журналисты показали эти фотографии нескольким врачам, которые и определили &quot;на глаз&quot; оставшиеся 6 недель мужчине на фото.</p>\r\n<p class=\"rtecen','<p>\r\n	Сегодня появились слухи про 6 недель, которые осталось жить Стиву Джобсу, главе компании Apple. Его отпуск по состоянию здоровья породил немало слухов, но таких радикальных еще не было. Виной всему фотография, на которой запечатлен пожилой исхудавший мужчина направляющийся в Stanford Cancer Center (Стенфордский Центр Раковых заболеваний). Журналисты показали эти фотографии нескольким врачам, которые и определили &quot;на глаз&quot; оставшиеся 6 недель мужчине на фото.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/stive-died.png\" style=\"width: 500px; height: 300px; \" /></p>\r\n<p>\r\n	Очень жаль, что мужчине на фото осталось так мало, только вот является ли он Стивом Джобсом - вот в чем вопрос. Судя по фотографиям - нет, похож &quot;со спины&quot; но не более. Ну а гардероб этого пожилого человека не оставляет никаких сомнений в том, что это кто угодно, но не глава самой известной IT-компании мира.</p>\r\n<p>\r\n	Для тех, кто желает удостоверится в здоровье господина Джобса рекомендую следить за выпусками новостей. Сегодня в Сан Франциско пройдет встреча президента США с главами трех крупнейших IT-компаний. Это Эрик Шмидт - пока еще СЕО <strong>Google</strong>, Марк Цукерберг - основатель <strong>Facebook</strong> и Стив Джобс собственной персоной. Барак Обама планирует заручится поддержкой кремниевой долины перед началом своей инновационной кампании. Там то и можно будет увидеть Стива Джобса и самим прикинуть, сколько ему осталось.</p>\r\n',NULL,3,1),(167,'Opera Mini для iPad скоро появится в App Store','<p>\r\n	Информация о выходе iPad версии браузера <strong>Opera Mini</strong> поступила с MWC2011. На выставке была представлена уже рабочая версия приложения. Каждый желающий мог подойти и самостоятельно убедится в высокой скорости работы нового браузера.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/opera-soft-logo.png\" style=\"width: 500px; height: 300px; \" /></p>\r\n<p>\r\n	По словам представителей компании Opera Mini уже отправлена модераторам App Store для рассмотрения и в ско','<p>\r\n	Информация о выходе iPad версии браузера <strong>Opera Mini</strong> поступила с MWC2011. На выставке была представлена уже рабочая версия приложения. Каждый желающий мог подойти и самостоятельно убедится в высокой скорости работы нового браузера.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/opera-soft-logo.png\" style=\"width: 500px; height: 300px; \" /></p>\r\n<p>\r\n	По словам представителей компании Opera Mini уже отправлена модераторам App Store для рассмотрения и в скором времени будет доступна для загрузки.</p>\r\n\r\n<h2>\r\n	КАК ЭТО ВЫГЛЯДИТ</h2>\r\n<p>\r\n	<br />\r\n	Работает браузер действительно быстро. Скорость работы достигается благодаря уникальной технологии Opera Turbo, когда страница предварительно закачивается на сервер, где происходит ее оптимизация. Стоит отметить приятный интерфейс и удобную панель управления. Лично убедится в этом можно посмотрев видео с MWC.</p>\r\n<p class=\"rtecenter\">\r\n	<iframe allowfullscreen=\"\" frameborder=\"0\" height=\"300\" src=\"http://www.youtube.com/embed/cnpH_TKk5Vc\" title=\"YouTube video player\" width=\"500\"></iframe></p>\r\n<p>\r\n	Использование этого браузера в последнее время стало очень актуальным. Операторы мобильной связи предлагают безлимитные тарифы для его пользователей, да и экономия трафика происходит существенная. Рекомендую попробовать как только появится.</p>\r\n',NULL,3,1),(168,'Почему Apple не собирается делать бюджетный iPhone ','<p>\r\n	Как мы и <a href=\"http://macov.net/news/iphone-nano-rumor\"><strong>предполагали</strong></a> ранее, Apple не собирается выпускать уменьшенную версию своего iPhone. Эту информацию в своей статье <a href=\"http://www.nytimes.com/2011/02/18/technology/18apple.html\"><strong>подтвердили</strong></a> журналисты New York Times. По словам их анонимного источника в Apple работают над новой версией устройства, которое появится уже этим летом. Да и уменьшение размера устройств никак не может повлиять ','<p>\r\n	Как мы и <a href=\"http://macov.net/news/iphone-nano-rumor\"><strong>предполагали</strong></a> ранее, Apple не собирается выпускать уменьшенную версию своего iPhone. Эту информацию в своей статье <a href=\"http://www.nytimes.com/2011/02/18/technology/18apple.html\"><strong>подтвердили</strong></a> журналисты New York Times. По словам их анонимного источника в Apple работают над новой версией устройства, которое появится уже этим летом. Да и уменьшение размера устройств никак не может повлиять на его стоимость, к тому же им будет не так просто управлять.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/iphone-nano-fail.png\" /></p>\r\n<p>\r\n	Снижение же стоимости комплектующих iPhone невозможно без снижения качества устройства. Вряд ли Apple пойдет на такие жертвы, ведь потеряют они намного больше чем приобретут.</p>\r\n\r\n<h2>\r\n	ПОЧЕМУ НЕ БУДЕТ БЮДЖЕТНОГО iPHONE</h2>\r\n<p>\r\n	<br />\r\n	На каждый новый &quot;слух&quot; о так называемом iPhone Nano имеется логичный контраргумент. Для начала, сам факт появления еще одного iPhone крайне сомнителен. В этом сегменте время революций пока еще не пришло. Медленный эволюционный рост качества, CDMA версия iPhone (которая по слухам и является таинственным N97) и развитие &quot;облачных&quot; технологий. Все остальное не принесет Apple значительных дивидендов, а возможно и навредит.</p>\r\n<h2>\r\n	ПОЧЕМУ НЕ УМЕНЬШАТ iPHONE</h2>\r\n<p>\r\n	Уменьшение размера устройства - это противоречит всякой логике. Все приложения для iOS пишутся под определенный размер экрана и его разрешение. Появление Retina Display и iPad заставила разработчиков переписывать свои приложения под новые стандарты. Хотя они могли бы и не переписывать, вот только покупать их бы тоже не стали. Появление устройства с меньшим экраном в низкой ценовой категории поставил бы разработчиков перед выбором - а стоит ли под него переделывать приложения? Вот тут мы и подошли к третьему контраргументу.</p>\r\n<h2>\r\n	БЮДЖЕТНЫЙ iPHONE ДЛЯ СТРАН ТРЕТЬЕГО МИРА</h2>\r\n<p>\r\n	iPhone является не просто телефоном, он обладает большой и обширной экосистемой. Довольно дорогостоящей экосистемой, хочу я вам сказать. App Store, iTunes Store, MobileMe который вот-вот станет бесплатным. Эти сервисы тоже приносят Apple какой-никакой доход. Но и затраты на поддержание этих сервисов довольно велики. Ценовая политика Apple рассчитана на людей, которые смогут себе позволить покупать музыку и приложения, не говоря уже о цене аппарата и контракта у оператора мобильной связи. Бюджетное устройство позволит набрать большую долю на рынке, но как показал опыт Nokia - купить долю рынка не значит добиться на нем успеха. Продажи в страны третьего мира некачественной и дешевой версии iPhone только ударят по репутации компании. Не вижу смысла Apple терять лояльность пользователей в погоне за большими продажами.</p>\r\n',NULL,3,1),(169,'ВИДЕО: Обзор iPad 2 и его новых функций','<p>\r\n	Американский блог <a href=\"http://obamapacman.com/2011/02/leaked-ipad-2-hd-hand-on-review-video/?utm_source=feedburner&amp;utm_medium=feed&amp;utm_campaign=Feed:+ObamaPacman+(Obama+Pacman)\"><strong>Obamapacman</strong></a> опубликовал видео обзор второго поколения iPad. Из его функций стоит выделить улучшенные характеристики, увеличенный экран, больше портов,две камеры и даже оптический привод!</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ipad2-review.jpeg\" style=\"wid','<p>\r\n	Американский блог <a href=\"http://obamapacman.com/2011/02/leaked-ipad-2-hd-hand-on-review-video/?utm_source=feedburner&amp;utm_medium=feed&amp;utm_campaign=Feed:+ObamaPacman+(Obama+Pacman)\"><strong>Obamapacman</strong></a> опубликовал видео обзор второго поколения iPad. Из его функций стоит выделить улучшенные характеристики, увеличенный экран, больше портов,две камеры и даже оптический привод!</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ipad2-review.jpeg\" style=\"width: 500px; height: 281px; \" /></p>\r\n<p>\r\n	Обзор планшета Apple iPad 2 HD был записан Eskild E. Fors &amp; Anders &Oslash;vergaard. Были показаны новые революционные функции, особенно понравились iMilk и iFruitCuttingTable. Со всеми функциями нового устройства вы можете ознакомится посмотрев обзор.</p>\r\n<p class=\"rtecenter\">\r\n	<iframe allowfullscreen=\"\" frameborder=\"0\" height=\"300\" src=\"http://www.youtube.com/embed/ct1_r_61sk8?rel=0\" title=\"YouTube video player\" width=\"500\"></iframe></p>\r\n<p>\r\n	Это видео было записано, чтобы показать какими нелепыми могут быть фантазии на тему нового iPad. Особенно поразил момент, когда на экране iMac 27&quot; резали апельсин - вот это тяга к искусству. Всем приятных выходных).</p>\r\n',NULL,3,1),(170,'Стив Джобс жив-здоров','<p>\r\n	Мы <a href=\"http://macov.net/news/fake-steve-jobs-may-die\"><strong>писали</strong></a> уже о встрече Обамы с главами крупнейших компаний Кремниевой долины, на которой должен был появится Стив Джобс. Встреча состоялась, о чем свидетельствует фотография, появившаяся на <a href=\"http://www.flickr.com/photos/whitehouse/5455525432/in/photostream/\"><strong>официальной странице</strong></a> Белого Дома в Flickr. На основатель Apple выглядит довольно здоровым.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt','<p>\r\n	Мы <a href=\"http://macov.net/news/fake-steve-jobs-may-die\"><strong>писали</strong></a> уже о встрече Обамы с главами крупнейших компаний Кремниевой долины, на которой должен был появится Стив Джобс. Встреча состоялась, о чем свидетельствует фотография, появившаяся на <a href=\"http://www.flickr.com/photos/whitehouse/5455525432/in/photostream/\"><strong>официальной странице</strong></a> Белого Дома в Flickr. На основатель Apple выглядит довольно здоровым.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/steve-obama-mark.png\" style=\"width: 500px; height: 274px; \" /></p>\r\n<p>\r\n	Добавим, что среди сидящих за столом глав крупных IT-компаний нет Стива Балмера, видимо Microsoft недостаточно &quot;инновационна&quot; в глазах лидера США. На эту тему ребята из <strong>theoatmeal</strong> даже нарисовали <a href=\"http://theoatmeal.com/blog/obama_meeting?utm_source=twitterfeed&amp;utm_medium=twitter\"><strong>интересный комикс</strong></a>.</p>\r\n',NULL,3,1),(171,'Новые MacBook Pro 24 февраля, возможно с Light Peak','<p>\r\n	О дате выхода новой линейки MacBook Pro сообщает <a href=\"http://www.macrumors.com/2011/02/18/new-macbook-pros-likely-to-launch-on-thursday/\"><strong>Macrumors</strong></a>. По словам их источника, 24 февраля в продажу поступит пять новых моделей. Пока не известны технические характеристики, но доступна информация о ценах в США.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/24feb-macbookpro.png\" style=\"width: 500px; height: 261px; \" /></p>\r\n<p>\r\n	По предварительным дан','<p>\r\n	О дате выхода новой линейки MacBook Pro сообщает <a href=\"http://www.macrumors.com/2011/02/18/new-macbook-pros-likely-to-launch-on-thursday/\"><strong>Macrumors</strong></a>. По словам их источника, 24 февраля в продажу поступит пять новых моделей. Пока не известны технические характеристики, но доступна информация о ценах в США.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/24feb-macbookpro.png\" style=\"width: 500px; height: 261px; \" /></p>\r\n<p>\r\n	По предварительным данным, новые модели будут стоить:<br />\r\n	<br />\r\n	MC724LL/A &ndash; 13 дюймов &ndash; $1499<br />\r\n	MC700LL/A &ndash; 13 дюймов &ndash; $1199<br />\r\n	MC725LL/A &ndash; 17 дюймов &ndash; $2499<br />\r\n	MC723LL/A &ndash; 15 дюймов &ndash; $2199<br />\r\n	MC721LL/A &ndash; 15 дюймов &ndash; $1799</p>\r\n<p>\r\n	О ценах на отечественные версии пока ничего не известно, но они традиционно будут отличаться от Американских в большую сторону.</p>\r\n\r\n<h2>\r\n	LIGHT PEAK</h2>\r\n<p>\r\n	<br />\r\n	По сообщению <a href=\"http://news.cnet.com/8301-13924_3-20033940-64.html\"><strong>CNET-news</strong></a> в новой серии Apple MacBook Pro может появится технология Light Peak, которая позволить в несколько раз ускорить скорость работы устройства. Журналисты утверждают, что над этой технологией уже довно работают инженеры из Купертино и вероятность ее появления в новых устройствах довольно велика.</p>\r\n<p class=\"rtecenter\">\r\n	<iframe allowfullscreen=\"\" frameborder=\"0\" height=\"300\" src=\"http://www.youtube.com/embed/izNoF1SWtSg?rel=0\" title=\"YouTube video player\" width=\"500\"></iframe></p>\r\n<p>\r\n	Сама технология основана на использовании оптических каналов передачи информации, взамен металлических. Благодаря этому, скорость передачи информации сможет достичь 100 Ггбит/с. На сегодняшний день эта технология позволяет передавать информацию со скоростью 10 Ггбит/с. На презентации в 2009 году инженеры Intel использовали для демонстрации своей технологии компьютер под управлением Mac OS X. Возможно эти фантастические скорости станут доступны уже в четверг.</p>\r\n',NULL,3,1),(172,'В Mac OS 10.7 Lion может появится облачное хранилище данных.  ','<p>\r\n	Новость о том, что в Mac OS 10.7 Lion может появиться облачное хранилище данных, связана с очередным патентом Apple. В нем <a href=\"http://www.patentlyapple.com/patently-apple/2011/02/apple-in-the-sky-with-diamonds-a-cloud-based-safe-deposit-box.html\"><strong>описывается</strong></a> технология удаленного сервиса для хранения файлов пользователя, встроенного в операционную систему.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/cloud-deposit-box.jpeg\" style=\"width: 500p','<p>\r\n	Новость о том, что в Mac OS 10.7 Lion может появиться облачное хранилище данных, связана с очередным патентом Apple. В нем <a href=\"http://www.patentlyapple.com/patently-apple/2011/02/apple-in-the-sky-with-diamonds-a-cloud-based-safe-deposit-box.html\"><strong>описывается</strong></a> технология удаленного сервиса для хранения файлов пользователя, встроенного в операционную систему.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/cloud-deposit-box.jpeg\" style=\"width: 500px; height: 226px; \" /></p>\r\n<p>\r\n	Возможно, данная функция будет как-то связана с ожидаемым сервисом беспроводной синхронизации iOS устройств с &quot;большим братом&quot; при помощи обновленной MobileMe.</p>\r\n\r\n<h2>\r\n	ТЕХНОЛОГИЯ В ДЕЙСТВИИ</h2>\r\n<p>\r\n	<br />\r\n	Технология будет довольно востребованной, хотя отдавать свою конфиденциальную информацию &quot;в облака&quot; пока еще готовы не все. Возможно, это еще одна технология Apple, которая изменит представление пользователей о работе с привычными устройствами. Робота новой технологии очень наглядно отображена в иллюстрации к патенту.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/cloud-deposit-tech.jpeg\" style=\"width: 500px; height: 268px; \" /></p>\r\n<p>\r\n	На мой взгляд, данную технологию можно повторить уже сейчас подручными средствами. Для этого вам понадобится учетная запись <a href=\"http://www.dropbox.com/\"><strong>Dropbox</strong></a> и лицензия на сервис шифрования файлов - <a href=\"http://agilewebsolutions.com/knox\"><strong>Knox</strong></a>. За несколько минут вы получите аналогичное шифрованное облачное хранилище, в котором вы сможете хранить конфиденциальную информацию и не бояться о ее сохранности.</p>\r\n',NULL,3,1),(173,'Дефицит iPad первого поколения говорит о появлении iPad 2','<p>\r\n	Как и в случае с MacBook Pro, на складах крупных европейских дистрибьюторов начали заканчиваться устройства прошлого поколения. На сей раз это касается iPad, обновления которого ждут с нетерпением. Как сообщает <a href=\"http://www.9to5mac.com/52981/ipad-2-incoming-ipad-sold-outon-sale-at-some-european-retailers\"><strong>9to5mac</strong></a>, у официального английского реселлера <strong>Carphone Warehouse</strong> в наличии остались только модели с WiFi, да и то с минимальным объемом памяти','<p>\r\n	Как и в случае с MacBook Pro, на складах крупных европейских дистрибьюторов начали заканчиваться устройства прошлого поколения. На сей раз это касается iPad, обновления которого ждут с нетерпением. Как сообщает <a href=\"http://www.9to5mac.com/52981/ipad-2-incoming-ipad-sold-outon-sale-at-some-european-retailers\"><strong>9to5mac</strong></a>, у официального английского реселлера <strong>Carphone Warehouse</strong> в наличии остались только модели с WiFi, да и то с минимальным объемом памяти. У другого крупного реселлера <strong>Ingram Micro</strong> остались только младшие модели с WiFi и совсем немного с 3G.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ipad_out_of_stock.png\" style=\"width: 500px; height: 157px; \" /></p>\r\n<p>\r\n	Аналогичная ситуация и в Канаде. У крупнейшего реселлера <strong>Future Shop</strong> некоторые модели распроданы, других осталось совсем мало. Последним аргументом в пользу скорого обновления планшета служит акция от европейских операторов <strong>Orange</strong> и <strong>T-Mobile</strong>. Они предлагают iPad 3G c контрактом на 2 года всего за &pound;99.99 - аттракцион невиданной щедрости или же предпосылки к выходу новой версии iPad.</p>\r\n',NULL,3,1),(174,'ДАЙТЕ ДВА: 2 марта покажут iPad 2','<p>\r\n	Информация о том, что 2 второго марта состоится презентация iPad второго поколения появилась сразу из нескольких источников. Первыми о грядущем событии проговорились <a href=\"http://www.electronista.com/articles/11/02/22/apple.expected.to.show.next.ipad.march.2/\"><strong>All Things Digital</strong></a>. Последние сомнения <strong><a href=\"http://www.reuters.com/article/2011/02/22/apple-ipad-idUSN2227848020110222\">развеяли</a></strong> авторитетные <strong>Reuters</strong>, которые в статье','<p>\r\n	Информация о том, что 2 второго марта состоится презентация iPad второго поколения появилась сразу из нескольких источников. Первыми о грядущем событии проговорились <a href=\"http://www.electronista.com/articles/11/02/22/apple.expected.to.show.next.ipad.march.2/\"><strong>All Things Digital</strong></a>. Последние сомнения <strong><a href=\"http://www.reuters.com/article/2011/02/22/apple-ipad-idUSN2227848020110222\">развеяли</a></strong> авторитетные <strong>Reuters</strong>, которые в статье, опровергающей задержку на производстве упомянули дату выхода нового iPad.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/2march-ipad2.png\" style=\"width: 500px; height: 300px; \" /></p>\r\n<p>\r\n	О месте проведения мероприятия пока ничего не известно. До появления официальных приглашений эта информация будет сохранятся в тайне. Предварительно это Центр Искусств <strong>Yerba Buena</strong> в Сан Франциско, хотя данную информацию сложно проверить. На официальном сайте Центра нет никакой информации о грядущем событии.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/2march-ipad2-place.png\" style=\"width: 500px; height: 163px; \" /></p>\r\n<p>\r\n	По предварительным данным, новый iPad получит мощный двухядерный процессор A5, корпус его станет тоньше и легче, а работать он сможет одинаково хорошо в сетях GSM и CDMA, благодаря соответствующим чипам. В устройстве появятся 2 камеры для фото и видеосъемки. Под вопросом появление новых портов и улучшение экрана устройства.</p>\r\n',NULL,3,1),(175,'Новые MacBook Pro получат SSD накопители и большой тачпад','<p>\r\n	Как сообщает <a href=\"http://www.bgr.com/2011/02/21/new-macbook-pro-models-imminent-weve-got-some-new-details/?utm_source=feedburner&amp;utm_medium=twitter&amp;utm_campaign=Feed:+TheBoyGeniusReport+(BGR+|+Boy+Genius+Report)\"><strong>BGR</strong></a> новые MacBook Pro получат ряд новых функций. По словам их источника, в новой серии ноутбуков появится большой стеклянный трекпад, который будет превосходить по размерам существующий аналог.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/d','<p>\r\n	Как сообщает <a href=\"http://www.bgr.com/2011/02/21/new-macbook-pro-models-imminent-weve-got-some-new-details/?utm_source=feedburner&amp;utm_medium=twitter&amp;utm_campaign=Feed:+TheBoyGeniusReport+(BGR+|+Boy+Genius+Report)\"><strong>BGR</strong></a> новые MacBook Pro получат ряд новых функций. По словам их источника, в новой серии ноутбуков появится большой стеклянный трекпад, который будет превосходить по размерам существующий аналог.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/mbp2011-touch.jpeg\" style=\"width: 500px; height: 281px; \" /></p>\r\n<p>\r\n	Еще одним интересны нововведением будет SSD накопитель, которым будут комплектоваться все модели MacBook Pro. И если топовые устройства получат один большой SSD диск, по аналогии с MacBook Air, то младшие модели обзаведутся 8/16 ГБ накопителем, и HDD в придачу. Такая хитрость позволит повысить скорость работы устройства и оставить ее цену доступной для покупателя. Пока не ясно, как будет осуществляться работа &quot;гибридных&quot; моделей, но все станет ясно после появления новых моделей в продаже. Напомним, что предположительно это произойдет <a href=\"http://macov.net/news/24-fevralya-obnovlenie-macbook-pro\"><strong>24 февраля</strong></a>.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/mbp2011%2Bssd.png\" style=\"width: 500px; height: 203px; \" /></p>\r\n<p>\r\n	Так же отметим, что новые модели станут на четверть килограмма легче. Это связано с использованием нового материала для производства корпусов MacBook Pro и, возможно, с новой технологией производства unibody-корпусов. Как будет на самом деле мы, возможно, узнаем уже на этой неделе.</p>\r\n',NULL,3,1),(176,'После обновления MacBook Pro  исчезнет Белый MacBook','<p>\r\n	Очередная порция слухов про обновление MacBook Pro. Ресурс <a href=\"http://www.macgeneration.com/news/voir/189562/nouveaux-macbook-pro-ssd-meilleure-autonomie-plus-legers-et-fin-du-macbook-blanc\"><strong>Macgeneration</strong> </a>сообщает, что увеличится объем памяти, увеличится время автономной работы и уменьшится вес. Об этом мы <a href=\"http://macov.net/news/mbp2011-ssd-update\"><strong>уже писали</strong></a>. А теперь сюрприз: за счет обновления из каталога исчезнут белые MacBook. По ','<p>\r\n	Очередная порция слухов про обновление MacBook Pro. Ресурс <a href=\"http://www.macgeneration.com/news/voir/189562/nouveaux-macbook-pro-ssd-meilleure-autonomie-plus-legers-et-fin-du-macbook-blanc\"><strong>Macgeneration</strong> </a>сообщает, что увеличится объем памяти, увеличится время автономной работы и уменьшится вес. Об этом мы <a href=\"http://macov.net/news/mbp2011-ssd-update\"><strong>уже писали</strong></a>. А теперь сюрприз: за счет обновления из каталога исчезнут белые MacBook. По словам их источника, Apple планирует оставить только два 13&quot; ноутбука. По всей видимости это будет MacBook Pro и Air, выполненные в алюминиевом корпусе.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/rip-macbook.png\" style=\"width: 500px; height: 292px; \" /></p>\r\n<p>\r\n	Эра поликарбонатных устройств Apple заканчивается. По сути, MacBook является последним &quot;не алюминиевым&quot; устройством среди продуктов Apple. Вполне логично, что его стоит заменить легким металлическим аналогом. MacBook Air 13&quot; лучше всего подходит на эту роль.</p>\r\n\r\n<h2>\r\n	13&quot; MACBOOK PRO - CORE i3</h2>\r\n<p>\r\n	<br />\r\n	Для MacBook Pro 13&quot; предрекают процессор i3, новый экран с разрешением 1440 х 900 и матовым покрытием, 16 ГБ SDD для системы и 320 ГБ для хранения файлов. Так же к слухам добавился новый - &quot;о третьем USB&quot;, что под этим понимать решайте сами. То ли технология USB 3.0 появится в MacBook вопреки словам Стива Джобса, то ли появится дополнительный третий порт. Об этом, я полагаю, будут спорить вплоть до официального релиза.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/rip-macbook-macbookpro.png\" style=\"width: 500px; height: 199px; \" /></p>\r\n<p>\r\n	Остается напомнить, что вся эта информация не более чем слухи и предсказания. Достоверную информацию мы получим из уст представителей компании, возможно уже завтра.</p>\r\n',NULL,3,1),(177,'Стали известны характеристики новых MacBook Pro','<p>\r\n	Информация о которой мы писали сегодня утром стала подтверждаться. Совсем недавно появились фотографии, на которых запечатлен экземпляр 13-и дюймового MacBook Pro и его характеристики. Как и предполагалось, разрешение экрана увеличилось до 1440х900, процессор - 2.3GHz Intel Core i5, 4GB 1333MHz DDR3 RAM, 320GB HDD и интегрированная FaceTime HD камера взамен iSight.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/macbookpro-ports.png\" style=\"width: 500px; height: 306px; \"','<p>\r\n	Информация о которой мы писали сегодня утром стала подтверждаться. Совсем недавно появились фотографии, на которых запечатлен экземпляр 13-и дюймового MacBook Pro и его характеристики. Как и предполагалось, разрешение экрана увеличилось до 1440х900, процессор - 2.3GHz Intel Core i5, 4GB 1333MHz DDR3 RAM, 320GB HDD и интегрированная FaceTime HD камера взамен iSight.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/macbookpro-ports.png\" style=\"width: 500px; height: 306px; \" /></p>\r\n<p>\r\n	Так же стоит отметить появление нового порта - Thunderbolt. Это Apple-версия интеловского Light Peak, которая совмещает в себе Mini Display Port и высокоскоростной интерфейс передачи данных. Данная технология будет презентована в четверг инженерами Intel. Одновременно с этим событием в продажу поступят новые MacBook Pro, готовые использовать этот интерфейс.</p>\r\n',NULL,3,1),(178,'ОФИЦИАЛЬНО: 2 марта покажут iPad 2','<p>\r\n	Как мы уже <a href=\"http://macov.net/news/2-marta-ipad-2\"><strong>писали</strong></a> ранее, iPad 2 покажут 2 марта в Центре Искусств <strong>Yerba Buena</strong> в Сан Франциско. Теперь эту информацию подтвердили Apple, прислав журналистам приглашение на событие, связанное с iPad.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/2marta-2ipad.png\" style=\"width: 500px; height: 300px; \" /></p>\r\n<p>\r\n	Пока не известно, будет ли присутствовать на презентации Стив Джобс. Возмо','<p>\r\n	Как мы уже <a href=\"http://macov.net/news/2-marta-ipad-2\"><strong>писали</strong></a> ранее, iPad 2 покажут 2 марта в Центре Искусств <strong>Yerba Buena</strong> в Сан Франциско. Теперь эту информацию подтвердили Apple, прислав журналистам приглашение на событие, связанное с iPad.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/2marta-2ipad.png\" style=\"width: 500px; height: 300px; \" /></p>\r\n<p>\r\n	Пока не известно, будет ли присутствовать на презентации Стив Джобс. Возможно, его состояние здоровья не позволит ему презентовать продукт, но поучаствовать в событии он сможет.</p>\r\n\r\n<h2>\r\n	МЕСТО ПРОВЕДЕНИЯ</h2>\r\n<p>\r\n	<br />\r\n	Уже сегодня ночью Yerba Buena оформляют в соответствующем стиле. Как известно, Apple всегда очень тщательно относится к мелочам. И оформление выставочного зала снаружи - всегда символично событию.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/2marta-2ipad-mesto.png\" style=\"width: 500px; height: 300px; \" /></p>\r\n<p>\r\n	Пока не ясно на что должны намекать разноцветные круги и как это связано с новым iPad. Но уже через неделю мы все узнаем.</p>\r\n<h2>\r\n	ОЖИДАЕМЫЕ ИЗМЕНЕНИЯ</h2>\r\n<p>\r\n	Из ожидаемых изменений - появится одна или две камеры, новый процессор и увеличится объем памяти. Так же вероятно изменение внешнего вида и уменьшение веса. Возможно и <a href=\"http://macov.net/news/macbook-pro-13-thunderbolt\"><strong>анонсируемый сегодня</strong></a> <strong>Thunderbolt</strong> появится совместно с DOC-коннектором.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/2marta-2ipad-concepr.png\" style=\"width: 500px; height: 300px; \" /></p>\r\n<p>\r\n	Вот такой концепт, объединяющий в себе все слухи о новом устройстве, пока является самым реальным взглядом на вещи. За исключением ужасного дизайна, естественно.</p>\r\n',NULL,3,1),(179,'Переучет в Apple Store принесет новые MacBook Pro','<p>\r\n	Традиционно перед обновлением продуктов Apple Store Online закрыт на переучет. Пока жители США мирно спят техники Apple добавляют в каталоги новые MacBook Pro.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/mbp2011-store-update.png\" style=\"width: 500px; height: 150px; \" /></p>\r\n<p>\r\n	Ожидается что в продажу новые модели поступят после презентации <strong>Intel</strong> Light Peak, которая появится в ноутбуках серии Pro под названием <strong>Thunderbolt</strong>. Ждать о','<p>\r\n	Традиционно перед обновлением продуктов Apple Store Online закрыт на переучет. Пока жители США мирно спят техники Apple добавляют в каталоги новые MacBook Pro.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/mbp2011-store-update.png\" style=\"width: 500px; height: 150px; \" /></p>\r\n<p>\r\n	Ожидается что в продажу новые модели поступят после презентации <strong>Intel</strong> Light Peak, которая появится в ноутбуках серии Pro под названием <strong>Thunderbolt</strong>. Ждать осталось совсем не много.</p>\r\n',NULL,3,1),(180,'Face Time для Mac появился в Mac App Store','<p>\r\n	В поддержку новым MacBook Pro уже выпущена официальная версия Face Time для Mac. В отличии от беты, версия 1.0 будет распространятся не бесплатно, а по скромной цене в $0.99 за штуку. В официальной версии приложения появилась функция видеозвонков с разрешением 720р. Купить приложение уже можно в <a href=\"http://itunes.apple.com/ru/app/facetime/id414307850?mt=12\"><strong>Mac App Store</strong></a>.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/face-time-mac-99.png\" styl','<p>\r\n	В поддержку новым MacBook Pro уже выпущена официальная версия Face Time для Mac. В отличии от беты, версия 1.0 будет распространятся не бесплатно, а по скромной цене в $0.99 за штуку. В официальной версии приложения появилась функция видеозвонков с разрешением 720р. Купить приложение уже можно в <a href=\"http://itunes.apple.com/ru/app/facetime/id414307850?mt=12\"><strong>Mac App Store</strong></a>.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/face-time-mac-99.png\" style=\"width: 500px; height: 147px; \" /></p>\r\n<p>\r\n	Face Time - это приложения для видеозвонков между iOS устройствами и компьютерами Apple. Для iPhone/iPod Touch распространяется бесплатно. Интересна хорошим качеством видео и довольно высокой скоростью работы.</p>\r\n',NULL,3,1),(181,'ВИДЕО: Новые функции Maс OS Lion ','<p>\r\n	Все уже слышали о выходе новых MacBook Pro. О них мы поговорим завтра, когда шумиха уляжется и появится больше информации, помимо официального пресс-релиза. Еще одним важным событием стал выход первой бета-версии Mac OS Lion.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/macos-lion.png\" style=\"width: 500px; height: 211px; \" /></p>\r\n<p>\r\n	Новая операционная система, представленная публике 20 октября 2010 в виде симбиоза iOS и Mac OS наконец обрела ясные черты, отделяющи','<p>\r\n	Все уже слышали о выходе новых MacBook Pro. О них мы поговорим завтра, когда шумиха уляжется и появится больше информации, помимо официального пресс-релиза. Еще одним важным событием стал выход первой бета-версии Mac OS Lion.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/macos-lion.png\" style=\"width: 500px; height: 211px; \" /></p>\r\n<p>\r\n	Новая операционная система, представленная публике 20 октября 2010 в виде симбиоза iOS и Mac OS наконец обрела ясные черты, отделяющие ее от остального семейства кошачьих.</p>\r\n\r\n<h2>\r\n	КАК ВЫГЛЯДИТ MAC OS LION</h2>\r\n<p>\r\n	<br />\r\n	Лучше один раз увидеть, чем сто раз услышать. Предлагаю вам посмотреть видеообзор новых функций и самим убедится в том, что Lion - это действительно следующий шаг в будущее.</p>\r\n<p class=\"rtecenter\">\r\n	<iframe allowfullscreen=\"\" frameborder=\"0\" height=\"320\" src=\"http://www.youtube.com/embed/_f89Mzm4fWw?rel=0\" title=\"YouTube video player\" width=\"500\"></iframe></p>\r\n<p>\r\n	Более детальный обзор системы постараемся сделать, как только появятся первые впечатления от бета-версии. Для пользователей Mac OS Lion станет доступной уже этим летом. Как правило релиз новой ОС состоится во время WWDC - всемирной конференции разработчиков Apple. В этом году она должна быть посвящена только платформе Mac.</p>\r\n',NULL,3,1),(182,'Sparrow Lite доступен для загрузки','<p>\r\n	Приложение для работы с почтой <strong>Sparrow</strong> прошло долгий путь от первой бета-версии до продаж в Mac App Store. Сегодня же в магазине появилась бесплатная версия клиента для <strong>GMail</strong>. Sparrow Lite отличается от платной версии всего тремя чертами: работает только с одним аккаунтом, имеет рекламу от Carbon и ко всем вашим письмам добавляет подпись &quot;Sent width Sparrow&quot;.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/sparrow-logo_0.png\" s','<p>\r\n	Приложение для работы с почтой <strong>Sparrow</strong> прошло долгий путь от первой бета-версии до продаж в Mac App Store. Сегодня же в магазине появилась бесплатная версия клиента для <strong>GMail</strong>. Sparrow Lite отличается от платной версии всего тремя чертами: работает только с одним аккаунтом, имеет рекламу от Carbon и ко всем вашим письмам добавляет подпись &quot;Sent width Sparrow&quot;.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/sparrow-logo_0.png\" style=\"width: 500px; height: 177px; \" /></p>\r\n<p>\r\n	Для тех, кто готов смериться с ограничениями или хочет сэкономит $9.99 - ссылка в <a href=\"http://itunes.apple.com/ru/app/sparrow-lite/id417418059?mt=12\"><strong>Mac App Store</strong></a>. Для обыкновенного пользователя бесплатная версия Sparrow будет достойной заменой <strong>Mail.app</strong>.</p>\r\n\r\n<h2>\r\n	ИНТЕРФЕЙС SPARROW</h2>\r\n<p>\r\n	<br />\r\n	Если же вы еще не слышали о Sparrow - маленький экскурс от разработчиков. На видео показана работа приложения и его отличительные черты.</p>\r\n<p class=\"rtecenter\">\r\n	<iframe frameborder=\"0\" height=\"280\" src=\"http://player.vimeo.com/video/19659649\" width=\"500\"></iframe></p>\r\n<p>\r\n	Но главное отличие Sparrow от традиционных почтовых клиентов - его интерфейс, похожий на твиттер-клиент. Сравните его с Twitter для Mac и увидите очень много схожих черт.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/sparrow-twitter.png\" style=\"width: 500px; height: 372px; \" /></p>\r\n<p>\r\n	И даже новая версия Mail.app, которая появится в Mac OS Lion не настолько революционно, как Sparrow. Наш вам совет - попробуйте.</p>\r\n',NULL,3,1),(183,'Инструкция по установке Mac OS Lion для \"не разработчиков\"','<p>\r\n	Вчера, одновременно с выходом новых MacBook Pro появилась первая версия <a href=\"http://macov.net/new/macos-lion-new-features-video\">Mac OS Lion</a>, доступная для установки. Правда доступна она стала лишь разработчикам, имеющим соответствующую лицензию и в виде Developer Preview. Это значит, что простому пользователю никак не получить эту версию, да и толку от нее будет мало - она предназначена не для роботы, а для проверки работоспособности приложений.</p>\r\n<p class=\"rtecenter\">\r\n	<img a','<p>\r\n	Вчера, одновременно с выходом новых MacBook Pro появилась первая версия <a href=\"http://macov.net/new/macos-lion-new-features-video\">Mac OS Lion</a>, доступная для установки. Правда доступна она стала лишь разработчикам, имеющим соответствующую лицензию и в виде Developer Preview. Это значит, что простому пользователю никак не получить эту версию, да и толку от нее будет мало - она предназначена не для роботы, а для проверки работоспособности приложений.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/macosx-lion-developer-preview.png\" style=\"width: 500px; height: 107px; \" /></p>\r\n<p>\r\n	Но мак-пользователь простым в большинстве своем не является. Как только появилась возможность &quot;пощупать&quot; Льва, многие твердо на это решились. Мы, как &quot;пощупавшие&quot; хотим предоставить всем желающим инструкцию по установке Mac OS Lion для &quot;не разработчиков&quot;.</p>\r\n\r\n<h2>\r\n	ГДЕ ВЗЯТЬ MAC OS LION</h2>\r\n<p>\r\n	<br />\r\n	Как мы выше упоминали, официально взять первую версию Mac OS Lion можно только будучи <a href=\"http://developer.apple.com/programs/mac/\"><strong>зарегистрированным</strong></a> разработчиком Apple. Стоит лицензия $99 на один год и она даст вам право получать все тестовые продукты компании, вплоть до версий Golden Master, которые идут в продажу.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/macosx-lion-be-developer.png\" style=\"width: 500px; height: 203px; \" /></p>\r\n<p>\r\n	Второй способ - воспользоваться услугами разработчиков-посредников. Они добавят ваше устройство в свой список тестеров всего за $10. Вы получите возможность установки новых версий программного обеспечения абсолютно на законных основаниях. Такое практикуют журналисты и &quot;гики&quot;, готовые платить за удовольствие первыми получать &quot;на руки&quot; бета-версии и обновления. Мы только можем рекомендовать сервис <a href=\"http://download.imodzone.net/\"><strong>imodzone</strong></a>, который всего за $7 даст вам возможность получать все бета-версии. Там же можно просто скачать требуемый образ.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/macosx-lion-be-betatester.png\" /></p>\r\n<p>\r\n	Третий путь самый распространенный - путь пиратства. Очень многие считают, что информация должна распространятся бесплатно и открыто, без всяких ограничений. А некоторые просто не хотят платить лишних денег. В любом случае, если у вас нет желания воспользоваться первыми двумя путями получения Mac OS Lion - поищите в <a href=\"http://thepiratebay.org/search/mac%20lion/0/99/0\"><strong>Пиратской Бухте</strong></a>, там всегда все есть.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/macosx-lion-thepiratebay.png\" style=\"width: 500px; height: 300px; \" /></p>\r\n<p>\r\n	Какой бы путь вы не выбрали, результат один - у вас есть нужный образ, который мы сейчас и будем устанавливать. Хотя у подписки разработчика есть свои плюсы - все новые версии разработчик получает через Mac App Store, вводя необходимый Redeem-код. Это нововведение появилось совсем недавно и позволяет сэкономит много времени.</p>\r\n<h2>\r\n	КУДА СТАВИТЬ MAC OS LION</h2>\r\n<p>\r\n	<br />\r\n	У вас уже есть заветный образ Mac OS Lion и вам не терпится начать установку. Для начала нужно определиться со способом установки. В можете либо поставить льва поверх работоспособной системы, либо установить второй системой на внешний носитель. Так как версия для работы не предназначена, мы рекомендуем второй вариант. Подойдет любой носитель, обладающий емкостью от 16 ГБ. Мы выбрали для установки внешний HDD, о чем ни разу не пожалели.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/macosx-lion-disc.png\" style=\"width: 500px; height: 300px; \" /></p>\r\n<p>\r\n	Для установки необходимо разметить диск в GUID и отформатировать нужный раздел в HFS+ журнальный. Все это делается в <strong>Дисковой Утилите</strong>, которая расположена в <strong>Служебных Программах</strong>. Выбор места установки - довольно важный момент. Версия пока еще очень сырая, и жертвовать работоспособностью системы ради того, чтобы посмотреть на новые жесты не очень логично. И еще - 8 ГБ флешки вам будет мало, проверено.</p>\r\n<h2>\r\n	КАК СТАВИТЬ MAC OS LION</h2>\r\n<p>\r\n	<br />\r\n	Mac OS Lion устанавливать очень просто. Монтируем образ и запускаем установщик. Без разворачивания на раздел или флешку, без записи болванки. Прямо из работающей системы, выполнив минимум операций.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/macosx-lion-install-start.png\" /></p>\r\n<p>\r\n	Мастер установки предложит вам прочесть лицензионное соглашение и выбрать место установки. Мы выбрали заранее подготовленный раздел и запустили установку.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/macosx-lion-install-bootrom.png\" style=\"width: 500px; height: 300px; \" /></p>\r\n<p>\r\n	Через десять минут подготовки ваш Мак перезагрузится. Без предупреждения и уведомления. Рекомендуем закончить все свои дела перед установкой и сохранить все свои рабочие документы. После перезагрузки начнется сама установка. Не задавая никаких вопросов Mac OS Lion установится на раздел и перезагрузится. Для выбора загрузочного раздела вам необходимо будет в самом начале загрузки, до появления яблока на экране, зажать клавишу <strong>option</strong>, после чего появится возможность выбирать нужный раздел.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/macosx-lion-boot.png\" style=\"width: 500px; height: 247px; \" /></p>\r\n<p>\r\n	Дальше будет произведена обычная процедура ввода личных данных, паролей, выбор языка и часового пояса. Здесь ничего пока не изменили, кроме внешнего вида. Еще одна перезагрузка и вуаля - Mac OS Lion готов к работе.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/macosx-lion-desctop-launchpad.png\" style=\"width: 500px; height: 313px; \" /></p>\r\n<p>\r\n	О том, что нового появилось в Mac OS Lion вы можете узнать посмотрев видео, которое мы вчера <a href=\"http://macov.net/new/macos-lion-new-features-video\"><strong>публиковали</strong></a>. Наши личные впечатления и найденные интересности мы расскажем позже, в отдельной статье. А пока, желаем удачи в установке и надеемся, что у вас все получится.</p>\r\n',NULL,3,1),(184,'ВИДЕО: iPad 2 захватывает  планету','<p>\r\n	Не только слухи появляются в преддверии новых продуктов от Apple. Так же появляются интересные видеоролики, которые так или иначе эти слухи высмеивают. Совсем недавно мы публиковали юмористический <a href=\"http://macov.net/news/review-ipad-2-hd-humor\"><strong>видеообзор 27-и дюймового iPad</strong></a>, сегодня пришла очередь вторжения инопланетных iPad-убийц. Смотрим.</p>\r\n<p class=\"rtecenter\">\r\n	<iframe allowfullscreen=\"\" frameborder=\"0\" height=\"300\" src=\"http://www.youtube.com/embed/EDU','<p>\r\n	Не только слухи появляются в преддверии новых продуктов от Apple. Так же появляются интересные видеоролики, которые так или иначе эти слухи высмеивают. Совсем недавно мы публиковали юмористический <a href=\"http://macov.net/news/review-ipad-2-hd-humor\"><strong>видеообзор 27-и дюймового iPad</strong></a>, сегодня пришла очередь вторжения инопланетных iPad-убийц. Смотрим.</p>\r\n<p class=\"rtecenter\">\r\n	<iframe allowfullscreen=\"\" frameborder=\"0\" height=\"300\" src=\"http://www.youtube.com/embed/EDU9An5aPLc?rel=0\" title=\"YouTube video player\" width=\"500\"></iframe></p>\r\n<p>\r\n	Настоящий iPad 2 мы увидим уже <a href=\"http://macov.net/news/oficialno-2-marta-ipad-2\"><strong>2 марта</strong></a>, надеюсь он не станет вести себя столь агрессивно. Так же появилась информация, что одна из представленных моделей появится в продаже уже в день презентации. Тогда и станет известно, будет ли новый iPad обладать карбоновым корпусом и портом Thunderbolt.</p>\r\n',NULL,3,1),(185,'Время работы MacBook снизилось до 7 часов вслед MacBook Pro','<p>\r\n	C выходом новых MacBook Pro закончилась эра мифических 10 часов работы от батареи. Теперь Apple внесла коррективы и срок работы батарей снизился до более реальных 7-и часов. Причем эти изменения коснулись и пластикового MacBook. Трех часов ноутбуки лишились без изменения техническиххарактеристик. Чисто маркетинговый ход.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/7-hours.png\" style=\"width: 500px; height: 225px; \" /></p>\r\n<p>\r\n	Сложно сказать, насколько реальны заявл','<p>\r\n	C выходом новых MacBook Pro закончилась эра мифических 10 часов работы от батареи. Теперь Apple внесла коррективы и срок работы батарей снизился до более реальных 7-и часов. Причем эти изменения коснулись и пластикового MacBook. Трех часов ноутбуки лишились без изменения техническиххарактеристик. Чисто маркетинговый ход.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/7-hours.png\" style=\"width: 500px; height: 225px; \" /></p>\r\n<p>\r\n	Сложно сказать, насколько реальны заявленные семь часов рабочего времени. По нашим наблюдениям, среднее время продуктивной работы от батареи составляет 5 часов. Но если замерять таким же образом как и Apple, можно растянуть и на большее количество времени.</p>\r\n<p>\r\n	От нас же совет - чтобы продлить время работы ноутбука, постарайтесь закрывать его, когда не используете. Пользователи, разбалованные iMac и iPad, порой забывают о настройках энергопитания и оставляют работающие лэптопы благополучно разряжаться во время отсутствия хозяев. Мы вам желаем, чтобы батареи вашего MacBook вам хватало, не взирая на изменения маркетинговой политики Apple.</p>\r\n',NULL,3,1),(186,'Впечатления от Mac OS Lion Developer Preview часть 1','<p>\r\n	Мы не являемся командой разработчиков ПО для Mac или iOS, так что получили свою версию Mac OS Lion Developer Preview не самым легальным способом. Точнее, тем способом, который значится под номером три в нашей <a href=\"http://macov.net/news/how-to-install-mac-os-lion-for-user\"><strong>инструкции</strong></a>. Делали мы это исключительно из мирных побуждений, чтобы самим иметь представление о новой ОС и рассказать другим о наших ощущениях. Так получилось, что в тестировании у нас есть некото','<p>\r\n	Мы не являемся командой разработчиков ПО для Mac или iOS, так что получили свою версию Mac OS Lion Developer Preview не самым легальным способом. Точнее, тем способом, который значится под номером три в нашей <a href=\"http://macov.net/news/how-to-install-mac-os-lion-for-user\"><strong>инструкции</strong></a>. Делали мы это исключительно из мирных побуждений, чтобы самим иметь представление о новой ОС и рассказать другим о наших ощущениях. Так получилось, что в тестировании у нас есть некоторый опыт, который мы и постарались использовать.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/macosc-lion-back-to-mac.png\" style=\"width: 500px; height: 300px; \" /></p>\r\n<p>\r\n	Не желая идти по стопам Капитана Очевидности и дублировать уже известные всем новости, мы решили проанализировать собственные чувства при работе с новой Mac OS. Начиная с процесса установки и заканчивая ощущениями от новых жестов и функций.</p>\r\n\r\n<h2>\r\n	УСТАНОВКА</h2>\r\n<p>\r\n	<br />\r\n	С установкой и раньше разобраться можно было любому свитчеру или ребенку. Проще не стало, зато стало красивее и более детальнее. Особенно понравилась установка из системы, без необходимости использовать внешние носители. В этот раз пользователи могут получить обновление напрямую через Mac App Store, минуя необходимость покупать лицензионный диск. Продавать ОС через магазин приложений - это очень удобно и быстро. Просто купить, а дальше Lion все сделает сам - вам останется только ввести свое имя и придумать пароль.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/macosx-lion-install-start.png\" style=\"width: 500px; height: 300px; \" /></p>\r\n<p>\r\n	Представьте себе WWDC&#39;11 и презентацию Mac OS X 10.7 Lion, заканчивающуюся словами:&quot;Когда мы закончим, Lion появится в Mac App Store и вы сможете его скачать всего за $9.99!&quot;. Все тут же бросятся обновляться и это будет просто апофеоз ликования и религиозный экстаз. Надеюсь, что все будет именно так.</p>\r\n<h2>\r\n	ВНЕШНИЙ ВИД</h2>\r\n<p>\r\n	<br />\r\n	После установки вы сразу же попадаете в непривычный Finder. Его функции остались прежними, вот только интерфейс теперь стал напоминать iOS и некоторые элементы управления явно предназначены для работы на сенсорном экране. Это не выглядит чужеродно, просто непривычно. К этому быстро привыкаешь, так что когда попадаешь в 10.6 интерфейс снежного барса уже не кажется таким крутым. Скроллбар теперь стал таким же как и в iOS, то есть полупрозрачный силуэт, который видно только при прокрутке. Монохромные значки из iTunes перебрались в Finder и, вероятно, станут уделом каждого Cocoa-приложения. Тени стали гуще и контрастнее, хотя выглядит это не очень.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/macosx-lion-desctop-launchpad.png\" style=\"width: 500px; height: 313px; \" /></p>\r\n<p>\r\n	Цветовая гамма стала более тусклой, очень много деталей выполнено в полутонах. После буйства контрастов и красок обоих леопардов это слегка отвлекает. Остается уповать на то, что эта версия предназначена для технического ознакомления, а не оценки визуальных эффектов и внешнего вида. Надеюсь ближе к релизу интерфейс значительно преобразится.</p>\r\n<h2>\r\n	ЖЕСТЫ</h2>\r\n<p>\r\n	<br />\r\n	Ну и самое интересное, что появилось в Mac OS Lion это <a href=\"http://macov.net/new/macos-lion-new-features-video\"><strong>многопальцевые жесты</strong></a>. О них уже много писали хвалебных отзывов, что хотелось бы внести свою ложку липкого дегтя. Все эти жесты удобны и привычны, но их уж слишком много для одного раза. Да и привычным перемещением 3-мя пальцами придется пожертвовать в пользу перелистывания между приложениями. Ди и охарактеризовать все эти жесты можно одним &quot;очень сложно&quot;. Эта характеристика совсем не вяжется с Apple и ее подходом к продуктам. Умышленно перегружать пользователя полезными функциями - это не тот путь по которому развивались Mac OS и iOS последнее время. Теперь каждое неосторожное движение по трекпаду будет вызывать неясную реакцию ОС. А дублирующиеся жесты меня вообще повергли в шок. Мне бы хотелось, чтобы от количества пальцев зависела реакция приложения.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/macosx-lion-gestures.png\" style=\"width: 500px; height: 313px; \" /></p>\r\n<p>\r\n	Пятипальцевый жест на трекпаде моего MacBook Pro вообще трудноосуществимая операция. И это у меня еще пальцы тонкие и длинные. Мне кажется, новые жесты сродни многопальцевым жестам в бета-версиях iOS 4.3 - для тестирования разработчиками, но не для использования в релизе. Надеюсь останутся останутся только полезные и удобные жесты, которые мы и увидим в финальной версии Mac OS X 10.7 Lion.</p>\r\n<h2>\r\n	Продолжение следует...</h2>\r\n',NULL,3,1),(187,'iPad будет обновлен уже завтра.','<p>\r\n	Уже завтра, 2 марта, на <a href=\"http://macov.net/news/oficialno-2-marta-ipad-2\"><strong>специальном мероприятии</strong></a> покажут приемника iPad. Судя по всему, называться он будет так же, без дополнительных цифровых индексов, по аналогии с компьютерной линейкой. Ранее предполагалось, что предыдущая модель останется в продаже по сниженной стоимости, как это было с iPhone, но Apple позиционирует iPad как компьютер и придерживается соответствующей политики.</p>\r\n<p class=\"rtecenter\">\r\n	<','<p>\r\n	Уже завтра, 2 марта, на <a href=\"http://macov.net/news/oficialno-2-marta-ipad-2\"><strong>специальном мероприятии</strong></a> покажут приемника iPad. Судя по всему, называться он будет так же, без дополнительных цифровых индексов, по аналогии с компьютерной линейкой. Ранее предполагалось, что предыдущая модель останется в продаже по сниженной стоимости, как это было с iPhone, но Apple позиционирует iPad как компьютер и придерживается соответствующей политики.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ipad поставки прекращены.png\" style=\"width: 500px; height: 200px; \" /></p>\r\n<p>\r\n	Как сообщает 9to5mac производство iPad первого поколения уже остановлено и поставки его не осуществляются. Возможно следующее поколение iPad, которое будет представлено завтра, поступит в продажу в день презентации или в кратчайшие сроки, вместо анонсированной даты в середине апреля.</p>\r\n\r\n<h2>\r\n	ЧТО ОЖИДАЕТСЯ</h2>\r\n<p>\r\n	<br />\r\n	Некоторые аналитики считают, что в продажу поступит сразу три модели. Это будет модель только с WiFi, модель с 3G GSM/UMTS и модель с 3G CDMA/EVDO для сетей Verizon. Возможно, в этот раз они поступят в продажу одновременно, а не с задержкой в несколько месяцев.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/iPad 2 появится в продаже сразу же после анонса.jpeg\" style=\"width: 500px; height: 262px; \" /></p>\r\n<p>\r\n	По слухам, возможен вариант iPad с 3G, который сможет работать и в UMTS и CDMA благодаря чипу, который уже сейчас установлен в CDMA iPhone от Verizon. Как все будет на самом деле мы узнаем уже завтра вечером.</p>\r\n<p>\r\n	Во время мероприятия мы постараемся вести твиттер-трансяляцию о всем происходящем. Мы в твиттере - <a href=\"http://twitter.com/macov_net\">@macov_net</a></p>\r\n',NULL,3,1),(188,'Сервисы Mobile Me возможно станут частично бесплатны','<p>\r\n	На завтрашнем событии помимо представления iPad второго поколения возможно произойдет еще одно важное обновление. Сервис Mobile Me который сейчас предоставляет возможность облачной синхронизации контактов, почты, календарей и еще много чего полезного может стать частично бесплатным. В данный момент годовая подписка на него стоит $99 для одного пользователя и $149 семейная подписка на 5 человек.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/Mobile Me станет бесплатным.p','<p>\r\n	На завтрашнем событии помимо представления iPad второго поколения возможно произойдет еще одно важное обновление. Сервис Mobile Me который сейчас предоставляет возможность облачной синхронизации контактов, почты, календарей и еще много чего полезного может стать частично бесплатным. В данный момент годовая подписка на него стоит $99 для одного пользователя и $149 семейная подписка на 5 человек.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/Mobile Me станет бесплатным.png\" style=\"width: 500px; height: 300px; \" /></p>\r\n<p>\r\n	По слухам, на презентации 2 марта основной упор будет сделан именно на сервис Mobile Me. Сейчас, когда облачные сервисы набирают обороты, самое время начать использовать свежепостроенные дата-центры в Северной Каролине на полную мощность. Возможно слухи про облачную версию iOS частично найдут свое подтверждение завтра, в виде анонса iOS 5.</p>\r\n\r\n<h2>\r\n	ЧТО СТАНЕТ БЕСПЛАТНЫМ</h2>\r\n<p>\r\n	Некоторые элементы Mobile Me, в свою очередь, могут быть исключены из пакета и стать доступными для всех Mac- и iOS-пользователей. Так уже случалось, когда сервис Find My iPhone стал доступен для каждого.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ipad %2B macbook.png\" style=\"width: 500px; height: 187px; \" /></p>\r\n<p>\r\n	Но самым ожидаемым обновлением Mobile Me является беспроводная синхронизация iOS устройств с домашним компьютером. Ранее устройства можно было синхронизировать только с помощью USB кабеля. Это позволяло максимально защитить ваши персональные данные. Теперь гарантом безопасности сможет выступать Mobile.</p>\r\n',NULL,3,1),(189,'Впечатления от Mac OS Lion часть 2. В картинках','<p>\r\n	Недолгие копания в Mac OS X 10.7 Lion вызвали у нас много противоречивых ощущений, которые мы описали в первой части. Во второй мы покажем то интересное, что нас заинтересовало. В картинках и с комментариями.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/1%23man-with-lion.jpeg\" style=\"width: 500px; height: 381px; \" /></p>\r\n<p>\r\n	Напомним, что наше мнение субъективно и все высказанные комментарии являются лишь точкой зрения автора и не претендуют на роль истины в послед','<p>\r\n	Недолгие копания в Mac OS X 10.7 Lion вызвали у нас много противоречивых ощущений, которые мы описали в первой части. Во второй мы покажем то интересное, что нас заинтересовало. В картинках и с комментариями.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/1%23man-with-lion.jpeg\" style=\"width: 500px; height: 381px; \" /></p>\r\n<p>\r\n	Напомним, что наше мнение субъективно и все высказанные комментарии являются лишь точкой зрения автора и не претендуют на роль истины в последней инстанции.</p>\r\n\r\n<h2>\r\n	ВЕРСИЯ FACE TIME</h2>\r\n<p>\r\n	<br />\r\n	На прошлой неделе вместе с обновлением MacBook Pro компания Apple представила последнюю версию Face Time 1.0 - сервиса видеозвонков между пользователей Mac OS и iOS. И если мобильная версия является абсолютно бесплатной, то Mac-версия появилась в Mac App Store по символической цене в $0,99 и доступна для покупки уже сейчас.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/2%23face-time-new-version.png\" style=\"width: 500px; height: 331px; \" /></p>\r\n<p>\r\n	А вот скриншот из MAc OS Lion, в котором это приложение уже присутствует абсолютно бесплатно. И версия этого приложения несколько новее, чем у продаваемого в App Store. Вот такой маркетинговый ход для покупки обновления.</p>\r\n<h2>\r\n	НЕ РАБОТАЕТ MAC APP STORE</h2>\r\n<p>\r\n	<br />\r\n	Магазин приложений пока не работает в новой ОС. Связано это с тем, что не все приложения пока совместимы с Львом да и для Developer Preview он не нужен.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/3%23mac-app-store-fake.png\" style=\"width: 500px; height: 263px; \" /></p>\r\n<p>\r\n	Хотя конектится магазин явно не по адресу. Ну ничего, еще будет допилен и тестирован.</p>\r\n<h2>\r\n	ПОКАЗЫВАЕТ КОЛИЧЕСТВО ФАЙЛОВ</h2>\r\n<p>\r\n	<br />\r\n	Для тех, кто много работает с файлами или работает с большим их количеством появится удобная функция. Теперь количество переносимых/перемещаемых/копируемых файлов будет показываться в аккуратном красном кружке рядом с курсором.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/4%23number-of-files.png\" style=\"width: 500px; height: 313px; \" /></p>\r\n<p>\r\n	К сожалению не смотрели как это будет выглядеть на красном фоне, но тень от кружка должна сделать свое дело.</p>\r\n<h2>\r\n	ПАПКИ В LAUNCHBAR</h2>\r\n<p>\r\n	<br />\r\n	Про это уже говорили и не раз, просто хотелось немного прокомментировать. Функция удобная, спору нет, и выглядит довольно привычно для пользователей iOS. Только увлечение такими папками и рабочими столами Launchbar&#39;a может только усложнить доступ к нужному приложению. Если запутаетесь сами виноваты.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/5%23folders-in-launchbar.png\" style=\"width: 500px; height: 313px; \" /></p>\r\n<p>\r\n	Мне, как увлеченному iOS пользователю и аккуратисту, очень нравится группировать все свои приложения по категориям и рабочим столам. Приятно, когда все лежит на своем месте и ты всегда знаешь где это место находится.</p>\r\n<h2>\r\n	БЕЛЫЙ ЦВЕТ ЛЬВУ НЕ ИДЕТ</h2>\r\n<p>\r\n	<br />\r\n	Эксперименты с внешним видом доказали, что Mac OS Lion пока лишь инструмент проверки работоспособности приложений. Зная отношение разработчиков Apple к мелочам, сложно представить, что ситуацию на скриншоте они считают оптимальной.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/6%23fail-with-white-bacground.png\" style=\"width: 500px; height: 313px; \" /></p>\r\n<p>\r\n	И дело даже не в белых буквах в Launchbar. В iOS точно такая же ситуация. Только там вышли из положения сместив тень от иконки под текст, так, что буквы располагаются на фоне тени. В Lion пока до этого не добрались.</p>\r\n<h2>\r\n	JAVA ПОСТИГЛА СУДЬБА FLASH</h2>\r\n<p>\r\n	<br />\r\n	При попытке подключить 3G модем к ноутбуку с Lion меня постигла неудача. Дело в том, что для работы модема необходимо установить некоторый софт, который идет в комплекте. При попытке установки я получил уведомление о том, что необходимо сначала установить Java виртуальную машину, которую мне предложили скачать из интернета, которого не будет, по причине неустановленного софта. Замкнутый круг.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/7%23java-from-the-web.png\" style=\"width: 500px; height: 205px; \" /></p>\r\n<p>\r\n	А интересный момент этого не в том, что я остался без интернета. Как и в случае с Adobe Flash, Java пропадет из Mac OS и пользователю ее придется устанавливать самому. Принимая во внимание тот факт, что Apple отказалась от разработки Java для Mac OS и переложила эту задачу на владельца технологии, отказ от предустановки Java вполне логичный шаг.</p>\r\n<h2>\r\n	УДОБНАЯ СОРТИРОВКА В FINDER&gt;</h2>\r\n<p>\r\n	<br />\r\n	Опять возвращаемся в Finder. Теперь для изменения режима сортировки файлов достаточно просто зайти в специальное меню. Для владельцев 27&quot; iMac проблема вряд ли была актуальной, но обладатели 13&quot; экранов меня поймут.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/8%23new-sortirovka-in-finder.png\" style=\"width: 500px; height: 312px; \" /></p>\r\n<p>\r\n	В Snow Leopard такая возможность тоже была, но для такого переключения необходимо было совершить несколько лишних движений. Теперь достаточно просто выбрать нужный режим. Теперь это будет удобно делать даже пальцами н сенсорном экране.</p>\r\n<h2>\r\n	НОВЫЙ PREVIEW И PDF</h2>\r\n<p>\r\n	<br />\r\n	Быстрый просмотрщик файлов Preview теперь полностью обновился. Помимо стильного внешнего вида и высокой скорости работы он получил еще и ряд интересных функций. Вот например PDF-файл. Теперь это можно использовать не только для быстрого просмотра.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/9%23pdf-books-in-preview.png\" style=\"width: 500px; height: 295px; \" /></p>\r\n<p>\r\n	Функция быстрого просмотра - одна из незаменимых функций, наравне с Exopse и Spaces. К ним быстро привыкаешь и радует, что они становятся все лучше.</p>\r\n<h2>\r\n	К EPIC FAIL ГОТОВ</h2>\r\n<p>\r\n	<br />\r\n	Последним номером программы по праву считаю вот этот скриншот. Если раньше все плохое заканчивалось &quot;ядреной паникой&quot; (Kernel Panic) и неизменным хардверным выключением, то теперь предусмотрена перезагрузка в критических ситуациях. В совокупности с Time Machine и хранением всех состояний файлов, Mac OS становится очень надежной. Теперь все бекапы делаются за вас.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/10%23epic-fail-ready.png\" style=\"width: 500px; height: 394px; \" /></p>\r\n<p>\r\n	В Mac OS Lion еще очень много чего интересного, но об этом уже в следующий раз.</p>\r\n<h2>\r\n	Продолжение следует...</h2>\r\n',NULL,3,1),(190,'iPad 2 на подходе. Apple Store Online закрыт на переучет','<p>\r\n	Ну вот кажется и дождались. Apple Store Online зкарыт на переучет за 2 часа до важного события. iPad 2 может появиться уже сегодня, сразу после презентации.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/mbp2011-store-update.png\" style=\"width: 500px; height: 150px; \" /></p>\r\n<p class=\"rteleft\">\r\n	Вместе с iPad может появиться еще нечто. Возможно это будут сервисы Mobile Me или бюджетный iPhone. А может нам покажут новые функции iOS 5. Об этом мы узнаем уже через несколь','<p>\r\n	Ну вот кажется и дождались. Apple Store Online зкарыт на переучет за 2 часа до важного события. iPad 2 может появиться уже сегодня, сразу после презентации.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/mbp2011-store-update.png\" style=\"width: 500px; height: 150px; \" /></p>\r\n<p class=\"rteleft\">\r\n	Вместе с iPad может появиться еще нечто. Возможно это будут сервисы Mobile Me или бюджетный iPhone. А может нам покажут новые функции iOS 5. Об этом мы узнаем уже через несколько часов.</p>\r\n',NULL,3,1),(191,'ВИДЕО: iPad 2 официальное видео и первый видеообзор','<p>\r\n	Всего несколько часов прошло с того момента, как Стив Джобс показал всему миру новую модель iPad. Вторая версия получилась тоньше, легче и мощнее. iPad 2 обзавелся двумя камерами и стильным чехлом. Впрочем, об этом мы уже писали в твиттер-трансляции и даже прилагали картинки. Теперь настало время увидеть все своими глазами.</p>\r\n<p class=\"rtecenter\">\r\n	<iframe allowfullscreen=\"\" frameborder=\"0\" height=\"300\" src=\"http://www.youtube.com/embed/BFgVFZVcJTw\" title=\"YouTube video player\" width=\"','<p>\r\n	Всего несколько часов прошло с того момента, как Стив Джобс показал всему миру новую модель iPad. Вторая версия получилась тоньше, легче и мощнее. iPad 2 обзавелся двумя камерами и стильным чехлом. Впрочем, об этом мы уже писали в твиттер-трансляции и даже прилагали картинки. Теперь настало время увидеть все своими глазами.</p>\r\n<p class=\"rtecenter\">\r\n	<iframe allowfullscreen=\"\" frameborder=\"0\" height=\"300\" src=\"http://www.youtube.com/embed/BFgVFZVcJTw\" title=\"YouTube video player\" width=\"500\"></iframe></p>\r\n<p>\r\n	Для тех, кому мало постановочного видео от команды режиссеров Apple, ресурс Engadget уже подготовил видеообзор нового iPad. Он действительно так быстр, как его нам представляли.</p>\r\n<p class=\"rtecenter\">\r\n	<object classid=\"clsid:D27CDB6E-AE6D-11cf-96B8-444553540000\" height=\"266\" id=\"viddler\" width=\"437\"><param name=\"movie\" value=\"http://www.viddler.com/simple/b3dd935d/\" /><param name=\"allowScriptAccess\" value=\"always\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"flashvars\" value=\"fake=1\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" flashvars=\"fake=1\" height=\"266\" name=\"viddler\" src=\"http://www.viddler.com/simple/b3dd935d/\" type=\"application/x-shockwave-flash\" width=\"437\"></embed></object></p>\r\n<p>\r\n	Осталось только дождаться 11 марта и увидеть его вживую.</p>\r\n',NULL,3,1),(192,'ДАЙДЖЕСТ: Наша твиттер-трансляция презентации iPad 2 в одном месте с картинками','<p>\r\n	Для отчетности и себе на память выкладываем дайджест вчерашней твиттер-трансляции с презентации нового iPad. Если вы нас не фоловите в twitter самое время это сделать <a href=\"http://twitter.com/macov_net\"><strong>FOLLOW @macov_net</strong></a></p>\r\n<h2>\r\n	<em>А в Сан Франциско солнечно) Yerba Buena нарядная и готовая ко всему)</em></h2>\r\n<br />\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" http:=\"\" img=\"\" p=\"\" src=\"/sites/default/files/А в Сан Франциско солнечно) Yerba Buena нарядная и готовая ко ','<p>\r\n	Для отчетности и себе на память выкладываем дайджест вчерашней твиттер-трансляции с презентации нового iPad. Если вы нас не фоловите в twitter самое время это сделать <a href=\"http://twitter.com/macov_net\"><strong>FOLLOW @macov_net</strong></a></p>\r\n<h2>\r\n	<em>А в Сан Франциско солнечно) Yerba Buena нарядная и готовая ко всему)</em></h2>\r\n<br />\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" http:=\"\" img=\"\" p=\"\" src=\"/sites/default/files/А в Сан Франциско солнечно) Yerba Buena нарядная и готовая ко всему).jpeg\" style=\"width: 500px; height: 333px; \" title=\"Ð� Ð² Ð¡Ð°Ð½ Ð¤Ñ�Ð°Ð½Ñ�Ð¸Ñ�ÐºÐ¾ Ñ�Ð¾Ð»Ð½ÐµÑ�Ð½Ð¾) Yerba Buena... on Twitpic\" twitpic.com=\"\" /></p>\r\n<h2>\r\n	Beatles играет в Yerba Buena, пока все ждут начала презентации.</h2>\r\n<br />\r\n<h2>\r\n	<em>Стив джобс на сцене Yerba Buena. Под громкие аплодисменты презентация начинается</em></h2>\r\n<br />\r\n<h2>\r\n	Он улыбается, но стал совсем худым</h2>\r\n<br />\r\n<p class=\"rtecenter\">\r\n	<br />\r\n	<img alt=\"\" src=\"/sites/default/files/Он улыбается%2C но стал совсем худым %23ipad2_ru.jpeg\" style=\"width: 500px; height: 333px; \" /></p>\r\n<h2>\r\n	<em>По традиции начали с успехов. iBooks, App Store...200 млн аккаунтов, 10 млрд. загрузок...</em></h2>\r\n<br />\r\n<h2>\r\n	&quot;Наша главная технология - чудесные и революционные устройства по невероятной цене&quot;</h2>\r\n<br />\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/Наша главная технология - чудесные и революционные устройства по невероятной цене %23ipad2_ru.jpeg\" style=\"width: 500px; height: 333px; \" /></p>\r\n<h2>\r\n	Стив Джобс делится результатами iPad</h2>\r\n<br />\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/Стив Джобс делится результатами iPad .jpeg\" style=\"width: 500px; height: 333px; \" /></p>\r\n<h2>\r\n	Настало время торжественного видео о судьбе iPad в 2010 году. Фил Шиллер:&quot;Некоторые называют iPad пост-PC устройством&quot;</h2>\r\n<br />\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/Настало время торжественного видео о судьбе iPad в 2010 году.jpeg\" style=\"width: 500px; height: 333px; \" /></p>\r\n<h2>\r\n	Бизнесс-сегмент полюбил iPad, это да.</h2>\r\n<br />\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/Бизнесс-сегмент полюбил iPad%2C это да.jpeg\" style=\"width: 500px; height: 333px; \" /></p>\r\n<h2>\r\n	iPad 2. Он будет называться именно так!</h2>\r\n<br />\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/iPad 2.jpeg\" style=\"width: 500px; \" /></p>\r\n<h2>\r\n	Как мы и предполагали - именно процессор A5 с новой графикой и низким энергопотреблением.</h2>\r\n<br />\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/Как мы и предполагали - именно процессор A5 с новой графикой и низким энергопотреблением.jpeg\" style=\"width: 500px; height: 333px; \" /></p>\r\n<h2>\r\n	Две камеры! Именно 2!</h2>\r\n<br />\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/Две камеры! Именно 2!.jpeg\" style=\"width: 500px; height: 333px; \" /></p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/Две камеры! Именно 2!-1.jpeg\" style=\"width: 500px; height: 333px; \" /></p>\r\n<h2>\r\n	iPad 2 будет тоньше iPhone 4 и других гаджетов</h2>\r\n<br />\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/iPad 2 будет тоньше iPhone 4 и других гаджетов.jpeg\" style=\"width: 500px; height: 333px; \" /></p>\r\n<h2>\r\n	iPad 2 будет в 2 цветах - черном и белом.</h2>\r\n<br />\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/iPad 2 будет в 2 цветах - черном и белом.jpeg\" style=\"width: 500px; height: 333px; \" /></p>\r\n<h2>\r\n	<em>Надеемся с белыми iPad проблем не возникнет)</em></h2>\r\n<br />\r\n<h2>\r\n	Цены остались прежними. 11 марта начало продаж в США, 25 марта в 26 странах мира.</h2>\r\n<br />\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/Цены остались прежними. 11 марта начало продаж в США%2C 25 марта в 26 странах мира.jpeg\" style=\"width: 500px; height: 333px; \" /></p>\r\n<h2>\r\n	<em>Россия не входит в первые 26 стран( </em></h2>\r\n<br />\r\n<h2>\r\n	Новые чехлы дл iPad уже не так унылы как первые.</h2>\r\n<br />\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/Новые чехлы дл iPad уже не так унылы как первые.jpeg\" style=\"width: 500px; height: 333px; \" /></p>\r\n<h2>\r\n	И это даже больше чем просто чехлы.</h2>\r\n<br />\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/И это даже больше чем просто чехлы.jpeg\" style=\"width: 500px; height: 333px; \" /></p>\r\n<h2>\r\n	И все уже не так просто как раньше - теперь чехол стал частью планшета</h2>\r\n<br />\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/И все уже не так просто как раньше - теперь чехол стал частью планшета.jpeg\" style=\"width: 500px; height: 333px; \" /></p>\r\n<h2>\r\n	Очень выразительной и красочной частью.</h2>\r\n<br />\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/Очень выразительной и красочной частью.jpeg\" style=\"width: 500px; height: 333px; \" /></p>\r\n<h2>\r\n	iOS 4.3 ничего необычного кроме Safari, этого в бета-версиях не было.</h2>\r\n<br />\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/iOS 4.3 ничего необычного кроме Safari%2C этого в бета-версиях не было.jpeg\" style=\"width: 500px; height: 333px; \" /></p>\r\n<h2>\r\n	Персональная точка доступа только для iPhone 4. Ожидаемо.</h2>\r\n<br />\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/Персональная точка доступа только для iPhone 4. Ожидаемо.jpeg\" style=\"width: 500px; height: 333px; \" /></p>\r\n<h2>\r\n	Photo Booth для iPad 2. А еще сейчас будет Face Time)</h2>\r\n<br />\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/Photo Booth для iPad 2.jpeg\" style=\"width: 500px; height: 333px; \" /></p>\r\n<h2>\r\n	А вот и Face Time, ну кто бы мог подумать))) Вот ради этого уже стоит подумать о приобретении iPad 2</h2>\r\n<br />\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/А вот и Face Time%2C ну кто бы мог подумать))) Вот ради этого уже стоит подумать о приобретении iPad 2.jpeg\" style=\"width: 500px; height: 333px; \" /></p>\r\n<h2>\r\n	iOS 4.3 выйдет 11 марта. Значит Golden Master выдадут сегодня.</h2>\r\n<br />\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/iOS 4.3 выйдет 11 марта. Значит Golden Master выдадут сегодня.jpeg\" style=\"width: 500px; height: 333px; \" /></p>\r\n<h2>\r\n	iMovie для iPad скоро появится в App Store</h2>\r\n<br />\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/iMovie для iPad скоро появится в App Store.jpeg\" style=\"width: 500px; height: 333px; \" /></p>\r\n<h2>\r\n	А вот это действительно круто - Garage Band для iPad. Приложение обязательное для установки музыкантами.</h2>\r\n<br />\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/А вот это действительно круто - Garage Band для iPad. Приложение обязатеьное для установки музыкантами.jpeg\" style=\"width: 500px; height: 333px; \" /></p>\r\n<h2>\r\n	Это действительно круто.</h2>\r\n<br />\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/Это действительно круто.jpeg\" style=\"width: 500px; height: 333px; \" /></p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/Это действительно круто-1.jpeg\" style=\"width: 500px; height: 333px; \" /></p>\r\n<h2>\r\n	<em>На <a href=\"http://www.apple.com/\"> <strong>apple.com</strong></a> редизайн. Новый iPad рулит</em></h2>\r\n<br />\r\n<h2>\r\n	Для тех, у кого не открывается Apple.com</h2>\r\n<br />\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/Для тех%2C у кого не открывается Apple.png\" style=\"width: 500px; height: 283px; \" /></p>\r\n<h2>\r\n	Барабанная установка в Garage Band - разработчики действительно приложили усилия.</h2>\r\n<br />\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/Барабанная установка в Garage Band - разработчики действительно приложили усилия.jpeg\" style=\"width: 500px; height: 333px; \" /></p>\r\n<h2>\r\n	ХАХАХАХА :))) я под столом - про презентацию айпад2</h2>\r\n<br />\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/Зарисовка сегодняшней презентации %23ipad2rus.jpeg\" style=\"width: 500px; height: 500px; \" /></p>\r\n<h2>\r\n	Garage Band выйдет 11 марта за $4.99</h2>\r\n<br />\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/С выходом Garage Band очень много музыкальных приложений в App Store потеряют свою популярность.jpeg\" style=\"width: 500px; height: 333px; \" /></p>\r\n<h2>\r\n	Финальный слайд триумфа от Apple.</h2>\r\n<br />\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/Финальный слайд триумфа от Apple.jpeg\" style=\"width: 500px; height: 333px; \" /></p>\r\n<h2>\r\n	Видео, которое сейчас показывают на презентации можно посмотреть на главной странице <a href=\"http://www.apple.com/\">apple.com</a></h2>\r\n<br />\r\n<h2>\r\n	<em>Стив Джобс вернулся на сцену</em></h2>\r\n<br />\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/Стив Джобс вернулся на сцену.jpeg\" style=\"width: 500px; height: 333px; \" /></p>\r\n<h2>\r\n	Вот и все, презентация закончилась. Спасибо ресурсу Engadget за материалы и читателям за обратную связь. Мы старались.</h2>\r\n<br />\r\n<br />\r\n<h2>\r\n	<em>Все, кому понравилась наша трансляция, добавляйтесь к нам в <a href=\"http://www.facebook.com/pages/macovnet/181670525210358\"><strong>Facebook</strong></a></em></h2>\r\n<br />\r\n<br />\r\n<h2>\r\n	PS:// Г**но ваш айпад! (via MacPartyMoscow HardRockCafe)</h2>\r\n<br />\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/Г**но ваш айпад! (via MacPartyMoscow HardRockCafe) .jpeg\" style=\"width: 500px; height: 374px; \" /></p>\r\n',NULL,3,1),(193,'ВИДЕО: Пародия на рекламу iPad 2','<p>\r\n	Все заинтересованные выходом iPad 2 уже наверное посмотрели <a href=\"http://macov.net/news/ipad-2-firs-video\">промо-видео</a>, в котором представители Apple нахваливают свой продукт. Юмористы не остались в стороне от такого события и подготовили короткую пародию на хвалебную оду новому планшету.</p>\r\n<p class=\"rtecenter\">\r\n	<iframe allowfullscreen=\"\" frameborder=\"0\" height=\"300\" src=\"http://www.youtube.com/embed/wmjyeE3jDAU?rel=0\" title=\"YouTube video player\" width=\"500\"></iframe></p>\r\n<p>','<p>\r\n	Все заинтересованные выходом iPad 2 уже наверное посмотрели <a href=\"http://macov.net/news/ipad-2-firs-video\">промо-видео</a>, в котором представители Apple нахваливают свой продукт. Юмористы не остались в стороне от такого события и подготовили короткую пародию на хвалебную оду новому планшету.</p>\r\n<p class=\"rtecenter\">\r\n	<iframe allowfullscreen=\"\" frameborder=\"0\" height=\"300\" src=\"http://www.youtube.com/embed/wmjyeE3jDAU?rel=0\" title=\"YouTube video player\" width=\"500\"></iframe></p>\r\n<p>\r\n	Видео получилось несколько обидным, но правдивым. Больше всего порадовал ответ на вопрос &quot;Что мне делать если я только что купил старый iPad?&quot;.</p>\r\n<p>\r\n	От себя добавлю - iPad нельзя рассматривать как устройство отдельное от инфраструктуры. Apple развивает все в комплексе и ОС и приложения и магазин и устройство. К сожалению люди из мира PC пока этого не понимают.</p>\r\n',NULL,3,1),(194,'ИНСТРУКЦИЯ: Как включить iTunes Home Sharing в iOS 4.3','<p>\r\n	2 марта на презентации iPad 2 публике так же представили iOS 4.3, одной из основных изюминок которой являлась функция Home Sharing. Она позволяет получить доступ ко всей iTunes медиатеке c любого iOS устройства. Теперь для того, чтобы посмотреть фильм или послушать любимый альбом вам не нужно синхронизировать ваше устройство с компьютером, достаточно подключиться к своей домашней коллекции.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/itunes-home-sharing.png\" style=\"w','<p>\r\n	2 марта на презентации iPad 2 публике так же представили iOS 4.3, одной из основных изюминок которой являлась функция Home Sharing. Она позволяет получить доступ ко всей iTunes медиатеке c любого iOS устройства. Теперь для того, чтобы посмотреть фильм или послушать любимый альбом вам не нужно синхронизировать ваше устройство с компьютером, достаточно подключиться к своей домашней коллекции.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/itunes-home-sharing.png\" style=\"width: 500px; height: 81px; \" /></p>\r\n<p>\r\n	О том как это сделать я вам сейчас расскажу.</p>\r\n\r\n<h2>\r\n	НАСТРАИВАЕМ iTUNES</h2>\r\n<p>\r\n	<br />\r\n	Для начала мы настроим iTunes на вашем компьютере. Поддержка Home Sharing появилась в iTunes 10.2 рекомендую убедится в актуальности вашего приложения</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/itunes-about.png\" style=\"width: 500px; height: 247px; \" /></p>\r\n<p>\r\n	iTunes обновлен до нужной версии и мы отправляемся в пункт меню &quot;<strong>Дополнительные</strong>&quot;, где находим пункт &quot;<strong>Включить Домашнюю Коллекцию</strong>&quot;.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/itunes-power-on-home-media.png\" style=\"width: 500px; height: 201px; \" /></p>\r\n<p>\r\n	Для работы с коллекцией вам понадобится <strong>Apple ID</strong>, при помощи которого вы логинитесь в App Store. Вводим его в нужное поле и iTunes к работе готов</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/itunes-home-media-login-apple-id.png\" style=\"width: 500px; height: 249px; \" /></p>\r\n<p>\r\n	Последовательность довольно простая, но для людей недавно знакомых с iTunes может показаться нетривиальной.</p>\r\n<h2>\r\n	НАСТРАИВАЕМ iPAD</h2>\r\n<p>\r\n	<br />\r\n	Так как у меня под рукой сейчас есть iPad c iOS 4.3 на борту, я покажу как его настроить. По такому же принципу вы сможете проделать эту операцию и в любом другом устройстве с iOS 4.3. Достаточно зайти в настройки устройства и в пункте iPod ввести свою учетную запись Apple ID о которой я уже упоминал ранее.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ipod-home-media-tools.png\" style=\"width: 500px; height: 133px; \" /></p>\r\n<p>\r\n	Теперь осталось зайти в приложение iPod и в разделе медиатек выбрать свою iTunes библиотеку.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ipod-home-media.png\" style=\"width: 500px; height: 312px; \" /></p>\r\n<p>\r\n	Вот собственно и вся инструкция. Теперь у вас появилась возможность посмотреть видео с компьютера без помощи сторонних приложений и послушать любимую музыку не синхронизируя ваше iOS-устройство с &quot;Большим Братом&quot;.</p>\r\n',NULL,3,1),(195,'ВИДЕО: Песня про iPad 2 в исполнении Джобса','<p>\r\n	Презентация 2 марта принесла много интересного и стала предметом многочисленных шуток и пародий. Вслед за веселым промо-видео, о котором мы недавно писали, появилась музыкальная версия презентации. На нем Стив Джобс в песне рассказывает о новом iPad и делится впечатлениями.</p>\r\n<p class=\"rtecenter\">\r\n	<iframe allowfullscreen=\"\" frameborder=\"0\" height=\"300\" src=\"http://www.youtube.com/embed/O3aHCZqUvfo?rel=0\" title=\"YouTube video player\" width=\"500\"></iframe></p>\r\n<p>\r\n	Довольно неплохой к','<p>\r\n	Презентация 2 марта принесла много интересного и стала предметом многочисленных шуток и пародий. Вслед за веселым промо-видео, о котором мы недавно писали, появилась музыкальная версия презентации. На нем Стив Джобс в песне рассказывает о новом iPad и делится впечатлениями.</p>\r\n<p class=\"rtecenter\">\r\n	<iframe allowfullscreen=\"\" frameborder=\"0\" height=\"300\" src=\"http://www.youtube.com/embed/O3aHCZqUvfo?rel=0\" title=\"YouTube video player\" width=\"500\"></iframe></p>\r\n<p>\r\n	Довольно неплохой комикс, с хорошей музыкой. Приятно радует, что авторы использовали реальный голос Стива, слегка изменив тональность. Отличное веселое видео для выходного дня.</p>\r\n',NULL,3,1),(196,'ВИДЕО: Появился новый 3D движок для iOS','<p>\r\n	У новейшего игрового движка для мобильных платформ Unreal Engine появился конкурент. По крайней мере для iOS разработчиков появится возможность использовать новейший движок Supermono от Тэка Фанга. Создатель таких игр как Epic Win и MiniSquadron сообщил на GDC&#39;11, что работает над своим 3D-движком и он будет ЛЕГЕН...подожди...ДАРНЫМ!</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/Supermono_3D.png\" /></p>\r\n<p>\r\n	У Тэка скромная цель создать лучший трехмерный движок д','<p>\r\n	У новейшего игрового движка для мобильных платформ Unreal Engine появился конкурент. По крайней мере для iOS разработчиков появится возможность использовать новейший движок Supermono от Тэка Фанга. Создатель таких игр как Epic Win и MiniSquadron сообщил на GDC&#39;11, что работает над своим 3D-движком и он будет ЛЕГЕН...подожди...ДАРНЫМ!</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/Supermono_3D.png\" /></p>\r\n<p>\r\n	У Тэка скромная цель создать лучший трехмерный движок для iOS, который будет смотреться лучше Unreal Engine. Судя по его наработкам и демонстрационному видео - ему это удастся.</p>\r\n\r\n<h2>\r\n	ЧЕГО СТОИТ ОЖИДАТЬ</h2>\r\n<p>\r\n	<br />\r\n	Никто не скажет лучше о продукте, чем его создатель. Тэк Фанг: &quot;Отложенный рендеринг. Он может справиться с обработкой диффузионного и зеркального освещения, исходящего от 20 динамических и разноцветных источников света, одновременно с этим обрабатывая Блум и отражения окружающей среды в реальном времени.&quot;</p>\r\n<p class=\"rtecenter\">\r\n	<iframe allowfullscreen=\"\" frameborder=\"0\" height=\"300\" src=\"http://www.youtube.com/embed/NAMjlJn-3Mw?rel=0\" title=\"YouTube video player\" width=\"500\"></iframe></p>\r\n<p>\r\n	В поддержку будущего движка автор переименовал свою студию в Supermono Studios. Первая игра на нем выйдет уже этим летом, это будет нечто связанное с автомобилями, но не гонки. Возможно аркада или нечто другое.</p>\r\n',NULL,3,1),(197,'CDMA iPhone от Verizon разлочен под другие операторы','<p>\r\n	Появилась информация, что iPhone CDMA был успешно разлочен в США. Теперь есть возможность использовать смартфон в сетях других операторов без заключения контракта. В частностиб оператор Cricket c радостью разлочит ваш CDMA iPhone для доступа к собственной сети.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/unlock-cdma-iphone.png\" style=\"width: 500px; height: 300px; \" /></p>\r\n<p>\r\n	Оператор Cricket Wireless обладает абонентской базой в 5 млн пользователей и предоставляе','<p>\r\n	Появилась информация, что iPhone CDMA был успешно разлочен в США. Теперь есть возможность использовать смартфон в сетях других операторов без заключения контракта. В частностиб оператор Cricket c радостью разлочит ваш CDMA iPhone для доступа к собственной сети.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/unlock-cdma-iphone.png\" style=\"width: 500px; height: 300px; \" /></p>\r\n<p>\r\n	Оператор Cricket Wireless обладает абонентской базой в 5 млн пользователей и предоставляет очень интересные <a href=\"http://www.mycricket.com/paygo/prepaid-cell-phone-plans\">безлимитные тарифы</a>.</p>\r\n\r\n<h2>\r\n	ДОКАЗАТЕЛЬСТВА</h2>\r\n<p>\r\n	<br />\r\n	В качестве доказательства приводится видео, на котором показана работа iPhone от Verizon в сети Cricket и корректный ответ на звонки по служебным номерам оператора.</p>\r\n<p class=\"rtecenter\">\r\n	<iframe allowfullscreen=\"\" frameborder=\"0\" height=\"349\" src=\"http://www.youtube.com/embed/m05itSAI_z8?rel=0\" title=\"YouTube video player\" width=\"425\"></iframe></p>\r\n<p>\r\n	Данную услугу уже начали предлагать в Китае. Надеемся, что вскоре такие предложения появятся и у отечественных операторов CDMA связи.</p>\r\n',NULL,3,1),(198,'HTML5 vs Flash - победа за Apple','<p>\r\n	С самого появления iPhone в 2007 году слабым местом iOS (а тогда iPhone OS) была позиция Apple в отношении технологии Adobe Flash. Позиция была простая - пока технология будет источником проблем для пользователей - ее не будет в iOS. В пору, когда 9/10 всего видео в интернете было исключительно в Flash такие заявления вызывали смех у конкурентов и завистников.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/Flash-on-iPhone-4.png\" style=\"width: 500px; height: 259px; \" /><','<p>\r\n	С самого появления iPhone в 2007 году слабым местом iOS (а тогда iPhone OS) была позиция Apple в отношении технологии Adobe Flash. Позиция была простая - пока технология будет источником проблем для пользователей - ее не будет в iOS. В пору, когда 9/10 всего видео в интернете было исключительно в Flash такие заявления вызывали смех у конкурентов и завистников.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/Flash-on-iPhone-4.png\" style=\"width: 500px; height: 259px; \" /></p>\r\n<p>\r\n	На дворе 2011 год и ситуация изменилась. Стремительный рост популярности HTML5 среди разработчиков и довольно большое количество гаджетов, которые не поддерживают Flash делают свое дело. Основные потребители контента диктуют условия на рынке. И сейчас их требования просты и понятны: &quot;Даешь HTML5 вместо проприетарного Flash&quot;.</p>\r\n<p>\r\n	Но компания Adobe не была бы такой успешной, если бы не прислушивалась к требованиям пользователей. И вот на сцене появляется невиданная вещь - конвертер из Flash в HTML5+CSS3 <strong>Wallaby</strong>. Он позволит разработчику насладится удобством создания анимации в пакете Adobe и просто перевести результат в HTML5.</p>\r\n\r\n<h2>\r\n	КАК ЭТО ВЫГЛЯДИТ</h2>\r\n<p>\r\n	<br />\r\n	Выглядит этот процесс довольно тривиально. Впрочем, предоставляем вам самим убедиться в этом, посмотрев видео с презентации.</p>\r\n<p class=\"rtecenter\">\r\n	<iframe allowfullscreen=\"\" frameborder=\"0\" height=\"300\" src=\"http://www.youtube.com/embed/ryZP00_KhYE?rel=0\" title=\"YouTube video player\" width=\"500\"></iframe></p>\r\n<p>\r\n	Теперь у вашего iOS гаджета появится намного больше возможностей. Благодаря этой технологии разработчики легко смогут перевести свои проекты в HTML5 и дать возможность насладится ими владельцам гаджетов Apple.</p>\r\n<h2>\r\n	НЕМНОГО ЗЛОРАДСТВА</h2>\r\n<p>\r\n	В то время, как создатели одной из самых распространенных технологий интернета прогибаются под компанию Apple, &quot;убийца iPad&quot; от Motorola не сможет порадовать своих владельцев поддержкой Flash. Но и поддержкой HTML5 он порадовать их тоже <a href=\"http://www.sencha.com/blog/motorola-xoom-the-html5-developer-scorecard/\">не сможет.</a></p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/FLASH FAIL%2C Xoom Android Honeycomb Tablet.jpeg\" style=\"width: 500px; height: 352px; \" /></p>\r\n<p>\r\n	Надеюсь это временные трудности и у амбициозного противника появится возможность дать достойный ответ своему конкуренту.</p>\r\n',NULL,3,1),(199,'iOS 5 могут показать уже в середине апреля','<p>\r\n	Ровно неделя прошла с презентации iPad 2. На ней был показан планшет нового поколения и представлена iOS 4.3, которая уже давно тестировалась бетатестерами и разработчиками. Но многие ожидали несколько большего. Мы ждали хоть какой то ясности в отношении сервисов Mobile Me, которые <a href=\"http://macov.net/news/mobile-me-partly-free\"><strong>пропали из продажи</strong></a>. Возможно они станут частично бесплатными. Хотелось бы так же увидеть preview iOS5, которая должна появиться уже этим','<p>\r\n	Ровно неделя прошла с презентации iPad 2. На ней был показан планшет нового поколения и представлена iOS 4.3, которая уже давно тестировалась бетатестерами и разработчиками. Но многие ожидали несколько большего. Мы ждали хоть какой то ясности в отношении сервисов Mobile Me, которые <a href=\"http://macov.net/news/mobile-me-partly-free\"><strong>пропали из продажи</strong></a>. Возможно они станут частично бесплатными. Хотелось бы так же увидеть preview iOS5, которая должна появиться уже этим летом.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ios-05-april.jpeg\" style=\"width: 500px; height: 224px; \" /></p>\r\n<p>\r\n	И вот ровно через неделю после презентации появились слухи, что в середине апреля Apple проведет еще одну презентацию, посвященную Mobile Me и iOS 5 на которой многое прояснится. Возможна ситуация, как и в случае с первым поколением iPad, когда выход iOS 4.2 с долгожданной многозадачностью кардинально изменил представление покупателей о продукте. Не пойдет ли Apple на эту хитрость и со вторым поколением?</p>\r\n',NULL,3,1),(200,'ВИДЕО: Обзор 64GB модели iPhone 4','<p>\r\n	Несколько дней назад появилась информация о <strong>64</strong>GB модели iPhone 4, несколько экземпляров которой были замечены в продаже. Такое устройство было куплено и тщательно осмотрено. Действительно iPhone 4 и точно 64 GB.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/iphone-4-64gb.png\" style=\"width: 500px; height: 300px; \" /></p>\r\n<p>\r\n	Внешне устройство ничем не отличается от оригинала, за исключением отсутствия &quot;<strong>+</strong>&quot; и &quot;<strong>-<','<p>\r\n	Несколько дней назад появилась информация о <strong>64</strong>GB модели iPhone 4, несколько экземпляров которой были замечены в продаже. Такое устройство было куплено и тщательно осмотрено. Действительно iPhone 4 и точно 64 GB.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/iphone-4-64gb.png\" style=\"width: 500px; height: 300px; \" /></p>\r\n<p>\r\n	Внешне устройство ничем не отличается от оригинала, за исключением отсутствия &quot;<strong>+</strong>&quot; и &quot;<strong>-</strong>&quot; на кнопках изменения громкости и маркировки &quot;<strong>xxGB</strong>&quot;.</p>\r\n\r\n<h2>\r\n	ДОКАЗАТЕЛЬСТВО</h2>\r\n<p>\r\n	<br />\r\n	На видео показан экран настроек, на котором показано количество памяти, а так же зафиксирована разборка устройства.</p>\r\n<p class=\"rtecenter\">\r\n	<iframe allowfullscreen=\"\" frameborder=\"0\" height=\"300\" src=\"http://www.youtube.com/embed/OXrT7-qyXuE?rel=0\" title=\"YouTube video player\" width=\"500\"></iframe></p>\r\n<p>\r\n	Серийный номер устройства и косвенные данные указывают, что это один из прототипов iPhone 4, выпущенный в начале 2010 года, еще до официального старта продаж. Видимо инженеры Apple посчитали ненужным для пользователя такой объем памяти.</p>\r\n',NULL,3,1),(201,'ОФИЦИАЛЬНО: iOS 4.3 доступна для обновления + ССЫЛКИ','<p>\r\n	Официально iOS 4.3 сегодня стала доступна для всех пользователей iOS устройств. Новая версия принесет такие функции, как персональная точка доступа, AirPlay для сторонних приложений, новый мобильный Safari. Так же появится функция Home Sharing для iTunes, для подключения которой мы даже <a href=\"\"><strong>написали инструкцию</strong></a>.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/IOS-4-3-release.png\" style=\"width: 500px; height: 248px; \" /></p>\r\n<p>\r\n	Для тех, кто ','<p>\r\n	Официально iOS 4.3 сегодня стала доступна для всех пользователей iOS устройств. Новая версия принесет такие функции, как персональная точка доступа, AirPlay для сторонних приложений, новый мобильный Safari. Так же появится функция Home Sharing для iTunes, для подключения которой мы даже <a href=\"\"><strong>написали инструкцию</strong></a>.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/IOS-4-3-release.png\" style=\"width: 500px; height: 248px; \" /></p>\r\n<p>\r\n	Для тех, кто не имеет возможности обновится через iTunes прилагаются прямые ссылки на новые версии iOS.</p>\r\n<h2>\r\n	ССЫЛКИ</h2>\r\n<p>\r\n	<br />\r\n	<a href=\"http://appldnld.apple.com/iPhone4/041-0330.20110311.Cswe3/iPhone3,1_4.3_8F190_Restore.ipsw\"><strong>iPhone 4</strong></a></p>\r\n<p>\r\n	<a href=\"http://appldnld.apple.com/iPhone4/041-0328.20110311.Lkhy6/iPhone2,1_4.3_8F190_Restore.ipsw\"><strong>iPhone 3GS</strong></a></p>\r\n<p>\r\n	<a href=\"http://appldnld.apple.com/iPhone4/061-8366.20110311.Fr45t/iPod3,1_4.3_8F190_Restore.ipsw\"><strong>iPod Touch 3G</strong></a></p>\r\n<p>\r\n	<a href=\"http://appldnld.apple.com/iPhone4/061-9588.20110311.GtP7y/iPod4,1_4.3_8F190_Restore.ipsw\"><strong>iPod Touch 4G</strong></a></p>\r\n<p>\r\n	<a href=\"http://appldnld.apple.com/iPhone4/041-0329.20110311.Cvfre/iPad1,1_4.3_8F190_Restore.ipsw\"><strong>iPad</strong></a></p>\r\n<p>\r\n	<a href=\"http://appldnld.apple.com/AppleTV/061-9584.20110308.Cxdew/AppleTV2,1_4.3_8F191m_Restore.ipsw\"><strong>Apple TV</strong></a></p>\r\n<p>\r\n	Качайте на здоровье, обновляйтесь, делитесь с нами своими впечатлениями и наблюдениями.</p>\r\n',NULL,3,1),(202,'Unlock iPhone 4 пока невозможен','<p>\r\n	Сегодня в Твиттере Musclenerd из iPhone Dev team сообщил, что у них пока нет ни одной подходящей уязвимости для unlock iPhone 4 с baseband <strong>02.10.04</strong> и <strong>03.10.01</strong>. Надежда на даунгрейд baseband не оправдалась, так что пока нет даже предположений когда выйдет unlock для аппаратов с новыми версиями baseband.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/unlock-iphone-4-sorry.png\" style=\"width: 500px; height: 182px; \" /></p>\r\n<p>\r\n	Проблема с','<p>\r\n	Сегодня в Твиттере Musclenerd из iPhone Dev team сообщил, что у них пока нет ни одной подходящей уязвимости для unlock iPhone 4 с baseband <strong>02.10.04</strong> и <strong>03.10.01</strong>. Надежда на даунгрейд baseband не оправдалась, так что пока нет даже предположений когда выйдет unlock для аппаратов с новыми версиями baseband.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/unlock-iphone-4-sorry.png\" style=\"width: 500px; height: 182px; \" /></p>\r\n<p>\r\n	Проблема стала актуальной еще и в связи с выходом iOS 4.3. Настоятельно рекомендуется не обновляться сегодня и дождаться заветного unlock, чтобы не оказаться с большим iPod на руках.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/unlock-iphone-4-ios.png\" style=\"width: 500px; height: 189px; \" /></p>\r\n<p>\r\n	Мы же вам рекомендуем не морочить себе голову с американскими устройствами, а покупать отечественные или европейские iPhone - с ними нет никаких проблем. Сделать это можно <a href=\"http://macov.net/kupit-apple-iphone-4-3gs\"><strong>даже у нас</strong></a>, благо наши цены одни из самых низких.</p>\r\n',NULL,3,1),(203,'ВИДЕО: Garage Band для iPad','<p>\r\n	За день до назначенного времени в App Store появились обещанные iMovie и Garage Band для iPad. И если первый пока не сильно актуален, то планшетная версия популярного музыкального редактора вызывает ажиотаж.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/garage-band-logo.png\" style=\"width: 500px; height: 200px; \" /></p>\r\n<p>\r\n	Я тоже поддался искушению и побаловал себя интересным приложением. О своих ощущениях я расскажу в следующих статьях, а пока предлагаю вам посмотр','<p>\r\n	За день до назначенного времени в App Store появились обещанные iMovie и Garage Band для iPad. И если первый пока не сильно актуален, то планшетная версия популярного музыкального редактора вызывает ажиотаж.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/garage-band-logo.png\" style=\"width: 500px; height: 200px; \" /></p>\r\n<p>\r\n	Я тоже поддался искушению и побаловал себя интересным приложением. О своих ощущениях я расскажу в следующих статьях, а пока предлагаю вам посмотреть официальное видео, в котором показаны все основные функции Garage Band.</p>\r\n\r\n<h2>\r\n	ВИДЕОГИД</h2>\r\n<p>\r\n	<br />\r\n	Если вы увлеченный музыкант и всю жизнь мечтали о портативной версии большого Garage Band - это приложение явно для вас. Если же вы делаете только первые шаги в музыке или просто хотите развлечься - видео расскажет вам о возможностях и особенностях программы.</p>\r\n<p class=\"rtecenter\">\r\n	<iframe allowfullscreen=\"\" frameborder=\"0\" height=\"300\" src=\"http://www.youtube.com/embed/PsTaX8oZnvY\" title=\"YouTube video player\" width=\"500\"></iframe></p>\r\n<p>\r\n	Из приятного - инструменты для профессионалов и новичков, все красиво и хорошо работает. Из минусов - на первом iPad скорость работы несколько ниже.</p>\r\n',NULL,3,1),(204,'ВИДЕО: Safari в iPad 2 быстрее чем в iPad','<p>\r\n	Вчера официально начались продажи второго поколения iPad. Первые устройства уже успели попасть в руки экспериментаторам и журналистам. Пока счастливые владельцы наслаждаются скоростью работы, толщиной и весом, все остальные хотят узнать, на самом ли деле iPad 2 лучше первого.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ipad2-vs-ipad-safari.png\" /></p>\r\n<p>\r\n	Начнем со скорости работы браузера. Смотрите сами и сравнивайте.</p>\r\n','<p>\r\n	Вчера официально начались продажи второго поколения iPad. Первые устройства уже успели попасть в руки экспериментаторам и журналистам. Пока счастливые владельцы наслаждаются скоростью работы, толщиной и весом, все остальные хотят узнать, на самом ли деле iPad 2 лучше первого.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ipad2-vs-ipad-safari.png\" /></p>\r\n<p>\r\n	Начнем со скорости работы браузера. Смотрите сами и сравнивайте.</p>\r\n\r\n<h2>\r\n	ПАМЯТЬ РЕШАЕТ ВСЕ</h2>\r\n<p>\r\n	<br />\r\n	В качестве примера приводим видео, на котором сравнивают скорость работы обоих устройств на примере браузера. Авторы видео утверждают, что прирост производительности обеспечивает больший объем оперативной памяти в iPad 2. Как уже известно, он составляет 512 Mb против 256 в первом iPad.</p>\r\n<p class=\"rtecenter\">\r\n	<iframe allowfullscreen=\"\" frameborder=\"0\" height=\"300\" src=\"http://www.youtube.com/embed/ojRzPyuJFcU\" title=\"YouTube video player\" width=\"500\"></iframe></p>\r\n<p>\r\n	К авторам видео только один вопрос - работает ли первый iPad под управлением iOS 4.3, в которой работа браузера была существенно улучшена, так как на наших устройствах под 4.3 таких задержек воспроизвести не удалось.</p>\r\n',NULL,3,1),(205,'У iPad 2 найдены проблемы с ремонтопригодностью','<p>\r\n	Не прошло и двух часов, как специалисты iFixit <a href=\"http://www.ifixit.com/Teardown/iPad-2-Wi-Fi-Teardown/5071/1\"><strong>разобрали</strong></a> iPad второго поколения на запчасти. Осмотр выявил как положительные, так и отрицательные стороны нового планшета.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ipad2-display.jpeg\" style=\"width: 500px; height: 375px; \" /></p>\r\n<p>\r\n	Как всегда, процесс разборки запечатлен на фото и снабжен емкими комментариями специалистов</','<p>\r\n	Не прошло и двух часов, как специалисты iFixit <a href=\"http://www.ifixit.com/Teardown/iPad-2-Wi-Fi-Teardown/5071/1\"><strong>разобрали</strong></a> iPad второго поколения на запчасти. Осмотр выявил как положительные, так и отрицательные стороны нового планшета.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ipad2-display.jpeg\" style=\"width: 500px; height: 375px; \" /></p>\r\n<p>\r\n	Как всегда, процесс разборки запечатлен на фото и снабжен емкими комментариями специалистов</p>\r\n\r\n<h2>\r\n	ПОЛОЖИТЕЛЬНЫЕ СТОРОНЫ</h2>\r\n<p>\r\n	<br />\r\n	Невооруженным глазом заметно, что iPad стал тоньше. Теперь же имеется точная информация о каждом изменении внесенном инженерами Apple. Так стало известно, что чип Apple A5 произведен компанией Samsung и снабжен 512 Mb оперативной памяти.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ipad2-batareya.jpeg\" style=\"width: 500px; height: 375px; \" /></p>\r\n<p>\r\n	Сам же девайс стал тоньше, а вот емкость батареи практически не изменилась. Это говорит про оптимизацию работы программной части устройства, которую провели в Купертино.</p>\r\n<h2>\r\n	ОТРИЦАТЕЛЬНЫЕ СТОРОНЫ</h2>\r\n<p>\r\n	<br />\r\n	Но чем меньше толщина устройства, тем проще его сломать. Причем сломать его может не только пользователь, но и специалист сервисного центра. По словам ребят из iFixit для того чтобы разобрать iPad 2 необходимо снять переднюю панель, приклеенную к корпусу. Сделать это можно только с помощью специального теплового пистолета.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ipad-2-teplovoy-pistolet.jpg\" style=\"width: 500px; height: 337px; \" /></p>\r\n<p>\r\n	Специалисты отметили высокий риск повреждения дисплея из-за его толщины. Кроме того защитное стекло тоже стало тоньше, что несет риск нанести вред своему устройству прилагая меньше усилий.</p>\r\n',NULL,3,1),(206,'Обновление Safari, iPhoto и Aperture.','<p>&nbsp;</p>\r\n<div><span style=\"font-size:16px;\"><strong>Обновление Safari, iPhoto и Aperture.</strong></span></div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://cs309422.userapi.com/v309422523/5b61/PeM0kIMGVCU.jpg\" style=\"width: 580px; height: 302px; \"></div>\r\n<div>&nbsp;</div>\r\n<div>Кроме обновлений прошивок мобильной ОС, Apple выпустили обновления и для программ, работающих на маке. Safari обновилась до версии 6.0.2, iPhoto до версии 9.4.2 и Aperture до версии 3.4.2. Скачать обновления в','<p>&nbsp;</p>\r\n<div><span style=\"font-size:16px;\"><strong>Обновление Safari, iPhoto и Aperture.</strong></span></div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://cs309422.userapi.com/v309422523/5b61/PeM0kIMGVCU.jpg\" style=\"width: 580px; height: 302px; \"></div>\r\n<div>&nbsp;</div>\r\n<div>Кроме обновлений прошивок мобильной ОС, Apple выпустили обновления и для программ, работающих на маке. Safari обновилась до версии 6.0.2, iPhoto до версии 9.4.2 и Aperture до версии 3.4.2. Скачать обновления вы можете уже сейчас через Mac App Store, а Safari вы можете скачать еще и с сайта Apple. Изменения в обновлениях минимальные.</div>\r\n<div>&nbsp;</div>\r\n',NULL,3,1),(207,'Фил Шиллер подтвердил белый iPhone этой весной','<p>\r\n	Фил Шиллер подтвердил выход белого iPhone уже этой весной. Сделал он это просто, написал в Твиттере в ответ на вопрос о том, когда же появится белая версия смартфона.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/iphone-white.png\" style=\"width: 500px; height: 157px; \" /></p>\r\n<p class=\"rtecenter\">\r\n	&quot;Привет Эрик. Белый iPhone будет доступен уже этой весной (и он прекрасен!).&quot;</p>\r\n<p>\r\n	К вице-президенту по маркетингу компании Apple сразу же появилось огромно','<p>\r\n	Фил Шиллер подтвердил выход белого iPhone уже этой весной. Сделал он это просто, написал в Твиттере в ответ на вопрос о том, когда же появится белая версия смартфона.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/iphone-white.png\" style=\"width: 500px; height: 157px; \" /></p>\r\n<p class=\"rtecenter\">\r\n	&quot;Привет Эрик. Белый iPhone будет доступен уже этой весной (и он прекрасен!).&quot;</p>\r\n<p>\r\n	К вице-президенту по маркетингу компании Apple сразу же появилось огромное количество вопросов. Будет ли это четвертая модель белого цвета или же нас ждет iPhone 5 уже этой весной? Об этом господин Шиллер скромно умолчал.</p>\r\n',NULL,3,1),(208,'МНЕНИЕ: Почему мне нужен iPad 2','<p>\r\n	Вот и вышел iPad 2 и теперь выбор стал еще сложнее. Не беру во внимание желающих попробовать планшеты на Android - для них выбор еще сложнее. Правда в продаже еще ничего не появилось, но это уже не моя епархия.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ipad2-ipad-mnenie.png\" style=\"width: 500px; height: 249px; \" /></p>\r\n<p>\r\n	Что купить, iPad или iPad 2? Первый подешевел, а второй стал мощнее. Я расскажу как справился с этим выбором.</p>\r\n<h2>\r\n	МОЙ СОБСТВЕННЫЙ ОПЫ','<p>\r\n	Вот и вышел iPad 2 и теперь выбор стал еще сложнее. Не беру во внимание желающих попробовать планшеты на Android - для них выбор еще сложнее. Правда в продаже еще ничего не появилось, но это уже не моя епархия.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ipad2-ipad-mnenie.png\" style=\"width: 500px; height: 249px; \" /></p>\r\n<p>\r\n	Что купить, iPad или iPad 2? Первый подешевел, а второй стал мощнее. Я расскажу как справился с этим выбором.</p>\r\n<h2>\r\n	МОЙ СОБСТВЕННЫЙ ОПЫТ</h2>\r\n<p>\r\n	<br />\r\n	В тот момент, когда я впервые увидел iPad в руках Стива Джобса, я понял, что это именно то устройство, которое мне необходимо. Оно легкое, мобильное и позволяет мне делать все, что я обычно делаю за своим ноутбуком. Кроме работы, естественно. Работа у каждого своя, и кому то iPad действительно облегчил труд и повысил продуктивность. Для некоторых он стал корпоративной удавкой, которая заставляла работать даже вдали от рабочего места, в любое время дня и ночи.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/apple_ipad_1_0.jpg\" style=\"width: 500px; height: 291px; \" /></p>\r\n<p>\r\n	У меня iPad первого поколения появился в мае, через месяц после начала продаж. Официально у нас он еще не продавался, а неофициальные цены как раз упали до приемлемого уровня. Я не жалел о покупке ни дня, он верой и правдой служил мне все это время. Во всех поездках он был просто незаменим. Но настало время ему освободить место для своего последователя. Для меня вопрос выбора не стоит вовсе. Я уже понял, что хочу от этого устройства и что мне необходимо. Попытаюсь помочь и тем, кто пока сомневается.</p>\r\n<h2>\r\n	ВЫБОР</h2>\r\n<p>\r\n	Для начала стоит определится, нужен ли вам планшет. Это пожалуй самый трудный вопрос. Маркетологи Apple постарались на славу и продукт обладает огромным ВАУ потенциалом. Но на самом деле для большинства возможность удобной работы с почтой не означает, что он ей воспользуется. И так со всеми отличительными чертами планшета, кроме видео игр и социальных сетей. Так что если вы хотите повысить продуктивность при помощи iPad - самоконтроль отныне ваша вторая добродетель. Иначе ваше время будет съедаться на бесполезные забавы.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ipad-2-appstore.jpg\" style=\"width: 500px; height: 234px; \" /></p>\r\n<p>\r\n	Часто ли вы ездите или часто вы пользуетесь компьютером? Вы часто читаете книги или новости? iPad именно то, что вам нужно. Устройство предназначено именно для этих целей. Но он не заставит вас читать, ездить и смотреть. Он просто предоставляет возможность удобного поглощения контента. Заставить вас его поглощать он не сможет.</p>\r\n<h2>\r\n	ПЕРВЫЙ ИЛИ ВТОРОЙ</h2>\r\n<p>\r\n	Ну и главный вопрос - первое поколение или второе. Новый iPad стал легче и тоньше, мощнее и обзавелся двумя камерами. Но нужны ли вам эти новшества? Мне нужны. Я хочу планшет полегче с возможностью звонить по Face Time и работать с фотографиями документов. Зачастую для того, чтобы прикрепить к письму фото документа приходится делать снимок и использоваться Dropbox. Теперь не придется. Да и гиковская натура вынуждает меня обновится и заполучить новую игрушку в свое распоряжение.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ipad-2-face-time.jpg\" style=\"width: 500px; height: 222px; \" /></p>\r\n<p>\r\n	Обыкновенному пользователю я бы советовал сначала познакомится с устройством первого поколения. Для корпоративных нужд его вообще хватит с головой. В виду падения цен на него покупка становится довольно выгодным вложением. Главное дождаться окончательного снижения цены. Новые игры потребуют более мощной графики, но игры и работа мало совместимы. Ну а тем кому подойдет второй iPad ничего советовать не нужно. Они уже отложили деньги и ждут удобного момента для покупки. А вы к кому присоединитесь?</p>\r\n',NULL,3,1),(209,'ВИДЕО: Как взломать экраны на Times square при помощи iPhone','<p>\r\n	Интересное видео появилось на просторах интернета. На нем гражданин азиатской наружности взламывает видеоэкраны на Times square при помощи iPhone и самодельного ресивера. Это нужно увидеть.</p>\r\n<p class=\"rtecenter\">\r\n	<object height=\"300\" width=\"500\"><param name=\"movie\" value=\"http://www.youtube.com/v/s_HUYi9aVvI?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height','<p>\r\n	Интересное видео появилось на просторах интернета. На нем гражданин азиатской наружности взламывает видеоэкраны на Times square при помощи iPhone и самодельного ресивера. Это нужно увидеть.</p>\r\n<p class=\"rtecenter\">\r\n	<object height=\"300\" width=\"500\"><param name=\"movie\" value=\"http://www.youtube.com/v/s_HUYi9aVvI?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"300\" src=\"http://www.youtube.com/v/s_HUYi9aVvI?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"500\"></embed></object></p>\r\n<p>\r\n	Видео как подтверждение открытости iOS, на которой, пусть и не совсем официально, можно реализовать такие чудеса.</p>\r\n',NULL,3,1),(210,'ВИДЕО: 1984 Mac быстрее 2007 PC ','<p>\r\n	В погоне за характеристиками, номинальной производительностью, гигагерцами, гигабайтами, ядрами и дюймами некоторые упускают тот важный факт, что для пользователя наиболее важно. Прикрываясь удвоенными характеристиками мир персональных компьютеров развивался в маркетинговом, но никак не в техническом плане. Доказательством тому послужит это видео, на котором сравнивается скорость загрузки Mac 1984 года и ноутбук 2007 года выпуска.</p>\r\n<p class=\"rtecenter\">\r\n	<object height=\"300\" width=\"50','<p>\r\n	В погоне за характеристиками, номинальной производительностью, гигагерцами, гигабайтами, ядрами и дюймами некоторые упускают тот важный факт, что для пользователя наиболее важно. Прикрываясь удвоенными характеристиками мир персональных компьютеров развивался в маркетинговом, но никак не в техническом плане. Доказательством тому послужит это видео, на котором сравнивается скорость загрузки Mac 1984 года и ноутбук 2007 года выпуска.</p>\r\n<p class=\"rtecenter\">\r\n	<object height=\"300\" width=\"500\"><param name=\"movie\" value=\"http://www.youtube.com/v/tmRJ649ICPU?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"300\" src=\"http://www.youtube.com/v/tmRJ649ICPU?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"500\"></embed></object></p>\r\n<p>\r\n	2007 PC vs. 1984 Mac<br />\r\n	1.73 GHz vs. 8 Mhz<br />\r\n	2 GB vs. 128 KB<br />\r\n	SATA vs Floppy<br />\r\n	32 Bit vs. 16 Bit<br />\r\n	OS на HD vs. OS на floppy и в ROM<br />\r\n	Еще Загружается vs. Готов к Роботе</p>\r\n<p>\r\n	Еще три года назад загрузка ноутбука длилась 2-3 минуты. Сейчас ситуация начала исправляться, но разве благодаря характеристикам? Нет, просто прогресс постепенно выходит из области маркетинга и преимуществам на бумаге уже никто не верит.</p>\r\n\r\n<p>\r\n	Ну а в качестве дополнения скажу, что схожая ситуация сейчас происходит с устройствами на Android. Ядра, гигагерцы и гигабайты против Just Works. Открытая OS, всевозможные неиспользуемые разъемы и ненужные технологии против нормального времени работы устройств. Надеюсь они тоже исправятся.</p>\r\n',NULL,3,1),(211,'ВИДЕО: Дополненная реальность на iPad 2','<p>\r\n	Ни для кого не секрет, что появление камеры в iPad 2 было вызвано не столько тягой пользователей к фотографии и видеосъемке, сколько желание Apple дать возможность пользователю использовать все преимущества камеры в различных приложениях. Одним из способов использования будет использование приложений Дополненной Реальности.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ipad-2-AR.png\" style=\"width: 500px; height: 279px; \" /></p>\r\n<p>\r\n	Дополненная реальность - это возмо','<p>\r\n	Ни для кого не секрет, что появление камеры в iPad 2 было вызвано не столько тягой пользователей к фотографии и видеосъемке, сколько желание Apple дать возможность пользователю использовать все преимущества камеры в различных приложениях. Одним из способов использования будет использование приложений Дополненной Реальности.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ipad-2-AR.png\" style=\"width: 500px; height: 279px; \" /></p>\r\n<p>\r\n	Дополненная реальность - это возможность дополнять реальные объекты виртуальными элементами. То есть изображение, транслируемое с камеры устройства дополняется различными полезными элементами. Например, наведя камеру на картину, вы увидите рядом с ней название и имя ее автора.</p>\r\n\r\n<h2>\r\n	ДОПОЛНЕННАЯ РЕАЛЬНОСТЬ И iPAD 2</h2>\r\n<p>\r\n	<br />\r\n	Так как в iPad 2 помимо камеры имеется еще и гироскоп в связке с GPS-модулем и компасом, то возможности дополненной реальности многократно возрастают. Один из разработчиков приложений для дополненной реальности уже подготовил демонстрационный ролик, который я и предлагаю вам посмотреть.</p>\r\n<p class=\"rtecenter\">\r\n	<object height=\"300\" width=\"500\"><param name=\"movie\" value=\"http://www.youtube.com/v/9txpbfDbq6I?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"300\" src=\"http://www.youtube.com/v/9txpbfDbq6I?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"500\"></embed></object></p>\r\n<p>\r\n	Для iPhone 4, обладающего такими же характеристиками, уже существует ряд полезных приложений подобного рода. Но на большом экране поместится куда больше полезной информации. Навигация в пространстве теперь станет намного удобнее. Жаль, что отечественные разработчики в этом плане несколько отстают от западных коллег.</p>\r\n',NULL,3,1),(212,'ВИДЕО: iPad 2 в блендере превращается в пыль','<p>\r\n	Компания Blendtec представила очередное умопомрачительное видео перемалывания гаджета в блендере собственного производства. Как это не печально, но планшет не устоял и превратился в горстку серебристой пыли. Авторы видео прекрасно спародировали момент презентации iPad, на которой якобы и была проведена эта ужасная процедура.</p>\r\n<p class=\"rtecenter\">\r\n	<object height=\"300\" width=\"500\"><param name=\"movie\" value=\"http://www.youtube.com/v/USiaeXzYkOE?fs=1&amp;hl=ru_RU\" /><param name=\"allowFu','<p>\r\n	Компания Blendtec представила очередное умопомрачительное видео перемалывания гаджета в блендере собственного производства. Как это не печально, но планшет не устоял и превратился в горстку серебристой пыли. Авторы видео прекрасно спародировали момент презентации iPad, на которой якобы и была проведена эта ужасная процедура.</p>\r\n<p class=\"rtecenter\">\r\n	<object height=\"300\" width=\"500\"><param name=\"movie\" value=\"http://www.youtube.com/v/USiaeXzYkOE?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"300\" src=\"http://www.youtube.com/v/USiaeXzYkOE?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"500\"></embed></object></p>\r\n<p>\r\n	Как видите, даже размер не помешал перемолоть продукт инженеров Apple. iPad стал тоньше и легче, но теперь его еще проще сломать. Но уж лучше сломать планшет, <a href=\"http://lenta.ru/news/2011/03/14/kiseleva/\"><strong>чем ногу</strong></a>.</p>\r\n',NULL,3,1),(213,'iPad 2 в роли магнитика на холодильник','<p>\r\n	Вы уже наверное слышали, что <a href=\"http://macov.net/shop/smart_cover_mc939\"><strong>Smart Cover</strong></a> для iPad 2 обладает 31 магнитом. Это позволяет ему надежно закрывать экран планшета, но существует и несколько другое применение. На форуме <a href=\"http://forums.macrumors.com/showthread.php?t=1118638\"><strong>Macrumors</strong></a> предложили крепить планшет к холодильнику, благо магниты позволяют. Выглядит это довольно экстравагантно.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" s','<p>\r\n	Вы уже наверное слышали, что <a href=\"http://macov.net/shop/smart_cover_mc939\"><strong>Smart Cover</strong></a> для iPad 2 обладает 31 магнитом. Это позволяет ему надежно закрывать экран планшета, но существует и несколько другое применение. На форуме <a href=\"http://forums.macrumors.com/showthread.php?t=1118638\"><strong>Macrumors</strong></a> предложили крепить планшет к холодильнику, благо магниты позволяют. Выглядит это довольно экстравагантно.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/holodilnik-ipad2.jpg\" style=\"width: 500px; height: 375px; \" /></p>\r\n<p>\r\n	Теперь домохозяйкам будет намного удобнее читать рецепты во время готовки, не боясь запачкать свой планшет. Да и при удачном расположении холодильника можно смотреть фильм во время еды, не занимая стол своим гаджетом.</p>\r\n',NULL,3,1),(214,'Весенний бандл от MacUpdate за $50','<p>\r\n	Во всех развитых и не очень странах покупать приложения для Mac - нормальная практика. Особенно, если они резко упали в цене. Если вы один из тех, кто готов платить за труд разработчиков, вашему вниманию предлагаю уникальное предложение <a href=\"https://www.mupromo.com/\"><strong>MacUpdate Promo Spring Bundle</strong></a> за $50. В его состав входит 11 приложений, каждое из которых стоит несколько больше той суммы, которую просят за весь пакет.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"','<p>\r\n	Во всех развитых и не очень странах покупать приложения для Mac - нормальная практика. Особенно, если они резко упали в цене. Если вы один из тех, кто готов платить за труд разработчиков, вашему вниманию предлагаю уникальное предложение <a href=\"https://www.mupromo.com/\"><strong>MacUpdate Promo Spring Bundle</strong></a> за $50. В его состав входит 11 приложений, каждое из которых стоит несколько больше той суммы, которую просят за весь пакет.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/macupdate-spring-bondle.png\" style=\"width: 634px; height: 358px; \" /></p>\r\n<p>\r\n	По настоящему интересным предложение делают <b>Parallels Desktop</b>, <b>1Password</b> и <b>TechTool Pro</b> которые просто незаменимы в хозяйстве, но цена которых превышает психологический барьер моментальной покупки. Теперь можно не раздумывая покупать.</p>\r\n',NULL,3,1),(215,'ВИДЕО: Майк Тайсон лечит от Angry Birds зависимости','<p>\r\n	Очень жизненное видео появилось в сети, на котором всемирно известный боксер Майк Тайсон рекламирует свою телепередачу на канале Animal Planet. В ней он обещает избавить каждого от Angry Birds зависимости всего за три простых этапа.</p>\r\n<p class=\"rtecenter\">\r\n	<object height=\"300\" width=\"500\"><param name=\"movie\" value=\"http://www.youtube.com/v/jC5lFMu2Qak?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscre','<p>\r\n	Очень жизненное видео появилось в сети, на котором всемирно известный боксер Майк Тайсон рекламирует свою телепередачу на канале Animal Planet. В ней он обещает избавить каждого от Angry Birds зависимости всего за три простых этапа.</p>\r\n<p class=\"rtecenter\">\r\n	<object height=\"300\" width=\"500\"><param name=\"movie\" value=\"http://www.youtube.com/v/jC5lFMu2Qak?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"300\" src=\"http://www.youtube.com/v/jC5lFMu2Qak?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"500\"></embed></object></p>\r\n<p>\r\n	Выглядит Майк очень мило и говорит очень вежливо. Но это не помешало ему избавить второго героя рекламного ролика от его iPad-зависимости всего за один этап.</p>\r\n',NULL,3,1),(216,'ВИДЕО: Order & Chaos аналог WoW на iPad 2','<h2>\r\n	GAMELOFT КОПИРУЕТ WOW</h2>\r\n<p>\r\n	<br />\r\n	Появился первый деморолик новой MMORPG от Gameloft. Игра странным образом очень похожа на знаменитый Word of Warkraft, правда называется Order &amp; Chaos и предназначена для планшета iPad 2 и, возможно, других iOS устройств. Как видно из ролика функционал несколько упрощен и внешний вид объектов несколько изменился. Но это все еще тот самый WoW, который фанаты игры узнают с первых секунд.</p>\r\n<p class=\"rtecenter\">\r\n	<object height=\"300\" width=\"','<h2>\r\n	GAMELOFT КОПИРУЕТ WOW</h2>\r\n<p>\r\n	<br />\r\n	Появился первый деморолик новой MMORPG от Gameloft. Игра странным образом очень похожа на знаменитый Word of Warkraft, правда называется Order &amp; Chaos и предназначена для планшета iPad 2 и, возможно, других iOS устройств. Как видно из ролика функционал несколько упрощен и внешний вид объектов несколько изменился. Но это все еще тот самый WoW, который фанаты игры узнают с первых секунд.</p>\r\n<p class=\"rtecenter\">\r\n	<object height=\"300\" width=\"500\"><param name=\"movie\" value=\"http://www.youtube.com/v/ql-iu98XGds?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"300\" src=\"http://www.youtube.com/v/ql-iu98XGds?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"500\"></embed></object></p>\r\n<p>\r\n	Такой беспрецедентный плагиат простителен компании, которая желает застолбить место на рынке. Быть первыми в таком прибыльном бизнесе как MMORPG стоит того, чтобы идти на конфликт с таким монстром игровой индустрии, как Близард.</p>\r\n\r\n<p>\r\n	Мое мнение по этому вопросу скорее отрицательное, чем нейтральное. И плагиат здесь не при чем. По сути &quot;овощи&quot; теперь станут намного мобильнее, ведь получат возможность играть во время передвижения. Раньше к использованию iPad во время работы относились снисходительно, ведь кроме мелких игрушек там есть огромное количество полезных приложений. Некоторые компании даже закупали их для своих сотрудников, некоторым я даже рекомендовал это сделать. Теперь же армия &quot;планктона&quot; получит в свои руки оружие массового убийства времени.</p>\r\n<p>\r\n	Gameloft выждал положенное время, пока люди массово не подсели на планшеты и готовится запустить вирус, который поразит целые офисы и предприятия. Менеджеры среднего звена, подверженные стадному инстинкту начнут массово заниматься делами гильдии вместо работы. Рейды клана бухгалтерии на замок отдела продаж, в общем полный разброд и шатание. Закончится это все конечно &quot;Родительским Контролем&quot; и падением продаж iPad 2 на корпоративном рынке. Как говорится &quot;Благими намерениями...&quot;</p>\r\n',NULL,3,1),(217,'Многофункциональная подставка для техники Apple поможет фан-бою','<h2>\r\n	ПОДСТАВКА ДЛЯ APPLE ФАН-БОЯ</h2>\r\n<p>\r\n	<br />\r\n	Настоящий Apple фан-бой должен обладать всем набором техники Apple. Но помимо iPad, iPod Classic и iPhone 4 ему явно не хватает удобной поставки для всего своего технопарка. И если раньше таких подставок не существовало в природе то теперь они появились.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/polyply12.jpg\" style=\"width: 500px; height: 334px; \" /></p>\r\n<p>\r\n	Удобная подставка thePolyply от Andrew Kim позволит удо','',NULL,3,1),(218,'ВИДЕО: Медленное уничтожение iPad 2','<p>\r\n	Для тех, кто уже успел обзавестись новеньким iPad 2 возможно будет интересно посмотреть, как будет выглядеть ваш планшет, если его расстрелять в упор. Для тех же, у кого пока еще нет его, появился повод увидеть, что произойдет с гаджетом в случае его разрушения и как это можно предотвратить.</p>\r\n<p class=\"rtecenter\">\r\n	<object height=\"390\" width=\"480\"><param name=\"movie\" value=\"http://www.youtube.com/v/dVc3yg8KN5Q?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param na','<p>\r\n	Для тех, кто уже успел обзавестись новеньким iPad 2 возможно будет интересно посмотреть, как будет выглядеть ваш планшет, если его расстрелять в упор. Для тех же, у кого пока еще нет его, появился повод увидеть, что произойдет с гаджетом в случае его разрушения и как это можно предотвратить.</p>\r\n<p class=\"rtecenter\">\r\n	<object height=\"390\" width=\"480\"><param name=\"movie\" value=\"http://www.youtube.com/v/dVc3yg8KN5Q?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/dVc3yg8KN5Q?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"480\"></embed></object></p>\r\n<p>\r\n	Это видео было снято компанией ZAGG, в качестве рекламы своей продукции, а именно защитных чехлов. Этим расстрелом они хотели показать, насколько уязвим новый iPad и как важна его защита. Не смотря на то, что девайс даже по внешнему виду кажется очень хрупким, спасибо за еще одно предупреждение.</p>\r\n',NULL,3,1),(219,'ВИДЕО: Маленький ребенок и iPad','<p>\r\n	Если у вас нет маленького ребенка и iPad, вы наверное и не знаете, как дети любят эти плоские устройства, полные игр и развлечений, которые можно трогать руками. Причем от возраста это абсолютно не зависит. Если даже коты научились играть на iPad, то дети уж подавно. К тому же они порой делают это с большим умением, чем старшее поколение, которое гордится своим отрицанием технологий. На видео типичный пример ребенка 21 века, который, уверен, многие могли лицезреть у себя дома.</p>\r\n<p clas','<p>\r\n	Если у вас нет маленького ребенка и iPad, вы наверное и не знаете, как дети любят эти плоские устройства, полные игр и развлечений, которые можно трогать руками. Причем от возраста это абсолютно не зависит. Если даже коты научились играть на iPad, то дети уж подавно. К тому же они порой делают это с большим умением, чем старшее поколение, которое гордится своим отрицанием технологий. На видео типичный пример ребенка 21 века, который, уверен, многие могли лицезреть у себя дома.</p>\r\n<p class=\"rtecenter\">\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/MGMsT4qNA-c?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/MGMsT4qNA-c?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	А еще на видео пример того, как инженеры Apple подходят к разработке продуктов. И стар и млад могут пользоваться этим продуктом абсолютно комфортно, не нуждаясь в посторонней помощи. Достаточно один раз показать, а дальше пальцы все сделают сами.</p>\r\n<p>\r\n	Интересно, а Android Honeycomb так же понятен детям как iOS или все пока еще не на столько хорошо?</p>\r\n',NULL,3,1),(220,'ВИДЕО: Smart Cover для iPad 1','<p>\r\n	<b>iPad 2</b> победоносно шагает по планете. И рука об руку с ним идет его верный спутник - магнитная обложка <b>Smart Cover</b>. А старший брат &quot;<b><i>просто iPad</i></b>&quot; видимо остался навеки без нее. Но не перевелись еще умельцы на нашей планете, и готов уже способ приладить чудесный защитный экран у iPad. Правда выглядит этот способ не очень эстетично и вряд ли получит распространение среди широких масс. Впрочем, смотрите видео, в нем рассказывают как совместить несовместимо','<p>\r\n	<b>iPad 2</b> победоносно шагает по планете. И рука об руку с ним идет его верный спутник - магнитная обложка <b>Smart Cover</b>. А старший брат &quot;<b><i>просто iPad</i></b>&quot; видимо остался навеки без нее. Но не перевелись еще умельцы на нашей планете, и готов уже способ приладить чудесный защитный экран у iPad. Правда выглядит этот способ не очень эстетично и вряд ли получит распространение среди широких масс. Впрочем, смотрите видео, в нем рассказывают как совместить несовместимое при помощи подручных средств.</p>\r\n<p class=\"rtecenter\">\r\n	<iframe frameborder=\"0\" height=\"280\" src=\"http://player.vimeo.com/video/21247198\" width=\"500\"></iframe></p>\r\n<p>\r\n	Как вы увидели, способ этот далек от реального применения и разработан лишь для того, чтобы доказать возможность использования Smart Cover. Но размер iPad явно не соответствует размеру обложки, магниты выглядят несколько нелепо да и отсутствие датчика приближения в первом iPad сводит на нет все преимущества &quot;суперобложки&quot;.</p>\r\n<p>\r\n	Если вы все же решили использовать <b>Smart Cover</b>, советую использовать их совместно с <a href=\"http://macov.net\"><b>iPad 2</b></a>, которые уже можно купить у нас.</p>\r\n',NULL,3,1),(221,'Официальное видео: HEiTMi - Отпусти ( При поддержке www.Macov.net )','<p>\r\n	В этой новости мы не будем рассказывать вам о новинках из мира Apple, вроде уже полюбившегося всем <a href=\"http://macov.net/kupit-apple-ipad-2\">iPad 2</a>, или iPhone 5. Сегодня Macov.net рады представить вам молодую, но достойную внимания группу <strong>HEiTMi</strong>. Ребята играют достаточно тяжелую музыку, но в последнее время стали более доступны для массового слушателя. Совместно с творческой группой Macov.net мы сняли для них клип на акустическую версию песни &quot;Отпусти&quot;, ','<p>\r\n	В этой новости мы не будем рассказывать вам о новинках из мира Apple, вроде уже полюбившегося всем <a href=\"http://macov.net/kupit-apple-ipad-2\">iPad 2</a>, или iPhone 5. Сегодня Macov.net рады представить вам молодую, но достойную внимания группу <strong>HEiTMi</strong>. Ребята играют достаточно тяжелую музыку, но в последнее время стали более доступны для массового слушателя. Совместно с творческой группой Macov.net мы сняли для них клип на акустическую версию песни &quot;Отпусти&quot;, который и хотим предоставить вашему вниманию.</p>\r\n<p class=\"rtecenter\">\r\n	<iframe allowfullscreen=\"\" frameborder=\"0\" height=\"390\" src=\"http://www.youtube.com/embed/0IOG7W8b_LI\" title=\"YouTube video player\" width=\"640\"></iframe></p>\r\n<p class=\"rtecenter\">\r\n	&nbsp;</p>\r\n<p>\r\n	Видео снято на Canon 7d, обьективы 24mm-105mm/f4 L и 50mm/f1.4 + много света, немного вспышек и капля энтузиазма.<br />\r\n	Рекомендуем&nbsp;смотреть в 1080p.</p>\r\n',NULL,3,1),(222,'ОФИЦИАЛЬНО: Цены на iPad 2 в Европе','<p>\r\n	Сегодня Apple официально объявила цены на Европейские <b>iPad 2</b>, которые поступят в продажу 25 марта. Не смотря на катаклизмы в Японии и небывалый ажиотаж в США планшеты поступят в срок.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ipad-prices.jpg\" style=\"width: 500px; height: 196px; \" /></p>\r\n<p>\r\n	Цены на новое поколение отличаются на 20 &euro; в меньшую сторону, что не может не радовать.<br />\r\n	<br />\r\n	iPad 2 Wi-Fi 16 Gb - 479 &euro;<br />\r\n	iPad 2 Wi-Fi 32 G','<p>\r\n	Сегодня Apple официально объявила цены на Европейские <b>iPad 2</b>, которые поступят в продажу 25 марта. Не смотря на катаклизмы в Японии и небывалый ажиотаж в США планшеты поступят в срок.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ipad-prices.jpg\" style=\"width: 500px; height: 196px; \" /></p>\r\n<p>\r\n	Цены на новое поколение отличаются на 20 &euro; в меньшую сторону, что не может не радовать.<br />\r\n	<br />\r\n	iPad 2 Wi-Fi 16 Gb - 479 &euro;<br />\r\n	iPad 2 Wi-Fi 32 Gb - 579 &euro;<br />\r\n	iPad 2 Wi-Fi 64 Gb - 679 &euro;<br />\r\n	<br />\r\n	Версии с 3G:<br />\r\n	<br />\r\n	iPad 2 Wi-Fi + 3G 16 Gb - 599 &euro;<br />\r\n	iPad 2 Wi-Fi + 3G 32 Gb - 699 &euro;<br />\r\n	iPad 2 Wi-Fi + 3G 64 Gb - 779 &euro;<br />\r\n	<br />\r\n	Напомним, что в стоимость устройства не входят налоги, так что учитывайте это при покупке.</p>\r\n',NULL,3,1),(223,'ВИДЕО: Неординарная реклама SonyEricsson','<p>\r\n	Совсем недавно на рынке появился очередной Android-смартфон. На сей раз это изделие от SonyEricsson, игровой смартфон Xperia. Но известность свою он получил не только благодаря формфактору &quot;смартфон-с-джойстиком&quot; но и благодаря необычной рекламной компании, которая скорее отталкивает, чем притягивает. Смотрите сами.</p>\r\n<p class=\"rtecenter\">\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/pt2LMfeJiw4?fs=1&amp;hl=ru_RU\" /><param name=\"allowF','<p>\r\n	Совсем недавно на рынке появился очередной Android-смартфон. На сей раз это изделие от SonyEricsson, игровой смартфон Xperia. Но известность свою он получил не только благодаря формфактору &quot;смартфон-с-джойстиком&quot; но и благодаря необычной рекламной компании, которая скорее отталкивает, чем притягивает. Смотрите сами.</p>\r\n<p class=\"rtecenter\">\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/pt2LMfeJiw4?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/pt2LMfeJiw4?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	И это только один из многих веселых роликов, рассказывающих о преимуществах нового смартфона в шутливой форме. Главная героиня ругается и перевоплощается в гнома, разговаривает сама с собой и бывает одержима дьяволом. В общем, полный набор</p>\r\n<p class=\"rtecenter\">\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/k8kiGSY6jqc?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/k8kiGSY6jqc?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	Эти и другие видео вы можете посмотреть на официальном <a href=\"http://www.youtube.com/user/sonyericsson\"><strong>канале SonyEricsson</strong></a> на YouTube.</p>\r\n<p>\r\n	От себя добавлю, что после просмотра такого рода рекламы, начинаешь совсем по другому смотреть на видео, снятое Apple в поддержку своих продуктов. Да что тут говорить, даже кампания &quot;I&#39;m a Mac, and I&#39;m PC&quot; выглядит просто детским утренником, по сравнению с Кристен и ее выходками.</p>\r\n',NULL,3,1),(224,'ВИДЕО: Everyday - ежедневный автопортрет','<p>\r\n	Представляю вашему вниманию <a href=\"http://itunes.apple.com/us/app/everyday/id398081659\"><strong>Everyday</strong></a>, приложение для создание ежедневных автопортретов. Оно напомнит вам, что необходимо сфотографировать себя и так будет каждый день, пока вы не наберете достаточно снимков для Time Laps видео. Если вы хотите запечатлеть продолжительный момент вашей жизни - приложение именно то, что вам нужно.</p>\r\n<p class=\"rtecenter\">\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" ','<p>\r\n	Представляю вашему вниманию <a href=\"http://itunes.apple.com/us/app/everyday/id398081659\"><strong>Everyday</strong></a>, приложение для создание ежедневных автопортретов. Оно напомнит вам, что необходимо сфотографировать себя и так будет каждый день, пока вы не наберете достаточно снимков для Time Laps видео. Если вы хотите запечатлеть продолжительный момент вашей жизни - приложение именно то, что вам нужно.</p>\r\n<p class=\"rtecenter\">\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/kVGIiQmChFg?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/kVGIiQmChFg?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	Стоит оно всего $1.99 но за эти деньги вы впоследствии получите море удовольствия. Пример этого вы можете посмотреть на следующем видео.</p>\r\n<p class=\"rtecenter\">\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/XaBhFGmkiVU?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/XaBhFGmkiVU?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	Надеюсь это приложение получит приложение и вскоре появится на <a href=\"http://macov.net\"><strong>iPad 2</strong></a>, чтобы легко отредактировать его в iMovie и показать друзьям. Впрочем, социальные функции приложения и сейчас на высоте, благо интеграция в Twitter и Facebook присутствует.</p>\r\n',NULL,3,1),(225,'ВИДЕО: Жириновский рассуждает про iPad','<p>\r\n	Совсем недавно скандальный политик Владимир Вольфович Жириновский признался в интервью каналу Россия 24, что не может пользоваться iPad, о чем очень жалеет. Признаться, я был удивлен такой лестной характеристике i-гаджетов из уст политика, который никогда не стеснялся высказывать свою критическую точку зрения.</p>\r\n<p class=\"rtecenter\">\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/ghFIbAiw3O0?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value','<p>\r\n	Совсем недавно скандальный политик Владимир Вольфович Жириновский признался в интервью каналу Россия 24, что не может пользоваться iPad, о чем очень жалеет. Признаться, я был удивлен такой лестной характеристике i-гаджетов из уст политика, который никогда не стеснялся высказывать свою критическую точку зрения.</p>\r\n<p class=\"rtecenter\">\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/ghFIbAiw3O0?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/ghFIbAiw3O0?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	На видео Владимир Вольфович рассуждает о пользе новых технологий для политики и молодых лидерах, которые эти технологии используют. Вот с этим мы абсолютно согласны.</p>\r\n',NULL,3,1),(226,'Сводная таблица мировых цен на iPad 2','<p>\r\n	Если вы решили отправится в путешествие по миру и планируете с собой привезти новенький iPad 2, то вам пригодится сводная таблица мировых цен на эти устройства. Из нее видно, что самым выгодным местом покупки является Новая Зеландия, с ценой в 409 &euro; за самую младшую модель. Так же выгодно отличаются цены в Великобритании, они несколько ниже <a href=\"http://macov.net/news/euro-ipad-2-price\"><strong>общеевропейских</strong></a>.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/defau','<p>\r\n	Если вы решили отправится в путешествие по миру и планируете с собой привезти новенький iPad 2, то вам пригодится сводная таблица мировых цен на эти устройства. Из нее видно, что самым выгодным местом покупки является Новая Зеландия, с ценой в 409 &euro; за самую младшую модель. Так же выгодно отличаются цены в Великобритании, они несколько ниже <a href=\"http://macov.net/news/euro-ipad-2-price\"><strong>общеевропейских</strong></a>.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ipad2-price-euro.jpg\" style=\"width: 500px; height: 404px; \" /></p>\r\n<p>\r\n	Впрочем, если судьба занесет вас в скандинавские страны, то вас ожидают самые высокие цены в Европе. Мы же рекомендуем подождать, ведь старт официальных продаж еще не означает, что во всех магазинах их будет достаточное количество. Ажиотаж довольно велик, и если цель вашей поездки заполучить долгожданный iPad 2 - не исключено, что вы вернетесь с пустыми руками.</p>\r\n',NULL,3,1),(227,'ВИДЕО: Деревянный конкурент Smart Cover для iPad 2','<p>\r\n	И недели не прошло с тех пор, как суперобложку для iPad 2 <a href=\"http://macov.net/news/smart-cover-ipad-1\"><b>научились</b></a> крепить к первому iPad. И теперь у Smart Cover появился настоящий конкурент, который не уступает оригиналу по функциональности, но в некоторых моментах даже опережает его. Вашему вниманию я представляю <a href=\"http://www.miniot.com\">Miniot Cover</a> - обложку для iPad 2 c магнитным креплением, выполненную из натурального дерева.</p>\r\n<p class=\"rtecenter\">\r\n	<b>','<p>\r\n	И недели не прошло с тех пор, как суперобложку для iPad 2 <a href=\"http://macov.net/news/smart-cover-ipad-1\"><b>научились</b></a> крепить к первому iPad. И теперь у Smart Cover появился настоящий конкурент, который не уступает оригиналу по функциональности, но в некоторых моментах даже опережает его. Вашему вниманию я представляю <a href=\"http://www.miniot.com\">Miniot Cover</a> - обложку для iPad 2 c магнитным креплением, выполненную из натурального дерева.</p>\r\n<p class=\"rtecenter\">\r\n	<b><object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/4SI6K-k_rBo?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/4SI6K-k_rBo?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></b></p>\r\n<p>\r\n	Обложка в точности повторяет контуры планшета, защищая его экран и может выступать в качестве удобной подставки. Стоит такое чудо всего 50 &euro;, что на 19 &euro; дешевле оригинального кожаного Smart Cover в Европе. Своим подходом он чем то напоминает <a href=\"http://www.iwool.ru/index.htm\">iWool</a> для первого iPad, возможно своей любовью к натуральным материалам.</p>\r\n',NULL,3,1),(228,'ВИДЕО: Проверка стекла iPad 2 на прочность','<p>\r\n	Для того, чтобы узнать, насколько прочным является стекло нового iPad 2 по сравнению с планшетом прошлого поколения их необходимо попытаться сломать. Чтобы не пришлось разламывать свой гаджет напополам, предлагаю вам посмотреть это видео, на котором специалисты из iFixYouri.com показательно надругались над экранами обоих планшетов. Цель они преследовали самую благую - выяснить насколько хрупким стал экран нового iPad, после того, как стал тоньше.</p>\r\n<p class=\"rtecenter\">\r\n	<object height','<p>\r\n	Для того, чтобы узнать, насколько прочным является стекло нового iPad 2 по сравнению с планшетом прошлого поколения их необходимо попытаться сломать. Чтобы не пришлось разламывать свой гаджет напополам, предлагаю вам посмотреть это видео, на котором специалисты из iFixYouri.com показательно надругались над экранами обоих планшетов. Цель они преследовали самую благую - выяснить насколько хрупким стал экран нового iPad, после того, как стал тоньше.</p>\r\n<p class=\"rtecenter\">\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/r4vvQq7BpiE?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/r4vvQq7BpiE?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	Как показал эксперимент, экран iPad 2 стал намного крепче экрана своего предшественника, не смотря на свою тонкость. Там, где экран устройства первого поколения разлетался на части, новичок оставался целым, ломалось только сенсорное покрытие. В целом экран стал очень гибким и пластичным. Хотя интересно было бы узнать, как он отреагирует на фронтальный удар - самое распространение гаджетов с сенсорными дисплеями.</p>\r\n',NULL,3,1),(229,'ССЫЛКИ: iOS 4.3.1 доступна для загрузки','<p>\r\n	Обновление iOS 4.3.1 готово, и доступно для загрузки в iTunes. Список обновленного крайне мал. Помимо ожидаемого обновления безопасности так же были исправлены технические неполадки, которые были обнаружены после обновления iOS 4.3.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/update-info-ios-431.png\" style=\"width: 500px; height: 308px; \" /></p>\r\n<p>\r\n	Для тех, кто не желает ждать несколько часов для загрузки iOS из iTunes, мы предлагаем вам прямые ссылки на свежую ве','<p>\r\n	Обновление iOS 4.3.1 готово, и доступно для загрузки в iTunes. Список обновленного крайне мал. Помимо ожидаемого обновления безопасности так же были исправлены технические неполадки, которые были обнаружены после обновления iOS 4.3.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/update-info-ios-431.png\" style=\"width: 500px; height: 308px; \" /></p>\r\n<p>\r\n	Для тех, кто не желает ждать несколько часов для загрузки iOS из iTunes, мы предлагаем вам прямые ссылки на свежую версию прошивки для вашего iДевайса.</p>\r\n<p>\r\n	<a href=\"http://appldnld.apple.com/iPhone4/041-0549.20110325.ZxP8u/iPhone2,1_4.3.1_8G4_Restore.ipsw\">iPhone 3GS</a></p>\r\n<p>\r\n	<a href=\"http://appldnld.apple.com/iPhone4/041-0551.20110325.Aw2Dr/iPhone3,1_4.3.1_8G4_Restore.ipsw\">iPhone 4 (GSM)</a></p>\r\n<p>\r\n	<a href=\"http://appldnld.apple.com/iPhone4/041-0550.20110325.Zsw6y/iPad1,1_4.3.1_8G4_Restore.ipsw\">iPad</a></p>\r\n<p>\r\n	<a href=\"http://appldnld.apple.com/iPhone4/041-0556.20110325.64rfg/iPad2,1_4.3.1_8G4_Restore.ipsw\">iPad 2</a> (так же <a href=\"http://appldnld.apple.com/iPhone4/041-0557.20110325.Jy76y/iPad2,2_4.3.1_8G4_Restore.ipsw\">iPad 2,2</a> и <a href=\"http://appldnld.apple.com/iPhone4/041-0544.20110325.Dew3r/iPad2,3_4.3.1_8G4_Restore.ipsw\">iPad 2,3</a>)</p>\r\n<p>\r\n	<a href=\"http://appldnld.apple.com/iPhone4/041-0552.20110325.Yt67u/iPod3,1_4.3.1_8G4_Restore.ipsw\">iPod touch 3G</a></p>\r\n<p>\r\n	<a href=\"http://appldnld.apple.com/iPhone4/041-0545.20110325.We3Rt/iPod4,1_4.3.1_8G4_Restore.ipsw\">iPod touch 4G</a></p>\r\n<p>\r\n	Напомню, что для обновления устройства скачанной прошивкой, вам необходимо зажать клавишу &quot;<strong>option</strong>&quot; и кликнуть по &quot;Восстановить&quot; в панели информации устройства в iTunes.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/vosstanovl-ios.png\" style=\"width: 500px; height: 100px; \" /></p>\r\n<p>\r\n	Вам останется выбрать нужный файл iOS и обновление начнется.</p>\r\n',NULL,3,1),(230,'WWDC пройдет с 6 по 10 июня','<p>\r\n	Стало известно время проведения WWDC&#39;11 - всемирной конференции i-разработчиков, которая проходит каждый год в калифорнии. Важной эта пятидневная конференция является не только для разработчиков, которые съезжаются со всего мира, чтобы показать свои наработки и посмотреть на чужие. Так же на WWDC показывают новые и долгожданные продукты Apple. В 2009 году это был Mac OS X 10.6 Snow Leopard, в 2010 - iPhone 4. В этом году будут презентовать Mac OS X Lion и возможно, покажут iOS 5 b iPho','<p>\r\n	Стало известно время проведения WWDC&#39;11 - всемирной конференции i-разработчиков, которая проходит каждый год в калифорнии. Важной эта пятидневная конференция является не только для разработчиков, которые съезжаются со всего мира, чтобы показать свои наработки и посмотреть на чужие. Так же на WWDC показывают новые и долгожданные продукты Apple. В 2009 году это был Mac OS X 10.6 Snow Leopard, в 2010 - iPhone 4. В этом году будут презентовать Mac OS X Lion и возможно, покажут iOS 5 b iPhone пятого поколения.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/wwdc-2011-2011-03-28.jpg\" style=\"width: 500px; height: 274px; \" /></p>\r\n<p>\r\n	Итак, с 6 по 10 июня в калифорнии будут представлены новые продукты компании Apple, а так же сторонних разработчиков, которые производят программное обеспечение для Mac OS и iOS. Не пропустите.</p>\r\n',NULL,3,1),(231,'ВИДЕО: Пластилиновый iPad и его возможности','<p>\r\n	Продолжаем тему интересных видео. На сей раз попался пластилиновый iPad, точнее его точная копия. На видео схематически показаны основные плюсы планшета в доступной форме. Выглядит это достаточно мило и точно заслуживает внимания.</p>\r\n<p class=\"rtecenter\">\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/9AB3caO7Acc?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen','<p>\r\n	Продолжаем тему интересных видео. На сей раз попался пластилиновый iPad, точнее его точная копия. На видео схематически показаны основные плюсы планшета в доступной форме. Выглядит это достаточно мило и точно заслуживает внимания.</p>\r\n<p class=\"rtecenter\">\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/9AB3caO7Acc?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/9AB3caO7Acc?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	Помимо этой инсталляции для Британской Школы Дизайна (Москва), у Светланы Шохановой накопилось довольно много интересных работ. Если вам интересно - милости просим на ее <a href=\"http://dribbble.com/wzwz\">страницу</a>.</p>\r\n',NULL,3,1),(232,'ВИДЕО: Концепт-трансформер для Windows Phone 7','<p>\r\n	Американские промышленные дизайнеры решили выбрать путь, альтернативный пути Джонатана Эйва из Apple. Разрабатывая свой концепт устройства на Windows Phone 7 они решили собрать все то, от чего решил отказаться выдающийся британский дизайнер. А именно они решили создать максимально сложное устройство с огромным количеством сомнительно полезных функций.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/wp7-concept.png\" style=\"width: 500px; height: 273px; \" /></p>\r\n<p>\r\n	В пи','<p>\r\n	Американские промышленные дизайнеры решили выбрать путь, альтернативный пути Джонатана Эйва из Apple. Разрабатывая свой концепт устройства на Windows Phone 7 они решили собрать все то, от чего решил отказаться выдающийся британский дизайнер. А именно они решили создать максимально сложное устройство с огромным количеством сомнительно полезных функций.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/wp7-concept.png\" style=\"width: 500px; height: 273px; \" /></p>\r\n<p>\r\n	В пику приевшейся &quot;айфоноподобной&quot; форме моноблока с сенсорным экраном, они предлагают устройство аж с тремя подвижными элементами. Помимо самой раскладывающейся конструкции, максимально защищающей экран и предоставляющей возможность кнопочного управления гаджетом, так же присутствует выдвигающаяся клавиатура.</p>\r\n\r\n<h2>\r\n	КАК ЭТО ВЫГЛЯДИТ</h2>\r\n<p>\r\n	<br />\r\n	И если наличие 21 настраиваемой кнопки на внутренней стороне &quot;створок&quot; еще можно объяснить удобством управления в играх, то зачем нужна выдвигающаяся клавиатура не совсем ясно. И это при наличии вполне достойной экранной клавиатуры. Впрочем - смотрите сами на видео все доходчиво показано.</p>\r\n<p class=\"rtecenter\">\r\n	<iframe frameborder=\"0\" height=\"250\" src=\"http://player.vimeo.com/video/21583051\" width=\"500\"></iframe></p>\r\n<p>\r\n	Концепт от <a href=\"http://www.schultzeworks.com/triple-flip-cell-phone/\">SchultzeWORKS</a> должен иметь miniHDMI и miniUSB порты, широкоформатный дисплеем с разрешением 2000х1000 пикселей и сдвоенную камеру, способную снимать 3D-контент. Впрочем, мне во всей этой конструкции понравилась только сама идея раскладного сенсорного телефона с защищенным экраном. Ну и &quot;мультикнопка&quot;, которая позволяет его раскрывать, с таким ностальгическим для фанатов фильма &quot;Матрица&quot;, звуком.</p>\r\n',NULL,3,1),(233,'JBL Onbeat - новая акустика для iPad 2','<p>\r\n	Сегодня стало известно, что уже апреле 2011 года в продажу поступит новая акустическая система от JBL. Новое творение акустических гуру будет называться OnBeat и представляет собой небольшую Док-станцию со стандартным разъемом, которая обеспечит отличный звук на все 360 градусов вокруг себя.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/JBL-OnBeat-1_0.png\" style=\"width: 500px; height: 300px; \" /></p>\r\n<p>\r\n	OnBeat появится во всех авторизированных точках продаж Apple у','<p>\r\n	Сегодня стало известно, что уже апреле 2011 года в продажу поступит новая акустическая система от JBL. Новое творение акустических гуру будет называться OnBeat и представляет собой небольшую Док-станцию со стандартным разъемом, которая обеспечит отличный звук на все 360 градусов вокруг себя.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/JBL-OnBeat-1_0.png\" style=\"width: 500px; height: 300px; \" /></p>\r\n<p>\r\n	OnBeat появится во всех авторизированных точках продаж Apple уже в апреле. Ориентировочная цена в США $150 без налогов. Напомню, что эти акустические системы продаются&nbsp;<a href=\"http://macov.net/akusitcheskie-sistemi\"><strong>у нас</strong></a>.</p>\r\n\r\n<h2>\r\n	КАК ВЫГЛЯДИТ</h2>\r\n<p>\r\n	<br />\r\n	Внешний вид устройства традиционно необычный. Собственно, смотрите сами.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/JBL-OnBeat-2.png\" style=\"width: 500px; height: 381px; \" /></p>\r\n<p>\r\n	Практически вся поверхность OnBeat состоит из динамиков. В их окружении и будет находится гаджет на специальном постаменте.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/JBL-OnBeat-3.png\" style=\"width: 500px; height: 381px; \" /></p>\r\n<p>\r\n	С обратной стороны устройства имеется USB порт и видео-выход. Акустическая система это не просто &quot;колонки&quot; для i-девайса, а полноценная Док-станция с прекрасным звуком.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/JBL-OnBeat-4.png\" style=\"width: 500px; height: 411px; \" /></p>\r\n<p>\r\n	Гаджет от JBL сможет работать как с новым iPad 2, так и с вашим iPhone или iPod, которые получат совершенно новое звучание.</p>\r\n',NULL,3,1),(234,'ВИДЕО: iPad 2 спускающийся с небес','<p>\r\n	Итальянский ресурс <a href=\"\"><strong>Apple Lounge</strong></a> на сей раз порадовал интересным видео распаковки новенького <a href=\"http://macov.net\"><strong>iPad 2</strong></a>. К этому делу ребята подошли творчески, и процесс этот скорее напоминал представление иллюзиониста-любителя, чем сухой гиковский монолог.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ipad-from-the-sky.png\" /></p>\r\n<p>\r\n	Владельцы iPad ничего нового для себя не увидят, ролик несет скорее худож','<p>\r\n	Итальянский ресурс <a href=\"\"><strong>Apple Lounge</strong></a> на сей раз порадовал интересным видео распаковки новенького <a href=\"http://macov.net\"><strong>iPad 2</strong></a>. К этому делу ребята подошли творчески, и процесс этот скорее напоминал представление иллюзиониста-любителя, чем сухой гиковский монолог.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/ipad-from-the-sky.png\" /></p>\r\n<p>\r\n	Владельцы iPad ничего нового для себя не увидят, ролик несет скорее художественную ценность.</p>\r\n\r\n<h2>\r\n	И ПРИШЕЛ iPAD...</h2>\r\n<p>\r\n	<br />\r\n	В самом начале еще не было ясно, что нам предстоит увидеть. Но когда планшета не оказалось на его привычном месте в коробке наступает интрига. Ну а после появления банана из коробки все еще сильнее запутывается.</p>\r\n<p class=\"rtecenter\">\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/X2BBDrwnCNo?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/X2BBDrwnCNo?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	Когда была извлечена бутылка, мне в голову пришла мысль, что герой ролика сейчас начнет готовить себе обед. Но снизошедший с небес iPad 2 вернул все на свои места. Не было сказано ни одного слова, но все и так понятно. Очень интересное видео и правильный подход.</p>\r\n',NULL,3,1),(235,'ВИДЕО: Angry Birds снимутся в кино','<p>\r\n	Совсем недавно вышел на экраны мультфильм Rio (в России с 7 Апреля) , рассказывающий о нелегкой судьбе птиц в Рио-де-Жанейро. Интересен он тем, что для рекламы его создатели используют самое популярное мобильное приложение - игру Angry Birds. По этому поводу разработчики из Rovio даже выпустили одноименное приложение <a href=\"http://itunes.apple.com/ru/app/angry-birds-rio-hd/id420636551?mt=8\">Angry Birds Rio</a> для мобильных устройств и даже Mac OS.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"','<p>\r\n	Совсем недавно вышел на экраны мультфильм Rio (в России с 7 Апреля) , рассказывающий о нелегкой судьбе птиц в Рио-де-Жанейро. Интересен он тем, что для рекламы его создатели используют самое популярное мобильное приложение - игру Angry Birds. По этому поводу разработчики из Rovio даже выпустили одноименное приложение <a href=\"http://itunes.apple.com/ru/app/angry-birds-rio-hd/id420636551?mt=8\">Angry Birds Rio</a> для мобильных устройств и даже Mac OS.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/angry-birds-movie.png\" /></p>\r\n<p>\r\n	На волне этого киноуспеха ребята из <a href=\"http:// http://roosterteeth.com/\">Roosterteeth</a> даже сняли трейлер будущего фильма о самих злых птицах и войне за украденные яйца и демократию.</p>\r\n\r\n<p class=\"rtecenter\">\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/2matH4B9bTo?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/2matH4B9bTo?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	Впрочем, трейлер это юмористический и реального фильма с таким сюжетом мы не увидим. А жаль.</p>\r\n',NULL,3,1),(236,'ВИДЕО: Появился клиент Vimeo для iOS','<p>\r\n	Вчера в App Store появился клиент для работы с видеохостингом Vimeo - минималистичным аналогом YouTube. Для тех, кто не знает, Vimeo знаменит своей поддержкой html5 и тем, что разрешает размещать только видео снятое пользователем. То есть, никакой рекламы и прочего мусора, которым пестрит YouTube.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/vimeo-ios-release.png\" style=\"width: 500px; height: 232px; \" /></p>\r\n<p>\r\n	Теперь пользоваться этим сервисом в iOS можно с помощ','<p>\r\n	Вчера в App Store появился клиент для работы с видеохостингом Vimeo - минималистичным аналогом YouTube. Для тех, кто не знает, Vimeo знаменит своей поддержкой html5 и тем, что разрешает размещать только видео снятое пользователем. То есть, никакой рекламы и прочего мусора, которым пестрит YouTube.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/vimeo-ios-release.png\" style=\"width: 500px; height: 232px; \" /></p>\r\n<p>\r\n	Теперь пользоваться этим сервисом в iOS можно с помощью нативного приложения. Для того, чтобы ознакомится с его функциями - смотрим видео.</p>\r\n\r\n<h2>\r\n	КАК РАБОТАЕТ VIMEO</h2>\r\n<p>\r\n	<br />\r\n	Приятной неожиданностью стал встроенный видеоредактор, который позволяет обрабатывать видео, прежде чем вы разместите его на сервисе. Так же полно различных удобных функций, некоторые из которых можно увидеть на видео.</p>\r\n<p class=\"rtecenter\">\r\n	<iframe frameborder=\"0\" height=\"281\" src=\"http://player.vimeo.com/video/21648733\" width=\"500\"></iframe></p>\r\n<p>\r\n	Ну что же, если вы все еще не стали известным кинорежиссером, у вас появилась такая возможность. При помощи iPhone и клиента для <noindex><a href=\"http://vimeo.com\">Vimeo</a></noindex> вы можете снимать понравившиеся вам сюжеты и делиться ими с друзьями. Приложение абсолютно бесплатное и уже находится в <noindex><a href=\"http://itunes.apple.com/app/vimeo/id425194759#\">App Store</a></noindex></p>\r\n',NULL,3,1),(237,'ВИДЕО: Photoshop для iOS получит поддержку слоев ','<p>\r\n	Если вы знаете, что такое Photoshop (да кто же этого не знает) и являетесь счастливым обладателем iPad, то эта новость вас порадует. Сегодня, на конференции Photoshop World 2011 была продемонстрирована новая версия PS Express - бесплатного редактора изображений для iPad.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/photoshop-for-ipad.jpg\" style=\"width: 500px; height: 227px; \" /></p>\r\n<p>\r\n	Новая версия получила долгожданную поддержку слоев, которую компания Adobe обещ','<p>\r\n	Если вы знаете, что такое Photoshop (да кто же этого не знает) и являетесь счастливым обладателем iPad, то эта новость вас порадует. Сегодня, на конференции Photoshop World 2011 была продемонстрирована новая версия PS Express - бесплатного редактора изображений для iPad.</p>\r\n<p class=\"rtecenter\">\r\n	<img alt=\"\" src=\"/sites/default/files/photoshop-for-ipad.jpg\" style=\"width: 500px; height: 227px; \" /></p>\r\n<p>\r\n	Новая версия получила долгожданную поддержку слоев, которую компания Adobe обещала добавить в этом году. Похоже, они сдерживают свои обещания.</p>\r\n\r\n<h2>\r\n	КАК ЭТО ВЫГЛЯДИТ</h2>\r\n<p>\r\n	<br />\r\n	Работа со слоями выглядит довольно интересно - для устройства с сенсорным управлением была разработана специальная система управления. Более подробно это можно увидеть на видео.</p>\r\n<p class=\"rtecenter\">\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/9LoCY3XfajU?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/9LoCY3XfajU?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	От себя добавлю, что мобильная версия Photoshop, несмотря на все свои функции, пока не может конкурировать со своим десктопным аналогом. PS Express служит скорее инструментом быстрого редактирования личных фотографий и вряд ли сгодится кому в профессиональной деятельности. Но то же самое когда то говорили и про iPad, так что все относительно.</p>\r\n',NULL,3,1),(238,'Scroll Reverser - доступна еще одна функция Mac OS Lion ','<p>\r\n	Для тех кто еще не знает - одной из отличительных особенностей Mac OS Lion стал скроллинг &quot;в обратную сторону&quot;, так же как на iPad. Если вы управляете своим маком не с помощью сенсорного дисплея, а по старинке трекпадом да мышкой - вас ожидает незабываемый экспириенс. Для тех, кто лишен возможности опробовать эту функцию в Mac OS Lion, есть возможность попробовать &quot;обратный скрол&quot; в действии на своем простом или снежном Leopard.</p>\r\n<p class=\"rtecenter\" style=\" text-al','<p>\r\n	Для тех кто еще не знает - одной из отличительных особенностей Mac OS Lion стал скроллинг &quot;в обратную сторону&quot;, так же как на iPad. Если вы управляете своим маком не с помощью сенсорного дисплея, а по старинке трекпадом да мышкой - вас ожидает незабываемый экспириенс. Для тех, кто лишен возможности опробовать эту функцию в Mac OS Lion, есть возможность попробовать &quot;обратный скрол&quot; в действии на своем простом или снежном Leopard.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/scrol-reverser.png\" style=\"width: 500px; height: 177px; \" /></p>\r\n<p>\r\n	Для этого вы можете воспользоваться утилитой <noindex><a href=\"http://blog.pilotmoon.com/post/4041089648/scroll-reverser-get-in-practice-for-lion\">Scroll Reverser</a></noindex>, чьи функции как раз и заключаются в изменении направления прокрутки. Если вы используете один из Magic-гаджетов или вы владелец MacBook - вы быстро приноровитесь к такому способу прокрутки. Со временем она даже будет казаться более естественной. Но пока живы кнопочные мыши - такая прокрутка только утомляет палец, который крутит колесико в обратную сторону.</p>\r\n',NULL,3,1),(239,'ВИДЕО: Управляй животными с iPhone','<p>\r\n	У вас есть домашний питомец? Тогда новое первоапрельское приложение от GameLoft специально для вас. Благодаря специальным разработкам ученых в области биоритмов животных, у вас появится возможность управлять своим питомцем при помощи ультразвука.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/iphone-cat-control.png\" style=\"width: 500px; height: 275px; \" /></p>\r\n<p>\r\n	Панель управления животным довольно проста. Стрелки направления и несколько','<p>\r\n	У вас есть домашний питомец? Тогда новое первоапрельское приложение от GameLoft специально для вас. Благодаря специальным разработкам ученых в области биоритмов животных, у вас появится возможность управлять своим питомцем при помощи ультразвука.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/iphone-cat-control.png\" style=\"width: 500px; height: 275px; \" /></p>\r\n<p>\r\n	Панель управления животным довольно проста. Стрелки направления и несколько функциональных кнопок заставят вашего четвероногого друга исполнять все ваши приказы. Как это выглядит - смотрите на видео.</p>\r\n\r\n<h2>\r\n	КАК ВЫГЛЯДИТ УПРАВЛЕНИЕ ЖИВОТНЫМ</h2>\r\n<p>\r\n	<br />\r\n	Выглядит это все довольно забавно. Вы отдаете приказ - животное подчиняется. Без разницы, будет ли это кот, собака или же аквариумная рыбка. Собственно, смотрите видео, там все показано.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/f0M30YnBhAw?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/f0M30YnBhAw?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	Самое главное - соблюдать технику безопасности при контроле за животным. Не доверяйте пульт управления некомпетентным или неподготовленным личностям - иначе быть беде. Но самое главное, что действие приложения заканчивается в ночь с 1 апреля на 2-е.</p>\r\n',NULL,3,1),(240,'ВИДЕО: Рентгеновская камера на iPad 2','<p>\r\n	Вы помните все эти поддельные сканеры для мобильных телефонов, которые позволяли смотреть на людей без одежды. Эра мошенничества прошла, но на смену ей пришла эра высоких технологий.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/ipad-2-x-ray-camera.png\" style=\"width: 500px; height: 274px; \" /></p>\r\n<p>\r\n	Теперь чтобы увидеть человека без одежды вам понадобятся лишь iPad 2, источник инфракрасного излучения и пищевая пленка. Что из этого полу','<p>\r\n	Вы помните все эти поддельные сканеры для мобильных телефонов, которые позволяли смотреть на людей без одежды. Эра мошенничества прошла, но на смену ей пришла эра высоких технологий.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/ipad-2-x-ray-camera.png\" style=\"width: 500px; height: 274px; \" /></p>\r\n<p>\r\n	Теперь чтобы увидеть человека без одежды вам понадобятся лишь iPad 2, источник инфракрасного излучения и пищевая пленка. Что из этого получится - смотрим на видео.</p>\r\n\r\n<h2>\r\n	КАК РАБОТАЕТ РЕНТГЕН</h2>\r\n<p>\r\n	<br />\r\n	Вся технология довольно проста - оборачиваем пищевой пленкой iPad 2, подсвечиваем объект инфракрасным фонариком и вуаля. Результат вы можете увидеть на видео.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/pFJxt8lZQjg?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/pFJxt8lZQjg?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	К счастью этот метод действует только на видео. В реальной жизни вы можете не опасаться людей, которые вас фотографируют на iPad. С первым апреля!</p>\r\n',NULL,3,1),(241,'Непривязанный Jailbrake iOS 4.3.1 [ссылки]','<p>\r\n	Вот и вышел, вот и дождались. Все любители бесплатных приложений или безумной кастомизации своих i-гаджетов могут насладится непривязанным джеилбрейком для iOS 4.3.1 которая вышла две недели назад. Одновременно стали доступными и <strong>PwnageTool</strong> и <strong>redsn0w</strong>, так что владельцы не залоченых устройств смело могут обновляться на более новую версию iOS.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/pwnagetool-screen.pn','<p>\r\n	Вот и вышел, вот и дождались. Все любители бесплатных приложений или безумной кастомизации своих i-гаджетов могут насладится непривязанным джеилбрейком для iOS 4.3.1 которая вышла две недели назад. Одновременно стали доступными и <strong>PwnageTool</strong> и <strong>redsn0w</strong>, так что владельцы не залоченых устройств смело могут обновляться на более новую версию iOS.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/pwnagetool-screen.png\" style=\"width: 500px; height: 440px; \" /></p>\r\n<p>\r\n	Напомним, что джеилбрейк позволяет расширить функционал вашего устройства. Если вы хотите разблокировать американский iPhone, одного джеилбрейка вам будет недостаточно.</p>\r\n\r\n<h2>\r\n	ДЖЕИЛБРЕЙК</h2>\r\n<p>\r\n	<br />\r\n	Для тех, кто просто хочет получить возможность установки пиратских или &quot;нелегальных&quot; приложений достаточно будет использовать <strong>redsn0w 0.9.6rc9</strong>, которая доступна для <noindex><a href=\"https://sites.google.com/a/iphone-dev.com/files/home/redsn0w_win_0.9.6rc9.zip?attredirects=0&amp;d=1\"><strong>Windows</strong></a></noindex> и <noindex><a href=\"https://sites.google.com/a/iphone-dev.com/files/home/redsn0w_mac_0.9.6rc9.zip?attredirects=0&amp;d=1\"><strong>Mac OS</strong></a></noindex> и позволит быстро взломать ваш девайс.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/redsn0w-screen.png\" /></p>\r\n<p>\r\n	Для тех, кому показана установка кастомной прошивки, следует использовать <noindex><a href=\"http://torrents.thepiratebay.org/6293151/PwnageTool_4.3.dmg.6293151.TPB.torrent\"><strong>PwnageTool 4.3</strong></a></noindex> и детально ознакомится с инструкцией к ней, которая размещена в блоге <noindex><a href=\"http://blog.iphone-dev.org/\"><strong>Dev-Team</strong></a></noindex>. Заранее предупреждаю, что для успешного джеилбрейка вам понадобится оригинальный файл iOS 4.3.1, который вы можете взять <a href=\"http://macov.net/news/ios-4-3-1-update-download\"><strong>здесь</strong></a>. И печальная новость - джеилбрейк для <a href=\"http://macov.net\">iPad 2</a> пока недоступен.</p>\r\n',NULL,3,1),(242,'Музыкальные каверы на iPad от Ben Powler [видео] ','<p>\r\n	Немного утреннего позитива в исполнении Бена Паулера. Этот музыкант-любитель известен тем, что делает кавер версии на музыкальные произведения. Но делает их оригинальным способом.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/ben-powler-ipad-music.png\" style=\"width: 500px; height: 182px; \" /></p>\r\n<p>\r\n	Для своего творчества он уже второй раз использовал только iPad и около 10 музыкальных приложений, которые каждый может найти в App Store. ','<p>\r\n	Немного утреннего позитива в исполнении Бена Паулера. Этот музыкант-любитель известен тем, что делает кавер версии на музыкальные произведения. Но делает их оригинальным способом.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/ben-powler-ipad-music.png\" style=\"width: 500px; height: 182px; \" /></p>\r\n<p>\r\n	Для своего творчества он уже второй раз использовал только iPad и около 10 музыкальных приложений, которые каждый может найти в App Store. Записывает все партии на видео и на выходе получается готовая композиция. И получается у него, надо заметить, очень даже неплохо.</p>\r\n\r\n<h2>\r\n	МУЗЫКА НА iPAD</h2>\r\n<p>\r\n	<br />\r\n	Последним записанным кавером стала <strong>Forget You</strong> (Cee Lo Green), ссылку на оригинал давать не будем, предлагаем вначале послушать кавер-версию.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/vUqOu7dM1Mc?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/vUqOu7dM1Mc?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	<br />\r\n	Прославился же Бэн благодаря своему первому iPad каверу на песню Кэти Пэрри - Teenage Dream. Работу молодого музыканта оценила и сама исполнительница, предлагаю вам последовать ее примеру.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/G8trXode5Z4?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/G8trXode5Z4?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	Интересный факт - свои музыкальные творения Бэн делал еще о появления Garage Band на iPad. С его появлением у него появилось еще больше возможностей для создания своих композиций.</p>\r\n',NULL,3,1),(243,'Поздравляем ORFO 2010 Lite для Mac OS с победой!','<p>\r\n	Как могли заметить наши читатели, последнее время в тексте наших новостей практически отсутствуют грамматические ошибки. Нет, мы не наняли редактора и вечерние курсы русского языка здесь тоже ни причем. Встроенная система проверки правописания поможет при работе с английским текстом, но уровень познаний в &quot;великом и могучем&quot; - просто беда. По этому мы начали использовать ORFO Lite - приложение для более профессиональной проверки грамматики и орфографии в Mac OS.</p>\r\n<p class=\"rt','<p>\r\n	Как могли заметить наши читатели, последнее время в тексте наших новостей практически отсутствуют грамматические ошибки. Нет, мы не наняли редактора и вечерние курсы русского языка здесь тоже ни причем. Встроенная система проверки правописания поможет при работе с английским текстом, но уровень познаний в &quot;великом и могучем&quot; - просто беда. По этому мы начали использовать ORFO Lite - приложение для более профессиональной проверки грамматики и орфографии в Mac OS.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/orfo-mini.png\" style=\"width: 500px; height: 300px; \" /></p>\r\n<p>\r\n	После нескольких месяцев продуктивного использования, я могу назвать ORFO приложением из списка &quot;Must Have&quot;. Это так мило, когда словарь знает милые сердцу гика &quot;девайс&quot; и &quot;гаджет&quot; и не выделяет их пугающей красной линией. Но ближе к поздравлениям.</p>\r\n\r\n<h2>\r\n	ПОЗДРАВЛЯЕМ С ПОБЕДОЙ!</h2>\r\n<p>\r\n	<br />\r\n	А поздравляем мы компанию &quot;Информатик&quot; с тем, что по итогам премии &laquo;Софт года &ndash; 2010&raquo; ОРФО 2010 lite для систем Mac OS X получила звание &laquo;Инновация года&raquo;. Победителей в восьми номинациях определяли пользователи портала <strong>Mail.Ru</strong>, а в дополнительных номинациях лауреаты были выбраны партнерами Премии. Звание &laquo;Инновация года&raquo; присуждено программе партнером премии компанией <strong>Mirapolis.ru</strong>, крупным разработчиком информационных систем и решений для электронного обучения.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/orfo-2010-big.png\" style=\"width: 500px; height: 300px; \" /></p>\r\n<p>\r\n	На сегодняшний день ОРФО 2010 Lite &ndash; это единственная программа, проверяющая грамматику и стиль русского языка в Мас OS! Программа включает в себя также усовершенствованную проверку орфографии русского и английского языков. ОРФО 2010 Lite для Mac OS доступна во всех приложениях, поддерживающих системную функцию &ldquo;Правописание&rdquo; и осуществляет проверку текста по словарю, выдачу подсказок, проверку грамматики в соответствии с правилами русского языка.</p>\r\n<p>\r\n	Демо-версию программы можно скачать <a href=\"http://downloads.informatic.ru/ORFOMacLite/ORFO2010Lite.dmg\">ЗДЕСЬ</a><br />\r\n	Демо-ключ (действителен до 05.05.11): VEFE-EBHE-3A0K-B8TV</p>\r\n',NULL,3,1),(244,'ВИДЕО: Управляем самолетом с iPhone','<p>\r\n	В отличие от первоапрельской <a href=\"http://macov.net/news/iphone-animals-controller\">шутки</a>, возможность управлять летательным аппаратом вполне реальна. Это, конечно, не настоящий самолет (и не кот), но ощущения неповторимые. Сегодня, после &quot;переучета&quot; в Apple Store Online, в нем появился AR. Drone - пенопластовый &quot;самолет&quot; с четырьмя винтами, которым можно управлять с помощью любого iOS устройства.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt','<p>\r\n	В отличие от первоапрельской <a href=\"http://macov.net/news/iphone-animals-controller\">шутки</a>, возможность управлять летательным аппаратом вполне реальна. Это, конечно, не настоящий самолет (и не кот), но ощущения неповторимые. Сегодня, после &quot;переучета&quot; в Apple Store Online, в нем появился AR. Drone - пенопластовый &quot;самолет&quot; с четырьмя винтами, которым можно управлять с помощью любого iOS устройства.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/Parrot-ARDrone.jpg\" style=\"width: 500px; height: 212px; \" /></p>\r\n<p>\r\n	Стоит такой самолет в США около $300, впрочем, в отечественных магазинах он уже встречался. Но ввиду сомнительного способа использования, особого распространения не получила.</p>\r\n\r\n<h2>\r\n	КАК ЭТО ВЫГЛЯДИТ</h2>\r\n<p>\r\n	<br />\r\n	Предлагаем вам посмотреть, как выглядит процесс управления AR. Drone. Благодаря обилию различных датчиков, таких как гироскоп и акселерометр. Конструкция &quot;Дрона&quot; позволяет ему быть необычайно маневренным, вплоть до зависания в воздухе. Две камеры установленные на борту позволят вам точно контролировать положение &quot;беспилотника&quot;.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/CG5pqTWVahQ?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/CG5pqTWVahQ?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	Одним из вау-факторов является возможность баталий в дополненной реальности. Для этого вам понадобится два AR. Drone и давольно большая площадка. Из минусов - конструкция &quot;Дрона&quot; не позволит вам комфортно управлять им на открытой местности. Наиболее популярен он будет в офисе, ближе к концу дня.</p>\r\n',NULL,3,1),(245,'ВИДЕО: Групповые видеоконференции в Fring на iPhone ','<p>\r\n	Довольно популярный сервис для мобильной IP-телефонии и одновременно популярный IM клиент &quot;Fring&quot; представил бета-версию сервиса групповых видеозвонков. Теперь при помощи вашего iPhone вы можете организовывать видеоконференции со своими друзьями и коллегами.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/Fring_iPhone_GroupVideo_high_res_270x501-642x453-1.png\" style=\"width: 500px; height: 353px; \" /></p>\r\n<p>\r\n	Пусть сервис и не так','<p>\r\n	Довольно популярный сервис для мобильной IP-телефонии и одновременно популярный IM клиент &quot;Fring&quot; представил бета-версию сервиса групповых видеозвонков. Теперь при помощи вашего iPhone вы можете организовывать видеоконференции со своими друзьями и коллегами.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/Fring_iPhone_GroupVideo_high_res_270x501-642x453-1.png\" style=\"width: 500px; height: 353px; \" /></p>\r\n<p>\r\n	Пусть сервис и не так распространен как Skype и предназначен только для мобильных платформ - это серьезный задел в борьбе с основным конкурентом.</p>\r\n\r\n<h2>\r\n	КАК ЭТО ВЫГЛЯДИТ</h2>\r\n<p>\r\n	<br />\r\n	Небольшого экрана iPhone вполне достаточно для разговора с 3 абонентами, если вам действительно важно их увидеть. На мой взгляд, групповые видеоконференции являются больше маркетинговым ходом, чем реально необходимым сервисом. Но выглядит интересно.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/hIcJHqYpktA?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/hIcJHqYpktA?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	Для получения бета-версии Fring вам достаточно заполнить простую форму, которую вы найдете в <noindex><a href=\"http://www.fring.com/blog/?p=2857\">Fring-блоге</a></noindex>. От себя же замечу, что десктопной версии приложения не хватает намного больше, чем видеозвонков. Ну да разработчикам виднее.</p>\r\n',NULL,3,1),(246,'Новые патенты Apple для iPhone и iPad','<p>\r\n	Сегодня ресурс <noindex><a href=\"http://www.patentlyapple.com/\">patentlyapple</a></noindex> поведал о двух интересных патентах, которые получила Apple. Эти патенты касаются iOS устройств и предполагают масштабные функциональные изменения как внешние, так и технические.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/2iphone-patenten-1.png\" style=\"width: 500px; height: 300px; \" /></p>\r\n<p>\r\n	Первый патент был выдан на использование гибридного ','<p>\r\n	Сегодня ресурс <noindex><a href=\"http://www.patentlyapple.com/\">patentlyapple</a></noindex> поведал о двух интересных патентах, которые получила Apple. Эти патенты касаются iOS устройств и предполагают масштабные функциональные изменения как внешние, так и технические.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/2iphone-patenten-1.png\" style=\"width: 500px; height: 300px; \" /></p>\r\n<p>\r\n	Первый патент был выдан на использование гибридного &quot;экрана электронная бумага + LCD&quot;. Второй патент закрепляет права Apple использовать корпус мобильного устройства как вспомогательный дисплей. Давайте поближе познакомимся с этими технологиями.</p>\r\n\r\n<h2>\r\n	ЭЛЕКТРОННАЯ БУМАГА</h2>\r\n<p>\r\n	<br />\r\n	Суть этого патента заключается в том, что экран мобильного девайса будет состоять из нескольких слоев. В самом низу будет располагаться традиционный LCD, над ним будет слой полупрозрачной &quot;электронной бумаги&quot;, а поверх этого сенсорная панель.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/iphone-patent-Dynamic-Hybrid-Display.jpg\" style=\"width: 500px; height: 282px; \" /></p>\r\n<p>\r\n	Эта технология позволит компенсировать недостаток работы i-гаджетов при ярком свете и существенно снизит энергопотребление экрана. Apple запатентовала именно технологию, которая позволит корректно совмещать работу обоих слоев для достижения максимального эффекта. С появлением этой технологии, например, на iPad, электронным читалкам будет намного труднее с ним конкурировать.</p>\r\n<h2>\r\n	УМНАЯ ЛИЦЕВАЯ ПАНЕЛЬ</h2>\r\n<p>\r\n	<br />\r\n	Второй патент предлагает более рационально использовать лицевую панель мобильного устройства. В отличие от распространенного размещения сенсорных кнопок инженеры из Купертино пошли по другому пути. Они решили использовать &quot;люминесцентные индикаторы&quot; которые одновременно являются и экранами и кнопками. В зависимости от приложения и ориентации устройства они смогут выполнять различные функции.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/iphone-patenp-smart-bezel.jpg\" style=\"width: 500px; height: 240px; \" /></p>\r\n<p>\r\n	Так одним из ожидаемых способов использования - кнопки управления в играх. Как бы не было удобно сенсорное управление на экране, кнопки за его пределами будут удобнее. Особенно, если вне этого приложения они выполняют другие функции или вообще не видны. Особенно полезны данные индикаторы будут в iPad, с его широкой рамкой вокруг экрана, которую можно будет теперь тоже использовать.</p>\r\n<p>\r\n	Но все эти технологии мы если и увидим, то не раньше, чем выйдет третье поколение планшетов и пятое поколение iPad.</p>\r\n',NULL,3,1),(247,'Вышло обновление Redsn0w rc11 [ссылки]','<p>\r\n	Утилита для джеилбрейка iOS 4.3.1 о выходе которой мы недавно писали, получила небольшое обновление. В версии rc11 были внесены незначительные технические исправления, а так же появилась возможность выбора анимированной загрузки iOS устройства.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/jailbrake.png\" style=\"width: 500px; height: 300px; \" /></p>\r\n<p>\r\n	В список разблокируемых устройств по прежнему не входит iPad 2 и CDMA iPhone. Но джеил','<p>\r\n	Утилита для джеилбрейка iOS 4.3.1 о выходе которой мы недавно писали, получила небольшое обновление. В версии rc11 были внесены незначительные технические исправления, а так же появилась возможность выбора анимированной загрузки iOS устройства.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/jailbrake.png\" style=\"width: 500px; height: 300px; \" /></p>\r\n<p>\r\n	В список разблокируемых устройств по прежнему не входит iPad 2 и CDMA iPhone. Но джеилбрейк по прежнему является непривязанным. Вполне возможно, что с выходом обновления iOS 4.3.2 данный способ джеилбрейка перестанет действовать.</p>\r\n\r\n<h2>\r\n	СКАЧАТЬ Redsn0w 0.9.6rc11</h2>\r\n<p>\r\n	<br />\r\n	А вот и прямые ссылки:</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	Redsn0w 0.9.6rc11 для <noindex><a href=\"https://sites.google.com/a/iphone-dev.com/files/home/redsn0w_mac_0.9.6rc11.zip?attredirects=0&amp;d=1\">MAC</a></noindex> или <noindex><a href=\"https://sites.google.com/a/iphone-dev.com/files/home/redsn0w_win_0.9.6rc11.zip?attredirects=0&amp;d=1\">WINDOWS</a></noindex></p>\r\n',NULL,3,1),(248,'ВИДЕО: Шар для боулинга, падающий на iPad ','<p>\r\n	Даже если вы не играете в боулинг, но являетесь обладателем iPad, следующая информация будет вам полезна. Компания <noindex><a href=\"http://g-form.com/\">G-FORM</a></noindex>, производитель защитной амуниции, разработала сверохзащищенный и очень легкий чехол, который выдерживает даже падение шара для боулинга.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/gslave.jpg\" /></p>\r\n<p>\r\n	В подтверждение этому компания записала несколько видео, на к','<p>\r\n	Даже если вы не играете в боулинг, но являетесь обладателем iPad, следующая информация будет вам полезна. Компания <noindex><a href=\"http://g-form.com/\">G-FORM</a></noindex>, производитель защитной амуниции, разработала сверохзащищенный и очень легкий чехол, который выдерживает даже падение шара для боулинга.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/gslave.jpg\" /></p>\r\n<p>\r\n	В подтверждение этому компания записала несколько видео, на которых iPad в их защитном чехле действительно выдерживает этот удар. Более того, он остается полностью работоспособным.</p>\r\n\r\n<h2>\r\n	НЕ СМЕРТЕЛЬНЫЙ УДАР</h2>\r\n<p>\r\n	<br />\r\n	Вот так выглядит эксперимент с падающим шаром.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<object height=\"390\" width=\"480\"><param name=\"movie\" value=\"http://www.youtube.com/v/k4uUhWtYDPk?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/k4uUhWtYDPk?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"480\"></embed></object></p>\r\n<p>\r\n	А вот еще один эксперимент, с идентичным чехлом и шаром, только проведенный ребятами из <noindex><a href=\"http://ironsleeve.com/\">Iron Sleeve</a></noindex>. Обе компании заявляют, что их технологии являются уникальными и патентованными. Кто у кого украл - не нам решать.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<object height=\"390\" width=\"480\"><param name=\"movie\" value=\"http://www.youtube.com/v/rlaKwFslEsQ?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/rlaKwFslEsQ?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"480\"></embed></object></p>\r\n<p>\r\n	В любом случае, можем порадоваться, что появилась действенная защита для наших планшетов.</p>\r\n',NULL,3,1),(249,'ВИДЕО: Как правильно складывать Smart Cover ','<p>\r\n	Мы как то и не задумывались о том, как правильно превращать Smart Cover в удобную и надежную подставку. Это вроде естественный процесс. Оказалось у некоторых эта манипуляция вызывает некоторые затруднения.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/smartcover-long.png\" style=\"width: 500px; height: 300px; \" /></p>\r\n<p>\r\n	Для тех, кто столкнулся с такой проблемой и предназначено это видео. Все что вам нужно запомнить - складывать нужно цве','<p>\r\n	Мы как то и не задумывались о том, как правильно превращать Smart Cover в удобную и надежную подставку. Это вроде естественный процесс. Оказалось у некоторых эта манипуляция вызывает некоторые затруднения.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/smartcover-long.png\" style=\"width: 500px; height: 300px; \" /></p>\r\n<p>\r\n	Для тех, кто столкнулся с такой проблемой и предназначено это видео. Все что вам нужно запомнить - складывать нужно цветной стороной наружу.</p>\r\n\r\n<h2>\r\n	КАК НУЖНО СКЛАДЫВАТЬ</h2>\r\n<p>\r\n	<br />\r\n	Все что вам нужно запомнить - складывать нужно цветной стороной внутрь.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/96RdoQB1DL4?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/96RdoQB1DL4?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	Это и не мудрено магниты такие магниты, что имеют свойство прилипать только к металлической поверхности или друг другу.</p>\r\n',NULL,3,1),(250,'ВИДЕО: Ребенка заменит iPad','<p>\r\n	Поучительное видео о судьбе еврейского мальчика, на замену которому пришел iPad. Точнее пришел мифический iBoy, но мы то понимаем этот неприкрытый намек. Фактически планшет не может заменить живого ребенка, или может?</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/iBoy.png\" style=\"width: 500px; height: 313px; \" /></p>\r\n<p>\r\n	В какой то мере, видео должно быть юмористическим. Но своеобразный юмор автора мне так и не удалось прочувствовать, ес','<p>\r\n	Поучительное видео о судьбе еврейского мальчика, на замену которому пришел iPad. Точнее пришел мифический iBoy, но мы то понимаем этот неприкрытый намек. Фактически планшет не может заменить живого ребенка, или может?</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/iBoy.png\" style=\"width: 500px; height: 313px; \" /></p>\r\n<p>\r\n	В какой то мере, видео должно быть юмористическим. Но своеобразный юмор автора мне так и не удалось прочувствовать, если он, конечно, там был.</p>\r\n\r\n<h2>\r\n	РЕБЕНОК НЕ ИГРУШКА</h2>\r\n<p>\r\n	<br />\r\n	Видео вызывает двоякие ощущения. Реалистично прикрученный к шее планшет, и печаль в глазах у сына отбивает всякое желание смеяться.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<object height=\"390\" width=\"480\"><param name=\"movie\" value=\"http://www.youtube.com/v/ON243l6cs8o?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/ON243l6cs8o?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"480\"></embed></object></p>\r\n<p>\r\n	Надеюсь, вы извлекли урок из этого маленького фильма. Живые люди ведь намного лучше рукотворных изделий.</p>\r\n',NULL,3,1),(251,'ВИДЕО: 3D изображение на iPad без специальных очков ','<p>\r\n	Мы уже рассуждали несколько месяцев назад о трехмерном изображении на iPod. Но тогда это были только слухи и патенты. Теперь же у вас есть возможность собственными глазами убедится в реальности данной технологии.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/ipad-3d-screen.png\" style=\"width: 500px; height: 295px; \" /></p>\r\n<p>\r\n	Реализована трехмерная картинка с помощью технологии &quot;<a href=\"http://macov.net/news/ipod-touch-3d-screen\"><','<p>\r\n	Мы уже рассуждали несколько месяцев назад о трехмерном изображении на iPod. Но тогда это были только слухи и патенты. Теперь же у вас есть возможность собственными глазами убедится в реальности данной технологии.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/ipad-3d-screen.png\" style=\"width: 500px; height: 295px; \" /></p>\r\n<p>\r\n	Реализована трехмерная картинка с помощью технологии &quot;<a href=\"http://macov.net/news/ipod-touch-3d-screen\"><strong>хэдтрэкинга</strong></a>&quot;, когда устройство показывает вам картинку, анализируя положение вашей головы относительно экрана. Это стало возможным, благодаря фронтальной камере, появившейся в <a href=\"http://macov.net\">iPad 2</a>.</p>\r\n\r\n<h2>\r\n	КАК ВЫГЛЯДИТ iPAD 3D</h2>\r\n<p>\r\n	<br />\r\n	Этой технологии еще далеко до релиза, но выглядит это уже довольно многообещающе. Осталось только убедить инженеров Apple в том, что это им действительно нужно.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/bBQQEcfkHoE?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/bBQQEcfkHoE?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	Данная технология позволяет еще сильнее погрузится в игровые приложения и увеличить полезное пространство для интерфейса управления любого приложения. Надеюсь, что нечто подобное вскоре появится в широком доступе.</p>\r\n',NULL,3,1),(252,'ВИДЕО: Photoshop идет на на встречу пользователям iPad ','<p>\r\n	Сегодня компания Adobe раскрыла некоторые свои планы касательно мобильной версии своего популярного редактора изображений. Разработчики были так обеспокоены отсутствием инструмента для быстрого редактирования фотографий с фотокамер и камерофонов, что решили всерьез заняться планшетной версией <strong>Photoshop Express</strong>.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/ipad-photosop-new.png\" style=\"width: 500px; height: 279px; \" /></p>\r','<p>\r\n	Сегодня компания Adobe раскрыла некоторые свои планы касательно мобильной версии своего популярного редактора изображений. Разработчики были так обеспокоены отсутствием инструмента для быстрого редактирования фотографий с фотокамер и камерофонов, что решили всерьез заняться планшетной версией <strong>Photoshop Express</strong>.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/ipad-photosop-new.png\" style=\"width: 500px; height: 279px; \" /></p>\r\n<p>\r\n	Так вышеупомянутый редактор получит поддержку слоев (про это уже писали), а желающие заняться живописью - несколько фирменных инструментов для этого.</p>\r\n\r\n<h2>\r\n	КАК ЭТО БУДЕТ ВЫГЛЯДЕТЬ</h2>\r\n<p>\r\n	<br />\r\n	Не смотря на свою извращенную любовь к технологии Flash, в компании Adobe таки работают здравомыслящие люди. Их стремление связать планшет и десктопную версию Photoshop заслуживает оваций. На картинках, во всяком случае, выглядит очень перспективно.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/bmAhoPIVXpc?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/bmAhoPIVXpc?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	Как это будет работать в реальности и получит ли распространение среди простых пользователей - неизвестно. Я надеюсь разработчики достаточно прониклись духом Apple, чтобы делать сложные вещи простыми, а не наоборот.</p>\r\n',NULL,3,1),(253,'ВИДЕО: \"История Игрушек 3\" - рисуем руками на iPad','<p>\r\n	Если вы считаете, что для создания шедевра вам не хватает новых <strong>приложений от Adobe</strong>, то поверьте, без них тоже можно рисовать. Британский художник Кайл Ламберт для создания своих картин пользуется только приложением <noindex><a href=\"http://click.linksynergy.com/fs-bin/stat?id=uO47tEUahH8&amp;offerid=146261&amp;type=3&amp;subid=0&amp;tmpid=1826&amp;RD_PARM1=http%253A%252F%252Fitunes.apple.com%252Fus%252Fapp%252Fid363590649%253Fmt%253D8%2526uo%253D4%2526partnerId%253D30\">Br','<p>\r\n	Если вы считаете, что для создания шедевра вам не хватает новых <strong>приложений от Adobe</strong>, то поверьте, без них тоже можно рисовать. Британский художник Кайл Ламберт для создания своих картин пользуется только приложением <noindex><a href=\"http://click.linksynergy.com/fs-bin/stat?id=uO47tEUahH8&amp;offerid=146261&amp;type=3&amp;subid=0&amp;tmpid=1826&amp;RD_PARM1=http%253A%252F%252Fitunes.apple.com%252Fus%252Fapp%252Fid363590649%253Fmt%253D8%2526uo%253D4%2526partnerId%253D30\">Brushes</a></noindex> и своими пальцами.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/toy-story-3-ipad.png\" style=\"width: 500px; height: 372px; \" /></p>\r\n<p>\r\n	Эти два инструмента позволяют ему рисовать довольно реалистичные картины. Мало того, он еще и записывает весь процесс на видео. Так что у вас есть прекрасная возможность увидеть процесс создания картины всего за несколько минут.</p>\r\n\r\n<h2>\r\n	КАК РИСОВАТЬ НА iPAD</h2>\r\n<p>\r\n	<br />\r\n	Как я уже говорил, самый главный инструмент в рисовании на планшете - это ваши пальцы. Использовать их для рисования не так привычно как стилус или карандаш. Но привыкнуть можно ко всему.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/_ofJCWEbf-c?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/_ofJCWEbf-c?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	Для тех, кто заинтересовался творчеством Кайла - милости просим в <noindex><a href=\"http://kyle-lambert.blogspot.com/2010/09/10-ipad-portraits-daily-mail-telegraph.html\"><strong>Галерею Кайла Ламберта</strong></a></noindex> где собрано довольно много видео-работ прогрессивного художника.</p>\r\n',NULL,3,1),(254,'ВИДЕО: Дополненная реальность на iPhone от Aurasma','<p>\r\n	О дополненной реальности на iPhone и iPad все уже слышали. На этот раз компания Aurasma предложила действительно нечто новое и интересное - использовать дополненную реальность на iPhone в качестве &quot;дополнения&quot; рекламе и новостям.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/aurasma-iphone.png\" style=\"width: 500px; height: 278px; \" /></p>\r\n<p>\r\n	Не смотря на трудную реализацию такой технологии, она остается очень заманчивой.</p>\r\n','<p>\r\n	О дополненной реальности на iPhone и iPad все уже слышали. На этот раз компания Aurasma предложила действительно нечто новое и интересное - использовать дополненную реальность на iPhone в качестве &quot;дополнения&quot; рекламе и новостям.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/aurasma-iphone.png\" style=\"width: 500px; height: 278px; \" /></p>\r\n<p>\r\n	Не смотря на трудную реализацию такой технологии, она остается очень заманчивой.</p>\r\n\r\n<h2>\r\n	КАК ЭТО ВЫГЛЯДИТ</h2>\r\n<p>\r\n	<br />\r\n	Здесь все просто - наводите объектив своего iPhone на интересующее вас изображение и получаете полное видео или рекламный ролик. В общем, смотрите сами.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/GBKy-hSedg8?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/GBKy-hSedg8?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	Отсутствие конкретных маркеров, которые так необходимы для дополненной реальности - это шаг вперед в этом направлении. Это если компания Aurasma действительно решила от них отказаться, а не планирует адаптировать свое приложение к конкретным изображением. Таким образом, это становится удобным средством для распространения рекламы. Но до газет, как в &quot;Гарри Поттере&quot; дело не дойдет.</p>\r\n',NULL,3,1),(255,'ВИДЕО: Необычный сенсорный стол для iPhone','<p>\r\n	Это конечно не сенсорный стол от Microsoft, а просто, еще одна док-станция с колонками. Но такая гарнитура в форме журнального стола нам встречается впервые.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/iTable_15.jpg\" style=\"width: 500px; height: 334px; \" /></p>\r\n<p>\r\n	Из его преимуществ - стильный внешний вид, хороший звук, управление прикосновением. В минусы сразу можно занести его размер и вес. О том, как этот стол &quot;работает&quot; ','<p>\r\n	Это конечно не сенсорный стол от Microsoft, а просто, еще одна док-станция с колонками. Но такая гарнитура в форме журнального стола нам встречается впервые.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/iTable_15.jpg\" style=\"width: 500px; height: 334px; \" /></p>\r\n<p>\r\n	Из его преимуществ - стильный внешний вид, хороший звук, управление прикосновением. В минусы сразу можно занести его размер и вес. О том, как этот стол &quot;работает&quot; можно узнать из нижерасположенного видео.</p>\r\n\r\n<h2>\r\n	КАК ЗВУЧИТ СТОЛ</h2>\r\n<p>\r\n	<br />\r\n	Если вы заинтересованы в большем и громоздком, но очень стильном и функциональном столике - эта модель именно для вас. Составить о ней впечатления вы можете из следующего видео.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/82lJay9w5RY?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/82lJay9w5RY?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	Обилие металлических деталей и LED подсветка подстаканников (как и их наличие) говорит о привязанности дизайнера к циганской культуре. Стол, конечно, поражает своей функциональностью, но его размеры напоминают бабушкино пианино, которое странным образом трансформировалось в стол. Кстати, идея с гарнитурой в форме музыкального инструмента пока, вроде, не реализована, а жаль.</p>\r\n',NULL,3,1),(256,'ВИДЕО: Принципиально новая концепция фотокамеры и смартфона','<p>\r\n	Все эти годы мы наблюдали за попытками симбиоза мобильного телефона и фотокамеры. С появлением моды на смартфоны эта традиция никуда не исчезла. Но по прошествии всего этого времени никто так и не смог создать реальную альтернативу фотоаппарата на базе мобильного устройства. Видимо поэтому компания <noinde><a href=\"http://www.artefactgroup.com/#/content/camera-futura-3/\"><strong>Artefact</strong></a> решила пойти противоположным путем.</noinde></p>\r\n<p class=\"rtecenter\" style=\" text-align:','<p>\r\n	Все эти годы мы наблюдали за попытками симбиоза мобильного телефона и фотокамеры. С появлением моды на смартфоны эта традиция никуда не исчезла. Но по прошествии всего этого времени никто так и не смог создать реальную альтернативу фотоаппарата на базе мобильного устройства. Видимо поэтому компания <noinde><a href=\"http://www.artefactgroup.com/#/content/camera-futura-3/\"><strong>Artefact</strong></a> решила пойти противоположным путем.</noinde></p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/Lens.png\" style=\"width: 500px; height: 343px; \" /></p>\r\n<p>\r\n	Они решили создать вышеупомянутый гибрид на базе фотоаппарата, что значительно повышало качество снимков и упрощала задачу. При нынешнем уровне технологий данное устройство под силу собрать любому крупному игроку на рынке. Но сама идея <strong>WVIL</strong> (WIRELESS VIEWFINDER INTERCHANGEABLE LENS) камеры заключается в использовании уникальных технологий, которые можно увидеть на прилагающемся видео.</p>\r\n\r\n<h2>\r\n	КАК ВЫГЛЯДИТ КАМЕРОФОН</h2>\r\n<p>\r\n	<br />\r\n	Сама идея такого устройства заключается в том, чтобы весь цикл съемки осуществлялся непосредственно объективом, снабженным необходимой аппаратурой. База (kit) используется лишь для управления съемкой и редактирования фотографий. Основным же козырем будет беспроводный обмен между объективом и базой камеры. То есть вы можете отделить объектив и направить его в любую сторону.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/VgBl0ejQ8c0&amp;hl=en_US&amp;feature=player_embedded&amp;version=3\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowScriptAccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/VgBl0ejQ8c0&amp;hl=en_US&amp;feature=player_embedded&amp;version=3\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	Выглядит это довольно фантастически, да и не стоит забывать, что данный девайс пока является только концептом. Но уж слишком заманчиво иметь смартфон который одним движением руки превращается в полупрофессиональную фотокамеру.</p>\r\n',NULL,3,1),(257,'ВИДЕО: Тест скорости мобильных браузеров','<p>\r\n	Сегодня в сети появился скандальный ролик, на котором в реальном времени представителями Microsoft тестируется скорость в браузерах iPhone 4, Samsung Galaxy S и WP7 смартфон от HTC с IE9 на борту. Естественно, HTC победил а iPhone проиграл.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/ie-9-fail-3.jpg\" style=\"width: 500px; height: 281px; \" /></p>\r\n<p>\r\n	Но не стоит отчаиваться по этому поводу. Существует масса причин, чтобы этого не делать.','<p>\r\n	Сегодня в сети появился скандальный ролик, на котором в реальном времени представителями Microsoft тестируется скорость в браузерах iPhone 4, Samsung Galaxy S и WP7 смартфон от HTC с IE9 на борту. Естественно, HTC победил а iPhone проиграл.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/ie-9-fail-3.jpg\" style=\"width: 500px; height: 281px; \" /></p>\r\n<p>\r\n	Но не стоит отчаиваться по этому поводу. Существует масса причин, чтобы этого не делать. Давайте посмотрим видео и обсудим увиденное.</p>\r\n\r\n<h2>\r\n	КАК ВЫИГРЫВАЕТ IE</h2>\r\n<p>\r\n	<br />\r\n	Я внимательно посмотрел это видео. Да, на нем действительно смартфон с IE 9 одерживает безоговорочную победу. Победу в тесте написанным ребятами из Microsoft для браузера от Microsoft, который работает в ОС от (не может быть) Microsoft. Вообще по поводу бредовости этих тестов начали говорить с первого дня тестирования IE9. Много говорили о том, что тесты заточены под конкретный браузер и даже предоставляли улики.</p>\r\n<p>\r\n	Ну да кучка гиков может кричать об этом на двух десятках &quot;гиковских&quot; сайтов, но обыкновенный пользователь об этом вряд ли услышит. Зато он увидит, как быстро двигаются буковки на экране WP7 смартфона и медленно на экране iPhone. Вот посмотрите, как это выглядит.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/Or3wvF9ts0I?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/Or3wvF9ts0I?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	Ну а теперь немного фактов. Мне стало интересно и я решил протестировать свои настольные браузеры. Использовал Chrome, Safari, Firefox и Opera. Все последних версий и без лишней нагрузки. Результат закономерный - браузеры, в основе которых лежит WebKit - результат 3 FPS, Огнелис и Опера - 10 FPS. За неимением IE 9 под рукой не смог его протестировать. Вероятно, результат должен быть около 20 или больше. Но у меня возникли вопросы. Первый - что конкретно тестируется в этом чудесном тесте и почему нельзя заглянуть в его внутренности. Без ответов на эти вопросы я предпочитаю считать эти тесты ошибочными.</p>\r\n',NULL,3,1),(258,'ВИДЕО: 3D в iPhone при помощи акселерометра ','<p>\r\n	Пока некоторые используют хэдтрекинг и камеру iPad 2 для достижения трехмерности, другие пошли по более простому пути. В App Store появилось приложение, которое продемонстрирует вам трехмерную тестовую площадку, где вы сможете ощутить эффект 3D без специальных очков.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/iphone-3d-acsellerometr.jpg\" style=\"width: 500px; height: 332px; \" /></p>\r\n<p>\r\n	Работает это приложение не ахти как, но для того,','<p>\r\n	Пока некоторые используют хэдтрекинг и камеру iPad 2 для достижения трехмерности, другие пошли по более простому пути. В App Store появилось приложение, которое продемонстрирует вам трехмерную тестовую площадку, где вы сможете ощутить эффект 3D без специальных очков.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/iphone-3d-acsellerometr.jpg\" style=\"width: 500px; height: 332px; \" /></p>\r\n<p>\r\n	Работает это приложение не ахти как, но для того, чтобы понять, как это работает его вполне достаточно.</p>\r\n\r\n<h2>\r\n	КАК РАБОТАЕТ ТРЕХМЕРНОСТЬ</h2>\r\n<p>\r\n	<br />\r\n	За неимением iPhone под рукой я тестировал приложение на iPad. Действительно, отсутствие камеры никак не повлияло на работоспособность приложения.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/ZfiUEpLpMXk?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/ZfiUEpLpMXk?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	В том виде, в котором представлена трехмерность в этом приложении, она вряд ли попадет на устройства Apple. Скажу одно - практической пользы в трехмерности пока не видно. Даже игры выглядят намного привлекательнее, когда остаются в границах экрана.</p>\r\n',NULL,3,1),(259,'ВИДЕО: Путешествие из Сан Франциско в Париж за 2 минуты ','<p>\r\n	Вы часто летаете из Сан Франциско в Париж? Тогда следующее видео вам будет не интересно - вы все это уже видели. Ну а если для вас такой маршрут в новинку, то у вас есть возможность посмотреть весь маршрут в ускоренной съемке.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/Mac-iPad-San-Francisco-to-Paris-in-2-Minutes-580x476.jpg\" style=\"width: 500px; height: 235px; \" /></p>\r\n<p>\r\n	Помимо привычных облаков у вас есть возможность насладится се','<p>\r\n	Вы часто летаете из Сан Франциско в Париж? Тогда следующее видео вам будет не интересно - вы все это уже видели. Ну а если для вас такой маршрут в новинку, то у вас есть возможность посмотреть весь маршрут в ускоренной съемке.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/Mac-iPad-San-Francisco-to-Paris-in-2-Minutes-580x476.jpg\" style=\"width: 500px; height: 235px; \" /></p>\r\n<p>\r\n	Помимо привычных облаков у вас есть возможность насладится северным сиянием и прочей красотой.</p>\r\n\r\n<h2>\r\n	КАК ВЫГЛЯДИТ ПОЕЗДКА</h2>\r\n<p>\r\n	<br />\r\n	Сама идея снимать Time Laps за бортом самолета крайне оригинальна. Стоит отметить, что задержка между кадрами варьируется от 2 до 30 секунд. И выглядит это все довольно интересно.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/8j36Erxd5rc?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/8j36Erxd5rc?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	Возвращаясь к тематике сайта, упомяну, что саундтрек к этому видео написан на iPad в приложении Korg iMS20, а видео обрабатывалось на Mac и создавалось в iMovie. Так что в следующий раз, когда будете лететь над красивыми местами захватите с собой фотоаппарат.</p>\r\n',NULL,3,1),(260,'ВИДЕО: Sony показала свои планшеты ','<p>\r\n	Не прошло и пол года с момента презентации <a href=\"http://macov.net\"><strong>iPad 2</strong></a>, как компания Sony представила свое видение продукта. Не смотря на трагические события в Японии планшеты таки увидели свет. Точнее, один планшет и один раскладной планшет.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/soni-pad-s1-s2.png\" /></p>\r\n<p>\r\n	Привычная аппаратная база и традиционный Android 3.0 компенсируется оригинальным дизайном перв','<p>\r\n	Не прошло и пол года с момента презентации <a href=\"http://macov.net\"><strong>iPad 2</strong></a>, как компания Sony представила свое видение продукта. Не смотря на трагические события в Японии планшеты таки увидели свет. Точнее, один планшет и один раскладной планшет.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/soni-pad-s1-s2.png\" /></p>\r\n<p>\r\n	Привычная аппаратная база и традиционный Android 3.0 компенсируется оригинальным дизайном первой модели и сверхоригинальным дизайном второй. Впрочем, смотрите сами как это выглядит.</p>\r\n\r\n<h2>\r\n	МОНОБЛОК И РАСКЛАДУШКА</h2>\r\n<p>\r\n	<br />\r\n	Если модель S1 еще похожа на привычный нам планшет, то S2 предлагает нам абсолютно новый опыт в работе с мобильными сенсорными устройствами. Это гремучая смесь нетбука, планшета и женской сумочки в которую помещается довольно много всего полезного.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/_9DZdXHzXwk?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/_9DZdXHzXwk?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	Предлагаю дождаться &quot;полевых испытаний&quot; девайса и посмотреть насколько оправдан такой конструкторский креатив. Возможно он придется по вкусу пользователям.</p>\r\n',NULL,3,1),(261,'Белые iPhone 4 появились в Бельгии ','<p>\r\n	Пока весь мир ожидает официального выхода белых iPhone 4, они уже появились в <noindex><a href=\"http://www.webstore.be/product/view/pid/10001378\">продаже</a></noindex> в Бельгии. Остается открытым вопрос аутентичности данного девайса. Но факт продажи налицо - никаких секретных позиций в каталогах или непонятных артикулов. Просто коробки с белыми iPhone.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/white_iphone_one.jpg\" style=\"width: 500px;','<p>\r\n	Пока весь мир ожидает официального выхода белых iPhone 4, они уже появились в <noindex><a href=\"http://www.webstore.be/product/view/pid/10001378\">продаже</a></noindex> в Бельгии. Остается открытым вопрос аутентичности данного девайса. Но факт продажи налицо - никаких секретных позиций в каталогах или непонятных артикулов. Просто коробки с белыми iPhone.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/white_iphone_one.jpg\" style=\"width: 500px; height: 292px; \" /></p>\r\n<p>\r\n	Беглый анализ ситуации позволяет понять, что невиданное в просвещенной Европе событие - обыденность по местным масштабам. Уже давно появилась возможность приобрести iPhone-альбинос, где оригинальная черная панель была заменена на ее белый аналог. На этом различия в устройствах исчерпывались.</p>\r\n',NULL,3,1),(262,'Быстрый способ скачать актуальную версию iOS для вашего iДевайса ','<p>\r\n	Помните, как по нескольку раз приходилось закачивать новую прошивку через iTunes когда сервера Apple не справлялись с наплывом желающих, или когда приходилось заново подключать устройство к компьютеру, чтобы обновится? Конечно, ситуацию в последнее время спасают прямые ссылки, но каждый раз искать их было не очень удобно. Теперь все это в прошлом.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/ipsw-downloader.png\" /></p>\r\n<p>\r\n	Появилась удо','<p>\r\n	Помните, как по нескольку раз приходилось закачивать новую прошивку через iTunes когда сервера Apple не справлялись с наплывом желающих, или когда приходилось заново подключать устройство к компьютеру, чтобы обновится? Конечно, ситуацию в последнее время спасают прямые ссылки, но каждый раз искать их было не очень удобно. Теперь все это в прошлом.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/ipsw-downloader.png\" /></p>\r\n<p>\r\n	Появилась удобная утилита, которая позволит быстро и комфортно скачать любую версию iOS под любой девайс. По правде сказать, таких утилит мне встречалось уже несколько, но это самая приятная. В ней тоже не все гладко. Отсутствует поддержка Mac OS младше 10.6 и встречаются мелкие баги, но разработчик обещал продолжать работу над приложением. Скачать его можно на <noindex><a href=\"http://ibrain.com.ua/2011/04/ipswdownloader-0-3-3264bit/\">сайте разработчика</a></noindex>.</p>\r\n',NULL,3,1),(263,'ВИДЕО: В продажу поступают белые iPhone 4','<h2>\r\n	КАК ЭТО ВЫГЛЯДИТ</h2>\r\n<p>\r\n	<br />\r\n	Авторы iSpazio.net подготовили небольшое видео, на котором можно увидеть белый iPhone &quot;в живую&quot;. Стоит отметить резкий контраст выключенного экрана и корпу\r\n\r\nса - выглядит довольно необычно. Да и вообще, благодаря такой долгой задержке, белая версия смартфона кажется новым продуктом, хотя &quot;начинка&quot; его пока остается старой.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<object height=\"390\" width=\"640\"><param name=\"movi','<h2>\r\n	КАК ЭТО ВЫГЛЯДИТ</h2>\r\n<p>\r\n	<br />\r\n	Авторы iSpazio.net подготовили небольшое видео, на котором можно увидеть белый iPhone &quot;в живую&quot;. Стоит отметить резкий контраст выключенного экрана и корпу\r\n\r\nса - выглядит довольно необычно. Да и вообще, благодаря такой долгой задержке, белая версия смартфона кажется новым продуктом, хотя &quot;начинка&quot; его пока остается старой.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/LjRM4fotWFs?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/LjRM4fotWFs?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	К сожалению сегодня эти аппараты не поступят в продажу в отечественных магазинах. Но задержка не должна быть большой. Надеюсь, что скоро они появятся в каталоге нашего магазина. Следите за обновлениями.</p>',NULL,3,1),(264,'ВИДЕО: Аргументы для джэилбрейка iPhone ','<p>\r\n	Если вы думаете, что джэилбрейк нужен лишь для установки пиратских приложений - вы заблуждаетесь. На самом деле основным мотивом к взлому устройств является та самая пресловутая &quot;открытость&quot;, которой так дорожат пользователи Android.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/cydia-arg.png\" style=\"width: 500px; height: 279px; \" /></p>\r\n<p>\r\n	Именно для этого и создавалась Cydia, чтобы каждый имел возможность делать со своим уст','<p>\r\n	Если вы думаете, что джэилбрейк нужен лишь для установки пиратских приложений - вы заблуждаетесь. На самом деле основным мотивом к взлому устройств является та самая пресловутая &quot;открытость&quot;, которой так дорожат пользователи Android.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/cydia-arg.png\" style=\"width: 500px; height: 279px; \" /></p>\r\n<p>\r\n	Именно для этого и создавалась Cydia, чтобы каждый имел возможность делать со своим устройством максимум возможного. С этим конечно можно поспорить, но у создателей сервиса есть довольно весомые аргументы.</p>\r\n\r\n<h2>\r\n	ЧТО МОЖНО ВЫЖАТЬ ИЗ iPHONE</h2>\r\n<p>\r\n	<br />\r\n	Cydia поможет найти вам средства для многих полезных вещей. Пять иконок в доке iPhone или клавиатура другого цвета - теперь нет необходимости ждать милости от разработчиков iOS. И это при том, что стабильность вашего устройства остается на прежнем уровне.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/hSePkhbpZIE?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/hSePkhbpZIE?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	Я не агитирую за джэилбрейк и сам предпочитаю его не использовать, но отказываться можно лишь испытав это на себе.</p>\r\n',NULL,3,1),(265,'ВИДЕО: Второй дисплей в Mac OS Lion ','<p>\r\n	Для меня совсем недавно стало очень актуально использовать большой второй дисплей вместе с моим 13&quot; МВР. Но порой мне достаточно и большого внешнего монитора, и открытый ноутбук только отвлекает. Те, кто это практиковал - знают, что в Snow Leopard при закрытии крышки MacBook даже с подключенным внешним монитором, система &quot;засыпает&quot;. В себя ее может привести только внешние периферийные устройства (мышь, клавиатура, трекпад).</p>\r\n<p class=\"rtecenter\" style=\" text-align: cente','<p>\r\n	Для меня совсем недавно стало очень актуально использовать большой второй дисплей вместе с моим 13&quot; МВР. Но порой мне достаточно и большого внешнего монитора, и открытый ноутбук только отвлекает. Те, кто это практиковал - знают, что в Snow Leopard при закрытии крышки MacBook даже с подключенным внешним монитором, система &quot;засыпает&quot;. В себя ее может привести только внешние периферийные устройства (мышь, клавиатура, трекпад).</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/lion-display.png\" style=\"width: 500px; height: 252px; \" /></p>\r\n<p>\r\n	В этой ситуации есть два негативных момента: не всегда есть внешняя периферия под рукой и очень раздражает каждый раз &quot;будить&quot; MacBook. Приятный момент заключается в том, что в Mac OS Lion этот досадный недостаток будет исправлен.</p>\r\n\r\n<h2>\r\n	КАК ВЫГЛЯДИТ БУДУЩЕЕ</h2>\r\n<p>\r\n	<br />\r\n	Я не стал повторять эксперимент и покажу вам чужое видео. На нем видно, что происходит с внешним монитором после закрытия крышки MacBook с Mac OS 10.6.7 и Mac OS 10.7 Lion.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/6HqH-7F6m08?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/6HqH-7F6m08?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	Для меня это послужит дополнительным аргументом к переходу на 10.7, хотя у меня и не осталось сомнений после выхода второго Developer Preview. Ждать до выхода осталось совсем не много, ведь WWDC&#39;11 на которой будет представлен лев, состоится в начале июня.</p>\r\n',NULL,3,1),(266,'Обновление iOS 4.3.3 избавит от паранойи но не продлит время автономной работы.','<p>\r\n	Прошу прощения за столь желтый заголовок, но это выдержка из всех похожих новостей. После скандала с базой данных телефонных вышек к которым подключались iPhone прошло не больше недели и ситуация была исправлена. Внеочередное обновление iOS под номером 4.3.3 принесло избавление от данной проблемы.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/ios433-1.png\" style=\"width: 500px; height: 364px; \" /></p>\r\n<p>\r\n	Но Apple продолжает игнорировать ','<p>\r\n	Прошу прощения за столь желтый заголовок, но это выдержка из всех похожих новостей. После скандала с базой данных телефонных вышек к которым подключались iPhone прошло не больше недели и ситуация была исправлена. Внеочередное обновление iOS под номером 4.3.3 принесло избавление от данной проблемы.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/ios433-1.png\" style=\"width: 500px; height: 364px; \" /></p>\r\n<p>\r\n	Но Apple продолжает игнорировать проблему со временем автономной работы в iOS 4.3, которую уже заметили даже самые оптимистичные пользователи. Ожидалось, что помимо исправлений в сервисе геолокации и изменений в политике безопасности будет решена и проблема с батареей. Но не сбылось. Возможно это будет исправлено в очередном минорном релизе или же придется ждать iOS 5, бета-версию которой должн показать на WWDC&#39;11.</p>\r\n\r\n<h2>\r\n	ГДЕ СКАЧАТЬ</h2>\r\n<p>\r\n	<br />\r\n	Напомню, мы отказались от публикации прямых ссылок в пользу использования <a href=\"http://macov.net/news/ipws-downloader-v03\"><strong>ipswDownloader</strong></a> которая намного удобнее.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/ios433-2.png\" style=\"width: 500px; height: 287px; \" /></p>\r\n<p>\r\n	Рекомендуем обновится всем, кто опасается за свою безопасность из-за &quot;слежки&quot; в iPhone.</p>\r\n',NULL,3,1),(267,'ВИДЕО: MacBook Pro падает с балкона и остается целым ','<p>\r\n	Неугомонные ребята из <a href=\"http://g-form.com\"><strong>G-Form</strong></a> опять принялись за свое. На этот раз героем их роликов стал не iPad, падающий с огромной высоты, а всего лишь MacBook Pro, упавший с балкона. Упавший и оставшийся невредимым.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/g-form-sleeve-mbp.jpg\" style=\"width: 500px; height: 500px; \" /></p>\r\n<p>\r\n	Всему виной &quot;магический&quot; чехол от G-Form, который позволяет ','<p>\r\n	Неугомонные ребята из <a href=\"http://g-form.com\"><strong>G-Form</strong></a> опять принялись за свое. На этот раз героем их роликов стал не iPad, падающий с огромной высоты, а всего лишь MacBook Pro, упавший с балкона. Упавший и оставшийся невредимым.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/g-form-sleeve-mbp.jpg\" style=\"width: 500px; height: 500px; \" /></p>\r\n<p>\r\n	Всему виной &quot;магический&quot; чехол от G-Form, который позволяет вашим гаджетам оставаться целыми после таких серьезных ударов.</p>\r\n\r\n<h2>\r\n	ОПАСНЫЙ ПОЛЕТ</h2>\r\n<p>\r\n	<br />\r\n	Не думаю, что каждый смог бы вот так сбросить свой MacBook с балкона, даже смахнуть его со стола - уже беда. Не смотря на прочный алюминиевый корпус владельцы беспокоятся о безопасности своих устройств. G-Form Sleeve призван защищать их в тот момент, когда они наиболее всего уязвимы. А именно - при транспортировке гаджетов по улице.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/sdAAbu-nMkY?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/sdAAbu-nMkY?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	Я сам лихо гоняю по городу на велосипеде с MBP в рюкзаке. Это видео заставило меня всерьез задуматься о его безопасности.</p>\r\n',NULL,3,1),(268,'ВИДЕО: Аналоговая док-станция для iPhone ','<p>\r\n	Рано или поздно каждый владелец iPhone приходит к тому, что ему удобно использовать свой гаджет как настольное устройство. Возникает вопрос - а какому именно устройству отдать предпочтение? На данный момент таких док-станций огромное множество, они предлагают различные функции. Но еще ни одно из подобных устройств не могло похвастаться полным отсутствием электроники.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/hive-banner3.jpg\" style=\"wid','<p>\r\n	Рано или поздно каждый владелец iPhone приходит к тому, что ему удобно использовать свой гаджет как настольное устройство. Возникает вопрос - а какому именно устройству отдать предпочтение? На данный момент таких док-станций огромное множество, они предлагают различные функции. Но еще ни одно из подобных устройств не могло похвастаться полным отсутствием электроники.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/hive-banner3.jpg\" style=\"width: 500px; height: 219px; \" /></p>\r\n<p>\r\n	Проект HiveDock это первый, пока еще не поступивший в серию гаджет, который выполняет роль аудио гарнитуры и увеличительного стекла для вашего iPhone. Причем делает он это исключительно благодаря законам физии, а не электронной начинке.</p>\r\n\r\n<h2>\r\n	КАК ЭТО ВЫГЛЯДИТ</h2>\r\n<p>\r\n	<br />\r\n	Ресурс Gizmodo назвал этот девайс &quot;Устройство для Пенсионеров&quot; которые плохо слышат и слабо видят. На самом деле это довольно универсальный гаджет, который позволит вам наслаждаться фильмами и музыкой не напрягая органы чувств.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/VdA2uWQTrc8?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/VdA2uWQTrc8?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	Есть только один минус - этот девайс пока находится в состоянии концепта. Для его серийного производства необходима самая малость - пожертвование в $200 в проекте Kickstarter. Если авторам удастся собрать необходимую сумму, то девайс поступит в продажу по $45 за штуку. Пожелаем удачи разработчикам.</p>\r\n',NULL,3,1),(269,'Слухи о iOS 5 и iPad 3 ','<p>\r\n	На этой неделе было целых два обсуждаемых слуха, которые как обычно приписывались неназываемым источникам, но по факту, были высосаны из того самого пальца.</p>\r\n<p>\r\n	Причем оба слуха исходили из других слухов, но буйная фантазия журналистов и толика здравого смысла присутствует превратила домыслы чуть ли не в настоящие факты.</p>\r\n<h2>\r\n	iOS 5 БУДЕТ ОБНОВЛЯТЬСЯ &quot;ПО ВОЗДУХУ&quot;</h2>\r\n<p>\r\n	<br />\r\n	Этот слух исходит из предположения, что Mac OS Lion будет доступен для обновления то','<p>\r\n	На этой неделе было целых два обсуждаемых слуха, которые как обычно приписывались неназываемым источникам, но по факту, были высосаны из того самого пальца.</p>\r\n<p>\r\n	Причем оба слуха исходили из других слухов, но буйная фантазия журналистов и толика здравого смысла присутствует превратила домыслы чуть ли не в настоящие факты.</p>\r\n<h2>\r\n	iOS 5 БУДЕТ ОБНОВЛЯТЬСЯ &quot;ПО ВОЗДУХУ&quot;</h2>\r\n<p>\r\n	<br />\r\n	Этот слух исходит из предположения, что Mac OS Lion будет доступен для обновления только через Mac App Store. От сюда вывод - iOS ничем не хуже и в новой версии появится такая возможность. Благо, масштабные датацентры построенные Apple справятся с такой задачей.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/ios-5update.png\" style=\"width: 500px; height: 480px; \" /></p>\r\n<p>\r\n	Тем самым может быть уменьшен размер обновления, ведь обновляться будут только необходимые компоненты. Во всяком случае iOS 5 придется обновлять традиционным способом, ведь в актуальной версии мобильной операционной системы такая функция пока не предусмотрена.</p>\r\n<h2>\r\n	В iPAD 3 БУДЕТ 3D ДИСПЛЕЙ</h2>\r\n<p>\r\n	<br />\r\n	Патенты, полученные Apple на производство мобильных устройств с объемными экранами и цифра &quot;3&quot; в названии наталкивает на логичную мысль, что третье поколение планшетников получат трехмерные экраны. Однако печальный опыт Nintendo 3DS может задержать появление устройств, использующих данную технологию на рынке.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/ipad3d.jpg\" style=\"width: 500px; height: 267px; \" /></p>\r\n<p>\r\n	Учитывая пристальное внимание со стороны конкурентов и армию ненавистников бренда, Apple вряд ли пойдет на это. Сомнительное маркетинговое преимущество, которое не несет никакой реальной пользы - это удел отстающих, но никак не лидеров.</p>\r\n',NULL,3,1),(270,'ВИДЕО: Showyou соберет все видео ваших друзей','<p>\r\n	Прогресс не стоит на месте и скорости доступа к интернету постоянно растут. Постоянный высокоскоростной интернет позволяет пользователям легко обмениваться интересными видео через социальные сети. Мало кому интересно читать скучные тексты, если можно посмотреть веселое видео.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/showyou.png\" style=\"width: 500px; height: 190px; \" /></p>\r\n<p>\r\n	Для того чтобы удобно собирать видео от ваших друзей в <','<p>\r\n	Прогресс не стоит на месте и скорости доступа к интернету постоянно растут. Постоянный высокоскоростной интернет позволяет пользователям легко обмениваться интересными видео через социальные сети. Мало кому интересно читать скучные тексты, если можно посмотреть веселое видео.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/showyou.png\" style=\"width: 500px; height: 190px; \" /></p>\r\n<p>\r\n	Для того чтобы удобно собирать видео от ваших друзей в <strong>facebook</strong> и <strong>twitter</strong> и было разработано приложение <a href=\"http://itunes.apple.com/us/app/showyou/id422698201?mt=8\"><strong>Showyou</strong></a>. Абсолютно бесплатно вы можете собрать все видео &quot;в одну корзину&quot; и в минуты отдыха не тратить время на поиск интересного.</p>\r\n\r\n<h2>\r\n	КАК ВЫГЛЯДИТ SHOWYOU</h2>\r\n<p>\r\n	<br />\r\n	Для доступа к видео вам достаточно просто подключить свои профили социальных сетей к приложению и начать смотреть. В результате Showyou станет вашим надежным компаньоном по убиванию свободного времени.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<iframe frameborder=\"0\" height=\"360\" src=\"http://player.vimeo.com/video/21409121?portrait=0\" width=\"640\"></iframe></p>\r\n<p>\r\n	Резюмирую - Showyou довольно удобное бесплатное приложение для просмотра интересных видео из социальных сетей с возможностью распространения этого самого видео. Рекомендуется всем обладателям высокоскоростного интернета и свободного времени.</p>\r\n',NULL,3,1),(271,'ВИДЕО: Вымышленный сервис, пользователи Apple и спонтанная песня','<p>\r\n	Посетители конференции GEL&#39;11 в Нью-Йорке стали свидетелями очередного спонтанного музыкального представления. На этот раз все начиналось довольно мирно. На сцену был приглашен очередной докладчик для представления нового вымышленного социального сервиса Twirlr. Свой поддельный доклад он начал со слов &quot;Прошу всех выключить мобильные устройства...&quot; и понеслось.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/gotta-share.jpg\" styl','<p>\r\n	Посетители конференции GEL&#39;11 в Нью-Йорке стали свидетелями очередного спонтанного музыкального представления. На этот раз все начиналось довольно мирно. На сцену был приглашен очередной докладчик для представления нового вымышленного социального сервиса Twirlr. Свой поддельный доклад он начал со слов &quot;Прошу всех выключить мобильные устройства...&quot; и понеслось.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/gotta-share.jpg\" style=\"width: 500px; height: 354px; \" /></p>\r\n<p>\r\n	В ответ на просьбу докладчика, со своих мест начали вставать посетители и петь. Этой песней они хотели высказать свой протест запрету на использование мобильных устройств.</p>\r\n\r\n<h2>\r\n	КАК ВЫГЛЯДИТ ПЕСНЯ</h2>\r\n<p>\r\n	<br />\r\n	До последнего момента зрителям было не ясно, это подготовленная акция или спонтанный флэш-моб. По своим местам все расставил докладчик, когда пропел свой ответ возмущенным посетителям.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/soAk3F0wX9s?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/soAk3F0wX9s?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	Примечателен тот факт, что в руках у недовольных любителей социальных сервисов были сплошь девайсы от фирмы Apple. Это символично, ведь мода на социальные сети сейчас развивается благодаря мобильным устройствам, лидерами среди которых по праву можно назвать компанию из Купретино.</p>\r\n',NULL,3,1),(272,'Angry Birds в браузере - официально и бесплатно ','<p>\r\n	Создатели популярнейшей игры прошлого года Angry Birds продолжают удивлять. Мало им бесплатной версии игры для Android OS, сегодня вышла версия для браузера Chrome. Мне неизвестно каким образом продукты Google добились такой благосклонности от Rovio? но факт на лицо.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/chrome-angybirds.png\" style=\"width: 500px; height: 150px; \" /></p>\r\n<p>\r\n	Для того, чтобы установить себе Angry Birds, вам достато','<p>\r\n	Создатели популярнейшей игры прошлого года Angry Birds продолжают удивлять. Мало им бесплатной версии игры для Android OS, сегодня вышла версия для браузера Chrome. Мне неизвестно каким образом продукты Google добились такой благосклонности от Rovio? но факт на лицо.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/chrome-angybirds.png\" style=\"width: 500px; height: 150px; \" /></p>\r\n<p>\r\n	Для того, чтобы установить себе Angry Birds, вам достаточно:</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<strong><a href=\"http://www.google.com/chrome\">Скачать браузер Goggle Chrome</a></strong></p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<strong><a href=\"https://chrome.google.com/webstore/detail/aknpkdffaafgjchaibgeefbgmgeghloj#\">Установить Angry Birds для Chrome</a></strong></p>\r\n<p>\r\n	Это пока бета-версия но ограничения вводятся на количество уровней, а не качество игры. Как приверженцев техники Apple, нас смущает реализация этой версии при помощи Adobe Flash. Но для обеденного перерыва - сойдет. В общем, пробуйте, играйте и убивайте свое время на здоровье.</p>\r\n',NULL,3,1),(273,'ВИДЕО: Google наносит ответный удар ','<p>\r\n	Эта неделя так была наполнена новостями из лагеря &quot;Империи Добра&quot;. Началось все с того, что на конференцию Google I/O 2011 ребята из вышеупомянутой компании пришли все как один с лэптопами Apple. Мелочь, а приятно. Дальше в уже в ход пошли крупные события. Помимо огромного количества интересных узким специалистам событий, были представлены и 2 потребительских продукта.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/google-clouds.pn','<p>\r\n	Эта неделя так была наполнена новостями из лагеря &quot;Империи Добра&quot;. Началось все с того, что на конференцию Google I/O 2011 ребята из вышеупомянутой компании пришли все как один с лэптопами Apple. Мелочь, а приятно. Дальше в уже в ход пошли крупные события. Помимо огромного количества интересных узким специалистам событий, были представлены и 2 потребительских продукта.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/google-clouds.png\" style=\"width: 500px; height: 361px; \" /></p>\r\n<p>\r\n	Первый продукт, а точнее сервис <a href=\"http://music.google.com/about/\"><strong>Music Beta</strong></a>, позволит реализовать несбыточную мечту Apple-фанов на облачное хранилище музыки. Бесплатно и всегда под рукой, плюс беспроводная синхронизация. Второй продукт - долгожданная платформа <strong>Chrome OS</strong>, первые <a href=\"http://www.google.com/chromebook/#features\"><strong>Chromebook</strong></a> с которой появятся в продаже уже 15 июня.</p>\r\n\r\n<h2>\r\n	МУЗЫКА В ОБЛАКАХ</h2>\r\n<p>\r\n	<br />\r\n	Посмотрите видео и на первый взгляд вам покажется это маной небесной и панацеей от всех ваших бед. Как же это прекрасно - иметь облачную медиатеку и получать к ней доступ из любого уголка планеты. Бонусом можно считать расширенные возможности Android в работе с Music Beta.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/ZrNhKcxBbZo?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/ZrNhKcxBbZo?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	Но этот сервис рискует разделить печальную судьбу таких продуктов как Google Wave, Buzz или же робота-водителя. Одна из причин, на вскидку - наличие конкурентов в лице Amazon и Apple. Первые уже запустили свой продукт, ну а вторые уж слишком тщательно готовятся к запуску. Так что приставка Beta, говорящая о несерьезности намерений, рискует никогда не превратиться в Release.</p>\r\n<h2>\r\n	КОМПЬЮТЕР В ОБЛАКАХ</h2>\r\n<p>\r\n	<br />\r\n	Это ждали уже около 2 лет и вот свершилось. Ждали не столько из желания начать использовать, сколько из желания посмотреть, что же получится. Те сырые сборки Chrome OS которые попадали лично мне в руки вызывали недоумение. Их качество оставляло желать лучшего и не дотягивало даже до уровня браузера, на основе которого и строилась ОС. Но рисованный ролик спешить убедить меня, циничного скептика, что все было исправлено и теперь все работает как надо.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/TVqe8ieqz10?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/TVqe8ieqz10?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	Стоит похвалить Acer и Samsung, которые стремясь выпустить свои Chromebook&#39;и раньше всех, впервые за долгое время отказались от традиционного ущербно-цыганского дизайна в пользу просвещенного минимализма. За это им стоит сказать спасибо ребятам из Google, которые довольно жестко контролируют этот процесс. Пожелаем же удачи пионерам облачных технологий. Я верю, что за ними возможно наше с вами будущее.</p>\r\n',NULL,3,1),(274,'Mac OS Lion обновился. Готовится обновление 10.6.8','<p>\r\n	Темп обновления девелоперской версии Mac OS Lion 10.7 увеличился. С поразительной регулярностью выходят обновления, которые несут все новые и новые улучшения и изменения.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/mac-os-x-lion-developer-preview-3.jpg\" style=\"width: 500px; height: 174px; \" /></p>\r\n<p>\r\n	На сей раз изменения коснулись внешнего вида окна входа и Launch Pad, который появился сравнительно недавно, но удобство которого уже по','<p>\r\n	Темп обновления девелоперской версии Mac OS Lion 10.7 увеличился. С поразительной регулярностью выходят обновления, которые несут все новые и новые улучшения и изменения.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/mac-os-x-lion-developer-preview-3.jpg\" style=\"width: 500px; height: 174px; \" /></p>\r\n<p>\r\n	На сей раз изменения коснулись внешнего вида окна входа и Launch Pad, который появился сравнительно недавно, но удобство которого уже по достоинству оценили все тестировавшие ранние версии. Небольшие изменения в Safari принесли новые функции, о которых расскажем в следующих статьях.</p>\r\n<h2>\r\n	СУДЬБА СНЕЖНОГО БАРСА</h2>\r\n<p>\r\n	<br />\r\n	Вместе с обновлением 10.7 в руки бета-тестеров попала и первая сборка Mac OS 10.6.8. Эта версия Mac OS станет последней из Снежных Барсов, которая увидит свет перед приходом Льва.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/10.6.8.png\" style=\"width: 456px; height: 242px; \" /></p>\r\n<p>\r\n	Уже стандартный набор тестируемых функций, который включает в себя AirPort, Graphics Drivers, Mac App Store, Networking, QuickTime и VPN. Скорее всего данный апдейт выйдет накануне релиза Mac OS 10.7, и ознаменует конец консервативных ОС на Mac.</p>\r\n',NULL,3,1),(275,'ВИДЕО: Опять G-FORM - на этот раз под колесами ','<p>\r\n	И снова, в который раз у нас видео от ребят из G-Form с их фантастическими чехлами, защищающими от ударов, падений и других механических повреждений ваши iДевайсы. На этот раз iPad был выброшен на дорогу, где его переехал автомобиль.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<object style=\"height: 390px; width: 640px\"><param name=\"movie\" value=\"http://www.youtube.com/v/yrLNVcYz6ZM?version=3\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowScriptAccess\" valu','<p>\r\n	И снова, в который раз у нас видео от ребят из G-Form с их фантастическими чехлами, защищающими от ударов, падений и других механических повреждений ваши iДевайсы. На этот раз iPad был выброшен на дорогу, где его переехал автомобиль.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<object style=\"height: 390px; width: 640px\"><param name=\"movie\" value=\"http://www.youtube.com/v/yrLNVcYz6ZM?version=3\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowScriptAccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/yrLNVcYz6ZM?version=3\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	Как и в предыдущих роликах гаджет остался цел и невредим, чехол проявил себя с лучшей стороны, а сердце замирало от увиденного. Мы ждем свой экземпляр для тестирования. Но хватит ли у нас храбрости на подобные безрассудства - вопрос пока открытый.</p>\r\n',NULL,3,1),(276,'ВИДЕО: MOPHO DJ - iPod заменит винил с таймкодом','<p>\r\n	Теперь для сведения музыки при помощи Final Scratch или Serato Scratch можно не использовать пластинки с таймкодом. Достаточно иметь сами вертушки и пару iPod Touch последнего поколения с гироскопом и акселерометром на борту. Ну и моток клейкой ленты для их соединения.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/mopho-dj.jpg\" style=\"width: 500px; height: 334px; \" /></p>\r\n<p>\r\n	Mopho DJ позволяет использовать ваши гаджеты для управления пр','<p>\r\n	Теперь для сведения музыки при помощи Final Scratch или Serato Scratch можно не использовать пластинки с таймкодом. Достаточно иметь сами вертушки и пару iPod Touch последнего поколения с гироскопом и акселерометром на борту. Ну и моток клейкой ленты для их соединения.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/mopho-dj.jpg\" style=\"width: 500px; height: 334px; \" /></p>\r\n<p>\r\n	Mopho DJ позволяет использовать ваши гаджеты для управления проигрыванием треков с той же легкостью, с какой вы используете пластинки с таймкодом или обычный винил. Акселерометр и гироскоп передают данные о положении гаджета на компьютер, где эта информация используется для определения позиции в проигрываемом треке. Вот как-то так.</p>\r\n\r\n<h2>\r\n	КАК ВЫГЛЯДИТ MOPHO DJ</h2>\r\n<p>\r\n	<br />\r\n	Безусловно, данный подход - это лишь концепт того, как можно использовать гироскоп и акселерометр в профессиональном музыкальном оборудовании. Вполне вероятно, что вскоре нечто подобное повторят именитые производители, только уже без гаджетов Apple. Вполне возможно воплотить это в форм-факторе 12&quot; виниловой пластинки, которую достаточно установить на проигрыватель и нажать кнопку &quot;Play&quot;. Никаких тебе лишних проводов - кругом один WiFi.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/PAHhJQQw7dI?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/PAHhJQQw7dI?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	Для меня, как человека тесно связанного с винилом и гаджетами Apple, эта технология стала настоящим открытием. Контроллер на базе обыкновенной пластинки не требующий иглы - это будущее, в котором найдется место и классическим проигрывателям и цифровым технологиям.</p>\r\n',NULL,3,1),(277,'ВИДЕО: Angry Birds на улицах Испании','<h2>\r\n	Angry Birds на улицах Испании</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/ZgsyCHdIiPg?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/ZgsyCHdIiPg?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></e','<h2>\r\n	Angry Birds на улицах Испании</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/ZgsyCHdIiPg?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/ZgsyCHdIiPg?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	Вот и добрались злые птицы из знойной Бразилии до не менее знойной Испании. Именно там, под живую музыку, повторяют славный подвиг японских камикадзе в птичьем обличии. Выглядит аттракцион довольно весело, особенно если учесть, что птицы взрываются по настоящему. Разваливающаяся прямо на глазах благодарной публики конструкция из разноцветных блоков вызывает ностальгию и умиление.</p>\r\n<p>\r\n	Вот так, теперь в Angry Birds можно поиграть не только на вашем смартфоне или в браузере, но и в реальных условиях. Взрывающиеся свиньи обеспечат максимум адреналина в крови и травматизма во время веселья.</p>\r\n',NULL,3,1),(278,'ВИДЕО: Концепт системы распознавания речи для iOS 5','<h2>\r\n	УДОБНО РАСПОЗНАЕМ ГОЛОС</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/hVRRMsmZ_Kg?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/hVRRMsmZ_Kg?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed><','<h2>\r\n	УДОБНО РАСПОЗНАЕМ ГОЛОС</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/hVRRMsmZ_Kg?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/hVRRMsmZ_Kg?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	Все больше слухов появляется в преддверии выхода iOS 5. Один из самых свежих и популярных - улучшенное голосовое управление вашим iOS гаджетом. Каким образом это будет выглядеть пока еще никто не знает кроме самих разработчиков. Но лучшие умы ищут способы реализации. Один из таких способов вы и можете увидеть на видео.</p>\r\n<p>\r\n	Концепт разработан Жан-Мишелем Картом, и на маленьком видео можно понять основную идею - долгое нажатие на кнопку Home переводит ваш гаджет в режим ожидания голосовой команды. Но это еще не все. Новые технологии позволят преобразовывать сказанное вами в текст, что очень удобно, когда нужно что либо набрать, а руки заняты. Фантазия дизайнера пошла еще дальше - он предлагает разрешить сторонним разработчикам использовать API сервиса для разработки своих приложений. Каково это начитывать статус на Facebook голосом? Правда больной вопрос поддержки русского языка пока остается открытым.</p>\r\n',NULL,3,1),(279,'ВИДЕО: GoFlex Satellite - беспроводная файлопомойка от Seagate ','<h2>\r\n	МНОГО ФАЙЛОВ ПО ВОЗДУХУ</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/-2W92Kh8la4?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/-2W92Kh8la4?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed><','<h2>\r\n	МНОГО ФАЙЛОВ ПО ВОЗДУХУ</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/-2W92Kh8la4?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/-2W92Kh8la4?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	Сегодня был представлен первый внешний HDD с возможностью доступа по WiFi. Seagate GoFlex Satellite позволяет хранить до 500 Gb и обмениваться файлами с вашими iOS устройствами через WiFi стандарта 802.11 b/g/n. Помимо этого он обладает батареей, которая позволяет использовать его автономно от источников питания, как самостоятельный гаджет.</p>\r\n<p>\r\n	На видео можно посмотреть обзор гаджета и примеры его взаимодействия с iPad 2. Вы легко сможете смотреть фильмы или слушать музыку не заботясь о размере файлов и их качестве. Даже видео в самом высоком разрешении теперь не проблема. Пока еще &quot;связка&quot; винчестер - устройство не доработана до идеального состояния, но то, что показано на видео уже впечатляет.</p>\r\n',NULL,3,1),(280,'ВИДЕО: Productive Macs Bundle - 8 приложений для повышения продуктивности за $29.99','<h2>\r\n	БЫТЬ ПРОДУКТИВНЫМ ТЕПЕРЬ ЛЕГКО</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/KDZcwYUgTfo?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/KDZcwYUgTfo?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></','<h2>\r\n	БЫТЬ ПРОДУКТИВНЫМ ТЕПЕРЬ ЛЕГКО</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/KDZcwYUgTfo?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/KDZcwYUgTfo?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	Если вы думаете о своей продуктивности, то следующее предложение именно для вас. Сегодня в продажу поступил уникальный комплект из 8 приложений, которые позволят вам экономить огромное количество времени. Говоря о экономии не забуду упомянуть, что цена бандла всего $29.99 за все 8 приложений. При условии, что каждое из них стоит приблизительно столько же - экономия на лицо. В видео подробно рассказывается о каждом приложении и наглядно показана польза каждого из них.</p>\r\n<p>\r\n	Вы можете заказать себе <a href=\"http://www.productivemacs.com/\"><strong>Productive Macs Bundle</strong></a> перейдя по ссылке. Я вот уже практически себе заказал. Даже если какое либо приложение вам не нужно - хотя бы одно вам точно пригодится.</p>\r\n',NULL,3,1),(281,'ВИДЕО: Запуск космического корабля снят на iPhone','<h2>\r\n	ПОСЛЕДНИЙ ПОЛЕТ ШАТЛА ENDEAVOUR</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<iframe class=\"twitvid-player\" frameborder=\"0\" height=\"360\" src=\"http://www.twitvid.com/embed.php?guid=W5SRJ&amp;autoplay=0\" title=\"Twitvid video player\" type=\"text/html\" width=\"480\"></iframe></p>\r\n<p>\r\n	Героем социальных сетей на этот раз стала пользователь твиттера <strong>Стефани Гордон</strong>.Точнее не она, а видеоролик, который она записала на свой iPhone. Ей посчастливилось заснят п','<h2>\r\n	ПОСЛЕДНИЙ ПОЛЕТ ШАТЛА ENDEAVOUR</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<iframe class=\"twitvid-player\" frameborder=\"0\" height=\"360\" src=\"http://www.twitvid.com/embed.php?guid=W5SRJ&amp;autoplay=0\" title=\"Twitvid video player\" type=\"text/html\" width=\"480\"></iframe></p>\r\n<p>\r\n	Героем социальных сетей на этот раз стала пользователь твиттера <strong>Стефани Гордон</strong>.Точнее не она, а видеоролик, который она записала на свой iPhone. Ей посчастливилось заснят последний запуск космического корабля Endeavour, который посчастливилось увидеть пассажирам самолета, на котором и летела Стефани. Она сделала несколько фото и сняла маленькое видео, которое опубликовала в твиттере по прилету.</p>\r\n<p>\r\n	Я испытываю страшную ностальгию по всему, что связано с космосом но к сожалению запуски наших ракет не вызывают такого ажиотажа. В эпоху наноинноваций можно было и заморочится на официальное освящение таких событий в интернете. Но пока все покрыто тайной и маком.</p>\r\n',NULL,3,1),(282,'ВИДЕО: Мим попал в iPhone ','<h2>\r\n	ТЮРЬМА ДЛЯ МИМА ИЗ ТРЕХ iPHONE</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<iframe frameborder=\"0\" height=\"400\" src=\"http://player.vimeo.com/video/23715004?title=0&amp;byline=0&amp;portrait=0\" width=\"630\"></iframe></p>\r\n<p>\r\n	Позитивное видео, снятое Ronen Verbit и Vanya Polunin (ну да) повествует о судьбе мима, который попал в заключение 3 гаджетов. В них он вполне вольготно себя чувствует, пока могущественная Рука не вмешивается и не создает различные испытания д','<h2>\r\n	ТЮРЬМА ДЛЯ МИМА ИЗ ТРЕХ iPHONE</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<iframe frameborder=\"0\" height=\"400\" src=\"http://player.vimeo.com/video/23715004?title=0&amp;byline=0&amp;portrait=0\" width=\"630\"></iframe></p>\r\n<p>\r\n	Позитивное видео, снятое Ronen Verbit и Vanya Polunin (ну да) повествует о судьбе мима, который попал в заключение 3 гаджетов. В них он вполне вольготно себя чувствует, пока могущественная Рука не вмешивается и не создает различные испытания для узника высоких технологий. Если без шуток, то выглядит постановка очень круто, слаженность действий персонажа просто поражает.</p>\r\n<p>\r\n	Еще раз доказано, даже наличие 3 iPhone не заменит талант, но поможет раскрыть все его грани. На моей памяти гаджет впервые использовали для такого рода постановок. Хотя был еще японский иллюзионист, который показывал фокусы при помощи iPad, но там была всего лишь ловкость рук. На этом же видео запечатлен кропотливый труд талантливых артистов.</p>\r\n',NULL,3,1),(283,'ВИДЕО: Если бы Android выглядел как WP7 ','<h2>\r\n	КАННИБАЛИЗМ СРЕДИ УБИЙЦ iPHONE</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/0S6NMH4ry-4?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/0S6NMH4ry-4?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></','<h2>\r\n	КАННИБАЛИЗМ СРЕДИ УБИЙЦ iPHONE</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/0S6NMH4ry-4?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/0S6NMH4ry-4?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	Как показывает практика, если вы хотите выпустить успешную операционную систему, вам нужно пока ее разработает Apple, а потом просто украсть все самое лучшее. Популярность Windows и Android - яркий тому пример. Но похоже в стане убийц iPhone наметились появились первые робкие попытки дружеского каннибализма. Японский опсос KDDI показала линейку Android-смартфонов, интерфейс которых странный образом похож на интерфейс Windows Phone 7.</p>\r\n<p>\r\n	Это досадное недоразумение вряд ли будет преданно широкой огласке. Фрагментация OC довольно масштабная и следить за каждым желающим изменить интерфейс Android по своему усмотрению - никакого Microsoft не хватит. Хотя данный факт говорит о признании японцами концепции редмондовской ОС. Как говорит народная мудрость:&quot;Если ты крут, у тебя по любому крадут.&quot;</p>\r\n',NULL,3,1),(284,'ВИДЕО: Тизер к выходу  Nokia N9 ','<h2>\r\n	НОВЫЙ ЕВРОПЕЙСКИЙ ФЛАГМАН</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/t-KfxrI1NXg?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/t-KfxrI1NXg?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed','<h2>\r\n	НОВЫЙ ЕВРОПЕЙСКИЙ ФЛАГМАН</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/t-KfxrI1NXg?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/t-KfxrI1NXg?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	Пока мы хороним Nokia с ее унылой собственной ОС и эфемерного будущего под крылом Microsoft, они продолжают нас радовать качественными железяками и интересными рекламными роликами. Вот еще один, на котором показаны ключевые фрагменты нового флагмана европейской компании. По всем предсказаниям смартфон будет довольно годный. Традиционно сильная аппаратная часть при поддержке ОС MeeGo - из этого может получиться отличный конкурент пластмассовым азиатским Андроидам.</p>\r\n<p>\r\n	Остается только гадать на сколько хорош будет продукт и не повторит ли он &quot;успех&quot; своего младшего брата N8, который не попинал только ленивый. Хотя для финско-американского мобильного гиганта это был гигантский шаг вперед. Так уж повелось, что у Nokia больше эволюционный подход к развитию продуктов. Их революции уже давно позади, что не мешает им оставаться на плаву и удерживать значительную часть рынка.</p>\r\n',NULL,3,1),(285,'ВИДЕО: Концепция виджетов для iPhone ','<h2>\r\n	ИНФОРМАТИВНЫЕ РАБОЧИЕ СТОЛЫ - НАШЕ ВСЕ</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/qjOV6je9dXM?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/qjOV6je9dXM?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=','<h2>\r\n	ИНФОРМАТИВНЫЕ РАБОЧИЕ СТОЛЫ - НАШЕ ВСЕ</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/qjOV6je9dXM?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/qjOV6je9dXM?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	Очередная поделка от дизайнера Жан-Мишеля Карта, который предлагает еще один концепт для iOS 5. На сей раз он коснулся еще одного слабого места - отсутствия виджетов. Собственно его концепт и предполагает их появление в следующей версии операционной системы. Действительно, зачем открывать приложение, если удобный виджет позволяет выполнить программу-минимум прям на рабочем столе.</p>\r\n<p>\r\n	Но это уже больной вопрос основного конкурента платформы, устройство под управлением которого благодаря таким виджетам работает не больше 12 часов. Вообще то отсутствие виджетов в iOS воспринимается как само собой разумеющееся. От себя добавлю, мне лично от их наличия/отсутствия ни холодно ни жарко. Наличие постоянно обновляющейся и абсолютно ненужной информации для меня не является приоритетом при работе с мобильным устройством. А вот время автономной работы и удобный интерфейс - являются.</p>\r\n',NULL,3,1),(286,'ВИДЕО: RecognizeME - опознание владельца iPhone','<h2>\r\n	МИЛОГО УЗНАЮТ ПО ОДЕЖКЕ И В ЛИЦО</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/u3dUCSvcffU?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/u3dUCSvcffU?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\">','<h2>\r\n	МИЛОГО УЗНАЮТ ПО ОДЕЖКЕ И В ЛИЦО</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/u3dUCSvcffU?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/u3dUCSvcffU?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	RecognizeMe - это первое приложение для iOS, которое позволит вам надежно защитить ваше устройство от несанкционированного доступа к нему. Пароль, который можно подсмотреть или подобрать не является эффективным средством защиты, особенно весной, когда приступы паранойи как никогда сильны. Устройство теперь распознает владельца через фронтальную камеру. В случае несоответствия желаемого и увиденного ваши данные останутся в целости и сохранности.</p>\r\n<p>\r\n	У этого способы защиты, конечно, есть много недостатков. Например, новая прическа или пластическая операция. Опять же опытный гример сможет воссоздать ваш портрет за несколько часов и тогда - прощай конфиденциальная информация. Ну и опять же, программе не важно в каком состоянии вы сейчас находитесь, во время сна ваше лицо так же пригодно для &quot;использования&quot; злоумышленниками. Ну и еще ко всему этому требуется джейлбрейк, но после вышеперечисленных недостатков это так, мелочи.</p>\r\n',NULL,3,1),(287,'ВИДЕО: Открытие первого  Apple Store 10 лет назад  ','<h2>\r\n	ПЕРВЫЙ В СВОЕМ РОДЕ</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<object height=\"390\" width=\"480\"><param name=\"movie\" value=\"http://www.youtube.com/v/xLTNfIaL5YI?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/xLTNfIaL5YI?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"480\"></embed></obj','<h2>\r\n	ПЕРВЫЙ В СВОЕМ РОДЕ</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<object height=\"390\" width=\"480\"><param name=\"movie\" value=\"http://www.youtube.com/v/xLTNfIaL5YI?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/xLTNfIaL5YI?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"480\"></embed></object></p>\r\n<p>\r\n	Ровно 10 лет назад был открыт первый Apple Store. Первый магазин, который ознаменовал новую эпоху продаж компьютерной техники. Это место поражало минимальным количеством техники и максимальным количеством впечатлений от ее использования. И вот через 10 лет Apple Store будет обновлен до версии 2.0, то есть станет еще минималистичнее, удобнее и настроенным на обратную связь с потребителем. Ходят слухи о внедрении Mac OS Lion и iPad для сотрудников, больших плазменных экранах вместо проекторов и приложении для iOS, которое поможет посетителям ориентироваться в магазине.</p>\r\n<p>\r\n	Но взгляните на самый первый магазин, разве какой либо технический апгрэйд сможет еще больше усилить эффект от уже существующего? Не думаю. Если нас и ждут какие либо масштабные изменения то это должны быть действительно &quot;Изменения&quot; именно с большой буквы. Ведь Apple это та компания, которая привыкла совершать революции, их эволюционный рост уже воспринимается как само собой разумеющееся. Впрочем, ждать осталось недолго. Скоро мы все увидим своими глазами.</p>\r\n',NULL,3,1),(288,'ВИДЕО: Qualcomm принесет дополненную реальность на iOS ','<h2>\r\n	ПОЛЕ ДЛЯ ИГР ВСЕГДА ПОД РУКОЙ</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/N-pvrZZ0PlE?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/N-pvrZZ0PlE?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></e','<h2>\r\n	ПОЛЕ ДЛЯ ИГР ВСЕГДА ПОД РУКОЙ</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/N-pvrZZ0PlE?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/N-pvrZZ0PlE?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	После небывалого успеха на ниве средств разработки приложений дополненной реальности для Android, компания Qualcomm все таки решила обратить свой взор на вторую по аудитории и первую по монетизации и успешности мобильную ОС. В связи с этим в скором времени будет выпущен специальный SDK для разработки приложений дополненной реальности для iOS от вышеупомянутой компании.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/8H58vRx75Kk?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/8H58vRx75Kk?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	На втором видео можно увидеть работу победителей конкурса на разработку самого лучшего приложения для Android с использованием средств разработки от Qualcomm. Уже представляю себе игры, использующие в роли специального маркера обыкновенный ковер. Не знаю как играть, но со стороны будет выглядеть крайне забавно.</p>\r\n',NULL,3,1),(289,'ВИДЕО: Новая порция игр для котов ','<h2>\r\n	БЕСПЛАТНЫЕ ИГРЫ В БРАУЗЕРЕ</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<object height=\"390\" width=\"480\"><param name=\"movie\" value=\"http://www.youtube.com/v/vaif2uq_0Vc?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/vaif2uq_0Vc?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"480\"></embe','<h2>\r\n	БЕСПЛАТНЫЕ ИГРЫ В БРАУЗЕРЕ</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<object height=\"390\" width=\"480\"><param name=\"movie\" value=\"http://www.youtube.com/v/vaif2uq_0Vc?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/vaif2uq_0Vc?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"480\"></embed></object></p>\r\n<p>\r\n	Вашему коту надоели старые игры на его iPad? Порадуйте вашего домашнего любимца тремя новыми играми от Friskies которые не требуют установки. В них можно поиграть прямо в вашем браузере. Для этого достаточно зайти на ресурс <a href=\"gamesforcats.com\">gameforcats</a>и выбрать понравившуюся из трех игру.</p>\r\n<p>\r\n	К сожалению у меня не было возможности лично убедится в интересе котов к играм в связи с отсутствием четвероногого хвостатого чуда. Но судя по реакции котовладельцев, игры таки годные для мяукающих созданий. Впрочем, основной целевой аудитории планшетника - детям до 3 лет, игры тоже покажутся забавными и интересными. Это проверено.</p>\r\n',NULL,3,1),(290,'ВИДЕО: Imaginary Interfaces - управление iPhone не касаясь экрана  ','<p>\r\n	Imaginary Interfaces - это исследовательский проект Шона Густафсона, Даниеля Бирвирта и Патрика Баудиха в HPI Потсдам, Германия. Технология подразумевает управление интерфейсом устройства без прикосновений и визуального восприятия. Всю визуализацию вы придумываете самостоятельно, иногда в прямом смысле, для нажатие кнопки вам придется ее сначала нарисовать. Эта технология позволит значительно уменьшить размер мобильных устройств, а возможно и вовсе оставить их без экранов, доверив управлен','<p>\r\n	Imaginary Interfaces - это исследовательский проект Шона Густафсона, Даниеля Бирвирта и Патрика Баудиха в HPI Потсдам, Германия. Технология подразумевает управление интерфейсом устройства без прикосновений и визуального восприятия. Всю визуализацию вы придумываете самостоятельно, иногда в прямом смысле, для нажатие кнопки вам придется ее сначала нарисовать. Эта технология позволит значительно уменьшить размер мобильных устройств, а возможно и вовсе оставить их без экранов, доверив управление устройством одним лишь жестам.</p>\r\n<h2>\r\n	УПРАВЛЯЕМ iPHONE НЕ КАСАЯСЬ РУКАМИ</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/aCARtauIS50?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/aCARtauIS50?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	На видео показана уже реальная технология, которая была создана исключительно ради эксперимента. Выше названные разработчики решили проверить, насколько хорошо пользователи iPhone помнят расположение элементов своего гаджета. Опыт показал довольно хороший результат - больше 60 процентов респондентов не испытывали трудностей, управляя смартфоном нажимая на воображаемый экран. Вот они, пресловутые гайдлайны по проектированию интерфейсов, пригодились в абсолютно непредсказуемом проекте. С Андроидом такой номер бы вряд ли прошел.</p>\r\n\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/718RDJeISNA?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/718RDJeISNA?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	Более подробно о самой технологии и принципах работы можно узнать из второго видео. На нем молодые ученые представляют свой проект и рассказывают о перспективах использования разработанной ими технологии. Поле для экспериментов воистину огромно. Чего будут стоить только огромные прозрачные экраны, не требующие прикосновений. Отсюда и до отказа от сенсорных датчиков не далеко. А если учесть тягу всем нам известной яблочной компании к такого рода революционным открытиям, то не удивлюсь, если ребята в скором времени сменят тихий Потсдам на солнечный Купертино.</p>\r\n',NULL,3,1),(291,'ВИДЕО: 3D экран для iOS устройств без использования очков','<h2>\r\n	ТЕХНОЛОГИЯ ХЭД ТРЭКИНГА ДЛЯ iOS</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/bBQQEcfkHoE?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/bBQQEcfkHoE?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"><','<h2>\r\n	ТЕХНОЛОГИЯ ХЭД ТРЭКИНГА ДЛЯ iOS</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/bBQQEcfkHoE?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/bBQQEcfkHoE?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	Об этой технологии мы рассказывали еще зимой. Появилась она в далеком 2007 году, когда 3D эффекта удалось добиться используя Nintendo Wii и два источника инфракрасного излучения, прикрепленных к голове экспериментатора (скотчем, чем же еще). С тех пор прогресс не стоял на месте и на стало возможным использовать обыкновенную фронтальную камеру вашего iPhone вместо приемника и отказаться от каких либо источников излучения.</p>\r\n<p>\r\n	Опять же в качестве эксперимента было разработано приложение i3D, которое совершенно бесплатно можно скачать из AppStore и самому убедится в крутости этой технологии. Огорчает только тот факт, что на iPhone 4 мне так и не удалось достойного результата. То ли мое лицо не подходящего формата, или свет падал не так как нужно, но повторить нечто подобное, что вы можете увидеть на видео мне не удалось. Возможно вам повезет больше.</p>\r\n',NULL,3,1),(292,'ВИДЕО: Opera Mini - теперь и для iPad ','<h2>\r\n	СКОРОСТНОЙ СЕРФИНГ И УДОБНЫЙ ИНТЕРФЕЙС</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/CD0IwRSpMSs?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/CD0IwRSpMSs?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=','<h2>\r\n	СКОРОСТНОЙ СЕРФИНГ И УДОБНЫЙ ИНТЕРФЕЙС</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/CD0IwRSpMSs?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/CD0IwRSpMSs?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	Вот и дождались фанаты Opera выхода их любимого браузера для iPad. Впрочем, все остальные тоже дождались, хотя ничего нового браузер не принес. Хотя такие плюшки, как Opera Turbo и функция &quot;Толстые Пальцы&quot; дорогого стоят. Удобная навигация по вкладкам, без необходимости уходить с читаемой страницы. А еще можно быстро поделиться ссылкой в твиттере или Facebook. Ну на этом положительные черты нового браузера и заканчиваются. И начинаются непонятные мне курьезы.</p>\r\n<p>\r\n	Во первых видео. В борьбе за с лишним трафиком разработчики решили вынести проигрывание видео в соответствующее приложение. Так видео с Youtube, которое располагается на этой странице вы сможете посмотреть только в специальном приложении на вашем iPad. Не дай бог видео будет расположено на Vimeo - его вы не увидите вовсе. Возможно это лечится, но если такого пункта нет в настройках, то дальше искать мне уже не интересно.</p>\r\n<p>\r\n	Ну а интерфейс для слабозрячих меня просто повеселил. Гигантские кнопки управления, нарисованные против всех законов физики и гайдлайнов по интерфейсу, оборванные на полуслове пункты меню и обрезание сайтов по ширине... Хочется закрыть лицо руками и подождать пока кошмар развеется. А вот Яндекс, как поисковик по умолчанию - это хорошо.</p>\r\n',NULL,3,1),(293,'ВИДЕО: Smart Cover спасает от ваш iPad 2 при падении на асфальт ','<h2>\r\n	НАСКОЛЬКО УМЕН SMART COVER</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/_vrJ83dEec4?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/_vrJ83dEec4?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embe','<h2>\r\n	НАСКОЛЬКО УМЕН SMART COVER</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/_vrJ83dEec4?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/_vrJ83dEec4?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	Если рекламные ролики супер-чехла g-form обходятся без последствий, то этот видео ролик посвящен именно разрушению устройств и тестированию их прочности. Так незащищенный iPad 2, выпавший из вашей сумки и плашмя упавший на асфальт скорее всего отделается разбитым экраном. Smart Cover же в этой ситуации сбережет экран вашего планшета.</p>\r\n<p>\r\n	В любом случае более экстремальное падение суперобдожка не выдержит и устройство придется ремонтировать. Ну а о фронтальном ударе клюшкой для гольфа и говорить нечего. Такое под силу выдержать только специальным защитным чехлам. Хотя если на удар выйдет Тайгер Вудс - результат сложно предугадать.</p>\r\n',NULL,3,1),(294,'ВИДЕО: Dot 360 - снимаем видео панорамы на iPhone 4 ','<h2>\r\n	ПАНОРАМА ЛУЧШЕ 10 ФОТОГРАФИЙ</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<iframe frameborder=\"0\" height=\"410px\" src=\"http://www.kickstarter.com/projects/dot/dot-360o-video-capture-for-the-iphone-4/widget/video.html\" width=\"480px\"></iframe></p>\r\n<p>\r\n	Очередной революционный проект на Kikstarter, на сей раз это гаджет для вашего iPhone, который позволит вам легко снимать панорамы вашим смартфоном. Оказались в красивом месте - порадуйте друзей атмосферным видео вмест','<h2>\r\n	ПАНОРАМА ЛУЧШЕ 10 ФОТОГРАФИЙ</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<iframe frameborder=\"0\" height=\"410px\" src=\"http://www.kickstarter.com/projects/dot/dot-360o-video-capture-for-the-iphone-4/widget/video.html\" width=\"480px\"></iframe></p>\r\n<p>\r\n	Очередной революционный проект на Kikstarter, на сей раз это гаджет для вашего iPhone, который позволит вам легко снимать панорамы вашим смартфоном. Оказались в красивом месте - порадуйте друзей атмосферным видео вместо нескольких однообразных фотографий. Технология, которую использует гаджет довольно проста, но она требует специального приложения которое и будет преобразовывать исходное видео с камеры в панорамную картину.</p>\r\n<p>\r\n	У каждого есть шанс поучаствовать. От вас потребуется $99 за которые вы получите сам девайс, специальное приложение и приглашение на вечеринку в честь запуска продаж. Сам запуск состоится в Августе и судя по количеству уже собранных денег, в том, что проект будет реализован, сомневаться не приходится. Сама технология управляемых панорамных камер была разработана для слежения за учащимися в учебных заведениях, теперь же поле примения ее просто безгранично.</p>\r\n',NULL,3,1),(295,'ВИДЕО: Mango - обновление для Windows Phone 7 ','<h2>\r\n	НОВЫЕ ТРЮКИ И СТАРЫЕ ПРОБЛЕМЫ</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/OP30F3ZxTmw?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/OP30F3ZxTmw?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></e','<h2>\r\n	НОВЫЕ ТРЮКИ И СТАРЫЕ ПРОБЛЕМЫ</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/OP30F3ZxTmw?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/OP30F3ZxTmw?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	Windows Phone 7 появилась совсем недавно, но уже решила показать какое будущее ожидает энтузиастов, которые решаться попробовать смартфоны с WP7 на борту. Джо Бэлфьери в лучших традициях господина Балмера (поддельный Стив) делит шкуру неубитого медведя и показывает некоторые плюшки, которые должный войти в грядущий этой осенью апдэйт мобильной Windows. Растянутый на десять минут ролик повествует об удобстве почты, многозадачности, скорости браузера и много чем еще. Правда впечатления остаются довольно неоднозначными.</p>\r\n<p>\r\n	Есть такая пословица:&quot;Дураку пол работы не показывают&quot;. Есть компании, которые строго придерживаются этого правила, другие же регулярно его опровергают. К сожалению, Microsoft относится к последним. В мире сложилась негативная тенденция, когда создатели мобильных ОС выпускают продукты, которые потом &quot;допиливают&quot; с разной степенью успешности при помощи миллионов добровольных тестеров. Google вообще половину своих продуктов держит в статусе перманентной беты, чтобы исключить претензии со стороны пользователя. Но и предоставляет свои бета-сервисы бесплатно, что делает им честь.</p>\r\n<p>\r\n	У Microsoft в этом плане свой взгляд на происходящее. Они выпустили Windows Phone 7 с функционалом на уровне 2007 года и без зазрения совести начали продажи. За пол года за релиза они показывают готовую свежую версию ОС, которую они не выпустят до релиза iOS 5. Боюсь, как бы Манго не постигла судьба HP Slate, который так и не поступил в продажу, хотя был точно так же показан за долго до выхода. Ведь даже с выходом свежего обновления мобильная Windows будет дотягивать до ОС 2010 года но конкуренты ведь не сидят сложа руки. Так что будем наблюдать за гонкой &quot;вооружений&quot;, благо от такой конкуренции выигрывает только пользователь.</p>\r\n',NULL,3,1),(296,'ВИДЕО: NowStream - потоковое видео на вашем iOS устройстве ','<h2>\r\n	СМОТРЕТЬ ТОРРЕНТ ФИЛЬМЫ ОНЛАЙН</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<object height=\"510\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/b2zYG5ec5yg?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"510\" src=\"http://www.youtube.com/v/b2zYG5ec5yg?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></','<h2>\r\n	СМОТРЕТЬ ТОРРЕНТ ФИЛЬМЫ ОНЛАЙН</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<object height=\"510\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/b2zYG5ec5yg?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"510\" src=\"http://www.youtube.com/v/b2zYG5ec5yg?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	С появлением сервиса NowStream у вас появится возможность смотреть любой фильм онлайн, при наличии ссылки на торрент файл с этим фильмом. К сожалению у меня пока нет возможности протестировать данный сервис самостоятельно, так что я полагаюсь лишь на короткое демо-видео. Сам принцип работы довольно прост: вы предоставляете сервису прямую ссылку на требуемый торрент файл и в ответ получаете потоковое видео на свой гаджет.</p>\r\n<p>\r\n	Пока что сервис находится в разработке и тестировании, но уже скоро у каждого появится возможность лично убедится в работоспособности этой технологии. Для получения большей информации по теме рекомендую подписаться на уведомление о начале работы на <a href=\"http://nowstre.am\"><strong>сайте проекта</strong></a>.</p>\r\n',NULL,3,1),(297,'ВИДЕО: Гитарное соло на iPad 2 ','<h2>\r\n	GARAGE BAND - МУЗЫКАЛЬНЫЙ КОЛЛЕКТИВ В ОДИНОЧКУ</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<object height=\"480\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/E_KYUF6pxR8?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"480\" src=\"http://www.youtube.com/v/E_KYUF6pxR8?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash','<h2>\r\n	GARAGE BAND - МУЗЫКАЛЬНЫЙ КОЛЛЕКТИВ В ОДИНОЧКУ</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<object height=\"480\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/E_KYUF6pxR8?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"480\" src=\"http://www.youtube.com/v/E_KYUF6pxR8?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	Уверен, что каждый, кто хоть раз в руках держал гитару и iPad уже попробовал себя в музыкальной импровизации, а возможно и начал использовать Garage Band для чего-то более серьезного. Для тех же, кто мало знаком с возможностями карманной студии звукозаписи - этот сюжет.</p>\r\n<p>\r\n	Вообще-то первую минуту автор разогревался и подготавливал основу композиции, так что у вас может сложится впечатление о унылости всего ролика. Ничего подобного. Самое интересное начинается именно в середине, когда начинается гитарное соло. Скажу честно, даже не любителям гитары стоит послушать или хотя бы посмотреть на что способен iPad 2 и Garage Band.</p>\r\n',NULL,3,1),(298,'ВИДЕО: DJ Miguel - и такую музыку играют на iPad ','<h2>\r\n	ЭТО ЕЩЕ НЕ ЖЕСТЬ, НО ОЧЕНЬ БЛИЗКО</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/NoNoh2oFMgc?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/NoNoh2oFMgc?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"','<h2>\r\n	ЭТО ЕЩЕ НЕ ЖЕСТЬ, НО ОЧЕНЬ БЛИЗКО</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/NoNoh2oFMgc?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/NoNoh2oFMgc?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	В продолжение музыкальной темы привожу вам пример как не стоит использовать iPad. Это напоминает забивание шурупов микроскопом, когда результат достигнут, а ощущения странные. Здесь нечего комментировать - смотреть и удивляться человеческой фантазии и полному отсутствию вкуса.</p>\r\n<p>\r\n	Надеюсь, летний сезон в отечественных заведениях на побережье черного моря, не будет перенасыщен подобными умельцами с планшетами в руках. Хотя кто знает, возможно именно iPad станет самым популярным музыкальным инструментом этого года.</p>\r\n',NULL,3,1),(299,'ВИДЕО: ASUS Padfone - первое знакомство ','<h2>\r\n	КАК МИНИМУМ СТРАННОЕ УСТРОЙСТВО</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/mRcx1rxTLG8?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/mRcx1rxTLG8?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"><','<h2>\r\n	КАК МИНИМУМ СТРАННОЕ УСТРОЙСТВО</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/mRcx1rxTLG8?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/mRcx1rxTLG8?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	Прошло уже больше года с тех пор, как мир увидел Apple iPad. И вот азиатские конкуренты понявшие всю утопичность своих попыток создания армии клонов да убийц и взялись за креатив. До работающих прототипов или, не дай бог, устройств дело пока не дошло. Пока что компания ASUS радует нас первым рекламным роликом, на котором показана первая модель их гибридного планшета-смартфона под названием Padfone. В официальном пресс-релизе абсолютно ничего не сказано о технических характеристиках устройства, производители ограничились лишь упоминанием о 3G, революции в мире гаджетов и симбиозе устройств в одном гаджете. Как все это сможет выглядеть - смотрите на видео.</p>\r\n<p>\r\n	Вообще не секрет, что реклама призвана играть на самых базовых инстинктах человека, вызывая желание купить рекламируемый товар, чтобы решить одну из существующих проблем покупателя. Но эти основы маркетинга ускользнули от создателей рекламного ролика. Два варианта, либо данное видео собираются показывать в позапрошлом году, или от маркетологов ASUS ускользнуло засилье яблочной техники в массах. Особо порадовал момент с девушками. Как нельзя кстати пришелся хит дня из твиттера: &quot;В сознании любого мужчины iPad= +15 см к члену&quot;. Вот только прокатит ли этот трюк с азиатским &quot;айпэдозаменителем&quot;? Вообще концепт модульной системы уже давно обговаривается в массах, каждый второй не отказался бы от портативного компьютера размером с современный смартфон, который можно было бы подключить к монитору и полноценно работать. Но, на мой взгляд, планшет слегка мелковат для такого подключения.</p>\r\n',NULL,3,1),(300,'ВИДЕО: А-а-а! Андроид! Бей его! ','<h2>\r\n	БЬЮТ - ЗНАЧИТ ЗАСЛУЖИЛ</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/mcr2uWkJkzI?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/mcr2uWkJkzI?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></','<h2>\r\n	БЬЮТ - ЗНАЧИТ ЗАСЛУЖИЛ</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/mcr2uWkJkzI?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/mcr2uWkJkzI?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	Тут собственно говорить практически нечего - стоит просто смотреть. Делаем громкость на максимум - это позволит вам прочувствовать весь букет впечатлений пользователей iOS при виде смартфонов с Android.</p>\r\n<p>\r\n	Если серьезно, то реклама прост супер, только е понятно, что именно она рекламирует. Может под рекламой сверхчеткого дисплея кроется нечто большее?</p>\r\n',NULL,3,1),(301,'ОФИЦИАЛЬНО: Стив Джобс представит Mac OS Lion, iOS 5 и iCloud 6 июня на WWDC\'11','<p>\r\n	Сегодня стало официально известно, что 6 июня, в первый день всемирной конференции разработчиков, Стив Джобс лично представит новые продукты компании. Из ожидаемых продуктов, нам покажут финальную версию Mac OS Lion и первую версию iOS 5.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/wwdc-11.jpg\" style=\"width: 600px; height: 448px; \" /></p>\r\n<p>\r\n	Из необычного - будет сервис iCloud, от которого пока неизвестно что ожидать. Давайте разберем','<p>\r\n	Сегодня стало официально известно, что 6 июня, в первый день всемирной конференции разработчиков, Стив Джобс лично представит новые продукты компании. Из ожидаемых продуктов, нам покажут финальную версию Mac OS Lion и первую версию iOS 5.</p>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<img alt=\"\" src=\"/sites/default/files/wwdc-11.jpg\" style=\"width: 600px; height: 448px; \" /></p>\r\n<p>\r\n	Из необычного - будет сервис iCloud, от которого пока неизвестно что ожидать. Давайте разберем по пунктам.</p>\r\n<h2>\r\n	Mac OS Lion</h2>\r\n<p>\r\n	<br />\r\n	Об основных функциях новой операционной системы уже говорено и переговорено, снято огромное количество видео, и написано большое количество инструкций. Из самого нового: обновление будет доступно для покупки в Mac App Store, возможно даже во время выступления Стива Джобса. Пока еще не ясно как будет выглядеть финальная версия, ведь все вышедшие Developer Preview версии отличались друг от друга. Что же, давайте дождемся понедельника и все узнаем.</p>\r\n<h2>\r\n	iOS 5</h2>\r\n<p>\r\n	<br />\r\n	О том, что новая версия мобильной операционной системы будет иметь цифру 5 в названии - это тоже слухи. Об остальном даже нечего говорить. Ожидается система виджетов, новая система уведомлений и многозадачности. Но это из разряда слухов и пожеланий. О более реальном - ждем поддержку облачной или хотя бы беспроводной синхронизации с iTunes, больше дополненной реальности поддержка 3D в каком либо виде.</p>\r\n<h2>\r\n	iCloud</h2>\r\n<p>\r\n	<br />\r\n	Вот этот сервис остается загадкой, за которой слухи стоят плотной стеной. Новейшее предположение журналистов - сервис будет предоставлять видео и телепередачи для потокового просмотра. И это в довесок к ожидаемой облачной музыкальной медиатеке и беспроводной синхронизации. В конце концов, все может свестись к банальному ребрендингу некоторых облачных функций MobileMe, оставив ветерану облачных технологий почту, календарь и ставший бесполезным с приходом dropbox - iDisc.</p>\r\n<p>\r\n	Напоминаю, что обо всех новинках вы сможете узнать из нашей твиттер трансляции, которую мы по обыкновению будем вести одновременно с представлением новых продуктов. До встречи в понедельник.</p>\r\n',NULL,3,1),(302,'ВИДЕО: Asus UX21 - дежавю или клон MacBook Air ','<h2>\r\n	ЧЕРТОВCКИ ПОХОЖ</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/-aLwBxaNDiA?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/-aLwBxaNDiA?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object>','<h2>\r\n	ЧЕРТОВCКИ ПОХОЖ</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/-aLwBxaNDiA?fs=1&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/-aLwBxaNDiA?fs=1&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	На выставке Computex 2011 помимо всего прочего был показан и ультратонкий лэптоп от ASUS, который своим внешним видом отчаянно напоминает новый &quot;воздушный&quot; MacBook. Это стоит увидеть своими глазами, тем более ребята из Engadget предоставили возможность воочию убедиться в схожести ноутбуков. Да, материал корпуса отличается, но первоисточник заметен с первого взгляда. Воистину, не смог превзойти - скопируй.</p>\r\n<p>\r\n	Вот только не смотря на довольно мощную техническую начинку у мобильного устройства есть несколько минусов. Во первых - цена. Такой гаджет явно не будет ей отличаться от яблочного собрата. Во вторых - батарея. О ней не сказано ничего, и на сколько хватит этой блестящей игрушки пока неизвестно. Ну и третий и самый важный фактор - он будет работать под управлением Windows, со всеми ее проблемами и преимуществами. А это значит, что конкурировать он сможет лишь в среде таких же клонов от других азиатских производителей, слегка разбавленных американскими. Напомню, что представленное на выставке устройство не обязательно пойдет в серию, но такое беспрецедентное копирование говорит о многом. Например о том, кто сегодня задает тон в сфере портативных компьютеров.</p>\r\n',NULL,3,1),(303,'ВИДЕО: Обзор новых функций в iOS 5 ','<h2>\r\n	ОФИЦИАЛЬНОЕ ПРЕДСТАВЛЕНИЕ</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<iframe allowfullscreen=\"\" frameborder=\"0\" height=\"377\" src=\"http://www.youtube.com/embed/LLJIef-e-7g?rel=0\" width=\"610\"></iframe></p>\r\n<p>\r\n	Не в наших правилах повторяться и подробный отчет о новинках, представленных на открытии WWDC&#39;11 вы можете легко найти на сайте Apple и в нашей твиттер трансляции. Сейчас же хотелось показать именно это видео, на котором очень доступно показаны те функц','<h2>\r\n	ОФИЦИАЛЬНОЕ ПРЕДСТАВЛЕНИЕ</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<iframe allowfullscreen=\"\" frameborder=\"0\" height=\"377\" src=\"http://www.youtube.com/embed/LLJIef-e-7g?rel=0\" width=\"610\"></iframe></p>\r\n<p>\r\n	Не в наших правилах повторяться и подробный отчет о новинках, представленных на открытии WWDC&#39;11 вы можете легко найти на сайте Apple и в нашей твиттер трансляции. Сейчас же хотелось показать именно это видео, на котором очень доступно показаны те функции, о которых так уныло вещали на конференции. В конкретном случае это нововведения в iOS 5, которые так &quot;должны&quot; выглядеть.</p>\r\n<p>\r\n	К сожалению пока руки не дошли до самоличного тестирования беты iOS 5, но по отзывам пробовавших - все еще очень нестабильно и багов имеет довольно много. Впрочем, на то она и бета, чтобы эти баги в ней отлавливать. Удивляет только нездоровый ажиотаж вокруг этих бета-версий. На что только не пойдут пользователи заморских смартфонов дабы заполучить долгожданную прошивку, которая официально выйдет не раньше сентября.</p>\r\n',NULL,3,1),(304,'ВИДЕО: Обзор функций OS X Lion ','<h2>\r\n	ПОСЛЕДНЯЯ БЕТА</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/tq2enQYTrbU?version=3&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/tq2enQYTrbU?version=3&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed>','<h2>\r\n	ПОСЛЕДНЯЯ БЕТА</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/tq2enQYTrbU?version=3&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/tq2enQYTrbU?version=3&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	Все это вы уже видели, если следили за ситуацией с OS X Lion. Обо всех этих функциях мы уже писали и сами лично тестировали. Теперь вышла вроде как последняя бета-версия, которая именуется Developer Preview 4 и которая уже должна действительно хорошо работать. Как сказал один мой знакомый: &quot;Разработка уже закончилась, теперь начинаются фиксы&quot;.</p>\r\n<p>\r\n	Мы же даем вам возможность убедиться в очередной раз, что новое творение инженеров из Купертино достойно внимания, ожидания Июля и $29,99 за обновление из Mac App Store.</p>\r\n',NULL,3,1),(305,'ВИДЕО: Джонатан Манн спел о презентации Apple 6 июня на открытии WWDC\'11 ','<h2>\r\n	ТАК НАМНОГО ВЕСЕЛЕЕ</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/xmxQLVE0cPk?version=3&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/xmxQLVE0cPk?version=3&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></e','<h2>\r\n	ТАК НАМНОГО ВЕСЕЛЕЕ</h2>\r\n<p class=\"rtecenter\" style=\" text-align: center;\">\r\n	<br />\r\n	<object height=\"390\" width=\"640\"><param name=\"movie\" value=\"http://www.youtube.com/v/xmxQLVE0cPk?version=3&amp;hl=ru_RU\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"allowscriptaccess\" value=\"always\" /><embed allowfullscreen=\"true\" allowscriptaccess=\"always\" height=\"390\" src=\"http://www.youtube.com/v/xmxQLVE0cPk?version=3&amp;hl=ru_RU\" type=\"application/x-shockwave-flash\" width=\"640\"></embed></object></p>\r\n<p>\r\n	Всем кто не видел еще оригинальную версию презентации и не следил за нашей трансляцией в твиттере заранее говорим - скука была жуткая. Так долго и нудно еще не представляли не один продукт и даже не смотря на появление Стива Джобса и громадный объем новинок было не очень интересно.</p>\r\n<p>\r\n	В свою очередь Джонатан Манн сумел ужать весь гигантский объем информации до трех с небольшим минут и облек это в стихотворно-музыкальную форму. Проще говоря, спел все то, что топ менеджеры Apple пытались растолковать всем около двух часов. Рекомендовано к просмотру и прослушиванию всеми, кто ценит свое время и уважает чужой труд.</p>\r\n',NULL,3,1),(306,'iPad 4 разобрали.','Ну вот дошла очередь и до iPad 4. Ребята из iFixit разобрали новый iPad 4-го поколения. Основные отличия iPad 4-го поколения от 3-го яэто более мощный чип A6X и новый разъем для кабеля Молния.\r\n\r\nЧто интересного внутри iPad 4:\r\nМаркировка чипа A6X показывает, что он был изготовлен ​​в середине сентября.\r\nИзготовителем дисплея является компания LG, в iPad 3 это был Samsung.\r\nФронтальная камера стала чуть толще, чем в предыдущей модели.\r\nБатарея осталась точно такой же, как в третьем поколении iPa','<p>&nbsp;</p>\r\n<div><span style=\"font-size:16px;\"><strong>iPad 4 разобрали.</strong></span></div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://cs309422.userapi.com/v309422523/5b6a/GuQkvcLbdBo.jpg\" style=\"width: 700px; height: 525px; \"></div>\r\n<div>&nbsp;</div>\r\n<div>Ну вот дошла очередь и до iPad 4. Ребята из iFixit разобрали новый iPad 4-го поколения. Основные отличия iPad 4-го поколения от 3-го яэто более мощный чип A6X и новый разъем для кабеля Молния.</div>\r\n<div>&nbsp;</div>\r\n<div><strong>Что интересного внутри iPad 4:</strong></div>\r\n<ul style=\"margin-left: 40px; \">\r\n	<li>Маркировка чипа A6X показывает, что он был изготовлен ​​в середине сентября.</li>\r\n	<li>Изготовителем дисплея является компания LG, в iPad 3 это был Samsung.</li>\r\n	<li>Фронтальная камера стала чуть толще, чем в предыдущей модели.</li>\r\n	<li>Батарея осталась точно такой же, как в третьем поколении iPad.</li>\r\n	<li>Оперативная память 1 ГБ, она расположена в двух чипах Elpida отдельно от A6X.</li>\r\n</ul>\r\n<p><img alt=\"\" src=\"http://cs309422.userapi.com/v309422523/5b73/-6h3879sM34.jpg\" style=\"width: 700px; height: 525px; \"></p>\r\n',NULL,3,1),(307,'ВИДЕО: Дроп-тест iPad mini vs Nexus 7','<p><span style=\"font-size:16px;\"><strong>Дроп-тест iPad mini vs Nexus 7</strong></span></p>\r\n<p>&nbsp;</p>\r\n<p><iframe allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"http://www.youtube.com/embed/ie6-8rrvXOY\" width=\"560\"></iframe></p>\r\n<p>&nbsp;</p>\r\n<p>Ребята из Android Authority засняли дроп-тест iPad mini и Nexus 7. После трех падений, у обоих устройств экраны были разбиты, но iPad mini продолжал функционировать в отличие от Nexus 7.</p>\r\n','<p><span style=\"font-size:16px;\"><strong>Дроп-тест iPad mini vs Nexus 7</strong></span></p>\r\n<p>&nbsp;</p>\r\n<p><iframe allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"http://www.youtube.com/embed/ie6-8rrvXOY\" width=\"560\"></iframe></p>\r\n<p>&nbsp;</p>\r\n<p>Ребята из Android Authority засняли дроп-тест iPad mini и Nexus 7. После трех падений, у обоих устройств экраны были разбиты, но iPad mini продолжал функционировать в отличие от Nexus 7.</p>\r\n',NULL,3,1),(308,'Почему Боб Мэнсфилд остался в Apple.','Популярный яблочный интернет-портал AllThingsD опубликовал информацию, в которой объясняется, почему Боб Мэнсфилд остался в Apple. Напомним, что в начале этого года  Боб хотел уйти в отставку. После кадровой перестановки он назначен главой специального отдела компании \"Технологии\", который занимается разработкой новых технологий, а также является консультантом CEO Apple Тима Кука. \r\nAllThingsD пишет:\r\nКак сообщил один источник, близкий к компании Apple – сроки возвращения Боба Мэнсфилда не случа','<p>&nbsp;</p>\r\n<div><span style=\"font-size:16px;\"><strong>Почему Боб Мэнсфилд остался в Apple.</strong></span></div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://cs309422.userapi.com/v309422523/5bc0/51X27Dl9DZ4.jpg\" style=\"width: 550px; height: 378px; \"></div>\r\n<div>&nbsp;</div>\r\n<div>Популярный яблочный интернет-портал AllThingsD опубликовал информацию, в которой объясняется, почему Боб Мэнсфилд остался в Apple. Напомним, что в начале этого года &nbsp;Боб хотел уйти в отставку. После <a href=\"http://macov.net/news/kadrovye-izmineniya-apple\">кадровой перестановки</a> он назначен главой специального отдела компании \"Технологии\", который занимается разработкой новых технологий, а также является консультантом CEO Apple Тима Кука.&nbsp;</div>',NULL,3,1),(309,'ВИДЕО: Дроп тест iPad mini, iPad 3 и Nexus 7.','<p><span style=\"font-size:16px;\"><strong><span style=\"font-family: tahoma, arial, verdana, sans-serif, \'Lucida Sans\'; line-height: normal; \">Дроп тест iPad mini, iPad 3 и Nexus 7.</span></strong></span></p>\r\n<p>&nbsp;</p>\r\n<p><iframe allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"http://www.youtube.com/embed/HbHomq4QwIk\" width=\"560\"></iframe></p>\r\n','<p><span style=\"font-size:16px;\"><strong><span style=\"font-family: tahoma, arial, verdana, sans-serif, \'Lucida Sans\'; line-height: normal; \">Дроп тест iPad mini, iPad 3 и Nexus 7.</span></strong></span></p>\r\n<p>&nbsp;</p>\r\n<p><iframe allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"http://www.youtube.com/embed/HbHomq4QwIk\" width=\"560\"></iframe></p>\r\n',NULL,3,1),(310,'ВИДЕО: Как изменяются продукты Apple?','<p><span style=\"font-size:16px;\"><strong>Как изменяются продукты Apple?</strong></span></p>\r\n<p>&nbsp;</p>\r\n<p><iframe allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"http://www.youtube.com/embed/RyWSEwKPo8s\" width=\"560\"></iframe></p>\r\n<p>В Америке есть очень популярное шоу Джимми Киммела на канале ABC. В последней передаче он показал видео-ролик, показывающий как изменяются продукты Apple. Получилось очень смешно, наслаждайтесь просмотром!</p>\r\n','<p><span style=\"font-size:16px;\"><strong>Как изменяются продукты Apple?</strong></span></p>\r\n<p>&nbsp;</p>\r\n<p><iframe allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"http://www.youtube.com/embed/RyWSEwKPo8s\" width=\"560\"></iframe></p>\r\n<p>В Америке есть очень популярное шоу Джимми Киммела на канале ABC. В последней передаче он показал видео-ролик, показывающий как изменяются продукты Apple. Получилось очень смешно, наслаждайтесь просмотром!</p>\r\n',NULL,3,1),(311,'ВИДЕО: Скорость работы iPad4 и iPad3.','<p>&nbsp;</p>\r\n<div><span style=\"font-size:16px;\"><strong>Скорость работы iPad4 (белый) и iPad3 (черный).</strong></span></div>\r\n<div>&nbsp;</div>\r\n<iframe width=\"560\" height=\"315\" src=\"http://www.youtube.com/embed/HMijuc5qX7M\" frameborder=\"0\" allowfullscreen></iframe>\r\n<div>&nbsp;</div>\r\n<div>Как видно по видео, третье поколение совсем капельку отстает от четвертого.</div>\r\n','<p>&nbsp;</p>\r\n<div><span style=\"font-size:16px;\"><strong>Скорость работы iPad4 (белый) и iPad3 (черный).</strong></span></div>\r\n<div>&nbsp;</div>\r\n<iframe width=\"560\" height=\"315\" src=\"http://www.youtube.com/embed/HMijuc5qX7M\" frameborder=\"0\" allowfullscreen></iframe>\r\n<div>&nbsp;</div>\r\n<div>Как видно по видео, третье поколение совсем капельку отстает от четвертого.</div>\r\n',NULL,3,1),(312,'iPad mini взломали.','<p><span style=\"font-size:16px;\"><strong>iPad mini взломали.</strong></span></p>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" src=\"http://cs309422.userapi.com/v309422523/5bef/CGBL-bCqJMs.jpg\" style=\"width: 400px; height: 533px; \"></p>\r\n<p>&nbsp;</p>\r\n<p>Спустя примерно сутки после официального старта продаж iPad mini, хакер MuscleNerd сообщил, что ему удалось взломать новинку Apple. Сделал он это методом хакера Chpwn «failbreak», который ранее использовался для взлома iPhone 5. Этот метод взлома плох тем, что','<p><span style=\"font-size:16px;\"><strong>iPad mini взломали.</strong></span></p>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" src=\"http://cs309422.userapi.com/v309422523/5bef/CGBL-bCqJMs.jpg\" style=\"width: 400px; height: 533px; \"></p>\r\n<p>&nbsp;</p>\r\n<p>Спустя примерно сутки после официального старта продаж iPad mini, хакер MuscleNerd сообщил, что ему удалось взломать новинку Apple. Сделал он это методом хакера Chpwn «failbreak», который ранее использовался для взлома iPhone 5. Этот метод взлома плох тем, что его схема не может быть выложена для общественности, поэтому жаждущим сделать джейлбрейк на своем мини пока придется подождать.&nbsp;</p>\r\n',NULL,3,1),(313,'Себестоимость iPad mini $188.','Себестоимость iPad mini $188.\r\n\r\nВ пятницу стартовали продажи iPad mini. Цена за младшую модель <p>&nbsp;</p>\r\n<div><span style=\"font-size:16px;\"><strong>Себестоимость iPad mini $188.</strong></span></div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://cs309422.userapi.com/v309422523/5ce3/QVcq38WO_VQ.jpg\" style=\"width: 593px; height: 583px; \"></div>\r\n<div>&nbsp;</div>\r\n<div>В пятницу стартовали продажи iPad mini. Цена за младшую модель планшетника составляет $329. Себестоимость же этой модели ','<p>&nbsp;</p>\r\n<div><span style=\"font-size:16px;\"><strong>Себестоимость iPad mini $188.</strong></span></div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://cs309422.userapi.com/v309422523/5ce3/QVcq38WO_VQ.jpg\" style=\"width: 593px; height: 583px; \"></div>\r\n<div>&nbsp;</div>\r\n<div>В пятницу стартовали продажи iPad mini. Цена за младшую модель планшетника составляет $329. Себестоимость же этой модели $188. И это не учитывая производство и доставку. Так что цена вполне оправдана и не завышена. Напомним, что себестоимость iPhone 5 также $188, а продается он по цене $650.&nbsp;</div>\r\n<div>Поэтому цена iPad mini выше чем у планшетов конкурентов ($199-250), но и производительность и скорость работы у яблочного планшета выше.</div>\r\n',NULL,3,1),(314,'В iOS 6.1 через Siri можно купить билеты в кино.','<p>&nbsp;</p>\r\n<div><span style=\"font-size:16px;\"><strong>В iOS 6.1 через Siri можно купить билеты в кино.</strong></span></div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://cs309422.userapi.com/v309422523/5cf5/Yy5mkwxs0m0.jpg\" style=\"width: 350px; height: 600px; \">&nbsp;<img alt=\"\" src=\"http://cs309422.userapi.com/v309422523/5cff/ecPemgHLoYo.jpg\" style=\"width: 350px; height: 600px; \"></div>\r\n<div>&nbsp;</div>\r\n<div>В бета версии прошивке iOS 6.1 заметили возможность покупать билеты в кино ч','<p>&nbsp;</p>\r\n<div><span style=\"font-size:16px;\"><strong>В iOS 6.1 через Siri можно купить билеты в кино.</strong></span></div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://cs309422.userapi.com/v309422523/5cf5/Yy5mkwxs0m0.jpg\" style=\"width: 350px; height: 600px; \">&nbsp;<img alt=\"\" src=\"http://cs309422.userapi.com/v309422523/5cff/ecPemgHLoYo.jpg\" style=\"width: 350px; height: 600px; \"></div>\r\n<div>&nbsp;</div>\r\n<div>В бета версии прошивке iOS 6.1 заметили возможность покупать билеты в кино через Siri. Для того чтобы приобрести билет надо просто попросить Siri, чтобы она она купила билет на интересующий вас фильм. Пока эта функция доступна только находящимся в США.</div>\r\n',NULL,3,1),(315,'Джейлбрейк на iPad 4.','<p><span style=\"font-size:16px;\"><strong>Джейлбрейк на iPad 4.</strong></span></p>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" src=\"http://cs309422.userapi.com/v309422523/5d08/KtLmcwtXs5Y.jpg\" style=\"width: 480px; height: 640px; \"></p>\r\n<p>Несколько дней назад взломали iPad mini, недавно взломали и iPad 4. Известный хакер Chpwn сделал джейлбрейк и установил Cydia на iPad четвертого поколения. Теперь стоит только немного подождать, пока хакеры адаптируют джейлбрейк для iOS 6.</p>\r\n','<p><span style=\"font-size:16px;\"><strong>Джейлбрейк на iPad 4.</strong></span></p>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" src=\"http://cs309422.userapi.com/v309422523/5d08/KtLmcwtXs5Y.jpg\" style=\"width: 480px; height: 640px; \"></p>\r\n<p>Несколько дней назад взломали iPad mini, недавно взломали и iPad 4. Известный хакер Chpwn сделал джейлбрейк и установил Cydia на iPad четвертого поколения. Теперь стоит только немного подождать, пока хакеры адаптируют джейлбрейк для iOS 6.</p>\r\n',NULL,3,1),(316,'Apple продала 3 млн. планшетов за выходные.','<p>&nbsp;</p>\r\n<div><span style=\"font-size:16px;\"><strong>Apple продала 3 млн. планшетов за выходные.</strong></span></div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://cs309422.userapi.com/v309422523/5d16/gAMYocLuu_M.jpg\" style=\"width: 580px; height: 435px; \"></div>\r\n<div>&nbsp;</div>\r\n<div>Apple объявила о продажах за первые выходные. Итак, за викенд Apple смогла продать 3 миллиона iPad mini и iPad 4 вместе взятых. Сколько продали отдельно iPad mini или iPad 4 неизвестно.&nbsp;</div>\r\n<div','<p>&nbsp;</p>\r\n<div><span style=\"font-size:16px;\"><strong>Apple продала 3 млн. планшетов за выходные.</strong></span></div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://cs309422.userapi.com/v309422523/5d16/gAMYocLuu_M.jpg\" style=\"width: 580px; height: 435px; \"></div>\r\n<div>&nbsp;</div>\r\n<div>Apple объявила о продажах за первые выходные. Итак, за викенд Apple смогла продать 3 миллиона iPad mini и iPad 4 вместе взятых. Сколько продали отдельно iPad mini или iPad 4 неизвестно.&nbsp;</div>\r\n<div>Напомним, что в марте на старте продаж iPad 3 за выходные продали в два раза меньше.</div>\r\n',NULL,3,1),(317,'Обзор iPad mini.','<p>&nbsp;</p>\r\n<div>Сегодня мы посмотрели и потрогали яблочную новинку iPad mini и расскажем вам наши впечатления.&nbsp;</div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://cs309422.userapi.com/v309422523/5d49/KxljE3PETA8.jpg\" style=\"width: 700px; height: 466px; \"></div>\r\n<div>&nbsp;</div>\r\n<div>Начнем немного издалека. Вокруг iPad mini слухи начали ходить уже очень давно. Многие не верили в то, что Apple вообще выпустит уменьшенный планшет. Сам Стив Джобс негативно отзывался об идеи выпуска ','<p>&nbsp;</p>\r\n<div>Сегодня мы посмотрели и потрогали яблочную новинку iPad mini и расскажем вам наши впечатления.&nbsp;</div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://cs309422.userapi.com/v309422523/5d49/KxljE3PETA8.jpg\" style=\"width: 700px; height: 466px; \"></div>\r\n<div>&nbsp;</div>\r\n<div>Начнем немного издалека. Вокруг iPad mini слухи начали ходить уже очень давно. Многие не верили в то, что Apple вообще выпустит уменьшенный планшет. Сам Стив Джобс негативно отзывался об идеи выпуска iPad mini. Но он все-таки появился и сегодня мы его обозреваем!</div>\r\n<div>\r\n	<div>&nbsp;</div>\r\n	<div><img alt=\"\" src=\"http://cs309422.userapi.com/v309422523/5d7b/S5FULwhcpy4.jpg\" style=\"width: 700px; height: 466px; \"></div>\r\n	<div>&nbsp;</div>\r\n	<div>Еще на презентации Apple заявила, что iPad mini сильно отличается от старшего брата iPad. Так и есть, трудно вообще определиться, уменьшенный ли это iPad или увеличенный iPod touch.&nbsp;</div>\r\n	<div>Внешне iPad mini выглядит очень хорошо. Все углы сглажены, приятный на ощупь анодированный алюминий, такой же как у последней линейки iPod. Насколько он прочный и надежный пока говорить рано, но если судить по дроп-тестам, то можно сказать что новый планшет достаточно прочный и надежный. Он прекрасно ложится в ладонь и его удобно держать одной рукой. Размеры и вес сразу поражает. Его толщина всего 7.2 мм, точно такая же, как у iPod touch 4-го поколения. А весит он всего 308 грамм, в два раза меньше полноразмерного iPad.</div>\r\n	<div>Расположение кнопок привычное: кнопка Home как располагается внизу под экраном, но немного ниже обычного и сама кнопка немного меньше, кнопка включения сверху, выключатель звука и кнопки громкости находятся вверху с правой стороны устройства. Вход для наушников остался сверху, а снизу располагается вход для кабеля Lightning. Вокруг отверстия под коннектор 2 динамика - да, в iPad mini стерео звук! А за счет того, что динамики расположили снизу, а не с задней стороны как у старшего брата, звук стал еще и громче. Кстати, на наушники придется потратиться отдельно, как у всей линейки iPad наушников в комплекте нет.</div>\r\n	<div>На задней панели располагается 5Мп камера iSight, точно такая же как у iPhone 4. Снимает она вполне не плохо, но стабилизации изображения, как у iPhone 5 нет и если у вас трясутся руки, то изображения получатся смазанные. Передняя камера также стоит неплохая – 1.2 Мп FaceTime HD.</div>\r\n	<div>&nbsp;</div>\r\n	<div><img alt=\"\" src=\"http://cs309422.userapi.com/v309422523/5d67/Rhsq_9JJxfA.jpg\" style=\"width: 700px; height: 466px; \"></div>\r\n	<div>&nbsp;</div>\r\n	<div>Перейдем к экрану. Размер дисплея 7.9\", соотношение сторон осталось таким же как и на iPad 16:9. Плотность пикселей 163ppi, тогда как у iPad 2 было всего 132ppi. В целом экран выглядит хорошо, но до Retina дисплеев в iPad4 с плотностью пикселей 264ppi и в iPhone 5 – 326dpi не дотягивает. Думаю в следующем году Apple выпустит второе поколение iPad mini с Retina дисплеем. Что интересно, яркость и цветопередача дисплея лучше, чем у iPad 2, цвета насыщенные, угол обзора просто шикарный, можно усесться компанией вокруг планшета и всем будет хорошо видно.</div>\r\n	<div>&nbsp;</div>\r\n	<div><img alt=\"\" src=\"http://cs309422.userapi.com/v309422523/5d5d/nDZlgnk61Gk.jpg\" style=\"width: 700px; height: 466px; \"></div>\r\n	<div>&nbsp;</div>\r\n	<div>Начинка iPad mini точь в точь такая же как и у iPad 2 (двухъядерный процессор с частотой 1Ггц с оперативкой 512Мб), что позволяет демонстрировать достаточную производительность во всех современных играх и приложениях. Если сравнивать iPad mini с iPad 4, то конечно, мини немного проигрывает, на нем игры и приложения загружаются и работают медленнее &nbsp;Но в повседневном использовании недостатка производительности вы не заметите. Зато время работа от одного заряда покоряет. При стандартном тесте с бесконечным просмотром видео с включенным WiFi iPad mini продержался 12 часов и 43 минуты! Это рекорд среди планшетов!</div>\r\n	<div>&nbsp;</div>\r\n	<div><img alt=\"\" src=\"http://cs309422.userapi.com/v309422523/5d8f/gitAh-enVPo.jpg\" style=\"width: 700px; height: 466px; \"></div>\r\n	<div>&nbsp;</div>\r\n	<div>Подведем итоги. Размеры планшета позволяют пользоваться им в повседневной жизни везде где только можно. С ним будет удобно стоять в метро или автобусе, держа мини одной рукой, а другой поручень. Его маленький вес не будет напрягать руку. С него будет удобно читать книги или смотреть фильмы в поездке на работу. Поэтому если вам не хватает размера экрана iPhone во время пути на работу, то iPad mini определенно для вас! Если же планшет вам нужен больше как прикроватный аксессуар и вы любите удобно расположившись на диване с чашкой чая посерфить интернет или почитать новости, то вам лучше присмотреться к полноразмерному iPad.&nbsp;</div>\r\n	<div>В целом всем сотрудникам Маков.Нет новый планшет понравился. Многие не пользовались iPad как раз из-за размера и веса, а теперь всерьез задумались о приобретении iPad mini. Пусть в нем и нет пока Reina дисплея, планшет получился очень хорошим.&nbsp;</div>\r\n</div>\r\n<p>&nbsp;</p>\r\n',NULL,1,1),(318,'Pixar назвал здание головного офиса в честь Стива Джобса.  ','<p>&nbsp;</p>\r\n<div><img alt=\"\" src=\"http://cs309422.userapi.com/v309422523/5d97/O4o0e-WsTic.jpg\" style=\"width: 600px; height: 377px; \"></div>\r\n<div>&nbsp;</div>\r\n<div>Сегодня стало известно, что здание Pixar, в котором находится главная студия и офисы было недавно названо в честь Стива Джобса. В создании этого самого здания принимал участие сам Стив Джобс в должности генерального директора Pixar. Стив хотел, чтобы здание и через 100 лет выглядело современным и не было стандартным.</div>\r\n','<p>&nbsp;</p>\r\n<div><img alt=\"\" src=\"http://cs309422.userapi.com/v309422523/5d97/O4o0e-WsTic.jpg\" style=\"width: 600px; height: 377px; \"></div>\r\n<div>&nbsp;</div>\r\n<div>Сегодня стало известно, что здание Pixar, в котором находится главная студия и офисы было недавно названо в честь Стива Джобса. В создании этого самого здания принимал участие сам Стив Джобс в должности генерального директора Pixar. Стив хотел, чтобы здание и через 100 лет выглядело современным и не было стандартным.</div>\r\n',NULL,3,1),(319,'Качественная копия iPhone 5.','<p>&nbsp;</p>\r\n<div><img alt=\"\" src=\"http://cs405919.userapi.com/v405919523/345e/Jpq2zsFuzek.jpg\" style=\"width: 700px; height: 681px; \"></div>\r\n<div>&nbsp;</div>\r\n<div>Китайцы сделали качественную копию iPhone 5 под названием Zophone i5. Вместо iOS используется операционная система Android. Zophone i5 оснащен &nbsp;двухъядерным процессором Mediatek MT6577 с частотой 1.2 ГГц и 1 GB оперативной памятью, 4 GB встроенной памятью, слотом MicroSD, аккумулятором с емкостью 1400 мАч и 8-мегапиксельной к','<p>&nbsp;</p>\r\n<div><img alt=\"\" src=\"http://cs405919.userapi.com/v405919523/345e/Jpq2zsFuzek.jpg\" style=\"width: 700px; height: 681px; \"></div>\r\n<div>&nbsp;</div>\r\n<div>Китайцы сделали качественную копию iPhone 5 под названием Zophone i5. Вместо iOS используется операционная система Android. Zophone i5 оснащен &nbsp;двухъядерным процессором Mediatek MT6577 с частотой 1.2 ГГц и 1 GB оперативной памятью, 4 GB встроенной памятью, слотом MicroSD, аккумулятором с емкостью 1400 мАч и 8-мегапиксельной камерой, четырех дюймовым экраном с разрешением 1136х640 пикселей. Китайцы даже не поленились сделать копию коннектора Ligthning и наушников EarPods.</div>\r\n<div>Цена всего $200 за Zophone i5, против $649 за iPhone 5.</div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://cs405919.userapi.com/v405919523/3465/Y3KCIJJscms.jpg\" style=\"width: 489px; height: 398px; \"></div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"http://cs405919.userapi.com/v405919523/346d/QH2AEpq6zvg.jpg\" style=\"width: 700px; height: 374px; \"></div>\r\n',NULL,3,1),(320,'\"Will it BLEND?\" iPad mini, Kindle Fire HD и Nexus 7.','<p>В последней серии интернет передачи \"Will it BLEND?\" (Взобьется ли это?) решили сравнить iPad mini и его конкурентов Kindle Fire HD и Nexus 7. Полностью в блендер планшеты не взлезли и предварительно их \"сложили\" пополам. В итоге iPad mini продержался немного дольше!</p>\r\n<p>&nbsp;</p>\r\n<p><iframe allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"http://www.youtube.com/embed/5MMmLQlrBws\" width=\"560\"></iframe></p>\r\n','<p>В последней серии интернет передачи \"Will it BLEND?\" (Взобьется ли это?) решили сравнить iPad mini и его конкурентов Kindle Fire HD и Nexus 7. Полностью в блендер планшеты не взлезли и предварительно их \"сложили\" пополам. В итоге iPad mini продержался немного дольше!</p>\r\n<p>&nbsp;</p>\r\n<p><iframe allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"http://www.youtube.com/embed/5MMmLQlrBws\" width=\"560\"></iframe></p>\r\n',NULL,3,1),(321,'Зарядная книжка.','<p>Компания&nbsp;AViiQ выпустила оригинальную \"зарядную книжку\". Внутри устройства встроен аккумулятор с емкостью 5200mAh. С помощью такой книжки вы сможете зарядить до четырех устройств. Цена составляет $169.</p>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" src=\"http://cs405919.userapi.com/v405919523/3475/ycPzt_mSPss.jpg\" style=\"width: 640px; height: 640px; \"></p>\r\n<p>&nbsp;</p>\r\n','<p>Компания&nbsp;AViiQ выпустила оригинальную \"зарядную книжку\". Внутри устройства встроен аккумулятор с емкостью 5200mAh. С помощью такой книжки вы сможете зарядить до четырех устройств. Цена составляет $169.</p>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" src=\"http://cs405919.userapi.com/v405919523/3475/ycPzt_mSPss.jpg\" style=\"width: 640px; height: 640px; \"></p>\r\n<p>&nbsp;</p>\r\n',NULL,3,1),(322,'Стив Джобс завлекает клиентов в Одессе.','<p>&nbsp;</p>\r\n<div><img alt=\"\" src=\"/sites/default/files/12772959.jpg\" style=\"width: 340px; height: 510px; \"></div>\r\n<div>Стив Джобс в очередной раз появился в центре Одессы – кукла компьютерного гения украсила витрину одного из магазинов электроники. Выглядит Джобс весьма реалистично – мастера «Волшебной мастерской», создавшие куклу, &nbsp;максимально приблизили параметры американца к его человеческим величинам.</div>','<p>&nbsp;</p>\r\n<div><img alt=\"\" src=\"/sites/default/files/12772959.jpg\" style=\"width: 340px; height: 510px; \"></div>\r\n<div>Стив Джобс в очередной раз появился в центре Одессы – кукла компьютерного гения украсила витрину одного из магазинов электроники. Выглядит Джобс весьма реалистично – мастера «Волшебной мастерской», создавшие куклу, &nbsp;максимально приблизили параметры американца к его человеческим величинам.</div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"/sites/default/files/12772958.jpg\" style=\"width: 510px; height: 417px; \"></div>\r\n<div>&nbsp;</div>\r\n<blockquote>\r\n	<div>Джобса мы запечатлели в полный рост, в привычном для него образе мыслителя, – рассказала руководитель художественного коллектива мастерской Светлана Геращенко. – Он сидит за компьютером в задумчивой позе, подпирая голову рукой.</div>\r\n</blockquote>\r\n<div>Две недели над созданием куклы трудились скульпторы и швеи – несмотря на короткие сроки исполнения, все детали мастера проработали до мелочей. В основе фигуры Джобса – жесткий металлокаркас высотой порядка двух метров. Его обтянули специальной кукольной массой, а голову выполнили из папье-маше. Портретной схожести герою придала борода – профессиональный постижер сделал ее из натуральных волос темного цвета, добавив немного седых вкраплений. Одели Джобса в традиционную черную водолазку и потертые джинсы, а дополнили образ харизматичного революционера электроники неизменные кроссовки.</div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"/sites/default/files/12772957.jpg\"></div>\r\n',NULL,3,1),(323,'Angry Birds Star Wars','<p><img alt=\"\" src=\"/sites/default/files/mzl.slmfkupp.480x480-75.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Вышла новая игра про злых птичек Angry Bireds Star Wars.&nbsp;Известные всем цветные птички перевоплотятся в Люка Скайуокера, принцессу Леи, Хана Соло, Чубакку и Оби-Вана Кеноби, зеленые свиньи станут имперскими штурмовиками, а король свиней исполнит роль Дарта Вейдера.</p>','<p><img alt=\"\" src=\"/sites/default/files/mzl.slmfkupp.480x480-75.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Вышла новая игра про злых птичек Angry Bireds Star Wars.&nbsp;Известные всем цветные птички перевоплотятся в Люка Скайуокера, принцессу Леи, Хана Соло, Чубакку и Оби-Вана Кеноби, зеленые свиньи станут имперскими штурмовиками, а король свиней исполнит роль Дарта Вейдера.</p>\r\n<p>Ссылки на игру:</p>\r\n<p><a href=\"https://itunes.apple.com/ru/app/angry-birds-star-wars/id557137623?mt=8\">iPhone</a>&nbsp;(33 р.)</p>\r\n<p><a href=\"https://itunes.apple.com/ru/app/angry-birds-star-wars-hd/id557138109?mt=8\">iPad</a>&nbsp;(99 р.)</p>\r\n<p><a href=\"https://itunes.apple.com/ru/app/angry-birds-star-wars/id557139259?mt=12\">Mac</a>&nbsp;(169 р.)</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<iframe width=\"560\" height=\"315\" src=\"http://www.youtube.com/embed/lyB6G4Cz9fI\" frameborder=\"0\" allowfullscreen></iframe>',NULL,3,1),(324,'iPad mini Retina уже разрабатывается.','<p><img alt=\"\" src=\"/sites/default/files/ipmini-retina-production-1.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Нынешняя модель iPad mini хорошо продается. Но многие привыкли к Retina дисплеем и экран на планшете кажется не таким четким. Для таких прекрасная новость. Китайская компания AU Optronics, которая является одним из поставщиков экранов iPad mini во второй половине 2013 года планирует начать производство новый&nbsp;7.9″ экранов с разрешением 2048*1536 и плотностью пикселей аж 497ppi!</p>\r\n','<p><img alt=\"\" src=\"/sites/default/files/ipmini-retina-production-1.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Нынешняя модель iPad mini хорошо продается. Но многие привыкли к Retina дисплеем и экран на планшете кажется не таким четким. Для таких прекрасная новость. Китайская компания AU Optronics, которая является одним из поставщиков экранов iPad mini во второй половине 2013 года планирует начать производство новый&nbsp;7.9″ экранов с разрешением 2048*1536 и плотностью пикселей аж 497ppi!</p>\r\n',NULL,2,1),(325,'Apple провела встречу с производителями аксессуаров.','<p>&nbsp;</p>\r\n<div><img alt=\"\" src=\"/sites/default/files/iPhone-5-Lightning-2012-09-12-closeup.jpg\" style=\"width: 606px; height: 399px;\"></div>\r\n<div>&nbsp;</div>\r\n<div>Apple провела встречу с производителями аксессуаров. Яблочный блог TUAW описали как прошла встреча. Apple собирается следить за всеми производителями, котороые изготавливают продукцию для iPhone, iPod и iPad. Это значит, что все не вошедшие в программу лицензирования <a href=\"https://developer.apple.com/programs/mfi/\">MFi Progra','<p>&nbsp;</p>\r\n<div><img alt=\"\" src=\"/sites/default/files/iPhone-5-Lightning-2012-09-12-closeup.jpg\" style=\"width: 606px; height: 399px;\"></div>\r\n<div>&nbsp;</div>\r\n<div>Apple провела встречу с производителями аксессуаров. Яблочный блог TUAW описали как прошла встреча. Apple собирается следить за всеми производителями, котороые изготавливают продукцию для iPhone, iPod и iPad. Это значит, что все не вошедшие в программу лицензирования <a href=\"https://developer.apple.com/programs/mfi/\">MFi Program</a>, не могут выпускать аксессуары для яблочных аппаратов. Кроме того, если производитель хочет изготавливать аксессуары с конектором Lightning, ему нужно получить свой код поставщика Apple. Пока не сообщается будут ли какие-нибудь штрафы и их размеры за нарушение данных условий, но они точно будут.</div>\r\n<div>Хорошая новость заключается в том, что кабель Lightning полностью водонепроницаемый.</div>\r\n<div>&nbsp;</div>\r\n',NULL,3,1),(326,'iPad mini Wi-Fi + Cellular с 16 ноября.','iPad mini с 4G доставят 21 ноября\r\nКогда еще только начались предзаказы на iPad mini, люди заказавшие именно модель с поддержкой SIM-карт начали замечать, что датой доставки значится 21 ноября, а у некоторых 23 ноября. Немного позже Apple удалила все эти даты, чем вызвала слухи о том, что данная модель будет задержана на неопределенный срок. Сегодня же те, кто оставил предзаказы снова увидели дату 21 ноября и доставку в 5 рабочих дней. Ждать осталось совсем немного.\r\n[Update] Старт продаж в мага','<p><img alt=\"\" src=\"/sites/default/files/ipad-mini-1351017524.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Когда начались предзаказы на iPad mini, тем кто заказал модель Wi-Fi + Cellular назначили дату доставки 21-23 ноября. Потом даты доставки и вовсе убрали и пошли слухи, что моедль с 4G бужет задержана на неопроделенное время. Но сегодня, предзаказавшие iPad mini&nbsp;Wi-Fi + Cellular вновь увидили в своем заказе дату 21 ноября.&nbsp;</p>\r\n<p>Продажа в магазинах iPad mini Wi-Fi + Cellular начнется с 16 ноября.</p>\r\n',NULL,3,1),(327,'Apple запатентовала коробку-док-станцию.','<p><img alt=\"\" src=\"/sites/default/files/dock-packaging-2.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Коробки к яблочным устройствам хорошо сделаны, удобные и красиво выглядит. Многим даже жалко их выбрасывать. Не зря у Apple есть целый отдел, который занимается упаковкой.&nbsp;</p>\r\n<p>Похоже, что в будущем коробку можно будет оставить на столе и использовать как док-станцию. Недавно Apple запатентовала новую коробку с док-станцией, после распаковки мы можете использовать ее как док-станцию для вашего iPhone ','<p><img alt=\"\" src=\"/sites/default/files/dock-packaging-2.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Коробки к яблочным устройствам хорошо сделаны, удобные и красиво выглядит. Многим даже жалко их выбрасывать. Не зря у Apple есть целый отдел, который занимается упаковкой.&nbsp;</p>\r\n<p>Похоже, что в будущем коробку можно будет оставить на столе и использовать как док-станцию. Недавно Apple запатентовала новую коробку с док-станцией, после распаковки мы можете использовать ее как док-станцию для вашего iPhone и iPad. Коробка, конечно, будет на самой краcивой док-станцией, но по крайней мере не будет пылиться в шкафу.&nbsp;</p>\r\n<p>Некоторые помнят времена, когда док-станция &nbsp;шла в комплекте с iPhone.&nbsp;</p>\r\n',NULL,3,1),(328,'Удобное кресло-качалка с подставкой для iPad и iPhone. ','<p>&nbsp;</p>\r\n<div><img alt=\"\" src=\"/sites/default/files/1352360093_charging_02.jpg\"></div>\r\n<div>&nbsp;</div>\r\n<div>Швейцары разработали шикарное кресло-качалка. Теперь вы можете усесться зимним вечером с чашечкой горячего кофе или чая и почитать последние новости или проверить почту с вашего iPad или iPhone. Яблочное устройство крепится на специальный держатель на подлокотнике. Самая главная особенность этого кресла - это то, что за счет энергии качания можно подзаряжать ваш iДевайс.</div>','<p>&nbsp;</p>\r\n<div><img alt=\"\" src=\"/sites/default/files/1352360093_charging_02.jpg\"></div>\r\n<div>&nbsp;</div>\r\n<div>Швейцары разработали шикарное кресло-качалка. Теперь вы можете усесться зимним вечером с чашечкой горячего кофе или чая и почитать последние новости или проверить почту с вашего iPad или iPhone. Яблочное устройство крепится на специальный держатель на подлокотнике. Самая главная особенность этого кресла - это то, что за счет энергии качания можно подзаряжать ваш iДевайс.</div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"/sites/default/files/1352360107_charging_01.jpg\"></div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"/sites/default/files/1352360114_charging_03.jpg\"></div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"/sites/default/files/1352360118_charging_04.jpg\" style=\"width: 670px; height: 354px;\"></div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"/sites/default/files/1352360064_charging_05.jpg\" style=\"width: 670px; height: 352px;\"></div>\r\n',NULL,3,1),(329,'Проблема с тачскрином на iPhone 5.','<p>Разработчик игр CMA MegaCorp сообщил о проблеме с тачскрином на iPhone 5. Проблема заключается в том, что если быстро и долго прокручивать экран вверх и вниз, то через несколько секунд \"прокрутка\" останавливается. Для обычного пользователя это не проблема. Но если вдруг разработчик запрограммировал игру так, что при \"прокрутке\" появляется какой-нибудь эффект, то данная проблема имеет смысл.</p>\r\n<p>На видео наглядно демонстрируется баг.</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<iframe width=\"560\" h','<p>Разработчик игр CMA MegaCorp сообщил о проблеме с тачскрином на iPhone 5. Проблема заключается в том, что если быстро и долго прокручивать экран вверх и вниз, то через несколько секунд \"прокрутка\" останавливается. Для обычного пользователя это не проблема. Но если вдруг разработчик запрограммировал игру так, что при \"прокрутке\" появляется какой-нибудь эффект, то данная проблема имеет смысл.</p>\r\n<p>На видео наглядно демонстрируется баг.</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<iframe width=\"560\" height=\"315\" src=\"http://www.youtube.com/embed/BnioM-nkhqc\" frameborder=\"0\" allowfullscreen></iframe>',NULL,3,1),(330,'ВИДЕО: Как работает Fusion Drive.','<p>На последней презентации Apple нам показали новую функцию в новых компьюторах Mac — Fusion Drive. Один из пользователей YouTube снял наглядное видео как работает новая яблочная фича.</p>\r\n<p>&nbsp;</p>\r\n<iframe width=\"560\" height=\"315\" src=\"http://www.youtube.com/embed/d0M6pv9qAH4\" frameborder=\"0\" allowfullscreen></iframe>','<p>На последней презентации Apple нам показали новую функцию в новых компьюторах Mac — Fusion Drive. Один из пользователей YouTube снял наглядное видео как работает новая яблочная фича.</p>\r\n<p>&nbsp;</p>\r\n<iframe width=\"560\" height=\"315\" src=\"http://www.youtube.com/embed/d0M6pv9qAH4\" frameborder=\"0\" allowfullscreen></iframe>',NULL,3,1),(331,'В подарок на Новый Год хотят iPad 4 и iPad mini.','<p>&nbsp;</p>\r\n<div><img alt=\"\" src=\"/sites/default/files/110120-cny-ipad-hampers.jpg\"></div>\r\n<div>&nbsp;</div>\r\n<div>Компания PriceGrabber провела опрос для выяснения самого желанного подарка на Новый Год. С 24 октября по 1 ноября было опрошено 1500 американских онлайн-покупателей. Их спрашивали \"Что бы вы выбрали в качестве рождественского подарка — ПК или планшет?\". 59% ответили, что им по душе больше планшет. Следущим вопром их спросили какой именно планшет они хотят. &nbsp;63% &nbsp;опроше','<p>&nbsp;</p>\r\n<div><img alt=\"\" src=\"/sites/default/files/110120-cny-ipad-hampers.jpg\"></div>\r\n<div>&nbsp;</div>\r\n<div>Компания PriceGrabber провела опрос для выяснения самого желанного подарка на Новый Год. С 24 октября по 1 ноября было опрошено 1500 американских онлайн-покупателей. Их спрашивали \"Что бы вы выбрали в качестве рождественского подарка — ПК или планшет?\". 59% ответили, что им по душе больше планшет. Следущим вопром их спросили какой именно планшет они хотят. &nbsp;63% &nbsp;опрошенных мечтают об iPad 4, а 24% — о младшем брате iPad mini.&nbsp;</div>\r\n<div>&nbsp;</div>\r\n<div>22% американских покупателей заявили, что хотели бы получить Samsung Galaxy Note Tab, а 20% — Amazon Kindle Fire HD. Еще 13% упомянули недавно выпущенные Microsoft Surface на Windows RT.</div>\r\n<div>&nbsp;</div>\r\n<div>\"Планшеты продолжают набирать популярность и остаются одними из самых желанных подарков на грядущие праздники, тем более, что на рынке теперь появились модели по более разумной цене $200-$300\", — отметил вице-президент PriceGrabber Рожех Аванесян.</div>\r\n<div>&nbsp;</div>\r\n<div>Исполнительный директор Nvidia Джен-Сан Хуанг подтверждает результаты опроса, он говорит, что планшетные компьютеры в последнее время все активнее заменяют ПК в глазах потребителей.</div>\r\n<div>&nbsp;</div>\r\n<div>\"Сейчас планшеты становятся все более универсальными и трансформируемыми. Теперь, с выходом Microsoft Surface и Windows RT, разница между планшетом и дешевым ноутбуком вообще практически исчезла. Будущее дешевых персональных компьютеров лежит в сегменте планшетов\", — Джен-Сан Хуанг.</div>\r\n<div>&nbsp;</div>\r\n<div>А какой подарок вы хотели бы получить на Новый Год?</div>\r\n<p>&nbsp;</p>\r\n',NULL,3,1),(332,'Apple и HTC договорились.','<p><img alt=\"\" src=\"/sites/default/files/Apple-HTC-california.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Вчера Apple опубликовала&nbsp;<a href=\"http://www.apple.com/pr/library/2012/11/11HTC-and-Apple-Settle-Patent-Dispute.html\">пресс-инфо</a>, в котором сообщается, что Apple и HTC пришли к 10-летнему соглашению. С этого момента, все текущие патентные иски отозваны.</p>\r\n<p>Патентная война между этими двумя компаниями началась еще в марте 2010 года, тогда яблочники подали иск в суд, обвинив HTC в нарушении 20 ','<p><img alt=\"\" src=\"/sites/default/files/Apple-HTC-california.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Вчера Apple опубликовала&nbsp;<a href=\"http://www.apple.com/pr/library/2012/11/11HTC-and-Apple-Settle-Patent-Dispute.html\">пресс-инфо</a>, в котором сообщается, что Apple и HTC пришли к 10-летнему соглашению. С этого момента, все текущие патентные иски отозваны.</p>\r\n<p>Патентная война между этими двумя компаниями началась еще в марте 2010 года, тогда яблочники подали иск в суд, обвинив HTC в нарушении 20 патентов. В 2011 году, HTC подал ответный патентный иск на Apple.</p>\r\n',NULL,3,1),(333,'Apple заплатила $21 млн за часы.','<p><img alt=\"\" src=\"/sites/default/files/apple-clock-design-copy-210912.jpeg\"></p>\r\n<p>&nbsp;</p>\r\n<p>В последнеей прошивке Apple iOS 6 на iPad циферблат часов очень сильно похож на циферблат часов, устанавливаемых на вокзалах и железнодорожных станциях Швейцарии. Швейцарские федеральные железные дороги (SBB) обвинили Apple в копировании их часов. Причем, SBB больше волновало не денежная компенсация, а урегулирование условий использования товарного знака. Пресс-секретарь SBB в сентябре даже заяв','<p><img alt=\"\" src=\"/sites/default/files/apple-clock-design-copy-210912.jpeg\"></p>\r\n<p>&nbsp;</p>\r\n<p>В последнеей прошивке Apple iOS 6 на iPad циферблат часов очень сильно похож на циферблат часов, устанавливаемых на вокзалах и железнодорожных станциях Швейцарии. Швейцарские федеральные железные дороги (SBB) обвинили Apple в копировании их часов. Причем, SBB больше волновало не денежная компенсация, а урегулирование условий использования товарного знака. Пресс-секретарь SBB в сентябре даже заявила, что Швецарские железнодорожные дороги гордятся тем, что такая известная компания Apple использовала дизайн их циферблата часов.</p>\r\n<p>Лицензионное соглашение было заключено еще в октябре, но сумма сделки тогда не распространялась. Сегодня о том, что Apple пришлось отчислить SBB $21 млн сообщила швецарская газета&nbsp;Tages-Anzeiger. Впрочем, в SBB и сейчас не подтвердили достоверность информации о сумме компенсации.</p>\r\n<p>Эти самые часы создал сотрудник SBB Ханс Хильфикер в 1944 году. Циферблат часов с узнаваемой красной стрелкой стал одним из символов Швейцарии.</p>\r\n',NULL,3,1),(334,'Две новые рекламы iPad mini.','<p>Apple представила целых два новых рекламных ролика для своего нового продукта iPad mini \"Books\" и \"Photos\".</p>\r\n<p>&nbsp;</p>\r\n<p><iframe allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"http://www.youtube.com/embed/9L11hutds-o\" width=\"560\"></iframe></p>','<p>Apple представила целых два новых рекламных ролика для своего нового продукта iPad mini \"Books\" и \"Photos\".</p>\r\n<p>&nbsp;</p>\r\n<p><iframe allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"http://www.youtube.com/embed/9L11hutds-o\" width=\"560\"></iframe></p>\r\n<p>&nbsp;</p>\r\n<p><iframe allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"http://www.youtube.com/embed/fdmkhzGD-ZA\" width=\"560\"></iframe></p>\r\n',NULL,3,1),(335,'Подарочные карты в Passbook.','<p><img alt=\"\" src=\"/sites/default/files/apple-store-app-gift-card-passbook2.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Apple добавила новую возможность в свое приложение Passbook. Теперь при подарочной карты (gift-card) вы можете дабить ее в Passbook. Вам не придется носить с собой платиковую карточку, которую можно еще и потерять. Подарочная карта всегда будет при вас, кроме того, если вы будете находится рядом с Apple Store, то приложение пришлет вам уведомление, что рядом магазин, где вы можете использова','<p><img alt=\"\" src=\"/sites/default/files/apple-store-app-gift-card-passbook2.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Apple добавила новую возможность в свое приложение Passbook. Теперь при подарочной карты (gift-card) вы можете дабить ее в Passbook. Вам не придется носить с собой платиковую карточку, которую можно еще и потерять. Подарочная карта всегда будет при вас, кроме того, если вы будете находится рядом с Apple Store, то приложение пришлет вам уведомление, что рядом магазин, где вы можете использовать свою подарочную карту.</p>\r\n',NULL,3,1),(336,'Вышла iOS 6.1 beta 2.','<p><img alt=\"\" src=\"/sites/default/files/ios-6-1-beta-2-540x413.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Этой ночью Apple порадовала нас выпустив вторую бету прошивку iOS 6.1. С момента предыдущей первой беты прошло две недели, данная прошивка истекает 28 декабря 2012 года.&nbsp;</p>\r\n<p><strong>Замечанные изменения:</strong></p>\r\n<ul>\r\n	<li style=\"margin-left: 40px; \">Исправлен iTunes Match, теперь можно скачивать и удалять по одной песне (до этого можно было скачивать и удалять поальбомно)</li>\r\n	<li styl','<p><img alt=\"\" src=\"/sites/default/files/ios-6-1-beta-2-540x413.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Этой ночью Apple порадовала нас выпустив вторую бету прошивку iOS 6.1. С момента предыдущей первой беты прошло две недели, данная прошивка истекает 28 декабря 2012 года.&nbsp;</p>\r\n<p><strong>Замечанные изменения:</strong></p>\r\n<ul>\r\n	<li style=\"margin-left: 40px; \">Исправлен iTunes Match, теперь можно скачивать и удалять по одной песне (до этого можно было скачивать и удалять поальбомно)</li>\r\n	<li style=\"margin-left: 40px; \">Добавлено описание Passbook, в нем описываются возможности приложения.</li>\r\n	<li style=\"margin-left: 40px; \">Исправлена проблема со съемкой панарамы.</li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n<p>Кроме того, сегодня вышла бета прошивки для Apple TV, а также обновился Xcode до 4.6 Developer Preview 2.</p>\r\n',NULL,3,1),(337,'Избавляемся от царапин на боках iPhone 5.','<p>Одна из самых популярных проблем iPhone 5 — царапины и потертости на боках смартфона. Чаще всего всего они появляются от неаккуратного обращения с телефоном.</p>\r\n<p>У вас появились царапины на торцах iPhone 5? В сегодняшнем видео наглядно показывается как избавиться от &nbsp;таких царапин.&nbsp;</p>\r\n<p>Нам кажется, что так iPhone 5 стал выглядить даже лучше, а вам?</p>\r\n<p>&nbsp;</p>\r\n<iframe width=\"420\" height=\"315\" src=\"http://www.youtube.com/embed/g92_17ZtH9Y\" frameborder=\"0\" allowfullsc','<p>Одна из самых популярных проблем iPhone 5 — царапины и потертости на боках смартфона. Чаще всего всего они появляются от неаккуратного обращения с телефоном.</p>\r\n<p>У вас появились царапины на торцах iPhone 5? В сегодняшнем видео наглядно показывается как избавиться от &nbsp;таких царапин.&nbsp;</p>\r\n<p>Нам кажется, что так iPhone 5 стал выглядить даже лучше, а вам?</p>\r\n<p>&nbsp;</p>\r\n<iframe width=\"420\" height=\"315\" src=\"http://www.youtube.com/embed/g92_17ZtH9Y\" frameborder=\"0\" allowfullscreen></iframe>',NULL,2,1),(338,'Избранные сотрудники Apple получили 2 недели для специальных проектов.','<p><img alt=\"\" src=\"/sites/default/files/12.01.11-TimCook.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Генеральный директор Apple Тим Кук решил выделить некоторым своим сотрудникам по 2 недели на сторонние проекты. Проект назван Blue Sky (Голубое Небо) и позволяет ограниченному количеству работников Apple работать 2 недели вне сферы своей обычной деятельности. Таким образом яблочная компания хочет повысить лояльность работников к своей компании.&nbsp;</p>\r\n<p>Компания Google давно уже предоставляет своим работн','<p><img alt=\"\" src=\"/sites/default/files/12.01.11-TimCook.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Генеральный директор Apple Тим Кук решил выделить некоторым своим сотрудникам по 2 недели на сторонние проекты. Проект назван Blue Sky (Голубое Небо) и позволяет ограниченному количеству работников Apple работать 2 недели вне сферы своей обычной деятельности. Таким образом яблочная компания хочет повысить лояльность работников к своей компании.&nbsp;</p>\r\n<p>Компания Google давно уже предоставляет своим работникам возможность 20% от рабочего времени тратить на свои личные проекты. Благодаря этому появилась в свое время почта Gmail и другие проекты.</p>\r\n',NULL,3,1),(339,'Производство iPhone 5s уже в декабре.','<p><img alt=\"\" src=\"/sites/default/files/iphone-5s-next-new-iphone-590x442.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Первая партия iPhone 5s сойдет с конвеера уже в декабре. Такую информацию сообщает китайское издание Commercial Times. Сейчас у Apple проблемы с производством iPhone 5 и видимо, чтобы избежать этих проблем в будующем яблочники решили подстраховаться и начать производство iPhone 5s заранее. В декабре будет выпущена тестовая партия, а массовое производство намечано на первый квартал 2013 года.&n','<p><img alt=\"\" src=\"/sites/default/files/iphone-5s-next-new-iphone-590x442.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Первая партия iPhone 5s сойдет с конвеера уже в декабре. Такую информацию сообщает китайское издание Commercial Times. Сейчас у Apple проблемы с производством iPhone 5 и видимо, чтобы избежать этих проблем в будующем яблочники решили подстраховаться и начать производство iPhone 5s заранее. В декабре будет выпущена тестовая партия, а массовое производство намечано на первый квартал 2013 года.&nbsp;</p>\r\n<p>Судя по всему, iPhone 5s может выйти уже весной или летом, раньше привычного сентября</p>\r\n',NULL,3,1),(340,'Гитара Fender с iOS-подключением.','<p><img alt=\"\" src=\"/sites/default/files/Fender-iOS-540x286.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Известный производитель гитар Fender совместно с Apple &nbsp;выпустил электрогитару Squier Stratocaster с выходом mini-USB. В комплект входит переходник c mini-USB на яблочный 30-пиновый коннектор, благодаря чему вы сможете подключить гитару к своему iPhone, iPad или iPod touch. После подключения, открыв на гаджете программу Garage Band, перед вами открываются шикарные возможности. Особенно это будет интерес','<p><img alt=\"\" src=\"/sites/default/files/Fender-iOS-540x286.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Известный производитель гитар Fender совместно с Apple &nbsp;выпустил электрогитару Squier Stratocaster с выходом mini-USB. В комплект входит переходник c mini-USB на яблочный 30-пиновый коннектор, благодаря чему вы сможете подключить гитару к своему iPhone, iPad или iPod touch. После подключения, открыв на гаджете программу Garage Band, перед вами открываются шикарные возможности. Особенно это будет интересно начинающим музыкантам для ежедневных занятий.</p>\r\n<p>Цена такой гитары со всеми необходимыми проводами и переходниками всего $200 и доступна только в магазинах Apple.</p>\r\n<p>&nbsp;</p>\r\n<iframe width=\"560\" height=\"315\" src=\"http://www.youtube.com/embed/1EtnS98l8q4\" frameborder=\"0\" allowfullscreen></iframe>',NULL,3,1),(341,'Тим Кук самый влиятельный человек в США.','<p><img alt=\"\" src=\"/sites/default/files/tim-cook.png\"></p>\r\n<p>&nbsp;</p>\r\n<p>Fierce Wireless опубликовал рейтинг ТОП 25 самых влиятельных людей в мире в технологической сфере США. И генеральный директор Apple оказался в самом вверху этого списка, это удалось ему благодаря гигантским продажам яблочных флагманов (77% всех проданных – iPhone) в AT&amp;T.&nbsp;</p>\r\n<p>В списке также находятся генеральные директора Джек Дорси (Square’s), Стив Балмер (Microsoft), Джефф Безос (Amazon), Стивен Элоп (','<p><img alt=\"\" src=\"/sites/default/files/tim-cook.png\"></p>\r\n<p>&nbsp;</p>\r\n<p>Fierce Wireless опубликовал рейтинг ТОП 25 самых влиятельных людей в мире в технологической сфере США. И генеральный директор Apple оказался в самом вверху этого списка, это удалось ему благодаря гигантским продажам яблочных флагманов (77% всех проданных – iPhone) в AT&amp;T.&nbsp;</p>\r\n<p>В списке также находятся генеральные директора Джек Дорси (Square’s), Стив Балмер (Microsoft), Джефф Безос (Amazon), Стивен Элоп (Nokia), а также руководители Sprint, Verizon и AT&amp;T.&nbsp;</p>\r\n',NULL,3,1),(342,'Apple расширяется.','<p><img alt=\"\" src=\"/sites/default/files/coop-1.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Зимой, в начале 2012 года Apple купила себе офисное помещение на 30000 кв. м. в Саннивейл, штат Калифорния. Сегодня Apple арендовала еще 5341кв. м. в Санта-Кларе и 1073 кв. м. в Купертино.&nbsp;</p>\r\n<p>Такую информацию сообщил&nbsp;Silicon Valley BizBlog.&nbsp;</p>\r\n<p>Это все временные решения, пока Apple не достроит свой собственный мини-городок iCamous.</p>\r\n','<p><img alt=\"\" src=\"/sites/default/files/coop-1.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Зимой, в начале 2012 года Apple купила себе офисное помещение на 30000 кв. м. в Саннивейл, штат Калифорния. Сегодня Apple арендовала еще 5341кв. м. в Санта-Кларе и 1073 кв. м. в Купертино.&nbsp;</p>\r\n<p>Такую информацию сообщил&nbsp;Silicon Valley BizBlog.&nbsp;</p>\r\n<p>Это все временные решения, пока Apple не достроит свой собственный мини-городок iCamous.</p>\r\n',NULL,3,1),(343,'Nokia представляет \"HERE\".','<p><img alt=\"\" src=\"/sites/default/files/91363-here-ios.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Компания Nokia, у которой дела сейчас идут не очень хорошо, сообщает о запуске картографическом сервиса \"HERE\" (\"Здесь\"). «Here» станет первым локационным облаком, которое предоставит лучшие в мире карты и локационный опыт для всех.&nbsp;</p>','<p><img alt=\"\" src=\"/sites/default/files/91363-here-ios.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Компания Nokia, у которой дела сейчас идут не очень хорошо, сообщает о запуске картографическом сервиса \"HERE\" (\"Здесь\"). «Here» станет первым локационным облаком, которое предоставит лучшие в мире карты и локационный опыт для всех.&nbsp;</p>\r\n<div>«Люди хотят потрясающие карты, и с HERE мы сможем объединить локационные услуги и предложить лучший вариант для исследования и открытия их мира, – говорит генеральный директор Nokia Стивен Элоп. – Кроме того, с HERE мы можем расширить наш 20-летний опыт и знания для новых устройств и операционных систем, которые выходят за рамки Nokia. Мы убеждены, что в результате еще больше людей будет пользоваться нашими картографическими и локационными сервисами».&nbsp;</div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"/sites/default/files/Nokia-here-app-mapas.png\" style=\"width: 680px; height: 400px;\"></div>\r\n<div>&nbsp;</div>\r\n<div>Приложение HERE станет доступно в App Store уже совсем скоро и за бесплатно! HERE позволит работать с картами оффлайн, строить пешеходные маршруты с ипользование общественного транспорта.&nbsp;</div>\r\n<div>Уж что-что, а с картами у Nokia всегда было хорошо, так что пожелаем удачи финской компании и ждем HERE в App Store.</div>\r\n',NULL,3,1),(344,'Связной договорился с Apple о поставках яблочной продукции в регионах.','<p><img alt=\"\" src=\"/sites/default/files/6043981_news_bigpic.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Связной договорился с Apple о продвижение ее товаров в регионах России путем создания монобрендовых магазинов Apple Premium Reseller. Магазины могут появится уже в следующем году. Первый магазин планируется в городе Казань.&nbsp;</p>\r\n<p>На сегодняшний день сертифицированных магазинов Apple всего 63 и большинство из них пренадлежат компании re:Store. Такие магазины с лицензией&nbsp;Apple Premium Reseller по','<p><img alt=\"\" src=\"/sites/default/files/6043981_news_bigpic.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Связной договорился с Apple о продвижение ее товаров в регионах России путем создания монобрендовых магазинов Apple Premium Reseller. Магазины могут появится уже в следующем году. Первый магазин планируется в городе Казань.&nbsp;</p>\r\n<p>На сегодняшний день сертифицированных магазинов Apple всего 63 и большинство из них пренадлежат компании re:Store. Такие магазины с лицензией&nbsp;Apple Premium Reseller получают от Apple маркетинговый бюджет за популяризацию яблочной продукции — 1,5% от суммы закупки техники в месяц.</p>\r\n<p>В январе, конкурирующая компания Евросеть сообщила о сотрудничестве с корейской компанией Samsung.</p>\r\n<p>&nbsp;</p>\r\n',NULL,3,1),(345,'iPad 4 и iPad mini с LTE завтра.','<p><img alt=\"\" src=\"/sites/default/files/ipad-mini-1351017524.jpg\" style=\"width: 581px; height: 450px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Недавно Apple представила iPad mini и обновленный iPad 4. Новые продукты активно продаются уже со 2 ноября, но пока только Wi-Fi версии. Сообщается, что сегодня iPad 4 WiFi + Cellular уже завезли в яблочные магазины Apple Store и скорее всего завтра начнутся продажи. На счет iPad mini Wi-Fi + Cellular ничего толком не сообщается, но вероятнее всего, что они поступят в п','<p><img alt=\"\" src=\"/sites/default/files/ipad-mini-1351017524.jpg\" style=\"width: 581px; height: 450px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Недавно Apple представила iPad mini и обновленный iPad 4. Новые продукты <a href=\"http://macov.net/news/3mln-ipad-za-weekend\">активно продаются</a> уже со 2 ноября, но пока только Wi-Fi версии. Сообщается, что сегодня iPad 4 WiFi + Cellular уже завезли в яблочные магазины Apple Store и скорее всего завтра начнутся продажи. На счет iPad mini Wi-Fi + Cellular ничего толком не сообщается, но вероятнее всего, что они поступят в продажу также завтра.&nbsp;</p>\r\n<p>&nbsp;</p>\r\n',NULL,3,1),(346,'Apple откроет свои магазины в Бразилии и Турции.','<p><img alt=\"\" src=\"/sites/default/files/apple_store_fith.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Розничная сеть Apple активно развивается и растет. В 2013 году Apple запланировала открыть 30-35 новых магазинов Apple Store, причем 20-25 из них будут располагаться за пределами США.&nbsp;</p>\r\n<p>Первые новые магазины откроются в Рио-де-Жанейро (Бразилия) и в Стамбуле (Турция).&nbsp;</p>\r\n<p>Когда появится первый официальный магазин Apple Store в России неизвестно. Остается только ждать, быть может нам повез','<p><img alt=\"\" src=\"/sites/default/files/apple_store_fith.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Розничная сеть Apple активно развивается и растет. В 2013 году Apple запланировала открыть 30-35 новых магазинов Apple Store, причем 20-25 из них будут располагаться за пределами США.&nbsp;</p>\r\n<p>Первые новые магазины откроются в Рио-де-Жанейро (Бразилия) и в Стамбуле (Турция).&nbsp;</p>\r\n<p>Когда появится первый официальный магазин Apple Store в России неизвестно. Остается только ждать, быть может нам повезет и в следующем году яблочная компания все-таки откроет свой магазин у нас.</p>\r\n',NULL,3,1),(347,'Золотой iPad mini.','<p><img alt=\"\" src=\"/sites/default/files/Sd7vqyQHmh8.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Британская компания Gold&amp;Co выпустила экслюзивные версии iPad mini. Планшеты сделали из 24-каратного золота (обычного и золотого). В продаже появится ограниченное количество, каждый аппарат будет иметь свой уникальный номер.&nbsp;<br>\r\n	Планшеты упакуют в деревянные боксы и положат сертификат подлинности.</p>\r\n','<p><img alt=\"\" src=\"/sites/default/files/Sd7vqyQHmh8.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Британская компания Gold&amp;Co выпустила экслюзивные версии iPad mini. Планшеты сделали из 24-каратного золота (обычного и золотого). В продаже появится ограниченное количество, каждый аппарат будет иметь свой уникальный номер.&nbsp;<br>\r\n	Планшеты упакуют в деревянные боксы и положат сертификат подлинности.</p>\r\n',NULL,3,1),(348,'Samsung не хочет мириться с Apple.','<p><img alt=\"\" src=\"/sites/default/files/samsung-galaxy-s3-samsung-ceo.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Мы <a href=\"http://macov.net/news/apple-i-htc-dogovorilis\">писали</a> вам, что HTC решила примириться с Apple &nbsp;и подписала с ней 10-летние соглашение.&nbsp;Yonhap News сообщает, что Samsung не планирует и не хочет пытаться придти к соглашению с Apple.&nbsp;</p>\r\n<p>\"Это может быть правдой, возможно HTC и согласилась заплатить $276 млн Apple, но мы не намерены идти на такие переговоры. У нас н','<p><img alt=\"\" src=\"/sites/default/files/samsung-galaxy-s3-samsung-ceo.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Мы <a href=\"http://macov.net/news/apple-i-htc-dogovorilis\">писали</a> вам, что HTC решила примириться с Apple &nbsp;и подписала с ней 10-летние соглашение.&nbsp;Yonhap News сообщает, что Samsung не планирует и не хочет пытаться придти к соглашению с Apple.&nbsp;</p>\r\n<p>\"Это может быть правдой, возможно HTC и согласилась заплатить $276 млн Apple, но мы не намерены идти на такие переговоры. У нас нет таких намерений.\" - заявил глава мобильного ИТ-подразделения компании Samsung Шин Джонг-Кюн.</p>\r\n',NULL,3,1),(349,'Стартовая страница Safari обновилась.','<p><img alt=\"\" src=\"/sites/default/files/12.11.14-StartPage.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>В браузере Safari домашней страницей по умолчанию установлена стартовая страница сайта Apple. Долгое время, стартовая страница вела пользователей на apple.com, но недавно Apple поменяла ее, как она выглядит теперь можете увидеть по <a href=\"http://www.apple.com/startpage/index.html\">этой ссылке</a>.</p>\r\n','<p><img alt=\"\" src=\"/sites/default/files/12.11.14-StartPage.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>В браузере Safari домашней страницей по умолчанию установлена стартовая страница сайта Apple. Долгое время, стартовая страница вела пользователей на apple.com, но недавно Apple поменяла ее, как она выглядит теперь можете увидеть по <a href=\"http://www.apple.com/startpage/index.html\">этой ссылке</a>.</p>\r\n',NULL,3,1),(350,'В США украли 3600 iPad mini.','<p><img alt=\"\" src=\"/sites/default/files/10_gruz.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>New York Post сообщает, что в понедельник вечером со склада аэропорта Кеннеди в Нью-Йорке украли большую партию IPad mini. Общая сумма украденного около $1,5 млн.&nbsp;</p>\r\n<p>Сообщается, что грабителям удалось украсть 3600 яблочных планшетов, прибывших из Китая. Так как старт продаж iPad mini Wi-Fi + LTE намечаетеся на сегодня, то вероятнее всего это были модели с поддержкой сотовой связи.</p>\r\n<p>&nbsp;</p>\r\n','<p><img alt=\"\" src=\"/sites/default/files/10_gruz.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>New York Post сообщает, что в понедельник вечером со склада аэропорта Кеннеди в Нью-Йорке украли большую партию IPad mini. Общая сумма украденного около $1,5 млн.&nbsp;</p>\r\n<p>Сообщается, что грабителям удалось украсть 3600 яблочных планшетов, прибывших из Китая. Так как старт продаж iPad mini Wi-Fi + LTE намечаетеся на сегодня, то вероятнее всего это были модели с поддержкой сотовой связи.</p>\r\n<p>&nbsp;</p>\r\n',NULL,3,1),(351,'Скоро в App Store появятся Google Maps. ','<p><img alt=\"\" src=\"/sites/default/files/iphone_maps_icon_ios6.jpg\" style=\"width: 542px; height: 543px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Компания Google распространила тестовую версию своего картографического приложения для операционной системы iOS. Сейчас в программу вносят последние доработки и после ее отправят на рассмотрение яблочникам в App Store.&nbsp;</p>\r\n<p>В программе будет достпуна голосовая навигация, из-за которой, как многие опасаются, ее могут не пустить в App Store.&nbsp;</p>\r\n<div>\"Мы ','<p><img alt=\"\" src=\"/sites/default/files/iphone_maps_icon_ios6.jpg\" style=\"width: 542px; height: 543px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Компания Google распространила тестовую версию своего картографического приложения для операционной системы iOS. Сейчас в программу вносят последние доработки и после ее отправят на рассмотрение яблочникам в App Store.&nbsp;</p>\r\n<p>В программе будет достпуна голосовая навигация, из-за которой, как многие опасаются, ее могут не пустить в App Store.&nbsp;</p>\r\n<div>\"Мы хотим сделать Google Maps доступными всем, кто хочет их использовать, вне зависимости от устройства, браузера или операционной системы\", — заявили представители Google.</div>\r\n<div>Напомним вам, что представить свои карты пользователям i-девайсов решила и финская компания.</div>\r\n<div>Когда приложение появится в App Srore не сообщается, нам остается только ждать.</div>\r\n<div>&nbsp;</div>\r\n',NULL,3,1),(352,'Старт продаж iPad Mini LTE сегодня. ','<p><img alt=\"\" src=\"/sites/default/files/sprint-ipad-mini-640.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Оператор сотовой связи в Америке Sprint опубликовала пресс-релиз, в котором сообщается, что iPad mini LTE поступит в продажу &nbsp;в некоторых магазинах сети сегодня! Скорее всего другие американские операторы начнут продажу планшетов с поддержкой сотовой связи также сегодня.&nbsp;</p>\r\n<p>&nbsp;</p>\r\n','<p><img alt=\"\" src=\"/sites/default/files/sprint-ipad-mini-640.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Оператор сотовой связи в Америке Sprint опубликовала пресс-релиз, в котором сообщается, что iPad mini LTE поступит в продажу &nbsp;в некоторых магазинах сети сегодня! Скорее всего другие американские операторы начнут продажу планшетов с поддержкой сотовой связи также сегодня.&nbsp;</p>\r\n<p>&nbsp;</p>\r\n',NULL,3,1),(353,'Бета-тестирование «Сообщений» закончится в декабре.','<p><img alt=\"\" src=\"/sites/default/files/Messages-Beta1-600x470.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Сегодня Apple сообщила о том, что бета-тестирования приложения на OS X Lion \"Сообщения\" закончится 14 декабря этого года. И предлагается обновится до последней версии OS X Mountain Lion в Mac App Store за $25. Программа \"Сообщения\" является одной из фич в \"Горном Льве\", она заменяет устаревшую программу iChat. С помощью \"Сообщений\" вы можете переписываться по iMessage&nbsp;с пользователями iOS или OS X б','<p><img alt=\"\" src=\"/sites/default/files/Messages-Beta1-600x470.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Сегодня Apple сообщила о том, что бета-тестирования приложения на OS X Lion \"Сообщения\" закончится 14 декабря этого года. И предлагается обновится до последней версии OS X Mountain Lion в Mac App Store за $25. Программа \"Сообщения\" является одной из фич в \"Горном Льве\", она заменяет устаревшую программу iChat. С помощью \"Сообщений\" вы можете переписываться по iMessage&nbsp;с пользователями iOS или OS X бесплатно.&nbsp;</p>\r\n<p>Это значит, что у вас есть чуть меньше месяца, чтобы сходить в Mac App Store и обновится.&nbsp;</p>\r\n',NULL,3,1),(354,'Приложения от Apple в Google Play.','<p><img alt=\"\" src=\"/sites/default/files/googleplayapple.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>В магазине приложений |Google Play для устройст на базе Android появились приложения от Apple, такие как iPhoto, iMovie, Garage Band и т. д. Естественно это фальшивые приложения и Apple не выкладывала их. Это сделал один находчивый разработчик. Он указал, что приложения сделаны Apple Inc. и даже email указал интересный&nbsp;— android@apple.com. Цены он взял такие же, какие указаны в App Store минус 1-2 цента. Н','<p><img alt=\"\" src=\"/sites/default/files/googleplayapple.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>В магазине приложений |Google Play для устройст на базе Android появились приложения от Apple, такие как iPhoto, iMovie, Garage Band и т. д. Естественно это фальшивые приложения и Apple не выкладывала их. Это сделал один находчивый разработчик. Он указал, что приложения сделаны Apple Inc. и даже email указал интересный&nbsp;— android@apple.com. Цены он взял такие же, какие указаны в App Store минус 1-2 цента. Некоторые уже купились на это и потратили свои денюжки на ветер.</p>\r\n<p>Такое в Google Play возможно из-за того, что Google практически не котролирует пропуск приложений в магазин. Часто бывает, что в Google Play приложения дублируются или несут в себе вирус.&nbsp;</p>\r\n<p>Поэтому будьте внимательны и если хотите обезопасить себя приобритайте Apple.</p>\r\n<p>&nbsp;</p>\r\n',NULL,3,1),(355,'Задержан подозреваемый в крупной краже iPad mini.','<p><img alt=\"\" src=\"/sites/default/files/jfk-airport-ipad-heist1.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Ранее <a href=\"http://macov.net/news/v-usa-ukrali-3600-ipadmini\">мы писали вам</a>, что на прошлой неделе из аэропорта Кеннеди в США была украдена большая партия iPad mini. Сотрудники ФБР начали раследование и уже в пятницу задержила подозреваемого&nbsp;Ренел Рене Ричардсон (Renel Rene Richardson). Ренел работал с двумя сообщниками, все они работники аэропорта.&nbsp;</p>','<p><img alt=\"\" src=\"/sites/default/files/jfk-airport-ipad-heist1.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Ранее <a href=\"http://macov.net/news/v-usa-ukrali-3600-ipadmini\">мы писали вам</a>, что на прошлой неделе из аэропорта Кеннеди в США была украдена большая партия iPad mini. Сотрудники ФБР начали раследование и уже в пятницу задержила подозреваемого&nbsp;Ренел Рене Ричардсон (Renel Rene Richardson). Ренел работал с двумя сообщниками, все они работники аэропорта.&nbsp;</p>\r\n<p>На них смогли выйти благодаря опросу работников. Многие служащие аэропорта заявили, что в тот день Рене вел себя оченб подозрительно. Он постоянно спрашивал когда придет партия iPad mini, а также где можно найти электропогрузчик.&nbsp;</p>\r\n<p>Ричардсон уже сознался и сообщил федеральным агентам и местонахождении грузовика с награбленными планшетами. Пока не сообщается нашли ли его.</p>\r\n<p>&nbsp;</p>\r\n',NULL,3,1),(356,'Новый раздел eBay — Apple Certified Refurbished.','<p><img alt=\"\" src=\"/sites/default/files/ipad121105-2.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>На аукционном сайте eBay появился <a href=\"http://stores.ebay.com/Refurbished-Outlet\">новый раздел Apple Certified Refurbished</a>, в котором расположились продукты Apple, востановленные после ремонта. Уже сейчас там можно найтиразличные модели MacBook, iPad или iPod. eBay сообщает, что все представленные в этом разделе яблочные продукты прошли специальный контроль качества и прода.тся с полной годовой гарантией.&','<p><img alt=\"\" src=\"/sites/default/files/ipad121105-2.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>На аукционном сайте eBay появился <a href=\"http://stores.ebay.com/Refurbished-Outlet\">новый раздел Apple Certified Refurbished</a>, в котором расположились продукты Apple, востановленные после ремонта. Уже сейчас там можно найтиразличные модели MacBook, iPad или iPod. eBay сообщает, что все представленные в этом разделе яблочные продукты прошли специальный контроль качества и прода.тся с полной годовой гарантией.&nbsp;</p>\r\n',NULL,3,1),(357,'Google заплатит рекордный штраф за обход защиты Safari.','<p><img alt=\"\" src=\"/sites/default/files/phpThumb_generated_thumbnail.jpeg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Летом, федеральный&nbsp;суд Сан-Франциско рассматривал возможность наложить обязаность на компанию Google выплатить штраф в размере&nbsp;$22.5 млн за незаконный сбор персональных данных у пользователей браузера Safari. Сегодня окружной судья США Сьюзен Илстон одобрил штраф за нарушение приватности и теперь Google обязана его выплатить. Это самы крупный штраф Федеральной Торговой Комиссии США.</p>\r','<p><img alt=\"\" src=\"/sites/default/files/phpThumb_generated_thumbnail.jpeg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Летом, федеральный&nbsp;суд Сан-Франциско рассматривал возможность наложить обязаность на компанию Google выплатить штраф в размере&nbsp;$22.5 млн за незаконный сбор персональных данных у пользователей браузера Safari. Сегодня окружной судья США Сьюзен Илстон одобрил штраф за нарушение приватности и теперь Google обязана его выплатить. Это самы крупный штраф Федеральной Торговой Комиссии США.</p>\r\n',NULL,3,1),(358,'Покупаем музыку в русском iTunes.','<p><img alt=\"\" src=\"/sites/default/files/iTunes_01.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Если вы зайдете на своем яблочном устройстве на iOS в приложение iTunes под русской учеткой, то вам будут доступны только два раздела: iTunes U и Подкасты. Американцы же, например, &nbsp;через iTunes доступна возможность покупать песни, сериалы фильмы и другой медиа-контент. В России это пока не доступно.</p>\r\n<p>Но несколько часов назад пользователь твиттера @Ivan_Kir обнаружил, что купить музыку в русском iTunes уж','<p><img alt=\"\" src=\"/sites/default/files/iTunes_01.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Если вы зайдете на своем яблочном устройстве на iOS в приложение iTunes под русской учеткой, то вам будут доступны только два раздела: iTunes U и Подкасты. Американцы же, например, &nbsp;через iTunes доступна возможность покупать песни, сериалы фильмы и другой медиа-контент. В России это пока не доступно.</p>\r\n<p>Но несколько часов назад пользователь твиттера @Ivan_Kir обнаружил, что купить музыку в русском iTunes уже можно! Для этого вам надо зайти в \"Музыку\", в поле для поиска вбить название любого исполните и нажать \"продолжить поиск в магазине...\".</p>\r\n<p>Причем для пользователей с русскими аккаунтами цена за песню значительно дешевле, чем, для тех же американцев. Так за песню $0.99 в США, мы заплатим 15 р. ($0.47), а зальбом всего&nbsp;99 р. ($3.12) против $6,99-14,99 в США.&nbsp;</p>\r\n<p>&nbsp;</p>\r\n',NULL,3,1),(359,'iPhone 5s и iPad 5 летом 2013.','<p><img alt=\"\" src=\"/sites/default/files/8c3cc51c1d592965277938cbcdbd82b5.jpeg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Интернет-портал &nbsp;Digitimes опубликовали слухи, которые, как они утверждают, получили от своих достоверных источников. Во-первых, они подтверждают слух, что производство iPhone 5s начнется уже в начале 2013 года. Во-вторых, они утверждают, что старт продаж&nbsp;iPad 5 и iPhone 5S состоится в середине 2013 года.</p>\r\n<p>Но не стоит сильно доверять этим слухам, Digitimes не раз ошибались со ','<p><img alt=\"\" src=\"/sites/default/files/8c3cc51c1d592965277938cbcdbd82b5.jpeg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Интернет-портал &nbsp;Digitimes опубликовали слухи, которые, как они утверждают, получили от своих достоверных источников. Во-первых, они подтверждают слух, что производство iPhone 5s начнется уже в начале 2013 года. Во-вторых, они утверждают, что старт продаж&nbsp;iPad 5 и iPhone 5S состоится в середине 2013 года.</p>\r\n<p>Но не стоит сильно доверять этим слухам, Digitimes не раз ошибались со своими прогнозами.</p>\r\n',NULL,3,1),(360,'ВИДЕО: 3D реклама новых iPod.','<p>Потрясающая реклама iPod touch.&nbsp;Один из пользователей ресурса pitchfork.com зайдя на главную страницу сайта увидел эту рекламу новых iPod.</p>\r\n<p>&nbsp;</p>\r\n<iframe width=\"560\" height=\"315\" src=\"http://www.youtube.com/embed/SgfJn8FsSKg\" frameborder=\"0\" allowfu<p>&nbsp;</p>\r\n<div>Один из пользователей ресурса pitchfork.com зайдя на главную страницу сайта увидел эту рекламу новых iPod.&nbsp;</div>\r\n<div>Потрясающая реклама iPod.</div>\r\n<p>&nbsp;</p>\r\n<p><iframe allowfullscreen=\"\" framebo','<p>&nbsp;</p>\r\n<div>Один из пользователей ресурса pitchfork.com зайдя на главную страницу сайта увидел эту рекламу новых iPod.&nbsp;</div>\r\n<div>Потрясающая реклама iPod.</div>\r\n<p>&nbsp;</p>\r\n<p><iframe allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"http://www.youtube.com/embed/SgfJn8FsSKg\" width=\"560\"></iframe></p>\r\n',NULL,3,1),(361,'Акции Apple начали расти вверх.','<p><img alt=\"\" src=\"/sites/default/files/aapl.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Акции Apple после продолжительного спада наконец-то опять начали рости вверх. За последние сутки цена за акцию выросла почти на 6% после высказывания известного аналитика \"Topeka\" Браена Вайта.&nbsp;</p>','<p><img alt=\"\" src=\"/sites/default/files/aapl.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Акции Apple после продолжительного спада наконец-то опять начали рости вверх. За последние сутки цена за акцию выросла почти на 6% после высказывания известного аналитика \"Topeka\" Браена Вайта.&nbsp;</p>\r\n<p>\"На наш взгляд за последние восемь недель акции компании Apple добрались до такой «безумно безумной» низкой стоимости, что это просто не могло продолжаться далее. Новые продукты компании очень популярны, надвигается праздничный сезон покупок, у Apple есть привлекательные долгосрочные возможности роста, а также компания распространяет значительный денежный поток инвесторам. Apple сильна как никогда, ведь исторически это был самым успешный квартал этого года для акций компании\", — заявил Вайт.</p>\r\n',NULL,3,1),(362,'Связной стал третьим в России прямым дистрибьютором Apple.','<p><img alt=\"\" src=\"/sites/default/files/russia_svyaznoy_reseller_apple_0.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Связной стал третьим поставщиком iPhone на российском рынке. До этого прямые поставки iPhone получали только МТС и Вымпелком (Билайн). Многие полагают, что Apple пошла на этот шаг из-за заявления Федеральной антимонопольной службы, в котором она признала ценовой сговор между МТС и Вымпелком по iPhone 4. Во всем мире Apple поставляет iPhone только избранным сотовым операторам и Связной стал искл','<p><img alt=\"\" src=\"/sites/default/files/russia_svyaznoy_reseller_apple_0.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Связной стал третьим поставщиком iPhone на российском рынке. До этого прямые поставки iPhone получали только МТС и Вымпелком (Билайн). Многие полагают, что Apple пошла на этот шаг из-за заявления Федеральной антимонопольной службы, в котором она признала ценовой сговор между МТС и Вымпелком по iPhone 4. Во всем мире Apple поставляет iPhone только избранным сотовым операторам и Связной стал исключением.&nbsp;</p>\r\n<p>\"Основная идея нашего сотрудничества с Apple – не заработать большую маржу на iPhone, которая, кстати, одна из самых невысоких на рынке, а получить возможность привлечения клиентов к сопутствующим аксессуарам, сервисам и тарифным предложениям, а также к экосистеме Apple – контенту App Store\",- пояснил Денис Людковский, генеральный директор ГК «Связной».</p>\r\n<p>Старт продаж iPhone 5 &nbsp;в России ожидается в декабре, его розничная цена пока не распространяется, но по слухам цена за младшую модель будет 29990 рублей. Возможно из-за прихода третьего игрока цена будет ниже.</p>\r\n',NULL,3,1),(363,'Nokia выложила свои карты HERE в App Store.','<p><img alt=\"\" src=\"/sites/default/files/mzl.cfkdfwso.480x480-75.jpg\" style=\"width: 360px; height: 480px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Неделю назад мы писали вам, что Nokia собирается выпустить свое картографическое приложение. &nbsp;И вот оно появилось в App Store. Приложение универсальное и достпуно для iPhone и iPad.&nbsp;</p>\r\n<p>Качайте по этой <a href=\"https://itunes.apple.com/ru/app/here-maps/id577430143?mt=8\">ссылке</a> и пишите в комментариях как вам нокиавские карты.&nbsp;</p>','<p><img alt=\"\" src=\"/sites/default/files/mzl.cfkdfwso.480x480-75.jpg\" style=\"width: 360px; height: 480px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Неделю назад мы писали вам, что Nokia собирается выпустить свое картографическое приложение. &nbsp;И вот оно появилось в App Store. Приложение универсальное и достпуно для iPhone и iPad.&nbsp;</p>\r\n<p>Качайте по этой <a href=\"https://itunes.apple.com/ru/app/here-maps/id577430143?mt=8\">ссылке</a> и пишите в комментариях как вам нокиавские карты.&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" src=\"/sites/default/files/mzl.xnmkzcps.480x480-75.jpg\" style=\"width: 360px; height: 480px;\"></p>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" src=\"/sites/default/files/mzl.yzbgtemj.480x480-75.jpg\" style=\"width: 360px; height: 480px;\"></p>\r\n',NULL,3,1),(364,'ВИДЕО: Две новые рекламы iPhone 5.','','<p>Совсем недавно Apple опубликовала две новые рекламы под названием \"Оркестр\" и \"Индейка\".</p>\r\n<p>&nbsp;</p>\r\n<iframe width=\"560\" height=\"315\" src=\"http://www.youtube.com/embed/3qBaTYNKluM\" frameborder=\"0\" allowfullscreen></iframe>\r\n<p>&nbsp;</p>\r\n<iframe width=\"560\" height=\"315\" src=\"http://www.youtube.com/embed/2xHquhlr45w\" frameborder=\"0\" allowfullscreen></iframe>',NULL,3,1),(365,'Siri заговорит еще на 5 языках.','<p><img alt=\"\" src=\"/sites/default/files/Siri.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Apple на сайте опубликовала новые вакансии. Яблочники ведут набор в команду голосового помощника Siri.&nbsp;</p>\r\n<p>Apple ищет носителей&nbsp;арабского, норвежского, голландского, шведского, финского и датского языков. Носители русского языка не теребуются, а жаль.</p>\r\n','<p><img alt=\"\" src=\"/sites/default/files/Siri.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Apple на сайте опубликовала новые вакансии. Яблочники ведут набор в команду голосового помощника Siri.&nbsp;</p>\r\n<p>Apple ищет носителей&nbsp;арабского, норвежского, голландского, шведского, финского и датского языков. Носители русского языка не требуются, а жаль.</p>\r\n',NULL,3,1),(366,'Скрытая реклама Surface.','Oprah Winfrey написала в twitter о своей любви к Microsoft Surface… с помощью iPad.\r\nЧасто в сети можно встретить много приколов, которые связаны с iГаджетами, но как же осенними вечерами веселят подобные истории. Известная телеведущая призналась в своей любви к Oprah Winfrey планшету Microsoft Surface постом в социальной сети twitter, но вся соль в том, что сделал она это с помощью iPad. Вот текст этого твита: “Gotta say love that SURFACE! Have bought 12 already for Christmas gifts. #FavoriteTh','<p><img alt=\"\" src=\"/sites/default/files/oprahlovessurfaceoftheipad-600x471.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Известная американская телеведущая Опра Уинфри написала в твиттере признание в своей любви к планшету Microsoft Surface. И все бы хорошо, но только написала она сообщение с яблочного планшета iPad, а это очень похоже на то, что это скрытая проплаченная реклама.</p>\r\n<p>Перевод твит: \"Хочу выразить любовь к&nbsp;Surface! Я уже купила 12 штук в качестве рождественных подарков.\"&nbsp;</p>\r\n<p>&nbsp;</p>\r\n',NULL,3,1),(367,'Каждое прикосновение на iOS гаджете будет записано.','<p><img alt=\"\" src=\"/sites/default/files/Apple-patent-drawing-002.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Apple будет записывать все ваши действия на устройстве. Яблочная компания зарегистрировала очередной патент, в котором сообщается, что в iOS будет функция запоминающая каждое действие пользователя гаджета на iOS. Каждый звонок, каждая смс, набор письма, достижение в игре — будет записываться в специальном месте. Будет формироваться уникальная подробная схема для каждого устройства.&nbsp;</p>','<p><img alt=\"\" src=\"/sites/default/files/Apple-patent-drawing-002.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Apple будет записывать все ваши действия на устройстве. Яблочная компания зарегистрировала очередной патент, в котором сообщается, что в iOS будет функция запоминающая каждое действие пользователя гаджета на iOS. Каждый звонок, каждая смс, набор письма, достижение в игре — будет записываться в специальном месте. Будет формироваться уникальная подробная схема для каждого устройства.&nbsp;</p>\r\n<p>Это позволит Apple узнать о вас больше, для того чтобы максимально подстраиваться под вас и оптимизировать поиск.&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" src=\"/sites/default/files/Apple-patent-drawing-001.jpg\"></p>\r\n',NULL,3,1),(368,'Выход iTunes 11 уже скоро.','<p><img alt=\"\" src=\"/sites/default/files/NewImage32%20%281%29.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Apple разослала письмо всем звукозаписывающим студиям, в котором яблочники просят предоставить различные данные обо всех артистах. Это нужно для того, чтобы к выходу iTunes 11 обработать фотографии под новый формат и показать информацию исполнителя во всей красе.&nbsp;</p>\r\n<p>Apple обещала выпустить iTunes 11 еще в октябре, но Apple решила отложить выпуск новой версии медиа комбайна, чтобы устранить все о','<p><img alt=\"\" src=\"/sites/default/files/NewImage32%20%281%29.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Apple разослала письмо всем звукозаписывающим студиям, в котором яблочники просят предоставить различные данные обо всех артистах. Это нужно для того, чтобы к выходу iTunes 11 обработать фотографии под новый формат и показать информацию исполнителя во всей красе.&nbsp;</p>\r\n<p>Apple обещала выпустить iTunes 11 еще в октябре, но Apple решила отложить выпуск новой версии медиа комбайна, чтобы устранить все ошибки и довести программу до совершенства. Ждем.</p>\r\n',NULL,3,1),(369,'GTA Vice City на iOS 6 декабря.','<p><img alt=\"\" src=\"/sites/default/files/actual_1353096298.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Компания Rockstar объявила дату выхода мобильной версии игры&nbsp;GTA: Vice City — 6 декабря. Игра&nbsp;появится на iOS и Android и для обеих платформ будет стоить $5.</p>','<p><img alt=\"\" src=\"/sites/default/files/actual_1353096298.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Компания Rockstar объявила дату выхода мобильной версии игры&nbsp;GTA: Vice City — 6 декабря. Игра&nbsp;появится на iOS и Android и для обеих платформ будет стоить $5.</p>\r\n<p>Это уже становится традицией для Rockstar портировать версии старых игр на мобильные устройства. Год назад издательство выпустила GTA III в честь 10-летия игры.&nbsp;</p>\r\n<p>Grand Theft Auto: Vice City 10th Anniversary Edition выйдет в честь десятилетнего юбилея игры. Ее первый релиз состоялся осенью 2002 года, когда игра появилась на консоли PS2. Позже она была портирована на другие платформы, в том числе Windows и Xbox.</p>\r\n',NULL,3,1),(370,'Apple запатентовала iMessage и Passbook.','<p><img alt=\"\" src=\"/sites/default/files/6a0120a5580826970c017c33d66904970b-800wi-642x785.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Apple запатентовала &nbsp;слово \"iMessage\", теперь никто не может использовать его, кроме яблочников. Кроме того была запатентована иконка Passbook.</p>\r\n','<p><img alt=\"\" src=\"/sites/default/files/6a0120a5580826970c017c33d66904970b-800wi-642x785.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Apple запатентовала &nbsp;слово \"iMessage\", теперь никто не может использовать его, кроме яблочников. Кроме того была запатентована иконка Passbook.</p>\r\n',NULL,3,1),(371,'ВИДЕО: Классный костюм с помощью iPhone или iPad.','<p>Хэллоин прошел совсем недавно, но вы можете использовать такой наряд для розыгрыша друзей. Или сохраните в закладках и используйте совет в следующем году.&nbsp;</p>\r\n<p>Итак, вам понадобится футболка и бесплатное приложение <a href=\"https://itunes.apple.com/ru/app/id554776718?mt=8\">Digital Dudz</a>. Можете приступать пугать друзей! Ну или выходите на улицу и развлекайтесь пугая прохожих.</p>\r\n<p>&nbsp;</p>\r\n<iframe width=\"560\" height=\"315\" src=\"http://www.youtube.com/embed/fU0gs6KQ8jw\" frameb','<p>Хэллоин прошел совсем недавно, но вы можете использовать такой наряд для розыгрыша друзей. Или сохраните в закладках и используйте совет в следующем году.&nbsp;</p>\r\n<p>Итак, вам понадобится футболка и бесплатное приложение <a href=\"https://itunes.apple.com/ru/app/id554776718?mt=8\">Digital Dudz</a>. Можете приступать пугать друзей! Ну или выходите на улицу и развлекайтесь пугая прохожих.</p>\r\n<p>&nbsp;</p>\r\n<iframe width=\"560\" height=\"315\" src=\"http://www.youtube.com/embed/fU0gs6KQ8jw\" frameborder=\"0\" allowfullscreen></iframe>\r\n<p>&nbsp;</p>\r\n<iframe width=\"560\" height=\"315\" src=\"http://www.youtube.com/embed/V6p5mbp_M98\" frameborder=\"0\" allowfullscreen></iframe>\r\n',NULL,3,1),(372,'Яблочный телевизор в 2013.','<p><img alt=\"\" src=\"/sites/default/files/zDnFZYwzTK4.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Появились слухи, что в 2013 году Apple выпустит свой телевизор. Предполагается, что диагональ устройства будет&nbsp;42-55 дюймов, а стоимость — примерно $1500-2000. Скорее всего в яблочном телевизоре будет доступны такие функции, как голосовой помощник Siri, видео-звонки FaceTime, выступать в качестве универсального дисплея для iOS-устройства. Без сомнения, телевизор от Apple должен выглядеть стильно, под стать дру','<p><img alt=\"\" src=\"/sites/default/files/zDnFZYwzTK4.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Появились слухи, что в 2013 году Apple выпустит свой телевизор. Предполагается, что диагональ устройства будет&nbsp;42-55 дюймов, а стоимость — примерно $1500-2000. Скорее всего в яблочном телевизоре будет доступны такие функции, как голосовой помощник Siri, видео-звонки FaceTime, выступать в качестве универсального дисплея для iOS-устройства. Без сомнения, телевизор от Apple должен выглядить стильно, под стать другим яблочным продуктам.</p>\r\n<p>&nbsp;</p>\r\n',NULL,3,1),(373,'Звоня в тех. поддержку Apple ругаясь, вас соединят немедленно.','<p><img alt=\"\" src=\"/sites/default/files/8UX4BB9EgF8.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Когда вы звоните в техническую поддержку крупной фирмы вы вначале попадаете на автоответчик, слушаете новости компании, отвечаете на ряд вопросов и потом после какого-то времени вас связывают со специалистом. Однако есть способ этого избежать, если вы звоните в тех. поддержку Apple. Оказывается, что если вы будете ругаться с автоответчиком, произнося \"fuck\" или другие однокоренные слова, то с вами моментально свяжу','<p><img alt=\"\" src=\"/sites/default/files/8UX4BB9EgF8.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Когда вы звоните в техническую поддержку крупной фирмы вы вначале попадаете на автоответчик, слушаете новости компании, отвечаете на ряд вопросов и потом после какого-то времени вас связывают со специалистом. Однако есть способ этого избежать, если вы звоните в тех. поддержку Apple. Оказывается, что если вы будете ругаться с автоответчиком, произнося \"fuck\" или другие однокоренные слова, то с вами моментально свяжуется глава call-центра.&nbsp;</p>\r\n<p>Такой способ обнаружил одни из пользователей соцсети Reddit.&nbsp;</p>\r\n',NULL,3,1),(374,'ВИДЕО: Новая мобильная ОС — Sailfish.','<p>Компания Jolla разрабатывает новую мобильную операционную систему — Sailfish. Разработчикам дали 10 млн евро инвесторы.&nbsp;</p>\r\n<p>Jolla считает, что их Sailfish сможет конкурировать другими ОС и получит распространение на азиатском рынке, особенно в Китае.&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<iframe width=\"560\" height=\"315\" src=\"http://www.youtube.com/embed/tRZxM9rNyZ4\" frameborder=\"0\" allowfullscreen></iframe>','<p>Компания Jolla разрабатывает новую мобильную операционную систему — Sailfish. Разработчикам дали 10 млн евро инвесторы.&nbsp;</p>\r\n<p>Jolla считает, что их Sailfish сможет конкурировать другими ОС и получит распространение на азиатском рынке, особенно в Китае.&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><iframe allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"http://www.youtube.com/embed/tRZxM9rNyZ4\" width=\"560\"></iframe></p>\r\n',NULL,3,1),(375,'Apple отказывается от производства батарей у Samsung.','<p><img alt=\"\" src=\"/sites/default/files/samsung-battery-540x336.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Недавно Apple прекратила собирать флеш-накопители у Samsung. Сегодня стало известно, что яблочники хотят отказаться и от производства батарей для iPad и Mac на заводах южнокорейской компании. Теперь этим будет заниматься&nbsp;компании Amperex Technology Limited и Tianjin Lishen Battery.&nbsp;Однако, пока Samsung еще будет работать на Apple по сборке процессоров A4, A5 и A6.</p>\r\n<p>Похоже, что постепенн','<p><img alt=\"\" src=\"/sites/default/files/samsung-battery-540x336.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Недавно Apple прекратила собирать флеш-накопители у Samsung. Сегодня стало известно, что яблочники хотят отказаться и от производства батарей для iPad и Mac на заводах южнокорейской компании. Теперь этим будет заниматься&nbsp;компании Amperex Technology Limited и Tianjin Lishen Battery.&nbsp;Однако, пока Samsung еще будет работать на Apple по сборке процессоров A4, A5 и A6.</p>\r\n<p>Похоже, что постепенно Apple хочет уйти от сотрудничества с Samsung.&nbsp;</p>\r\n',NULL,3,1),(376,'ВИДЕО: Best Buy рекламирует Apple.','<p>В \"черную пятницу\" Best Buy решила продемонстрировать возможности самых продаваемых устройств —продукты Apple, выпустив отличный рекламный ролик.</p>\r\n<p>&nbsp;</p>\r\n<iframe width=\"560\" height=\"315\" src=\"http://www.youtube.com/embed/VEKer84625Q\" frameborder=\"0\" allowfullscreen></iframe>','<p>В \"черную пятницу\" Best Buy решила продемонстрировать возможности самых продаваемых устройств —продукты Apple, выпустив отличный рекламный ролик.</p>\r\n<p>&nbsp;</p>\r\n<iframe width=\"560\" height=\"315\" src=\"http://www.youtube.com/embed/VEKer84625Q\" frameborder=\"0\" allowfullscreen></iframe>',NULL,3,1),(377,'Покупатели выбирают Apple.','<p>Компания IBM отследила какие продукты продавались в Черную пятницу. В подробной инфографике все очень наглядно видно.&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" src=\"/sites/default/files/ibm2-jpg.jpg\"></p>\r\n','<p>Компания IBM отследила какие продукты продавались в Черную пятницу. В подробной инфографике все очень наглядно видно.&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" src=\"/sites/default/files/ibm2-jpg.jpg\"></p>\r\n',NULL,3,1),(378,'Apple перекупила права на \"Lightning\" у Harley-Davidson.','<p><img alt=\"\" src=\"/sites/default/files/98222-640.jpg\" style=\"width: 410px; height: 640px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Apple перекупила права на использование торговой марки \"Lightning\" у Harley-Davidson. &nbsp;Но производитель мотоциклов оставил за собой право на использование слова Lightning в своей продукции.</p>\r\n','<p><img alt=\"\" src=\"/sites/default/files/98222-640.jpg\" style=\"width: 410px; height: 640px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Apple перекупила права на использование торговой марки \"Lightning\" у Harley-Davidson. &nbsp;Но производитель мотоциклов оставил за собой право на использование слова Lightning в своей продукции.</p>\r\n',NULL,3,1),(379,'Apple-1 1976 года был продан на аукционе за 400000 евро.','<p><img alt=\"\" src=\"/sites/default/files/Qjw0tWxxeYk.jpg\" style=\"width: 576px; height: 400px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Компьютер Apple-1 был куплен на немецком аукционе анонимным покупателем за 400000 евро. &nbsp;Это полностью рабочий компьютер 1976 года с небольшим монитором. Всего таких было произведено примерно 200 машин.</p>\r\n<p>Напомним вам, что Apple-1 был создан в гараже Стивом Джобсом и Стивом Возняком. В 1976 году они продавали его за $666.66.&nbsp;</p>\r\n<p>&nbsp;</p>\r\n','<p><img alt=\"\" src=\"/sites/default/files/Qjw0tWxxeYk.jpg\" style=\"width: 576px; height: 400px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Компьютер Apple-1 был куплен на немецком аукционе анонимным покупателем за 400000 евро. &nbsp;Это полностью рабочий компьютер 1976 года с небольшим монитором. Всего таких было произведено примерно 200 машин.</p>\r\n<p>Напомним вам, что Apple-1 был создан в гараже Стивом Джобсом и Стивом Возняком. В 1976 году они продавали его за $666.66.&nbsp;</p>\r\n<p>&nbsp;</p>\r\n',NULL,3,1),(380,'Вышла новая бета-версия Mac OS X 10.8.3.','Apple выложила бету OSX 10.8.3\r\nНу вот День Благодарения пролетел и Apple, как и обещала сразу завалила разработчиков работой выдав им бету OSX 10.8.3. Изменений там нет, а исправлений всего три, поэтому особо радоваться пока рано. Однако Apple просит разработчиков поглядеть по внимательней на AirPlay, AirPort, GameCenter и графические драйверы. Обычные пользователи могут начинать ждать финал, а разработчики бежать в на сайт Apple и качать новинку.\r\n обратить особое внимание на графические драйв','<p><img alt=\"\" src=\"/sites/default/files/image16.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Как и обещали яблочники, после дня Благодарения выпустили бету&nbsp;OS X 10.8.3. Списка изменений нет, поэтому пока неизвестно изменилось ли что-то. Однако Apple просит разработчиков&nbsp;&nbsp;обратить особое внимание на графические драйвера, на работу AirPort и AirPlay, а также на интеграцию с Game Center.</p>\r\n',NULL,3,1),(381,'Facebook позволил своим пользователям дарить  медиа-контент iTunes. ','<p><img alt=\"\" src=\"/sites/default/files/displaymedia-ashx.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Facebook объявил нововведение — возможность дарить подарочные сертификаты iTunes внутри социальной сети. Также теперь вы можете рекомендовать или подарить своему другу в Facebook трек, альбом, сериал или фильм. Хорошая возможность порадовать близких людей, поднять настроение хорошим медиа-контентом.</p>\r\n<p>&nbsp;</p>\r\n','<p><img alt=\"\" src=\"/sites/default/files/displaymedia-ashx.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Facebook объявил нововведение — возможность дарить подарочные сертификаты iTunes внутри социальной сети. Также теперь вы можете рекомендовать или подарить своему другу в Facebook трек, альбом, сериал или фильм. Хорошая возможность порадовать близких людей, поднять настроение хорошим медиа-контентом.</p>\r\n<p>&nbsp;</p>\r\n',NULL,3,1),(382,'Apple запатентовала иконку Siri.','<p><img alt=\"\" src=\"/sites/default/files/12.11.27-Siri_Icon.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Очередной патент Apple, на этот раз иконка Siri. Сегодня ведомство патентов и торговых марок США (USPTO) удовлетворили заявку Apple о патенте на иконку Siri. Запрос был сделан еще 4 октября 2011 года и только сегодня одобрили.&nbsp;<br>\r\n	После выхода яблочных продуктов с голосовым помощником, многие стали копировать не только функционал, но и саму иконку — микрофон в круге с рамками.</p>\r\n','<p><img alt=\"\" src=\"/sites/default/files/12.11.27-Siri_Icon.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Очередной патент Apple, на этот раз иконка Siri. Сегодня ведомство патентов и торговых марок США (USPTO) удовлетворили заявку Apple о патенте на иконку Siri. Запрос был сделан еще 4 октября 2011 года и только сегодня одобрили.&nbsp;<br>\r\n	После выхода яблочных продуктов с голосовым помощником, многие стали копировать не только функционал, но и саму иконку — микрофон в круге с рамками.</p>\r\n',NULL,3,1),(383,'Медведев уже получил свой iPhone 5.','<p>&nbsp;</p>\r\n<div><img alt=\"\" src=\"/sites/default/files/000-par7398206.jpg\"></div>\r\n<div>&nbsp;</div>\r\n<div>Премьер-министр России, Дмитрий Медведев, прибыл во вторник во Францию с рабочим визитом. В Париже его встретил французский премьер Жан-Марк Эйро. Главы правительств провели весь день вместе, обсуждая государственные дела. Во время прогулки по Парижу между запланированными мероприятиями Дмитрий Медведев нашел время сфотографировать Эйфелеву башню на свою новенький iPhone 5 и выложить фот','<p>&nbsp;</p>\r\n<div><img alt=\"\" src=\"/sites/default/files/000-par7398206.jpg\"></div>\r\n<div>&nbsp;</div>\r\n<div>Премьер-министр России, Дмитрий Медведев, прибыл во вторник во Францию с рабочим визитом. В Париже его встретил французский премьер Жан-Марк Эйро. Главы правительств провели весь день вместе, обсуждая государственные дела. Во время прогулки по Парижу между запланированными мероприятиями Дмитрий Медведев нашел время сфотографировать Эйфелеву башню на свою новенький iPhone 5 и выложить фотку в свой Instagram.</div>\r\n<div>Главы двух правительств завершили встречу торжественным открытием выставки «Писатели и интеллектуалы между Россией и Францией», которая была посвящена связям между русской и французской творческой интеллигенцией, передает AFP.</div>\r\n',NULL,3,1),(384,'Leofrutta вставила логотип Apple в свой.','<p><img alt=\"\" src=\"/sites/default/files/mela_mela_camion_600.jpg\" style=\"width: 600px; height: 450px; \"></p>\r\n<p>&nbsp;</p>\r\n<p>Сицилийская компания&nbsp;Leofrutta, занимающаяся продажей фруктов, украла всеми известный логотип Apple — надкусанное яблоко. Логитип&nbsp;Leofrutta с яблочком красуется на фирменных грузовиках, а также на <a href=\"http://leofrutta.it/\">сайте</a>.</p>\r\n<p>Похоже скоро им придется делать ребрендинг, попутно выплачивая Apple за использования яблочка.</p>\r\n','<p><img alt=\"\" src=\"/sites/default/files/mela_mela_camion_600.jpg\" style=\"width: 600px; height: 450px; \"></p>\r\n<p>&nbsp;</p>\r\n<p>Сицилийская компания&nbsp;Leofrutta, занимающаяся продажей фруктов, украла всеми известный логотип Apple — надкусанное яблоко. Логитип&nbsp;Leofrutta с яблочком красуется на фирменных грузовиках, а также на <a href=\"http://leofrutta.it/\">сайте</a>.</p>\r\n<p>Похоже скоро им придется делать ребрендинг, попутно выплачивая Apple за использования яблочка.</p>\r\n',NULL,3,1),(385,'Реклама iPad mini в журналах.','<p><img alt=\"\" src=\"/sites/default/files/ipad_mini_ad_time.jpg\" style=\"width: 580px; height: 386px; \"></p>\r\n<p>&nbsp;</p>\r\n<p>Apple запустила оригинальную рекламу в печатных журналах TIME и New York Times. На развороте журнала на одной стороне обложка печатного издания, а на другой изабражен iPad mini в формате 1:1. На экране планшета отрыт этот же журнал в программе Киоск. Тем самым, Apple показывает, что читать ваш любимый журнал удобнее будет на iPad mini.</p>','<p><img alt=\"\" src=\"/sites/default/files/ipad_mini_ad_time.jpg\" style=\"width: 580px; height: 386px; \"></p>\r\n<p>&nbsp;</p>\r\n<p>Apple запустила оригинальную рекламу в печатных журналах TIME и New York Times. На развороте журнала на одной стороне обложка печатного издания, а на другой изабражен iPad mini в формате 1:1. На экране планшета отрыт этот же журнал в программе Киоск. Тем самым, Apple показывает, что читать ваш любимый журнал удобнее будет на iPad mini.</p>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" src=\"/sites/default/files/ipad_mini_ad_new_yorker.jpg\" style=\"width: 580px; height: 433px; \"></p>\r\n',NULL,3,1),(386,'iTunes 11 сегодня?','<p><img alt=\"\" src=\"/sites/default/files/up-next-itunes-11.png\"></p>\r\n<p>&nbsp;</p>\r\n<p>Apple обещала iTunes 11 в октябре, но потом объявила что выход откладывается на ноябрь. И вот сегодня уже 29 ноября, либо сегодня либо завтра должно выйти долгожданное обновление медиа-комбайна. По многочисленным слухам, выход должен состоятся именно сегодня. Ждем!</p>\r\n<p>&nbsp;</p>\r\n','<p><img alt=\"\" src=\"/sites/default/files/up-next-itunes-11.png\"></p>\r\n<p>&nbsp;</p>\r\n<p>Apple обещала iTunes 11 в октябре, но потом объявила что выход откладывается на ноябрь. И вот сегодня уже 29 ноября, либо сегодня либо завтра должно выйти долгожданное обновление медиа-комбайна. По многочисленным слухам, выход должен состоятся именно сегодня. Ждем!</p>\r\n<p>&nbsp;</p>\r\n',NULL,3,1),(387,'Apple одобрила кампус в Санта-Клара. ','<p><img alt=\"\" src=\"/sites/default/files/AppleSantaClaracampus.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Apple заключила сделку с&nbsp;Peery Arrillaga по строительству двух зданий в Санта-Кларе. Детали сделки не разглашается, но несолько источников пишут, что Apple арендовала 296000 кв. м.&nbsp;</p>\r\n<p>Новый кампус будет расположен всего в нескольких минутах хотьбы от центрального офиса Apple в Купертино. Площадь первого здания&nbsp;188 тыс. кв. футов, а второго&nbsp;108 тыс. кв. футов. Предположительно стр','<p><img alt=\"\" src=\"/sites/default/files/AppleSantaClaracampus.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Apple заключила сделку с&nbsp;Peery Arrillaga по строительству двух зданий в Санта-Кларе. Детали сделки не разглашается, но несолько источников пишут, что Apple арендовала 296000 кв. м.&nbsp;</p>\r\n<p>Новый кампус будет расположен всего в нескольких минутах хотьбы от центрального офиса Apple в Купертино. Площадь первого здания&nbsp;188 тыс. кв. футов, а второго&nbsp;108 тыс. кв. футов. Предположительно строительство будет закончено к 2014 году.&nbsp;</p>\r\n<p>Все существующие здания на этой территории будут снесены.</p>\r\n',NULL,3,1),(388,'Вышел iTunes 11.','<p><img alt=\"\" src=\"/sites/default/files/download_hero_title.png\"></p>\r\n<p>&nbsp;</p>\r\n<p>Вчера вечером Apple выпустила долгожданное обновление своего медиа-комбайна. iTunes 11 был анонсирован еще 12 сентября, должен был выйти в конце октября, но выход был отложен на ноябрь. Вы можете обновится в Mac App Store или скачать с <a href=\"http://www.apple.com/ru/itunes/download/\">сайта</a>.&nbsp;</p>','<p><img alt=\"\" src=\"/sites/default/files/download_hero_title.png\"></p>\r\n<p>&nbsp;</p>\r\n<p>Вчера вечером Apple выпустила долгожданное обновление своего медиа-комбайна. iTunes 11 был анонсирован еще 12 сентября, должен был выйти в конце октября, но выход был отложен на ноябрь. Вы можете обновится в Mac App Store или скачать с <a href=\"http://www.apple.com/ru/itunes/download/\">сайта</a>.&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" src=\"/sites/default/files/download_hero.jpg\"></p>\r\n',NULL,3,1),(389,'Apple запустит iTunes Store в России на следующей неделе.','<p><img alt=\"\" src=\"/sites/default/files/747004812.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Открытие&nbsp;онлайн-магазина музыки Apple iTunes Store в России намечено на 4-6 декабря. Такую информацию сообщили два источника, знающие планы яблочной компании.&nbsp;</p>\r\n<p>Запуск планировался на октябрь, но дважды переносился. Apple не отвечает на данный вопрос, но представитель компании сообщил, что 4 декабря пройдет пресс-брифинг Apple в Москве. Такого плана мероприятия яблочная компания еще не проводила в Ро','<p><img alt=\"\" src=\"/sites/default/files/747004812.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Открытие&nbsp;онлайн-магазина музыки Apple iTunes Store в России намечено на 4-6 декабря. Такую информацию сообщили два источника, знающие планы яблочной компании.&nbsp;</p>\r\n<p>Запуск планировался на октябрь, но дважды переносился. Apple не отвечает на данный вопрос, но представитель компании сообщил, что 4 декабря пройдет пресс-брифинг Apple в Москве. Такого плана мероприятия яблочная компания еще не проводила в России.</p>\r\n<p>Магазин Apple iTunes Store &nbsp;работает уже в десятках странах, даже в таких маленьких странах, как Камбоджа, Лаос, Шри-Ланка, но в России до сих пор не работает.</p>\r\n<p>&nbsp;</p>\r\n',NULL,3,1),(390,'Новые iMac 21,5\" поступили в продажу.','<p>Сегодня старт продаж обновленных 21,5 дюймовых&nbsp;iMac. Кроме самого компьютера изменилась и коробка, теперь она имеет не квадратную форму, а трапецеидальную.&nbsp;Уже появилось видео распаковки этого невероятного iMac.&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<iframe width=\"560\" height=\"315\" src=\"http://www.youtube.com/embed/_r3-pRIt51Y\" frameborder=\"0\" allowfullscreen></iframe>','<p>Сегодня старт продаж обновленных 21,5 дюймовых&nbsp;iMac. Кроме самого компьютера изменилась и коробка, теперь она имеет не квадратную форму, а трапецеидальную.&nbsp;Уже появилось видео распаковки этого невероятного iMac.&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><iframe allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"http://www.youtube.com/embed/_r3-pRIt51Y\" width=\"560\"></iframe></p>\r\n',NULL,3,1),(391,'ВИДЕО: Grand Theft Auto: Vice City - Anniversary Trailer.','<p>&nbsp;</p>\r\n<div>6 декабря в App Store.</div>\r\n<div>&nbsp;</div>\r\n<iframe width=\"560\" height=\"315\" src=\"http://www.youtube.com/embed/f_VBXRZuHTc\" frameborder=\"0\" allowfullscreen></iframe>\r\n','<p>&nbsp;</p>\r\n<div>6 декабря в App Store.</div>\r\n<div>&nbsp;</div>\r\n<iframe width=\"560\" height=\"315\" src=\"http://www.youtube.com/embed/f_VBXRZuHTc\" frameborder=\"0\" allowfullscreen></iframe>\r\n',NULL,3,1),(392,'Apple начала продавать новые гифт-карты.','<p><img alt=\"\" src=\"/sites/default/files/itunes-cards.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>К праздничному сезону Apple представила свои новые подарочные гифт-карты. Теперь вы можете выбрать номинал карты от $15 до $500. То есть первоначально на карте нет денег, вы их можете положеть на кассе. Таким образом Apple защищает себя и от воровства карт.&nbsp;</p>\r\n<p>До этого можно было купить карты с фиксированной суммой в&nbsp;$15, $25, $50 или $100.&nbsp;</p>\r\n','<p><img alt=\"\" src=\"/sites/default/files/itunes-cards.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>К праздничному сезону Apple представила свои новые подарочные гифт-карты. Теперь вы можете выбрать номинал карты от $15 до $500. То есть первоначально на карте нет денег, вы их можете положеть на кассе. Таким образом Apple защищает себя и от воровства карт.&nbsp;</p>\r\n<p>До этого можно было купить карты с фиксированной суммой в&nbsp;$15, $25, $50 или $100.&nbsp;</p>\r\n',NULL,3,1),(393,'Люди начали называть своих детей Сири, Эппл и Мак чаще.','<p><img alt=\"\" src=\"/sites/default/files/xlKPwEoib8k.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Англоязычное издание BabyCenter назвала самые популярные имена в этом году. Согласно исследованию, все чаще родители стали называть своих детох яблочными именами.</p>\r\n<p>Итак, среди&nbsp;450 000 детей, родившихся в 2012 году, 6 девочек получили имя Эппл (в 2011 - 3), 17 девочек с именем Сири (в 2011 - 11), 49 мальчиков назвали Мак (в 2011 - 25).</p>\r\n<p>«Смартфоны могут стать самыми лучшими электронными няньками у','<p><img alt=\"\" src=\"/sites/default/files/xlKPwEoib8k.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Англоязычное издание BabyCenter назвала самые популярные имена в этом году. Согласно исследованию, все чаще родители стали называть своих детох яблочными именами.</p>\r\n<p>Итак, среди&nbsp;450 000 детей, родившихся в 2012 году, 6 девочек получили имя Эппл (в 2011 - 3), 17 девочек с именем Сири (в 2011 - 11), 49 мальчиков назвали Мак (в 2011 - 25).</p>\r\n<p>«Смартфоны могут стать самыми лучшими электронными няньками уже с пеленок, поэтому некоторые мамы и папы отдают дань уважения Apple – признанному лидеру в этой отрасли, и называют своих детей в честь популярных гаджетов», – пишет BabyCenter.</p>\r\n',NULL,3,1),(394,'iMac 21.5\" разобрали.','<p><img alt=\"\" src=\"/sites/default/files/MwjRqELAmH4KPMUQ.jpg\" style=\"width: 592px; height: 444px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Как только ребятам из iFixit достался новый невероятно тонкий iMac 21.5\" они сделали с ним то, что обычно делают со всей техникой - разобрали и посмотрели, что внутри у этого революционного моноблока. Ремонтоспособность оценили всего на 3 балла из 10, в основном из-за того, что стекло и LCD приклеены к раме iMac очень хорошим клеем, а оператику и вовсе самому не получится п','<p><img alt=\"\" src=\"/sites/default/files/MwjRqELAmH4KPMUQ.jpg\" style=\"width: 592px; height: 444px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Как только ребятам из iFixit достался новый невероятно тонкий iMac 21.5\" они сделали с ним то, что обычно делают со всей техникой - разобрали и посмотрели, что внутри у этого революционного моноблока. Ремонтоспособность оценили всего на 3 балла из 10, в основном из-за того, что стекло и LCD приклеены к раме iMac очень хорошим клеем, а оператику и вовсе самому не получится поменять. &nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" src=\"/sites/default/files/QQDYBIFSGB3oNwlg.jpg\" style=\"width: 592px; height: 444px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Итак, что находится внутри нового iMac:&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<ul style=\"margin-left: 40px;\">\r\n	<li>Много резиновых прокладок для гашения вибрации винта</li>\r\n	<li>Вентилятор расположен по-другому, забудьте о пыли внутри</li>\r\n	<li>Два микрофона</li>\r\n	<li>LG сделали дисплей на 5мм тоньше</li>\r\n	<li>Процессор Intel Core i5-3330S, работающий на частоте 2,7 ГГц</li>\r\n	<li>Nvidia GeForce GT 640M GPU</li>\r\n	<li>Intel E213B384 Platform Controller Hub</li>\r\n	<li>2x Hynix GDDR5 H5GQ2H24AFR SGRAM</li>\r\n	<li>Broadcom BCM57765A1KMLG</li>\r\n	<li>Intel Thunderbolt DSL3510L контроллер</li>\r\n	<li>Analog Devices SSM3302 звуковой усилитель</li>\r\n	<li>Vimicro VC0359 процессор веб-камеры</li>\r\n	<li>Cirrus Logic 4206BCNZ аудио контроллер</li>\r\n	<li>Вскрывается долгим нагревом тепловыми пушками и медиаторами</li>\r\n	<li>Монитор имеет туже самую модель как в прошлых iMac (LM215WF3), но стал тоньше</li>\r\n	<li>Apple ушла от 3,5″ винтов к 2,5″ от HGST тем самым освободим кучу места</li>\r\n	<li>Компоненты стоят довольно плотно, поэтому все и везде прорезинено, что бы вибрация винта не отдавалась на другие детали</li>\r\n	<li>Кабель камеры FaceTime HD перестал быть тонким и хлипким, теперь он плоский и толстый</li>\r\n</ul>\r\n<p><img alt=\"\" src=\"/sites/default/files/BGggUhP3gLIuilLW.jpg\" style=\"width: 592px; height: 444px;\"></p>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" src=\"/sites/default/files/sWbNMqcmnASsND3V.jpg\" style=\"width: 592px; height: 444px;\"></p>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" src=\"/sites/default/files/l2Q33IXorUcc6vMu.jpg\" style=\"width: 592px; height: 444px;\"></p>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" src=\"/sites/default/files/mDaO5UYWXpeRL5Vn.jpg\" style=\"width: 592px; height: 444px;\"></p>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" src=\"/sites/default/files/aG5DK2jhGVWBABQc.jpg\" style=\"width: 592px; height: 444px;\"></p>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" src=\"/sites/default/files/UkQvOIYy3ZqBih6c.jpg\" style=\"width: 592px; height: 444px;\"></p>\r\n',NULL,3,1),(395,'Поставки iPhone 5 налаживаются.','<p><img alt=\"\" src=\"/sites/default/files/Screen-Shot-2012-12-03-at-08.18.03.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Уже декабрь наступил, скоро новогодние и рождественские праздники, на продукцию компании Apple предвидется гигантский спрос. Поэтому китайские заводы Foxconn переведены на круглосуточный режим.&nbsp;Apple еще не убрала ограничение в два телефона на заказ для каждого клиента, но вот сроки доставки в онлайн магазине Apple изменились с недели до 2-4 дней. Эти сроки пока что действуют только в Ав','<p><img alt=\"\" src=\"/sites/default/files/Screen-Shot-2012-12-03-at-08.18.03.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Уже декабрь наступил, скоро новогодние и рождественские праздники, на продукцию компании Apple предвидется гигантский спрос. Поэтому китайские заводы Foxconn переведены на круглосуточный режим.&nbsp;Apple еще не убрала ограничение в два телефона на заказ для каждого клиента, но вот сроки доставки в онлайн магазине Apple изменились с недели до 2-4 дней. Эти сроки пока что действуют только в Австралии.</p>\r\n',NULL,3,1),(396,'iTunes Store в России.','<p><img alt=\"\" src=\"/sites/default/files/IMG_0322.png\"></p>\r\n<p>&nbsp;</p>\r\n<p>Мы дождались этого! Apple официально запустила онлайн-магазин iTunes Store в нашей родной России. Теперь вы можете приобрести легально музыку, фильмы, рингтоны в яблочном магазине. Число исполнителей, треков, фильмов несомненно &nbsp;будет расти с каждым днем. К сожалению, сериалы пока не доступны, но надеямся, что скоро и их можно будет приобретать.&nbsp;</p>','<p><img alt=\"\" src=\"/sites/default/files/IMG_0322.png\"></p>\r\n<p>&nbsp;</p>\r\n<p>Мы дождались этого! Apple официально запустила онлайн-магазин iTunes Store в нашей родной России. Теперь вы можете приобрести легально музыку, фильмы, рингтоны в яблочном магазине. Число исполнителей, треков, фильмов несомненно &nbsp;будет расти с каждым днем. К сожалению, сериалы пока не доступны, но надеямся, что скоро и их можно будет приобретать.&nbsp;</p>\r\n<p>Самый интересный вопрос — какова цена? Цена для российского рынка значительно ниже, чем в Америке и Европе, у нас она одна из самых низких во всем мире. Вы можете приобрести лицензионную музыку по цене 15-19 рублей за трек, либо 99-149 рублей за альбом. Фильмы можно покупать или брать в арендку, что значительно дешевле —&nbsp;69 рублей против 199-350&nbsp; за покупку.</p>\r\n<p>Также стал доступен сервис&nbsp;iTunes Match за 799 рублей в год.</p>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" src=\"/sites/default/files/IMG_0324.png\"></p>\r\n<p>&nbsp;</p>\r\n<div>&nbsp;</div>\r\n<div>Вместе с Россией iTunes Store заработал в:</div>\r\n<div>Европа: Беларусь, Молдова, Россия, Турция, Украина.</div>\r\n<div>Африке и Ближний Восток, Индия: Азербайджан, Армения, Бахрейн, Ботсвана, Буркина-Фасо, Кабо-Верде, Египет, Гамбия, Гана, Гвинея-Бисау, Индия, Израиль, Иордания, Кения, Ливан, Маврикий, Мозамбик, Намибия, Нигер, Нигерия, Оман, Катар, Саудовская Аравия, Южная Африка, Свазиленд, Объединенные Арабские Эмиратов, Уганды, Зимбабве.</div>\r\n<div>Азиатско-Тихоокеанский регион: Фиджи, Индонезия, Казахстан, Кыргызстан, Микронезия, Монголия, Непал, Папуа-Новая Гвинея, Таджикистан, Туркменистан, Узбекистан.</div>\r\n<div>Страны Латинской Америки: Ангилья, Антигуа и Барбуда, Багамские Острова, Барбадос, Белиз, Бермудские острова, Виргинские острова, Каймановы острова, Доминика, Гренада, Сент-Китс и Невис, Тринидад и Тобаго.</div>\r\n<div>Кроме того только 4 из всех новых стран получили доступ к фильмам: Индии, Индонезии, России и Турции.</div>\r\n',NULL,3,1),(397,'Вышла iOS 6.1 Beta 3. ','<p><img alt=\"\" src=\"/sites/default/files/iosbeta361.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Прошедшей ночью Apple выложила новую версию бету прошивку iOS 6.1. С момента выхода предыдущей беты прошло 3 недели. Когда будет финал iOS 6.1 неизвестно, возможно уже до конца 2012.</p>\r\n','<p><img alt=\"\" src=\"/sites/default/files/iosbeta361.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Прошедшей ночью Apple выложила новую версию бету прошивку iOS 6.1. С момента выхода предыдущей беты прошло 3 недели. Когда будет финал iOS 6.1 неизвестно, возможно уже до конца 2012.</p>\r\n',NULL,3,1),(398,'Сравнение планшетов: время работы от батареи.','<p><img alt=\"\" src=\"/sites/default/files/99576.jpg\" style=\"width: 550px; height: 340px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<div>Интернет-портал Which.co.uk провел тестирование и сравнил все самые популярные планшеты по показателям заряда батареи.</div>\r\n<div>При одинаковых условиях с включенным WiFi и проигрыванием видео в категории 10″ планшетов победил iPad. Среди 7″ планшетов лидером также стала Apple со своим новеньким iPad mini.</div>\r\n<div>&nbsp;</div>','<p><img alt=\"\" src=\"/sites/default/files/99576.jpg\" style=\"width: 550px; height: 340px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<div>Интернет-портал Which.co.uk провел тестирование и сравнил все самые популярные планшеты по показателям заряда батареи.</div>\r\n<div>При одинаковых условиях с включенным WiFi и проигрыванием видео в категории 10″ планшетов победил iPad. Среди 7″ планшетов лидером также стала Apple со своим новеньким iPad mini.</div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"/sites/default/files/99575.jpg\" style=\"width: 550px; height: 340px;\"></div>\r\n',NULL,3,1),(399,'Новый iMac 27\" в январе.','<p><img alt=\"\" src=\"/sites/default/files/imac_27_january_shipping.jpg\" style=\"width: 436px; height: 465px;\"></p>\r\n<p>&nbsp;</p>\r\n<p style=\"margin: 1em 0px; padding: 0px; border: 0px; vertical-align: baseline; color: rgb(0, 0, 0); font-family: \'Lucida Grande\', \'Lucida Sans Unicode\', sans-serif; font-size: 13px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20px; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space','<p><img alt=\"\" src=\"/sites/default/files/imac_27_january_shipping.jpg\" style=\"width: 436px; height: 465px;\"></p>\r\n<p>&nbsp;</p>\r\n<p style=\"margin: 1em 0px; padding: 0px; border: 0px; vertical-align: baseline; color: rgb(0, 0, 0); font-family: \'Lucida Grande\', \'Lucida Sans Unicode\', sans-serif; font-size: 13px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20px; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px;\">Похоже представив за осень две новых линейки iPad, новый iPhone, MacBook Pro 13″ Retina, iMac и другие продукты немного не рассчитатали и выясняется, что новые&nbsp;iMac 27″ поступят в продажу только в январе 2013 года. Напомним, что старт продаж 27-дюймовых моноблоков должен был состояться в декабре.&nbsp;</p>\r\n<p style=\"margin: 1em 0px; padding: 0px; border: 0px; vertical-align: baseline; color: rgb(0, 0, 0); font-family: \'Lucida Grande\', \'Lucida Sans Unicode\', sans-serif; font-size: 13px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 20px; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px;\">Модель 21.5\" уже поступила в продажу.</p>\r\n',NULL,3,1),(400,'Старт продаж iPhone 5 в России 14 декабря.','<p><img alt=\"\" src=\"/sites/default/files/42142343421.jpg\" style=\"width: 532px; height: 289px;\"><br>\r\n	&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>Apple опубликовала пресс-релиз, в котором сообщается, что в декабре начнется старт продаж iPhone 5 в еще 50 странах, в котором есть Россия!<br>\r\n	<br>\r\n	Итак, россияне официально сомгут купить iPhone 5 c 14 декабря. Билайн уже даже открыл предзаказ. По слухам цена на младшую модель 16gb будет начинаться с 36000 рублей.<br>','<p><img alt=\"\" src=\"/sites/default/files/42142343421.jpg\" style=\"width: 532px; height: 289px;\"><br>\r\n	&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>Apple опубликовала пресс-релиз, в котором сообщается, что в декабре начнется старт продаж iPhone 5 в еще 50 странах, в котором есть Россия!<br>\r\n	<br>\r\n	Итак, россияне официально сомгут купить iPhone 5 c 14 декабря. Билайн уже даже открыл предзаказ. По слухам цена на младшую модель 16gb будет начинаться с 36000 рублей.<br>\r\n	<br>\r\n	Полный список стран:<br>\r\n	<br>\r\n	7 декабря:<br>\r\n	Южная Корея<br>\r\n	<br>\r\n	14 декабря:<br>\r\n	Албания<br>\r\n	Антигуа<br>\r\n	Барбуда<br>\r\n	Армения<br>\r\n	Багамские острова<br>\r\n	Бахрейн<br>\r\n	Боливия<br>\r\n	Бразилия<br>\r\n	Чили<br>\r\n	Китай<br>\r\n	Коста-Рика<br>\r\n	Кипр<br>\r\n	Эквадор<br>\r\n	Гренада<br>\r\n	Индонезия<br>\r\n	Израиль<br>\r\n	Иордания<br>\r\n	Кувейт<br>\r\n	Македония<br>\r\n	Малайзия<br>\r\n	Молдова<br>\r\n	Черногория<br>\r\n	Панама<br>\r\n	Парагвай<br>\r\n	Филиппины<br>\r\n	Катар<br>\r\n	Россия<br>\r\n	Саудовская Аравия<br>\r\n	Южная Африка<br>\r\n	Тайвань<br>\r\n	Турция<br>\r\n	Объединенные Арабские Эмираты<br>\r\n	Венесуэла<br>\r\n	<br>\r\n	21 декабря:<br>\r\n	Барбадос<br>\r\n	Ботсвана<br>\r\n	Камерун<br>\r\n	Центральная Африканская Республика<br>\r\n	Египет<br>\r\n	Гвинея<br>\r\n	Кот-д’Ивуар<br>\r\n	Кения<br>\r\n	Мадагаскар<br>\r\n	Мали<br>\r\n	Маврикий<br>\r\n	Марокко<br>\r\n	Нигер<br>\r\n	Сенегал<br>\r\n	Сент-Китс<br>\r\n	Сент-Люсия<br>\r\n	Сент-Винсент<br>\r\n	Гренадины<br>\r\n	Тунис<br>\r\n	Уганда<br>\r\n	Вьетнам</p>\r\n',NULL,3,1),(401,'Подарочные карты iTunes в России.','<p><img alt=\"\" src=\"/sites/default/files/russianitunesgiftcard11.jpg\" style=\"width: 600px; height: 207px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Вчера заработал iTunes Store в России, теперь можно купить легальную музыку и фильмы прямо с вашего iДевайса. Кроме того, стало известно что появилась возможность пополнения яблочного кошелька с помощью ваучеров и подарочных карт. Скоро в магазинах появятся подарочные карты с номиналом в 500, 1000 и 1500 рублей. Вы также можете купить карты через QIWI-кошелек или тер','<p><img alt=\"\" src=\"/sites/default/files/russianitunesgiftcard11.jpg\" style=\"width: 600px; height: 207px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Вчера заработал iTunes Store в России, теперь можно купить легальную музыку и фильмы прямо с вашего iДевайса. Кроме того, стало известно что появилась возможность пополнения яблочного кошелька с помощью ваучеров и подарочных карт. Скоро в магазинах появятся подарочные карты с номиналом в 500, 1000 и 1500 рублей. Вы также можете купить карты через QIWI-кошелек или терминал.<br>\r\n	&nbsp;</p>\r\n',NULL,3,1),(402,'Очередные патенты Apple. ','Очередные патенты Apple. \r\n\r\nБюро по патентам и товарным знакам США сегодня официально опубликовала 6 новых патентов Apple. Это патент на стильный дизайн iPhone 4, дизайн разъема MagSafe, механизм магнитной откидной крышки для iPad, заднюю металлическую сторону корпуса iPad, дизайн и интерфейс интеграции Twitter в iOS, который появился в iOS 5, а также дизайн оригинального бампера для iPhone 4.','<p><img alt=\"\" src=\"/sites/default/files/6a0120a5580826970c017c3443914d970b-800wi.jpg\" style=\"width: 800px; height: 533px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Бюро по патентам и товарным знакам США сегодня официально опубликовала 6 новых патентов Apple. Это патент на стильный дизайн iPhone 4, дизайн разъема MagSafe, механизм магнитной откидной крышки для iPad, заднюю металлическую сторону корпуса iPad, дизайн и интерфейс интеграции Twitter в iOS, который появился в iOS 5, а также дизайн оригинального бампера для iPhone 4.</p>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" src=\"/sites/default/files/6a0120a5580826970c017c3443a720970b-800wi.jpg\" style=\"width: 566px; height: 1200px;\"></p>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" src=\"/sites/default/files/6a0120a5580826970c017c34440b19970b-800wi.jpg\" style=\"height: 1037px; width: 700px;\"></p>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" src=\"/sites/default/files/6a0120a5580826970c017c34439330970b-800wi.jpg\" style=\"height: 924px; width: 700px;\"></p>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" src=\"/sites/default/files/6a0120a5580826970c017ee5e78e63970d-800wi.jpg\" style=\"width: 641px; height: 410px;\"></p>\r\n',NULL,3,1),(403,'Google обновила приложение YouTube для iOS.','<p><img alt=\"\" src=\"/sites/default/files/mzl.elgtuubh.480x480-75.jpg\" style=\"width: 480px; height: 360px;\"><br>\r\n	&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>В новой версии добавлена долгожданная поддержка iPad и трансляция медиа-контента через AirPlay.<br>\r\n	Качаем, обновляемся!</p>\r\n','<p><img alt=\"\" src=\"/sites/default/files/mzl.elgtuubh.480x480-75.jpg\" style=\"width: 480px; height: 360px;\"><br>\r\n	&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>В новой версии добавлена долгожданная поддержка iPad и трансляция медиа-контента через AirPlay.<br>\r\n	Качаем, обновляемся!</p>\r\n',NULL,3,1),(404,'Apple обновила iWork на iOS и OSX.','<p><img alt=\"\" src=\"/sites/default/files/overview_hero.png\" style=\"width: 1032px; height: 289px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Apple выпустила обновление для своего пакета iWork. Все программы для iOS, включая Pages, Numbers и Keynote, были обновления до версии 1.7, а аналогичные программы для OSX обновились для их поддержки.<br>','<p><img alt=\"\" src=\"/sites/default/files/overview_hero.png\" style=\"height: 196px; width: 700px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Apple выпустила обновление для своего пакета iWork. Все программы для iOS, включая Pages, Numbers и Keynote, были обновления до версии 1.7, а аналогичные программы для OSX обновились для их поддержки.<br>\r\n	<br>\r\n	<u>Что нового в Pages 1.7</u><br>\r\n	Используйте функцию «Трекинг изменений» для отслеживания изменений в текстовом блоке документа.<br>\r\n	Принимайте и отклоняйте отдельные изменения при просмотре документа.<br>\r\n	Импортируйте документы Pages и Microsoft Word с трекингом изменений и продолжайте отслеживать изменения в текстовом блоке документа.<br>\r\n	Сохраняйте отслеженные изменения в документах, экспортированных в форматах Microsoft Word или Pages.<br>\r\n	Сохраняйте расчеты в таблицах при импорте из «Pages для Mac» и экспорте в эту программу.<br>\r\n	Добавляйте в фигуры отражения.<br>\r\n	Закрепляйте и открепляйте объекты.<br>\r\n	<br>\r\n	<u>Что нового в Numbers 1.7</u><br>\r\n	Скрывайте и снова показывайте столбцы и строки.<br>\r\n	Импортируйте и экспортируйте таблицы программы «Numbers для Mac» с фильтрами, а также включайте и выключайте фильтры.<br>\r\n	Сохраняйте форматированный текст в таблицах при импорте и экспорте.<br>\r\n	Добавляйте в фигуры отражения.<br>\r\n	Закрепляйте и открепляйте объекты.<br>\r\n	<br>\r\n	<u>Что нового в Keynote 1.7</u><br>\r\n	Импортируйте и экспортируйте все размеры слайдов, поддерживаемые Microsoft PowerPoint и «Keynote для Mac».<br>\r\n	Импортируйте и экспортируйте темы презентаций с использованием мастер-слайдов и наборов стилей.<br>\r\n	Воспроизводите все анимации действий Keynote, включая анимации «Переместить», «Поворот», «Изменить масштаб» и «Непрозрачность».<br>\r\n	Добавляйте новые переходы на слайдах, включая «Мерцание» и «Сверкание».<br>\r\n	Сохраняйте расчеты в таблицах при импорте из «Keynote для Mac» и экспорте в эту программу.<br>\r\n	Добавляйте в фигуры отражения.<br>\r\n	Новые макеты печати включают в себя параметры для печати с заметками докладчика, с анимациями и без фонов.<br>\r\n	Закрепляйте и открепляйте объекты.</p>\r\n',NULL,3,1),(405,'iPhone 5 признали лучшим гаджетом 2012 года.','<p><img alt=\"\" src=\"/sites/default/files/iPhone-5-111.jpg\" style=\"width: 576px; height: 501px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Журнал TIME опубликовал свой рейтинг \"Лучший гаджет 2012 года\". Первое место занял iPhone 5, издание отметило, что&nbsp;сейчас на рынке много достойных смартфонов, в том числе и Samsung Galaxy III, но когда речь заходит о слиянии программного и аппаратного обеспечения, а также сервисов, то телефону Apple нет равных.</p>','<p><img alt=\"\" src=\"/sites/default/files/iPhone-5-111.jpg\" style=\"width: 576px; height: 501px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Журнал TIME опубликовал свой рейтинг \"Лучший гаджет 2012 года\". Первое место занял iPhone 5, издание отметило, что сейчас на рынке много достойных смартфонов, в том числе и Samsung Galaxy III, но когда речь заходит о слиянии программного и аппаратного обеспечения, а также сервисов, то телефону Apple нет равных.</p>\r\n<p><br>\r\n	<u>Топ-10 гаджетов 2012 года по версии Time:</u><br>\r\n	1. iPhone 5<br>\r\n	2. Nintendo Wii U<br>\r\n	3. Sony Cyber-shot RX100<br>\r\n	4. Raspberry Pi Model B<br>\r\n	5. Lytro<br>\r\n	6. 15-дюймовый MacBook Pro с дисплеем Retina<br>\r\n	7. Microsoft Surface на базе Windows RT<br>\r\n	8. Samsung Galaxy Note II<br>\r\n	9. Nest<br>\r\n	10. Simple.TV</p>\r\n',NULL,3,1),(406,'Apple TV будет официально продаваться в России.','<p><img alt=\"\" src=\"/sites/default/files/07-Apple-TV-3Gen.jpg\" style=\"height: 484px; width: 700px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>По слухам Apple начнет поставлять Apple TV в России после Нового Года. Цена телеприставки пока неизвестна, в Америка она продается за $99.<br>\r\n	Apple TV позволяет просматривать видео, прослушивать музыку с яблочных девайсов через AirPlay и ряда интернет-сервисов.</p>\r\n','<p><img alt=\"\" src=\"/sites/default/files/07-Apple-TV-3Gen.jpg\" style=\"height: 484px; width: 700px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>По слухам Apple начнет поставлять Apple TV в России после Нового Года. Цена телеприставки пока неизвестна, в Америка она продается за $99.<br>\r\n	Apple TV позволяет просматривать видео, прослушивать музыку с яблочных девайсов через AirPlay и ряда интернет-сервисов.</p>\r\n',NULL,3,1),(407,'Туалетная вода Apple pour homme.','<p><img alt=\"\" src=\"/sites/default/files/Z-S6SIDnTbA.jpg\" style=\"width: 576px; height: 576px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>В Минске, в центральном магазине продаются яблочные духи Apple pour homme. Только вот яблоком они не пахнут, от яблока у них скопированный дизайн упаковки с коробки Apple.&nbsp;</p>\r\n<p>Дизайн флакона напоминает первую модель iPhone. Создатели многое взяли у американской компании, но скопировать логотип не посмели, они создали что-то между яблочком Apple и зайцем Playboy.&nbsp;<','<p><img alt=\"\" src=\"/sites/default/files/Z-S6SIDnTbA.jpg\" style=\"width: 576px; height: 576px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>В Минске, в центральном магазине продаются яблочные духи Apple pour homme. Только вот яблоком они не пахнут, от яблока у них скопированный дизайн упаковки с коробки Apple.&nbsp;</p>\r\n<p>Дизайн флакона напоминает первую модель iPhone. Создатели многое взяли у американской компании, но скопировать логотип не посмели, они создали что-то между яблочком Apple и зайцем Playboy.&nbsp;</p>\r\n<p>Цена при переводе в российские рубли составляет примерно 300 рублей.</p>\r\n',NULL,3,1),(408,'Первые iMac 27\" Apple отправит до Нового Года.','<p><img alt=\"\" src=\"/sites/default/files/imacshipment.jpg\" style=\"width: 588px; height: 150px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Только вчера мы <a href=\"http://macov.net/news/new-imac27-v-yanvare\">писали</a> о том, что iMac 27\" мы увидим только в январе, а теперь выясняется, что некоторые счастливчики смогут получить моноблок в конце декабря. Возможно Apple удалось разобраться с производством новых iMac и первым делом занимается подготовкой к отправке по предзаказам. Когда iMac 27\" появится на прилавках','<p><img alt=\"\" src=\"/sites/default/files/imacshipment.jpg\" style=\"width: 588px; height: 150px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Только вчера мы <a href=\"http://macov.net/news/new-imac27-v-yanvare\">писали</a> о том, что iMac 27\" мы увидим только в январе, а теперь выясняется, что некоторые счастливчики смогут получить моноблок в конце декабря. Возможно Apple удалось разобраться с производством новых iMac и первым делом занимается подготовкой к отправке по предзаказам. Когда iMac 27\" появится на прилавках магазинов неизвестно. Скорее всего это будет январь.</p>\r\n',NULL,3,1),(409,'Instagram кардинально обновился.','Instagram кардинально обновился.\r\n\r\nМинувшей ночью приложение Instagram обновилось до версии 3.2.0. \r\nInstagram — самое популярное фотоприложение для мобильных устройств. \r\n\r\nИзменился дизайн экрана камеры и выбора уже снимка из галереи — теперь делать это стало гораздо проще. Экран камеры теперь очень сильно напоминает видоискатель старенького Kodak.\r\nИз других изменений — новый черно-белый фильтр Willow, который хорошо использовать, снимая портреты, пейзажи и архитектуру. \r\nТакже совсем немног','<p><img alt=\"\" src=\"/sites/default/files/article_image-image-article.1ef5b64d-0210-4881-b866-86a11bed65ff.png\" style=\"height: 413px; width: 233px;\">&nbsp;<img alt=\"\" src=\"/sites/default/files/article_image-image-article.c5fbd3c2-56b8-4ca6-bc93-24e1000aa827.png\" style=\"height: 413px; width: 233px;\">&nbsp;<img alt=\"\" src=\"/sites/default/files/article_image-image-article.d2f96ce6-8a4b-4530-87bd-0f42f56e5d26.png\" style=\"height: 413px; width: 233px;\"></p>\r\n<p>Минувшей ночью приложение Instagram обновилось до версии 3.2.0.<br>\r\n	Instagram — самое популярное фотоприложение для мобильных устройств.<br>\r\n	<br>\r\n	Изменился дизайн экрана камеры и выбора уже снимка из галереи — теперь делать это стало гораздо проще. Экран камеры теперь очень сильно напоминает видоискатель старенького Kodak.<br>\r\n	Из других изменений — новый черно-белый фильтр Willow, который хорошо использовать, снимая портреты, пейзажи и архитектуру.<br>\r\n	Также совсем немного изменилась лента с обновлениями, увеличились превью фотографий, а профили пользователей теперь имеют бесконечную плавную прокрутку.<br>\r\n	Добавлена кнопка Foursquare, которая открывает приложение Foursquare с подробной информацией о месте съемки.<br>\r\n	Еще одна функция которая многим будет очень полезна — теперь все обработанные фотографии будут сохранены в Фотопленке в отдельный альбом под названием Instagram.<br>\r\n	<br>\r\n	Наш ник в инстаграме - <a href=\"http://instagram.com/macov_net\">macov_net</a>, добавляйтесь в друзья ;)</p>\r\n',NULL,3,1),(410,'iPad mini опережает по популярности Amazon Kindle Fire. ','<p><img alt=\"\" src=\"/sites/default/files/12.12.10-iPad_mini_Impressions.jpg\" style=\"width: 573px; height: 654px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Уже больше месяца продается новика от Apple iPad mini. Планшет продается очень хорошо и даже превзошел конкурента от Amazon Kindle Fire.&nbsp;В течение ноября, количество запросов на iPad mini вырастало на 28% ежедневно,&nbsp;в прошлом году подобные запросы на Amazon Kindle Fire вырастали всего на 19% в день.&nbsp;Millennial Media считает, что iPad mini \"измен','<p><img alt=\"\" src=\"/sites/default/files/12.12.10-iPad_mini_Impressions.jpg\" style=\"width: 573px; height: 654px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Уже больше месяца продается новика от Apple iPad mini. Планшет продается очень хорошо и даже превзошел конкурента от Amazon Kindle Fire.&nbsp;В течение ноября, количество запросов на iPad mini вырастало на 28% ежедневно,&nbsp;в прошлом году подобные запросы на Amazon Kindle Fire вырастали всего на 19% в день.&nbsp;Millennial Media считает, что iPad mini \"изменит правила игры\".&nbsp;</p>\r\n<p>iPad mini по опросам является самым желанным подарком на Рождество и Новый Год. Успей купить в подарок iPad mini, закажи на Маков.Нет!</p>\r\n',NULL,3,1),(411,'15 декабря откроются 4 новых Apple Store. ','<p><img alt=\"\" src=\"/sites/default/files/apple_store_causeway_bay_curtains.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Интернет-портал&nbsp;ifoAppleStore сообщает, что 15 декабря&nbsp;в мире откроются еще 4 яблочных магазина Apple Store. Один из них будет в Гонконге,&nbsp;который будет находится в торговом центре Hysan Place в районе Causeway Bay. Второй будет находится в Австралии в центре Fountain Gate, третий откроется в центре Emporia, Мальмё, Швеция. Четвертый же магазин, в Rockaway, Нью-Джерси, просто зн','<p><img alt=\"\" src=\"/sites/default/files/apple_store_causeway_bay_curtains.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Интернет-портал&nbsp;ifoAppleStore сообщает, что 15 декабря&nbsp;в мире откроются еще 4 яблочных магазина Apple Store. Один из них будет в Гонконге,&nbsp;который будет находится в торговом центре Hysan Place в районе Causeway Bay. Второй будет находится в Австралии в центре Fountain Gate, третий откроется в центре Emporia, Мальмё, Швеция. Четвертый же магазин, в Rockaway, Нью-Джерси, просто значительно расширится.</p>\r\n',NULL,3,1),(412,'Самая маленькая флешка для вашего Mac.','<p><img alt=\"\" src=\"/sites/default/files/1_70_8.jpg\" style=\"height: 350px; width: 350px;\">&nbsp;<img alt=\"\" src=\"/sites/default/files/3_48_8.jpg\" style=\"height: 350px; width: 350px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Компания geekstuff4u, решила тоже позаботиться о стиле и внешних размерах и сделала шикарную флешку, которая идеально подойдет к вашим тонким компьютерам Mac. Стоит такая флешка $59.90 за 16Гб и $92.56 за 32Гб.<br>\r\n	Флешка отлично будет сочетаться с вашим MacBook Air или MacBook Pro Retina. ','<p><img alt=\"\" src=\"/sites/default/files/1_70_8.jpg\" style=\"height: 350px; width: 350px;\">&nbsp;<img alt=\"\" src=\"/sites/default/files/3_48_8.jpg\" style=\"height: 350px; width: 350px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Компания geekstuff4u, решила тоже позаботиться о стиле и внешних размерах и сделала шикарную флешку, которая идеально подойдет к вашим тонким компьютерам Mac. Стоит такая флешка $59.90 за 16Гб и $92.56 за 32Гб.<br>\r\n	Флешка отлично будет сочетаться с вашим MacBook Air или MacBook Pro Retina. Она сделана из того же материала, что и макбук.</p>\r\n',NULL,3,1),(413,'Angry Birds в кино в 2016 году.','<p><img alt=\"\" src=\"/sites/default/files/angrybirdscharacters.jpg\" style=\"width: 539px; height: 281px;\"><br>\r\n	<br>\r\n	Вчера компания Rovio отпраздновала день рождения их игры Angry Birds. Нет такого человека, который бы не играл в злых птичек или хотя бы не слышал о них — она уже стала культовой и принесла создателям огромную прибыль. Rovio собирается снять целый фильм про то, как свинки разозлили пернатых. По словам компании, на экраны фильм должен выйти в 2016 году.</p>\r\n','<p><img alt=\"\" src=\"/sites/default/files/angrybirdscharacters.jpg\" style=\"width: 539px; height: 281px;\"><br>\r\n	<br>\r\n	Вчера компания Rovio отпраздновала день рождения их игры Angry Birds. Нет такого человека, который бы не играл в злых птичек или хотя бы не слышал о них — она уже стала культовой и принесла создателям огромную прибыль. Rovio собирается снять целый фильм про то, как свинки разозлили пернатых. По словам компании, на экраны фильм должен выйти в 2016 году.</p>\r\n',NULL,3,1),(414,'Бесплатный адаптер с iPhone 5 в Китае.','<p><img alt=\"\" src=\"/sites/default/files/8264775109_80175a37d3_o.jpg\" style=\"width: 550px; height: 366px;\"><br>\r\n	<br>\r\n	Стартовали продажи iPhone 5 в Китае. Комплектация смартфона в поднебесной отличается от остальных стран тем, что кроме стандартного набора iPhone, зарядки, UBS-кабеля и наушников, в коробке лежит адаптер с Lightning на Micro-USB. Apple пришлось пойти на это из-за закона этой страны об унифицированном зарядном устройстве.</p>\r\n','<p><img alt=\"\" src=\"/sites/default/files/8264775109_80175a37d3_o.jpg\" style=\"width: 550px; height: 366px;\"><br>\r\n	<br>\r\n	Стартовали продажи iPhone 5 в Китае. Комплектация смартфона в поднебесной отличается от остальных стран тем, что кроме стандартного набора iPhone, зарядки, UBS-кабеля и наушников, в коробке лежит адаптер с Lightning на Micro-USB. Apple пришлось пойти на это из-за закона этой страны об унифицированном зарядном устройстве.</p>\r\n',NULL,3,1),(415,'Вышел iTunes 11.0.1. ','<p><img alt=\"\" src=\"/sites/default/files/iTunes11IconX_270x270.png\" style=\"width: 270px; height: 270px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Две недели назад вышел iTunes 11. Сегодня вышло первое обновление для него. Изменений немного, но они есть:</p>\r\n<p style=\"margin-left: 40px;\">&nbsp;</p>\r\n<ul style=\"margin-top: 0px; margin-bottom: 5px; margin-left: 25px; line-height: 22px; list-style-position: inside; font-family: Verdana, Geneva, sans-serif; font-size: 12px;\">\r\n	<li>Исправлена ошибка, в результате ко','<p><img alt=\"\" src=\"/sites/default/files/iTunes11IconX_270x270.png\" style=\"width: 270px; height: 270px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Две недели назад вышел iTunes 11. Сегодня вышло первое обновление для него. Изменений немного, но они есть:</p>\r\n<p style=\"margin-left: 40px;\">&nbsp;</p>\r\n<ul style=\"margin-top: 0px; margin-bottom: 5px; margin-left: 25px; line-height: 22px; list-style-position: inside; font-family: Verdana, Geneva, sans-serif; font-size: 12px;\">\r\n	<li>Исправлена ошибка, в результате которой некоторые мультимедийные материалы, купленные Вами и находящиеся в iCloud, могли не отображаться в Вашей медиатеке при включенной функции iTunes Match;</li>\r\n	<li>Обновление повышает скорость реакции на поисковые запросы при работе в iTunes с крупными медиатеками;</li>\r\n	<li>Исправлена проблема, при которой кнопка AirPlay могла отображаться некорректно;</li>\r\n	<li>Добавлена возможность поиска дубликатов в Вашей медиатеке.</li>\r\n</ul>\r\n',NULL,3,1),(416,'Apple назвала лучших из лучших 2012','<p><img alt=\"\" src=\"/sites/default/files/itunes-app-store-best-of-2012.jpg\" style=\"width: 650px; height: 366px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>2012 год подходит к концу и пора уже подводить его итоги. Apple выбрала лучшие продукты в своих магазинах App Store, Mac Store и iTunes Store. Были выбраны лучшие среди игр, фильмов, музыки и книг.&nbsp;</p>\r\n<p>&nbsp;</p>','<p><img alt=\"\" src=\"/sites/default/files/itunes-app-store-best-of-2012.jpg\" style=\"width: 650px; height: 366px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>2012 год подходит к концу и пора уже подводить его итоги. Apple выбрала лучшие продукты в своих магазинах App Store, Mac Store и iTunes Store. Были выбраны лучшие среди игр, фильмов, музыки и книг.&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<div><u><strong>Приложения для Mac:&nbsp;</strong></u></div>\r\n<div>Приложение года – Day One</div>\r\n<div>Игра года – Deus Ex: Human Revolution – Ultimate Edition</div>\r\n<div>&nbsp;</div>\r\n<div><strong><u>Приложения для iOS:&nbsp;</u></strong></div>\r\n<div>Приложение года для iPhone – Action Movie FX</div>\r\n<div>Игра года для iPhone – Rayman Jungle Run</div>\r\n<div>Приложение года для iPad – Paper by FiftyThree</div>\r\n<div>Игра года для iPad – The Room</div>\r\n<div>&nbsp;</div>\r\n<div><u><strong>Музыка:&nbsp;</strong></u></div>\r\n<div>Лучший исполнитель – Frank Ocean</div>\r\n<div>Лучший альбом – Grizzly Bear – Shields</div>\r\n<div>Лучшая песня – Fun. – We are young</div>\r\n<div>&nbsp;</div>\r\n<div><u><strong>Фильмы:&nbsp;</strong></u></div>\r\n<div>Лучший блокбастер – Мстители</div>\r\n<div>Лучший режиссер – Королевство полной луны</div>\r\n<div>Лучший сценарий – Звери дикого Юга</div>\r\n<div>Лучший кино-дебьют – Голодные игры</div>\r\n<div>&nbsp;</div>\r\n<div><u><strong>Сериалы:&nbsp;</strong></u></div>\r\n<div>Лучший сериал – Во все тяжкие</div>\r\n<div>Лучши новый сериал – Девчонки</div>\r\n<div>&nbsp;</div>\r\n<div>Если вы еще чего-то из перечисленного выше не видели, не слышали или не играли, бегом качать!</div>\r\n<p>&nbsp;</p>\r\n',NULL,3,1),(417,'В App Store для iOS вернулась возможность дарить приложения!','<p>&nbsp;</p>\r\n<div><img alt=\"\" src=\"/sites/default/files/H2n6uoV5Lbk.jpg\" style=\"height: 525px; width: 350px;\"></div>\r\n<div>&nbsp;</div>\r\n<div>В iOS 6 пропала возможность дарить приложения &nbsp;с iPhone или iPad. Совсем недавно Apple передумала и вернула эту возможность. Теперь, перейдя к любому платному приложения, нажав на кномку \"Отправить\" в верхнем правом углу, вы увидите среди прочих кнопок \"Подарить\".&nbsp;</div>\r\n<div>&nbsp;</div>\r\n<div>Дарите друзьям приложения, это будет маленьким, н','<p>&nbsp;</p>\r\n<div><img alt=\"\" src=\"/sites/default/files/H2n6uoV5Lbk.jpg\" style=\"height: 525px; width: 350px;\"></div>\r\n<div>&nbsp;</div>\r\n<div>В iOS 6 пропала возможность дарить приложения &nbsp;с iPhone или iPad. Совсем недавно Apple передумала и вернула эту возможность. Теперь, перейдя к любому платному приложения, нажав на кномку \"Отправить\" в верхнем правом углу, вы увидите среди прочих кнопок \"Подарить\".&nbsp;</div>\r\n<div>&nbsp;</div>\r\n<div>Дарите друзьям приложения, это будет маленьким, но приятным подарком ;)</div>\r\n',NULL,3,1),(418,'Рейтинг лучших компаний для работы.','<p><img alt=\"\" src=\"/sites/default/files/BPTW12-Badge.png\" style=\"width: 196px; height: 211px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Каждый год интернет-портал Glassdoor составляет <a href=\"http://www.glassdoor.com/Best-Places-to-Work-LST_KQ0,19.htm\">список лучших компаний для работы</a>. Рейтинг компании считается на основании анонимных оценок реальных работников этой компании. В этом году Apple упала с 10-го места на 34-ое.</p>\r\n<p>Лучшей компанией для работы в 2012 году стала Facebook, в прошлом году у не','<p><img alt=\"\" src=\"/sites/default/files/BPTW12-Badge.png\" style=\"width: 196px; height: 211px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Каждый год интернет-портал Glassdoor составляет <a href=\"http://www.glassdoor.com/Best-Places-to-Work-LST_KQ0,19.htm\">список лучших компаний для работы</a>. Рейтинг компании считается на основании анонимных оценок реальных работников этой компании. В этом году Apple упала с 10-го места на 34-ое.</p>\r\n<p>Лучшей компанией для работы в 2012 году стала Facebook, в прошлом году у нее была 3-я строчка в рейтинге. Google с 5-го места спустился на 6-ое.</p>\r\n',NULL,3,1),(419,'Новый почтовой клиент Mailbox для iOS. ','<p><iframe allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"http://www.youtube.com/embed/CICMxwgm274\" width=\"560\"></iframe></p>\r\n<p>&nbsp;</p>\r\n<div>Появился анонс приложения Mailbox, создатели обещают взять все самое лучшее от очень удобных и красивых приложений Sparrow и Clear.&nbsp;</div>\r\n<div>Посмотрите видео, выглядит потрясающе!</div>\r\n<div>Выход намечен на ближайшее время - ждем!</div>\r\n','<p><iframe allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"http://www.youtube.com/embed/CICMxwgm274\" width=\"560\"></iframe></p>\r\n<p>&nbsp;</p>\r\n<div>Появился анонс приложения Mailbox, создатели обещают взять все самое лучшее от очень удобных и красивых приложений Sparrow и Clear.&nbsp;</div>\r\n<div>Посмотрите видео, выглядит потрясающе!</div>\r\n<div>Выход намечен на ближайшее время - ждем!</div>\r\n',NULL,3,1),(420,'Google выпустили YouTube Capture.','<p><img alt=\"\" src=\"/sites/default/files/mzl.yeoosmrw.320x480-75.jpg\" style=\"width: 233px; height: 414px;\">&nbsp;<img alt=\"\" src=\"/sites/default/files/mzl.ilbxkzle.320x480-75.jpg\" style=\"width: 233px; height: 414px;\">&nbsp;<img alt=\"\" src=\"/sites/default/files/mzl.htrgmbth.320x480-75.jpg\" style=\"width: 233px; height: 414px;\"></p>\r\n<div>&nbsp;</div>\r\n<div>YouTube Capture позволит снимать видео-ролики и делиться!&nbsp;</div>\r\n<div>Описание:</div>\r\n<div>– Удобная запись.</div>\r\n<div>– Коррекция цве','<p><img alt=\"\" src=\"/sites/default/files/mzl.yeoosmrw.320x480-75.jpg\" style=\"width: 233px; height: 414px;\">&nbsp;<img alt=\"\" src=\"/sites/default/files/mzl.ilbxkzle.320x480-75.jpg\" style=\"width: 233px; height: 414px;\">&nbsp;<img alt=\"\" src=\"/sites/default/files/mzl.htrgmbth.320x480-75.jpg\" style=\"width: 233px; height: 414px;\"></p>\r\n<div>&nbsp;</div>\r\n<div>YouTube Capture позволит снимать видео-ролики и делиться!&nbsp;</div>\r\n<div>Описание:</div>\r\n<div>– Удобная запись.</div>\r\n<div>– Коррекция цвета, стабилизация, обрезка и возможность добавления музыкальных треков.</div>\r\n<div>&nbsp;</div>\r\n<div><a href=\"https://itunes.apple.com/ru/app/youtube-capture/id576941441?\">Скачать</a> из App Store бесплатно.</div>\r\n',NULL,3,1),(421,'Инфографика AppStore 2012.','<p>Ребята из AppsFire подсчитали статистику магазина App Store за 2012 год и создали красивую инфографику.&nbsp;</p>\r\n<p><img alt=\"\" src=\"/sites/default/files/100465.png\"></p>','<p>Ребята из AppsFire подсчитали статистику магазина App Store за 2012 год и создали красивую инфографику.&nbsp;</p>\r\n<p><img alt=\"\" src=\"/sites/default/files/100465.png\"></p>\r\n',NULL,3,1),(422,'Умные прыгалки. ','<p><img alt=\"\" src=\"/sites/default/files/1355726096_revolve-bluetooth-jump-rope-by-quirky-5.jpg\" style=\"width: 600px; height: 343px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Компания Quirky разработала умные прыгалки, которые отслеживают ваши успехи. Они считают количество сделанных прыжков, время вашей тренировки, сердечный ритм и многое другое. Благодаря Bluetooth 4.0 вы сможете синхронизировать прыгалки с вашим смартфоном. Когда и по какой цене выйдут умные прыгалки пока неизвестно.&nbsp;</p>','<p><img alt=\"\" src=\"/sites/default/files/1355726096_revolve-bluetooth-jump-rope-by-quirky-5.jpg\" style=\"width: 600px; height: 343px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Компания Quirky разработала умные прыгалки, которые отслеживают ваши успехи. Они считают количество сделанных прыжков, время вашей тренировки, сердечный ритм и многое другое. Благодаря Bluetooth 4.0 вы сможете синхронизировать прыгалки с вашим смартфоном. Когда и по какой цене выйдут умные прыгалки пока неизвестно.&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" src=\"/sites/default/files/1355726110_revolve-bluetooth-jump-rope-by-quirky.jpg\" style=\"width: 600px; height: 343px;\"></p>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" src=\"/sites/default/files/1355726112_revolve-bluetooth-jump-rope-by-quirky-2.jpg\" style=\"width: 600px; height: 343px;\"></p>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" src=\"/sites/default/files/1355726122_revolve-bluetooth-jump-rope-by-quirky-3.jpg\" style=\"width: 600px; height: 343px;\"></p>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" src=\"/sites/default/files/1355726147_revolve-bluetooth-jump-rope-by-quirky-4.jpg\"></p>\r\n',NULL,3,1),(423,'Обновление прошивок. ','<p>&nbsp;</p>\r\n<div><img alt=\"\" src=\"/sites/default/files/Update_iOS%20%281%29.jpg\" style=\"width: 308px; height: 297px;\"></div>\r\n<div>&nbsp;</div>\r\n<div>Вчера вечером Apple выпустила iOS 6.1 beta 4. Данная версия идет под кодом 10B5126b, с момента выхода третьей беты прошло две недели.&nbsp;</div>\r\n<div>А сегодня вышла iOS 6.0.2. Но она доступна не для всех, а только для владельцев iPhone 5 и iPad mini. Это обновление содержит улучшения и исправляет ошибку, в том числе: Исправлена ​​ошибка, кото','<p>&nbsp;</p>\r\n<div><img alt=\"\" src=\"/sites/default/files/Update_iOS%20%281%29.jpg\" style=\"width: 308px; height: 297px;\"></div>\r\n<div>&nbsp;</div>\r\n<div>Вчера вечером Apple выпустила iOS 6.1 beta 4. Данная версия идет под кодом 10B5126b, с момента выхода третьей беты прошло две недели.&nbsp;</div>\r\n<div>А сегодня вышла iOS 6.0.2. Но она доступна не для всех, а только для владельцев iPhone 5 и iPad mini. Это обновление содержит улучшения и исправляет ошибку, в том числе: Исправлена ​​ошибка, которая может повлиять на Wi-Fi.&nbsp;</div>\r\n<div>&nbsp;</div>\r\n<div>Кроме этого вышла третья бета для Apple TV, а также вышел Xcode 4.6 Developer Preview 4</div>\r\n<div>&nbsp;</div>\r\n',NULL,3,1),(424,'Google запустила аналог iTunes Match. ','<p><img alt=\"\" src=\"/sites/default/files/google-play-apk-%D0%B3%D1%83%D0%B3%D0%BB-%D0%BF%D0%BB%D0%B5%D0%B9.jpg\" style=\"width: 700px; height: 375px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Сегодня американская компания добавила музыкальный сервис в свой магазин Google Play. Действует он также, как и iTunes Match, сканирует вашу музыку и заливает в облако. Потом из облака вы можете послушать свою музыку &nbsp;или скачать на свой девайс.&nbsp;</p>\r\n','<p><img alt=\"\" src=\"/sites/default/files/google-play-apk-%D0%B3%D1%83%D0%B3%D0%BB-%D0%BF%D0%BB%D0%B5%D0%B9.jpg\" style=\"width: 700px; height: 375px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Сегодня американская компания добавила музыкальный сервис в свой магазин Google Play. Действует он также, как и iTunes Match, сканирует вашу музыку и заливает в облако. Потом из облака вы можете послушать свою музыку &nbsp;или скачать на свой девайс.&nbsp;</p>\r\n',NULL,3,1),(425,'Гибкая тач-клавиатура Qii. ','<p><img alt=\"\" src=\"/sites/default/files/20121128052237-Black_Qii_unrolled_1.jpg\" style=\"width: 600px; height: 450px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>На сайте indiegogo.com разместили <a href=\"http://www.indiegogo.com/Qii\">проект гибкой клавиатуры Qii</a>. Из компактного контейнера &nbsp;\"вытягивается\" лента и получается полноразмерная тач-клавиатура. Она ударостойкая и водостойкая.&nbsp;</p>\r\n<p>Сейчас создатели клавиатуры-рулона собирают деньги на реалицию своего проекта, им нужно $1850000, а на данн','<p><img alt=\"\" src=\"/sites/default/files/20121128052237-Black_Qii_unrolled_1.jpg\" style=\"width: 600px; height: 450px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>На сайте indiegogo.com разместили <a href=\"http://www.indiegogo.com/Qii\">проект гибкой клавиатуры Qii</a>. Из компактного контейнера &nbsp;\"вытягивается\" лента и получается полноразмерная тач-клавиатура. Она ударостойкая и водостойкая.&nbsp;</p>\r\n<p>Сейчас создатели клавиатуры-рулона собирают деньги на реалицию своего проекта, им нужно $1850000, а на данный момент у них уже есть $12582.&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><iframe allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"http://www.youtube.com/embed/S-_hzLIcU8E\" width=\"560\"></iframe></p>\r\n',NULL,3,1),(426,'Pudding Monsters новая игра от создателей Cut the Rope.','<iframe width=\"560\" height=\"315\" src=\"http://www.youtube.com/embed/jPvbXi-yry4\" frameborder=\"0\" allowfullscreen></iframe>\r\n<p>&nbsp;</p>\r\n<p>Наш отечественный разработчик игр для iOS Zeptolab выпустил свою новую игру Pudding Monsters. Судя по видео-ролику игра получилась в том же стиле, что и хит Cut the Rope — такая же интересная, понятная и веселая, а герои игры очень милые.&nbsp;</p>\r\n<p>Смотрим видео, а потом бегом в <a href=\"https://itunes.apple.com/ru/app/pudding-monsters/id569185650?mt=8\"','<iframe width=\"560\" height=\"315\" src=\"http://www.youtube.com/embed/jPvbXi-yry4\" frameborder=\"0\" allowfullscreen></iframe>\r\n<p>&nbsp;</p>\r\n<p>Наш отечественный разработчик игр для iOS Zeptolab выпустил свою новую игру Pudding Monsters. Судя по видео-ролику игра получилась в том же стиле, что и хит Cut the Rope — такая же интересная, понятная и веселая, а герои игры очень милые.&nbsp;</p>\r\n<p>Смотрим видео, а потом бегом в <a href=\"https://itunes.apple.com/ru/app/pudding-monsters/id569185650?mt=8\">App Store приобритать игру</a>!</p>\r\n',NULL,3,1),(427,'App Store уходит на каникулы','<p><img alt=\"\" src=\"/sites/default/files/1321897658_itunes-connect2.jpg\" style=\"width: 580px; height: 169px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Сегодня команда iTunes Connect уйдет в отпуск. А это означает, что с 22-го по 29-ое декабря&nbsp; не будут меняться цены, если какое-то приложение распространяется со скидкой, то продержится она до 28 декабря, также не будут выходить новые приложения и обновления для старых приложений.&nbsp;</p>\r\n<p>Не будут меняться и топы приложений, поэтому для разработчиков ва','<p><img alt=\"\" src=\"/sites/default/files/1321897658_itunes-connect2.jpg\" style=\"width: 580px; height: 169px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Сегодня команда iTunes Connect уйдет в отпуск. А это означает, что с 22-го по 29-ое декабря&nbsp; не будут меняться цены, если какое-то приложение распространяется со скидкой, то продержится она до 28 декабря, также не будут выходить новые приложения и обновления для старых приложений.&nbsp;</p>\r\n<p>Не будут меняться и топы приложений, поэтому для разработчиков важно сейчас вырваться на первые места в топах, чтобы находиться там всю неделю.</p>\r\n',NULL,3,1),(428,'Siri стала меньше использовать Google. ','<p><img alt=\"\" src=\"/sites/default/files/asteroid_siri-google.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<div>В iOS 6 Apple отказалась от некоторых сервисов компании Google - Карты и YouTube. Но оказывается это еще не все. В шестой прошивке запросы Siri все реже стали обрабатываться через Google. Если в iOS 5 около 60% всех запросов голосового помощника использовали данные или сервисы Google,то в iOS 6 этот процент снизился до 30%.</div>\r\n<div>&nbsp;</div>\r\n','<p><img alt=\"\" src=\"/sites/default/files/asteroid_siri-google.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<div>В iOS 6 Apple отказалась от некоторых сервисов компании Google - Карты и YouTube. Но оказывается это еще не все. В шестой прошивке запросы Siri все реже стали обрабатываться через Google. Если в iOS 5 около 60% всех запросов голосового помощника использовали данные или сервисы Google,то в iOS 6 этот процент снизился до 30%.</div>\r\n<div>&nbsp;</div>\r\n',NULL,3,1),(429,'Рождественская реклама iPad mini и iPad 4. ','<p><iframe allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"http://www.youtube.com/embed/eWyZUszyJHg\" width=\"560\"></iframe></p>\r\n','<p><iframe allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"http://www.youtube.com/embed/eWyZUszyJHg\" width=\"560\"></iframe></p>\r\n',NULL,3,1),(430,'Магия Рождества с iPad. ','<p><iframe allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"http://www.youtube.com/embed/-FAN7nFk6Xo\" width=\"560\"></iframe></p>\r\n<div>&nbsp;</div>\r\n<div>Фокусник iSimon выложил новогодние фокусы с использованием iPad. Видео невероятно завораживает, смотрите сами!</div>\r\n','<p><iframe allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"http://www.youtube.com/embed/-FAN7nFk6Xo\" width=\"560\"></iframe></p>\r\n<div>&nbsp;</div>\r\n<div>Фокусник iSimon выложил новогодние фокусы с использованием iPad. Видео невероятно завораживает, смотрите сами!</div>\r\n',NULL,3,1),(431,'Apple TV пришел в Россию.','<p><img alt=\"\" src=\"/sites/default/files/Apple-TV-Russia.jpg\" style=\"width: 600px; height: 387px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Сегодня стартуют официальные продажи Apple TV в России. Яблочная приставка уже появилась на прилавках в некоторых московских магазинах re:Store, через 3-5 дней поступит и в регионы. Приобрести Apple TV можно за 5290 рублей.&nbsp;</p>\r\n<p>На сайте Связного вы можете сделать предзаказ на тв-приставку, но когда она поступит в продажу пока неизвестно.&nbsp;</p>\r\n<p>Apple TV была','<p><img alt=\"\" src=\"/sites/default/files/Apple-TV-Russia.jpg\" style=\"width: 600px; height: 387px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Сегодня стартуют официальные продажи Apple TV в России. Яблочная приставка уже появилась на прилавках в некоторых московских магазинах re:Store, через 3-5 дней поступит и в регионы. Приобрести Apple TV можно за 5290 рублей.&nbsp;</p>\r\n<p>На сайте Связного вы можете сделать предзаказ на тв-приставку, но когда она поступит в продажу пока неизвестно.&nbsp;</p>\r\n<p>Apple TV была представлена в 2007 году. На данный момент вышла уже третья версия яблочной приставки, которая позводляет транслировать видео с разрешением 1080р.&nbsp;</p>\r\n',NULL,3,1),(432,'Рождественская песенка в исполнении робота. ','<p><iframe allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"http://www.youtube.com/embed/RyyIx3EVX9g\" width=\"420\"></iframe></p>\r\n<p>&nbsp;</p>\r\n<p>Известный пользователям YouTube робот Robo-Erectus исполнил популярный рождественский джингл.</p>\r\n','<p><iframe allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"http://www.youtube.com/embed/RyyIx3EVX9g\" width=\"420\"></iframe></p>\r\n<p>&nbsp;</p>\r\n<p>Известный пользователям YouTube робот Robo-Erectus исполнил популярный рождественский джингл.</p>\r\n',NULL,3,1),(433,'Посещаемость магазинов в Рождество. ','<p><img alt=\"\" src=\"/sites/default/files/101191-1280.jpg\" style=\"height: 176px; width: 234px;\">&nbsp;<img alt=\"\" src=\"/sites/default/files/101192-1280.jpg\" style=\"height: 176px; width: 234px;\">&nbsp;<img alt=\"\" src=\"/sites/default/files/101194-1280.jpg\" style=\"height: 176px; width: 234px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Праздники наступают на пятки, многие еще еще не приобрели подарки и делают это сейчас. Постоянный читатель Fortune Говард Каплан решил пройтись по монобрендовым магазинам и посмотреть н','<p><img alt=\"\" src=\"/sites/default/files/101191-1280.jpg\" style=\"height: 176px; width: 234px;\">&nbsp;<img alt=\"\" src=\"/sites/default/files/101192-1280.jpg\" style=\"height: 176px; width: 234px;\">&nbsp;<img alt=\"\" src=\"/sites/default/files/101194-1280.jpg\" style=\"height: 176px; width: 234px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Праздники наступают на пятки, многие еще еще не приобрели подарки и делают это сейчас. Постоянный читатель Fortune Говард Каплан решил пройтись по монобрендовым магазинам и посмотреть на обстановку и запечатлить все на фотоаппарат. Первый магазин - Apple, второй - Microsoft, а третий - Sony.&nbsp;</p>\r\n<p>На фото и так все видно, несомненно, Apple любят больше всего!</p>\r\n',NULL,3,1),(434,'Музыкальные рейтинги русского iTunes Store. ','<p><img alt=\"\" src=\"/sites/default/files/itunessoterussia.jpg\" style=\"width: 700px; height: 540px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Уже прошло 3 недели с момента официального запуска iTunes Store в России. Apple выложила рейтинги самых скачиваемых песен и альбомов.&nbsp;Лучшим альбомом стал «Полный вперёд!» Григория Лепса, а лучшая песня в русском iTunes Store стал мировой хит – «Skyfall» от Адель.</p>\r\n<p>&nbsp;</p>','<p><img alt=\"\" src=\"/sites/default/files/itunessoterussia.jpg\" style=\"width: 700px; height: 540px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Уже прошло 3 недели с момента официального запуска iTunes Store в России. Apple выложила рейтинги самых скачиваемых песен и альбомов.&nbsp;Лучшим альбомом стал «Полный вперёд!» Григория Лепса, а лучшая песня в русском iTunes Store стал мировой хит – «Skyfall» от Адель.</p>\r\n<p>&nbsp;</p>\r\n<div><u><strong>Самые скачиваемые альбомы в iTunes Store Россия:</strong></u></div>\r\n<div>Григорий Лепс – «Полный вперёд!»</div>\r\n<div>Иван Дорн – «Co’n’dorn»</div>\r\n<div>Дэвид Гетта – «Nothing But the Beat Ultimate»</div>\r\n<div>Елка – «Негромкий Концерт»</div>\r\n<div>Вася Обломов – «Стабильность»</div>\r\n<div>ADELE – «21»</div>\r\n<div>Лана Дель Рей – «Born to Die (Paradise Edition)»</div>\r\n<div>Нюша – «Выбирать чудо»</div>\r\n<div>Depeche Mode – «The Best of Depeche Mode, Vol. 1»</div>\r\n<div>Skrillex – «Bangarang»</div>\r\n<div>&nbsp;</div>\r\n<div><u><strong>Самые скачиваемые песни в iTunes Store Россия:</strong></u></div>\r\n<div>ADELE – «Skyfall»</div>\r\n<div>Ленка – «Everything At Once»</div>\r\n<div>PSY – «Gangnam Style»</div>\r\n<div>Рианна – «Diamonds»</div>\r\n<div>Доминик Джокер – «Если ты со мной»</div>\r\n<div>Алекс Клэр – «Too Close»</div>\r\n<div>Люкке Ли – «I Follow Rivers (The Magician Remix)»</div>\r\n<div>ADELE – «Rolling In the Deep»</div>\r\n<div>Готье – «Somebody That I Used to Know»</div>\r\n<div>Баста – «Моя Вселенная” (feat. Тати)»</div>\r\n',NULL,3,1),(435,'На Times Square зависла реклама.','<p><img alt=\"\" src=\"/sites/default/files/Screen-Shot-2012-12-26-at-9.35.14-AM.jpg\" style=\"width: 612px; height: 610px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>На&nbsp;Times Square не раз зависал рекламный здоровенный экран. И вместо роликов туристы наблюдали \"синий экран смерти\" Windows. На этот раз появился на большом дисплее красовалась яблочная операционка OS X.&nbsp;</p>\r\n','<p><img alt=\"\" src=\"/sites/default/files/Screen-Shot-2012-12-26-at-9.35.14-AM.jpg\" style=\"width: 612px; height: 610px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>На&nbsp;Times Square не раз зависал рекламный здоровенный экран. И вместо роликов туристы наблюдали \"синий экран смерти\" Windows. На этот раз появился на большом дисплее красовалась яблочная операционка OS X.&nbsp;</p>\r\n',NULL,3,1),(436,'Мужчина отрубил себе палец, чтобы удобней было пользоваться iPhone. ','<p><img alt=\"\" src=\"/sites/default/files/1_maxi_47be8d-500x500.jpg\" style=\"width: 500px; height: 500px;\"><br>\r\n	&nbsp;</p>\r\n<p><br>\r\n	Шокирующая новость, жителю Денвера Томас Мартел/Thomas Martel срезали часть большого пальца до размеров иконок в iPhone. Хирурги удалили часть костной ткани, модифицировали корни ногтей, сшили мышцы по-другому. Теперь палец Томаса напоминает женский палец. Теперь с каждым днем он все быстрее печатает смски.<br>\r\n	<br>\r\n	Томас невероятно большой мужчина, из-за чего','<p><img alt=\"\" src=\"/sites/default/files/1_maxi_47be8d-500x500.jpg\" style=\"width: 500px; height: 500px;\"><br>\r\n	&nbsp;</p>\r\n<p><br>\r\n	Шокирующая новость, жителю Денвера Томас Мартел/Thomas Martel срезали часть большого пальца до размеров иконок в iPhone. Хирурги удалили часть костной ткани, модифицировали корни ногтей, сшили мышцы по-другому. Теперь палец Томаса напоминает женский палец. Теперь с каждым днем он все быстрее печатает смски.<br>\r\n	<br>\r\n	Томас невероятно большой мужчина, из-за чего он столкнулся с проблемой, у него не всегда получается большими пальцами попасть по иконкам и клавишам iPhone. Поразмыслив, он решил позвонить доктору, который посоветовал ему хирургическое вмешательство.</p>\r\n',NULL,3,1),(437,'Мисс Mac 2012','<p>Ребята из интернет-портала&nbsp;AllThingsD нарисовали портрет типичной пользовательницы Mac. В основном девушки, которые имеют Mac, носят джинсы и кроссовки, а их возраст чаще всего не превышает и 20 лет. Смотрите подробную картинку.&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" src=\"/sites/default/files/Ms.Mac-2012.jpg\" style=\"width: 482px; height: 1200px;\"></p>\r\n','<p>Ребята из интернет-портала&nbsp;AllThingsD нарисовали портрет типичной пользовательницы Mac. В основном девушки, которые имеют Mac, носят джинсы и кроссовки, а их возраст чаще всего не превышает и 20 лет. Смотрите подробную картинку.&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" src=\"/sites/default/files/Ms.Mac-2012.jpg\" style=\"width: 482px; height: 1200px;\"></p>\r\n',NULL,3,1),(438,'Hackulous закрывает Installous и AppSync. ','<p><img alt=\"\" src=\"/sites/default/files/Installous_closed.png\" style=\"width: 500px; height: 333px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Создатели программы&nbsp;Installous и AppSynс сообщили о завершении своей работы. Для тех кто не знает, данные приложения доступны только для тех, кто сделал джейлбрейк своего девайса. Они позволяют качать все приложения для iOS бесплатно.&nbsp;</p>\r\n<p>Проект перестал быть популярным как раньше, все меньше людей занимается пиратством, пропала активность на форуме. Еще одн','<p><img alt=\"\" src=\"/sites/default/files/Installous_closed.png\" style=\"width: 500px; height: 333px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Создатели программы&nbsp;Installous и AppSynс сообщили о завершении своей работы. Для тех кто не знает, данные приложения доступны только для тех, кто сделал джейлбрейк своего девайса. Они позволяют качать все приложения для iOS бесплатно.&nbsp;</p>\r\n<p>Проект перестал быть популярным как раньше, все меньше людей занимается пиратством, пропала активность на форуме. Еще одна причина закрытия то, что для iOS 6 до сих пор нет джейлбрейка, пользователи устали его ждать и начали покупать приложения.&nbsp;</p>\r\n',NULL,3,1),(439,'Новая реклама Apple. ','<p>Applу представила новую рекламу, на этот раз компания решила рассказать нам о функции \"Не беспокоить\". В ролике снялись знаменитые теннисистки сестры Уильямс - Серена и Винус.</p>\r\n<p>&nbsp;</p>\r\n<p><iframe allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"http://www.youtube.com/embed/eLJN_d2sVjk\" width=\"560\"></iframe></p>\r\n','<p>Applу представила новую рекламу, на этот раз компания решила рассказать нам о функции \"Не беспокоить\". В ролике снялись знаменитые теннисистки сестры Уильямс - Серена и Винус.</p>\r\n<p>&nbsp;</p>\r\n<p><iframe allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"http://www.youtube.com/embed/eLJN_d2sVjk\" width=\"560\"></iframe></p>\r\n',NULL,3,1),(440,'Angry Birds ставит новые рекорды. ','<p>Финская компания поставила новые рекорды. Новый рекорд просто ошеломляет, злых птичек за предновогоднюю неделю скачали 30 млн раз. В день Рождества 25 декабря было отмечено 8 млн скачиваний.&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" src=\"/sites/default/files/Angry-Birds-holiday-2012-sales-infographic.jpg\" style=\"width: 700px; height: 550px;\"></p>\r\n','<p>Финская компания поставила новые рекорды. Новый рекорд просто ошеломляет, злых птичек за предновогоднюю неделю скачали 30 млн раз. В день Рождества 25 декабря было отмечено 8 млн скачиваний.&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" src=\"/sites/default/files/Angry-Birds-holiday-2012-sales-infographic.jpg\" style=\"width: 700px; height: 550px;\"></p>\r\n',NULL,3,1),(441,'В Питере открыли памятник Стиву Джобсу в виде огромного iPhone. ','<p>&nbsp;</p>\r\n<div><img alt=\"\" src=\"/sites/default/files/917434686.jpg\" style=\"width: 600px; height: 340px;\"></div>\r\n<div>&nbsp;</div>\r\n<div>Сегодня в Санкт-Петербурге открыли памятник в честь основателя корпорации Apple Стива Джобса.&nbsp;</div>\r\n<div>Изначально планировалось воплотить проект, победивший в конкурсе, который называется \"Солнечный QR-код\". Согласно задумке автора, QR-код должен был бы ежедневно меняться, а прочесть его можно было бы только около полудня. Однако, как пояснил авто','<p>&nbsp;</p>\r\n<div><img alt=\"\" src=\"/sites/default/files/917434686.jpg\" style=\"width: 600px; height: 340px;\"></div>\r\n<div>&nbsp;</div>\r\n<div>Сегодня в Санкт-Петербурге открыли памятник в честь основателя корпорации Apple Стива Джобса.&nbsp;</div>\r\n<div>Изначально планировалось воплотить проект, победивший в конкурсе, который называется \"Солнечный QR-код\". Согласно задумке автора, QR-код должен был бы ежедневно меняться, а прочесть его можно было бы только около полудня. Однако, как пояснил автор идеи Глеб Тарасов на открытии памятника, от этого проекта было решено отказаться из-за свойственной Петербургу пасмурной погоды.</div>\r\n<div>В итоге мы получили мемориал, отдаленно напоминающий iPhone 4.&nbsp;</div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"/sites/default/files/917434978.jpg\" style=\"width: 600px; height: 340px;\"></div>\r\n',NULL,3,1),(442,'Бюджетный iPhone в 2013 году. ','<p><img alt=\"\" src=\"/sites/default/files/iPhone-mini-teaser-001.jpg\" style=\"width: 600px; height: 529px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Известный интернет-портал DigiTimes сообщил о том, что уже в этом году мы увидим более дешевый iPhone, этакий iPhone mini. Вот только меньше он не будет. Дизайн изменится, но диагональ экрана останется прежней, как у iPhone 5.&nbsp;</p>\r\n<p>Эту информацию подтвердил журнал&nbsp;Wall Street Journal, который поти никогда не врет и еще один интернет-ресурс&nbsp;iLounge.<','<p><img alt=\"\" src=\"/sites/default/files/iPhone-mini-teaser-001.jpg\" style=\"width: 600px; height: 529px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Известный интернет-портал DigiTimes сообщил о том, что уже в этом году мы увидим более дешевый iPhone, этакий iPhone mini. Вот только меньше он не будет. Дизайн изменится, но диагональ экрана останется прежней, как у iPhone 5.&nbsp;</p>\r\n<p>Эту информацию подтвердил журнал&nbsp;Wall Street Journal, который поти никогда не врет и еще один интернет-ресурс&nbsp;iLounge.</p>\r\n<p>Вероятнее всего бюджетный яблочный смартфон появится во второй половине 2013 года.&nbsp;</p>\r\n',NULL,3,1),(443,'Неисправный iPhone чуть было не разорил британца. ','<p><img alt=\"\" src=\"/sites/default/files/mNbQ1VUMBb0.jpg\" style=\"width: 576px; height: 365px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Когда житель&nbsp;британского города Тильбюри Крис Бовис узнал о двух неоплаченных счетах на £8 900 и £10 000 за пользование мобильным интернетом, то вначале просто рассмеялся. Но когда сотовый оператор попробовал снять долг со счета британца, Крису стало не до смеху. К счастью для него, транзакция была заблокирована банком.&nbsp;</p>\r\n<p>Крис Бовис отнес свой iPhone в Apple Sto','<p><img alt=\"\" src=\"/sites/default/files/mNbQ1VUMBb0.jpg\" style=\"width: 576px; height: 365px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Когда житель&nbsp;британского города Тильбюри Крис Бовис узнал о двух неоплаченных счетах на £8 900 и £10 000 за пользование мобильным интернетом, то вначале просто рассмеялся. Но когда сотовый оператор попробовал снять долг со счета британца, Крису стало не до смеху. К счастью для него, транзакция была заблокирована банком.&nbsp;</p>\r\n<p>Крис Бовис отнес свой iPhone в Apple Store, где сотрудники после проверки выяснили, что смартфон британца неисправен - iPhone сам подключался к интернету и расходовал трафик с большой скоростью.&nbsp;</p>\r\n<p>iPhone Крису заменили на новый, а сотовый оператор сначала снизил долг до смешных&nbsp;£300, а потом и вовсе решил не взимать с пострадавшего какие-либо деньги.</p>\r\n',NULL,3,1),(444,'iPad mini с Retina дисплеем.','<p><img alt=\"\" src=\"/sites/default/files/iPad-mini-landscape-in-hand-display.jpg\" style=\"width: 593px; height: 323px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Мы уже публиковали слухи о том, что следующее поколение iPad mini будет с Retina дисплеем. Сегодня из Тайваня пришел очередной слух, в котором сообщается, что Apple их уже тестирует. Дисплеи для iPad mini поставляют LG Display и AU Optronics, но похоже Apple собирается добавить к этому списку еще и Shenzhen Century Science &amp; Technology (SCST). Их дисп','<p><img alt=\"\" src=\"/sites/default/files/iPad-mini-landscape-in-hand-display.jpg\" style=\"width: 593px; height: 323px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Мы уже публиковали слухи о том, что следующее поколение iPad mini будет с Retina дисплеем. Сегодня из Тайваня пришел очередной слух, в котором сообщается, что Apple их уже тестирует. Дисплеи для iPad mini поставляют LG Display и AU Optronics, но похоже Apple собирается добавить к этому списку еще и Shenzhen Century Science &amp; Technology (SCST). Их дисплеи по стандарту Retina основаны на технологии One Glass Solution (OGS). Эти дисплеи уже были отправлены в Купертино и Apple начала их тестирование.</p>\r\n',NULL,3,1),(445,'iPad mini с Retina дисплеем.','<p><img alt=\"\" src=\"/sites/default/files/iPad-mini-landscape-in-hand-display.jpg\" style=\"width: 593px; height: 323px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Мы уже публиковали слухи о том, что следующее поколение iPad mini будет с Retina дисплеем. Сегодня из Тайваня пришел очередной слух, в котором сообщается, что Apple их уже тестирует. Дисплеи для iPad mini поставляют LG Display и AU Optronics, но похоже Apple собирается добавить к этому списку еще и Shenzhen Century Science &amp; Technology (SCST). Их дисп','<p><img alt=\"\" src=\"/sites/default/files/iPad-mini-landscape-in-hand-display.jpg\" style=\"width: 593px; height: 323px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Мы уже публиковали слухи о том, что следующее поколение iPad mini будет с Retina дисплеем. Сегодня из Тайваня пришел очередной слух, в котором сообщается, что Apple их уже тестирует. Дисплеи для iPad mini поставляют LG Display и AU Optronics, но похоже Apple собирается добавить к этому списку еще и Shenzhen Century Science &amp; Technology (SCST). Их дисплеи по стандарту Retina основаны на технологии One Glass Solution (OGS). Эти дисплеи уже были отправлены в Купертино и Apple начала их тестирование.</p>\r\n',NULL,3,1),(446,'Фил Шиллер опроверг слухи о бюджетном iPhone. ','<p>&nbsp;</p>\r\n<div><img alt=\"\" src=\"/sites/default/files/phil-schiller.jpg\" style=\"width: 394px; height: 318px;\"></div>\r\n<div>&nbsp;</div>\r\n<div>Фил Шиллер дал небольшое интервью китайской газете Shanghai Evening News. Журналист задал вопрос, который многих интересует, будет ли бюджетная версия яблочного смартфона? На что Фил ответил следующее:&nbsp;</div>\r\n<div>&nbsp;</div>\r\n<div>\"Мы не такие, как наши конкуренты, которые выпускают по несколько продуктов на одном дыхании в надежде, что хоть ка','<p>&nbsp;</p>\r\n<div><img alt=\"\" src=\"/sites/default/files/phil-schiller.jpg\" style=\"width: 394px; height: 318px;\"></div>\r\n<div>&nbsp;</div>\r\n<div>Фил Шиллер дал небольшое интервью китайской газете Shanghai Evening News. Журналист задал вопрос, который многих интересует, будет ли бюджетная версия яблочного смартфона? На что Фил ответил следующее:&nbsp;</div>\r\n<div>&nbsp;</div>\r\n<div>\"Мы не такие, как наши конкуренты, которые выпускают по несколько продуктов на одном дыхании в надежде, что хоть какой-то из них понравится потребителю. Они анонсируют одинаковые устройства по несколько раз в году, а в итоге покупатель выясняет, что заявленные возможности не соответствуют действительности.</div>\r\n<div>Дешевые телефоны пользуются большой популярностью, но они никогда не будут будущими продуктами Apple. Мы выбираем самые лучшие технологии, например, дисплеи Retina, алюминиевые корпуса, чтобы сделать лучший продукт.\"</div>\r\n',NULL,3,1),(447,'Apple вручили Emmy. ','<p><img alt=\"\" src=\"/sites/default/files/Screen-Shot-2013-01-11-at-10.32.40_0.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Несколько дней назад в Лас-Вегасе прошло награждение победителей \"Национальной Академией Телевизионных Искусств и Наук\" (\"Technology &amp; Engineering Emmy Award\").&nbsp;</p>\r\n<p>Apple выиграла в номинации \"Экосистема Реального Времени Презентации Телевизионного Контента на Мобильных Устройствах без использования Специализированных Аппаратных Средств Телевидения\" (\"Eco-system for Real Time ','<p><img alt=\"\" src=\"/sites/default/files/Screen-Shot-2013-01-11-at-10.32.40_0.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Несколько дней назад в Лас-Вегасе прошло награждение победителей \"Национальной Академией Телевизионных Искусств и Наук\" (\"Technology &amp; Engineering Emmy Award\").&nbsp;</p>\r\n<p>Apple выиграла в номинации \"Экосистема Реального Времени Презентации Телевизионного Контента на Мобильных Устройствах без использования Специализированных Аппаратных Средств Телевидения\" (\"Eco-system for Real Time Presentation of TV Content to Mobile Devices without the use of Specialized Television Hardware\"). Название очень длинное и не очень понятно за что именно яблочная компания получила награду. Пишите свои мысли в комментариях.&nbsp;</p>\r\n',NULL,3,1),(448,'iOS 6.1 GM уже на этой неделе. ','<p><img alt=\"\" src=\"/sites/default/files/ios_6_1_GM.jpg\" style=\"width: 600px; height: 450px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Редко ошибающийся интернет-портал iFun сообщил, что уже на этой неделе выйдет обновление мобильной яблочной операционной системы iOS 6.1 GM. А это значит, что примерно к середине февраля, к началу марта выйдет финальная сборка iOS 6.1. Возможно, что выход обновления ОС совпадет с представлением новых планшетов от Apple.&nbsp;</p>\r\n','<p><img alt=\"\" src=\"/sites/default/files/ios_6_1_GM.jpg\" style=\"width: 600px; height: 450px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Редко ошибающийся интернет-портал iFun сообщил, что уже на этой неделе выйдет обновление мобильной яблочной операционной системы iOS 6.1 GM. А это значит, что примерно к середине февраля, к началу марта выйдет финальная сборка iOS 6.1. Возможно, что выход обновления ОС совпадет с представлением новых планшетов от Apple.&nbsp;</p>\r\n',NULL,3,1),(449,'Обновление Apple Thunderbolt Display скоро. ','<p><img alt=\"\" src=\"/sites/default/files/thunderbolt-display-640x678.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Крупный зарубежный интернет-портал&nbsp;AppleInsider.com сообщил, что у самых крупных реселлеров Apple, таких как Amazon и MacMall, заканчиваются&nbsp;Apple Thunderbolt Display. Обычно дефецит товара в таких крупных магазинах является признаком скорого обновления продукта. Это значит, что похоже скоро Apple представит нам обновленные мониторы. Вероятнее всего &nbsp;дизайн будет похожим на &nbsp;ныне','<p><img alt=\"\" src=\"/sites/default/files/thunderbolt-display-640x678.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p>Крупный зарубежный интернет-портал&nbsp;AppleInsider.com сообщил, что у самых крупных реселлеров Apple, таких как Amazon и MacMall, заканчиваются&nbsp;Apple Thunderbolt Display. Обычно дефецит товара в таких крупных магазинах является признаком скорого обновления продукта. Это значит, что похоже скоро Apple представит нам обновленные мониторы. Вероятнее всего &nbsp;дизайн будет похожим на &nbsp;нынешний свертонкий iMac.&nbsp;</p>\r\n',NULL,3,1),(450,'Инфографика опасных зон для iPhone. ','<p>Ребята из&nbsp;SquareTrade создали интересную инфографику, на которой очень красиво нарисовали, где чаще всего ваш iPhone подвержен наибольшему риску.&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" src=\"/sites/default/files/102427.jpg\"></p>\r\n','<p>Ребята из&nbsp;SquareTrade создали интересную инфографику, на которой очень красиво нарисовали, где чаще всего ваш iPhone подвержен наибольшему риску.&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" src=\"/sites/default/files/102427.jpg\"></p>\r\n',NULL,3,1),(451,'Вышла OS X 10.8.3 beta 5','<p><img alt=\"\" src=\"/sites/default/files/image16_0.jpg\" style=\"width: 580px; height: 301px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>С момента выхода предыдущей беты прошло всего 8 дней, а Apple уже выпустил новую сборку под кодом12D54. Списка изменений нет, а разработчиков как и в прошлые беты просят обратить внимание на&nbsp;AirPlay, AirPort, Game Center, Safari и графические драйвера. Судя по всему финал прошивки уже совсем скоро!</p>\r\n','<p><img alt=\"\" src=\"/sites/default/files/image16_0.jpg\" style=\"width: 580px; height: 301px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>С момента выхода предыдущей беты прошло всего 8 дней, а Apple уже выпустил новую сборку под кодом12D54. Списка изменений нет, а разработчиков как и в прошлые беты просят обратить внимание на&nbsp;AirPlay, AirPort, Game Center, Safari и графические драйвера. Судя по всему финал прошивки уже совсем скоро!</p>\r\n',NULL,3,1),(452,'Кадры из фильма о Джобсе.','<p>В сети появились новые кадры из фильма о Стиве Джобсе, который будет называться jOBS.</p>\r\n<p><img alt=\"\" src=\"/sites/default/files/jobssneakpeek1-4_3_r536_c534.jpg\"></p>\r\n<p>&nbsp;</p>','<p>В сети появились новые кадры из фильма о Стиве Джобсе, который будет называться jOBS.</p>\r\n<p><img alt=\"\" src=\"/sites/default/files/jobssneakpeek1-4_3_r536_c534.jpg\"></p>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" src=\"/sites/default/files/03_kutcher_gad-4_3_rx512_c680x510.jpg\"></p>\r\n',NULL,3,1),(453,'Джейлбрек iOS 6 скоро. ','<p><img alt=\"\" src=\"/sites/default/files/dvQakUNBuK0.jpg\" style=\"width: 550px; height: 217px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Многие уже и не надеются на джейлбрейк iOS 6, уж очень много времени прошло со дня выска новой мобильной прошивки. Но вчера хакер&nbsp;Planetbeing опубликовал в своем твиттере такой твит: \"Сегодня мы с @pod2g добились большого прогресса. Думаю я заслужил немного поспать в качестве награды. :)\".&nbsp;</p>\r\n<p>Это значит, что хакеры не опускают руки и возможно совсем скоро появитс','<p><img alt=\"\" src=\"/sites/default/files/dvQakUNBuK0.jpg\" style=\"width: 550px; height: 217px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Многие уже и не надеются на джейлбрейк iOS 6, уж очень много времени прошло со дня выска новой мобильной прошивки. Но вчера хакер&nbsp;Planetbeing опубликовал в своем твиттере такой твит: \"Сегодня мы с @pod2g добились большого прогресса. Думаю я заслужил немного поспать в качестве награды. :)\".&nbsp;</p>\r\n<p>Это значит, что хакеры не опускают руки и возможно совсем скоро появится джейл для шестой прошивки.&nbsp;</p>\r\n',NULL,3,1),(454,'Temple Run 2 скачали 20 млн. раз за 4 дня. ','Temple Run 2 скачали 20 млн. раз\r\n\r\nTemple Run 2 от разработчика Imangi Studios вышла в App Store на прошлой неделе. Уже спустя 8 часов после выхода Temple Run 2 заняла первое место в Топ Бесплатных приложений. \r\nСегодня создатели игры опубликовали официальный пресс-релиз, в котором рассказали о своих рекордах. \r\nЗа дня  прибывания в App Store игру скачали более 20 млн. раз и  было пройдено более 210 млн. сессий!\r\nЕсли вы еще не испробовали Temple Run 2, то сделайте это. Игра простая, но затягив','<p><img alt=\"\" src=\"/sites/default/files/59515_502743353109262_1442076507_n.png\" style=\"height: 259px; width: 700px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<div>Temple Run 2 от разработчика Imangi Studios вышла в App Store на прошлой неделе. Уже спустя 8 часов после выхода Temple Run 2 заняла первое место в Топ Бесплатных приложений.&nbsp;</div>\r\n<div>Сегодня создатели игры опубликовали официальный пресс-релиз, в котором рассказали о своих рекордах.&nbsp;</div>\r\n<div>За дня &nbsp;прибывания в App Store игру скачали более 20 млн. раз и &nbsp;было пройдено более 210 млн. сессий!</div>\r\n<div>Если вы еще не испробовали Temple Run 2, то сделайте это. Игра простая, но затягивающая.&nbsp;</div>\r\n',NULL,3,1),(455,'MOUZ превратит ваш iPhone в мышку с дополнительными возможностями. ','<p>Совсем недавно прошла выставка CES 2013, на которой было представлено огромное количество новинок различных девайсов. Компания Spicebox представила на выставке очень интересный аксессуар MAUZ. Это устройство превращает ваш iPhone в&nbsp;сенсорную мышку для управления вашего Mac, кроме того позволяет имитировать движения iPhone в реальном пространстве.&nbsp;</p>\r\n<p>Spicebox говорит, что это еще не все на что они способны и на ближайшей выставке Macworld планируют показать нечто очень впечатля','<p>Совсем недавно прошла выставка CES 2013, на которой было представлено огромное количество новинок различных девайсов. Компания Spicebox представила на выставке очень интересный аксессуар MAUZ. Это устройство превращает ваш iPhone в&nbsp;сенсорную мышку для управления вашего Mac, кроме того позволяет имитировать движения iPhone в реальном пространстве.&nbsp;</p>\r\n<p>Spicebox говорит, что это еще не все на что они способны и на ближайшей выставке Macworld планируют показать нечто очень впечатляющее.&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<iframe src=\"http://player.vimeo.com/video/55952718?title=0&amp;byline=0&amp;portrait=0\" width=\"400\" height=\"300\" frameborder=\"0\" webkitAllowFullScreen mozallowfullscreen allowFullScreen></iframe>\r\n',NULL,3,1),(456,'ВИДЕО: Классная пародия на Temple Run. ','<iframe width=\"560\" height=\"315\" src=\"http://www.youtube.com/embed/wusV7B6HEmQ\" frameborder=\"0\" allowfullscreen></iframe>','<iframe width=\"560\" height=\"315\" src=\"http://www.youtube.com/embed/wusV7B6HEmQ\" frameborder=\"0\" allowfullscreen></iframe>',NULL,3,1),(457,'Эштон Катчер и Джош Гад выступят на выставке Macworld. ','<p><img alt=\"\" src=\"/sites/default/files/LlWk-n30414.jpg\" style=\"width: 576px; height: 409px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Совсем скоро выставка Macworld, а открывать ее будут&nbsp;Эштон Катчер и Джош Гад, актеры, которые сыграют главные роли в фильме о Стиве Джобсе \"jOBS\". Премьера фильма состоится на&nbsp;закрытии кинофестиваля независимого кино «Сандэнс» в конце января, а выход фильма в прокат намечен на апрель этого года.&nbsp;</p>\r\n<p>На выставке Macworld кинозвезды &nbsp;поделяться своими впеч','<p><img alt=\"\" src=\"/sites/default/files/LlWk-n30414.jpg\" style=\"width: 576px; height: 409px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Совсем скоро выставка Macworld, а открывать ее будут&nbsp;Эштон Катчер и Джош Гад, актеры, которые сыграют главные роли в фильме о Стиве Джобсе \"jOBS\". Премьера фильма состоится на&nbsp;закрытии кинофестиваля независимого кино «Сандэнс» в конце января, а выход фильма в прокат намечен на апрель этого года.&nbsp;</p>\r\n<p>На выставке Macworld кинозвезды &nbsp;поделяться своими впечатлениями от съемок в фильме, а также ответят на вопросы журналистов.&nbsp;</p>\r\n',NULL,3,1),(458,'Разноцветные iPhone 5S. ','<p><img alt=\"\" src=\"/sites/default/files/iPhone-5S-concept-Alexander-Kormishin-003.jpg\" style=\"width: 700px; height: 520px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>В сети все чаще стали появляться слухи о том, что iPhone 5S будет представлен в разных цветах. Блог&nbsp;NowhereElse изобразили свое видение будущего iPhone 5S в различных цветах. Они считают что новый яблочный смартфон будет напоминать нынешние модели iPad mini и iPod touch.&nbsp;</p>\r\n<p>&nbsp;</p>\r\n','<p><img alt=\"\" src=\"/sites/default/files/iPhone-5S-concept-Alexander-Kormishin-003.jpg\" style=\"width: 700px; height: 520px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>В сети все чаще стали появляться слухи о том, что iPhone 5S будет представлен в разных цветах. Блог&nbsp;NowhereElse изобразили свое видение будущего iPhone 5S в различных цветах. Они считают что новый яблочный смартфон будет напоминать нынешние модели iPad mini и iPod touch.&nbsp;</p>\r\n<p>&nbsp;</p>\r\n',NULL,3,1),(459,'ВИДЕО: iPhone 44g','<iframe width=\"560\" height=\"315\" src=\"http://www.youtube.com/embed/r4t9_p5nWd4\" frameborder=\"0\" allowfullscreen></iframe>','<iframe width=\"560\" height=\"315\" src=\"http://www.youtube.com/embed/r4t9_p5nWd4\" frameborder=\"0\" allowfullscreen></iframe>',NULL,3,1),(460,'Колорчузер поможет вам узнать название цвета мгновенно! ','<p>&nbsp;</p>\r\n<div><img alt=\"\" src=\"/sites/default/files/mzl.mdvsgfmh.320x480-75.jpg\" style=\"width: 320px; height: 480px;\">&nbsp;<img alt=\"\" src=\"/sites/default/files/mzl.vfwscodi.320x480-75.jpg\" style=\"width: 320px; height: 480px;\"></div>\r\n<div>&nbsp;</div>\r\n<div>Простое приложение которое поможет вам узнать название цвета мгновенно! Стоит только навести камеру и вы увидите шестнадцатеричный код цвета и его полное название.</div>\r\n<div>Приложение будет полезно не только дизайнерам и людям, свя','<p>&nbsp;</p>\r\n<div><img alt=\"\" src=\"/sites/default/files/mzl.mdvsgfmh.320x480-75.jpg\" style=\"width: 320px; height: 480px;\">&nbsp;<img alt=\"\" src=\"/sites/default/files/mzl.vfwscodi.320x480-75.jpg\" style=\"width: 320px; height: 480px;\"></div>\r\n<div>&nbsp;</div>\r\n<div>Простое приложение которое поможет вам узнать название цвета мгновенно! Стоит только навести камеру и вы увидите шестнадцатеричный код цвета и его полное название.</div>\r\n<div>Приложение будет полезно не только дизайнерам и людям, связанным с графикой, но и поможет разрешить спор с девушкой о цвете ее блузки, сформулировать название цвета который как вам казалось раньше не передать словами и просто посмеяться над разными оттенками которые встречаются в повседневной жизни.&nbsp;</div>\r\n<div><u>Особенности:</u></div>\r\n<div>- Простой интерфейс</div>\r\n<div>- Фиксация цвета одним кликом</div>\r\n<div>- Возможность скопировать название цвета</div>\r\n<div>- Возможность поделиться получившимся цветом через Twitter</div>\r\n<div>&nbsp;</div>\r\n<div>Приложение бесплатное и <a href=\"https://itunes.apple.com/ru/app/colorchuzer/id593545705?mt=8\">доступно в App Store.&nbsp;</a></div>\r\n<div>&nbsp;</div>\r\n<div><img alt=\"\" src=\"/sites/default/files/mzl.kyliwwpw.320x480-75.jpg\" style=\"width: 320px; height: 480px;\">&nbsp;<img alt=\"\" src=\"/sites/default/files/mzl.hoqzybww.320x480-75.jpg\" style=\"width: 320px; height: 480px;\"></div>\r\n',NULL,1,1),(461,'Первый отрывок из фильма «jOBS». ','<p>Появился первый отрывок из фильма о Стиве Джобсе \"jOBS\". Кроме того, стала известна дата выхода кино в прокат - 19 апреля этого года.&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><iframe allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"http://www.youtube.com/embed/F86jjdHrn-E\" width=\"560\"></iframe></p>\r\n','<p>Появился первый отрывок из фильма о Стиве Джобсе \"jOBS\". Кроме того, стала известна дата выхода кино в прокат - 19 апреля этого года.&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><iframe allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"http://www.youtube.com/embed/F86jjdHrn-E\" width=\"560\"></iframe></p>\r\n',NULL,3,1),(462,'Звездная команда хакеров \"evad3rs\" начала работать над джейлбрейком iOS 6. ','<p><img alt=\"\" src=\"/sites/default/files/evad3rs.png\" style=\"width: 516px; height: 255px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Вчера хакер pod2g написал твит, о том, что над джейлбрейком iOS 6 начала работать команда \"evad3rs\". В нее вошли&nbsp;MuscleNerd (из iPhone Dev-Team), pimskeks (из Chronic-Dev Team), planetbeing и pod2g (которые занимаются джейлом iOS 6).&nbsp;</p>\r\n<p>Возможно общими усилиями звездная команда \"evad3rs\" быстрее придет к успеху.&nbsp;</p>','<p><img alt=\"\" src=\"/sites/default/files/evad3rs.png\" style=\"width: 516px; height: 255px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Вчера хакер pod2g написал твит, о том, что над джейлбрейком iOS 6 начала работать команда \"evad3rs\". В нее вошли&nbsp;MuscleNerd (из iPhone Dev-Team), pimskeks (из Chronic-Dev Team), planetbeing и pod2g (которые занимаются джейлом iOS 6).&nbsp;</p>\r\n<p>Возможно общими усилиями звездная команда \"evad3rs\" быстрее придет к успеху.&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" src=\"/sites/default/files/evad3rs2.png\" style=\"width: 535px; height: 314px;\"></p>\r\n',NULL,3,1),(463,'Apple запатентовала вход в Apple Store. ','<p><img alt=\"\" src=\"/sites/default/files/6a0120a5580826970c017c36379c09970b-800wi.jpg\" style=\"width: 640px; height: 404px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Apple запатентовала вход в свои магазины Apple Store. В патенте описывается&nbsp;отличительный дизайн и архитектура яблочных магазинов.&nbsp;</p>\r\n<p>Заявка на этот патент была подана еще в мае 2010 года и только недавно получила одобрение патентного бюро США.&nbsp;</p>','<p><img alt=\"\" src=\"/sites/default/files/6a0120a5580826970c017c36379c09970b-800wi.jpg\" style=\"width: 640px; height: 404px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Apple запатентовала вход в свои магазины Apple Store. В патенте описывается&nbsp;отличительный дизайн и архитектура яблочных магазинов.&nbsp;</p>\r\n<p>Заявка на этот патент была подана еще в мае 2010 года и только недавно получила одобрение патентного бюро США.&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" src=\"/sites/default/files/6a0120a5580826970c017ee7dae68d970d-800wi.jpg\" style=\"width: 640px; height: 790px;\"></p>\r\n',NULL,3,1),(464,'Вышла OS X 10.8.3 бета 6. ','<p><img alt=\"\" src=\"/sites/default/files/softwareupdate.jpg\" style=\"width: 688px; height: 208px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Apple выпустила очередную бету&nbsp;OS X 10.8.3. Данная сборка идет под кодом&nbsp;12D58 (предыдущая была&nbsp;12D54).&nbsp;</p>\r\n<p>Информации про изменения в ОС нет, как обычно Apple просит уделить особое внимание&nbsp;AirPlay, AirPort, Game Center, Safari и графическим драйверами.&nbsp;</p>\r\n<p>Ждем финал прошивки&nbsp;OS X 10.8.3.&nbsp;</p>\r\n','<p><img alt=\"\" src=\"/sites/default/files/softwareupdate.jpg\" style=\"width: 688px; height: 208px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Apple выпустила очередную бету&nbsp;OS X 10.8.3. Данная сборка идет под кодом&nbsp;12D58 (предыдущая была&nbsp;12D54).&nbsp;</p>\r\n<p>Информации про изменения в ОС нет, как обычно Apple просит уделить особое внимание&nbsp;AirPlay, AirPort, Game Center, Safari и графическим драйверами.&nbsp;</p>\r\n<p>Ждем финал прошивки&nbsp;OS X 10.8.3.&nbsp;</p>\r\n',NULL,3,1),(465,'Вышла iOS 6.1 beta 5. ','<p><img alt=\"\" src=\"/sites/default/files/ios61beta5.jpg\" style=\"width: 600px; height: 435px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Apple выпустила очередную беты прошивки iOS 6.1, с момента выхода предыдущей четвертой беты прошло полтора месяца. Какаие нововведения появились в данной сборке неизвестно.&nbsp;</p>\r\n<p>Финал прошивки скорее всего будет представлен в марте на презентации Apple.&nbsp;</p>\r\n','<p><img alt=\"\" src=\"/sites/default/files/ios61beta5.jpg\" style=\"width: 600px; height: 435px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Apple выпустила очередную беты прошивки iOS 6.1, с момента выхода предыдущей четвертой беты прошло полтора месяца. Какаие нововведения появились в данной сборке неизвестно.&nbsp;</p>\r\n<p>Финал прошивки скорее всего будет представлен в марте на презентации Apple.&nbsp;</p>\r\n',NULL,3,1),(466,'iOS 6.1 beta 5 - не \"бета\" версия?','<p><span style=\"line-height: 1.538em;\">Сразу после выхода новой беты&nbsp;iOS 6.1 beta 5 в твиттере стали появлсятся сообщения о том, что может это вовсе и не \"бета\", а настоящий релиз, так как&nbsp;сборка активируется из без регистрации разработчика.&nbsp;</span></p>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" src=\"/sites/default/files/image1_0.jpg\" style=\"width: 600px; height: 685px;\"></p>\r\n','<p><span style=\"line-height: 1.538em;\">Сразу после выхода новой беты&nbsp;iOS 6.1 beta 5 в твиттере стали появлсятся сообщения о том, что может это вовсе и не \"бета\", а настоящий релиз, так как&nbsp;сборка активируется из без регистрации разработчика.&nbsp;</span></p>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" src=\"/sites/default/files/image1_0.jpg\" style=\"width: 600px; height: 685px;\"></p>\r\n',NULL,3,1),(467,'Джейлбрейк для iOS 6.1 готов. ','<p><img alt=\"\" src=\"/sites/default/files/UZtitPntz9o.jpg\" style=\"width: 516px; height: 255px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Хакер Pod2g вчера написал, что джейлбрейк для iOS 6.1 готов! Он отметил, что во взламывании яблочной ОС ему помогли&nbsp;@planetbeing и @pimskeks.&nbsp;</p>\r\n<p>Сейчас хакеры ждут&nbsp;фициального выхода iOS 6.1 GM, после чего проверят&nbsp;не ввела ли Apple какие-то новые защиты и выложат джейлбрейк на общий доступ.</p>\r\n<p>&nbsp;</p>\r\n','<p><img alt=\"\" src=\"/sites/default/files/UZtitPntz9o.jpg\" style=\"width: 516px; height: 255px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Хакер Pod2g вчера написал, что джейлбрейк для iOS 6.1 готов! Он отметил, что во взламывании яблочной ОС ему помогли&nbsp;@planetbeing и @pimskeks.&nbsp;</p>\r\n<p>Сейчас хакеры ждут&nbsp;фициального выхода iOS 6.1 GM, после чего проверят&nbsp;не ввела ли Apple какие-то новые защиты и выложат джейлбрейк на общий доступ.</p>\r\n<p>&nbsp;</p>\r\n',NULL,3,1),(468,'В новом iPad 128gb? ','<p><img alt=\"\" src=\"/sites/default/files/ipadskus2.jpg\" style=\"width: 600px; height: 211px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Вчера хакер&nbsp;@iNeal сообщил&nbsp;о том, что нашел в iOS 6.1 beta 5 поддержку устройств с 128Гб объемом памяти. А сегодня интернет-портал&nbsp;9to5mac написал, что от своих источников удалось выяснить о том, что Apple готовит еще одну версию iPad 4 с 128гб памяти.&nbsp;</p>\r\n<p>Похоже скоро нам представят яблочные планшеты, а возможно и смартфоны с 128 гб объемом памяти.&nbsp;<','<p><img alt=\"\" src=\"/sites/default/files/ipadskus2.jpg\" style=\"width: 600px; height: 211px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Вчера хакер&nbsp;@iNeal сообщил&nbsp;о том, что нашел в iOS 6.1 beta 5 поддержку устройств с 128Гб объемом памяти. А сегодня интернет-портал&nbsp;9to5mac написал, что от своих источников удалось выяснить о том, что Apple готовит еще одну версию iPad 4 с 128гб памяти.&nbsp;</p>\r\n<p>Похоже скоро нам представят яблочные планшеты, а возможно и смартфоны с 128 гб объемом памяти.&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" src=\"/sites/default/files/compareios6and61.jpg\" style=\"width: 600px; height: 612px;\"></p>\r\n',NULL,3,1),(469,'Спортивный зал с изменяющейся LED-подсветкой. ','<p>В Германии компания&nbsp;ASB Systembau разработала прочрачный пол из полимерных плит&nbsp;ASB Glassfloor с LED-подсветкой. Мгновенно разметку спортивного зала можно поменять для разных видов спорта: баскетбола, минифутбола, волейбола или бадминтона, что очень удобно. Сейчас новинка проходит тестирование на прочность и долговечность.&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<iframe width=\"560\" height=\"315\" src=\"http://www.youtube.com/embed/yQXyks57FWU\" frameborder=\"0\" allowfullscreen></iframe>','<p>В Германии компания&nbsp;ASB Systembau разработала прочрачный пол из полимерных плит&nbsp;ASB Glassfloor с LED-подсветкой. Мгновенно разметку спортивного зала можно поменять для разных видов спорта: баскетбола, минифутбола, волейбола или бадминтона, что очень удобно. Сейчас новинка проходит тестирование на прочность и долговечность.&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><iframe allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"http://www.youtube.com/embed/yQXyks57FWU\" width=\"560\"></iframe></p>\r\n',NULL,3,1),(470,'Вышла iOS 6.1. ','<p><img alt=\"\" src=\"/sites/default/files/ghghg1.PNG\" style=\"width: 350px; height: 525px;\">&nbsp;<img alt=\"\" src=\"/sites/default/files/ghghg2.PNG\" style=\"width: 350px; height: 525px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Позавчера Apple выпустила iOS 6.1 beta 5, а уже вчера выпустила полноценная финальная прошивка iOS 6.1.&nbsp;</p>\r\n<p>Изменений немного, но все равно советуем обновиться.&nbsp;</p>\r\n<p>Кроме того вышла&nbsp;iOS 6.1&nbsp;и для AppleTV, изменений там побольше. А еще обновился Xcode до версии 4.','<p><img alt=\"\" src=\"/sites/default/files/ghghg1.PNG\" style=\"width: 350px; height: 525px;\">&nbsp;<img alt=\"\" src=\"/sites/default/files/ghghg2.PNG\" style=\"width: 350px; height: 525px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Позавчера Apple выпустила iOS 6.1 beta 5, а уже вчера выпустила полноценная финальная прошивка iOS 6.1.&nbsp;</p>\r\n<p>Изменений немного, но все равно советуем обновиться.&nbsp;</p>\r\n<p>Кроме того вышла&nbsp;iOS 6.1&nbsp;и для AppleTV, изменений там побольше. А еще обновился Xcode до версии 4.6, добавлены некоторые доработки под Mountain Lion и iOS 6.1.</p>\r\n',NULL,3,1),(471,'В iOS 6.1 новые кнопки управления музыкой на экране блокировки. ','<p><img alt=\"\" src=\"/sites/default/files/redesigned-music-controls-6.1.jpg\" style=\"width: 640px; height: 177px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>В описании прошивке пишутся только большие изменения, а маленькие нет, например, новые кнопки управления музыкой на экране блокировки. Теперь кнопочки похожи по дизайну с остальными элементами управления в&nbsp;в iOS и iTunes.&nbsp;</p>\r\n<p>Если вы нашли еще какие-нибудь изменения - пишите в комментариях!</p>\r\n','<p><img alt=\"\" src=\"/sites/default/files/redesigned-music-controls-6.1.jpg\" style=\"width: 640px; height: 177px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>В описании прошивке пишутся только большие изменения, а маленькие нет, например, новые кнопки управления музыкой на экране блокировки. Теперь кнопочки похожи по дизайну с остальными элементами управления в&nbsp;в iOS и iTunes.&nbsp;</p>\r\n<p>Если вы нашли еще какие-нибудь изменения - пишите в комментариях!</p>\r\n',NULL,3,1),(472,'Фото задней панели iPad 5. ','<p><img alt=\"\" src=\"/sites/default/files/ipadside.jpg\" style=\"line-height: 1.538em; width: 700px; height: 167px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>В сеть попали фотографии задней панели предположительно iPad 5. На фотографии крышка сильно напоминает по дизайну iPad mini.&nbsp;</p>\r\n<p>&nbsp;</p>','<p><img alt=\"\" src=\"/sites/default/files/ipadside.jpg\" style=\"line-height: 1.538em; width: 700px; height: 167px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>В сеть попали фотографии задней панели предположительно iPad 5. На фотографии крышка сильно напоминает по дизайну iPad mini.&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><img alt=\"\" src=\"/sites/default/files/ipad5back.jpg\" style=\"width: 700px; height: 534px;\"></p>\r\n',NULL,3,1),(473,'iPad 4 на 128Гб в продаже с 5 февраля. ','<p><img alt=\"\" src=\"/sites/default/files/retina-ipad.png\" style=\"width: 640px; height: 313px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Только вчера мы написали о том, что возможно скоро Apple представит iPad c 128гб памяти. А меньше часа назад яблочная компания выпустила пресс-релиз, в котором анонсировала iPad 4 на 128гб. Модель только с Wi-Fi в США&nbsp;<span style=\"line-height: 1.538em;\">будет стоить&nbsp;</span><span style=\"line-height: 1.538em;\">$799, а вариант с поддержкой мобильного интернета Wi-Fi+Cellu','<p><img alt=\"\" src=\"/sites/default/files/retina-ipad.png\" style=\"width: 640px; height: 313px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Только вчера мы <a href=\"http://macov.net/news/v-novom-ipad-128gb\">написали</a> о том, что возможно скоро Apple представит iPad c 128гб памяти. А меньше часа назад яблочная компания выпустила <a href=\"http://www.apple.com/pr/library/2013/01/29Apple-Increases-iPad-with-Retina-Display-to-128GB.html\">пресс-релиз</a>, в котором анонсировала iPad 4 на 128гб. Модель только с Wi-Fi в США&nbsp;<span style=\"line-height: 1.538em;\">будет стоить&nbsp;</span><span style=\"line-height: 1.538em;\">$799, а вариант с поддержкой мобильного интернета Wi-Fi+Cellular&nbsp;</span><span style=\"line-height: 1.538em;\">обойдется&nbsp;</span><span style=\"line-height: 1.538em;\">в</span><span style=\"line-height: 1.538em;\">&nbsp;$929.&nbsp;</span></p>\r\n<p><span style=\"line-height: 1.538em;\">На прилавках магазинов новинка появится уже 5 февраля.&nbsp;</span></p>\r\n',NULL,3,1),(474,'Новый Apple TV. ','<p><img alt=\"\" src=\"/sites/default/files/newappletv.jpg\" style=\"width: 475px; height: 450px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>FCC (Федеральное Агентство по Связи) выложила чертеж новой модели Apple TV. То, что новая модель тв-приставки разрабатывается подкрепляется и найденной в&nbsp;iOS 6.1 поддержкой некого AppleTV3,2.&nbsp;</p>\r\n<p>На чертеже вижно, что устройство останется квадратным, но уменьшится в размере примерно по 4,5мм с каждой стороны.&nbsp;</p>\r\n','<p><img alt=\"\" src=\"/sites/default/files/newappletv.jpg\" style=\"width: 475px; height: 450px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>FCC (Федеральное Агентство по Связи) выложила чертеж новой модели Apple TV. То, что новая модель тв-приставки разрабатывается подкрепляется и найденной в&nbsp;iOS 6.1 поддержкой некого AppleTV3,2.&nbsp;</p>\r\n<p>На чертеже вижно, что устройство останется квадратным, но уменьшится в размере примерно по 4,5мм с каждой стороны.&nbsp;</p>\r\n',NULL,3,1),(475,'Умные часы Pebble SmartWatch. ','<p><img alt=\"\" src=\"/sites/default/files/1359094348_zshdsc05398.jpg\" style=\"width: 640px; height: 425px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Прошло 9 месяцев со дня анонсирования умных наручных часов Pebble SmartWatch и совсем недавно они поступили в продажу. Упаковка часов напоминает коробки Amazon, и те и другие сделаны из вторсырья. В комлекте кроме часов поставляется USB-кабель для зарядки.&nbsp;</p>','<p><img alt=\"\" src=\"/sites/default/files/1359094348_zshdsc05398.jpg\" style=\"width: 640px; height: 425px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Прошло 9 месяцев со дня анонсирования умных наручных часов Pebble SmartWatch и совсем недавно они поступили в продажу. Упаковка часов напоминает коробки Amazon, и те и другие сделаны из вторсырья. В комлекте кроме часов поставляется USB-кабель для зарядки.&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><iframe allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"http://www.youtube.com/embed/neYmkQMkSvc\" width=\"560\"></iframe></p>\r\n',NULL,3,1),(476,'Заряжаем iPhone от огня. ','<p><img alt=\"\" src=\"/sites/default/files/135121212.png\" style=\"width: 640px; height: 363px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>Кип Кедерша, известный больше под ником Kipkay, разработал прототип устройства, генерирующего энерегию для зарядки смартфона от огня.&nbsp;</p>\r\n<p>Девайс сделан из банки от кофе,&nbsp;проволоки и хомута. Такое устройство будет очень полезно походникам.&nbsp;</p>\r\n<div>&nbsp;</div>','<p><img alt=\"\" src=\"/sites/default/files/135121212.png\" style=\"width: 640px; height: 363px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>Кип Кедерша, известный больше под ником Kipkay, разработал прототип устройства, генерирующего энерегию для зарядки смартфона от огня.&nbsp;</p>\r\n<p>Девайс сделан из банки от кофе,&nbsp;проволоки и хомута. Такое устройство будет очень полезно походникам.&nbsp;</p>\r\n<div>&nbsp;</div>\r\n<p><iframe allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"http://www.youtube.com/embed/yNDxkXVjRFU\" width=\"560\"></iframe></p>\r\n',NULL,3,1),(477,'iPad - произведение искусства. ','<p><img alt=\"\" src=\"/sites/default/files/1358928462_ipad-art-foxconn.jpg\" style=\"width: 640px; height: 349px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Художник Ли Ляо выставил свою экспозицию \"ON | OFF\" в&nbsp;Улленском центре современного искусства в Пекине. Ли проработал на заводе Foxconn полторамесяца, где собирал технику Apple и настолько вдохновился яблочной магией, что решил приравнять iPad к искусству. Кроме планшета он также представил публике бейдж, униформу и даже контракт с&nbsp;Foxconn.&nbsp;</p>\r\n<','<p><img alt=\"\" src=\"/sites/default/files/1358928462_ipad-art-foxconn.jpg\" style=\"width: 640px; height: 349px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>Художник Ли Ляо выставил свою экспозицию \"ON | OFF\" в&nbsp;Улленском центре современного искусства в Пекине. Ли проработал на заводе Foxconn полторамесяца, где собирал технику Apple и настолько вдохновился яблочной магией, что решил приравнять iPad к искусству. Кроме планшета он также представил публике бейдж, униформу и даже контракт с&nbsp;Foxconn.&nbsp;</p>\r\n<p>&nbsp;</p>\r\n',NULL,3,1),(478,'Mailbox вышел в AppStore. ','<p>&nbsp;</p>\r\n<div><img alt=\"\" src=\"/sites/default/files/mzl.vskunyjq.320x480-75.jpg\" style=\"width: 233px; height: 399px;\">&nbsp;<img alt=\"\" src=\"/sites/default/files/mzl.bsrvehtg.320x480-75.jpg\" style=\"width: 233px; height: 399px;\">&nbsp;<img alt=\"\" src=\"/sites/default/files/mzl.dbfpleyo.320x480-75.jpg\" style=\"width: 233px; height: 399px;\"></div>\r\n<div>&nbsp;</div>\r\n<div>Красивый почтовый клиент, о котором мы уже вам рассказывали в декабре, вышел в App Store.&nbsp;</div>\r\n<div>TechCrunch уже у','<p>&nbsp;</p>\r\n<div><img alt=\"\" src=\"/sites/default/files/mzl.vskunyjq.320x480-75.jpg\" style=\"width: 233px; height: 399px;\">&nbsp;<img alt=\"\" src=\"/sites/default/files/mzl.bsrvehtg.320x480-75.jpg\" style=\"width: 233px; height: 399px;\">&nbsp;<img alt=\"\" src=\"/sites/default/files/mzl.dbfpleyo.320x480-75.jpg\" style=\"width: 233px; height: 399px;\"></div>\r\n<div>&nbsp;</div>\r\n<div>Красивый почтовый клиент, о котором мы уже вам рассказывали в декабре, вышел в App Store.&nbsp;</div>\r\n<div>TechCrunch уже успели назвать Mailbox \"лучшим почтовым клиентом, среди тех, которые выдели\". Такое звание клиент получил благодаря легкости, быстроте и удобства, это некая смесь Sparrow и Clear. Работает она только с Gmail, но это только «пока».&nbsp;</div>\r\n<div>Есть только одна проблема – пользоваться Mailbox сразу же вы не сможете.</div>\r\n<div>&nbsp;</div>\r\n<div>Разработчики решили допускать к программе ограниченное количество пользователей и постепенно. При запуске программы вы встаете в очередь. Вам показывают столько людей перед вами и сколько за вами. Постепенно число стоящих впереди будет уменьшаться, а очередь за вами увеличиваться.&nbsp;</div>\r\n<div>&nbsp;</div>\r\n<div>Занять место в очереде вы можете скачав приложение из <a href=\"https://itunes.apple.com/ru/app/mailbox/id576502633?mt=8\">яблочного магазина</a>.&nbsp;</div>\r\n',NULL,3,1),(479,'Evasi0n взломала 7 млн устройств за 4 дня.','<p><img alt=\"\" src=\"/sites/default/files/evasi0n-2.jpg\" style=\"width: 211px; height: 299px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>В первые 24 часа после релиза джейлбрейка прошивок iOS 6.x было взломано 1,7 млн устройств. Сегодня создатель Cydia Jay Freeman, больше известный как saurik, поделился новыми данными. &nbsp;За 4 дня существования джейлбрейка Evasi0n, Cydia была поставлена на более чем на 7 млн устройств. &nbsp;</p>\r\n','<p><img alt=\"\" src=\"/sites/default/files/evasi0n-2.jpg\" style=\"width: 211px; height: 299px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>В первые 24 часа после релиза джейлбрейка прошивок iOS 6.x было взломано 1,7 млн устройств. Сегодня создатель Cydia Jay Freeman, больше известный как saurik, поделился новыми данными. &nbsp;За 4 дня существования джейлбрейка Evasi0n, Cydia была поставлена на более чем на 7 млн устройств. &nbsp;</p>\r\n',NULL,3,1),(480,'Вышла iOS 6.1.1. ','<p><img alt=\"\" src=\"/sites/default/files/image-1360613308.jpeg\" style=\"width: 333px; height: 500px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>После обновления на прошивку iOS 6.1 пользователи iPhone 4s заметили проблемы с 3G и прочие неприятности. Сегодня Apple выпустила обновление прошивки и только для iPhopne 4s. Поэтому если вы обладатель этой модели яблочного смартфона обновляйтесь!&nbsp;</p>\r\n','<p><img alt=\"\" src=\"/sites/default/files/image-1360613308.jpeg\" style=\"width: 333px; height: 500px;\"></p>\r\n<p>&nbsp;</p>\r\n<p>После обновления на прошивку iOS 6.1 пользователи iPhone 4s заметили проблемы с 3G и прочие неприятности. Сегодня Apple выпустила обновление прошивки и только для iPhopne 4s. Поэтому если вы обладатель этой модели яблочного смартфона обновляйтесь!&nbsp;</p>\r\n',NULL,3,1),(481,'Real Racing 3 выйдет 28 февраля. ','<p>Сегодня стало известно, когда выйдет долгожданная игра&nbsp;Real Racing 3, которая должна была выйти еще осенью прошлого года. Итак, 28 февраля вы сможете скачать возможно самые лучшие гонки на мобильных платформах.&nbsp;Игра одновременно появится в App Store и Google Play с Amazon Appstore.</p>\r\n<p>&nbsp;</p>\r\n<p><iframe allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"http://www.youtube.com/embed/UmkYHZpel3Y\" width=\"560\"></iframe></p>\r\n','<p>Сегодня стало известно, когда выйдет долгожданная игра&nbsp;Real Racing 3, которая должна была выйти еще осенью прошлого года. Итак, 28 февраля вы сможете скачать возможно самые лучшие гонки на мобильных платформах.&nbsp;Игра одновременно появится в App Store и Google Play с Amazon Appstore.<